INSERT INTO `jbd_poet` VALUES (1, 'ef051124b8a343b4a3b8ed7cb942047c', '卢纶', '卢纶(739-799年)，字允言，河北涿州人，大历十才子之一。屡试不第，大历六年，经元载举荐授阌乡尉，后历任集贤学士，秘书省校书郎，升监察御史，出为陕州户曹、密县令、昭应县令，官至检校户部郎中。有《卢户部诗集》。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (2, '70798a6a5b7541eba04bcfe6474b0110', '杨炯', '杨炯（650—693），华州华阴（今属陕西）人，唐代文学家。与王勃、卢照邻、骆宾王并称初唐四杰。杨炯自幼聪敏博学，唐显庆四年（659年）应童子举及第，翌年待制弘文馆，后补秘书省校书郎，擢升太子（李显', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (3, '4da832c875cb4805b9de904ffe6c47ce', '骆宾王', '骆宾王（约619年—约687年），  字观光，婺州义乌（今浙江义乌市）人。唐代著名诗人。\n出身寒微，少有才名。永徽年间，成为道王李元庆府属，历任武功主簿，迁长安主簿。仪凤三年（678年），入为侍御史，因事下狱，遇赦而出。调露二年（680年），除临海丞，坐事辞官。光宅元年（684年），跟随徐敬业起兵讨伐武则天时，撰写《为徐敬业讨武曌檄》。徐敬业败亡后，下落不明，或说被乱军所杀，或说遁入了空门。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (4, '1024dd9a13ac4909bc38b2f88a7bb969', '王昌龄', '王昌龄（698—757），字少伯，河东晋阳人，盛唐著名边塞诗人。王昌龄30岁及第。初任秘书省校书郎，后历任汜水尉，再迁为江宁丞。而后被谤谪龙标尉。王昌龄诗以七绝见长，尤以所作边塞诗最著，被后人誉为“七绝圣手”。今存文集六卷，诗集四卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (5, '726d7353ce674853a8e98efec4725891', '李白', '李白（701－762），字太白，号青莲居士，又号“谪仙人”。唐代浪漫主义诗人，被后人誉为“诗仙”。据《新唐书》载，李白与李唐诸王同宗。其人一生富有传奇色彩，为人爽朗大方，喜饮酒作诗。有《李太白集》传世，风格豪迈，清新飘逸，颇能见太白之志。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (6, '910c38f3fe554813b25e3d3a6aee320d', '岑参', '岑参（715-770），荆州江陵人，唐代边塞诗人。岑参早年遍览史籍。天宝三年进士及第，初为率府兵曹参军。后历任高仙芝幕府掌书记；封常清幕府判官。官至嘉州刺史，世称“岑嘉州”。岑参长于七言歌行，《全唐诗》编诗四卷，今有《岑嘉州集》七卷行世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (7, '53094c05b14142c4985fa17b1de70e86', '韦应物', '韦应物（737～792），长安人。历任洛阳丞、京兆府功曹参军、鄂县令、滁州和江州刺史。官至苏州刺史。世称“韦苏州”。其诗风恬淡高远。今传有《韦江州集》十卷、《韦苏州诗集》两卷、《韦苏州集》十卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (8, '4882f704f0ab43f185dc20369324ac5e', '孟郊', '孟郊（751—814），字东野，湖州武康人。孟郊四十六岁及第，任溧阳县尉，不得志，遂放迹林泉，徘徊赋诗。后因河南尹郑余庆之荐，任职河南。孟郊工诗。因其诗作多写世态炎凉，民间苦难，有“诗囚”之称。今传有《孟东野诗集》10卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (9, 'eb45727e19034154b2e5ea30225e4c9f', '韦元旦', '韦元旦,京兆万年人。擢第补东阿尉，迁左台监察御史。后被贬感义尉，终中书舍人。与沈佺期友善。其诗多为应制，饯别类型，带有明显的宫廷诗色彩。《全唐诗》、《全唐诗补编》共存其诗十二首，《全唐文》存其文一篇。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (10, '078618525bed4378bd407c2c5e855083', '卢藏用', '卢藏用（664-713），字子潜，幽州范阳人。少以文辞才学著称，举进士不得，遂归隐终南山。而后召授左拾遗，历任为礼部侍郎，兼昭文馆学士。与陈子昂友善，赞子昂“横制颓波，天下翕然，质文一变”。能属文，善功诗。有文集三十卷，《全唐诗》录存其诗八首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (11, '8c04765ae14f477ab3b140bfcd8080d0', '刘长卿', '刘长卿(726—786)，字文房，宣城人。唐玄宗天宝年间进士，历任监察御史。后因事下狱，左迁南巴尉。官终随州刺史，世称“刘随州”。长卿善工诗，长于五言，自称“五言长城”。《全唐诗》收刘长卿诗5卷，《全唐诗外编》及《全唐诗续拾》补诗2首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (12, 'f95e9ef80158412d9169d3e1d7e23211', '白居易', '白居易（772－846），字乐天，号香山居士。河南新郑人。官至翰林学士、左赞善大夫。唐代三大诗人之一。白居易与元稹交好，二人有唱和诗歌传世，世称“元白”。乐天前期诗歌以讽谏为主，有《新乐府》传世；后期专攻通俗诗歌创作。语言平易通俗。有《白氏长庆集》传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (13, '37c1e85d8eae433e9d52cd404fb9a161', '许浑', '许浑（约791～约858），字用晦（一作仲晦），唐代诗人，润州丹阳人。大和六年（832）进士及第，历任当涂、太平令。后转入为监察御史，又任润州司马。历户部员外郎，转睦、郢二州刺史。晚年归润州丁卯桥村舍闲居，自编诗集，曰《丁卯集》。其诗多写“水”，故有“许浑千首湿”之称。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (14, '92210671208840beb4b764ce30a3d27e', '李商隐', '李商隐（约813年-约858年），\n字义山，号玉溪生，又号樊南生，祖籍怀州河内。出生于郑州荥阳。开成二年（837年），李商隐登第，曾任秘书省校书郎、弘农尉等职。因卷入“牛李党争”而备受排挤，蹭蹬一生。李商隐工诗，善属文。其爱情与无题诗最佳：缠绵悱恻，优美动人，为人传诵。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (15, '2d77d40ff5ca469db61c901ca831b18f', '张九龄', '张九龄（678年—740年）字子寿。唐朝韶州曲江人，世称“张曲江”或“文献公”。唐朝开元年间名相，诗人。少属文，景龙间进士，始调校书郎，俄迁右补阙。历任官中书侍郎、同中书门下平章事、中书令。张九龄善五言古诗，诗风清淡，语言素练质朴。有《曲江集》传世', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (16, '4669855f00a74db08165dd9c1a4d0b60', '阎朝隐', '阎朝隐，字友倩，赵州栾城人。累迁给事中，预修《三教珠英》。历任麟台少监，因张易之事，贬谪岭外。又贬通州别驾。今存诗十三首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (17, '224478f25b56459f9b33e2d9aceab36d', '李适', '李适，生卒年不详，字子至，京兆万年人，举进士，再调猗氏尉。武后修《三教珠英》书，以李峤、张昌宗为使子思与孟子并提，后世称思孟学派。该学派注重内心省察的，取文学士缀集，于是适与王无竞、尹元凯、富嘉谟、宋之问、沈佺期、阎朝隐、刘允济在选。书成，迁户部员外郎，俄兼脩书学士。景龙初，又擢脩文馆学士。睿宗时，待诏宣光阁，再选工部侍郎。卒，年四十九，赠贝州刺史。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (18, '235b3190e3d84563ac88f6b45891fac2', '刘宪', '刘宪（655年―711年），字元度，宋州宁陵人，年少有才，十五岁举进士，初授阜城尉，历任武德、白水、伊阙三县尉，累进左台监察御史、殿中侍御史、侍御史、尚书工部员外郎。官终太子詹事，兼崇文馆学士。善属文，著有文集三十卷。《新唐书艺文志》有传。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (19, 'ee7093c735f64eb686b8aa381e2b1138', '徐彦伯', '徐彦伯，名洪，以字行，兖州瑕丘人。生年不详，卒于公元714年。七岁能文。初调永寿尉，又任蒲州司兵参军。与司户韦暠，司士李亘，并称河东三绝。官终太子宾客。其诗多为闺怨、应制之作。有诗集二十卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (20, '5478a9a117994eaca91b4171a2e66193', '李乂', '李乂（647-714）字尚真，赵州房子人（今河北邢台市临城县）。工属文，与兄尚一，尚贞俱以文章见称。举进士，历任万年尉、监察御史、中书舍人，修文馆学士。官终刑部尚书。乂著有文集五卷，《两唐书志》又与兄尚一、尚贞同为一集，号为李氏花萼集，凡二十卷，并行于世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (21, 'f5914d42cee3483298b54f16c8e67a8e', '薛稷', '薛稷（649～713年），字嗣通，蒲州汾阴（今山西万荣县）人。曾任黄门侍郎、参知机务、太子少保、礼部尚书等职。书法方面有很高的造诣，与褚遂良、欧阳询、虞世南并列初唐四大书法家。又具备很高的文字才能。《全唐诗》中共收录其作品14篇。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (22, '1e779e679d62409694b685081a7876fd', '马怀素', '马怀素（659―718），字惟白。润州丹徒（今江苏镇江）人。年少家贫，苦读经史。开元初，任吏部侍郎，加银青光禄大夫。累封常山县公。兼昭文馆学士，又迁左台监察御史。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (23, '4fe6bafebf3143af9eee00c91471d02c', '沈佺期', '沈佺期（约656—约715），字云卿，相州内黄人。与宋之问齐名，称“沈宋”。长安中，累迁通事舍人，稍迁台州录事参军。历任中书舍人，太子少詹事。开元初卒。善属文，尤长七言之作。原有文集10卷，已佚。明人辑有《沈佺期集》。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (24, '5240ab3c8074455faa8783dbdd6e16d5', '武平一', '武平一，名甄。博学，通《春秋》。为人谨慎，武后在位时，平一隐于嵩山，修佛法。而后召为起居舍人，又兼修文馆直学士，迁考功员外郎。与唐中宗同游，常写诗规诫自己。官终苏州参军。著有诗一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (25, 'a05bd9d32e4045fbab4f87be90bca07f', '徐坚', '徐坚（660年-729年），字元固，浙江长兴人。少举进士，历任太子左庶子、秘书监、左散骑常侍、崇文馆学士、集贤院学士。与刘知几等同修《三教珠英》、《初学记》、《则天实录》。著有《大隐传》、辑有《初学记》传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (26, '50bf7f925ab641f8a4fcef50044ca109', '宋之问', '宋之问（约656—约712），字延清，汾州隰城人，与沈佺期并称“沈宋”。武则天时，以文才为宫廷侍臣，颇受恩宠。中宗景龙中（708年）转考功员外郎，与杜审言、薛稷等同为修文馆学士。后因受贿罪贬越州长史。睿宗景云元年（710年）流放钦州。善工诗，有《宋之问集》传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (27, '110643ac3c41483d9dcfc9a6e99a3f63', '崔湜', '崔湜（671年－713年），字澄澜，定州安喜人进士及第，初调考功员外郎，累迁至中书侍郎、同平章事。后被贬为江州司马。历任尚书左丞，吏部侍郎。升任同中书门下三品。官终中书令。《全唐文》收录其文三篇。《全唐诗》收录其诗32首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (28, '835cf61ac8a34cfdadd92532b6071972', '苏颋', '苏颋670年－727年）字廷硕，京兆武功（今陕西武功）人，历任监察御史。神龙中迁给事中、修文馆学士、中书舍人。官终礼部尚书。善属文，颇受玄宗赞赏。与燕国公张说相称，世称燕许。有诗集三十卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (29, '2d420c9c27234981acd51b6a66f8d5ca', '张说', '张说（667年－730年），字道济。历任太子校书、左补阙、右史、内供奉、凤阁舍人，参与编修《三教珠英》官至丞相。执掌文坛三十年，为开元前期一代文宗，与许国公苏颋齐名，号称“燕许大手笔”。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (30, '5a307058d31846c5abf16d30227604ec', '赵彦昭', '赵彦昭（生年不详），字奂然，甘州张掖人，约卒于唐玄宗开元二年（714年）后不久。少豪迈，风骨秀爽。及进士第，调南部尉。历左台监察御史。中宗时，累迁中书侍郎，同中书门下平章事。睿宗立，出为宋州刺史。后入为吏部侍郎，迁刑部尚书，封耿国公。寻贬江州别驾，卒。彦昭所作诗，《全唐诗录》存一卷，传于世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (31, '07b76567cef645b2af0b237d25c40dbf', '李峤', '李峤（645~714年），字巨山，赵郡赞皇（今河北赞皇县）人。唐朝时期宰相。出身赵郡李氏东祖房，早年进士及第，历任安定小尉、长安尉、监察御史、给事中、润州司马、凤阁舍人、麟台少监等职。武周时期，依附张易之兄弟。中宗年间，依附韦皇后和梁王武三思，官至中书令、特进，封为赵国公。唐睿宗时，贬为怀州刺史，以年老致仕。唐玄宗时，再贬滁州别驾，迁庐州别驾。开元二年（714年）病逝于庐州，终年七十岁。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (32, '1d608ae139c7499f858f8ebe679e1e1b', '蔡希周', '蔡希周（688年—747年4月15日），字良傅，润州丹阳（今江苏丹阳）人。唐代诗人。少年为太学生，后登进士第，调补广平郡肥乡县尉。历官监察御史，转侍御史兼殿中。寻除膳部员外郎，稍转刑部员外郎。天宝五载刑部尚书韦坚为李林甫所构，蔡希周受牵连贬咸安郡司马。次年因消渴病（糖尿病），卒于郡之官舍，享年六十。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (33, 'dbbc146f178f4705b63c053aaef4194d', '无', '无', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (34, '1ab57ed4680445b382bebddfbc7a92a4', '吕道生', '吕道生，为文宗时人，生平不详。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (35, 'a599b97415c04643a4c1f6fdb15fff75', '张读', '张读（约公元八五三年前后在世），唐代人，字圣用，（书录解题作圣朋。此从旧唐书张荐传及新唐书志注）深州陆浑人，张荐之孙。生卒年均不详，约唐宣宗大中中前后在世。年十九，登进士第。有俊才。累官至中书舍人，礼部侍郎，典贡举，时称得士。位终尚书左丞。读著有建中西狩录十卷，宣室志十卷，（《新唐书志》及《四库总目提要》）传于世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (36, 'ca91f27c195b42869aa7825a8c291c85', '钟辂', '钟辂，唐文宗大和（八二七～八三五）中，官至崇文馆校书郎。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (37, 'aa6cc2fd46c44ca69573d99be951476a', '卢僎', '卢僎，字守成，范阳涿县人，吏部尚书卢从愿之从父，唐玄宗时期大臣，诗人。开元六年，自闻喜尉入为集贤殿学士，出为襄阳令。开元末年，历任祠部、司勋员外郎，终吏部员外郎。馔工诗，所作今存十四首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (38, '043200d645ee46c8a7104283c6c35652', '岑羲', '岑羲（？－713年），字伯华，南阳棘阳（今河南新野）人，岑羲进士出身，历任太常博士、郴州司法参军、金坛令、汜水令、天官员外郎、中书舍人、秘书少监、吏部侍郎。《全唐文》收录有奏疏一篇，《全唐诗》收录有诗作六首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (39, '40a37c9512814a10914ca7ec7fb99c55', '储光羲', '储光羲（约706—763）润州延陵人，田园山水诗派代表诗人之一。开元十四年（726年）举进士，授冯翊县尉，转汜水、安宣、下邽等地县尉。因仕途失意，遂隐居终南山。后复出任太祝，官至监察御史。今有《储光羲集》5卷传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (40, '70fc83cd5e784a44878cd8c9f0d51d3c', '沈颂', '沈颂，生卒年不详，吴兴武康（今浙江德清）人，官无锡尉。《全唐诗》存诗六首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (41, '4b8fba2359cf456bb29057f4ad62df3c', '贾邕', '贾邕，生卒年不详，天宝九年登进士第，曾在濮阳师从萧颖士。《全唐诗》存诗一首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (42, '92a375712cfa4e26b694b2577952a66e', '刘舟', '刘舟，天宝中登进士第，师从萧颖士，《全唐诗》诗一首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (43, '92f3d5e0c3ed4abaa48cc65c05a64e5e', '长孙铸', '长孙铸，天宝十二年登进士第，师从萧颖士，《全唐诗》存诗一首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (44, '3015db337e2843a781cb3efd9c5a9b5e', '元晟', '元晟 ，河南洛阳人，玄宗朝师从萧颖士，后隐居杭州潜山，《全唐诗》存诗1首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (45, '1ac70d79a784402c89f6be72b8eaa688', '刘太冲', '刘太冲，郡望彭城（今徐州），宣州（今属安徽）人。少时师事萧颖士，有《送萧颖士赴东府得路字》一首相赠。天宝十二载(753)登进士第。颜真卿仕平原太守时，曾为其从事。《全唐诗》录存其诗一首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (46, '3a535b1db4344746b084f821dba02c4e', '姚发', '姚发，天宝十二年登进士第，师从萧颖士，《全唐诗》存\n诗一首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (47, 'c0767425578c4a7d8444f5dd3a4b25db', '郑愕', '郑愕，天宝十二年登进士第。与岑参友善，《高冠谷口招郑鄂》一首可证。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (48, '820e0cb500eb42118685acc472506af4', '殷少野', '殷少野，天宝十二（一作六）年登进士第。师从萧颖士，《全唐诗》存\n诗一首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (49, '3a8bd44f0a62490da6fd27bb53ba6823', '邬载', '邬载，天宝十二年登进士第。师从萧颖士，《全唐诗》存\n诗一首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (50, 'c42b6dfbba9344bf84ce88eda8852036', '钱起', '钱起（722？—780年），字仲文，吴兴人，唐代诗人。早年科举不利，唐天宝十年登第。初为秘书省校书郎、蓝田县尉，后任司勋员外郎、考功郎中、翰林学士等。曾任考功郎中，故世称“钱考功”。钱起工诗，被誉为“大历十才子之冠”。有《钱考功集》传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (51, '1ece0d6a0998494984dc4e25df63d691', '郎士元', '郎士元（727—780），字君胄，中山（今河北定州）人。天宝十五载(756)登进士第。历任拾遗、补阙、校书等职，官至郢州刺史。与钱起齐名，世称“钱郎”。时有“前有沈宋，后有钱郎”之说。有《郎士元集》2卷，集中多投赠送别之作。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (52, '12af5b41cee644389bfa7ecb3dbe9c77', '戴叔伦', '戴叔伦(732-789),字幼公，也作次公，金坛（今江苏）人。年轻时师事萧颖士。曾任新城令、东阳令、抚州刺史、容管经略使。其诗多表现隐逸生活和闲适情调，少数篇章也反映了人民生活的艰苦。诗的各种体裁皆有涉猎，存诗二卷，', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (53, '8de3d32371d142b2ae27725789249f8c', '李益', '李益(约750—约830)， 字君虞，祖籍凉州姑臧（今甘肃武威市凉州区），后迁河南郑州。大历四年(769)进士，初任郑县尉，久不得升迁，建中四年(783)登书判拔萃科。因仕途失意，后弃官在燕赵一带漫游。以边塞诗作出名，尤擅七言绝句。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (54, '298fbf2919934434961f00886d1bfc32', '李端', '李端（737—784）字正已。大历十才子之一。年少尝隐居庐山，师事诗僧皎然。大历五年进士。曾任秘书省校书郎、杭州司马。其诗多为应酬之作，多表现消极避世思想一些写闺情的诗也清婉可诵。今存《李端诗集》三卷', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (55, '80e67a7f345f4e1291ee09612dcb525e', '刘商', '刘商（766-779），字子夏，徐州彭城县人。大历年间进士。能文善画，诗以乐府体裁见长。官至汴州观察判官。其作。《唐才子传》称其《琴曲歌辞·胡笳十八拍》为“拟蔡琰《胡笳曲》，脍炙当时”。有集二十卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (56, 'f5786fa207264e34b61077c472c9a1b1', '刘复', '刘复，生卒年里贯均未详。登大历进士第，贞元中为东台侍御，后官至水部员外郎。《全唐诗》存诗十六首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (57, '3467f452ce2a488882e1ef3759a76a93', '武元衡', '武元衡（758-815）字伯苍，河南缑氏人。建中四年登第，任监察御史，后改华原县令。又授比部员外郎。俄擢升御史中丞。历任户部侍郎，门下侍郎平章事，官至剑南节度使。著有《临淮集》十卷传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (58, '18ab5dcf5e6c4533b9fb35813dd3caf6', '柳宗元', '柳宗元（773-819），字子厚，河东（今山西省运城县解州镇）人。唐顺宗时，任礼部员外郎。永贞革新失败后，被贬为永州司马，后迁为柳州刺史。他在散文和诗歌的创作方面，有着很高造诣。其诗俊雅峭拔。有诗集《河东先生集》传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (59, '9b4639bc141a4987a03eaf9ca29772e4', '刘禹锡', '刘禹锡（772－842）字梦得，洛阳人。贞元九年进士及第，初任记室，后历任朗州司马、主客郎中、礼部郎中、苏州刺史等职。晚年任太子宾客，世称“刘宾客”。刘诗精炼含蓄，语言清新，善于表达对人生与历史的深刻理解。为白居易所推崇，誉为“诗豪”。存世有《刘宾客集》。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (60, '2ba45b108f064e0da7d0cec260de645d', '张籍', '张籍（约766年—约830年），字文昌，唐代诗人，和州乌江（今安徽和县乌江镇）人。长庆元年，受韩愈荐为国子博士，迁水部员外郎，世称“张水部”、“张司业”。其乐府诗与王建齐名，并称“张王乐府”。作品有明嘉靖万历间刻本《唐张司业诗集》8卷，共收诗450多首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (61, '8b75d212986d49e1a54016d65f3b9944', '元稹', '元稹（779年—831年）字微之，别字威明，河南府东都洛阳（今河南洛阳）人，唐朝著名诗人、文学家，少时即有才名，与白居易同科及第，并结为终生诗友，二人共同倡导新乐府运动，世称“元白”，其诗作被称为“元和体”。元稹的创作广泛，其中以诗成就最大。现存诗八百三十余首，收录诗赋、诏册、铭谏、论议等共100卷，留世有《元氏长庆集》。另外元稹还积极创作唐传奇。代表作有传奇《莺莺传》。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (62, 'c09b91cc6e614d819d68dd9bc173f934', '周贺', '周贺，字南卿，《全唐诗》作南乡。东洛人。初为浮屠，名清塞。后因杭州太守姚合爱其诗，加以冠巾，遂改名贺。诗一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (63, '6927d274aab741f68c06d9cb1e767044', '喻坦之', '喻坦之，晚唐诗人，名列“咸通十哲”。唐懿宗咸通年间屡试不中，后久居长安，与建州刺史李频为友，今存诗十八首。事迹见于《唐才子传》。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (64, '02255f5fe01646eba87836c33a380515', '罗隐', '罗隐（833年－909年），字昭谏，杭州新城（今浙江省杭州市富阳区新登镇）人，屡试不第。55岁时归乡依吴越王钱镠，历任钱塘令、司勋郎中、给事中等职。著有《谗书》及《太平两同书》。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (65, '301292eab33e4579890cdbe2d759bead', '高蟾', '高蟾，生卒年不详。郡望渤海(今河北沧州一带)人。僖宗乾符三年(876)登进士第，官至御史中丞。好诗，孙光宪谓其“诗思虽清，务为奇险，意疏理寡”辛文房则谓其诗“气势雄伟，态度谐远，如狂风猛雨之来，物物竦动，深造理窟”', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (66, '96b5905355744c0c99b2e0c28c416770', '郑谷', '郑谷（约851年～约910年），唐朝末期著名诗人。字守愚，汉族，江西宜春市袁州区人。僖宗时进士，官至都官郎中，人称郑都官。又以《鹧鸪诗》得名，人称郑鹧鸪。其诗多写景咏物之作，表现士大夫的闲情逸致。曾与许棠、张乔等唱和往还，号“芳林十哲”。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (67, '43e330f10d5a435e9bdeebaeccad069d', '黄滔', '黄滔（公元840-911年）字文江，莆田人。昭宗乾宁二年，擢进士第。历任监察御史里行，充威武军节度推官。《全唐诗》收录其诗作一百多首。他是莆田早期的文学家，人称“闽中文章初祖”，曾辑《泉山秀句集》30卷，是第一部闽人诗歌总集。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (68, '12cdf852ee934542a74292586b160062', '贾岛', '贾岛（779-843），字阆仙，范阳（今河北省涿县）人。早年为僧，后还俗。屡试不第。唐文宗时任职主簿小官。他惟喜作诗苦吟，诗风清淡朴素。贾岛与孟郊并称“郊寒岛瘦”。一生不喜与常人往来，《唐才子传》称他“所交悉尘外之士”。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (69, '5dc1a42e5c0e40da8c5febe05d3f63a4', '喻凫', '喻凫，字坦之，号均羽，开成五年，进士及第，初任乌程县令。喜作诗，一生创作颇丰，曾与唐代名诗人李商隐、方干、可等唱和。《全唐诗》收录其诗一卷又一首，共计65首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (70, '431eb751684e420dae8f092766443245', '方干', '方干（836—903）字雄飞，号玄英，睦州青溪（今浙江淳安）人。唐宪宗元和三年举进士。方干擅长律诗，清润小巧，且多警句。其诗题材多样，或同情人民疾苦；或感怀求名未遂。方干逝世后，其门人搜集方干遗诗370余篇，编成《方干诗集》传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (71, '813fdbb957614b7689d6f54068d9ebab', '唐彦谦', '唐彦谦（？～893）字茂业，号鹿门先生，并州晋阳（今山西省太原市）人。生年不详。咸通二年进士，累迁节度副使，晋、绛二州刺史。晚年隐居鹿门山，专事著述。着有《鹿门集》3卷，附拾遗1卷，续补遗1卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (72, '8a69b5fd27af40b498e176e28facbcc4', '韩愈', '韩愈（768-824）字退之，南阳人。贞元八年，擢进士第。才高，好直言，累被黜贬。初为监察御史，旋贬阳山令，又改比部郎中、史馆修撰，转考功、知制诰，进中书舍人，又改庶子。因谏迎佛骨一事，贬谪为潮州刺史，移袁州。官终兵部侍郎。其诗豪放，有古人风范。有诗集四十卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (73, 'f1d98967f3034c24a9cf9370a4181172', '温庭筠', '温庭筠（812—870）原名岐，字飞卿，太原人。年少任才，好讥讽权贵，以致一生宦途失意，官仅国子助教。通音律，擅作词。词作多属意女子闺阁绣户。开“花间词”派香艳之风。有《温庭筠诗集》，《金奁集》。存词70余首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (74, '1a4854c5055c4f4c9f92a6a6ea015b73', '韩琮', '韩琮,约公元八三五年前后在世，字成封，生卒年均不详。长庆四年登进士第。初任陈许节度判官，后历任中书舍人。官至湖南观察使。琮著有诗集一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (75, '966e0c0b066e43a58e20c01e39da8570', '陈羽', '陈羽，江东人。约唐宪宗元和初前后在世。贞元八年登进士第，历任官乐宫尉佐，官至东宫卫佐。喜作诗，与上人灵一交游，唱答颇多。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (76, '399eaaf505de4d6e95a1b450880196e1', '韦蟾', '韦蟾（？-873）字隐珪，下杜（今陕西省西安市东南）人。大中七年登进士及第，历翰林学士、中书舍人、刑部侍郎。官终尚书左丞。韦蟾在襄阳与徐商等唱和诗编为《汉上题襟集》，已佚。《全唐诗》今存十首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (77, '2b3eb43133364b3a99ddafb83daef589', '李约', '李约，字在博，一作存博，自号为“萧斋”。宋州宋城人，唐朝宗室之后，为郑王元懿玄孙，汧公李勉之子。李约历任兵部员外郎，后弃官终隐。李约着有《东杓引谱》一卷传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (78, 'e4a2e0daf9b242fc8eba47a94d96e56f', '李贺', '李贺（约公元791年-约817年），\n字长吉，河南福昌（今河南洛阳宜阳县）人，后世称李昌谷，为唐高祖李渊的叔父李亮（大郑王）后裔。少有诗名，受谤毁不得登第，后受举荐任九品奉礼郎。作诗颇用力，其诗以想象奇特，喜用色彩字著称，后世称其诗歌为长吉体。着有《昌谷集》传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (79, '1473fff842c4415e9d9420c68a8650bd', '杜牧', '杜牧（803年－约852年），字牧之，号樊川居士，京兆万年（今陕西西安）人。唐文宗大和二年进士。初授弘文馆校书郎，历任江西观察使幕，睦州刺史等职。晚年因隐居长安樊川别墅，故后世称“杜樊川”。其诗歌以七言绝句为最，风格英发俊爽，在晚唐成就颇高。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (80, '7da5c151f40c4782a2bff7863ab5c27f', '孟迟', '孟迟（约公元859年前后在世）\n字迟之，平昌人。约唐宣宗大中末前后在世。有诗名，尤工绝句。会昌五年（公元八四五年）同举进士第。与顾非熊甚相得，于亦与杜牧友善，杜牧有诗赠之。著有诗集一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (81, 'baaa01b008c04c60b93b73b40ff2db45', '卢渥', '卢渥，字子章，生卒年不详。幽州范阳（今河北涿州）人。大中年间举进士第，历中书舍人、陕府观察使，终检校司徒。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (82, '0e0ef24107cb487da62ef49881840221', '高彦休', '高彦休(854～?)，号参寥子，籍贯不详。884年（僖宗中和四年），曾任淮南节度使高骈从事，官衔为摄盐铁巡官、朝议郎、守京兆府咸阳县尉、柱国', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (83, '8036e4a4b62c44dead62bfc550936176', '窦巩', '窦巩（约公元七六二年至八二一年间在世）字友封，唐朝京兆金城人。约自唐肃宗宝应元年至穆宗长庆元年间在世。元和二年进士。历任侍御史，司勋员外刑部郎中。后元稹移镇武昌，窦巩为副使。其诗见于窦氏联珠集。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (84, '8df6a9219273477c886f317aade9d9f4', '薛能', '薛能（817-880）字太拙，汾州人，登会昌六年进士第，任周至县尉。历任御史、刑部员外郎。官至工部尚书。薛能善诗，日赋一章，时人谓其“诗古赋纵横，令人畏后生”。多有寄送、赠答、酬唱之作。著有《薛能诗集》十卷，又《繁城集》一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (85, '11560f40ea7b4d1e8a22167695bac663', '王建', '王建（768年—835年），字仲初，颍川（今河南许昌）人，出身寒微，一生潦倒。曾一度从军，约46岁始入仕，曾任昭应县丞、太常寺丞等职。后出为陕州司马，世称王司马。与张籍友善，乐府与张齐名，世称张王乐府。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (86, 'b884f88fdcbb4894937acc4782383a8f', '顾况', '顾况，字逋翁，号华阳真逸（一说华阳真隐）。晚年自号悲翁，海盐人（今在浙江海宁境内）人。他一生仕途蹭蹬，曾任著作郎，因作诗嘲讽权贵，左贬饶州司户参军。晚年隐居茅山，有《华阳集》行世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (87, '5de9ff9c87da49599e4a17e592060514', '耿湋', '耿湋（约公元七六三年前后在世）字洪源，河东人。宝应二年进士登第。任周至县尉，历任右拾遗。官至大理司法。曾在江淮充扩图书。工诗，与钱起、卢纶、司空曙诸人齐名，号“大历十才子”。其诗不深琢削，而自有风格。有诗集三卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (88, 'c9c1219e650649629211fa01113b51e3', '孙叔向', '孙叔向，德宗时人，《全唐诗》存诗三首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (89, '86389374c0654108943b30a37cfebe90', '沈亚之', '沈亚之(781—832)，字下贤，吴兴（今浙江湖州）人。与李贺，杜牧、张祜、徐凝等人友善。落第后，李贺曾作诗送归。元和十年及第，初为泾原李汇辟掌书记，后为秘书省正字。历任栎阳尉，福建团练副使。官终郢州掾。亚之工诗善文，著有文集三卷，有《湘中怨解》、《异梦记》、《秦梦记》三文。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (90, '7c0d2dc98817431d8955d3171152e9d1', '赵嘏', '赵嘏（806-853）字承佑，楚州山阳（今江苏省淮安市淮安区）人。少时游历四方，大和七年落第，寓居长安多年，期间以诗文干谒王官大臣。会昌四年进士及第,初为渭南尉。少有诗名，因“残星几点雁横塞，长笛一声人倚楼”一句杜牧赞赏为“赵倚楼”。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (91, '85a0fba43ac74dccb841a529280b3dcd', '杜甫', '杜甫（712年—770年），字子美，自号少陵野老，唐代伟大的现实主义诗人，与李白合称“李杜”。原籍湖北襄阳，后徙河南巩县。三十五岁以后，先在长安应试，落第。天宝十四载（755年），安史之乱爆发，潼关失守，杜甫先后辗转多地。乾元二年（759年）杜甫弃官入川，大历五年（770年）冬病逝，时年五十九岁。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (92, '3b8bedf1c184414794b4cd8c4390a616', '郑嵎', '郑嵎（约公元八五九年前后在世）字宾光（一作“宾先”）。其里居及生卒年均不详，约唐宣宗大中末前后在世。大中五年（八五一年），他举进士及第。嵎著有《津阳门诗》一卷，《新唐书·艺文志》载称津阳为华清宫之外阙，嵎于逆旅中询父老以开、天旧事，为诗百韵，盛行于世', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (93, 'd2027320b2da4ed39c720d1749a298a2', '项斯', '项斯（约公元836年前后在世），字子迁，台州府乐安县（今浙江仙居）人。因受国子祭酒杨敬之的赏识而声名鹊起，诗达长安，于会昌四年擢进士第，官终丹徒尉。著有诗集一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (94, 'aaa7aff3236749fcb406df1076cf604d', '张乔', '张乔（生卒年不详），池州（今安徽省池州市贵池区）人，懿宗咸通中年进士，当时与许棠、郑谷、张宾等东南才子称“咸通十哲”。黄巢起义时，隐居九华山以终。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (95, '139e01e05e0a415c8a778058f894659e', '吴融', '吴融（850-903），字子华，越州山阴人。龙纪初，及进士第。初随韦昭度入蜀平反，任表掌书记，累迁侍御史，又召为左补阙，官终中书舍人。吴融善工诗，兼擅各体，又以七绝诗为最。其诗工整对仗，擅长援引经史。有《唐英集》三卷传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (96, 'dbb7eda933c7405f95089da72c127967', '罗邺', '罗邺，生于825年，卒年不详。字不详，余杭人，有“诗虎”之称。一生郁郁不得志，仅在光化中追赐进士及第，赠官补阙。罗邺尤长律诗，才智杰出，笔端超绝，气概非凡。以七言诗见长。其诗写身世之感，颇有理趣，著有诗集一卷传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (97, 'dc565f6f4e9a4f88a4398e85379cad89', '司马扎', '司马扎，生卒年里贯均未详，唐宣宗大中（847~858）前后在世。曾登进士第，素有诗名，与储嗣宗友善。其《宫怨》诗被认为“具盛唐风韵，晚唐绝句当推第一”传世有《司马先辈集》一卷。《全唐诗》编诗一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (98, 'b7f5184d074945ef92d113f88f506e7f', '窦牟', '窦牟，字贻周，贞元二年登进士第，任秘书省校书郎、东都留守巡官。历任河阳、昭义从事，检校水部郎中，再为留守判官。入为都官郎中，出为泽州刺史，官终国子祭酒。有诗集传世，已佚。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (99, 'ae2cd059f37b473db8df71836c90b363', '权德舆', '权德舆，字载之，天水略阳人。少以文章著称，因召为太常博士，改左补阙，兼制诰，升任中书舍人，历礼部侍郎。官终山南西道节度使。德舆心系经术，贯通古今。其文雅正，为贞元、元和间缙绅羽所称道。有文集五十卷传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (100, '2abd43d8915b46f3b852b3dae6985913', '李郢', '李郢,字楚望，长安人。大中十年及进士第，官终侍御史。喜作诗，其诗多写景状物，风格以老练沉郁为主。传世有诗集一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (101, '867fba77aba841fb805cfdb965342b1f', '崔颢', '崔颢（704—754），汴州（今河南开封市）人，唐代诗人。开元十一年及进士第，官至太仆寺丞。天宝中为尚书司勋员外郎。少年为诗，诗多浮艳，有轻薄之弊；晚年诗风大变。有《黄鹤楼》诗，颇令李白折服。《全唐诗》收录诗四十二首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (102, 'd0d8cced76ee41839e92c88b17fa263e', '韦济', '韦济（687-754）郑州阳武（今原阳县）人。韦思谦孙，宰相韦嗣立第三子，少以能文知名。开元初为鄄城令，三迁库部员外郎。又历任户部侍郎，河南尹，尚书左丞，冯翊太守等职。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (103, '0fd99bb1506643f19adf4f51226d627f', '李林甫', '李林甫（683年－753年1月3日），小字哥奴，祖籍陇西，唐朝宗室、宰相，唐高祖李渊堂弟长平肃王李叔良曾孙，画家李思训之侄。李林甫出身于唐朝宗室郇王房，早年历任千牛直长、太子中允、太子谕德、国子司业、御史中丞、刑部侍郎、吏部侍郎、黄门侍郎，后以礼部尚书之职拜相，加授同三品。开元二十四年（736年），李林甫接替张九龄，升任中书令（右相），后进封晋国公，又兼尚书左仆射。天宝十一年十一月（753年1月），李林甫病逝，追赠太尉、扬州大都督。后被杨国忠诬告谋反，遭削官改葬，抄没家产，子孙流放。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (104, '4bf1b91025b946a9a8b6349a6eb4e865', '皇甫曾', '皇甫曾（约公元七五六年前后在世）字孝常，生卒年均不详。皇甫冉同母弟。天宝十二载登进士第。历侍御史，后坐事贬谪舒州司马、阳翟令。诗名与兄相亚，高仲武称其诗“体制清洁，华不胜文”。著有集一卷传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (105, '95ff32e02c164d9f978f80a7446a25f7', '独孤及', '独孤及（725～777年），字至之，河南洛阳人。唐朝大臣、散文家。天宝十三年及第，补华阴县尉。唐代宗召为左拾遗，改太常博士。迁礼部员外郎，历任濠、舒二州刺史。官至常州刺史。少诵五经，后勤于经史。著有《毘陵集》三十卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (106, 'cfac7190ef2b40e196491850015eb30e', '林琨', '林琨,生卒年不详。三原(今属陜西)人。历任司驾员外、知制诰、膳部、司封、左司三司郎中、谏议大夫。德宗贞元前后在世。撰有《元和姓纂》10卷。《全唐诗》存诗1首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (107, 'e03fd183cf884d17b3f773bc6544cce7', '许棠', '许棠，字文化，宣州泾县人。“咸通十哲”之一。咸通十二年，登进士第，任泾县尉，又尝为江宁丞。后辞官，潦倒以终。许棠性格孤僻难与人合。因作洞庭诗著名，时人号曰“许洞庭”。有诗集一卷传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (108, 'ddd1461f53ac48a69221b0f83480a1b9', '杨凝', '杨凝,字懋功。虢州弘农人。生年不详，约卒于唐德宗贞元十八年。凝由协律郎三迁侍御史，为司封员外郎。为权幸所忌，徙吏部。稍迁右司郎中。官终兵部郎中。杨凝善文辞，与兄凭、弟凌皆有名，时号“三杨”。有诗集二十卷，今只存一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (109, 'a2ca6bc2491f4e319e1d3eef90d4b27d', '薛逢', '薛逢（生卒年不详），字陶臣，蒲州河东（今山西永济市）人。会昌元年（841）进士及。历侍御史、尚书郎。薛逢年少文词俊拔，志向远大。只因恃才傲物，忤逆权贵，故宦途失意。《全唐诗》收录其诗一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (110, '4a81945156ae4c52b832d534cb98a641', '张祜', '张祜（约785年—849年?），字承吉，唐代清河（今邢台市清河县）人。长庆中，令狐楚表荐之，不报。辟诸侯府，为元稹排挤，遂至淮南寓居。《全唐诗》收录其349首诗歌。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (111, '227c8c1cdbe042568bb673df0081fe1c', '王定保', '王定保（870-954），字翊圣，吴融之婿，南昌（今属江西）人。光化三年，（公元900年）举进士及第。唐末避中原之乱入湖南，不见遇马殷，遂入岭南，附于刘氏无北归意，任南汉宁远军节度使、中书侍郎同平章事等职。著有《唐摭言》十五卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (112, 'a831ff2da2214f559220b44552a5b2f8', '戴孚', '戴孚，唐朝谯郡（今安徽亳州）人，生平事略不见史传。据顾况所作《戴氏广异记序》（《文苑英华》卷七百三十七 ），知戴孚于唐肃宗至德二（757）年与顾况同登进士第，任校书郎，终于饶州录事参军，卒年五十七岁。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (113, '7e33d5f50e9b47bc90d4c121967b931e', '有的', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (114, '754955fa54294baab8c3a978e4aa2000', '裴铏', '裴铏，字、里、生卒年均不详，约唐懿宗咸通初前后在世。咸通中（公元八六七年左右）为静海军节度使高骈掌书记，加侍御史内供奉。唐僖宗乾符五年（公元八七八年）以御史大夫为成都节度副使，作题《文翁石室诗》。有《传奇》三卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (115, 'a72f49e23093430684b5da2472155427', '韦庄', '韦庄（约836年— 约910年），字端己。长安杜陵人。六十及第，历任校书郎，又升任左补阙。韦庄工诗，其诗多以感怀伤时为主题。又善作词，其词善用白描手法，词风清丽。《全唐诗》录其诗三百一十六首，后人又辑其词作为《浣花词》。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (116, '42f09555508a4e48bf93a8fe1f07c11a', '薛曜', '薛曜（？-704年），字异华，蒲州汾阴（今山西万荣县）人。唐朝大臣，中书令薛元超长子。世为儒雅之家，以文学知名，尚城阳公主。附会张易之，官至正谏大夫。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (117, '6d92a7b8113a4fec9836513f343f67f9', '李涉', '李涉（约806年前后在世），字不详，自号清溪子，洛阳（今河南洛阳）人。早岁客梁园。兵乱时，与弟李渤同隐庐山香炉峰下。后出山作幕僚。宪宗时，曾任太子通事舍人。旋贬为峡州（今湖北宜昌）司仓参军，蹭蹬十年，复归洛阳。官终国子博士，世称“李博士”。著有《李涉诗》一卷。存词六首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (118, '9b2d02d6e6184731a456e335bc2d2cb5', '于邺', '于邺,杜曲人，约公元八六七年前后在世。工五言诗，飘逸多感。仕途蹭蹬，屡考不第。携琴书游历四方，飘零各处，未能安居。后归嵩阳别墅，终老其地。邺著有诗集一卷传世，又著传奇文一篇。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (119, '45117ee306e44dd7ae6b7fbf754de661', '杨齐哲', '杨齐哲,长安四年官洛阳县尉。撰写《韦维善政论》。《全唐诗》存诗1首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (120, '6ea7f30fd59247bab5711907abf405a3', '李景亮', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (121, '4ad05e224c8c4fa4837509dcb57f215b', '韩偓', '韩偓，字致光，京兆万年人。龙纪元年进士及第，佐河中幕府，召拜左拾遗，累迁谏议大夫，历任翰林学士、中书舍人、兵部侍郎。之后两次被贬，卒于任上。其诗多写艳情，称为“香奁体”。著有《翰林集》一卷，《香奁集》三卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (122, 'ddee21c24a5049ef9c1a2d73fd04ca2a', '崔涂', '崔涂，字礼山，江南人。生卒年不详。光启四年登第。终生飘泊，漫游四方，故其诗多以飘泊生活为题材，其诗喜用古事，而不见拘束。情调苍凉。著有诗集一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (123, '0e3420a67c034e6ca8a1756f6453c445', '房由', '房由，河南洛阳人。天宝十三年（754年）进士。历任官祠部郎中、户部员外郎、度支郎中。与戴叔伦、郎士元友善。《全唐诗》存诗1首，误署作房白。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (124, '67cedee890c44a8bbfea27bf8950d4d9', '谷神子', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (125, '4e4fd699cb034c899a16d87379a1f2a1', '温畲', '温畲,生平事迹不明，元和十五年任左拾遗。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (126, '71c0164f870c45719af53b0d36f870a9', '祖咏', '祖咏(699～746),字、号均不详。洛阳人。少有文名，擅长诗歌创作。开元十二年(724)，进士及第，长期未授官。后入仕，又遭迁谪，仕途落拓，后归隐汝水一带。祖咏与王维友善，王维有诗赠之。祖咏有诗一卷传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (127, '1a4d5744d60042a597850c327867d65d', '虞世南', '虞世南（558年－638年7月11日），字伯施，越州余姚（今浙江省慈溪市观海卫镇鸣鹤场）人。历仕陈、隋二代，官拜秘书郎、起居舍人。入唐为秦王府参军、记室参军、弘文馆学士，与房玄龄等共掌文翰，为“十八学士”之一。贞观年间，历任著作郎、秘书少监、秘书监等职，贞观十二年（638年）去世，获赠礼部尚书，谥号“文懿” ，配葬昭陵。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (128, '4e218acb3abe437a8eb737232b1d3be3', '杨巨源', '杨巨源（755-？）字景山，后改名巨济。河中治所（今山西永济）人。贞元五年进士。初为张弘靖从事，历任秘书郎、擢太常博士、迁虞部员外郎。官终河中少尹。杨巨源与白居易、元稹、刘禹锡、王建等人交好，甚受尊重。有诗集五卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (129, 'cb622a754d66403f9d54f8fc5c274f34', '刘驾', '刘驾，生卒年不详，江东人。大中六年（852）进士。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (130, '69204ed1a34e47c7a4bf1d2303480979', '刘沧', '刘沧（约公元八六七年前后在世），字蕴灵，汶阳（今山东宁阳）人。生卒年均不详，约唐懿宗咸通中前后在世。公元854年（大中八年），刘沧与李频同榜登进士第。调华原尉，迁龙门令。著有诗集一卷传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (131, '61473ae951934203b146fb4149d4e42e', '麹信陵', '麹信陵，贞元元年进士第，为舒州望江令。为人善政工诗，著有诗集一卷，《全唐诗》存诗六首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (132, 'f54a8c9e46e54288b85cc9632650a790', '雍陶', '雍陶出身贫寒。文宗大和八年（834年）进士，曾任侍御史。大中六年（852年），授国子毛诗博士。大中八年（854年），出任简州（今四川简阳县）刺史，世称雍简州。工诗。与王建、贾岛、姚合、章孝标等交往唱和。其诗多旅游题咏、送别寄赠之作，擅长律诗和七绝。《全唐诗》录存其诗一百三十一首，编为一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (133, '8e6461bd75614fafbfc0646cd614b6f8', '豆卢复', '豆卢复，唐玄宗开元末年为崇玄生，屡试不第。《全唐诗》存诗2首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (134, '1f5ab994c34948e18f08e08d4d7af6f2', '李嘉祐', '李嘉祐，字从一，生卒年俱不可考，赵州（今河北省赵县）人。天宝七年（748）进士，授秘书正字。以罪谪鄱阳，量移江阴令。上元中，出为台州刺史。大历中，又为袁州刺史。与李白、刘长卿、钱起、皇甫曾和皎然相识。善为诗，绮丽婉靡。《新唐书·艺文志》著录《李嘉祐诗》一卷，《全唐诗》编为三卷', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (135, '28f38a8d77564c3eba8477087cf2aa0f', '皇甫冉', '皇甫冉（约公元717年—约公元771年），字茂政，男，汉族文人。祖籍甘肃泾州，出生于润州丹阳（今江苏镇江）。天宝十五年进士。曾官无锡尉，大历初入河南节度使王缙幕，终左拾遗、右补阙。其诗清新飘逸，多飘泊之感。流传诗作共有241首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (136, 'c5b198678f56496cab1cf762edb5c477', '张继', '张继（生卒年不详）字懿孙，湖北襄州（今湖北襄阳）人。生平事迹不详，约公元753年前后在世。天宝十二年进士。《全唐诗》存诗50余首，其中最为著名的是《枫桥夜泊》。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (137, '20019c9d560345878129995b0d661407', '姚合', '姚合（约779～约855），中国唐代著名诗人。陕州（今河南陕县）人，祖籍吴兴（今浙江省湖州市）。元和十一年（816）进士，授武功主簿。历任监察御史，金、杭二州刺史、刑部郎中、给事中等职，终秘书少监。世称姚武功，其诗派称“武功体”。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (138, '6a647a2c71d34d709739e6132d8b28d7', '司空图', '司空图（837～908），河中虞乡（今山西永济）人。晚唐诗人、诗论家。字表圣，自号知非子，又号耐辱居士。唐懿宗咸通十年（869年）应试，擢进士上第，懿宗朝时曾被召为殿中侍御史。后梁开平二年（908年），唐哀帝被弑，他绝食而死，终年七十二岁。司空图成就主要在诗论，《二十四诗品》为不朽之作。《全唐诗》收诗三卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (139, '0f2a69158e8e4969864598f618a40503', '徐夤', '字昭梦，莆田即今福建莆田市人。博学多才，尤擅作赋。为唐末至五代间较著名的文学家。文集有《徐正字诗赋》二卷，收赋八首，收诗三百六十八首。著作有《徐正字诗赋》二卷等。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (140, '2fc67555b7014f35995f29af4dfab490', '李显', '唐中宗李显（656年—710年），原名李哲，陇西成纪人，656年出生于长安。唐朝第四位皇帝，唐高宗李治第七子，武则天第三子。683年至684年、705年至710年两度在位。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (141, '375c9b9d013942f3a57cbce8b69f9456', '李植', '生卒年不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (142, '8963a3dec2cd434a8cee0dbf80f0ba07', '李贻孙', '德宗时人，刺史。工书法，贞元中尝书唐酆都宫阴真人祠刻，诗三章。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (143, 'b66617b2c9b84076956be3967e5ca8c5', '严□', '生卒年不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (144, '69393ee2f12d411980f5b75427e42600', '李频', '李频（818—876），字德新，唐寿昌长汀源（今浙江建德李家镇）人，葬于永乐（今李家），唐代后期诗人。幼读诗书，博览强记，领悟颇多。著有《梨岳集》一卷，附录一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (145, '642e3f94e8624815997caadb536f2586', '裴说', '裴说，桂州（今广西桂林）人。唐哀帝天祐三年（906年）状元及第。与曹松、王贞白、诗僧贯休、处默为友。后梁时，累迁补阙，终礼部员外郎。与弟裴谐皆有诗名，诗风近贾岛，苦吟有奇思。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (146, '5627f1899702468291a473cea586833c', '窦群', '窦群（763—814），字丹列。兄弟皆擢进士第，独群以处士客于毗陵。韦夏卿荐之，为左拾遗，转膳部员外郎，兼侍御史，知杂事。出为唐州刺史，武元衡、李吉甫共引之，召拜吏部郎中。元衡辅政，复荐为中丞。后出为湖南观察使，改黔中，坐事，贬开州刺史。稍迁容管经略使，召还卒。诗二十三首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (147, 'fc677258af254702a8a36d8597d7abed', '皮日休', '皮日休（约公元838年—约公元883年），晚唐文学家。字袭美，一字逸少，复州竟陵（今湖北天门）人。曾居住在鹿门山，道号鹿门子，又号间气布衣、醉吟先生、醉士等。咸通八年（867）进士及第，在唐时历任苏州军事判官（《吴越备史》）、著作佐郎、太常博士、毗陵副使、任翰林学士，起义失败后不知所踪。与陆龟蒙齐名，世称\"皮陆\"。其诗文兼有奇朴二态，且多为同情民间疾苦之作，被鲁迅赞誉为唐末“一塌糊涂的泥塘里的光彩和锋芒”。《新唐书·艺文志》录有《皮日休集》、《皮子》、《皮氏鹿门家钞》多部，对于社会民生有深刻的洞察和思考。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (148, 'd8f0379c15224ff0a298594b5400d177', '李洞', '李洞，字才江，唐京兆（今陕西西安）人。平生累式不第，遂失意游蜀而卒。传世有《李洞诗集》。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (149, '5d2876084c9344ed8b3ec819468c2dcf', '陆龟蒙', '陆龟蒙（？～公元881年），字鲁望，号天随子、江湖散人、甫里先生，长洲（今苏州）人。曾任湖州、苏州刺史幕僚，后隐居松江甫里（今甪直镇），编著有《甫里先生文集》陆龟蒙与皮日休交友，世称“皮陆”，诗以写景咏物为多。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (150, '922f13f5af4c4914bf86f17a32137cd6', '汪遵', '汪遵，宣州泾县人，生卒年均不详。咸通七年（公元八六六年）擢进士第。遵诗有集传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (151, '6f8437dc15c54322aef9281645965679', '贾登', '贾登，开元时官中书舍人。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (152, '193d8f8fe59f42bf9bb7eb67e14e461b', '谢观', '谢观（793-865），字梦锡，安徽省寿春人。其先陈郡阳夏人，晋太傅谢安之十八世孙，开成二年的进士，释褐曹州冤句县尉，后任慈州刺史。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (153, 'f4f203c253d1431ba1062f30747b3557', '阎伯玙', '阎伯玙，唐代进士，上元时官洪州都督，开元时谪华州郑县尉，天宝中迁吏部郎中，出为袁州刺史，历抚州。徵拜户部侍郎，未至卒。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (154, 'a9c3f5ea442c4f4297d29dd6cb19de08', '李隆基', '唐玄宗李隆基（685年9月8日—762年5月3日）先天元年（712年）至天宝十五载（756年）在位，因安史之乱退位为太上皇，是唐朝在位最长的皇帝，亦是唐朝极盛时期的皇帝。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (155, '7c3fd41275f947ef8e1de52d59e19789', '颜真卿', '颜真卿（709年－784年8月23日），字清臣，小名羡门子，别号应方，京兆万年（今陕西西安）人。颜真卿书法精妙，擅长行、楷。初学褚遂良，后师从张旭，得其笔法。其正楷端庄雄伟，行书气势遒劲，创“颜体”楷书，对后世影响很大。与赵孟頫、柳公权、欧阳询并称为“楷书四大家”。又与柳公权并称“颜柳”，被称为“颜筋柳骨”。又善诗文，有《韵海镜源》、《礼乐集》、《吴兴集》、《庐陵集》、《临川集》，均佚。宋人辑有《颜鲁公集》。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (156, 'b13776ada5374d399387fb9fe69bbb5f', '王涯', '王涯（764～835）字广津，山西太原人。唐代大臣，诗人。约生于唐代宗广德二年，卒于文宗太和九年。博学工文。元和时，累官中书侍郎，同中书门下平章事。穆宗立，出为剑南、东川节度使，《全唐诗》有诗一卷。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (157, '4a44eb5e886a43978365aa99cf893073', '张义方', '张义方，南唐时人。累任兵部侍郎、左散骑常侍、勤政殿学士。《全唐诗》存诗1首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (158, 'ce2de92684c640468267ee2a87033858', '薛调', '薛调，（约公元829——872）唐代河中宝鼎（今山西万荣）人。宣宗大中朝进士及第，宪宗时曾任户部员外郎加驾部郎中、翰林学士承旨等职。咸通十三年暴卒，赠户部侍郎。有人认为被鸩杀（毒死）。薛调是唐代传奇小说作家，著有传奇小说《无双传》等。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (159, 'f55f877d8c7f49e8a42d0b43e733d944', '不详', '无', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (160, 'f21ec00f19164a43abffa9b7a8c02ddc', '未详', '无', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (161, 'c4afab1c4f164606a19382df3d13e265', '郄昂', '郄昂，即郄纯，因避唐文宗李昂讳而改。举进士，历任员外、郎中、谏议大夫、中书舍人。有文集六十卷传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (162, 'cb566979414f4227a9e8e581b5e561b1', '徐寅', '徐寅，生卒年不详，莆田（今福建莆田）人。大顺三年进士及第。仕途坎坷蹭蹬，须鬓交白时始获官秘书省正字。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (163, '65f7d870a9554147893532b052c13ab3', '乔潭', '乔潭，字德源。梁（今河南开封）人。天宝十三年进士，任陆浑尉。以辞赋文章著名。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (164, '678ecf7d243949fea1af3f4e53544ae1', '皇甫浞', '皇甫浞，字持正，新安人。元和中进士。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (165, 'cbd4363513dd43f786622fe34d167d30', '韩休', '韩休，京兆长安人。举贤良，历任礼部侍郎、拜黄门侍郎。终太子侍郎。《全唐诗》有诗三首。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (166, 'cf4e0129048a44f491192c3f1b1535b6', '陈鸿', '陈鸿，字大亮，生卒年不详。唐贞元二十一年(805)进士﹐登太常第。曾任太常博士﹑虞部员外郎﹑主客郎中等职。《全唐文》存其文3篇。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (167, '53306516606d4d10b0636ebc331925d5', '李程', '李程，生卒年不详，字表臣。贞元十二年进士，元和时为知制诰，拜礼部侍郎。唐代著名辞赋名家，著有《日五色赋》等二十篇赋传世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (168, 'a296f78d2be443618d2f0f1c53226406', '李蠙', '李蠙字懿川,宣宗朝歷官仓部、考功员外,咸通元年迁谏议大夫。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (169, '3ccce95880bb45cdb9c5bdec5ff58913', '李憺', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (170, '29947aea13604c0c88bc2077881d0013', '苏颎', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (171, '085a95d0641f42e896d9d04d02f6fcbd', '张惟一', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (172, '1fc98a5072704cdabbf3428e69bdc073', '王宥', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (173, '29b55a45fa0949689475692c7493d70b', '丘据', '兵部员外郎、谏议大夫丘据 丘 据 丘行本五代孙，司马、库部郎中，兼殿中侍御史中丞、兵部员外郎、谏议大夫。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (174, 'f6db59414e414639a9b3128ca6bd977e', '韦□', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (175, '7c65b7b811a04e1c9a9c5ca28a14449d', '李仲昌', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (176, '4522a31ea7874c29af9cac44d867a55f', '第五琦', '第五琦（712年-782年），字禹珪，京兆长安（今陕西省西安市）人，唐朝中期政治家、理财家。开元十四年（726年），明经科入仕，历任御史中丞、御史大夫、京兆尹、太子宾客、户部侍郎判度支、同中书门下平章事、度支使、铸钱使和盐铁使等官职，封为扶风郡公。建中三年（782年）八月去世，享年七十一岁，追赠太子少保。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (177, 'e1730f0cdb8e416cb261753a2fdc0e84', '焦□', '生卒年不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (178, '6793e78dbd2445a0a2fd665eaa09e54b', '苏敦', '生卒年不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (179, 'd5a63b1dfc504a959554a27bf6938f07', '裴士淹', '开元中任给事中，天宝末年为京兆尹，宝应二年（763）为左散骑常侍、绛郡开国公，永泰二年（766）任检校礼部尚书、礼仪使。大历五年（770）贬为虔州刺史。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (180, '83775f2fcebd4293bdc578362bd84f21', '元澄', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (181, '3ec641ba3b9d41cca58ddfa00f64fc60', '上官沼', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (182, '5c85e221c7914e9a8b49abcb9281281f', '崔汉衡', '崔汉衡（?～795），博陵茌平人。始为沂州费县令，滑州节度使令狐章奏署为掌书记，累迁殿中侍御史，贞元元年(785)，为幽州宣慰使。所至皆称职。贞元十一年卒，赠尚书左仆射。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (183, '87a902012fec4db886c869dd1d701fd8', '裴潾', '裴潾，生年不详，卒于唐文宗开成三年(838年)，河东闻喜(今山西闻喜县)人，以门荫入仕，唐敬宗宝历初年，裴潾曾任给事中，文宗大和四年(830年)调任汝州(河南省临汝县)刺史，兼御史中丞，大和七年（833年)升任左散骑常侍，充任集贤殿学士。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (184, '7700ecb092a14432bf92316432994e2d', '裴颖', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (185, 'dc4a44e024b74e9fbb01038a9c87a732', '方参', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (186, 'fe92f72cd60a4ca2b3d7b1e4b45fdab3', '韦公式', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (187, 'd7a566dd19e74b3bafd7304fb3647ffa', '李虞仲', '李虞仲（公元772年——836年），字见之（一作建之），赵州（旧唐书作赵郡）人，李端之子。生于唐代宗大历七年，卒于文宗开成元年，年六十五岁。工诗，第进士及宏辞。累迁太常博士。宝历初，（公元825年）以兵部郎中，知制诰，进中书舍人。出为华州刺史。历吏部侍郎。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (188, '2c5730eddbd44ccbae84baf9e6d50a02', '宝存辞', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (189, '67bb602b156840cea0382d5ecc3e2b77', '□庚', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (190, 'c892aa3daf014616a501d429f1124220', '陈商', '陈商（?--855，）字述圣，宣州当涂(今安徽当涂县)人，陈朝皇室后裔，元和九年(公元814年)进士，曾任谏议大夫、侍郎、秘书监等职，封许昌县男，好古文，与李贺有交游。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (191, '8a303301c24f497eaa3ff7c2ad0c98c5', '常侍□□', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (192, 'a87fadc0e7a746ddacb7c14dce2c1d59', '崔郇', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (193, '1fe1fafa4f244026b4e589896ac3b894', '崔慎由', '崔慎由，宣宗时工部侍郎、同中书门下平章事。字敬止，崔从之子，清河东武城（今河北故城县）人，出身清河崔氏之南祖崔氏乌水房，唐宣宗时宰相。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (194, 'ef250aa2362e47f089ca8623499ac0bd', '李□方', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (195, '281eb968343549a3b4f3e3bf1d4e56d5', '尉旻', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (196, 'e7f249d42085463895acfcd74b13555b', '王璠', '王璠   ，字鲁玉。父础，进士，文辞知名。元和五年，擢进士第，登宏辞科。风仪修饰，操履甚坚，累辟诸侯府。元和中，入朝为监察御史，再迁起居舍人，副郑覃宣慰于镇州。长庆中，累历员外郎。十四年，以职方郎中知制诰。宝历元年二月，转御史中丞。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (197, 'cbca645b260944e195e2f6ce1a1b2610', '李□', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (198, '559764b8c4d2422d85f8f8a6b19449c1', '许环', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (199, '6089faafa5434968ae21ce52dcd6cafc', '韦公右', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (200, '7e5587429682445480a062ce1ee9d534', '于逖', '于逖（约公元七四九年前后在世），字、里、生卒年均不详，约唐玄宗天宝中前后在世。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (201, '2f9f40fa2d0a4d8ead3a2a070d509bc9', '徐铉', '徐铉（公元916年—公元991年），字鼎臣。五代至北宋初年诗人，初仕杨吴，为校书郎。又仕南唐三主，历官知制诰、中书舍人、翰林学士、吏部尚书。 [5]  后随后主李煜归宋，官至散骑常侍，世称“徐骑省”。淳化初因事贬静难军行军司马。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (202, '5d02b40cbbf2476a8c50d343bbcf8d34', '薛用弱', '薛用弱，字中胜，唐河东（今山西永济一带）人，生卒年不详，薛用弱长庆时为光州刺史，大和初自仪曹郎出守弋阳', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (203, '1ff46ce721404c29bb35e989417f4308', '范资', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (204, '7bd8a8d6e6d64c199392a7ab39a3b380', '杜光庭', '杜光庭（850—933），字圣宾，号东瀛子，处州缙云（今属浙江）人。唐末五代时期高道。唐懿宗时，考进士未中，后到天台山入道。僖宗时，如为供奉麟德殿文章应制。随僖宗入蜀，后来追随前蜀王建，官至户部侍郎。赐号传真天师。晚年辞官隐居四川青城山。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (205, 'a7ac4c3f37574d15938b5cf404f994bb', '卢肇', '卢肇（818—882）字子发，江西新余市分宜县人，唐会昌三年（公元843年）状元，先后在歙州、宣州、池州、吉州做过刺史。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (206, 'd3b2e40c5f0b4902ad6eafbca9751bab', '牛肃', '牛肃，约唐德宗贞元末前后在世。事迹不详。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (207, '5fc50cef6327435f8c8bc3909f26555c', '郑繇', '生平不详', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (208, '2c280d02bf2a4672b06aeb6507458d6d', '高适', '高适（704—765年），字达夫，一字仲武，渤海蓨（今河北景县） 人，后迁居宋州宋城（今河南商丘睢阳）。安东都护高侃之孙，唐代大臣、诗人。曾任刑部侍郎、散骑常侍，封渤海县侯，世称高常侍。 于永泰元年正月病逝，卒赠礼部尚书，谥号忠。', 1574400380, 0, 0);
INSERT INTO `jbd_poet` VALUES (209, '7b0686ba0b0b44c6b7e1a44396a0ee39', '杜审言', '杜审言(约645-约708)，字必简，襄州襄阳人(今湖北襄阳)。唐高宗咸亨进士，唐中宗时，因与张易之兄弟交往，被流放峰州(今越南越池东南)。\"诗圣\"杜甫的祖父。与李峤、崔融、苏味道被称为\"文章四友\"，是唐代\"近体诗\"的奠基人之一。其五言律诗，格律谨严。后人辑有《杜审言诗集》。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (210, 'a9c1463da3cf4912b61965daa3fcec41', '孟浩然', '孟浩然(689-740)， 唐代襄阳(今湖北省襄阳县)人。他与王维交好，并称王、孟，唐代著名的山水田园诗人，世称“孟襄阳”。因他未曾入仕，又称“孟山人”。曾在太学赋诗，名动公卿，一座倾服，为之搁笔。开元二十五年（737）张九龄召致幕府，后隐居。有《孟浩然集》三卷传世。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (211, '36e44ea78a0d42e28832f4ee501e54d5', '', '韦应物（737—792），长安人。历任洛阳丞、京兆府功曹参军、鄂县令、滁州和江州刺史。官至苏州刺史。世称“韦苏州”。其诗风恬淡高远。今传有《韦江州集》十卷、《韦苏州诗集》两卷、《韦苏州集》十卷。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (212, '9a282c44665c42bbb11bfcca0b972844', '李德裕', '李德裕(787年-850年)，字文饶，赵郡赞皇(今河北赞皇)人，唐代政治家、文学家。牛李党争中李党领袖，中书侍郎李吉甫次子。他历仕宪宗、穆宗、敬宗、文宗四朝，一度入朝为相，但因党争倾轧，多次被排挤出京。李商隐在为《会昌一品集》作序时将其誉为\"万古良相\"。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (213, 'f7e38693080a4ca98c069decadbc5da4', '李咸用', '李咸用，唐，生卒年不详。族望陇西(今甘肃临洮)。习儒业，久不第，曾应辟为推官。因唐末乱离，仕途不达，遂寓居庐山等地。咸用工诗，尤擅乐府、律诗。所作多忧乱失意之词。为善写“征人凄苦之情”，有“国风之遗音，江左之异曲”(《唐李推官披沙集序》)。与修睦、来鹏等有交谊，时有唱酬。《全唐诗》存诗3卷。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (214, '889d4b97bf7b4efda9106e7c9561b5b4', '胡曾', '胡曾，唐代诗人，湖南邵阳人。生卒年、字号均不详。咸通中，举进士不第，滞留长安。咸通十二年(871)，路岩为剑南西川节度使，召为掌书记。乾符元年(874),复为剑南西川节度使高骈掌书记。乾符五年，高骈徙荆南节度使，又从赴荆南，后终老故乡。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (215, '39fb20b999a942148da7537ef6e8f044', '蔡希寂', '蔡希寂，字季深，润州丹阳人，蔡希周七弟。历官渭南尉、洛阳尉、金部郎中。善于书法，现存有其兄蔡希周的楷书墓志铭。诗五首，《同家兄题渭南王公别业》、《洛阳客舍逢祖咏留宴》、《登福先寺上方然公禅室》、《陕中作》、《赠张敬微》。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (216, '5bd697ee8f1740a094422e45a5aa6829', '李复言', '李复言，生卒年均不详。太和四年（830），游巴蜀，与进士沈田，会于蓬州。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (217, 'c4a060fca38a490fb432ff39958742b2', '司空曙', '司空曙，唐朝人(约公元七六六年前后在世)字文初，或作文明。广平府(今河北省永年县）人。大历年间登科进士，韦皋为剑南节度使，召致幕府。累官左拾遗，终水部郎中。家贫，性耿介，曾流寓长沙，迁谪江右。磊落有奇才，与李约为至交，大历十才子之一。他诗多幽凄情调，间写乱后的心情。常有好句。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (218, '659816defa0841098f5698a806a9dcda', '崔融', '崔融(653-706)，字安成。唐代齐州全节(今济南市章丘市)人。初应八科制举，皆及第，累补宫门丞、崇文馆学士。崔融为文华美，当时无出其上者。凡朝廷大手笔，多由皇帝手敕，付其完成。其《洛出宝图颂》、《则天哀册文》尤见工力。作《则天哀册文》时，苦思过甚，遂发病而卒。中宗以其有侍读之恩，追赠为卫州刺史，谥号\"文\"。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (219, 'd1533943c6ea41b08b941b51feb3dba6', '李绅', '李绅(772年-846年)，字公垂。祖籍亳州谯县(今安徽省亳州市谯城区)。唐朝宰相、诗人。二十七岁时中进士，补国子助教。后历任中书侍郎、尚书右仆射、淮南节度使等职。李绅与元稹、白居易交游甚密，为新乐府运动的参与者。著有《乐府新题》二十首，已佚。《全唐诗》存其诗四卷。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (220, '0f323b984f094bb098717c08cbf7f6dd', '于武陵', '于武陵，唐代诗人。其诗以写景送别为主，流传颇广。《全唐诗》中有藏诗一卷。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (221, '08de85f8bb0a44f4ae4433bfd34e8c50', '欧阳詹', '欧阳詹（公元755—800），字行周，福建晋江潘湖人，中唐诗人。有《欧阳行舟文集》10卷。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (222, 'cbaa307f55ec4bb2b9bc4abdffd99703', '王贞白', '王贞白（公元875—公元958年），字有道，号灵溪，信州永丰（今江西上饶）人。唐末五代十国著名诗人。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (223, 'bf9831220a094e9bb692fd1f6c015f45', '郑渥', '郑渥，生卒年不详，约为晚唐时人。有诗集一卷，《崇文总目》别集类著录，已佚。《全唐诗》存诗二首。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (224, 'deb1702f9a0d4e8d80e645aa08eadfb0', '任翻', '任翻，唐朝诗人。翻一作蕃，又作藩，江东（今江苏南浙江北一带）人。会昌间（公元814—846）在世。出身贫寒，初举进士，步行至京，落第而归。放浪江湖，以吟诗弹琴自娱。《全唐诗》录存其诗十八首，以《宿中子山禅寺》、《洛阳道》较著名。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (225, '22a588312d9c4f3e882ed5028422a1f7', '朱长文', '无考', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (226, 'edd021e03f154790855c9f080b5343cd', '孟云卿', '孟云卿，字升之，河南人，约生于公元725年。天宝间不第，代宗始为校书郎。孟云卿少学成名，禀通济之才，沦吞噬之俗，栖栖南北，苦无所遇。孟云卿身世造化于诗歌便是哀哭之音，多离别伤怀、自叹身世之作。《全唐诗》编孟云卿诗一卷，共计十七首。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (227, '3eb69920da404d1fa657e18a013c07c6', '羊士谔', '羊士谔（约672—819），泰安泰山人。贞元元年礼部侍郎鲍防下进士。顺宗时，累至宣歙巡官，为王叔文所恶，贬汀州宁化尉。元和初，宰相李吉甫知奖，擢为监察御史，掌制诰。士谔工诗，妙造梁《选》，作皆典重。与韩梓材同在越州，亦以文翰称。著集有《墨池编》、《晁公武郡斋读书志》。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (228, '0b0e987db30b47afbc1636b157bece94', '尚颜', '尚颜，俗姓薛，字茂圣，于“公元八七四年，至荆门城区唐安寺出家”。他主要活动在公元840年至920年，大约经历了唐宣宗、懿宗、僖宗、昭宗、哀帝、后梁太祖、后梁末帝诸多皇帝，享年90岁以上。尚颜长于五言律诗，有诗千首。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (229, '70d03313ddd64a17abb268aea360983d', '崔橹', '崔橹，荆南（今湖北荆州）人，晚唐生人，生卒年不详，宣宗大中年间登进士第。仕至棣州司马。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (230, '8ddf4e8f5d8f40f5a9c51c07780e8c88', '王勃', '王勃，字子安，绛州龙门人，素敦儒业，为四杰之首。六岁善辞章，九岁能正讹，十四岁时应幽素科及第，授职朝散郎。后被沛王（李贤）召为王府修撰，因不满于贵族中日渐盛行的斗鸡之风，拟作《斗鸡檄》被逐出王府。之后，王勃历时三年游览巴蜀，在此期间创作了大量诗文。归返长安后，求补得虢州参军。在任上，因私藏官奴二次被贬。其父原任雍州司功参军，也因此牵连而累被贬至交趾（今越南）。上元三年（公元676年），王勃前往交趾省父，不幸渡海溺水，惊悸而亡，年才二十八岁。盛行一时的《滕王阁序》当作于王勃此行经过南昌期间，构思无滞，援笔成篇，“腹稿”一词滥觞于此。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (231, '8240439983464686a1a3d3e740de6299', '皇甫湜', '皇甫湜(公元777-835年)，唐代散文家。引字持正，唐睦州新安(今浙江建德淳安)人。十多岁时就漫游各地，投梁肃，谒杜佑；后又结交顾况，师从韩愈，还求见江西观察使李巽，作书献文，希图荐举，未成。唐德宗贞元十八年(802)，湜在长安参加进士科考试，不第。他广为交游，与白居易、李翱、刘敦质等人往来。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (232, 'de6e9466ab6742a2b38ff042a2e48cac', '若耶溪女子', '生卒年不详。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (233, '0ed9ddf567464d9c80316456cde2c3aa', '张绮', '生卒年不详。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (234, '94847a2d8d6c47f98c48c6c77097e839', '刘谷同', '生卒年不详。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (235, '5b8acfd7e6c2494ebc71ad5d0fe7d6a6', '李缟', '生卒年不详。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (236, '32923deaa8fc4b3cb54d8c77dd8b2c29', '高衢', '生卒年不详。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (237, 'c772008f5c384a5daa65bdaa0c3e1013', '王涤', '生卒年不详。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (238, 'c63132d3c6124cbf9f118cad398e5c53', '王硕', '生卒年不详。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (239, '252659a71bda4ec68ef60712755577af', '李昌邺', '生卒年、籍贯皆不详。唐末进士。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (240, '8a848aedb77a4ba4892d8ec948fa7f90', '韦冰', '生卒年、籍贯皆不详。唐末进士。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (241, 'a7a730af05ca4432ba0517bc78ca0510', '王祝', '生卒年、籍贯皆不详。唐末进士。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (242, '0f5f6c5a462649e8a05fac4435dd074a', '陆直洞', '生卒年、籍贯皆不详。唐末进士。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (243, '620bff24f2a643febf17057eddbb80af', '张鷟', '张鷟，字文成，号浮休子，史称“青钱学士”；深州陆泽（今河北深县北）人。生卒年不详，大致在武则天到唐玄宗朝前期，以词章知名，下笔神', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (244, '83c23bb0279540159dfa5618d3a9ae98', '王维', '王维（701—761）字摩诘，号摩诘居士，河东蒲州（今山西运城）人。唐朝著名诗人、画家。王维参禅悟理，学庄信道，精通诗、书、画等。以诗名盛于开元、天宝间，尤长五言，多咏山水田园，与孟浩然合称“王孟”，有“诗佛”之称。存诗400余首，代表诗作有《相思》、《山居秋暝》等。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (245, 'c2f1d3952ae34fea9dd9c08a294d9331', '杜荀鹤', '杜荀鹤，字彦之，自称九华山叟。唐池州石（安徽石台）人，晚唐著名诗人。大顺元年及第。有《唐风集》（一称《杜荀鹤文集》）传世。今存五七言律诗及绝句326首。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (246, 'e07c34524fdf4151931f6f9db4847fb9', '释道世', '释道世，字玄恽，俗姓韩氏，京兆长安人。因避唐太宗（世民）之讳，以字行。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (247, 'd69ffe3fcdb0413591e0262aa54d955c', '李颀', '李颀(约690-约751)，字、号均不详，郡望赵郡(今河北赵县)，唐代诗人。开元二十三年中进士，曾任新乡县尉，后辞官归隐于颍阳之东川别业 。\n\n李颀擅长七言歌行，诗以边塞题材为主，风格豪放，慷慨悲凉，与王维、高适、王昌龄等人皆有唱和。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (248, 'f3e67897a639403cb5b2f05281684c78', '李翱', '李翱（772年—841年），字习之，唐陇西成纪（今甘肃秦安东）人，西凉李暠后裔，北魏李冲十世孙。贞元年间进士，历任国子博士、史馆修撰、考功员外郎、礼部郎中、中书舍人、桂州刺史、山南东道节度使等职。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (249, '592351addd7b4047b0ef7700c0f7ac97', '姚嵘', '姚嵘，生卒年不详,中晚唐间进士。事迹见《云溪友议》卷下。《全唐诗》存诗1首。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (250, 'ae1722e62e5f4582a8dcb692d4744ff5', '灵一', '灵一（约公元七六四年前后在世），姓吴氏，人称一公。童子出家。初隐麻源第三谷中，结茆读书。后居若耶溪云门寺，又曾居余杭宜丰寺。修禅之余，喜作诗歌。与皇甫冉兄弟、灵澈为诗友，唱和不绝。后终于岑山。有《文献通考》传于世。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (251, 'fb2f2b968d78490bbf478af47f890b2b', '清江', '大约公元775年前后在世。会稽人，唐代浙江诸暨若邪云门寺僧。擅诗文，与名僧清昼齐名，称会稽二清。有诗二十余首，编为一卷，收入《全唐诗》。', 1574483322, 0, 0);
INSERT INTO `jbd_poet` VALUES (252, '36b4a942f18a456eba49796021d11ad0', '李夔', '无考', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (1, '5e07f90741b74647b37cce7dbf7c57c0', '将赴阌乡灞上留别钱起员外', 1, 3, 772, '772年', '暖景登桥望，分明春色来。离心自惆怅，车马亦裴回。远雪和霜积，高花占日开。从官竟何事，忧患已相催。', '暖景登桥望，分明春色来。离心自惆怅，车马亦裴回。<br>远雪和霜积，高花占日开。从官竟何事，忧患已相催。', '卢纶于天宝末始举进士，屡试不第，困守场屋。大历七载经宰相元载举荐，授阌乡尉，此诗即作于离开长安前往阌乡任时作。首联交代诗作时间和地点，登灞桥远眺，长安一片春色，引人沈醉。“暖景”“分明”暗指诗人筮仕的喜悦。然次联忽抑，即将离开长安故友，不由心生惆怅。“裴回”即“徘徊”意。颈联写景，远处终南山上积雪皑皑，而近处春花已向日盛开，“高花向日开”似表达诗人入仕的雀跃欢欣和欲在阌乡有所作为的心境。然尾联忽又一转，表达对前景的茫然和焦虑。全诗欲抑先扬，甫喜复忧。诗人身处大历时期，盛唐国运不再，文人普遍有萧散和迷茫心态，故此诗中的矛盾扞格情绪，自想而可知', '大历七年（772年）赴阌乡尉任于灞水作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (2, 'a4e5b8b1e87e4dc9ad9a19b59a6e53e8', '送李庶子致仕还洛', 2, 3, 683, '683年', '此地倾城日，由来供帐华。亭逢李广骑，门接邵平瓜。原野烟氛匝，关河游望赊。白云断岩岫，绿草覆江沙。诏赐扶阳宅，人荣御史车。灞池一相送，流涕向烟霞。', '此地倾城日，由来供帐华。亭逢李广骑，门接邵平瓜。原野烟氛匝，关河游望赊。白云断岩岫，绿草覆江沙。诏赐扶阳宅，人荣御史车。灞池一相送，流涕向烟霞。', '唐高宗弘道元年，杨炯在长安任太子詹事府司直、崇文馆学士。同年三月,李义琰致仕还洛,有诗以赠，因有此作。李义琰此人，曾位及宰相，因厌倦宫廷斗争，于弘道元年以足疾还家，之后至弘道四年，李义琰因曾反对武则天当政，不敢担任武则天治下的怀州刺史，于是一直赋闲在家。这首作品，当作于李义琰以足疾乞骸骨还洛阳之时。“此地”两句描写送别李义琰的场面宏大，应该是作者的夸张之语。“帐”是指古时郊外送别搭设的帐幕。“亭逢”两句用典。“李广骑”指李广赋闲在家，遭人欺凌事，盖指李义琰此时将要赋闲回家；“邵平瓜”指秦东陵侯在秦灭亡以后，曾在长安郊外种植瓜果，味道甘美，这里是指曾居高位的李义琰现在赋闲在家，隐居的生活。从“原野”两句写景的句子可以看出，此次送别当是在清晨薄雾笼罩时。“诏赐”两句当是指朝廷为犒劳李义琰为国家做出的贡献，而为李义琰准备的退休礼物。诗的最后两句言情，从此可以看出，作者与李义琰的感情较为深厚。此诗为传统的送别诗，值得称赞的一点是其写景的四句话，对仗工稳，与言情紧密结合，这是全诗的一大亮点。', '683年3月，在太子詹事府司直、崇文馆学士任.三月,李义琰致仕还洛,有诗以赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (3, 'b38a2ccf8b024185bbf7519d7603bada', '春日离长安客中言怀', 3, 3, 655, '655年', '年华开早律，霁色荡芳尘。城阙千门晓，山河四望春。御沟通太液，戚里对平津。宝瑟调中妇，金罍引上宾。剧谈推曼倩，惊坐揖陈遵。意气一言合，风期万里亲。自惟安直道，守拙忌因人。谈器非先木，图荣异后薪。揶揄惭路鬼，憔悴切波臣。玄草终疲汉，乌裘几滞秦。生涯无岁月，岐路有风尘。还嗟太行道，处处白头新。', '年华开早律，霁色荡芳尘。城阙千门晓，山河四望春。<br>御沟通太液，戚里对平津。宝瑟调中妇，金罍引上宾。<br>剧谈推曼倩，惊坐揖陈遵。意气一言合，风期万里亲。<br>自惟安直道，守拙忌因人。谈器非先木，图荣异后薪。<br>揶揄惭路鬼，憔悴切波臣。玄草终疲汉，乌裘几滞秦。<br>生涯无岁月，岐路有风尘。还嗟太行道，处处白头新。', '春日离京，登灞上回首望长安，宫雉相望，飞甍参差，伤何如哉？守拙笃志，不与世同流，守直道，恪忠信，终受人排挤。韶光空逝，前路迷茫，不知归处。', '为东台详正学士间因事被谪，离长安作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (4, 'c1671bb7504a4fb486678c57fe8c22bf', '宿灞上寄侍御玙弟', 4, 3, 745, '745年', '独饮灞上亭，寒山青门外。长云骤落日，桑枣寂已晦。古人驱驰者，宿此凡几代。佐邑由东南，岂不知进退。吾宗秉全璞，楚得璆琳最。茅山就一征，柏署起三载。道契非物理，神交无留碍。知我沧溟心，脱略腐儒辈。孟冬銮舆出，阳谷群臣会。半夜驰道喧，五侯拥轩盖。是时燕齐客，献术蓬瀛内。甚悦我皇心，得与王母对。贱臣欲干谒，稽首期殒碎。哲弟感我情，问易穷否泰。良马足尚踠，宝刀光未淬。昨闻羽书飞，兵气连朔塞。诸将多失律，庙堂始追悔。安能召书生，愿得论要害。戎夷非草木，侵逐使狼狈。虽有屠城功，亦有降虏辈。兵粮如山积，恩泽如雨霈。羸卒不可兴，碛地无足爱。若用匹夫策，坐令军围溃。不费黄金资，宁求白璧赉。明主忧既远，边事亦可大。荷宠务推诚，离言深慷慨。霜摇直指草，烛引明光佩。公论日夕阻，朝廷蹉跎会。孤城海门月，万里流光带。不应百尺松，空老钟山霭。', '独饮灞上亭，寒山青门外。长云骤落日，桑枣寂已晦。<br>古人驱驰者，宿此凡几代。佐邑由东南，岂不知进退。<br>吾宗秉全璞，楚得璆琳最。茅山就一征，柏署起三载。<br>道契非物理，神交无留碍。知我沧溟心，脱略腐儒辈。<br>孟冬銮舆出，阳谷群臣会。半夜驰道喧，五侯拥轩盖。<br>是时燕齐客，献术蓬瀛内。甚悦我皇心，得与王母对。<br>贱臣欲干谒，稽首期殒碎。哲弟感我情，问易穷否泰。<br>良马足尚踠，宝刀光未淬。昨闻羽书飞，兵气连朔塞。<br>诸将多失律，庙堂始追悔。安能召书生，愿得论要害。<br>戎夷非草木，侵逐使狼狈。虽有屠城功，亦有降虏辈。<br>兵粮如山积，恩泽如雨霈。羸卒不可兴，碛地无足爱。<br>若用匹夫策，坐令军围溃。不费黄金资，宁求白璧赉。<br>明主忧既远，边事亦可大。荷宠务推诚，离言深慷慨。<br>霜摇直指草，烛引明光佩。公论日夕阻，朝廷蹉跎会。<br>孤城海门月，万里流光带。不应百尺松，空老钟山霭。', '唐玄宗天宝四年，王昌龄自长安返回江宁，夜宿灞上，因有此作。全诗共有三十句，可分为四部分内容。第一部分为前四句，主要写夜宿灞上时的景色，并且“古人”两句为之后道出自己的政治理想作铺垫。“吾宗”至“问易”十句，主要写王玙在山中被征召入朝，担任御史要职，还写王玙曾为王昌龄的政治前途求卦象，问周易，显示出二人交好的感情。“良马”至“宁求”十句主要是作者向王玙叙述自己的政治理想。作者自比良马，言自己能够带兵打仗，一招制敌，破除军队被围困的危局。作品的后六句主要写作者希望通过此时官居高位的族弟王玙的举荐，在朝为官，实现自己远大的政治理想。全诗结构清晰，感情因用典的缘故显得婉转不至于直露，是一篇合格的求人引荐的作品。', '745年，由长安返回江宁前夕。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (5, '2cdd924006d648be9bad214d41d037af', '题灞池二首', 4, 3, 727, '727年', '腰镰欲何之，东园刈秋韭。世事不复论，悲歌和樵叟。开门望长川，薄暮见渔者。借问白头翁，垂纶几年也。', '腰镰欲何之，东园刈秋韭。世事不复论，悲歌和樵叟。开门望长川，薄暮见渔者。借问白头翁，垂纶几年也。', '此诗大致创作于唐玄宗开元十五年，应是王昌龄出仕之前，家中贫居时的作品。全诗共分为两首，通过对这两首作品的阅读，可以得知王昌龄早年间的生活是依靠农耕，是十分贫苦的。第一首作品先言作者在家农耕，种植韭菜。韭菜因为生长极快，也便于做饭等优点，所以当时的人们多有种植。次句写作者在家时心志所向：不参与讨论世间万事，只与乡间野老相互唱和取乐。质朴无华的诗句显示出作者平静淡泊的志向。作品的第二首开头先以作者家乡环境开头：家乡有山有水，可牧可渔，生活于其间，岂不美哉？在次句中，作者又借助与白头翁的谈话，表白自己的心迹：看似是问白头翁在这个地方隐居生活几年了，实际上是表达自己对这种可歌可渔亦可牧的生活的羡慕。这两首作品中表现出的淡泊名利，平静随和的心态似乎与一位正值年少时期的青年身份不相符合，但阅读作者的同期作品，可以发现王昌龄在年少时期，就属意于山水之间，这一点与诸多诗人年少时期的心迹是不同的。', '唐玄宗开元十五年，王昌龄于家中贫居', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (6, '4e312b5cc34544be9703bf1fbe01c075', '灞陵行送别', 5, 3, 744, '744年', '送君灞陵亭，灞水流浩浩。上有无花之古树，下有伤心之春草。我向秦人问路岐，云是王粲南登之古道。古道连绵走西京，紫阙落日浮云生。正当今夕断肠处，黄鹂愁绝不忍听。', '送君灞陵亭，灞水流浩浩。上有无花之古树，下有伤心之春草。我向秦人问路岐，云是王粲南登之古道。古道连绵走西京，紫阙落日浮云生。正当今夕断肠处，黄鹂愁绝不忍听。', '唐玄宗天宝二年春，李白于灞上送别友人，因有此作。“送君”一句点明送别友人一事。这一句以“浩浩水流”起兴，以浩荡水流兴作者对友人即将离别的离愁别绪。“无花古树”言初春树未抽芽的荒凉景象；“春草”采用《楚辞》中春草的含义。这两种物体，一个使作者见了产生伤春感情，一个使作者根据《楚辞》中春草的含义，生发出浓郁离愁，故言“伤心之春草”。“王粲”一句因为王粲曾避难荆州，途经南下之路，所以说此时境遇同王粲的友人的前路也很崎岖。“紫阙”一句借助飘荡无定的浮云，比喻即将漂流异地的友人对长安的思念之情。作品的最后一句中“骊歌”指送别的经典作品《骊驹》，此于前文送君情长遥相呼应，使全诗结构圆融，情韵悠长。', '天宝二年春，灞上送友人，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (7, 'b50b2daf779c4b40931267ce2d95f083', '送贺监归四明应制', 5, 2, 744, '744年春', '久辞荣禄遂初衣，曾向长生说息机。真诀自从茅氏得，恩波宁阻洞庭归。瑶台含雾星辰满，仙峤浮空岛屿微。借问欲栖珠树鹤，何年却向帝城飞。', '久辞荣禄遂初衣，曾向长生说息机。真诀自从茅氏得，恩波宁阻洞庭归。<br>瑶台含雾星辰满，仙峤浮空岛屿微。借问欲栖珠树鹤，何年却向帝城飞。', '唐玄宗天宝三年，贺知章告老还乡，上疏请为道士。李白相送，因有此作。题目中的“贺监”即贺知章，因贺知章曾任秘书监，故有此言。诗作的首联先言贺知章辞官回家事，也就是送别贺知章的缘由。“初衣”是指贺知章还未做官时穿的衣服，这句是说贺知章辞去官职，将要回家修道。颔联写贺知章想要追随汉代茅盈的步伐，学习道教思想，修炼内心。颈联中“瑶台”“仙峤”等地都是道教中神仙居住的场所，这是希望贺知章在修道的时候能够得道成仙，实现自己的理想，显示出作者对友人的期望。尾联寄托作者对友人的思念，借助“苏仙公得道”的典故，作者对友人发出恳切的邀请，邀请友人在得道的时候，驾鹤来到长安，与作者共聚。全诗紧密结合贺知章请求回家修道一事，根据作者早年丰富的修道经验，援笔写就，情思婉转，意境优美。', '744年春，玄宗命群臣送贺知章归越，赋诗命群臣赓和。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (8, 'a27624e73a5b428fb3eac8486c948d0b', '浐水东店送唐子归嵩阳', 6, 2, 738, '738年', '野店临官路，重城压御堤。山开灞水北，雨过杜陵西。归梦秋能作，乡书醉懒题。桥回忽不见，征马尚闻嘶。', '野店临官路，重城压御堤。<br>山开灞水北，雨过杜陵西。<br>归梦秋能作，乡书醉懒题。<br>桥回忽不见，征马尚闻嘶。', '唐玄宗开元二十六年，岑参往来长安与洛阳之间，尝于浐水东店送别友人唐子，因有此作。因友人此次自长安返回岑参的老家嵩阳，所以此诗在表现离愁别绪的同时，还表现出旅居外地的作者的思想情感。诗作的首联对浐水东店的周围环境进行了简要描写。由此可知浐水东店的地理位置及周围环境：地处郊外，却靠近宫城。诗作的颔联点明送别的时天气的状况及周围的环境。虽然大山分列河流的两侧，给人以开阔豁达的感受；但蒙蒙细雨却为此时的送别增添了几分伤感。这两句中“灞水”“杜陵”与本诗的创作地点不相符合，所以诗人使用这两个地名并不是确指，有可能只是借助相对有名的地点渲染气氛而已。颈联两句写自己对家乡的思念。但这种对家乡真挚的感情及时为作者所抑制，所以作者在诗作的尾联中写到友人东去的行迹。没有写握手泪别，也没有折柳相送，只是描写友人离开后作者听到的阵阵马嘶，用意新奇，情思婉转而浓郁。', '738年，往来京洛，尝于浐水客店送友人唐子。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (9, 'd4c2e654222e4b2cac82898fc2cbbd65', '送郭乂杂言', 6, 3, 740, '740年', '地上青草出，经冬今始归。博陵无近信，犹未换春衣。怜汝不忍别，送汝上酒楼。初行莫早发，且宿霸桥头。功名须及早，岁月莫虚掷。早年已工诗，近日兼注易。何时过东洛，早晚度盟津。朝歌城边柳亸地，邯郸道上花扑人。去年四月初，我正在河朔。曾上君家县北楼，楼上分明见恒岳。中山明府待君来，须计行程及早回。到家速觅长安使，待汝书封我自开。', '地上青草出，经冬今始归。<br>博陵无近信，犹未换春衣。<br>怜汝不忍别，送汝上酒楼。<br>初行莫早发，且宿霸桥头。<br>功名须及早，岁月莫虚掷。<br>早年已工诗，近日兼注易。<br>何时过东洛，早晚度盟津。<br>朝歌城边柳亸地，邯郸道上花扑人。<br>去年四月初，我正在河朔。<br>曾上君家县北楼，楼上分明见恒岳。<br>中山明府待君来，须计行程及早回。<br>到家速觅长安使，待汝书封我自开。', '唐玄宗开元二十八年，岑参自河朔边疆归来，客居长安。多与当时俊茂之士往来。有诗赠别郭乂。作品的前四句作者写个人近况。“经冬”一句写自己刚从河朔边疆返回长安。“博陵”两句应该写友人前往的地方为唐定州博陵郡，在今河北定县。自“怜汝”一下的六句全是作者对友人的谆谆教导及殷勤勉励。“早年”二句写友人才能出众，年少即有诗名，稍长又精通《易经》。作者接着勉励友人，早日成才，早日来长安应举，一定能一举成名。“朝歌”以下五句全写作者对友人家乡的回想以及作者对友人即将离别的依依不舍之情。“邯郸道上花扑人”一句极有画面感，“扑”字为花赋予了动态美，既写出春风拂面的温柔，又写出了花落满地的绚烂美景，这是此诗的出彩之处。', '740年，自河朔归来，一度客居长安，多与当时俊茂之士往来。有诗赠别郭乂。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (10, '9ac11aaf2cf34331990cdb859d91f22d', '送郑堪归东京汜水别业（得闲字）', 6, 3, 745, '745年', '客舍见春草，忽闻思旧山。看君灞陵去，匹马成皋还。对酒风与雪，向家河复关。因悲宦游子，终岁无时闲。', '客舍见春草，忽闻思旧山。<br>看君灞陵去，匹马成皋还。<br>对酒风与雪，向家河复关。<br>因悲宦游子，终岁无时闲。', '唐玄宗天宝四年，友人郑堪东归洛阳，岑参有诗以赠。诗的首联写郑堪东归洛阳一事的缘由。诗中写郑堪在长安客舍中看到窗外青草漫漫，便兴起思乡感情，于是起行，东归洛阳。颔联两句对仗极工，以“灞陵”对应友人此次前往的“成皋”地区。“匹马”一词虽是对将行友人的真实描写，却富有情感。试想，一位独自回家的旅人，如何不令读者读此伤感呢？颈联两句对送别的场面进行描写，点明送别的季节应该是在冬季飞雪日，恶劣的天气环境为本已伤感的送别平添一份凄凉。对送别场景的真实描写有助于作者在诗作的结尾表述出自己的友人离别后的伤感情感，但这样的风险在于，整篇诗作都会流于俗套，毫无新意。所以岑参在诗作的尾联宕开一笔，转写因看到友人回家，想到自己多年宦游多地的辛苦。由友人回家联想到自己多年宦游的辛苦，并不突兀；而避开离愁别绪的抒发，使全诗不流于俗套，构思实在巧妙。', '友人郑堪归洛阳，岑参赋诗赠别。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (11, '83758db37a074af29345121dc81529c7', '送王伯伦应制授正字归', 6, 3, 748, '748年', '当年最称意，数子不如君。战胜时偏许，名高人共闻。半天城北雨，斜日灞西云。科斗皆成字，无令错古文。', '当年最称意，数子不如君。战胜时偏许，名高人共闻。<br>半天城北雨，斜日灞西云。科斗皆成字，无令错古文。', '唐玄宗天宝七年，王伯伦应制举，授官秘书省正字，将归，岑参有诗赠别。王伯伦事载于《旧唐书》，曾任北庭节度掌书记，行军司马等职位。唐肃宗至德二年，曾大破敌军于至苑门。此诗当作于王伯伦早年应举之时。诗的首、颔两联写王伯伦年少时不仅文采出众，高人一等；而且在科举考试时也有出众的表现，名满当时。颈联两句写作者与王伯伦分别时的场景。亦晴亦雨，为整场送别蒙上伤感气息。诗作的尾联较为难解，看似与整篇作品毫无联系：前文写王伯伦应举，尾联写古时候的科斗文，行文逻辑或者说情感意绪上出现了断连。其实这是作者有意而为之，其目的是避免作品落于俗套，落于结尾言情的传统送别诗的创作方式。这里写的科斗文应该是指宫廷中发出的榜文上写的文字，并不是确指秦朝的科斗文。这句话通过表达“宫廷发出的榜文上确确实实有您的名字，已经无法更改”的意思，表现作者为友人得以应举感到高兴的喜悦情感。', '王伯伦应制举，授官秘书省正字，将归，岑参有诗赠别。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (12, '146878b0de8642e08b3515acf1938588', '送祈乐归河东', 6, 3, 753, '753年', '祁乐后来秀，挺身出河东。往年诣骊山，献赋温泉宫。天子不召见，挥鞭遂从戎。前月还长安，囊中金已空。有时忽乘兴，画出江上峰。床头苍梧云，帘下天台松。忽如高堂上，飒飒生清风。五月火云屯，气烧天地红。鸟且不敢飞，子行如转蓬。少华与首阳，隔河势争雄。新月河上出，清光满关中。置酒灞亭别，高歌披心胸。君到故山时，为谢五老翁。', '祁乐后来秀，挺身出河东。往年诣骊山，献赋温泉宫。<br>天子不召见，挥鞭遂从戎。前月还长安，囊中金已空。<br>有时忽乘兴，画出江上峰。床头苍梧云，帘下天台松。<br>忽如高堂上，飒飒生清风。五月火云屯，气烧天地红。<br>鸟且不敢飞，子行如转蓬。少华与首阳，隔河势争雄。<br>新月河上出，清光满关中。置酒灞亭别，高歌披心胸。<br>君到故山时，为谢五老翁。', '唐玄宗天宝十二载，祁乐归河东，岑参有诗送行。诗作的前四句对祁乐少年时为人称道的事迹作了简要阐述。“献赋”是指祁乐曾经像普通文人一样，向皇帝进献一份作品希望在宫廷中谋取一份官职。但当这一切不能如愿的时候，祁乐便挥鞭从戎，建功沙场。从这两件事，我们可以体会到祁乐为人的那种豪爽洒脱的气质，不愧为“后来秀”。“有时”以下三句是言，祁乐不仅文采出众，可以献赋温泉宫，而且他本人的绘画才能也十分高超。“五月”以下两句言祁乐出使河东事，“五月”一句写河东地区环境恶劣，后一句则写祁乐却行如转蓬，独自飘零无依靠。“少华”以下四句似写祁乐隐居之志。作品的最后四句言别，没有儿女情长的故作姿态，只有英雄相别的豪迈洒脱。“五老翁”一句可以看出祁乐有归隐之心。在这一句中，岑参亦寄托了自己的身世之感，希望祁乐在膜拜五老翁像的时候为自己带去一份羡慕及向往的感情。全诗看似写祁乐之不遇，实则是岑参自己的伤感而已。', '祁乐归河东，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (13, '6e1db234d62340fd99e9ff378796d60a', '送魏升卿擢第归东都因怀魏校书陆浑乔潭', 6, 3, 753, '753年', '井上桐叶雨，灞亭卷秋风。故人适战胜，匹马归山东。问君今年三十几，能使香名满人耳。君不见三峰直上五千仞，见君文章亦如此。如君兄弟天下稀，雄辞健笔皆若飞。将军金印亸紫绶，御史铁冠重绣衣。乔生作尉别来久，因君为问平安否。魏侯校理复何如，前日人来不得书。陆浑山下佳可赏，蓬阁闲时日应往。自料青云未有期，谁知白发偏能长。垆头青丝白玉瓶，别时相顾酒如倾。摇鞭举袂忽不见，千树万树空蝉鸣。', '井上桐叶雨，灞亭卷秋风。故人适战胜，匹马归山东。问君今年三十几，能使香名满人耳。<br>君不见三峰直上五千仞，见君文章亦如此。如君兄弟天下稀，雄辞健笔皆若飞。将军金印亸紫绶，御史铁冠重绣衣。乔生作尉别来久，因君为问平安否。魏侯校理复何如，前日人来不得书。陆浑山下佳可赏，蓬阁闲时日应往。自料青云未有期，谁知白发偏能长。垆头青丝白玉瓶，别时相顾酒如倾。摇鞭举袂忽不见，千树万树空蝉鸣。', '天宝十二年秋，魏叔虬中举东归，有诗送行，兼怀魏季龙、乔潭有诗赠别魏升卿。“井上”两句点明离别的时间及地点，应是长安灞上秋雨时。“故人”至“满人耳”四句写魏叔虬中举事。唐人喜称中举为“战胜”。君不见两句写魏叔虬诗文功力深厚，笔力劲健。“将军”“御史”两句写魏叔虬的两位兄弟的官职，魏家人丁兴旺，家世显赫。以上几句都是言魏叔虬中举事，并由此推开，写魏家显赫。诗作从此开始转折，写作者对魏侯，乔生的怀念。“乔生”至“前日”四句就言此事。“蓬阁”是唐掌管图书的秘书省的代称，此句写作者对魏侯的寄语。“自料”两句诗意再度转折，由对友人的思念，转为对时光流逝的感叹，继而怀念旧时与友人一同郊游的美好时光。“摇鞭”两句极好，上句写友人分离，下句言作者独处。用蝉在树林中的鸣叫烘托作者独处的寂寞感，以此加深作者对友人的怀念程度。', '秋，魏叔虬中举东归，有诗送行，兼怀魏季龙、乔潭有诗赠别魏升卿。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (14, '5d1153caf014416ca19550d0acde1166', '送怀州吴别驾', 6, 3, 765, '765年', '灞上柳枝黄，垆头酒正香。春流饮去马，暮雨湿行装。驿路通函谷，州城接太行。覃怀人总喜，别驾得王祥。', '灞上柳枝黄，垆头酒正香。春流饮去马，暮雨湿行装。驿路通函谷，州城接太行。覃怀人总喜，别驾得王祥。', '唐代宗永泰元年，岑参客居长安。吴姓友人赴任怀州，任怀州别驾。有诗送行。友人此次赴任的怀州地区是今天的河南省沁阳县附近，与太行山接壤，因有“州城接太行”句。诗作的前两联点明送别时间为长安的初春，杨柳抽芽的日子。“春流”二句对送别场景进行了真实书写，和其他送别作品一样，送别时的蒙蒙细雨总能给行人及送别者以伤感气息。颈联两句和其他送别作品一样，通过对仗句，既写行人的出发地，也写行人的目的地。诗作的最后一联蕴含作者对友人才能的称赞以及对友人政治前途的期望。“覃怀”是怀州旧称，“王祥”一句用东汉同为别驾的王祥治理州县有功而受人称赞的典故。所以尾联两句是说怀州百姓应该欣喜，您来上任怀州，希望您在任上有所作为。全诗最为出色的是开头描写送别场景的两联诗句，既与本诗的主题相契合，又诗意盎然，意境优美，脱离整篇诗作来看，也不失为两句好诗句。', '在长安，吴别驾赴怀州，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (15, 'a201f2e790244b1890b140b8ed32bba7', '送孟孺卿落第归济阳', 6, 3, -1, '年份未详', '献赋头欲白，还家衣已穿。羞过灞陵树，归种汶阳田。客舍少乡信，床头无酒钱。圣朝徒侧席，济上独遗贤。', '献赋头欲白，还家衣已穿。<br>羞过灞陵树，归种汶阳田。<br>客舍少乡信，床头无酒钱。<br>圣朝徒侧席，济上独遗贤。', '此作年份未能详考，创作缘由大致是作者友人孟孺卿科举落第，归济阳，作者有诗送行。在送别诗作中，面对出于不同原因不得不离开长安的友人，作者在创作作品时所包含的内容也有不同。例如在为应制成功的王伯伦创作的作品中，作者写“当年最称意，数子不如君。战胜时偏许，名高人共闻。”全是听闻王伯伦应制消息之后的欣喜之情，全为赞语。但是面对刚好落第不得不离开长安的友人，岑参在作品中不仅有勉励语，还有为友人打抱不平的对现实社会进行抨击的发泄语。试看此篇。作品前三联言友人应举失败，落第而归。不仅如此，作者还将友人因科举失败而落的家道贫穷的结局写入作品中。前三联的写作是为了作者在最后一联为友人打抱不平将行铺垫。最后一联作者言“济上独遗贤”，是为友人未能高中而感到愤愤不平。而前一句“圣朝徒侧席”，则加入了作者根据友人未能高中一事而对朝廷选官制度的不满，可以算得上是对社会制度的抨击。', '在长安，孟孺卿科举落第，归济阳，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (16, 'f43ebfa332df444d8be153b4720b54f0', '送张卿郎君赴硖石尉', 6, 3, -1, '年份未详', '卿家送爱子，愁见灞头春。草羡青袍色，花随黄绶新。县西函谷路，城北大阳津。日暮征鞍去，东郊一片尘。', '卿家送爱子，愁见灞头春。<br>草羡青袍色，花随黄绶新。<br>县西函谷路，城北大阳津。<br>日暮征鞍去，东郊一片尘。', '此诗创作年份未详，创作背景为张卿赴任硖石县令，岑参送行，因有此作。诗作首联点明送行时间及地点为长安春日灞桥边。作品的颔联写卿此次离别长安的原因是赴任硖石县令。青袍是指唐代县尉穿着的衣物，黄绶是指唐代县尉上任时佩戴的凭证。颈联两句写作者想象中的硖石县景色，也作点出张卿此次赴任的地点。“大阳津”同“太阳津”，《元和郡县志》中记载位于硖石县的西北方向四里处。诗作尾联以作者的视角写离别之后的所见景象。日暮即一天将尽之时，也是最能勾起作者愁苦情绪的时候。我们不知道作者在面对远去的友人，扬起的尘土，心中有何种感受。但未确指的心中感情反而给读者以较大的空间，留给读者较多的空白，等待我们用自己的亲身感受去填补这份空白。', '张卿赴任硖石县令，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (17, 'f750b01b16a448f589ebf85a7ee68f67', '送冯着受李广州署为录事', 7, 3, 769, '769年', '郁郁杨柳枝，萧萧征马悲。送君灞陵岸，纠郡南海湄。名在翰墨场，群公正追随。如何从此去，千里万里期。大海吞东南，横岭隔地维。建邦临日域，温燠御四时。百国共臻奏，珍奇献京师。富豪虞兴戎，绳墨不易持。州伯荷天宠，还当翊丹墀。子为门下生，终始岂见遗。所愿酌贪泉，心不为磷缁。上将玩国士，下以报渴饥。', '郁郁杨柳枝，萧萧征马悲。送君灞陵岸，纠郡南海湄。名在翰墨场，群公正追随。如何从此去，千里万里期。大海吞东南，横岭隔地维。建邦临日域，温燠御四时。百国共臻奏，珍奇献京师。富豪虞兴戎，绳墨不易持。州伯荷天宠，还当翊丹墀。子为门下生，终始岂见遗。所愿酌贪泉，心不为磷缁。上将玩国士，下以报渴饥。', '唐代宗大历四年春，在长安。春，冯着受广州刺史李勉之邀任录事，韦应物相送，因有此作。作品的前四句言送别。此日正是长安春日，满眼葱郁，杨柳烂漫。值此良日，作者同友人在灞陵岸边分别，征马的嘶鸣声为绚丽的春景添上了几分萧瑟的气氛。接下来的四句是作者发问，作者不理解为什么友人要离开所有人都想待在的宫廷内院，而去偏僻遥远的南部沿海地区。“横岭”句至“还当”句，是作者告诫友人此去作官的难处：南部沿海地区虽多有珍奇异宝，但是要在那里作官，以公平的态度对待所有人是不容易的。作品的后半部分，是作者对友人的劝勉之语。这一段中，作者借用晋代吴隐之饮贪泉的典故，劝勉友人要为官清廉；借用《论语》中“磨而不磷”的典故，告诫友人不要因为环境改变自己最初的信念。最后两句，作者还借用《战国策》中的典故，告诫友人要心志远大，要成为国家中具有才能的人，为国效劳。此诗不同于其他的离别诗，近一半篇幅全部是写作者对友人的建议，这些建议都是作者对友人的殷切期望与谆谆教导，可见作者同友人关系密切，情深意浓。', '在长安。春，冯着受广州刺史李勉之邀任录事，韦应物相送，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (18, '916b75efcece4630b74a791106536622', '灞上轻薄行', 8, 3, 791, '791年', '长安无缓步，况值天景暮。相逢灞浐间，亲戚不相顾。自叹方拙身，忽随轻薄伦。常恐失所避，化为车辙尘。此中生白发，疾走亦未歇。', '长安无缓步，况值天景暮。相逢灞浐间，亲戚不相顾。自叹方拙身，忽随轻薄伦。常恐失所避，化为车辙尘。此中生白发，疾走亦未歇。', '唐德宗贞元七年，孟郊在长安应进士试，有诗揭露当时长安人心不古、竞相逐利之世态。全诗语言浅近易懂，古朴无华。“长安”句写日晚时分的长安，行走在长安街头的人们步履频频，丝毫没有因到夜晚，所以放慢脚步的打算。“相逢”两句写长安街头的人，不仅走得很快，而且都很陌生，即使是亲戚朋友在街头遇见，也不会亲呢地打招呼。“自叹”句以下是作者自谓。在这几句中，作者说自己年老体弱还要生活在这样轻薄的世态中，唯恐遭人陷害，即使是已经苍老到满头白发，作者也愿，也不能停下奔走的脚步。此诗写得很委婉，但具体写了什么，读完就懂，意思很浅近。长安的人为何奔走不停，是因为他们在追求名利。在追逐名利的过程中，每一个人都是对手，是敌人。所以到晚上还在奔走，遇到亲戚朋友也不打招呼。在这样的追名逐利的过程中，作者被裹挟其中，深受其害，也深有感触。作者写下的“疾走亦未歇”并不是自己想走，而是自己不得不走。只有明白了这一点，才能进一步理解到作者创作这个作品意图是在批判而不是歌颂这个追名逐利的社会。', '791年，本年前后，在长安应进士试，有诗揭露当时长安人心不古、竞相逐利之世态。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (19, '578b85e025c34888868b18c59077284a', '饯唐州高使君赴任', 9, 3, 710, '710年', '桐柏膺新命，芝兰惜旧游。鸣皋夜鹤在，迁木早莺求。传拥淮源路，尊空灞水流。落花纷送远，春色引离忧。', '桐柏膺新命，芝兰惜旧游。鸣皋夜鹤在，迁木早莺求。传拥淮源路，尊空灞水流。落花纷送远，春色引离忧。', '唐中宗景龙四年三月，高询赴任唐州刺史，诸学士设宴饯别，有诗唱和，因有此作。唐州，即现在的河南南阳一带，附近有桐柏山。所以诗人言“桐柏膺新命”，是说高询新接到赴任唐州的命令。“芝兰”一词指旧有的美好友情。这两句是写高询出任唐州一事以及作者对友人即将离别的殷切期许希望友人不要忘记就有的感情。颔联“鸣皋”是指河南嵩县的九皋山，昔日有白鹤啼鸣于此，故诗人以此为喻。诗的颈联中“传”是古代远行人乘坐的“征传”车。因淮河水出自桐柏山，所以诗人用“传拥淮源路”一句言高询即将赴任唐州。下句是其对仗句，极为工整。以“灞水”对“淮源”，一个是今日饯别，一个是明日远行，立意巧妙。诗的尾联点明此时诗人设宴是为暮春落花时节。暮春落花，总引起人们的年华将逝，时不我待的忧伤，此时送别，更易引起友人及作者的离愁别绪。全诗比喻繁多，影响诗意表达。仅最后一联，将暮春与离别相联系，既是对饯别现场的真实书写，亦是诗人愁思的含蓄表达', '景龙四年三月，诸学士送高询赴唐州刺史任。有诗相赠', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (20, '15e35cef78e24ee4909d8b839c6b2abe', '饯许州宋司马赴任', 10, 3, 708, '708年秋', '国为休征选，舆因仲举题。山川襄野隔，朋酒灞亭暌。零雨征轩骛，秋风别骥嘶。骊歌一曲罢，愁望正凄凄。', '国为休征选，舆因仲举题。山川襄野隔，朋酒灞亭暌。零雨征轩骛，秋风别骥嘶。骊歌一曲罢，愁望正凄凄。', '唐中宗景龙二年秋，修文馆诸学士饯别许州宋司马，因有此作。本诗根据内容划分，可分为三部分内容，是工整的送别诗作品。第一部分内容是全诗的首联，两句话点明此次饯别宋司马的缘由以及作者对宋司马仕途前景的美好期待。首联中“休征”、“仲举”词语是用三国时期王祥与汉代陈蕃的典故。此二人都有仕途顺达，为人重用，都曾任一州司马，并且政绩卓越的共同点，所以作者此处用典，点明友人赴任的官职，并希望友人在任上可以有一番作为。诗的颔联是全诗的第二部分内容，两句话对饯别时的场景作了简要描写的同时，还以饯别场景起兴，掺杂一点愁思。“山川”一句同“朋酒”一句同时来看，作者与友人即将分离两地，山阻水隔，音情书信无法寄达，这种痛苦再与此时把酒言欢的饯别场景相对比，更能显出作者与友人的离别之情。诗的后两联接着延续颔联生发出的离愁别绪，写分别时的场景及别后的相思，·“零雨”两句点明分别时间为景龙二年秋日，秋日送别，恶劣的天气环境的衬托下，作者送别的心情更显忧郁。尾联为这种忧郁愁苦的心情作结，使此诗表情达意婉转而不至于直露流俗。既有对友人的期待，也有友人分别时的愁苦离思，此诗可算得上工整的送别作品。', '708年秋，修文馆学士饯别许州宋司马。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (21, '555a25a0ab83486ea8623f9a5e3df7d1', '送友人东归', 11, 3, 750, '约750年', '对酒灞亭暮，相看愁自深。河边草已绿，此别难为心。关路迢迢匹马归，垂杨寂寂数莺飞。怜君献策十余载，今日犹为一布衣。', '对酒灞亭暮，相看愁自深。河边草已绿，此别难为心。关路迢迢匹马归，垂杨寂寂数莺飞。怜君献策十余载，今日犹为一布衣。', '唐玄宗天宝九年，友人东归，刘长卿相送，因有此作。全诗除了离别时候因感于日后无复相见的离愁之外，还有作者为友人多年以来，无所收获而感到的心中不快。这种不快情绪埋藏颇深，只有在理解全诗的基础上才可以理解作者这种深层情绪。作品的首联写景起兴。在这两句中，作者写到“相看愁自深”。而看的对象不同，所生出的愁也是不相同的。作者同灞亭相看，生发出的是离愁别绪；作者同友人相看，生发出的是作者为友人多年终无所获的不快情绪。“河边”两句写离别情绪。以满眼葱郁的春景为底色，衬托情意绵绵，难分难舍的离别场景，突出送别人与离别人的感情深浓，为全诗蒙上伤感气息的做法，是唐人创作离别诗的统一范式。“关路”两句也是这样写法。诗作的最后一联，作者对友人心生怜意，为友人在外多年，终无所获而感到心有不快。可以说，这首诗在前半部分写得婉转蕴藉，将情绪隐藏得很好。但选择在最后一联将自己心中情绪一吐为快，实在是“相看愁自深”，愁绪郁郁凝结的结果。', '唐玄宗天宝九年，友人东归，刘长卿相送，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (22, '51f8c0c6fd224f16991c6812dd1bec10', '长乐亭留别', 12, 2, 829, '829年', '灞浐风烟函谷路，曾经几度别长安。昔时蹙促为迁客，今日从容自去官。优诏幸分四皓秩，祖筵惭继二疏欢。尘缨世网重重缚，回顾方知出得难。', '灞浐风烟函谷路，曾经几度别长安。昔时蹙促为迁客，今日从容自去官。优诏幸分四皓秩，祖筵惭继二疏欢。尘缨世网重重缚，回顾方知出得难。', '唐文宗大和三年五月，白居易罢刑部侍郎，赴任洛阳，当时的友人悉数与白居易在长乐亭送别。此诗即作于此时期。诗的首联言作者此时四别长安城，分别在四十四岁时，左贬江州；五十一岁时又任杭州刺史；五十二岁时，改任苏州刺史；此次离别长安则是第四次离别。因为前三次离开长安都是以被贬谪的身份仓促离开，所以说：“昔时蹙促为迁客”，此次离开则是自主去官，在心态上较为从容一些，所以称此次离开为：“今日从容自去官”。颈联使用“商山四皓”与“二疏”的典故抒写自己高洁的品格及从容恬淡的暮年心态。“商山四皓”是秦末汉初四位不愿意为新朝作官，于是隐居商山的高人；“二疏”则是汉朝时疏广、疏受两位贤臣，作官已达顶峰。遂辞官，返居田园的典故。这一句写白居易在离开长安官场的洒脱和怡然自得的乐趣，大有陶潜复归园田的雅士风范。可毕竟是久在官场，多方力量的束缚，使作者发出“回顾方知出得难”的感叹。', '大和三年五月，白乐天分司东洛，朝贤悉会兴化亭送别。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (23, '9123e046cffc45a6bd24223984d6c3a3', '过永宁', 12, 34, 840, '840年', '村杏野桃繁似雪，行人不醉为谁开。赖逢山县卢明府，引我花前劝一杯。', '村杏野桃繁似雪，行人不醉为谁开。赖逢山县卢明府，引我花前劝一杯。', '白乐天作诗不为语出惊人，只为老妪能解。此诗洵为一首明白如话的小诗。其诗质朴平易，造语自然，形成别具一格的浅切诗风。“村杏”、“野桃”撷取日常化和生活化的题材入诗，“繁似雪”，运用浅显的比喻，简单润色，使诗词的关注度和吸引力更强。诗人写离别，却不直言自己的留恋眷念。而采用特殊的“对写法”，通过朋友的劝杯与挽留，给情感上一个深入的递进。这种反弹琵琶式的抒情，撰写离情，却并不羸弱，更容易引发读者共鸣。', '由洛赴长安过永宁作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (24, '8b3d4cb7d7bc49e59781d93dd7166108', '灞东晚晴，简同行薛弃、朱训', 11, 3, 767, '767年至768年间', '客心豁初霁，霁色暝玄灞。西向看夕阳，曈曈映桑柘。二贤诚逸足，千里陪征驾。古树枳道傍，人烟杜陵下。伊余在羁束，且复随造化。好道当有心，营生苦无暇。高贤幸兹偶，英达穷王霸。迢递客王程，裴回主人夜。一薰知异质，片玉谁齐价。同结丘中缘，尘埃自兹谢。', '客心豁初霁，霁色暝玄灞。西向看夕阳，曈曈映桑柘。二贤诚逸足，千里陪征驾。古树枳道傍，人烟杜陵下。伊余在羁束，且复随造化。好道当有心，营生苦无暇。高贤幸兹偶，英达穷王霸。迢递客王程，裴回主人夜。一薰知异质，片玉谁齐价。同结丘中缘，尘埃自兹谢。', '唐代宗大历二年，刘长卿自淮南幕入长安，在长安逗留一年时间，此诗或于此时所作。作品题目为“灞东晚晴”，所以作品的前四句点题，描写灞东晚晴的绚丽景象。“初霁”言整日下雨，到黄昏时分天空转晴；“暝玄”状黄昏时分，天空上晚霞将天空渲染成的绚丽景色；“曈曈”是写黄昏时分的夕阳景象，言太阳即将下山，却明亮依旧。“二贤”至“古树”点出题目中提到的薛弃，朱训两人。“伊余”至“一薰”句是作者表白心迹，也是这篇作品的主要内容。这几句先言作者在近几年的仕途中渐渐生出不自由，羁绊的感觉；后来又写到作者为了王朝奔走疲惫不堪；最后作者将自己比作香草与美玉，认为自己虽然具备美好的品质，但依然没人赏识，没有得到应有的对待，遗憾可惜之情自在言外。作品的最后两句又写到与作者同游的两位友人，表达了作者对友人长期以来的陪伴以及勉励的感谢之情。', '唐代宗大历二年，刘长卿自淮南幕入长安，在长安逗留一年时间，此诗或于此时所作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (25, 'fb66a023393a47a992d2c1e6f84e2513', '题灞西骆隐士', 13, 3, -1, '年份未详', '磻溪连灞水，商岭接秦山。青汉不回驾，白云长掩关。雀喧知鹤静，凫戏识鸥闲。却笑南昌尉，悠悠城市间。', '磻溪连灞水，商岭接秦山。青汉不回驾，白云长掩关。雀喧知鹤静，凫戏识鸥闲。却笑南昌尉，悠悠城市间。', '此诗具体创作年份无考，当是作者游玩灞陵，因感骆隐士事迹，因有此作。因为是受到骆隐士事迹的启发，所以全诗多写山水隐逸景象，反应作者生活在末世，心生不如遁去，归隐深山，怡然自得的心态。作品首联写景起兴，先述隐居的地点之幽深。首句从磻溪写起，写及下游的灞水，以水之悠长代指隐居地点之深不可见；此句写商山连接秦岭，以山之逶迤言隐居地点之幽不可闻。颔联两句更是在此基础上，以山上云雾缭绕，暗示隐居地点的神秘莫测，高深不可见。颈联两句从对山水的描写跳出，宕开一笔写隐居者的生活及悠闲自在的心境。以“鹤”、“鸥”鸟类同“雀”“凫”鸟进行对比，写出隐居者心境之闲逸自在。最后一联作者用汉代梅福典故，暗含自己生活末世，希冀隐居山林，追求闲逸自在生活的心境。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (26, '4da108db34e24699b572e5b6066705f2', '灞上逢元九处士东归', 13, 3, -1, '年份未详', '瘦马频嘶灞水寒，灞南高处望长安。何人更结王生袜，此客虚弹贡氏冠。江上蟹螯沙渺渺，坞中蜗壳雪漫漫。旧交已变新知少，却伴渔郎把钓竿。', '瘦马频嘶灞水寒，灞南高处望长安。何人更结王生袜，此客虚弹贡氏冠。江上蟹螯沙渺渺，坞中蜗壳雪漫漫。旧交已变新知少，却伴渔郎把钓竿。', '此诗具体创作时间无考。根据作品内容，当是作者灞上逢故人，为故人鸣不平而作。作品首联中“瘦马”凸显出故人身为处士，生活贫困的特征；“望长安”又体现出故人一心想要在长安为官的愿望。颔联两句中作者两处用典为故人的生命经历鸣不平：第一处是首句用西汉王生令廷尉结袜的典故，意思是说如今世道不好，无人愿意礼贤下士；第二处是次句用东汉贡禹因友人得官，受到援引，进而得官的故事，意思是说无人愿意礼贤下士，所以你也不要对做官抱有太大希望。颈联两句对灞水边的景物作一番描写，为下文归隐田园作铺垫。尾联两句，作者劝勉故人，希望他能够调整心态，归隐山林，过一种隐逸的生活，也是一种别样的人生。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (27, 'e8f4caa37ec04f51a497a7ab0b2782f4', '及第东归次灞上，却寄同年', 14, 3, 837, '837年', '芳桂当年各一枝，行期未分压春期。江鱼朔雁长相忆，秦树嵩云自不知。下苑经过劳想象，东门送饯又差池。灞陵柳色无离恨，莫枉长条赠所思。', '芳桂当年各一枝，行期未分压春期。江鱼朔雁长相忆，秦树嵩云自不知。下苑经过劳想象，东门送饯又差池。灞陵柳色无离恨，莫枉长条赠所思。', '唐文宗开成二年，李商隐及第，有诗赠同年。诗作首联为全诗伤感离别的情思奠定基调，此联写及第后就和同窗分别的不舍情绪。诗作的颔联由上联对友人不舍情绪，生发出别后不知何时再见的愁苦情绪，情绪再度加深。颈联两句，一是写作者未能同友人共赏曲江美景；二是写作者因在分离时候没有同友人相聚，感到遗憾的情绪。尾联写作者刚刚及第，喜悦的心情还未散去，所以此时送别，尽管没有同窗友人共聚，也没有令作者过度沉湎于分手的痛苦中。最后一联属于唐人创作送别诗的标准范式：首联先为全诗奠定基调；颔联、颈联写同友人的回忆，或者送别时的盛大场景；尾联则当写友人离开之后，作者的心情独白。多数作者在尾联中，对其情绪有所控制，表现为由前文写到的离愁别绪“退回一步”，从苦痛的离愁中跳脱出来，再写别的情绪。正如此诗，前三联写作者对友人的不舍，而最后一联则转而写及第后喜悦情绪，不足以使作者过度沉湎于离愁中。这种类似劝勉的话语，实则是已经沉湎于苦痛情绪的作者的自我解脱，自我纾解。', '开成二年，李商隐及第后有诗赠同年', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (28, '579ef88b5c88455fa1448d3625972166', '饯陈学士还江南同用征字', 15, 3, 731, '731年', '荷莜旋江澳，衔杯饯霸陵。别前林鸟息，归处海烟凝。风土乡情接，云山客念凭。圣朝岩穴选，应待鹤书征。', '荷莜旋江澳，衔杯饯霸陵。别前林鸟息，归处海烟凝。风土乡情接，云山客念凭。圣朝岩穴选，应待鹤书征。', '唐玄宗开元十九年，张九龄返京后，饯别友人，因有此作。与其他诗人创作离别诗作不同，张九龄出乎意料地采用“荷”这种喻示高雅情性的意象入诗，这必然是他作学士期间培养的独特审美意趣的体现。作品的首联言饯别。作者同友人相聚灞亭，眼观美景，口酌美酒，结合张九龄为官级别之高，这样的送别场景怎么高雅也不为过。从颔联，我们可以看出，友人此次离京所去往的应该是道教修身的场所。颈联两句，作者劝勉友人不要过多想念长安的生活，安心修道。最后一联中，我们可以大胆推断这位友人应该是还未入仕，此次离京正是想要走所谓的“终南捷径”，通过修炼道术的方式，为自己的前途命运带来更多的选择。从作者对友人此去的态度来看，可以得知作者是认同友人这种“终南捷径”的，由此我们可以认为中唐时候，唐人对“终南捷径”持一种不批判甚至有点跃跃欲试的态度，认为这是一种同科考一样改变自己命运前途的做法。', '开元十九年，返京后所作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (29, '39c952009840463c98a5d663154ee904', '饯唐永昌', 16, 3, 709, '709年', '洛阳难理若棼丝，椎破连环定不疑。鹦鹉休言秦地乐，回头一顾一相思。', '洛阳难理若棼丝，椎破连环定不疑。鹦鹉休言秦地乐，回头一顾一相思。', '唐中宗景龙三年九月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。因有此作。诗的第一句表达作者对唐永昌出任洛阳令的期望。“棼丝”出自《左传》中治丝益棼的典故，是言洛阳官场鱼龙混杂，混乱不稳定，希望唐永昌能够找到头绪解决这一问题。“椎破连环”出自《战国策》中秦昭王送齐国君王一只九玉连环的故事。因为齐人不知九玉连环如何解开，就用锥子将其破开。这是鼓励唐永昌不如果断一点，一举破除洛阳政坛乱局。诗的下两句，是对友人劝勉的话。“鹦鹉”自古出自秦地陇州，时人都以高价蓄养鹦鹉。作者这里将唐永昌比作为人高价购得的鹦鹉，劝诫唐永昌在洛阳的生活应克制自己，多多想念远在西京长安的友人们。', '唐中宗景龙三年九月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (30, '097de53ee3654959b762a21bba3faaae', '饯唐永昌赴任东都', 17, 3, 709, '709年', '闻道飞凫向洛阳，翩翩矫翮度文昌。因声寄意三花树，少室岩前几过香。', '闻道飞凫向洛阳，翩翩矫翮度文昌。因声寄意三花树，少室岩前几过香。', '唐中宗景龙三年九月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。因有此作。全诗用典偏僻，诗意隐约，试解之如下。第一句中“飞凫”用曹植《洛神赋》“体迅飞凫，飘忽若神”典故，言唐永昌才能非凡；“矫翮”一词有施展才能之意。所以此句应该是听闻唐永昌出为洛阳令后，作者对其才能的称赞及认可。诗的末句极为难解。“三花树”出自《齐民要术》，是指生长在嵩山附近的一种树，因一年三次开花而闻名，又称贝多。“少室岩”因达摩祖师曾于此修行而闻名天下，而“贝多”树似又与佛教的贝叶菩提树相类似，所以此句应该是借用佛教典故描绘河南地区的自然风光，借以对唐永昌的任期表达期待。', '709年9月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (31, '455d64b30e874d6286d2675c2150cf06', '饯唐永昌', 18, 3, 709, '709年', '始见郎官拜洛阳，旋闻近侍发雕章。绪言已勖期年政，绮字当生满路光。', '始见郎官拜洛阳，旋闻近侍发雕章。绪言已勖期年政，绮字当生满路光。', '唐中宗景龙三年九月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。因有此作。全诗表现作者对友人为官洛阳，有所作为的美好期待。诗的首句对仗工稳。“郎官”“近侍”两词均指君王的近臣，这里都是指唐永昌。“雕章”则是指唐永昌创作过的华丽的文章。这句是说听闻唐永昌出任洛阳令后，友人对其的勉励。诗的下句写唐永昌出为洛阳令，当是一年为期。“绮”表示美貌的样子，这里是指作者对唐永昌出任洛阳令的美好期待及祝福。', '709年9月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (32, 'e13f682f84bd40a482e58cb9bea497ed', '饯唐永昌', 19, 3, 709, '709年', '金溪碧水玉潭沙，凫舄翩翩弄日华。斗鸡香陌行春倦，为摘东园桃李花。', '金溪碧水玉潭沙，凫舄翩翩弄日华。斗鸡香陌行春倦，为摘东园桃李花。', '唐中宗景龙三年九月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。因有此作。“金溪”一句言送别时的美景：春日暖阳，映照着每一个景物都光彩夺目。“凫舄”句用典。《后汉书》中记载，河东有王乔者，叶县令。自县往来诣台朝，帝怪其来速，故暗自关注。见王乔将至，有双凫从东南飞来，日后搜寻，但得一鞋履。后世作者常用此典故代指县令或是即将成为县令的人，此诗中取后者意。后两句中“斗鸡”一词用魏明帝在洛阳修建斗鸡场一事，暗指洛阳。此句遥想洛阳景象。因离别时长安时春日，遂想象洛阳春日景象：满目鲜花，香气满城。此诗立意新奇，虽言送别，却不露离愁别绪。前两句言送别时，后两句写送别后，遥想友人在洛阳的生活，也算是作者对友人未来生活的期许及美好祝愿。', '709年9月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (33, 'b440aab4b349459abcfa5a4ff69ae309', '饯唐永昌', 20, 3, 709, '709年', '田郎才貌出咸京，潘子文华向洛城。愿以深心留善政，当令强项谢高名。', '田郎才貌出咸京，潘子文华向洛城。愿以深心留善政，当令强项谢高名。', '唐中宗景龙三年九月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。因有此作。前两句“田郎”、“潘子文”用东汉尚书田凤、西晋诗人潘岳典故，借指即将出任洛阳令的唐永昌。此二人皆以才貌出众为后世称道，用此二人代指，暗含作者对友人的称赞。以下两句用东汉董宜一事。东汉董宜因秉公处死公主，且不向公主认罪道歉，被世人称为强项令。此句用典，暗示作者对友人的祝愿。希望友人在洛阳令任上，能够秉公执法，留心善政，留下一世功名。此作与徐彦伯的作品相比，立意平淡无奇，虽然都是写了对友人的期待，但徐彦伯的作品更含蓄婉转，更有咀嚼含咏的空间。', '709年9月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (34, 'f517c45069984b6382f2919f75d14fe8', '饯唐永昌', 21, 3, 709, '709年', '河洛风烟壮市朝，送君飞凫去渐遥。更思明年桃李月，花红柳绿宴浮桥。', '河洛风烟壮市朝，送君飞凫去渐遥。更思明年桃李月，花红柳绿宴浮桥。', '唐中宗景龙三年九月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。因有此作。“河洛”是指黄河与洛水，前者在洛阳的北面，后者则从洛阳西南而来，穿洛阳城而过。“河洛”一句当说黄河及洛水上往来不息的航船及行人，写洛阳繁华的景象。“飞凫”一句也化用东汉王乔典故，言唐永昌即将赴任洛阳令。诗的下两句对洛阳春日的喧闹景象进行遥想，暗含作者对友人离别后的相思。因桃李花分别盛开于春日，所以诗人用“桃李月”代指春日。“浮桥”当指洛阳城中天津桥，因天津桥始建为浮桥，以铁锁勾连，后又加厚为石桥，故又名“浮桥”。此诗全写洛阳，仅一句提及唐永昌赴任之事。写洛阳之事寄托作者对友人的美好祝愿，立意新奇，情思婉转细腻。', '709年9月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (35, '73774a40354949c5926aa3cebf93dd3d', '饯唐永昌', 22, 3, 709, '709年', '闻君出宰洛阳隅，宾友称觞饯路衢。别后相思在何处，只应关下望仙凫。', '闻君出宰洛阳隅，宾友称觞饯路衢。别后相思在何处，只应关下望仙凫。', '唐中宗景龙三年九月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。因有此作。诗的前两句言友人即将出任洛阳令，宾朋奉觞相送之事。诗的后两句写分别后作者对友人的思念。“仙凫”一词化用东汉王乔典故。《后汉书》中记载，河东有王乔者，叶县令。自县往来诣台朝，帝怪其来速，故暗自关注。见王乔将至，有双凫从东南飞来，日后搜寻，但得一鞋履。后世作者常用此典故代指县令或是即将成为县令的人，此诗中取后者意。全诗诗意简单，既有对分别因由作简单描述，也有对分别后离愁别绪进行描写，可算得上是一首工整的送别诗，只是与他人饯别唐永昌的作品相比，相差甚远。', '709年9月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (36, '981fb3a2a3f944d593c608a4dabe656c', '饯唐永昌', 23, 3, 709, '709年', '洛阳旧有神明宰，辇毂由来天地中。余邑政成何足贵，因君取则四方同。', '洛阳旧有神明宰，辇毂由来天地中。余邑政成何足贵，因君取则四方同。', '唐中宗景龙三年九月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。因有此作。因唐高宗神龙二年更名洛阳为永昌，故言即将上任洛阳令的唐贞休为唐永昌。诗的前两句言洛阳不仅地位重要，而且自古以来有贤臣出世。“神明宰”是说具有很好观察能力的县令，此指东汉洛阳令董宜。东汉董宜因秉公处死公主，且不向公主认罪道歉，又被世人称为强项令。此句用典，作者希望友人能够上任洛阳后，继承这一优良传统，成为执政出色的地方官。“天地中”一句言洛阳作为京城，位居天地中央。此句不仅言洛阳地位重要，还说洛阳令官职重要，希望友人严肃对待。诗的后两句是作者对友人的夸赞之词：其他地方治理好没有什么可以称赞的，不过是借鉴您的治理经验罢了。此诗虽然不同于他人作品，实则是对典故的使用角度有全新的见解。例如他人用东汉董宜事皆言强项令，而沈作则用神明宰一词避开陈言，力求新意。', '709年9月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (37, '844fe48d6a14437b9110358b291870e9', '饯唐永昌', 24, 3, 709, '709年', '闻君墨绶出丹墀，双舄飞来伫有期。寄谢铜街攀柳日，无忘粉署握兰时。', '闻君墨绶出丹墀，双舄飞来伫有期。寄谢铜街攀柳日，无忘粉署握兰时。', '唐中宗景龙三年九月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。因有此作。诗的前两句言唐贞休出任洛阳令的经过。“墨绶”本指印环上的绶带，这里指唐贞休得官洛阳令一事。“双舄”一词化用东汉王乔事。《后汉书》中记载，河东有王乔者，叶县令。自县往来诣台朝，帝怪其来速，故暗自关注。见王乔将至，有双凫从东南飞来，日后搜寻，但得一鞋履。后世作者常用此典故代指县令或是即将成为县令的人，此诗中取后者意。诗的下两句表达作者在离别后对友人的思念。洛阳曾有“铜驼街”，所以作者用“铜街”代指洛阳，“寄谢”是说寄语，这是作者自谦之语。“粉署”是指尚书省的别称，汉时尚书省常用胡粉涂壁，故言。“握兰”当有交友之意。此句言虽然你到了洛阳出任县令，但请你不要忘记我们曾在尚书省为官的情谊。', '709年9月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (38, '0e7ae49cfde74120b2956721482d3a5f', '饯唐永昌', 25, 3, 709, '709年', '郎官出宰赴伊瀍，征传駸駸灞水前。此时怅望新丰道，握手相看共黯然。', '郎官出宰赴伊瀍，征传駸駸灞水前。此时怅望新丰道，握手相看共黯然。', '唐中宗景龙三年九月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。因有此作。“伊瀍”一词是指最终流入洛水的伊水与瀍水，此代指洛阳。“征传”是指远行人所乘车辆，此代指即将出行的唐贞休。诗的这两句是说唐贞休出任洛阳令之事发突然。因为事发突然，唐贞休及友人都毫无准备，所以此时友人宾朋在饯别唐贞休时是“怅望黯然”的情态。徐坚此作与马怀素作品相同，都交代了饯别的因由以及对友人离别后的离愁别绪，是一首工整的送别诗。只是与他人作品相比，无论是用典还是情意表达都相差甚远。只是作者避开泛指洛阳的“河洛”不谈，反而以最终汇入洛水的伊水与瀍水指代洛阳，饶有新意。', '709年9月，比部郎中唐贞休出为洛阳令，李适、刘宪、徐彦伯等景龙文馆学士赋诗饯行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (39, '05ad4732247640f6ab3320457e44fd59', '送许州宋司马赴任', 26, 3, 708, '708年', '颍郡水东流，荀陈兄弟游。偏伤兹日远，独向聚星州。河润在明德，人康非外求。当闻力为政，遥慰我心愁。', '颍郡水东流，荀陈兄弟游。偏伤兹日远，独向聚星州。<br><br>河润在明德，人康非外求。当闻力为政，遥慰我心愁。', '唐中宗景龙二年秋，修文馆诸学士饯别许州宋司马，因有此作。此篇诗作与修文馆学士创作的同题作品，有不同的作品风貌，充斥着兄弟间的手足情深，兄对弟的谆谆教导。诗的首联点明宋司马赴任的地点是颖水经过的许州。“荀陈”一句用东汉人荀淑、陈寔典故，作者此处用典，言自己同弟弟是有德望家族出身，是品识具佳的贤人。颔联一句抒情，表达宋之问在弟弟赴任许州之后的伤感情绪。诗的最后两联主要写宋之问作为兄长对弟弟的谆谆教导。“河润”一句言作者希望弟弟在许州任上可以完善内心，保持自己良好的品德。“当闻”一句写作者对弟弟精神上的教导外，还教导其在工作上有所作为，因为只有在工作上有一番成就，才可以安慰因兄弟两隔而产生的苦闷。', '唐中宗景龙二年秋，修文馆诸学士饯别许州宋司马，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (40, '33a6f820c9c8453d857064ea184a026a', '饯唐州高使君赴任', 27, 3, 710, '710年', '芳春桃李时，京都物华好。为岳岂不贵，所悲涉远道。远道不可思，宿昔梦见之。赠君双佩刀，日夕视来期。', '芳春桃李时，京都物华好。为岳岂不贵，所悲涉远道。远道不可思，宿昔梦见之。赠君双佩刀，日夕视来期。', '唐中宗景龙四年三月，高询赴任唐州刺史，诸学士设宴饯别，有诗唱和，因有此作。唐州，即现在的河南南阳一带。诗的首联点明此日饯别高询的时间为桃李芳菲时的仲春时节，此时的长安当是一片花红柳绿的世界，这是长安最美好的季节。以下三联避开饯别时的场景不写，反而想象高询离别后，自己关于高询的离愁。颔联言自己为高询离自己太远，或者离京都太远感到伤悲。颈联写自己与友人相距甚远，思念已不足以缓解离愁，只能借助梦境这一独特的方式排解自己心中对友人的思念。尾联是对饯别宴上作者与友人分别赠送信物的描写，也是化用了东汉张衡的《四愁诗》中的立意，表达作者对友人离别后的深浓愁绪。全诗避开饯别时现场的描写，全写作者对高询离别后的思念。因用古诗较多，所以本诗呈现出一副质朴的面貌，正是这种质朴的外表更有利于作品中浓郁情思的表达。', '景龙四年三月，诸学士送高询赴唐州刺史任。有诗相赠', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (41, 'a87938167a4f4b618451aac90ca5ab72', '饯唐州高使君赴任', 28, 3, 710, '710年', '永日奏文时，东风摇荡夕。浩然思乐事，翻复饯征客。淮水春流清，楚山暮云白。勿言行路远，所贵专城伯。', '永日奏文时，东风摇荡夕。浩然思乐事，翻复饯征客。淮水春流清，楚山暮云白。勿言行路远，所贵专城伯。', '唐中宗景龙四年三月，高询赴任唐州刺史，诸学士设宴饯别，有诗唱和，因有此作。唐州，即现在的河南南阳一带。全诗共分两部分，前两联为第一部分，是作者回想旧日与高询一起工作，游乐的趣事。“翻复”作为转折词，有突然之意。既是对上一部分作总结，又凸显出作者听闻友人即将赴任唐州的惊讶情绪。第二部分为诗的后两联，是作者对友人离别后的期许及想象。因淮水发源自唐州境内的桐柏山，故言“淮水春流清。而唐州在春秋时期又是楚国的属地，所以说“楚山暮云白”也是合理之辞。这两句是作者对友人离别后的生活的遥想。诗的尾联寄托了作者对友人的美好祝愿。“专城伯”一词出自《陌上桑》中“四十专城居”一句，是对高询出任刺史的别称。这句是说，你不要嫌弃路途遥远，你所追求的刺史的职位已经达到了。从过去直接度过到未来，此诗立意新奇。从“翻复”“勿言”等词中见作者心意，又凸显出诗思之巧妙。', '景龙四年三月，诸学士送高询赴唐州刺史任。有诗相赠', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (42, '0c479d0acb8f40d4a7c8388dec1f7403', '饯唐州高使君赴任', 19, 3, 710, '710年', '香萼媚红滋，垂条萦绿丝。情人拂瑶袂，共惜此芳时。骕骦已踯躅，鸟隼方葳蕤。跂予望太守，流润及京师。', '香萼媚红滋，垂条萦绿丝。情人拂瑶袂，共惜此芳时。<br>骕骦已踯躅，鸟隼方葳蕤。跂予望太守，流润及京师。', '唐中宗景龙四年三月，高询赴任唐州刺史，诸学士设宴饯别，有诗唱和，因有此作。唐州，即现在的河南南阳一带。全诗可根据内容分为两部分。第一部分是诗的前三联，是对友人饯别高询场景的真实书写。首联点时，说明友人饯别高询在这一年的春日：正是柳荡花摇之季，确是离别分袂之时。诗的第三联中“骕骦”是指古时有名的两匹好马。“葳蕤”一词本是指草木繁盛的样子，在这里代指鸟隼的羽毛初成的样子。这两句写分别时刻，良马踯躅，鸟隼初成。大有还未准备好，就即将出征上任的突然之意。诗的第二部分是尾联，和寻常的送别诗一样，作者常在这一联寄托自己对即将分别的友人的期望，此诗也不免于俗。“跂予”用《荀子》“吾跂而望之”的典故，比喻作者对友人归来的渴盼之情。在这两句中，作者不仅期盼友人早日回归，还期盼友人能够在唐州任上有所作为，希望其政治功绩及能力沾溉京都。', '景龙四年三月，诸学士送高询赴唐州刺史任。有诗相赠', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (43, '9237cbb47ec748a7ba8f26af85e8f945', '饯唐州高使君赴任', 29, 3, 710, '710年', '常时好闲独，朋旧少相过。及尔宣风去，方嗟别日多。淮流春晼晚，江海路蹉跎。百岁屡分散，欢言复几何。', '常时好闲独，朋旧少相过。及尔宣风去，方嗟别日多。<br>淮流春晼晚，江海路蹉跎。百岁屡分散，欢言复几何。', '唐中宗景龙四年三月，高询赴任唐州刺史，诸学士设宴饯别，有诗唱和，因有此作。唐州，即现在的河南南阳一带。全诗可根据内容分为两部分。第一部分是诗作的前两联。主要是写作者因友人之间不能常聚，今日相别导致的感伤时间的自我嗟叹。诗的首联写作者内心独白，言自己因为岁数的关系，不能与宾朋长时间聚在一起。颔联紧接着写作者此时因高询赴任一事感叹聚少离多。诗的第二部分是全诗的后两句。主要写作者对即将离别的高询的担心及寄托。和寻常的送别诗一样，作者常在诗作的最后两联寄托自己对即将分别的友人的期望，此诗也不免于俗。颈联二句是说作者对高询此去路程遥远的担心。淮河发源于桐柏山，故言“淮流春晼晚”。诗的尾联是写作者希望高询早日回归长安的寄托。', '景龙四年三月，诸学士送高询赴唐州刺史任。有诗相赠', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (44, 'd6e201d412d94993b7d254d4aa15632b', '奉和圣制途次陕州应制', 29, 23, 725, '725年', '周召尝分陕，诗书空复传。何如万乘眷，追赏二南篇。郡带洪河侧，宫临大道边。洛城将日近，佳气满山川。', '周召尝分陕，诗书空复传。何如万乘眷，追赏二南篇。<br>郡带洪河侧，宫临大道边。洛城将日近，佳气满山川。', '扈从玄宗赴洛，诗歌重心不是在陕州为中心，而是向洛城，体现出“地方感”中坚持“中心说”时又会“反中心说”。万乘与二南为专用典故，体现精英意识。', '扈从赴洛应制', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (45, '919f9a2afb114698be325d45a4b69c43', '奉和圣制登骊山瞩眺应制', 29, 5, 711, '711年', '寒山上半空，临眺尽寰中。是日巡游处，晴光远近同。川明分渭水，树暗辨新丰。岩壑清音暮，天歌起大风。', '寒山上半空，临眺尽寰中。是日巡游处，晴光远近同。<br>川明分渭水，树暗辨新丰。岩壑清音暮，天歌起大风。', '无', '扈从登骊山远眺应制作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (46, 'c37061bb04a84da7baf3f0b30ad79946', '奉和圣制登骊山高顶寓目应制', 18, 5, 711, '711年', '骊阜镇皇都，銮游眺八区。原隰旌门里，风云扆座隅。直城如斗柄，官树似星榆。从臣词赋末，滥得上天衢。', '骊阜镇皇都，銮游眺八区。原隰旌门里，风云扆座隅。<br>直城如斗柄，官树似星榆。从臣词赋末，滥得上天衢。', '无', '扈从登骊山远眺应制作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (47, 'd1fe3279182d4f6fa9f7a5f4326fdbd7', '奉和登骊山高顶寓目应制', 20, 5, 711, '711年', '崖巘万寻悬，居高敞御筵。行戈疑驻日，步辇若登天。城阙雾中近，关河云外连。谬陪登岱驾，欣奉济汾篇。', '崖巘万寻悬，居高敞御筵。行戈疑驻日，步辇若登天。<br>城阙雾中近，关河云外连。谬陪登岱驾，欣奉济汾篇。', '无', '扈从登骊山远眺应制作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (48, 'e5d35cd97b8f4131b8e2b690d057ec40', '奉和圣制登骊山高顶寓目应制', 30, 5, 711, '711年', '皇情遍九垓，御辇驻昭回。路若随天转，人疑近日来。河看大禹凿，山见巨灵开。愿扈登封驾，常持荐寿杯。', '皇情遍九垓，御辇驻昭回。路若随天转，人疑近日来。<br>河看大禹凿，山见巨灵开。愿扈登封驾，常持荐寿杯。', '无', '扈从登骊山远眺应制作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (49, 'a49ffa867f9644338c63959db1352526', '奉和圣制登骊山高顶寓目应制', 28, 5, 711, '711年', '仙跸御层氛，高高积翠分。岩声中谷应，天语半空闻。丰树连黄叶，函关入紫云。圣图恢宇县，歌赋小横汾。', '仙跸御层氛，高高积翠分。岩声中谷应，天语半空闻。<br>丰树连黄叶，函关入紫云。圣图恢宇县，歌赋小横汾。', '无', '扈从登骊山远眺应制作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (50, 'a5deb282cb244ccbaddac725d7f72f8e', '奉和登骊山高顶寓目应制', 24, 5, 711, '711年', '銮舆上碧天，翠帟拖晴烟。绝崿纡仙径，层岩敞御筵。云披丹凤阙，日下黑龙川。更睹南熏奏，流声入管弦。', '銮舆上碧天，翠帟拖晴烟。绝崿纡仙径，层岩敞御筵。<br>云披丹凤阙，日下黑龙川。更睹南熏奏，流声入管弦。', '无', '扈从登骊山远眺应制作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (51, '46829063b15d4ca6a5d613e942d9dee8', '奉和登骊山应制', 16, 5, 711, '711年', '龙行踏绛气，天半语相闻。混沌疑初判，洪荒若始分。', '龙行踏绛气，天半语相闻。混沌疑初判，洪荒若始分。', '无', '扈从登骊山远眺应制作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (52, 'ea8a3a46e8d84158bdb20a010fbe442d', '奉和幸新丰温泉宫应制', 24, 61, 729, '729年', '秦王登碣石，周后袭昆仑。何必在遐远，方称万宇尊。我皇顺时豫，星驾动轩辕。雄戟交驰道，清笳度国门。回舆长乐观，校猎上林园。行漏移三象，连营总八屯。旌摇鹦鹉谷，骑转凤凰原。绝壁苍苔古，灵泉碧溜温。参差开水殿，窈窕敞岩轩。丰邑模犹在，骊宫迹尚存。烟松衔翠幄，雪径绕花源。侍从推玄草，文章召虎贲。深仁浃夷夏，洪造溢乾坤。谬忝王枚列，多惭雨露恩。', '秦王登碣石，周后袭昆仑。何必在遐远，方称万宇尊。<br>我皇顺时豫，星驾动轩辕。雄戟交驰道，清笳度国门。<br>回舆长乐观，校猎上林园。行漏移三象，连营总八屯。<br>旌摇鹦鹉谷，骑转凤凰原。绝壁苍苔古，灵泉碧溜温。<br>参差开水殿，窈窕敞岩轩。丰邑模犹在，骊宫迹尚存。<br>烟松衔翠幄，雪径绕花源。侍从推玄草，文章召虎贲。<br>深仁浃夷夏，洪造溢乾坤。谬忝王枚列，多惭雨露恩。', '无', '扈从幸温泉作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (53, '229271fe1e084c04b52cbee700bf578e', '奉和幸新丰温泉宫应制', 19, 61, 729, '729年', '姬典歌时迈，虞篇记省方。何如黑帝月，玄览白云乡。翠仗萦船岸，明旆应萯阳。风摇花眊彩，雪艳宝戈芒。御陌开函次，离宫夹树行。桂枝笼騕褭，松叶覆堂皇。仙石含珠液，温池孕璧房。涌疑神瀵溢，澄若帝台浆。独沸流常热，潜蒸气转香。青坛环玉甃，红础铄金光。藻曜凝芳洁，葳蕤献淑祥。五龙归宝算，九扈叶时康。同预华封老，中衢祝圣皇。', '姬典歌时迈，虞篇记省方。何如黑帝月，玄览白云乡。<br>翠仗萦船岸，明旆应萯阳。风摇花眊彩，雪艳宝戈芒。<br>御陌开函次，离宫夹树行。桂枝笼騕褭，松叶覆堂皇。<br>仙石含珠液，温池孕璧房。涌疑神瀵溢，澄若帝台浆。<br>独沸流常热，潜蒸气转香。青坛环玉甃，红础铄金光。<br>藻曜凝芳洁，葳蕤献淑祥。五龙归宝算，九扈叶时康。<br>同预华封老，中衢祝圣皇。', '无', '扈从幸温泉作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (54, 'd34d90087e954599a4921040e7535f96', '奉和圣制温泉言志应制', 29, 61, 729, '729年', '温谷媚新丰，骊山横半空。汤池薰水殿，翠木暖烟宫。起疾逾仙药，无私合圣功。始知尧舜德，心与万人同。', '温谷媚新丰，骊山横半空。汤池薰水殿，翠木暖烟宫。<br>起疾逾仙药，无私合圣功。始知尧舜德，心与万人同。', '无', '扈从幸温泉作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (55, '2bdd0a2bb5e84b4bbf627761d8fd1578', '扈从温泉宫献诗', 29, 61, 729, '729年', '温泉启蛰气氛氲，渭浦归鸿日数群。骑仗联联环北极，鸣笳步步引南熏。松间彩殿笼佳气，山上朱旗绕瑞云。不知远梦华胥国，何如亲奉帝尧君。', '温泉启蛰气氛氲，渭浦归鸿日数群。骑仗联联环北极，<br>鸣笳步步引南熏。松间彩殿笼佳气，山上朱旗绕瑞云。<br>不知远梦华胥国，何如亲奉帝尧君。', '无', '扈从幸温泉作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (56, '64d05330428f4e698f7495015e562335', '奉和登骊山高顶寓目应制', 27, 61, 711, '711年', '名山何壮哉，玄览一徘徊。御路穿林转，旌门倚石开。烟霞肘后发，河塞掌中来。不学蓬壶远，经年犹未回。', '名山何壮哉，玄览一徘徊。御路穿林转，旌门倚石开。<br>烟霞肘后发，河塞掌中来。不学蓬壶远，经年犹未回。', '无', '扈从幸骊山寓目作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (57, '3388b53ffce9417dbe8835412d732340', '奉和骊山高顶寓目应制', 31, 61, 711, '711年', '步辇陟山巅，山高入紫烟。忠臣还捧日，圣后欲扪天。迥识平陵树，低看华岳莲。帝乡应不远，空见白云悬。', '步辇陟山巅，山高入紫烟。忠臣还捧日，圣后欲扪天。<br>迥识平陵树，低看华岳莲。帝乡应不远，空见白云悬。', '无', '扈从幸骊山寓目作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (58, 'f51e490cdae3492fba24c3893f994b6b', '奉和扈从温泉宫承恩赐浴', 32, 61, 729, '729年', '天行云从指骊宫，浴日馀波锡诏同。彩殿氤氲拥香溜，纱窗宛转闭和风。来将兰气冲皇泽，去引星文捧碧空。自怜遇坎便能止，愿托仙槎路未通。', '天行云从指骊宫，浴日馀波锡诏同。彩殿氤氲拥香溜，<br>纱窗宛转闭和风。来将兰气冲皇泽，去引星文捧碧空。<br>自怜遇坎便能止，愿托仙槎路未通。', '无', '扈从幸骊山温泉作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (59, '91851128b70e41a48158e35d4dd942b7', '会昌解颐之曲思明', 0, 61, -1, '无', '令史曲思明对吏部尚书赵冬曦言己来年将在某地做官，并将之书于纸上阴封于冬曦壁间。来年会昌改昭应，冬曦命曲赴昭应为官，并发壁验之，果昭应也。遂知曲有奇术。', '令史曲思明对吏部尚书赵冬曦言己来年将在某地做官，并将之书于纸上阴封于冬曦壁间。来年会昌改昭应，冬曦命曲赴昭应为官，并发壁验之，果昭应也。遂知曲有奇术。', '赵冬曦的热心执着、曲思明的随分淡定，皆见槠墨。在细节上，《曲思明》也更为详尽，如所“赌”之书的内容包括授官日月、俸禄、藏匿方法为泥封、坏壁等细节，均纤毫备至，增加了阅读的兴味。为了吸引读者的期待，曲思明甚至发“毒誓”云若错一字，当死于阶下，如此更加吸引人。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (60, 'efd9fd333c6645f28bbc97c2941fd27d', '谈宾录之马周', 0, 6, -1, '无', '马周西行长安，至新丰，宿于逆旅。主人唯供诸商贩而不顾周，遂命酒悠然独酌。主人翁深异之。及为常何陈便宜二十余事，太宗怪其能。问何，何答曰：“此非臣发虑，乃臣家客马周也。”太宗即日召之。未至间，遣使使催促者数四。及谒见，语甚悦，授监察御史。奏罢传呼，置鼓，每击以惊众，时人便之。迁中书令。周病消渴，弥年不瘳。时驾幸翠微宫，敕求胜地，为周起宅，名医内使，相望不绝。每令常食以御膳供之，太宗躬为调药，皇太子临问。', '马周西行长安，至新丰，宿于逆旅。主人唯供诸商贩而不顾周，遂命酒悠然独酌。主人翁深异之。及为常何陈便宜二十余事，太宗怪其能。问何，何答曰：“此非臣发虑，乃臣家客马周也。”太宗即日召之。未至间，遣使使催促者数四。及谒见，语甚悦，授监察御史。奏罢传呼，置鼓，每击以惊众，时人便之。迁中书令。周病消渴，弥年不瘳。时驾幸翠微宫，敕求胜地，为周起宅，名医内使，相望不绝。每令常食以御膳供之，太宗躬为调药，皇太子临问。', '赵冬曦的热心执着、曲思明的随分淡定，皆见槠墨。在细节上，《曲思明》也更为详尽，如所“赌”之书的内容包括授官日月、俸禄、藏匿方法为泥封、坏壁等细节，均纤毫备至，增加了阅读的兴味。为了吸引读者的期待，曲思明甚至发“毒誓”云若错一字，当死于阶下，如此更加吸引人。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (61, '96c799b540c7472f8cf800cd7f07e8d5', '定命录之卖馅媪', 34, 6, -1, '无', '唐马周字宾王，少孤贫，明诗传。落魄不事产业，不为州里所重。补博州助教，日饮酒。刺史达奚怒，屡加咎责。周乃拂衣南游曹汴之境。因酒后忤浚仪令崔贤，又遇责辱。西至新丰，宿旅次。主人唯供设诸商贩人，而不顾周。周遂命酒一斗，独酌。所饮余者，便脱靴洗足，主人窃奇之。因至京，停于卖馅媪肆。数日，祈觅一馆客处，媪乃引致于中郎将常何之家。媪之初卖馅也，李淳风、袁天纲尝遇而异之。皆窃云：“此妇人大贵，何以在此。”马公寻娶为妻。后有诏，文武五品官已上，各上封事。周陈便宜二十条事，遣何奏之。乃请置街鼓，乃文武官绯紫碧绿等服色，并城门左右出入，事皆合旨。太宗怪而问何所见，何对曰：“乃臣家客马周所为也。”召见与语，命直门下省。仍令房玄龄试经及策，拜儒林郎，守监察御史。以常何举得其人，赐帛百匹。周后转给事中中书舍人，有机辩，能敷奏。深识事端，动无不中。岑文本见之曰：“吾见马君，令人忘倦。然鸢肩火色，腾上必速，但恐不能久耳。”数年内，官至宰相，其媪亦为妇人。后为吏部尚书，病消渴，弥年不瘳。年四十八而卒。追赠右仆射高唐公。', '唐马周字宾王，少孤贫，明诗传。落魄不事产业，不为州里所重。补博州助教，日饮酒。刺史达奚怒，屡加咎责。周乃拂衣南游曹汴之境。因酒后忤浚仪令崔贤，又遇责辱。西至新丰，宿旅次。主人唯供设诸商贩人，而不顾周。周遂命酒一斗，独酌。所饮余者，便脱靴洗足，主人窃奇之。因至京，停于卖馅媪肆。数日，祈觅一馆客处，媪乃引致于中郎将常何之家。媪之初卖馅也，李淳风、袁天纲尝遇而异之。皆窃云：“此妇人大贵，何以在此。”马公寻娶为妻。后有诏，文武五品官已上，各上封事。周陈便宜二十条事，遣何奏之。乃请置街鼓，乃文武官绯紫碧绿等服色，并城门左右出入，事皆合旨。太宗怪而问何所见，何对曰：“乃臣家客马周所为也。”召见与语，命直门下省。仍令房玄龄试经及策，拜儒林郎，守监察御史。以常何举得其人，赐帛百匹。周后转给事中中书舍人，有机辩，能敷奏。深识事端，动无不中。岑文本见之曰：“吾见马君，令人忘倦。然鸢肩火色，腾上必速，但恐不能久耳。”数年内，官至宰相，其媪亦为妇人。后为吏部尚书，病消渴，弥年不瘳。年四十八而卒。追赠右仆射高唐公。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (62, 'f100454434264591a20775eee5b100e5', '定命录之袁天纲', 34, 43, -1, '无', '武德中天纲至洛，宿清化坊，为杜淹、王珪、韦挺三人看相，言其仕途，后果验之。', '武德中天纲至洛，宿清化坊，为杜淹、王珪、韦挺三人看相，言其仕途，后果验之。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (63, 'ee954621ffc74ecfbf386a1336321efc', '乾鐉子之阎济美', 0, 43, -1, '无', '阎济美下第，赠主考诗言己不平，主考深惋之。秋日济美赴洛再试，宿清化坊。主考以《天津桥望洛城残雪》易帖经，济美诗未完，主考唱过。后又命济美誊抄策问以替换错漏之处。云不负素约耳。', '阎济美下第，赠主考诗言己不平，主考深惋之。秋日济美赴洛再试，宿清化坊。主考以《天津桥望洛城残雪》易帖经，济美诗未完，主考唱过。后又命济美誊抄策问以替换错漏之处。云不负素约耳。', '诗歌与小说的互动在此作中表现典型。主考官之为履约而随意改动科举规则，反映出唐时制度的不合理，也是士子渴望仕进的文学想象。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (64, '60266a39827849ff94fc23b32a21cf12', '东阳夜怪录', 0, 10, -1, '无', '王洙遇秀才成自虚于荥阳逆旅，自虚谓其曾夜宿东阳驿佛舍，有高公、卢倚马、朱中正、敬去文、奚锐金、苗介、胃藏瓠、胃藏立等人咏诗，所咏诗为诗谜，含暗咏诗人之真身。', '王洙遇秀才成自虚于荥阳逆旅，自虚谓其曾夜宿东阳驿佛舍，有高公、卢倚马、朱中正、敬去文、奚锐金、苗介、胃藏瓠、胃藏立等人咏诗，所咏诗为诗谜，含暗咏诗人之真身。', '说中的部分诗歌从艺术品位看堪称上乘，又巧用拆字、合字、隐喻、典故等法交待人物背景，若平平读来但觉有诗意，然若深品其机巧，又觉深厚巧妙矣。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (65, 'c446417f35f24d9eaf4d455631f24ad4', '定命录之王晙', 34, 9, -1, '无', '王晙任渭南已数载。自云：“久厌此县，但得蒲州司马可矣。”时奚三儿从北来，见一鬼云：“送牒向渭南，报明府改官。”问何官，云改蒲州司马，便与相随来渭南，见晙云：“公即改官为蒲州司马。”当时鬼在厅阶下曲躬立。三儿言讫，走出。果三数日改蒲州司马。改后二十余日，敕不到。问三儿，三儿后见前鬼，问故。鬼云：“缘王在任剩请官钱，所以折除，今折欲尽，至某时，当得上。后验如其言。', '王晙任渭南已数载。自云：“久厌此县，但得蒲州司马可矣。”时奚三儿从北来，见一鬼云：“送牒向渭南，报明府改官。”问何官，云改蒲州司马，便与相随来渭南，见晙云：“公即改官为蒲州司马。”当时鬼在厅阶下曲躬立。三儿言讫，走出。果三数日改蒲州司马。改后二十余日，敕不到。问三儿，三儿后见前鬼，问故。鬼云：“缘王在任剩请官钱，所以折除，今折欲尽，至某时，当得上。后验如其言。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (66, '310809729ec044cab03caeb029538aec', '宣室志之 谢翱', 35, 6, -1, '无', '谢翱居长安升道里，晚霁出门眺南山，遇一姝丽妇人愿与翱同赏牡丹，翱置酒开宴，与女饮酒赏花。女告知己非人，翱并不以为意。夜阑女欲行，与翱唱和。后翱下第，于昭应复见此妇，匆匆一别，翱赴弘农寻觅，终不得。', '谢翱居长安升道里，晚霁出门眺南山，遇一姝丽妇人愿与翱同赏牡丹，翱置酒开宴，与女饮酒赏花。女告知己非人，翱并不以为意。夜阑女欲行，与翱唱和。后翱下第，于昭应复见此妇，匆匆一别，翱赴弘农寻觅，终不得。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (67, '0ac8b6ec14924fc8934b8b5802f53ea0', '年号记之卢尚卿', 0, 23, -1, '无', '咸通十一年，以庞勋盗据徐州，久屯戎卒，连年飞輓，物力方虚，因诏权停贡举一年。是岁，进士卢尚卿自远至关，闻诏而回。乃赋东归诗曰：“九重丹诏下尘埃，深琐文闱罢选才。桂树放教遮月长，杏园终待隔年开。自从玉帐论兵后，不许金门谏猎来。今日霸陵桥上过，关人应笑腊前回。”', '咸通十一年，以庞勋盗据徐州，久屯戎卒，连年飞輓，物力方虚，因诏权停贡举一年。是岁，进士卢尚卿自远至关，闻诏而回。乃赋东归诗曰：“九重丹诏下尘埃，深琐文闱罢选才。桂树放教遮月长，杏园终待隔年开。自从玉帐论兵后，不许金门谏猎来。今日霸陵桥上过，关人应笑腊前回。”', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (68, 'dd2ecdb00a054b529af7c0305c94126a', '宣室志之 计真', 35, 23, -1, '无', '元和中青齐人计真至陕州访友，醉后误入李外郎别墅，相谈甚欢，留宿二日。后李外郎将其女妻之，十余年生七子二女。妻死之前歔欷告真己本为狐，奉帚廿载，血诚勤恳，惟求以人礼葬。计真从之。后至李外郎居，已为荆棘丘墟矣。', '元和中青齐人计真至陕州访友，醉后误入李外郎别墅，相谈甚欢，留宿二日。后李外郎将其女妻之，十余年生七子二女。妻死之前歔欷告真己本为狐，奉帚廿载，血诚勤恳，惟求以人礼葬。计真从之。后至李外郎居，已为荆棘丘墟矣。', '行旅艳遇，唐书生的“白日梦”，其中可知士人内心的焦虑和渴望。狐女钟情志诚，令人感动。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (69, 'db4cc469bea84c258b36d0a8b0fbe758', '慕异记之杨祯', 0, 5, -1, '无', '进士杨祯寓昭应石瓮寺读书，夜遇红衣女子，自云燧人氏后，愿自荐枕席。夜至晨去，风雨愆期。后杨祯将此事告知乳母，母潜佛榻下窥之，红衣女原为佛前油灯。遂扑灭，女自此未至。', '进士杨祯寓昭应石瓮寺读书，夜遇红衣女子，自云燧人氏后，愿自荐枕席。夜至晨去，风雨愆期。后杨祯将此事告知乳母，母潜佛榻下窥之，红衣女原为佛前油灯。遂扑灭，女自此未至。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (70, 'd7d91d0ba04c4a878b9de68d04b53944', '前定录之马游秦', 36, 5, -1, '无', '吏部令史马游秦，开元中，以年满当选。时侍郎裴光庭，以本铨旧吏。问其所欲，游秦不对。固问之，曰：“某官已知矣，不敢复有所闻。”光庭曰：“当在我，安得之？”游秦不答，亦无惧色。光庭怒曰：“既知可以言乎。游秦曰：“此可誌之，未可言之。”乃命疏其目，藏于楹栋之间，期注唱后而发之。后老君见于骊山，銮舆亲幸其地，因改会昌县为昭应县。光庭以旧无昭应之名，谓游秦莫得而知也，遂补其县录事。及唱官之日，发栋间所誌之书，则如其言尔。', '吏部令史马游秦，开元中，以年满当选。时侍郎裴光庭，以本铨旧吏。问其所欲，游秦不对。固问之，曰：“某官已知矣，不敢复有所闻。”光庭曰：“当在我，安得之？”游秦不答，亦无惧色。光庭怒曰：“既知可以言乎。游秦曰：“此可誌之，未可言之。”乃命疏其目，藏于楹栋之间，期注唱后而发之。后老君见于骊山，銮舆亲幸其地，因改会昌县为昭应县。光庭以旧无昭应之名，谓游秦莫得而知也，遂补其县录事。及唱官之日，发栋间所誌之书，则如其言尔。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (71, '29ddf97d07e841e8a05d23ce8a969cd8', '奉和圣制温泉歌', 15, 61, 729, '729年', '有时神物待圣人，去后汤还冷，来时树亦春。今兹十月自东归，羽旆逶迤上翠微。温谷葱葱佳气色，离宫奕奕叶光辉。临渭川，近天邑，浴日温泉复在兹，群仙洞府那相及。吾君利物心，玄泽浸苍黔。渐渍神汤无疾苦，薰歌一曲感人深。', '有时神物待圣人，去后汤还冷，来时树亦春。<br>今兹十月自东归，羽旆逶迤上翠微。温谷葱葱佳气色，<br>离宫奕奕叶光辉。临渭川，近天邑，浴日温泉复在兹，<br>群仙洞府那相及。吾君利物心，玄泽浸苍黔。<br>渐渍神汤无疾苦，薰歌一曲感人深。', '无', '扈从幸骊山温泉作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (72, 'b88c46a267a84e60a2975a2e88b6657d', '奉和圣制途次陕州作', 15, 23, 725, '725年', '驰道当河陕，陈诗问国风。川原三晋别，襟带两京同。后殿函关尽，前旌阙塞通。行看洛阳陌，光景丽天中。', '驰道当河陕，陈诗问国风。川原三晋别，襟带两京同。<br>后殿函关尽，前旌阙塞通。行看洛阳陌，光景丽天中。', '中两联佳，用“三”“两”等数字词及“前”“后”等方位词写帝王出行仪仗之盛，使诗歌仪式化。“精英团体”的排他性语词，体现出应制诗的特点。', '扈从幸洛应制', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (73, 'f69c2f43e119426b906ce5252252d658', '奉和李令扈从温泉宫赐游骊山韦侍郎别业', 37, 61, 717, '717年', '风后轩皇佐，云峰谢客居。承恩来翠岭，缔赏出丹除。飞盖松溪寂，清笳玉洞虚。窥岩详雾豹，过水略泉鱼。乡入无何有，时还上古初。伊皋羞过狭，魏丙服粗疏。白雪缘情降，青霞落卷舒。多惭郎署在，辄继国风馀。', '风后轩皇佐，云峰谢客居。承恩来翠岭，缔赏出丹除。<br>飞盖松溪寂，清笳玉洞虚。窥岩详雾豹，过水略泉鱼。<br>乡入无何有，时还上古初。伊皋羞过狭，魏丙服粗疏。<br>白雪缘情降，青霞落卷舒。多惭郎署在，辄继国风馀。', '无', '开元间扈从玄宗幸温泉并访韦嗣立别业作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (74, 'bd3ea1028b64437a9fbafedaa2ce4e34', '饯唐州高使君赴任', 20, 3, 710, '710年', '淮源之水清，可以濯君缨。彼美称才杰，亲人伫政声。岁寒畴曩意，春晚别离情。终叹临岐远，行看拥传荣。', '淮源之水清，可以濯君缨。彼美称才杰，亲人伫政声。<br><br>岁寒畴曩意，春晚别离情。终叹临岐远，行看拥传荣。', '唐中宗景龙四年三月，高询赴任唐州刺史，诸学士设宴饯别，有诗唱和，因有此作。唐州，即现在的河南南阳一带。高询此次赴任的唐州境内，有桐柏山，恰好是淮河的发源地，故以此起兴。“濯缨”典故出自《孟子》，实际上是楚地歌谣，在《楚辞》中早有记载：“沧浪之水清兮，可以濯我缨；沧浪之水浊兮，可以濯我足。”这里用典的含义是比喻诗人远离世俗，清高自守的质量。颔联是作者对即将上任的唐州刺史高询的期待与告诫。作者期待才高八斗的友人能够在唐州任上作出一番业绩，为家人，友人称道。颈联两句对仗工稳。上一句念旧，怀念去年冬日作者与友人一起游乐的深厚情谊；下句言今，写今日别离，却在花落柳舞的长安的暮春时节，令人又增烦恼。尾联两句作者及时控制了自己因离别而感受到的消极情绪，反而劝勉即将赴任的友人：虽然此日过后，你还是感叹行路遥远，聚少离多。但你还是要看到此日过后，你就是受人拥戴的刺史长官了，难道这不会让你的心情好一点么？饯别高询的诗作一共有9首，这是第一首在诗尾安慰友人不要因为别离干扰了上任得官的好心情的作品。可见作者与友人的感情应该十分深厚。分别之时，才能讲出如此深入肺腑的话。', '景龙四年三月，诸学士送高询赴唐州刺史任。有诗相赠', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (75, '047f2f33f4cf498688c29fd0c77be2df', '饯唐州高使君赴任', 10, 3, 710, '710年', '饯酒临丰树，褰帷出鲁阳。蕙兰春已晚，桐柏路犹长。祖逖方城镇，安期外氏乡。从来二千石，天子命唯良。', '饯酒临丰树，褰帷出鲁阳。蕙兰春已晚，桐柏路犹长。<br><br>祖逖方城镇，安期外氏乡。从来二千石，天子命唯良。', '唐中宗景龙四年三月，高询赴任唐州刺史，诸学士设宴饯别，有诗唱和，因有此作。唐州，即现在的河南南阳一带。本诗最大的特点就是摈弃了其他送别诗中常见的对离愁别绪的渲染及描写，仅以对友人的劝诫及勉励灌注全篇，其言恳切，令人叹服。诗的前两联形式及表达内容一致。每一联的前一句都是点明送别的时间是花落的暮春时节，后一句皆是借助典故对友人的劝勉。“褰帷”一词用东汉贾琮典故。相传贾琮为冀州刺史时，乘车赴任时，曾撩开车帘，以广视听，体察民情，在当地引起很好的反响。后来文人用此典故表示官吏贤明，整肃弊端。本诗作者亦用此意，劝勉即将赴任的唐州刺史高询。诗的下两联依然是借典劝勉。诗的颈联借助东晋祖逖发誓收取中原的典故，劝勉友人在唐州任上有所作为。在给予了友人这么多劝勉之后，作者在诗的尾联表示了对友人的信任，认为友人有能力完成皇帝交付他的任务。', '景龙四年三月，诸学士送高询赴唐州刺史任。有诗相赠', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (76, 'd4dab3d018c1404a86c6145d097e13f9', '饯唐州高使君赴任', 38, 3, 710, '710年', '苍茫南塞地，明媚上春时。目极伤千里，怀君不自持。征车别岐路，斜日下崦嵫。一叹轺轩阻，悠悠即所思。', '苍茫南塞地，明媚上春时。目极伤千里，怀君不自持。<br>征车别岐路，斜日下崦嵫。一叹轺轩阻，悠悠即所思。', '唐中宗景龙四年三月，高询赴任唐州刺史，诸学士设宴饯别，有诗唱和，因有此作。唐州，即现在的河南南阳一带。本诗全写离愁别绪。首联写饯别时，先以高询赴任的唐州的苍茫景象与此日饯别时长安的明媚景象相对比起兴。颔联将时间推远，写离别之后，作者登高怀远，思念友人，怆然泪下的景象。如果说首联里作者的离愁别绪只是发端的话，那么颔联中作者的离愁则是发展到不能自持的地步了。颈联两句紧连上一联情思，感叹作者与友人相别的路程遥远，思念漫长。诗人在这一联试图收敛自己不能自持的情绪。在诗的尾联中，作者将浓郁的不能自持的离愁别绪收敛起来，浓缩成“悠悠”一词，极有韵味。如果说其他的同题饯别高询的作品都是程序化的作品的话（既有离愁的表达，又有对友人的期待、劝勉及担心。），那么岑义的这篇作品则是摒弃了程序化的创作，完全是自我感情的真实流露了。', '景龙四年三月，诸学士送高询赴唐州刺史任。有诗相赠', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (77, '8268db428d12467d96a0fb4dbd682f7b', '饯唐州高使君赴任', 22, 3, 710, '710年', '外牧资贤守，斯人奉帝俞。淮南膺建隼，渭北暂分符。坐叹烟波隔，行嗟物候殊。何年升美课，回首北城隅。', '外牧资贤守，斯人奉帝俞。淮南膺建隼，渭北暂分符。<br><br>坐叹烟波隔，行嗟物候殊。何年升美课，回首北城隅。', '唐中宗景龙四年三月，高询赴任唐州刺史，诸学士设宴饯别，有诗唱和，因有此作。唐州，即现在的河南南阳一带。本诗是十分标准的送别诗，短短四联，分别融入了说明分别缘由、对友人的期待以及分别时刻离愁别绪的描述。诗的前两联说明饯别高询的缘由。颔联中“建隼”本为直立的鸷鸟（猛禽的一种），诗人在此希望友人在唐州任上能够雷厉风行，努力干出一番政绩。“分符”本是指古代带兵打仗的凭证，太平时节一分为二，战争时候，皇帝的符与将军的符相互核验，即可出征作战。作者在此用此典故表示自己与友人即将分离。诗的颈联表现作者与友人离别后的愁思。作者感叹与友人分别甚远，就连气候都大有不同，这里暗含作者对友人的不舍。诗的尾联同其他送别作品一样，表示对友人的期待。“美课”一词在其他版本的作品中作“美政”，可以互解其意，意为出色的政治治理情况。作者在这里期待友人在唐州任上有一番作为，更期待友人能在某一天回首北方，远望长安。因为唐州在长安之南，故言“回首北城隅”。这里作者将对友人的期待及对友人的不舍情绪杂糅在一起，不可明分，以示情绪之重。', '景龙四年三月，诸学士送高询赴唐州刺史任。有诗相赠', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (78, '823a34bf4d7b45a1a8a5f67af911090c', '秋次霸亭寄申大', 39, 3, 753, '753年', '橘柚植寒陵，芙蓉蒂修阪。无言不得意，得意何由展。况我行且徒，而君往犹蹇。既伤人事近，复言天道远。薄暮入空亭，中夜不能饭。南听鸿雁尽，西见招摇转。千门汉王宫，百里周王苑。杲杲初景出，油油鲜云卷。会朝幸岁正，校猎从新獮。念君久京国，双涕如露泫。无人荐子云，太息竟谁辨。', '橘柚植寒陵，芙蓉蒂修阪。无言不得意，得意何由展。<br><br>况我行且徒，而君往犹蹇。既伤人事近，复言天道远。<br><br>薄暮入空亭，中夜不能饭。南听鸿雁尽，西见招摇转。<br><br>千门汉王宫，百里周王苑。杲杲初景出，油油鲜云卷。<br><br>会朝幸岁正，校猎从新獮。念君久京国，双涕如露泫。<br><br>无人荐子云，太息竟谁辨。', '开元十四年，储光羲进京赶考，此诗或作于此时。作品首两句先是写秋季景物以应题。“橘柚”、“芙蓉”等物皆是秋季产物，以此应题，最是恰当。以下两句是作者自叹之言。“无言”句是作者在考试之前，自怨自艾的感叹之言；“况我”句先写自己的境遇，再以此推及至申大友人，对友人的情谊从此可见；“既伤”两句是说自我沉溺于感叹之中不可自拔，又感到命运的不仁慈，遂有无奈之感产生。“薄暮”至“南听”句写作者秋天，独身旅居灞亭的感受。因受到旅居外地必然产生的思家情绪、即将考试的紧张心态的影响，作者寝食难安。“千门”至“会朝”句从皇宫内苑的活动写开元时节，唐帝国的兴盛场面。“念君”句至“无人”句是写作者想到友人旅居长安，联想至自己此时旅居灞亭，无依无靠，无人举荐，前途渺茫的境遇，不禁心生悲闷之感，诗意也随之自盛大景象的描述，写及自我心境的悲诉。正是由于前文写到皇宫内苑的盛大景象，此时再写作者独自一人的自哀自叹的景象，其中自我哀怨的情绪就在衬托中凸显出来了。', '唐玄宗天宝十二年，储光羲在长安。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (79, 'a97be091518d47d1bda1fbbe172a3853', '青门歌，送东台张判官', 6, 3, 753, '753年', '青门金锁平旦开，城头日出使车回。青门柳枝正堪折，路傍一日几人别。东出青门路不穷，驿楼官树灞陵东。花扑征衣看似绣，云随去马色疑骢。胡姬酒垆日未午，丝绳玉缸酒如乳。灞头落花没马蹄，昨夜微雨花成泥。黄鹂翅湿飞转低，关东尺书醉懒题。须臾望君不可见，扬鞭飞鞚疾如箭。借问使乎何时来，莫作东飞伯劳西飞燕。', '青门金锁平旦开，城头日出使车回。青门柳枝正堪折，路傍一日几人别。东出青门路不穷，驿楼官树灞陵东。花扑征衣看似绣，云随去马色疑骢。胡姬酒垆日未午，丝绳玉缸酒如乳。灞头落花没马蹄，昨夜微雨花成泥。<br>黄鹂翅湿飞转低，关东尺书醉懒题。须臾望君不可见，扬鞭飞鞚疾如箭。借问使乎何时来，莫作东飞伯劳西飞燕。', '唐玄宗天宝十二年，岑参在长安，有诗送张判官赴洛阳。《三辅黄图》中说，长安城东的第一座城门因颜色发青，所以百姓居民都称其为“青门”，这是这首作品的发生地。“青门”两句写张判官车马自长安出城，踏上返归洛阳的路途。接下来的几句主要写张判官离开长安时的绝美景色。此时正值春日，柳枝抽芽，唐人有“折柳送别”的习俗，更应此种送别的场景。“东出”至“黄鹂”几句随着张判官的视角，为我们勾勒出一副长安春景。灞陵上，花随风舞；酒市中，美姬当垆。此时正是全长安最欢乐的时节，作者送别友人，可以相见作者此时的心情。“关东”句以下，写作者对友人的嘱托及留恋之情。全诗虽为送别题材的作品，但是作者以其细致入微的笔端为我们描绘了一副长安春景，为我们留下了解读唐人生活的一项工具。', '753年，在长安，有诗送张判官赴洛阳。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (80, 'd005b79235dd442cbc417644514aa15a', '旅次灞亭', 40, 3, -1, '年份未详', '闲琴开旅思，清夜有愁心。圆月正当户，微风犹在林。苍茫孤亭上，历乱多秋音。言念待明发，东山幽意深。', '闲琴开旅思，清夜有愁心。圆月正当户，微风犹在林。<br><br>苍茫孤亭上，历乱多秋音。言念待明发，东山幽意深。', '此诗具体创作年份无考，当是作者旅居灞亭，一夜无声，抚琴见月，有感而作。作品首联先以抚琴起兴，引出下文抒发的乡愁。作者在灞亭驿站暂住一晚，闲来无事，恰逢明月清风，于是在驿站中抚琴聊以自娱，消解因旅居外地多年产生的乡愁。颔联写驿站外景色。“圆月”写其明，“微风”状其柔。这虽然是驿站外客观景象的真实书写，写景物之柔美，景色之明丽，但却是作者心中杂乱心绪的艺术写照，作者近乡情怯，心情多有不平，所以才寄怀于柔美明丽的景物景色之间，是排解消遣之法。颈联两句写作者望远所见所闻：见万里苍茫，唯有自己这一座孤亭，心生漂泊孤独之感；闻冽冽秋风，多有唤己回家之意，心中又泛起乡愁。尾联两句是作者自我安慰句，紧接上文的情绪抒发，言明日即可回家，今日不必担心意。此句用《诗经》中《东山》典故也是此意。全诗中规中矩，有开有合，既有抒情，又有曼妙的景色描绘。尤其是颔联两句，读来自觉身临其境，如见此景。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (81, 'b10de31faf9d4297ad85a0a8baa46463', '送萧颖士赴东府，得路字', 41, 3, 753, '753年', '子欲适东周，门人盈歧路。高标信难仰，薄官非始务。绵邈千里途，裴回四郊暮。征车日云远，抚已惭深顾。', '子欲适东周，门人盈歧路。高标信难仰，薄官非始务。<br><br>绵邈千里途，裴回四郊暮。征车日云远，抚已惭深顾。', '萧颖士，颍州汝阴人，以推引后进为己任，故名重当时，门人称其为“萧夫子”。唐玄宗天宝年间，任集贤校理。因不肯依附李林甫，受其排斥，天宝十二年时出为河南参军。离京时，门人相送，唱和作诗，此诗即为当时所作。诗的首联写此次作诗的因由及门人相送的壮观场面“门人盈歧路”。“东周”曾定都洛阳，在此代指河南。颔联赞颂萧颖士的德行及政治理想。“高标”一词出自《世说新语》《德行》篇：“李元礼风格秀整，高自标持。”一句，后以“高标”指清高脱俗的风范。“薄官”是言萧颖士品性高尚，却无人重用，一直在朝担任小官。诗的下两联写萧颖士此次出行千里，门人对萧颖士的思念感伤之情。', '天宝十二年，萧颖士调河南参军，门人有诗相赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (82, 'f991f3632b1b4388a6a77ee6cbfdaa83', '送萧颖士赴东府，得适字', 42, 3, 753, '753年', '大名掩诸古，独断无不适。德遂天下宗，官为幕中客。骊山浮云散，灞岸零雨夕。请业非远期，圆光再生魄。', '大名掩诸古，独断无不适。德遂天下宗，官为幕中客。<br><br>骊山浮云散，灞岸零雨夕。请业非远期，圆光再生魄。', '唐玄宗天宝年间，萧颖士任集贤校理。因不肯依附李林甫，受其排斥，天宝十二年时出为河南参军。离京时，门人相送，唱和作诗，此诗即为当时所作。全诗根据内容可大致分为两部分。诗的前两联赞颂萧颖士的德行及政治成就。在德行上，作者言其名气与诸多古人有过之而无不及；政治成就上，虽然萧颖士一生多在野担任幕府参军等职，但其不远在朝与李林甫等辈同流合污的高尚情操则是作者想要赞颂的一方面。第二部分是全诗的后两联，主要写对萧颖士此去的怀念以及由此而发的对萧颖士回归的期待。颈联主要是描写萧颖士此去洛阳道上的风光景物，力在表达对对萧颖士此去的担忧及怀念之情。诗的尾联主要是以弟子身份表达对老师再次回归长安的期待。其中，“请业”是指向老师请教学问，“非远期”则是说作者对老师再次回归的期待。“圆光”一词是佛教名词，此言老师对弟子的关照。“再生魄”老师对弟子的再度关照，表达意思与“请业”句相同。', '天宝十二年，萧颖士调河南参军，门人有诗相赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (83, '7e412df38e854d90856d59e57b8f40ab', '送萧颖士赴东府，得离字', 43, 3, 753, '753年', '大德讵可拟，高梧有长离。素怀经纶具，昭世犹安卑。落日去关外，悠悠隔山陂。我心如浮云，千里相追随。', '大德讵可拟，高梧有长离。素怀经纶具，昭世犹安卑。<br><br>落日去关外，悠悠隔山陂。我心如浮云，千里相追随。', '唐玄宗天宝年间，萧颖士任集贤校理。因不肯依附李林甫，受其排斥，天宝十二年时出为河南参军。离京时，门人相送，唱和作诗，此诗即为当时所作。全诗根据内容可大致分为两部分。诗的前两联赞颂萧颖士的德行。首联言作者认为萧颖士的德行高尚已经没有物体或者古人可与之比拟。颔联言萧颖士通古博今，满腹经纶，即使是在开元天宝这样的繁荣盛世依旧保持着“安卑”的初心。诗的后两联是第二部分，主要是写萧颖士此去路途遥远，作者作为门人弟子愿意千里追随的深厚情谊。', '天宝十二年，萧颖士调河南参军，门人有诗相赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (84, '3fb8e87afea8400699ebab7d949ce2ec', '送萧颖士赴东府，得引字', 44, 3, 753, '753年', '吾见夫子德，谁云习相近。数仞不可窥，言味终难尽。处喧虑常澹，作吏心亦隐。更有嵩少峰，东南为胜引。', '吾见夫子德，谁云习相近。数仞不可窥，言味终难尽。<br><br>处喧虑常澹，作吏心亦隐。更有嵩少峰，东南为胜引。', '唐玄宗天宝年间，萧颖士任集贤校理。因不肯依附李林甫，受其排斥，天宝十二年时出为河南参军。离京时，门人相送，唱和作诗，此诗即为当时所作。此诗不同于其他门人所作，仅关注萧颖士的德行情操。全诗的前两联从总的方面赞颂萧颖士的德行高尚，将其德行比作山峰，则“数仞不可窥，言味终难尽。”与长孙铸同题作品中“大德讵可拟”一句有异曲同工之妙。诗的颈联从萧颖士德行的一个方面入手，赞颂其为官作吏不愿身陷官场污浊的不同流合污的高尚情操。诗的尾联再度以河南境内嵩山、少室山两座著名山峰与萧颖士的高尚德行比拟。一则写出萧颖士此次出行的目的地，二则与全篇内容构思保持一致，精妙至极。', '天宝十二年，萧颖士调河南参军，门人有诗相赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (85, 'c98b68e810a946da85d136bbc9710c23', '送萧颖士赴东府，得浅字', 45, 3, 753, '753年', '吾师继微言，赞述在坟典。寸禄聊自资，平生宦情鲜。逶迟东州路，春草深复浅。日远夫子门，中心曷由展。', '吾师继微言，赞述在坟典。寸禄聊自资，平生宦情鲜。<br><br>逶迟东州路，春草深复浅。日远夫子门，中心曷由展。', '唐玄宗天宝年间，萧颖士任集贤校理。因不肯依附李林甫，受其排斥，天宝十二年时出为河南参军。离京时，门人相送，唱和作诗，此诗即为当时所作。此诗根据内容可分为三部分。第一部分为首联。主要写萧颖士的学术成就。萧颖士曾因“昔左氏失于烦，谷梁失于短，公羊失于俗”而折衷重修《春秋》，所以诗中言“吾师继微言，赞述在坟典。”诗的第二部分在于颔联，主要写作者对萧颖士高尚品德的赞颂。因萧颖士官运未曾显大，此次出京又是不愿依附李林甫等人导致的，所以作者赞颂其“穷则独善其身”以及不愿失德以致宦途显达的高尚情操。全诗第三部分则是表达作者送别萧颖士时的不舍以及将别未别之时的怀念之情。颈联中“春草”一词常用来表示离愁别绪，例如《楚辞》中：“王孙游兮不归，春草生兮萋萋。”一句。诗的尾联则用疑问词“曷”字表现作者对即将离别的萧颖士的怀念。', '天宝十二年，萧颖士调河南参军，门人有诗相赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (86, 'dad51cf04ea7429884d87a2233a881dc', '送萧颖士赴东府，得草字', 46, 3, 753, '753年', '天生良史笔，浪迹擅文藻。中夏授参谋，东夷愿闻道。行轩玩春日，饯席藉芳草。幸得师季良，欣留箧笥宝。', '天生良史笔，浪迹擅文藻。中夏授参谋，东夷愿闻道。行轩玩春日，饯席藉芳草。幸得师季良，欣留箧笥宝。', '唐玄宗天宝年间，萧颖士任集贤校理。因不肯依附李林甫，受其排斥，天宝十二年时出为河南参军。离京时，门人相送，唱和作诗，此诗即为当时所作。此诗根据内容可分为三部分。全诗的第一部分为前两联，赞颂萧颖士的文学与学术成就以及高尚的品德情操。“天生良史笔”一句言萧颖士除了重修《春秋》，又作《伐樱桃赋》以刺李林甫。颔联是说萧颖士高尚的品德影响之大，已达东夷。诗的第二部分是描绘天宝十二年，门人弟子为萧颖士饯别的场景。此句与刘舟的序中描述的“春云轻阴，草色新碧”的饯别场景相一致，大概可以推断萧颖士东去是在春日。诗的第三部分言老师对自己的厚爱以及关心，并且表达作者对老师的感恩之情。', '天宝十二年，萧颖士调河南参军，门人有诗相赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (87, '267e1def15ba458b810c5eccf799ebc8', '送萧颖士赴东府，得往字', 47, 3, 753, '753年', '斤溪数亩田，素心拟长往。繄君曲得引，使我缨俗网。风尘岂不劳，道义成心赏。春郊桃李月，忍此戒征两。', '斤溪数亩田，素心拟长往。繄君曲得引，使我缨俗网。<br><br>风尘岂不劳，道义成心赏。春郊桃李月，忍此戒征两。', '唐玄宗天宝年间，萧颖士任集贤校理。因不肯依附李林甫，受其排斥，天宝十二年时出为河南参军。离京时，门人相送，唱和作诗，此诗即为当时所作。此诗与其他门人的同题作品多有不同，没有过多地赞颂萧颖士的高尚品德以及怀念即将离别的萧颖士，而是用诗笔叙述出作者因萧颖士举荐作官的经历，并继而表达自己对萧颖士的感恩之情。诗的首联当写作者在作官之前的心之所向，可能是说如果没有恩师的指点，自己只能是个心系农田的田舍老翁罢了。颔联言作者因萧颖士举荐得官的经历。萧颖士因以推引后进为己任，而备受时人称赞，名重当时。此篇作品的作者也当是受到萧颖士引进的后进之一。颈联一句言萧颖士不仅在政治上推荐后进，而在道德修养上也影响着具有“成心”的后进们，使其成为具有完美人格的人。诗作的尾联点出送别的时间，并含蓄地表达了自己对即将离别的萧颖士的离愁别绪。', '天宝十二年，萧颖士调河南参军，门人有诗相赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (88, 'fbcfee6c0e0e437bb0d0457fa1427a3b', '送萧颖士赴东府，得散字', 48, 3, 753, '753年', '官闲幕府下，聊以任纵诞。文学鲁仲尼，高标嵇中散。出门时雨润，对酒春风暖。感激知己恩，别离魂欲断。', '官闲幕府下，聊以任纵诞。文学鲁仲尼，高标嵇中散。<br><br>出门时雨润，对酒春风暖。感激知己恩，别离魂欲断。', '唐玄宗天宝年间，萧颖士任集贤校理。因不肯依附李林甫，受其排斥，天宝十二年时出为河南参军。离京时，门人相送，唱和作诗，此诗即为当时所作。全诗可根据内容分为两部分。第一部分自首联至颈联。主要写萧颖士官卑德高，文学成就可比鲁国孔仲尼；道德情操可比晋国嵇康。古人信奉一人的德行可以感动天地，所以萧颖士出门即有润雨，饮酒是即有暖风拂面，言萧颖士道德情操高尚。诗的第二部分即是尾联一句。表现作者对萧颖士的知己之恩，并且表达作者对萧颖士离别后的离愁别绪。', '天宝十二年，萧颖士调河南参军，门人有诗相赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (89, '699c01c5cc7d4598b9a3c77f8a85e5dd', '送萧颖士赴东府，得君字', 49, 3, 753, '753年', '策名十二载，独立先斯文。迩来及门者，半已升青云。青云岂无姿，黄鹄素不群。一辞芸香吏，几岁沧江濆。散职既不羁，天听亦昭闻。虽承急贤诏，未谒陶唐君。薄俸还自急，此言那足云。和风媚东郊，时物滋南薰。蕙草正可摘，豫章犹未分。宗师忽千里，使我心氛氲。', '策名十二载，独立先斯文。迩来及门者，半已升青云。<br><br>青云岂无姿，黄鹄素不群。一辞芸香吏，几岁沧江濆。<br><br>散职既不羁，天听亦昭闻。虽承急贤诏，未谒陶唐君。<br><br>薄俸还自急，此言那足云。和风媚东郊，时物滋南薰。<br><br>蕙草正可摘，豫章犹未分。宗师忽千里，使我心氛氲。', '唐玄宗天宝年间，萧颖士任集贤校理。因不肯依附李林甫，受其排斥，天宝十二年时出为河南参军。离京时，门人相送，唱和作诗，此诗即为当时所作。全诗可根据内容分为三部分。第一部分为诗的前两句，总说萧颖士门人的成就。第二部分为诗的第四、六、八句。这一部分写萧颖士虽然辞去“芸香吏”（校书郎的别称，因萧担任集贤校理，故称。）但是依旧不能掩盖萧颖士高洁的品德及令人赞叹的学术能力。其中，“黄鹄”又称“鸿鹄”，比喻能力很强的贤能人才，此指萧颖士。诗的第三部分为第十句结尾部分。“蕙草”、“豫章”一为草，一为树，都是比喻贤能有才干的人才。这是说萧颖士门下学生还未成才，老师就被迫东行，这使得作者心中感到“氛氲”，也就是心绪杂乱的情状。此句既表达了作者对恩师的感恩，也表达了作者对恩师的不舍之情。', '天宝十二年，萧颖士调河南参军，门人有诗相赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (90, '36e5df096b2d4ba8b2390a52c2b22219', '送钟评事应宏词下第东归', 50, 3, -1, '年份未详', '芳岁归人嗟转蓬，含情回首灞陵东。蛾眉不入秦台镜，鹢羽还惊宋国风。世事悠扬春梦里，年光寂寞旅愁中。劝君稍尽离筵酒，千里佳期难再同。', '芳岁归人嗟转蓬，含情回首灞陵东。蛾眉不入秦台镜，鹢羽还惊宋国风。世事悠扬春梦里，年光寂寞旅愁中。劝君稍尽离筵酒，千里佳期难再同。', '此诗具体创作年份无考，当是作者送下第友人离京所作。友人离京，作者相送，自有一番离愁别绪在心间；友人又是在科举落榜的情况下失意离京，于是作者在创作作品时，不仅抒发对友人的不舍，还多处以典故、语言勉励友人，表现自己对友人失意经历的同情。作品首联以友人视角写就：友人正值芳年，却失意落榜，正如无处安放的蓬草一般，今日又要离京，失意之中，只能回望长安，无奈离去。颔联两句用典，表现作者对友人经历的同情。首句先以“蛾眉”之美貌比喻友人之才高，后用“秦镜”典（秦宫有镜，明察秋毫，可照人心腹）是说友人才高却不能为朝廷所录用；此句用“鹢鸟退飞”的典故言友人落榜后出京之事。这两句话先是写作者为友人遭遇有打抱不平之意，后写作者知友人离去而产生的同情心。颈联两句安慰友人，言每人都经历过这样的落榜经历，不必为此烦恼。尾联两句从安慰、勉励友人的语境中脱离，只言送别事，实际上是作者转移友人注意力的一种方式。全诗从同情到勉励再到安慰最后以离愁转移友人的失意情绪，作者为排解友人情绪所作的努力都付诸文字，我们读此可以感受到作者同友人的深厚友谊。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (91, 'f992a41850364482b458b7a3623ebdfc', '赠万生下第还吴', 51, 3, -1, '年份未详', '直道多不偶，美才应息机。灞陵春欲暮，云海独言归。为客成白首，入门嗟布衣。莼羹若可忆，惭出掩柴扉。', '直道多不偶，美才应息机。灞陵春欲暮，云海独言归。<br><br>为客成白首，入门嗟布衣。莼羹若可忆，惭出掩柴扉。', '此诗创作年份无考。盖因友人落第返家而作。作品首联言仕途多有不顺，劝勉友人拾起信心，另谋别的出路。“息机”一词出自《楞严经》，作者以此劝勉友人放弃科举进仕，另谋别的人生路径。颔联两句写景，极为广阔。时值暮春，作者同友人登高凌远，视天地四方，云气纵生。作者此谓天地宽广，独友人仕途不顺，究其原因，难以明说，似云海一般。体现作者对友人经历的同情。颈联两句继续对友人多年考试的艰辛经历予以同情心。首句写友人多年在外准备考试，青丝白发，古卷寒灯，写其艰辛；次句言友人落第，多年辛酸，付之东流，写作者同情之心。最后一联仍写同情。首句反用“莼羹”典故：“莼羹”出自晋代张翰典故，言张翰在外做官，因思念家乡美食，辞官还家。后来用此典故赞美不追求名利之人。首句中，作者反用此典，是站在友人的角度考虑的。友人此时落第，心里除了失望外，还有对登第的向往，作者正是领会到友人的心情，才说出“如果真的因为莼羹回家，你肯定会羞愧的不敢出门”这句话，充分体现作者对友人无微不至的关怀。全篇作者以体己之心，度友人心思，显示出作者同友人深厚的感情。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (92, 'a4523a74d4f6478f8e2e79fdfe534a4c', '送友人东归', 52, 3, 762, '约762年', '万里杨柳色，出关送故人。轻烟拂流水，落日照行尘。积梦江湖阔，忆家兄弟贫。裴回灞亭上，不语自伤春。', '万里杨柳色，出关送故人。轻烟拂流水，落日照行尘。<br>积梦江湖阔，忆家兄弟贫。裴回灞亭上，不语自伤春。', '唐代宗宝应元年，戴叔伦在京因得刘晏举荐，任秘书正字。此诗或作于此时。全诗根据内容可分为三部分内容。第一部分为诗的首联，主要点明创作此诗的因由以及送别友人的时间是杨柳抽芽的初春时间。诗的第二部分内容是诗作的颔联部分，这两句写得平淡优美，真实再现了送别友人时，驿站旁边繁忙的送别场景。第三部分为全诗的后两联部分。这两联由友人离别返家一事起兴，引起作者对家乡的思念情感。此时的戴叔伦虽然经举荐得官，但长年旅居长安的生活，使其终究难以忘怀对家乡的思念。“江湖阔”一句写尽戴叔伦此时思家却不能归家的无奈之情。诗的尾联以伤春情感作结，这是古人常用的“退回一步”写法，目的是避免在诗作的结尾流露太多真实直白的情感，使得全诗流于直白浅显。比如此句，上一句作者还沉浸在对家乡的思念之中，这一句作者却说自己此时的伤感情绪是伤春所致，婉转至极。', '唐代宗宝应元年，戴叔伦在京因得刘晏举荐，任秘书正字', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (93, '1402073d896f4ff4b337f0aa8e99d0c5', '灞岸别友', 52, 3, 770, '约770年', '车马去迟迟，离言未尽时。看花一醉别，会面几年期。樵路高山馆，渔洲楚帝祠。南登回首处，犹得望京师。', '车马去迟迟，离言未尽时。看花一醉别，会面几年期。<br>樵路高山馆，渔洲楚帝祠。南登回首处，犹得望京师。', '唐代宗大历五年春，戴叔伦经刘晏举荐，自秘书正字，拜湖南监察御史。此诗或作于戴叔伦离京时。这篇作品全写诗人在离别长安时的自我感受。首联写“车马去迟迟”是言作者对长安的不舍；“离言”是写作者对友人的不舍。颔联两句写离别友人时心中所想，作者因想到此次一别，不知何时再能重见古人，所以更显出作者对友人的不舍之情及浓郁离愁。诗作的后两联以作者此去的路程为序。“高山馆”应指商山附近的馆驿；“楚帝祠”是言祭祀楚帝的祠堂，这里应该是在诗人此次赴任的湖南境内了。尾联两句写作者到了湖南任上，再度回首北方，依然眺望着长安的方向。从戴叔伦的其他作品可以得知，戴叔伦早年家贫。虽然进入萧颖士门下学习，但与萧颖士的其他门生相比，戴叔伦成才较晚，直到宝应元年，他三十一岁时才蒙举荐得官。可以说，戴叔伦的仕途并不平坦。此次灞岸别友，又作于戴叔伦升任湖南监察御史之时。所以，混迹长安多年最终得官，现又升官，这种人生变化，必然使戴叔伦对刘晏及皇帝，心怀感激。但又不好明说，所以只能几度回望“京师”表示自己的感激之情。', '唐代宗大历五年春，戴叔伦经刘晏举荐，自秘书正字，拜湖南监察御史。此诗或作于戴叔伦离京时。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (94, '882d3824d0354bcfae51e08ace2f2e8d', '春日灞亭同苗员外寄皇甫侍御', 1, 3, 771, '771年（大历六年）', '坐见春云暮，无因报所思。川平人去远，日暖雁飞迟。对酒山长在，看花鬓自衰。谁堪登灞岸，还作旧乡悲。', '坐见春云暮，无因报所思。川平人去远，日暖雁飞迟。<br><br>对酒山长在，看花鬓自衰。谁堪登灞岸，还作旧乡悲。', '唐代宗大历六年春，与苗发同登灞亭，睹景思人，因有此作。全诗虽写怀人，实则是发时光之嗟。作品首联写春云起兴。因见春云，感怀时间飞速，遥想当年往事，思念旧友。“无因”句写作者心中郁郁凝结的相思情绪无法排解，令人读来愁苦。颔联两句写久远送别之事。遥想当年，征人远去，车马嘶鸣声还不绝于耳，此日却不能见到这景象，令作者心生感叹。颈联两句亦是感叹时光流逝。作者在与花对视的时候，心理上同花建立起同构关系，将自己无法阻挡的年华消逝过程同花朵凋谢的过程相比拟，令人在感叹落花的无奈，凄凉意味的同时，也能感受到年华消逝时的无奈、凄凉。就是在这样的无奈、凄凉情绪的驱使下，作者登上这个多次被人写及的送别地点——灞岸，最终生发出对家乡的怀念感情。而无论是怀念旧友，还是感叹生命，还是怀念家乡，都是建立在作者对时间流逝的深刻领悟基础上的。', '771年春日，同苗发登灞亭。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (95, 'f7c99560b49a4fd68d250789b6997006', '途中寄李二', 53, 3, -1, '年份未详', '杨柳含烟灞岸春，年年攀折为行人。好风若借低枝便，莫遣青丝扫路尘。', '杨柳含烟灞岸春，年年攀折为行人。<br>好风若借低枝便，莫遣青丝扫路尘。', '此诗一说为戎昱作。应是作者途经灞岸，目睹春色，有感而作。作品的第一句描绘灞河春日清晨，岸边雾气蒙蒙的美丽景象，作者观其景象，心有所感，故以此起兴。第二句写作者看到灞岸柳的感受：看到柳树，联想至唐代折柳送别的风俗，进而萌生出送别时常有的伤感情绪。作品的三四句承接这种情绪，书写作者对春风的请求，饶有趣味。“低枝”是指柳树上生长的离地面较近的树枝，这种树枝凭借春风的便利，在被吹拂的同时，也轻扫地面，将离人归去的痕迹也一扫而空，令人无法睹迹思人。出于这样的思考，作者向春风发出请求，请求它不要将痕迹扫空，令人无处怀念。全诗四句，只有这两句最不平常。作者直接和春风对话，显示出作者潇洒的气魄；向春风提出看似可爱实则无理的请求，反映出作者浪漫的情怀。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (96, '35a61107d988469eb67b47316fea9c81', '送丁少府往唐上', 54, 3, 768, '768年至770年', '因君灞陵别，故国一回看。共食田文饭，先之梅福官。江风转日暮，山月满潮寒。不得同舟望，淹留岁月阑。', '因君灞陵别，故国一回看。共食田文饭，先之梅福官。<br><br>江风转日暮，山月满潮寒。不得同舟望，淹留岁月阑。', '唐代宗大历三年至大历五年间，李端逗留长安数年，以干谒名家以求仕途。此作当是其逗留长安期间与他人酬唱赠别的作品。全诗从送别时对友人的嘱托期许写起，写及作者此时逗留长安的心迹。作品首联写送别时，作者同友人环顾望家乡的场景。虽然只是离别时候的寻常举动，但联系作者此时已经因为科举的原因逗留长安数年的处境，就可以体会作者此时返乡还家的心理愿望。颔联两句借用“孟尝君”的典故暗示李端与友人曾同为门生，只是友人比自己先得到官职，自己此时还未及第。身份命运的疏忽有别，令作者在为友人欣喜的同时也感叹自己时运不济，命运不佳。颈联两句借写景抒情，可看作是作者从对友人的书写转向对自我心迹表达的一次转折。承接前文生发的感叹时运不济的情绪，作者在尾联抒发出同友人分别，自己依然逗留长安的无奈与苦楚情绪。没有勉励与激励，在诗作的最后一联作者抒发的是一种无能为力的无奈情绪。这种无奈情绪是受到衰败的国运、仕途的艰涩以及个人生命经历影响而成的，这也是大历诗人普遍具有的一种情绪。', '李端于大历五年进士及第，授官，逗留长安数年，此作当是他在大历五年至大历十四年间与他人酬唱赠别的作品。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (97, 'f38aaec43c5d495889fa964e814b0709', '留别故人', 54, 3, -1, '年份未详', '此别不可道，此心当语谁。春风灞水上，饮马桃花时。误作好文士，只应游宦迟。留书下朝客，我有故山期。', '此别不可道，此心当语谁。春风灞水上，饮马桃花时。<br><br>误作好文士，只应游宦迟。留书下朝客，我有故山期。', '李端晚年曾隐居衡山，此作或作于隐居衡山前后。此作虽为送别故人的作品，却借送别故人书写自我心迹，与前作《送丁少府往唐上》同一窠臼。诗作的首联即表现出作者借此诗抒发心迹的端倪。句中的“心”字当是作者自我的心迹表现。颔联两句描绘作者送别故人的场景，与其他作者相同，李端此处也是选用了温柔和美的意象入诗，营造静美浪漫的春景，掩饰离别时候应有的愁苦情绪，令人读来赏心悦目，如景在眼前，却又感觉有愁苦情绪，蕴藉之至。诗作的下半部分应是作者的心迹表达。颈联两句作者讲自己在元和五年才进士及第，远游作官的经历耽误了作者生命中大部分时间，作者为其愤懑不平。诗作的最后一联中，作者为这种不满情绪找到了表达的突破口。作者在最后一联言自己有归隐之心，将俗务抛诸脑后，一心只关注自我修养。从这首诗与前作相对比，可以明显看出李端从进士及第到为官多年这段时间里的心迹变化：李端再也不是那个逗留长安多年，自怨自艾无能为力的青年士子，而成长为一个追求自我内心世界满足，自我内心世界超越的世外高人，这是令人读来欣喜的一面。', '李端晚年曾隐居衡山，此作或作于此时期前后。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (98, '48396daaa6b847fa9343133af3834b88', '送别', 55, 3, -1, '年份未详', '灞岸青门有弊庐，昨来闻道半丘墟。陌头空送长安使，旧里无人可寄书。', '灞岸青门有弊庐，昨来闻道半丘墟。陌头空送长安使，旧里无人可寄书。', '此诗的具体创作年份无考，应作于刘商长安为官期间。同其他的大历诗人相同，刘商在这首作品中也书写了受到国运衰败，个人命运不济影响而成的愁苦情绪。这样的愁苦情绪体现在诗作多处。在意象的选用上，诗人选择用“弊庐”、“半丘墟”等具有破败凋敝意味的意象描绘送别场景。这或许是作者的真实书写，或者是作者心绪的体现，但这种意象所发生的愁苦情绪真真切切能够被读者所感知。不仅如此，作者还用“空送”来写自己送别友人的情景。这意味着，此次的送别没有盛大的帷幕，也没有饯别的酒杯，仅仅是两个人站在灞河边握手送别。这个词从另一侧面显示出刘商此时经济上的窘迫，囊中羞涩，因此只能“空送友人”，这又是诗中愁苦情绪的体现。如果说，前几句只是作者有意为之，因情造景的话，那么诗作的最后一联就是作者内心的真实书写。作者在这一句中书写家乡无亲友，自己逗留长安，因无人寄书而产生的飘零之感，这也是一种愁苦情绪的体现。', '此诗的具体创作年份无考，应作于刘商长安为官期间。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (99, '4716934869d94404bdcd8272c02950dd', '送刘秀才南归', 56, 3, -1, '年份未详', '鸟啼杨柳垂，此别千万里。古路入商山，春风生灞水。停车落日在，罢酒离人起。蓬户寄龙沙，送归情讵已。', '鸟啼杨柳垂，此别千万里。古路入商山，春风生灞水。<br><br>停车落日在，罢酒离人起。蓬户寄龙沙，送归情讵已。', '此诗创作年份无考，盖因刘秀才及第归省，作者相送，因有此作。作品首联言送别景色，借用春景起兴。首句写鸟鸣柳舞，一片可爱动人的春日景象；此句写作者同友人送别，此为乐景衬哀情法。面对春景，本应是喜悦的情绪，次句作者又言离别，离别又是具有强烈伤感情绪的活动。离别同春景相合，本来离别所具有的伤感情绪上又加一层因春景送别，浪费时光的失落感受，情绪更有力度，更能让读者读来有感，所以唐人作诗经常用此范式。颔联两句写友人此去的路程。颈联两句接着写送别场景。作者同友人一路自城内出发，出游之灞陵离别之地，已是傍晚黄昏，简单地饮酒之后，接着就是残酷的离别时刻。从此句看，作者同友人的相聚时间是短暂的，就是这短暂的时间令读者读来生感，有浓烈的不舍情绪涌上心头。尾联两句是作者对友人说的嘱托的话语，体现作者同友人的深厚友谊。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (100, 'e52c1dd0014e4427afc294982160631d', '送唐次', 57, 3, -1, '年份未详', '都门去马嘶，灞水春流浅。青槐驿路长，白日离尊晚。望望烟景微，草色行人远。', '都门去马嘶，灞水春流浅。青槐驿路长，白日离尊晚。<br><br>望望烟景微，草色行人远。', '此诗创作年份无考，应是作者送别友人时的作品。作品的前两句描绘离别的场景起兴。此日应是春日，灞水呜咽，去马嘶鸣，一声一鸣都牵动著作者因送别友人而泛起的离愁。中间两句亦是描绘送别场景。写作者同友人在灞岸边设宴畅谈，不经意间时间已经到了分手的时候，作者同友人一起望着即将踏上的远去的驿路，不免心中忧伤有所加深。最后两句以作者的视角言送别。此句中，友人早已离开，送别处只剩下作者一人面对友人离去的方向，空怅望。“微”字写作者眺望，极目远眺，看远处的景色已是微微可见。除了写出作者远眺的结果外，这个字还写出作者极目远眺的姿态，暗示著作者与友人的情谊深浓。全诗没有一句言情，却在写景的句子中，处处言情，含蓄至极。', '此诗创作年份无考，应是作者送别友人时的作品。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (101, 'af8c3348c090440aa5bc353aa6b7412a', '送严秀才', 57, 3, -1, '年份未详', '灞浐别离肠已断，江山迢递信仍稀。送君偏下临岐泪，家在南州身未归。', '灞浐别离肠已断，江山迢递信仍稀。送君偏下临岐泪，家在南州身未归。', '此作年份无考，应是作者送别严秀才的作品。诗作的前两句写作者送别友人至灞岸，联想起此地是与浐水并称的来往送别之胜地，不禁心有所感，写下“肠已断”句排解自己的心中感受。此时作者眺望远山，远方的亲友没有一封书信寄来，从而加深了这种感受。诗作的后两句同样写送别。作者送别友人，将别未别时，想到两人从此身逢歧路，心在两处，一股离愁涌上心头。但这样的离愁被作者及时处理为劝勉语，作者劝勉友人应早日回家才是。全诗语言秀丽婉约，虽言送别，却不见离愁的直接表达，委婉之至，令人读来心有所感，似分别场景就在眼前。', '此作年份无考，应是作者送别严秀才的作品。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (102, 'c304d20292d74513af5239395b0f885e', '诏追赴都二月至灞亭上', 58, 3, 815, '815年', '十一年前南渡客，四千里外北归人。诏书许逐阳和至，驿路开花处处新。', '十一年前南渡客，四千里外北归人。诏书许逐阳和至，驿路开花处处新。', '唐宪宗元和十年，柳宗元奉诏还京，途中有感所作。唐德宗贞元二十一年，因受到永贞革新的影响，柳宗元先是被贬邵州刺史，又在途中被贬为永州司马，开始了长达十年的贬所生活。元和十年，柳宗元奉诏还京。作品的前两句，作者就以极为简短的话语概括了自己这段时间的遭遇。在前两句中，作者以“南渡客”同“北归人”相对，“十一年”与“四千里”相对，将近十年的贬谪生活的喜怒悲欢都寓于其中，语言简练，却意味深浓。作品的后两句写自己奉诏得还长安，在返京途中的喜悦情绪。此时正是二月，虽然已有春意，但初春还是很寒冷，而作者在作品中写道“阳和至”，这是说得到诏书的一瞬间，作者的生活就象是充满了春意一般，温暖备至。在返京途中，作者也难掩返京的喜悦，写到“驿路开花处处新”，我们不能否认这是自然现象，但必须要承认这句话中更多地是掺杂了作者此时喜悦难耐情绪写就的。全诗逻辑严密，先讲自己十年贬所生活，再述自己的喜悦心情。我们在得知柳宗元此时的喜悦心情的同时，还能体会到十年贬所生活为其带来的心灵上的折磨。', '元和十年，柳宗元奉诏还京，途中有感而作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (103, '083e8b5039b54d889a214e317445525a', '请告东归发灞桥却寄诸僚友', 59, 3, 797, '约797年', '征徒出灞涘，回首伤如何。故人云雨散，满目山川多。行车无停轨，流景同迅波。前欢渐成昔，感叹益劳歌。', '征徒出灞涘，回首伤如何。故人云雨散，满目山川多。<br>行车无停轨，流景同迅波。前欢渐成昔，感叹益劳歌。', '唐德宗贞元九年至贞元十一年间，刘禹锡先是进士及第，登博学鸿词科；再于两年后释褐太子校书。不久，贞元十三年，其父葬于扬州，刘禹锡请告东归。此诗或作于此时。诗的首联言自己东出长安，回首远眺，满心感伤。“灞涘”一词化用出自谢眺《晚登三山还望京邑》中“灞涘望长安，河阳视京县。”一句。颔联写自己同友人相别，如今自己面对的只有满眼不尽的山川。颈联言车马奔忙，出发迅速。尾联“前欢”一词当指贞元九年至贞元十一年间，刘禹锡在长安度过的“春风得意”的时日。可以感到，全诗充满著作者对友人的依依惜别与归心似箭的矛盾情绪。父死奔丧，乃是人之常情，而对于曾经把酒言欢的朋友们，作者亦不能轻易忘怀。矛盾的情绪杂糅成章，使全诗含蓄婉转，情深意浓。', '唐德宗贞元十三年，刘禹锡父卒于扬州，葬于荥阳。此诗作于东归葬父之际。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (104, '6811e0d52b504b8c9124366f57a73e02', '送施肩吾东归', 60, 3, 820, '约820年', '知君本是烟霞客，被荐因来城阙间。世业偏临七里濑，仙游多在四明山。早闻诗句传人遍，新得科名到处闲。惆怅灞亭相送去，云中琪树不同攀。', '知君本是烟霞客，被荐因来城阙间。世业偏临七里濑，仙游多在四明山。早闻诗句传人遍，新得科名到处闲。惆怅灞亭相送去，云中琪树不同攀。', '唐宪宗元和十五年，施肩吾登进士第，不及授官，东归返家。遂有此作。史料记载，施肩吾此人少有隐逸之心，以诗作闻名天下。唐宪宗元和十五年登进士第，因为人有隐逸之心，于是不及授官，便东归返家。本诗的首联即写明此事。“烟霞客”一词盖指心尚山水，追求隐逸生活的人。“被荐”一句写施肩吾进京登第事。诗的颔联写明施肩吾心之所向，皆名山大川，非宫廷事务。作品的颈联言施肩吾的才能出众，所作诗句天下遍传，近日又登科及第，却因心之所向，所以辞京东归。诗作的尾联言送别，诗人在惆怅之余还不忘安慰施肩吾，称其此次离开只是人生的不同轨迹而已，在名山大川，云中树里，友人依旧能够实现自己的人生理想。此诗不像传统的送别诗作，全写施肩吾的人生理想，才能及诗名。借助对这首诗作的解读，我们可以对唐宪宗元和十五年时发生在施肩吾身上的事件进行遥想，对这位埋没在历史尘烟中的有名诗人的年谱进行推测。', '唐宪宗元和十五年，施肩吾登进士第，不及授官，遂东归返家。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (105, '2548efc8e35f4224b9bcaa2b3a1db724', '西还', 61, 3, 810, '810年', '悠悠洛阳梦，郁郁灞陵树。落日正西归，逢君又东去。', '悠悠洛阳梦，郁郁灞陵树。落日正西归，逢君又东去。', '唐宪宗元和五年，元稹自洛阳返京，途中有所作。此诗富有趣味，体现在此诗全是叙事，无一句抒情之语。通过叙述事件的发生经过作为诗作，而抒情全在诗外，依靠读者在读懂全诗的基础上自我体会得知。作品的前两句先写作者自洛阳返京，未写明西归的缘由。后两句写等到作者西归长安时，作者日夜思念的友人却又准备向东而去，此句中才说明作者西归的缘由，并写出作者同友人因时间的原因导致相互错过，造成遗憾。前两句写作者思友人而出发，后两句写作者因错过友人而心生遗憾。由“思”至“憾”，逻辑严密，又颇具趣味。俞陛云先生在《诗境浅说》中有言：“诗止言我还君去，而离情旅思皆于诗外见之。”可谓至论。', '唐宪宗元和五年，元稹自洛阳返京，途中有所作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (106, 'e01d9ecc22d342f7ae1741168ef0fd24', '送李亿东归', 62, 3, 822, '约822至826年间', '黄山远隔秦树，紫禁斜通渭城。别路青青柳发，前溪漠漠花生。和风澹荡归客，落日殷勤早莺。灞上金樽未饮，宴歌已有余声。', '黄山远隔秦树，紫禁斜通渭城。别路青青柳发，前溪漠漠花生。<br>和风澹荡归客，落日殷勤早莺。灞上金樽未饮，宴歌已有余声。', '唐穆宗长庆二年至唐敬宗宝历二年，周贺逗留长安，此诗或作于此时。作品的首联写友人此去的目的地是安徽黄山一带，与题目中“东归”相吻合。“远隔”一词暗含作者对友人的不舍情绪。颔联、颈联两联宕开一笔，转写送别时候的场景。送别时应是春日，花生溪前，柳发路边，一幅温柔静美的春日景象。就在这样和煦美好的日子里，作者即将同友人分别，令读者读来也心生不忍。尾联两句写作者同友人的送别场景，写作者同友人的饯别酒还未饮尽，宴会上的歌曲就已经唱完，提示友人，时间已到，不得不离开了。在这一联中，作者借用时间的仓促，写离别时候的残忍及不舍，多有新意，饶有趣味。', '周贺此时在长安', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (107, '49197edb2eee4c5abecfdf8c88471079', '下第别友人杨至之', 13, 3, 827, '827年', '花落水潺潺，十年离旧山。夜愁添白发，春泪减朱颜。孤剑北游塞，远书东出关。逢君话心曲，一醉灞陵间。', '花落水潺潺，十年离旧山。夜愁添白发，春泪减朱颜。<br><br>孤剑北游塞，远书东出关。逢君话心曲，一醉灞陵间。', '唐穆宗长庆四年春日，许浑落第后欲北游，与友人告别，因有此作。作品的首联单刀直入，直接写作者离家在外已有十年之多。作者离家本是意欲求功名，而此日作者落第，十年的努力功亏一篑，作者难免有嗟叹人生不顺，年少无成的感叹。颔联两句作者写在这十年里，为了求取功名，作者日夜愁苦，不仅添了白发，而且在时间的摧残下，还改变了容颜。这是一位落第士子的心态最为直接的表达。受到落第无成的打击，作者继而在颈联书写下自己愿意出关北游，建功边塞的愿望。唐代士子或是科举为官，或是出塞入幕，都是他们实现人生价值的两条途径。许浑此时在京落第，遂生出塞入幕之心。作品的尾联是作者对友人说的答谢的话。作者对友人的知己之恩以及陪伴自己借酒消愁的行为表示感谢。', '唐文宗大和元年春日，许浑落第后欲北游，与友人告别，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (108, 'b571c9d7a9eb49db9a389f677e94f227', '灞上逢故人', 63, 3, -1, '年份未详', '花落杏园枝，驱车问路岐。人情谁可会，身事自堪疑。岳雨狂雷送，溪槎涨水吹。家山如此景，几处不相随。', '花落杏园枝，驱车问路岐。人情谁可会，身事自堪疑。<br><br>岳雨狂雷送，溪槎涨水吹。家山如此景，几处不相随。', '此诗具体创作年份无考，当是作者在灞上逢故人，有感所作。故人，或是故乡之人；或是有过故交之人，从诗中内容看，此“故人”当是故乡之人。作者在灞上逢故乡之人来京，交谈寒暄中，遂生思家之心，于是援笔成诗，是为此作。首联“花落”句点明此诗创作于暮春落花时节，不仅如此，作者还借“落花”意象起兴，渲染下文生发的思乡情绪。颔联两句写作者同故人在灞上寒暄交谈过程中，作者联想起过去以往发生家乡的故事，并由此引起颈联想象家中此时的自然景象。颈联两句写作者家乡的自然景物，虽然是想象，但却是作者思乡情绪的艺术表现：长安虽是暮春，一片平和景象。反观家乡，却已是江南狂风送雨，水涨船高的梅雨时节。尾联两句为颈联想象家乡景象作结，也为全诗思念家乡情绪作结：作者写虽然自己身处长安多年，但一见故人，还是能够想起家乡的模样，感叹家乡对自己影响之深远。由逢故人生发思乡写及家乡景象再作感叹，作者以思乡情绪灌注全篇，令全诗趣味非凡，意境浑成。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (109, 'a9f204a2359d415e854ade00fbd9895d', '送溪州使君', 64, 3, 886, '886年', '兵寇伤残国力衰，就中南土藉良医。凤衔泥诏辞丹阙，雕倚霜风上画旗。官职不须轻远地，生灵只是计临时。灞桥酒醆黔巫月，从此江心两所思。', '兵寇伤残国力衰，就中南土藉良医。凤衔泥诏辞丹阙，雕倚霜风上画旗。<br>官职不须轻远地，生灵只是计临时。灞桥酒醆黔巫月，从此江心两所思。', '唐僖宗光启二年，罗隐在长安，灞桥送别友人，因有此作。诗的首联“兵寇伤残国力衰”一句写尽晚唐时期国力衰弱，生灵涂炭的残酷现实，故罗隐将即将去溪州赴任的友人比作拯救一方百姓的“良医”，将自己政治抱负寄寓友人。颔联“凤衔泥诏辞丹阙，雕倚霜风上画旗。”言友人即将去溪州赴任。溪州，因在湖南省境内，故称之为“南土”、“黔巫”。“官职”一句再度体现出罗隐的政治抱负，作者告诫即将赴任的友人，务必不要因为天高地远所以玩忽职守，一定以百姓的生计为头等大事。可以看出，虽然罗隐的仕途坎坷蹭蹬，但他是关心国家，关心百姓的。在他看到晚唐时期国力衰败，民不聊生的现实之后，自己虽然无能为力，却告诫为官一方的友人，务必以民生为重。诗的尾联以灞桥与黔巫代指作者与友人，喻示此后二人分隔两地，蕴含浓郁的伤感情绪。可以说，送别友人，表达不舍情绪的同时，还传达出自己的政治抱负及理想，这是此篇诗作的价值所在。', '886年，冬，在长安灞桥送人赴溪州刺史任。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (110, '83d45521fe3b4653a570553709ec43a6', '灞陵亭', 65, 3, -1, '年份未详', '一条归梦朱弦直，一片离心白羽轻。明日灞陵新霁后，马头烟树绿相迎。', '一条归梦朱弦直，一片离心白羽轻。明日灞陵新霁后，马头烟树绿相迎。', '此诗具体创作年份无考。此诗言作者离京返家之事。前两句状作者回家之心切，比喻新颖，饶有趣味：“归梦”指想要回家的心情，“朱弦”指琴瑟上的琴弦，二者合一是讲作者归家心切就如朱弦的琴弦一样直，言归家心急；“白羽”是指鸟的羽毛，因其轻盈，所以导致其在风中能够长时间飞舞，这是说作者归家心切如白羽一般轻盈，想要很快地到达目的地，言归家心速。作品后两句写诗人想象。明日也顾不得离愁，顾不上亲友送别，只想快点抵达灞陵送别之地，幽草绿树之处，就可以早一点回家了，此言作者回家之迫切。通篇四句话，全是从各种角度取喻，摹状作者归家之心切，令读者读来能够深有所感，并觉得其中自有趣味。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (111, '2c26a857ad604df2aec2a7b9ac0c38fe', '送进士卢棨东归', 66, 3, 880, '880年', '灞岸草萋萋，离觞我独携。流年俱老大，失意又东西。晓楚山云满，春吴水树低。到家梅雨歇，犹有子规啼。', '灞岸草萋萋，离觞我独携。流年俱老大，失意又东西。<br><br>晓楚山云满，春吴水树低。到家梅雨歇，犹有子规啼。', '唐僖宗广明元年，郑谷在长安落第，因有此作。此作可根据内容分为三部分，第一部分是作品的首联，写作者登灞岸远望，写景抒怀。作者看见灞水边茂密的草木，自觉时光流逝，离进京赶考又过了几个年月，不觉有年命之喈，故在次句写“离觞我独携”。落第之后，唐人一般选择回家接着准备，或是在京郊默默准备下一年的考试，郑谷的选择是回家。所以在即将离别的时候，面临灞岸萋萋草木，既有时光飞速之感，又有一事无成的失落之感。第二部分是作品的颔联，这两句中，作者将自己的感受进一步讲明：首句言自己年岁已高，次句言自己一事无成，这两句可以看作是首联中“离觞”的解释。作品的第三部分为后两联。在这四句话中，作者站在灞岸边，想象了自己返家途中的种种景象，以期以此来抚平自己因落第而产生的一事无成之感。全诗抒情直白，写景明丽自然，前两联同后两联的转折也是因情而转，总的来说，是自然流畅的作品。', '880年，在长安落第后，有诗。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (112, 'b12a5843a7f744589a1e7d4f946e68d7', '送林宽下第东归', 67, 3, -1, '年份未详', '为君惆怅惜离京，年少无人有屈名。积雪未开移发日，鸣蝉初急说来程。楚天去路过飞雁，灞岸归尘触锁城。又得新诗几章别，烟村竹径海涛声。', '为君惆怅惜离京，年少无人有屈名。积雪未开移发日，鸣蝉初急说来程。楚天去路过飞雁，灞岸归尘触锁城。又得新诗几章别，烟村竹径海涛声。', '此诗具体创作时间无考，大致是作者送别落第友人而作。作品根据内容大致可分为两部分内容。第一部分为作品的首联，此联先写作者因友人落第离京而为友人感到的惆怅不快情绪，再写对友人经历的不满情绪，认为世间所有人都可以获取功名，只有友人不能获取，颇有为友人打抱不平的意思。第二部分为作品的后三联内容，以时间为统摄总写作者想象友人返家，又回京再度准备科举的过程。其中，中间两联谊“积雪”、“鸣蝉”等代表时间的意象颇有新意。总的来说，此诗还是在表现作者为友人的劝勉鼓励之语作了很多功夫，首联的直白叙述自不用说，中间两联以时间为统摄，写友人往来京城与家的过程，实际上是作者鼓励友人回家准备，来年再度应试的婉转表达。正因如此，此诗才颇有趣味，耐人寻味。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (113, '88a1ce8fb7e94c80aeda3bc8dc40ad12', '送罗少府归牛渚', 68, 3, 828, '828年', '作尉长安始三日，忽思牛渚梦天台。楚山远色独归去，灞水空流相送回。霜覆鹤身松子落，月分萤影石房开。白云多处应频到，寒涧泠泠漱古苔。', '作尉长安始三日，忽思牛渚梦天台。楚山远色独归去，灞水空流相送回。霜覆鹤身松子落，月分萤影石房开。白云多处应频到，寒涧泠泠漱古苔。', '罗少府疑为罗邵京，邵京于太和二年登贤良方正能直言极谏科。此时回牛渚，应是登科后归宁。因此此诗大约创作于太和二年。作品首联两句言罗少府离京返家之原因：此写罗少府因想家所以离京，是夸张写法。任有官职的人，离京怎能如此随意，所以此次离京的原因应是罗少府登科后归宁。“梦天台”用典引出天台山，为下文写景作铺垫。诗作的后三联全部描绘浙江天台山景色，其中，值得称道的应属颈联两句。这两句中，“霜覆鹤身”言此山之仙逸，“松子落”言山中环境之幽静，“月分萤影”写山中被月色照射下的明丽景象，“石房开”暗示有人，却未见人影，是写山静。从这两句足见贾岛作诗，推敲炼字之功力，将一处山景，描绘的静中有幽，幽中亦有明，多重意境，令人读来神往。', '罗少府疑为罗邵京，邵京于大和二年登贤良方正能直言极谏科。此时回牛渚，应是登科后归宁', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (114, '7334f1dfe92d422ca7e07049d47dccfd', '送友人下第归宁', 69, 3, 830, '约830年', '紫阁雪未尽，杏园花亦寒。灞西辞旧友，楚外忆新安。细雨猿啼枿，微阳鹭起滩。旋应赴秋贡，讵得久承欢。', '紫阁雪未尽，杏园花亦寒。灞西辞旧友，楚外忆新安。<br><br>细雨猿啼枿，微阳鹭起滩。旋应赴秋贡，讵得久承欢。', '此诗具体创作时间无考，大致创作于唐文宗太和四年之前。此诗虽题曰“送友人下第”，其实从诗中内容看，全是作者在送别时，观景有感所作，同友人下第事关系不大。首联写初春景色，先写长安户县紫阁峰，冰雪未化；又写长安城内曲江园林，杏花未开。颔联两句写送别友人，送别时作者因友人离京，所以自己也生乡愁。颈联两句当是作者想象家乡同长安完全不同的初春景色：家乡因在江南，所以气候温暖，空气湿润，较之北方更先觅到春天的气息，所以作者在诗中写及几种动物在春日生机勃发的姿态，体现作者对家乡的喜爱感情。尾联是作者自谓。作者自言快要到秋贡考试日，告诫自己不要沉迷玩乐，应专心学业。全诗最为出色在于写景，尤其是对长安及江南两处初春景色的描写，写得既有差别，又细腻如在眼前。', '喻凫于830年入长安求取功名。诗中提“秋贡”一词，是唐宋时各州府向朝廷举荐会试人员的选拔考试，所以此诗应作于830年之前。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (115, '95521a3a0fc24977ba29dac4ffef34fe', '途中逢孙辂因得李频消息', 70, 3, 832, '832年', '灞上寒仍在，柔条亦自新。山河虽度腊，雨雪未知春。正忆同袍者，堪逢共国人。衔杯益无语，与尔转相亲。', '灞上寒仍在，柔条亦自新。山河虽度腊，雨雪未知春。<br><br>正忆同袍者，堪逢共国人。衔杯益无语，与尔转相亲。', '唐文宗太和六年，方干旅居长安。路逢孙辂，得李频消息，因有此作。作品前两联写长安初春景象，状作者失望景象：初春时分，正是未暖仍寒时节，灞岸边的柳树还没有抽芽，虽然已经时过腊月，但长安大地上仍然是寒妆素裹，没有春意。正值失望之际，作者在路上恰逢友人，在寒暄交谈的过程中，又得知另一位友人的近况，作者立刻从刚才因见没有春意而生发出的失望转而产生故人相逢的喜悦之情，这种喜悦之情弥漫在作品的后两联中。颈联两句写作者恰逢故人，喜悦之意难以言表，以“堪逢共国人”作为程度修饰自己见到友人的喜悦之情。尾联两句，写作者同友人的酒宴。久别未见，一时间不知从何叙起，所以在首句中写“衔杯无语”，既写作者同友人离别甚久，又写作者遇见友人的喜悦之情。全诗前两联写因初春未暖颇感失望，后两联转折写见到友人的喜悦，有开有合，结构浑圆。', '832年，在长安。路逢孙辂，得李频消息', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (116, 'f26ff0794e7a4b2c99b9bd56ceb4184a', '都亭驿送郭判官之幽州幕府', 54, 1, -1, '年份未详', '幕府参戎事，承明伏奏归。都亭使者出，杯酒故人违。细雨沾官骑，轻风拂客衣。还从大夫后，吾党亦光辉。', '幕府参戎事，承明伏奏归。都亭使者出，杯酒故人违。<br>细雨沾官骑，轻风拂客衣。还从大夫后，吾党亦光辉。', '此诗具体创作时间无考。根据作品内容，当是作者在都亭驿送别友人的作品。作品首联先点明送别友人之身份是幕府判官，接着说明友人此次离去是公务在身。颔联两句写送别场景：饯别酒已喝过，即将到分别的时刻。颈联两句对送别的场景作了细致的描绘：送别之时正是有小雨微风的时候。小雨微风正为悲伤的送别蒙上了一层凄凉的气氛。作品的最后一联写作者对友人的谆谆教诲，在这两句话中亦带有作者对友人的希望。全诗按时间为序，先言送别之前友人离去的原因，后写送别时环境以及作者对友人的离别寄语，虽然只是酬唱作品，但在结构上算得上是一首合格的送别作品。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (117, 'dac5cc33a66a4e0f97e3f0a34249d961', '留别四首', 71, 1, -1, '年份未详', '鹏程三万里，别酒一千钟。好景当三月，春光上国浓。野花红滴滴，江燕语喃喃。鼓吹翻新调，都亭酒正酣。登庸趋俊乂，厕用野无遗。起喜赓歌日，明良际会时。盐车淹素志，长阪入青云。老骥春风里，奔腾独异群。', '鹏程三万里，别酒一千钟。好景当三月，春光上国浓。<br>野花红滴滴，江燕语喃喃。鼓吹翻新调，都亭酒正酣。<br>登庸趋俊乂，厕用野无遗。起喜赓歌日，明良际会时。<br>盐车淹素志，长阪入青云。老骥春风里，奔腾独异群。', '此诗具体创作时间无考。根据作品内容，当是作者在都亭驿送别友人的作品。第一首作品先对友人此次离别的前程抱有极大的希望“鹏程三万里”，后来点明友人此去的目的地是上国长安，作者幻想长安此时的景象仅用“浓”字概括，但具体怎样的“浓”法需要读者在阅读过程中自己解读，或是黄金柳条粉桃花；或是小雨杏花斜燕飞等等。第二首作品对送别的场景作了一番描述。先写送别时周围的环境：野花娇红，江燕细语，一片春日江边景色。后来又对送别的宴会场景作了描述：鼓吹歌舞，把酒言欢，一片潇洒恣肆的饯别场景。作品的第三首是对友人的前程抱有希望，仅用四句就寄托了作者对友人的关怀与希望。作品的第四首点明友人此去长安应是暮年，但即使是暮年也不减能力，依然能够胜任官职，完成工作。全作分为四小部分，前两部分写送别的场景，后两部分寄托作者对友人的期望以及为友人出任官职而感到的欣喜之情。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (118, 'a4c584848b694f3cbbe9c025a521f798', '送诸葛觉往随州读书（李繁时为随州刺史宰相泌之子也）', 72, 2, 823, '823年', '邺侯家多书，插架三万轴。一一悬牙签，新若手未触。为人强记览，过眼不再读。伟哉群圣文，磊落载其腹。行年五十余，出守数已六。京邑有旧庐，不容久食宿。台阁多官员，无地寄一足。我虽官在朝，气势日局缩。屡为丞相言，虽恳不见录。送行过浐水，东望不转目。今子从之游，学问得所欲。入海观龙鱼，矫翮逐黄鹄。勉为新诗章，月寄三四幅。', '邺侯家多书，插架三万轴。一一悬牙签，新若手未触。<br>为人强记览，过眼不再读。伟哉群圣文，磊落载其腹。<br>行年五十余，出守数已六。京邑有旧庐，不容久食宿。<br>台阁多官员，无地寄一足。我虽官在朝，气势日局缩。<br>屡为丞相言，虽恳不见录。送行过浐水，东望不转目。<br>今子从之游，学问得所欲。入海观龙鱼，矫翮逐黄鹄。<br>勉为新诗章，月寄三四幅。', '唐穆宗长庆三年，韩愈有诗赠友人。此诗是诸葛觉赴随州读书时，韩愈给他的赠别之作。在此之前，韩愈曾多次向宰相举荐诸葛觉，但都没有被录用。所以，在此送行之际，韩愈为这位年少有才干的友人多言劝勉语，希望其在以后能够拥有一份远大的前程。诗作的开篇即写读书之重要，可分为三个方面。第一方面，韩愈告诫诸葛觉要保护书籍；第二方面，韩愈劝勉诸葛觉读书应多加背诵；第三方面，韩愈告诫诸葛觉读书应以圣贤书为主，只有这样才能培养良好的性格品德。从“行年”句至“虽恳”句，韩愈委婉地表达了他虽在朝为官，但无法向上举荐的无奈。这一部分中“气势”句从侧面表现出中晚唐时候，唐朝的颓势。“送行”句至篇末，写作者送别时的不舍及勉励之语。“东望”一句写作者对友人即将离别的不舍；“今子”至“入海”句是作者对诸葛觉的勉励之语，希望其能够在日后有所作为。从向上举荐，终无所获的无奈写及对远行友人的劝勉，全诗无华言，全是谆谆教导语，令人读来如沐春风，似韩愈在侧，耳提面命一般', '唐穆宗长庆三年，韩愈有诗赠友人。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (119, '39cf7fe8c9be4b32a7ebb8a863bb2122', '早春浐水送友人', 73, 2, 838, '约838年', '青门烟野外，渡浐送行人。鸭卧溪沙暖，鸠鸣社树春。残波青有石，幽草绿无尘。杨柳东风里，相看泪满巾。', '青门烟野外，渡浐送行人。鸭卧溪沙暖，鸠鸣社树春。<br>残波青有石，幽草绿无尘。杨柳东风里，相看泪满巾。', '唐文宗开成三年，温庭筠于年前居家迁往长安。春日，浐水送友人，因有此作。此诗结构独特，根据内容可分为两部分。前三联渲染离别的场景，烘托离别时的不舍情绪。首联写在掺水辽阔无际的城郊，送别友人。句中“烟”字可谓诗眼：写送别时天还未亮，草色朦胧之感；又写前路漫漫，友人去处无法追随的迷茫感受。颔联两句用乐景衬哀情法，全写一片春日和煦温暖的景象，借此加深离别的不舍情绪。颈联两句中，“残”、“幽”两字是为诗眼。作者送别友人，此时心情大有不快，看景观物都带有伤感情绪，故选择景物也多是残破幽微之物。读者每每读此，可以通过体会意象，体会作者此时面临分别时的不舍情绪。作品的第二部分是作品的最后一联，此联作者直言自己的不舍、伤感情绪，将前文利用景物烘托的情绪和盘托出，无尾曲婉转语。但由于前文景物的烘托，此处直言，也就没有不妥之处。', '唐文宗开成三年，温庭筠于年前居家迁往长安。春日，浐水送友人。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (120, 'a7cbd887877f4b64a1396297ad3a0023', '暮春浐水送别（一作暮春送客）', 74, 2, -1, '年份未详', '绿暗红稀出凤城，暮云楼阁古今情。行人莫听宫前水，流尽年光是此声。', '绿暗红稀出凤城，暮云楼阁古今情。<br>行人莫听宫前水，流尽年光是此声。', '此诗创作年份无考，或作于韩琮任中书舍人期间。作品的前两句中“暗”“稀”是为诗眼。以形容颜色的程度词语描绘暮春景象：暮春时节，杨柳虽依依起舞，却已不如早春抽芽时鲜嫩，故言“绿暗”；暮春时节，枝头花已尽，就连地面累积的落花也已随风不知所往，故称“红稀”。此句用颜色字代指景物，深得长吉诗真谛。此句中“凤城”当指长安，用秦穆公女吹箫引凤典故。秦穆公女同情侣萧史吹箫引凤为千古流传，作者此处用情人关系比喻作者同友人的关系密切，也未尝不可，后句“古今情”当言此恰如弄玉萧史一般的知己之情。诗作的后两句不叙离别情绪，转写护城河水，以感叹时间流转，这是此诗值得称道之处。至于作者为何脱俗出尘，转写流水，不外乎几个原因：首先是作者别开蹊径，另辟新路的创作初衷；再次是感叹同友人聚少离多的相聚机会，最后一次离别，也匆匆过去，故借流水，感叹时光；另外是作者见友人出京，联想到自己宦居长安，不得回家的无奈窘境，只能将自己的满心怨愤排解在对时光的感叹之中。', '或作于任中书舍人期间', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (121, '723c356dcf8a46e88941a72d7b032b96', '早秋浐水送人归越', 75, 2, -1, '年份未详', '凉叶萧萧生远风，晓鸦飞度望春宫。越人归去一摇首，肠断马嘶秋水东。', '凉叶萧萧生远风，晓鸦飞度望春宫。<br>越人归去一摇首，肠断马嘶秋水东。', '此诗具体创作年份无考，大致是友人离京，作者相送，因有此作。从作品的最后一句看，此诗当作于秋季黄昏时节。首句中“凉”字炼得精准：以此饰叶，令人读来心生凉气，顿觉身在秋日。“晓鸦”句点明送别时间是在黄昏。“望春宫”此写友人在即将离别时候面向曾经生活、工作过的宫殿，心里满怀不舍之情。友人回望，再辅以寒鸦飞过的阵阵嘶鸣，每每读此，心生不舍。作品的后两句写作者同友人的分别场景。首句中“摇首”一词极有力量，将友人此时离别长安的痛苦并对此无奈的感情凸显于纸上。“肠断”一句从作者角度写友人离去。友人离去，作者驻足远望，无奈路远不见人，只闻马嘶鸦鸣肠尽断。将作者对友人离去的不舍情绪全部借助马的鸣叫表现出来，而作者不直抒感情，婉转至极。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (122, '898673adc18a44828adbd4c24122335f', '长乐驿谑李汤给事题名', 76, 2, -1, '年份未详', '渭水秦川拂眼明，希仁何事寡诗情。只应学得虞姬婿，书字才能记姓名。', '渭水秦川拂眼明，希仁何事寡诗情。<br>只应学得虞姬婿，书字才能记姓名。', '此诗具体创作年份无考，当是作者见李汤题名长乐驿，却无题诗，因作此诗疾风。给事李汤因萌荫得官，少有诗才。游玩长乐驿时，只题其名，并无诗作。作者见到后，因感其不学无术，遂作诗讽刺。“渭水”两句写长乐驿面对的优美风景，按常理说，有诗才者见此美景，当有佳篇问世，李汤却没有作品题壁，作者以此相问，是为讽刺。“只应”句以只会武功，稍输文采的项羽比喻李汤，讽刺其没有作品就题壁留名，是不合规范的。此诗讽刺意味浓重，是作者睹物所感，也是作者情性所致。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (123, '48a8a71fcf0545fe849cedd4a5799ff6', '雨中长乐水馆送赵十五滂不及', 14, 2, -1, '年份未详', '碧云东去雨云西，苑路高高驿路低。秋水绿芜终尽分，夫君太骋锦障泥。', '碧云东去雨云西，苑路高高驿路低。<br>秋水绿芜终尽分，夫君太骋锦障泥。', '此诗年份无考，创作背景大致为赵滂东去，义山相送，因有此作。题目中”长乐水馆“当指长安通化门外长乐坡下的长乐驿站，因靠近浐水，游人经长乐驿站多行水路，所以长乐驿站又称为长乐水馆。诗作首联写作者同友人分别的场景。“碧云”句以东去预示赵滂去往的方向，雨云西则是作者返回长安的方向。“苑路”句写长安地势，自皇宫至郊外，呈现北高东低的趋势。不仅如此，作者还借助地势起伏，暗示自己送别友人的情绪也是起伏变化的：从皇宫处出发一路景色美好，心情也不错。等到了驿站送别处，作者因即将与友人分别所以心情低落不已。“秋水”句写作者同友人自长安城至长乐驿站这一路上的所见，虽说已是秋日，但景色不减春时。最后一句“夫君”化用屈原《九歌》中“思夫君兮太息”句，语带戏谑意。显示出作者对离别情绪的控制：对友人的不舍全在此戏谑语中。', '友人赵滂东去，义山送别，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (124, '3fdba6b47c0b441e8ae51a5d181c40bc', '赠孙绮新及第', 14, 2, 838, '838年', '长乐遥听上苑钟，彩衣称庆桂香浓。陆机始拟夸文赋，不觉云间有士龙。', '长乐遥听上苑钟，彩衣称庆桂香浓。<br><br>陆机始拟夸文赋，不觉云间有士龙。', '唐文宗开成三年，孙绮及第，义山有诗相赠。诗作的首联点明送别的地点为长安的长乐坡。“上苑钟”则是作者对孙绮祝贺之语，言孙绮此日及第，离皇宫更近，可以平步青云，追求政治理想了。“彩衣”是未冠者之服装，“桂香”实因古人称及第为折桂，折桂后必有桂香遗身，故有此称。这里是指孙绮年少及第，少有所为。诗作的后两句用典，将孙绮兄弟与晋代陆机兄弟相提并论。写孙绮之前早有其兄才高八斗，登第为官了，此日又有孙绮及第。不仅写及孙家兴盛，又以孙绮比拟陆云，有庆贺之意。', '开成三年，孙绮及第，义山有诗相赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (125, '50c00078d4fa4cb198b2d15eefb8d0d5', '过华清宫', 77, 5, 793, '793年', '君王游乐万机轻，一曲霓裳四海兵。玉辇升天人已尽，故宫犹有树长生。', '君王游乐万机轻，一曲霓裳四海兵。玉辇升天人已尽，故宫犹有树长生。', '唐德宗贞元九年，即安史之乱平息后三十年，李约途经骊山，目睹华清宫遗迹，心生对旧日繁华的感伤，遂有此诗。首句言古事，是对历史的反思。“霓裳”是指杨贵妃擅长的《霓裳羽衣舞》。唐玄宗天宝年间因宠幸杨贵妃，每日纵酒寻欢，不理朝政，百姓生活苦不堪言，于是揭竿而起，掀起了安史之乱。白居易在《长恨歌》中对此有一句极为凝练的诗句：“渔阳鼙鼓动地来，惊破霓裳羽衣曲。”本诗就是化用此句，言君王纵酒寻欢的行为已引起身处水深火热生活中的百姓的不满，继而导致叛乱的发生。下句言今日所见，是对人生短暂的感叹。“玉辇升天”指唐玄宗早已作古归西，然而宫中的宫树依旧繁荣茂盛。全诗仅有四句，却除了对“杨李”故事的反思之外，还有作者睹物思人，生发出有关宇宙与生命的终极思考。可谓用思巧妙。', '唐贞元九年，李约途经骊山，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (126, 'd98f2e5d32c04149be838c5f7a7713f6', '过华清宫', 78, 5, 811, '811年', '春月夜啼鸦，宫帘隔御花。云生朱络暗，石断紫钱斜。玉碗盛残露，银灯点旧纱。蜀王无近信，泉上有芹芽。', '春月夜啼鸦，宫帘隔御花。云生朱络暗，石断紫钱斜。<br>玉碗盛残露，银灯点旧纱。蜀王无近信，泉上有芹芽。', '李贺在长安任奉礼郎期间，有机会去华清宫，有诗。唐人，尤其是出生于中晚唐时期的诗人，在创作“华清宫”相关主题的作品时，总是会对“李杨”二人出现指责或惋惜的态度。指责“李杨”二人奢靡的纵酒寻欢生活；惋惜“李杨”二人苦中作乐的爱情悲剧。此诗当属指责一类。本诗只写李贺亲眼所见，全然没有对华清宫往日繁华景象的想象描绘。虽然只是对遗迹作真实书写，但读者可以跟随作者的笔端遥想旧日华清宫的繁华景象，也可体会此时作者面对遗迹的感叹，继而领会作者创作此诗的讽刺意味。之所以本诗具有如此高超的艺术表现力，全在于作者对形容词的把握。试看：颔联中“暗”朱络、“断”石、“斜”紫钱；颈联中“残”露、“旧”纱等词，都是些色调灰暗，破碎破裂的形容词。读者通过阅读这些诗句，可以在目睹遗迹的基础上，遥想华清宫往日繁华的景象，并且通过尾联“蜀王无近信，泉上有芹芽。”一句的今昔对比体会作者创作此诗的讽刺意味。', '长安任奉礼郎期间，有机会去华清宫，有诗。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (127, 'bc79696eafc74ac9917c8b01a9d84adf', '过华清宫绝句三首', 79, 5, -1, '年份未详', '其一长安回望绣成堆，山顶千门次第开。一骑红尘妃子笑，无人知是荔枝来。其二新丰绿树起黄埃，数骑渔阳探使回。霓裳一曲千峰上，舞破中原始下来。其三万国笙歌醉太平，倚天楼殿月分明。云中乱拍禄山舞，风过重峦下笑声。', '其一<br>长安回望绣成堆，山顶千门次第开。<br>一骑红尘妃子笑，无人知是荔枝来。<br>其二<br>新丰绿树起黄埃，数骑渔阳探使回。<br>霓裳一曲千峰上，舞破中原始下来。<br>其三<br>万国笙歌醉太平，倚天楼殿月分明。<br>云中乱拍禄山舞，风过重峦下笑声。', '本诗的创作时间及创作背景无法确定，或作于作者途经华清宫之时。纵览唐代泳华清宫的诗作，尤其是中唐以后的作品，大多不超过歌咏杨李爱情及讽刺君王享乐误国或者感伤太平盛世等三个主题。此作为后者，分别借助“妃嗜荔枝”、“禄山贿赂”、“君王享乐”三件事情，讽刺唐玄宗晚年因贪图享乐，宠幸杨玉环，导致“安史之乱”的事情。第一首作品借助《新唐书》中关于杨贵妃喜啖荔枝一件事，写到唐玄宗对杨贵妃的宠幸，并通过“一骑红尘”与“妃子笑”的对比，起到强烈的讽刺效果。第二首借助史料中关于唐玄宗试探安禄山，派去试探安禄山的使者却反被贿赂，导致叛乱计划没有及时得以发觉一事，既写安禄山为人狡猾无常，又暗示唐玄宗贪图享乐，不能明查是非。作品的第三首先写华清宫中的太平景象，接下来对宫中的享乐活动作简要描写，其目的还是同其他作品一样，都是讽刺之作', '或作于作者途经华清宫之时', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (128, 'de47638169f6408a95f67998e2efe218', '经古行宫（一作经华清宫）', 79, 5, -1, '年份未详', '台阁参差倚太阳，年年花发满山香。重门勘锁青春晚，深殿垂帘白日长。草色芊绵侵御路，泉声呜咽绕宫墙。先皇一去无回驾，红粉云环空断肠。', '台阁参差倚太阳，年年花发满山香。重门勘锁青春晚，深殿垂帘白日长。草色芊绵侵御路，泉声呜咽绕宫墙。先皇一去无回驾，红粉云环空断肠。', '本诗的创作时间及创作背景无法确定，或作于作者途经华清宫之时。纵览唐代泳华清宫的诗作，尤其是中唐以后的作品，大多不超过歌咏杨李爱情及讽刺君王享乐误国或者感伤太平盛世等三个主题。此作属于因见到太平时代的行宫而感时伤怀的作品。首联中“台阁”一句言华清宫之宏大巍峨。“年年”一句写华清宫自安史之乱被毁之后，长年无人居住，这里早已变成了植物的天堂。颔联两句在写华清宫规模宏大之余，还点明华清宫周围环境的寂静，衬托其遭战火被毁的凋敝景象。颈联中“草色”一句写作者所见，“侵”字很好，写明作者在看到此景后的心理感受：既有宫殿无人修整的无奈，也有太平时代已不在的愤恨。后一句“呜咽”一词也表明作者着中心理感受。作品的最后，作者点明题旨，这篇作品的内涵全在于作者对唐玄宗太平盛世的凭吊，以及对于时代已不在的扼腕叹息', '或作于作者途经华清宫之时', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (129, '828f4e41abdc4abc9a9e6e70893f6678', '过华清宫二十二韵', 73, 5, -1, '年份未详', '忆昔开元日，承平事胜游。贵妃专宠幸，天子富春秋。月白霓裳殿，风干羯鼓楼。斗鸡花蔽膝，骑马玉搔头。绣毂千门妓，金鞍万户侯。薄云欹雀扇，轻雪犯貂裘。过客闻韶濩，居人识冕旒。气和春不觉，烟暖霁难收。涩浪和琼甃，晴阳上彩斿。卷衣轻鬓懒，窥镜澹蛾羞。屏掩芙蓉帐，帘褰玳瑁钩。重瞳分渭曲，纤手指神州。御案迷萱草，天袍妒石榴。深岩藏浴凤，鲜隰媚潜虬。不料邯郸虱，俄成即墨牛。剑锋挥太皞，旗焰拂蚩尤。内嬖陪行在，孤臣预坐筹。瑶簪遗翡翠，霜仗驻骅骝。艳笑双飞断，香魂一哭休。早梅悲蜀道，高树隔昭丘。朱阁重霄近，苍崖万古愁。至今汤殿水，呜咽县前流。', '忆昔开元日，承平事胜游。贵妃专宠幸，天子富春秋。<br>月白霓裳殿，风干羯鼓楼。斗鸡花蔽膝，骑马玉搔头。<br>绣毂千门妓，金鞍万户侯。薄云欹雀扇，轻雪犯貂裘。<br>过客闻韶濩，居人识冕旒。气和春不觉，烟暖霁难收。<br>涩浪和琼甃，晴阳上彩斿。卷衣轻鬓懒，窥镜澹蛾羞。<br>屏掩芙蓉帐，帘褰玳瑁钩。重瞳分渭曲，纤手指神州。<br>御案迷萱草，天袍妒石榴。深岩藏浴凤，鲜隰媚潜虬。<br>不料邯郸虱，俄成即墨牛。剑锋挥太皞，旗焰拂蚩尤。<br>内嬖陪行在，孤臣预坐筹。瑶簪遗翡翠，霜仗驻骅骝。<br>艳笑双飞断，香魂一哭休。早梅悲蜀道，高树隔昭丘。<br>朱阁重霄近，苍崖万古愁。至今汤殿水，呜咽县前流。', '此诗具体创作年份无考。诗作前四句总写开元年间，承平盛世景象。自“月白”至“屏掩”句，以宫殿的格局“霓裳殿”、“羯鼓楼”；女子服饰“花蔽膝”、“玉搔头”以及出行所乘车马“绣毂”、“金鞍”等物写宫殿中奢华的生活，以此衬托开元时期的太平景象，符合作者首句所说的“忆”。自“重瞳”句至“御案”句，以“舜有重瞳”典故比喻唐玄宗李隆基，以女子纤手比喻当时的贵妃杨玉环，这几句当是写唐玄宗同杨贵妃在开元时期的奢靡生活。“深岩”两句是转折，“浴凤”一词因杨玉环喜泡温泉得名，因此代指杨玉环。“潜虬”一词因安禄山叛乱是在承平年代中产生的，所以用“潜”，“虬”当用《安禄山事迹》中记载的安禄山化龙典故，所以这句是写在承平盛世开元时期，宫中奢靡的生活已经埋下了日后延及全国的战火的种子。自“不料”至“早梅”句，主要是写安禄山叛乱已经殃及全国，在即将灭国的危险面前，李隆基只得采取大臣的建议除掉杨玉环、杨国忠等人，急匆匆地逃离长安，奔入蜀道。作品的后四句先写李隆基在蜀道怀念同杨玉环往日的缠绵感情，后写作者面对滔滔不绝的温泉水时，感叹时间，感叹杨李爱情的悲惨。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (130, 'bbf30d32327541f9b4248b4ccfddff0e', '骊山（一作途经骊山（一作望华清宫感事', 13, 5, -1, '年份未详', '闻说先皇醉碧桃，日华浮动郁金袍。风随玉辇笙歌迥，云卷珠帘剑佩高。凤驾北归山寂寂，龙旟西幸水滔滔。贵妃没后巡游少，瓦落宫墙见野蒿。', '闻说先皇醉碧桃，日华浮动郁金袍。风随玉辇笙歌迥，<br>云卷珠帘剑佩高。凤驾北归山寂寂，龙旟西幸水滔滔。<br>贵妃没后巡游少，瓦落宫墙见野蒿。', '此诗具体创作时间无考，当是作者途经骊山，目睹华清宫残垣断壁，有感而作。根据内容，作品可以分为两部分，前一部分是作品的前三联，主要是对唐玄宗开元天宝年间在华清宫举行的奢靡的享乐生活进行了一番描写。其中首联中第二句以服饰之华美衬托生活之奢靡；颔联以“玉辇”、“笙歌”等器物衬托恣肆的生活；颈联两句又引出唐玄宗杨玉环二人，为后文的叙述作铺垫。作品的第二部分为作品的最后一联，主要是写李杨二人在经历了安史之乱后，一个马嵬自缢，一个宫中失权，所以自安史之乱后，华清宫中的临幸活动也愈加减少，直到变成作者看到的残垣断壁景象。值得注意的是，作品的第一部分同作品的第二部分没有转折，也没有过渡，直接由奢靡生活转向破败景象，落差之大，足以让人痛恨此前的奢靡生活，并领会作者想要表达的历史教训。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (131, '9a2979710f34467b80cd061a388a5308', '过骊山（一作赵嘏诗）', 80, 5, -1, '年份未详', '冷日微烟渭水愁，华清宫树不胜秋。霓裳一曲千门锁，白尽梨园弟子头。', '冷日微烟渭水愁，华清宫树不胜秋。<br>霓裳一曲千门锁，白尽梨园弟子头。', '此诗具体创作年份无考，当是作者途经骊山，有感而作。作品前两句写作者途中所见之景。秋日清晨，作者途经骊山，仰望山上华清宫，只见薄雾朦胧；远望渭水，又见江雾正浓，不能清晰见到江水之美景，心生惆怅，遂言“渭水愁”，通感之法。从首句“华清宫”一词即可看出，此作应是作者目见华清宫，心生对唐玄宗开元盛世的向往以及追忆之情。 作品最后两句言情。首句“霓裳”是指杨贵妃善舞的《霓裳曲》，代指李杨二人的奢靡生活；“千门锁”是指经历了安史之乱，唐朝皇帝不再临幸华清宫，华清宫大门紧闭，充斥着凋敝萧瑟的气息。这一句虽然没有提到对唐朝皇室冲击甚大的安史之乱，只言安史之乱的起因及结果，其经过不言自明。最后一句中“梨园”是唐玄宗设立的戏曲机构，实际上代指对唐玄宗开元盛世心存眷恋的一部分人。“白头”是言这些人心中发愁，从此可以看见作者借梨园子弟之口，说出自己对唐玄宗开元盛世的眷恋之情。唐诗中，凡是涉及华清宫意象的作品，内容大致相同。此篇作品中，作者不仅写到华清宫，还提及唐玄宗朝设立的梨园机构，其思念追忆开元盛世之情不言自明。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (132, '36b2b62b7bab4363b2af06d2a24ea6ef', '题嘉祥驿', 80, 33, -1, '年份未详', '树顶烟微绿，山根菊暗香。何人独鞭马，落日上嘉祥。', '树顶烟微绿，山根菊暗香。何人独鞭马，落日上嘉祥。', '莎栅驿即嘉祥驿', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (133, '7abb06ea435945209458b8fb0f471e02', '题嘉祥驿', 81, 33, 857, '857年', '交亲荣饯洛城空，秉钺戎装上将同。星使自天丹诏下，雕鞍照地数程中。马嘶静谷声偏响，旆映晴山色更红。到后定知人易化，满街棠树有遗风。', '交亲荣饯洛城空，秉钺戎装上将同。星使自天丹诏下，雕鞍照地数程中。<br>马嘶静谷声偏响，旆映晴山色更红。到后定知人易化，满街棠树有遗风。', '诗首联云洛城万人空巷，赴临都驿饯别卢渥；颔联出句用星使典，点明自己蒙帝王恩宠的高贵身份，所谓“星使”指帝王的使臣，古人认为天节八星主臣事，故云。对句“雕鞍照地”可谓精彩，“照”字活，鞍本无光彩，然却言“照地”，则宝马香车、仪仗盛大鲜明之状可见；颈联写嘉祥驿附近景色，数峰无语，马嘶悠扬，旌斾闪闪，山色苍黛，与使臣悠闲从容之心境相合；尾联用召伯典，召伯治陕州，曾在甘棠树下理讼，民敬爱之，不忍伐其树，《诗·召南·甘棠》有“蔽芾甘棠，勿翦勿伐，召伯所茇”之句，赞召伯治陕有美政。而卢渥此行赴陕州，定承召伯遗风，化育陕郊万民。诗歌雍容富贵，恬淡高华，与叙事的“氛围”完全吻合。卢渥伯促四人显要，渥、绍、沆、沼均为人中龙凤，在士林传为美谈，咸思仰慕。渥赴陕州之前，临都驿向西五十里祖道筵席不断，驿卒感叹未曾见此盛事，更有洛阳人云未睹此景，当以为耻。如此隆盛高贵、热闹煊赫的氛围与《题嘉祥驿诗》的从容雍雅形成了非常和谐的响应与对话，象是一次小说与诗歌间精彩的“互文”。', '宣宗大中间任出镇陕州作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (134, '51113d0f41074e319803b9b57daeae79', '唐阙史之卢渥', 82, 42, 857, '857年', '唐左丞相卢渥，轩冕之盛，近代无比，伯仲四人咸居显列。乾符初，母忧服阕。渥自前中书舍人拜陕府观察使。又旬日，其弟绍自前长安令除给事中。又旬日，弟沆自前集贤校理除左拾遗。又旬日，弟沼自畿尉迁监察御史。诏书叠至，士族荣之。及赴任陕郊，洛城自居守分司朝臣以下，互设祖筵，遮于行路，洛城为之一空。都人观者肩望击毂，盛于清明洒扫之日。自临都驿以至于行，凡五十里，连翩不绝。有白须传卒，鸣指叹曰：“老人为驿吏垂五十年，阅事多矣，而未曾见祖送之盛有如此者。”时士流窃语，以此日在家者为耻。渥有题嘉祥驿诗曰：“交亲荣饯洛城空，善戏戎装上将同。星使自天丹诏下，雕鞍照地数程中。马嘶静谷声偏响，旆映清山色更红。到后定知人易化，满街棠树有遗风。”诗版后为易定帅王存尚书碎之。', '唐左丞相卢渥，轩冕之盛，近代无比，伯仲四人咸居显列。乾符初，母忧服阕。渥自前中书舍人拜陕府观察使。又旬日，其弟绍自前长安令除给事中。又旬日，弟沆自前集贤校理除左拾遗。又旬日，弟沼自畿尉迁监察御史。诏书叠至，士族荣之。及赴任陕郊，洛城自居守分司朝臣以下，互设祖筵，遮于行路，洛城为之一空。都人观者肩望击毂，盛于清明洒扫之日。自临都驿以至于行，凡五十里，连翩不绝。有白须传卒，鸣指叹曰：“老人为驿吏垂五十年，阅事多矣，而未曾见祖送之盛有如此者。”时士流窃语，以此日在家者为耻。渥有题嘉祥驿诗曰：“交亲荣饯洛城空，善戏戎装上将同。星使自天丹诏下，雕鞍照地数程中。马嘶静谷声偏响，旆映清山色更红。到后定知人易化，满街棠树有遗风。”诗版后为易定帅王存尚书碎之。', '无', '', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (135, 'f29419eecf044a318070cfac4cdfa874', '酬郑户曹骊山感怀', 7, 5, -1, '年份未详', '苍山何郁盘，飞阁凌上清。先帝昔好道，下元朝百灵。白云已萧条，麋鹿但纵横。泉水今尚暖，旧林亦青青。我念绮襦岁，扈从当太平。小臣职前驱，驰道出灞亭。翻翻日月旗，殷殷鼙鼓声。万马自腾骧，八骏按辔行。日出烟峤绿，氛氲丽层甍。登临起遐想，沐浴欢圣情。朝燕咏无事，时丰贺国祯。日和弦管音，下使万室听。海内凑朝贡，贤愚共欢荣。合沓车马喧，西闻长安城。事往世如寄，感深迹所经。申章报兰藻，一望双涕零。', '苍山何郁盘，飞阁凌上清。先帝昔好道，下元朝百灵。<br>白云已萧条，麋鹿但纵横。泉水今尚暖，旧林亦青青。<br>我念绮襦岁，扈从当太平。小臣职前驱，驰道出灞亭。<br>翻翻日月旗，殷殷鼙鼓声。万马自腾骧，八骏按辔行。<br>日出烟峤绿，氛氲丽层甍。登临起遐想，沐浴欢圣情。<br>朝燕咏无事，时丰贺国祯。日和弦管音，下使万室听。<br>海内凑朝贡，贤愚共欢荣。合沓车马喧，西闻长安城。<br>事往世如寄，感深迹所经。申章报兰藻，一望双涕零。', '此诗具体创作时间无考。根据作品内容，当是作者途经骊山时，有感而作。作品起首四句先写骊山之葱郁景象以及骊山同唐朝皇室的密切关系，凸显骊山的显赫地位。“白云”至“泉水”四句主要对骊山上的景色作了一番细致描绘：骊山之上，白云掩映，泉水尚暖，山林依旧不减当年风采。“我念”至“合沓”几句为一部分，主要写唐玄宗开元天宝年间，唐朝皇室游幸骊山的重大场面，凸显当年的承平景象。“无事”、“国祯”是感叹当时社会和谐，一番承平景象；“圣情”一词则是将这种承平景象归结于皇帝的功勋。“合沓”句则是对骊山之上的游幸活动进行侧面描写，通过声音之大，长安城都可以听闻的程度凸显骊山上游幸活动之奢靡盛大。作品的最后四句则是作者点题之句，是作者对郑户曹所说的话，也是作者在感叹玄宗朝盛世之后获得的一些感慨。凭借这首作品，可以从一个侧面对玄宗朝皇家游幸骊山的活动有所了解。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (136, 'ec2f3f226a8a43f48d5db399f9a65cf7', '过骊山', 83, 5, -1, '年份未详', '翠辇红旌去不回，苍苍宫树锁青苔。有人说得当时事，曾见长生玉殿开。', '翠辇红旌去不回，苍苍宫树锁青苔。<br>有人说得当时事，曾见长生玉殿开。', '此诗具体创作年份无考，当是作者途经骊山，目睹华清宫荒凉景象，心有所感，故有此作。唐诗中，但凡是涉及到骊山、华清宫等文学景观的作品，其内容或是忆往昔，怀玄宗盛世；或是叹过往，念李杨爱情。其表现形式多是从今日华清宫凋敝景象入手，与往日想象中的华清宫的奢靡生活相对比，因以抒情。此篇也不例外。作品中“翠辇”句当是写玄宗等人在安史之乱后仓惶出逃的事情，自此之后，唐朝的帝王们就很少临幸华清宫，华清宫也因此凋敝破败。“苍苍”句写今日所见华清宫凋敝破败的景象：自然景色一旦侵蚀了人文景观，总能带给人一些荒凉凄冷的感觉。作品的后两句以他人的口吻想象当年华清宫内奢华的景象：“当时事”谓今非昔比。“曾见”句仅用五个字就概括了当年华清宫内的奢华景象，整座宫殿用玉铺就，而且常开不闭，一派恢弘气势。将华清宫的今日与往日写完之后，作者顿然停笔，仅为读者留下两幅画面，为读者留下了足够的想象以及体悟空间。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (137, '4e6fdf935bcc496bb917b9671275ec8c', '过骊山作', 79, 5, -1, '年份未详', '始皇东游出周鼎，刘项纵观皆引颈。削平天下实辛勤，却为道傍穷百姓。黔首不愚尔益愚，千里函关囚独夫。牧童火入九泉底，烧作灰时犹未枯。', '始皇东游出周鼎，刘项纵观皆引颈。削平天下实辛勤，却为道傍穷百姓。黔首不愚尔益愚，千里函关囚独夫。牧童火入九泉底，烧作灰时犹未枯。', '此诗的创作时间无法确定，或作于作者途经骊山，过始皇墓之时。杜牧喜作咏史诗，虽然关注的都是前朝事，但却着眼于奄奄一息的晚唐社会。或凭吊，或讽刺，杜牧总能借助咏史得出对于晚唐社会有益的教训。这篇作品也是如此。诗的开篇写始皇东游事，史书中记载刘邦和项羽都分别在道旁目睹了始皇真容，这首诗中将二人并列写出，并不是二人一同观看，而是诗人要在下文引出二人所使用的处理手法而已。诗的颔联有三种解释，自以为杜牧在颔联想要称赞的是刘邦与项羽推翻暴秦，平定天下的功绩，“道旁穷百姓”一句也与上文“纵观皆引颈”相符合。诗作的颈联写秦始皇“以愚黔首”的事情，侧面写秦始皇的残暴统治。尾联为以上诸事作总结，以始皇残暴无情，死后尸骨不腐一事告诫社会的统治者应该辛勤执政，体察民情，劝喻君王不要胡作非为，以免引起人民的反抗。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (138, 'cc675b4da9d643e0949ed5b5f38b196f', '过骊山', 84, 5, -1, '年份未详', '丹雘苍苍簇背山，路尘应满旧帘间。玄宗不是偏行乐，只为当时四海闲。', '丹雘苍苍簇背山，路尘应满旧帘间。<br>玄宗不是偏行乐，只为当时四海闲。', '此诗具体创作年份无考，当是作者途径骊山，有感而作。作品开头两句写骊山上郁郁苍苍的景色。另外，次句中“旧”字点出此作应是感叹旧事的作品。作品的最后两句写感叹旧事。作者因途径骊山，目睹华清宫旧迹，遂心生感念唐玄宗开元盛世之情。在这两句话中，作者虽是感叹，但取意甚巧。不同于其他诗人一味地指摘唐玄宗贪图享乐的性格，而是以开元盛世，承平景象为由，为唐玄宗奢靡浮华的生活作开解之语，也算是为唐玄宗下了一个较为公正客观的评价。', '赴京至昭应作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (139, '42a5a3bef5b04330b4dd80c7a07d8b9e', '雨后早发永宁', 84, 34, 847, '847年', '春霖朝罢客西东，雨足泥声路未通。独爱千峰最高处，一峰初日白云中。', '春霖朝罢客西东，雨足泥声路未通。独爱千峰最高处，一峰初日白云中。', '春雨在清晨时分停歇，寓居在外的游子常常转涉飘零。那原是舒生解冻的春霖，却也兆征了生命的悲哀。雨水将道路淋浇得泥泞，本就崎岖的前路更加难行。但是天空终会放晴，黑夜也终将破晓。在攀援上一座又一座人生的险峰后，登临山顶，必会拨云见日。瞳瞳旭日挂在天空，心底蓦地照进一束希望的光。全诗造语自然，不用惊人之笔，也不撷溢美之词，在看似平淡的叙事描写中又突出了主题，既情思显露，又吟味隽永。以“雨足泥声”抑，复以千峰最高处扬。妙。', '由长安赴洛过永宁作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (140, '7cd00b25826d46fbb20e736b7774f74a', '过绮岫宫（东都永宁县西五里）', 85, 34, 821, '821年', '玉楼倾倒粉墙空，重叠青山绕故宫。武帝去来罗袖尽，野花黄蝶领春风。', '玉楼倾倒粉墙空，重叠青山绕故宫。武帝去来罗袖尽，野花黄蝶领春风。', '途中怀古，汉汉喻唐。王建曾连用七绝百首敷陈宫闱琐事，对宫阙亦倾注了超乎常人的感情。诗人途经绮岫宫，昔日粉楼紫阁在历史的侵蚀下已慢慢褪色。映入眼帘的是倾颓的楼宇，破败的残垣。连绵迢递的山峦将深宫旧苑裹挟得密不透风。雀翎断折，红颜香消。纵是身居高位的权臣也只能在身后留得一纸虚名。时间的手翻云覆雨，人事已非，只有满山野花悄然自开，为这荒芜之地平添了一抹生机。', '赴长安过永宁作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (141, '9bce65bfc939404fba3c64a33307ec36', '骊山道中', 71, 5, -1, '年份未详', '月殿真妃下彩烟，渔阳追虏及汤泉。君王指点新丰树，几不亲留七宝鞭。', '月殿真妃下彩烟，渔阳追虏及汤泉。<br>君王指点新丰树，几不亲留七宝鞭。', '此诗具体创作年份无考。作者在首句中将杨贵妃比作月殿真妃，随着彩烟下凡人间。将杨贵妃的形象艺术化，并以神女形象衬托杨贵妃之花容月貌。作品次句言安禄山叛乱已经蔓延至骊山华清宫，可见叛乱范围之大。望着排山而来的叛军，唐玄宗只能在华清宫中指点江山，排兵布阵，慌忙应对来势汹汹的安禄山叛军。诗作的最后一句用《晋书》中明帝让逆旅老妪向叛军传示七宝鞭，以此延缓叛军进攻的典故，作者是想说明如果当是能够采用策略延缓敌军的攻势的话，也不至于仓惶出逃，也不至于马嵬兵变，缢杀贵妃了。全诗开头写贵妃下凡，最后又以贵妃作结，结构前后照应，因此也可以判定全诗的主题是以安史之乱感叹李杨二人的爱情悲剧。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (142, '0b6f06e2f1eb4f27920d449d26c5f1ae', '宿昭应', 86, 5, -1, '年份未详', '武帝祈灵太乙坛，新丰树色绕千官。那知今夜长生殿，独闭山门月影寒。', '武帝祈灵太乙坛，新丰树色绕千官。<br>那知今夜长生殿，独闭山门月影寒。', '此诗具体创作年份无考。此诗对仗极工。“武帝”同“那知”句都借用典故：“武帝”句用汉武帝遵奉东皇太一为神，祈求长生的典故；“那知”用唐代华清宫内有长生殿一事，暗指李杨二人的爱情故事。“武帝”句言长生，“那知”句言世上无长生之道，讽刺意味浓厚。“新丰”与“独闭”则是写昭应县的自然景物，用意是说如果真的有长生一事，那也只能是自然景色得以长生，人与事是不可能长生的。另外，此诗以汉喻唐，虽然在作品中作者借用汉武帝的典故，但是用意是借助汉武帝求仙典故讽刺当是唐代帝王的愚昧无知的求仙行为，这是唐人写讽刺诗的一贯作法。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (143, '962467dd97c945f5aae287ee10de0f38', '昭应官舍书事', 85, 5, 813, '813年', '县在华清宫北面，晓看楼殿正相当。庆云出处依时报，御果呈来每度尝。腊月近汤泉不冻，夏天临渭屋多凉。两衙早被官拘束，登阁巡溪亦属忙。', '县在华清宫北面，晓看楼殿正相当。庆云出处依时报，<br>御果呈来每度尝。腊月近汤泉不冻，夏天临渭屋多凉。<br>两衙早被官拘束，登阁巡溪亦属忙。', '唐宪宗元和八年，王建出任昭应县丞，遂有此作。诗作首联点明昭应官舍的地理位置：位于华清宫北面，恰好可以透过窗棂看到华清宫的楼台殿阁。颔联句言因近华清宫，所以昭应县的官员生活比较优越。“庆云”即五色祥云，官舍近皇帝行宫，故言此处有“庆云”。“御果”是因官舍临近行宫，所以可以较快品尝到行宫内部的美食佳肴。颈联对仗工稳，从冬、夏两个季节说明昭应官舍生活条件优越。“汤泉”因昭应县近骊山，骊山多有地热资源，唐代诗人遂多有赞咏。昭应县距离渭水仅有十里路程，故言“临渭”。以上几联从政治地位、地理位置等方面分别谈了昭应县的优越性，然而尾联却说，虽然如此优越，但却免不了官家俗务的干扰。“两衙”是指古代官员需要一天两次向长官请示公事，称“衙参”，因一天两次，故言“两衙”。“登阁巡溪”当是官员的日常工作。诗中描写的昭应县生活忙中有闲，可见王建此时很享受此种生活。', '元和八年，王建任昭应县丞', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (144, '1ce228dafbd44d5fbb0f375719951903', '晚次昭应', 87, 5, -1, '年份未详', '落日向林路，东风吹麦陇。藤草蔓古渠，牛羊下荒冢。骊宫户久闭，温谷泉长涌。为问全盛时，何人最荣宠。', '落日向林路，东风吹麦陇。藤草蔓古渠，牛羊下荒冢。<br>骊宫户久闭，温谷泉长涌。为问全盛时，何人最荣宠。', '本诗创作时间无考。昭应，唐时长安城周边的小县城，本无名气。因昭应县内有华清宫，玄宗常与杨贵妃居住于此，遂颇有盛名，玄宗退位后，华清宫的地位迅速衰落，无人修缮，破败不堪。也是因此，唐人每每旅次昭应时，都会对唐天宝旧事感叹一番。感叹之事不过玄宗朝之鼎盛与此日萧条景象的对比、玄宗骄奢淫逸的生活以及杨国忠等人执政弄权，祸国乱政之事。本作亦是如此。首联先写作者进入昭应县城的所见所闻。路边林荫，风吹麦长，一片和谐的农家景象。颔联开始，作者慢慢写及带有年代感的意象入诗。“古渠”、“荒冢”等意象都在提示作者此处曾经的繁盛景象。颈联两句作者写皇家离宫的标志性景观——华清宫及温泉。一处是年久失修，长久关闭，一处则是无人看管，泉水长涌。人文景观在自然景观的衬托之下略显荒芜之状。看到这一切荒芜凋敝的景象后，作者不禁在尾联发问，这个问题当是讽刺性质，也不需要回答，因为答案人尽皆知。作者借助这个问题向读者抒发自己见到这些凋敝景象后的愤懑情绪。全诗前三联写景，最后一联以提问的形式发出讽刺，实有趣味。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (145, '2c7e77d3c1be419b8d76efa56a315566', '初到昭应呈同僚', 85, 5, 813, '813年', '白发初为吏，有惭年少郎。自知身上拙，不称世间忙。秋雨悬墙绿，暮山宫树黄。同官若容许，长借老僧房。', '白发初为吏，有惭年少郎。自知身上拙，不称世间忙。秋雨悬墙绿，暮山宫树黄。同官若容许，长借老僧房。', '唐宪宗元和八年，王建出任昭应县丞，遂有此作。诗的首联为作者自嘲。王建生于公元768年，却在公元813年自己45岁时才作了一个县丞的小官。所以自言惭愧。颈联亦是谦辞。作者因老年得官，又是新来昭应，必然会自谦一番。“不称世间忙”一句蕴含佛教思想，下文仍有，此处不赘述。“秋雨”一句雨水滴落在宫墙上，产生的化学反应，使墙变绿。此句全用浓重色调，全然没有新任作官的喜悦之情。这与王建长久以来接受到的佛教思想，不无关系。从尾联“长借老僧房”一句也可看出，王建此诗有参禅的习惯或意愿。实际上，王建于20岁与张籍同窗之时，就已经接触到佛教的洗涤了。后来二人多与僧人交往，遂生参禅之心。此诗即是很好的明证。', '元和八年，王建任昭应县丞', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (146, 'a8a1b272b1a545e181573ef20507c518', '昭应官舍', 85, 5, 813, '813年', '痴顽终日羡人闲，却喜因官得近山。斜对寺楼分寂寂，远从溪路借潺潺。眇身多病唯亲药，空院无钱不要关。文案把来看未会，虽书一字甚惭颜。', '痴顽终日羡人闲，却喜因官得近山。斜对寺楼分寂寂，远从溪路借潺潺。眇身多病唯亲药，空院无钱不要关。文案把来看未会，虽书一字甚惭颜。', '唐宪宗元和八年，王建出任昭应县丞，遂有此作。首联自谦。虽言自己愚钝无知，却仍因自己作官昭应而暗暗自喜。颔联写景。先写昭应官舍因临近骊山华清宫，所以能分得华清宫的一点清寂；又写昭应官舍临近渭水，有小溪流过，所以能在官舍听到小溪的潺潺流水声。此句借写景，表达自己因作官昭应而感到的内心喜悦。颈联言己。“眇身”本是皇帝自称，此代指作者。此句写作者年长多病，多靠医药维持生计，故称“眇身多病唯亲药”。“空院”是指昭应官舍，此句是说昭应官舍清闲无事。尾联还是言己，写自己因初任官职，所以对官场上的文书、文案还不曾熟悉，并且作者在诗的结尾表达了因此感到的惭愧之情。', '元和八年，王建任昭应县丞', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (147, 'f0b0f10f678f41d58359b4fcae420fab', '题昭应温泉', 88, 5, -1, '年份未详', '一道温泉绕御楼，先皇曾向此中游。虽然水是无情物，也到宫前咽不流。', '一道温泉绕御楼，先皇曾向此中游。虽然水是无情物，也到宫前咽不流。', '此诗创作年份无考。昭应是现在的陕西省临潼市。唐时皇帝建立行宫——华清宫于此，后来在宫城的基础上就形成了昭应城。正因为如此，凡是写及昭应的作品，都不免对唐玄宗旧事作感慨缅怀。又因为昭应地区温泉资源丰富，所以写及昭应的作品大多都有温泉意象。这首作品短小精干，内容大致是作者行至昭应，眼见温泉，心中感叹唐玄宗杨贵妃往事，不禁心生感慨。全诗在语言上也写得质朴无华，似口语一般，无可称赞处。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (148, 'bc74ea1a3cf2484ebac985ef8998cd88', '宿后自华阳行次昭应寄王直方', 89, 5, 818, '818年——819年', '重归能几日，物意早如春。暖色先骊岫，寒声别雁群。川光如戏剑，帆态似翔云。为报东园蝶，南枝日已曛。', '重归能几日，物意早如春。暖色先骊岫，寒声别雁群。<br>川光如戏剑，帆态似翔云。为报东园蝶，南枝日已曛。', '此诗创作年份大致可确定为唐宪宗元和十三年、十四年作。大致是作者自华阳出发，行旅途中，眼观秀色，心有所感，因作诗以寄友人。全诗从三个方面描绘华州境内的春日景色，充斥著作者因见到春日而感到的喜悦情绪。首联总说此时正值春日，作者眼见的每件物品都沾染着春天的气息。首联以下，分别从三个角度描绘华州春景。颔联鸟瞰骊山。阳光映射在骊山上，反射出耀眼的光芒，如釉彩一般。此时虽是春日，但仍有寒意。大雁飞过，鸣叫响彻山岭，似乎在迎接春天的到来。颈联鸟瞰骊山下逶迤的渭水。同样被阳光映照，水川上发出耀眼的光芒，似刀光剑影一般。远处的航帆拥挤错杂，熙熙攘攘，似云朵一般。尾联两句作者将视角聚焦于枝头上的花朵。作者本来写花枝上已有花朵点点，但加入的蝴蝶意象，一下子将蝴蝶绕枝飞这样一种动态的春意图景呈现给读者，构思巧妙。', '此诗创作年份大致可确定为唐宪宗元和十三年、十四年作。元和十一年，沈亚之因科考中举，回家省亲。元和十三年沈亚之逗留梁州，并于元和十四年返回长安。行旅路径同题目相同，作品表现的思想感情也大致与刚刚自家返京的作者心情相吻合。本诗的创作背景大致是作者自华阳出发，行旅途中，眼观秀色，心有所感，因作诗以寄友人。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (149, '241cbdf7fe55484a8a26cf408c97d83f', '除官行至昭应，闻友人出官因寄', 79, 5, -1, '年份未详', '贱子来千里，明公去一麾。可能休涕泪，岂独感恩知。草木穷秋后，山川落照时。如何望故国，驱马却迟迟。', '贱子来千里，明公去一麾。可能休涕泪，岂独感恩知。<br>草木穷秋后，山川落照时。如何望故国，驱马却迟迟。', '此诗创作年份无考。作者因刚好除官，行至昭应，却闻好友由京官调任至地方官，心有所感，遂有此作。通过对全诗出现的“贱子”、“感恩”等词进行理解，可以推断这位友人应该是同杜牧有恩，并深受杜牧尊敬的人。古人常称县令或者刺史为“明公”、“一麾”，所以可以推测杜牧的这位友人此次出京应该是担任县令、刺史一类的官职。作品的首联除了点明友人的前途之外，还i将自己得官一件事告诉友人。颔联两句主要是对友人曾经为杜牧提供过的帮助表示感恩。颈联两句描写秋日山川的萧瑟破败景象，为后文抒情渲染气氛。尾联两句，作者以友人的视角抒情，想象友人出城赴任时，心中充斥着的对前途命运的迷茫与故国家乡的怀念情感。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (150, '4e4e5bb2eefb4220a9f2fc3007bc8833', '行次昭应县道上，送户部李郎中充昭义攻讨', 14, 5, 843, '843年', '将军大旆扫狂童，诏选名贤赞武功。暂逐虎牙临故绛，远含鸡舌过新丰。鱼游沸鼎知无日，鸟覆危巢岂待风。早勒勋庸燕石上，伫光纶綍汉廷中。', '将军大旆扫狂童，诏选名贤赞武功。暂逐虎牙临故绛，<br>远含鸡舌过新丰。鱼游沸鼎知无日，鸟覆危巢岂待风。<br>早勒勋庸燕石上，伫光纶綍汉廷中。', '唐武宗会昌三年，武宗征讨叛逆刘稹。李郎中时任攻讨使。赴任之时，义山有诗相赠。诗作首联写李郎中暂时担任李彦佐幕中攻讨使一事。此联中“将军”指攻讨大军中节度使李彦佐，“狂童”指起兵反唐的刘稹。颔联写李郎中随攻讨大军行至昭应事。此联用汉尚书郎奏事于明光殿，口含鸡舌香典故，暗示李郎中此时除了担任幕中攻讨使外，还担任户部郎中一职。颈联两句是说刘稹处境不好，如沸鼎之鱼，危巢之鸟，失败指日可待。尾联两句是庆贺之语。借用东汉窦宪北击匈奴，登燕然山刻石记功一事，希望李郎中此去，大获成功。全诗语言浅近，气势豪迈，读来似有大军在前。', '会昌三年，武宗征讨叛逆刘稹。李郎中时任攻讨使。赴任之时，义山有诗相赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (151, 'e4f6f297ede84de88f92fc83936f5578', '题昭应王明府溪亭', 90, 5, -1, '年份未详', '靖节何须彭泽逢，菊洲松岛水悠溶。行人自折门前柳，高鸟不离溪畔峰。晓渭度檐帆的的，晚原含雨树重重。轩车过尽无公事，枕上一声长乐钟。', '靖节何须彭泽逢，菊洲松岛水悠溶。行人自折门前柳，高鸟不离溪畔峰。晓渭度檐帆的的，晚原含雨树重重。轩车过尽无公事，枕上一声长乐钟。', '此诗具体创作年份无考。王明府，其人不详。按照唐人称县令为明府的惯例，此人应是昭应县令。作品前四句以王明府比作陶渊明，从诗中提到的“靖节”、“彭泽”、“菊洲”等词中可以看出。陶渊明在《五柳先生传》中提到“门前有五柳树，因以为号焉”，此诗中提到的“门前柳”也是用此典，将王明府比作陶渊明。“晓渭”两句写昭应县周边的明丽景色：首句写晨景，渭水边的船帆清晰可见；次句写晚景，原上的树在雨滴的关怀下雾气蒙蒙，隐约可见。这两句写景对于全诗抒发感情有重要作用，所写的景物都是一些平常的、习见的、看来让人心生闲适感情的景色。有了此种景色的衬托，作者在最后两句中，写到了王明府整日悠闲无公事，过着一种令人艳羡的闲适生活。在对这种生活的描写中，还蕴含著作者对于王明府这样的闲适悠然的生活的向往，这才是作者创作这首作品的初衷所在。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (152, '12269ea308044f7c8b8e50e41fa8e9c9', '自京赴奉先县咏怀五百字', 91, 5, 755, '755年', '杜陵有布衣，老大意转拙。许身一何愚，窃比稷与契。居然成濩落，白首甘契阔。盖棺事则已，此志常觊豁。穷年忧黎元，叹息肠内热。取笑同学翁，浩歌弥激烈。非无江海志，潇洒送日月。生逢尧舜君，不忍便永诀。当今廊庙具，构厦岂云缺。葵藿倾太阳，物性固莫夺。顾惟蝼蚁辈，但自求其穴。胡为慕大鲸，辄拟偃溟渤。以兹悟生理，独耻事干谒。兀兀遂至今，忍为尘埃没。终愧巢与由，未能易其节。沈饮聊自适，放歌颇愁绝。岁暮百草零，疾风高冈裂。天衢阴峥嵘，客子中夜发。霜严衣带断，指直不得结。凌晨过骊山，御榻在嵽嵲。蚩尤塞寒空，蹴蹋崖谷滑。瑶池气郁律，羽林相摩戛。君臣留欢娱，乐动殷樛嶱。赐浴皆长缨，与宴非短褐。彤庭所分帛，本自寒女出。鞭挞其夫家，聚敛贡城阙。圣人筐篚恩，实欲邦国活。臣如忽至理，君岂弃此物。多士盈朝廷，仁者宜战栗。况闻内金盘，尽在卫霍室。中堂舞神仙，烟雾散玉质。暖客貂鼠裘，悲管逐清瑟。劝客驼蹄羹，霜橙压香橘。朱门酒肉臭，路有冻死骨。荣枯咫尺异，惆怅难再述。北辕就泾渭，官渡又改辙。群冰从西下，极目高崒兀。疑是崆峒来，恐触天柱折。河梁幸未坼，枝撑声窸窣。行旅相攀援，川广不可越。老妻寄异县，十口隔风雪。谁能久不顾，庶往共饥渴。入门闻号咷，幼子饥已卒。吾宁舍一哀，里巷亦呜咽。所愧为人父，无食致夭折。岂知秋未登，贫窭有仓卒。生常免租税，名不隶征伐。抚迹犹酸辛，平人固骚屑。默思失业徒，因念远戍卒。忧端齐终南，澒洞不可掇。', '杜陵有布衣，老大意转拙。<br>许身一何愚，窃比稷与契。<br>居然成濩落，白首甘契阔。<br>盖棺事则已，此志常觊豁。<br>穷年忧黎元，叹息肠内热。<br>取笑同学翁，浩歌弥激烈。<br>非无江海志，潇洒送日月。<br>生逢尧舜君，不忍便永诀。<br>当今廊庙具，构厦岂云缺。<br>葵藿倾太阳，物性固莫夺。<br>顾惟蝼蚁辈，但自求其穴。<br>胡为慕大鲸，辄拟偃溟渤。<br>以兹悟生理，独耻事干谒。<br>兀兀遂至今，忍为尘埃没。<br>终愧巢与由，未能易其节。<br>沈饮聊自适，放歌颇愁绝。<br>岁暮百草零，疾风高冈裂。<br>天衢阴峥嵘，客子中夜发。<br>霜严衣带断，指直不得结。<br>凌晨过骊山，御榻在嵽嵲。<br>蚩尤塞寒空，蹴蹋崖谷滑。<br>瑶池气郁律，羽林相摩戛。<br>君臣留欢娱，乐动殷樛嶱。<br>赐浴皆长缨，与宴非短褐。<br>彤庭所分帛，本自寒女出。<br>鞭挞其夫家，聚敛贡城阙。<br>圣人筐篚恩，实欲邦国活。<br>臣如忽至理，君岂弃此物。<br>多士盈朝廷，仁者宜战栗。<br>况闻内金盘，尽在卫霍室。<br>中堂舞神仙，烟雾散玉质。<br>暖客貂鼠裘，悲管逐清瑟。<br>劝客驼蹄羹，霜橙压香橘。<br>朱门酒肉臭，路有冻死骨。<br>荣枯咫尺异，惆怅难再述。<br>北辕就泾渭，官渡又改辙。<br>群冰从西下，极目高崒兀。<br>疑是崆峒来，恐触天柱折。<br>河梁幸未坼，枝撑声窸窣。<br>行旅相攀援，川广不可越。<br>老妻寄异县，十口隔风雪。<br>谁能久不顾，庶往共饥渴。<br>入门闻号咷，幼子饥已卒。<br>吾宁舍一哀，里巷亦呜咽。<br>所愧为人父，无食致夭折。<br>岂知秋未登，贫窭有仓卒。<br>生常免租税，名不隶征伐。<br>抚迹犹酸辛，平人固骚屑。<br>默思失业徒，因念远戍卒。<br>忧端齐终南，澒洞不可掇。', '仇兆鳌《杜诗详注》：胡夏客曰：诗凡五百字，而篇中叙发京师，过骊山，就泾渭，抵奉先，不过数十字耳，馀皆议论，感慨成文，此最得“变雅”之法而成章者也。又曰：《奉先咏怀》全篇议论，杂以叙事；《北征》则全篇叙事，杂以议论。盖曰“咏怀”、自应以议论为主；曰“北征”，自应以叙事为主也。卢世㴶曰：《赴奉先》及《北征》，肝肠如火，涕泪横流，读此而不感动者，其人必不忠。作长篇古诗，布势须要宽转。此二条（按指“穷年忧黎元”至“放歌颇愁绝”）各四句转意，抚时慨己，或比或兴，迭开迭合，备极排荡顿挫之妙。', '天宝十四载十月初自长安取京汴道赴奉先探家过骊山作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (153, '341b9ac5a2a344acbaa13db6dd01cc66', '津阳门诗', 92, 5, 851, '851年', '津阳门北临通逵，雪风猎猎飘酒旗。泥寒款段蹶不进，疲童退问前何为。酒家顾客催解装，案前罗列樽与卮。青钱琐屑安足数，白醪软美甘如饴。开垆引满相献酬，枯肠渴肺忘朝饥。愁忧似见出门去，渐觉春色入四肢。主翁移客挑华灯，双肩隐膝乌帽欹。笑云鲐老不为礼，飘萧雪鬓双垂颐。问余何往凌寒曦，顾翁枯朽郎岂知。翁曾豪盛客不见，我自为君陈昔时。时平亲卫号羽林，我才十五为孤儿。射熊搏虎众莫敌，弯弧出入随佽飞。此时初创观风楼，檐高百尺堆华榱。楼南更起斗鸡殿，晨光山影相参差。其年十月移禁仗，山下栉比罗百司。朝元阁成老君见，会昌县以新丰移。幽州晓进供奉马，玉珂宝勒黄金羁。五王扈驾夹城路，传声校猎渭水湄。羽林六军各出射，笼山络野张罝维。雕弓绣韣不知数，翻身灭没皆蛾眉。赤鹰黄鹘云中来，妖狐狡兔无所依。人烦马殆禽兽尽，百里腥膻禾黍稀。暖山度腊东风微，宫娃赐浴长汤池。刻成玉莲喷香液，漱回烟浪深逶迤。犀屏象荐杂罗列，锦凫绣雁相追随。破簪碎钿不足拾，金沟残溜和缨緌。上皇宽容易承事，十家三国争光辉。绕床呼卢恣樗博，张灯达昼相谩欺。相君侈拟纵骄横，日从秦虢多游嬉。朱衫马前未满足，更驱武卒罗旌旗。画轮宝轴从天来，云中笑语声融怡。鸣鞭后骑何躞蹀，宫妆襟袖皆仙姿。青门紫陌多春风，风中数日残春遗。骊驹吐沫一奋迅，路人拥篲争珠玑。八姨新起合欢堂，翔鹍贺燕无由窥。万金酬工不肯去，矜能恃巧犹嗟咨。四方节制倾附媚，穷奢极侈沽恩私。堂中特设夜明枕，银烛不张光鉴帷。瑶光楼南皆紫禁，梨园仙宴临花枝。迎娘歌喉玉窈窕，蛮儿舞带金葳蕤。三郎紫笛弄烟月，怨如别鹤呼羁雌。玉奴琵琶龙香拨，倚歌促酒声娇悲。饮鹿泉边春露晞，粉梅檀杏飘朱墀。金沙洞口长生殿，玉蕊峰头王母祠。禁庭术士多幻化，上前较胜纷相持。罗公如意夺颜色，三藏袈裟成散丝。蓬莱池上望秋月，无云万里悬清辉。上皇夜半月中去，三十六宫愁不归。月中秘乐天半间，丁珰玉石和埙篪。宸聪听览未终曲，却到人间迷是非。千秋御节在八月，会同万国朝华夷。花萼楼南大合乐，八音九奏鸾来仪。都卢寻橦诚龌龊，公孙剑伎方神奇。马知舞彻下床榻，人惜曲终更羽衣。禄山此时侍御侧，金鸡画障当罘罳。绣祤衣褓日屃赑，甘言狡计愈娇痴。诏令上路建甲第，楼通走马如飞翚。大开内府恣供给，玉缶金筐银簸箕。异谋潜炽促归去，临轩赐带盈十围。忠臣张公识逆状，日日切谏上弗疑。汤成召浴果不至，潼关已溢渔阳师。御街一夕无禁鼓，玉辂顺动西南驰。九门回望尘坌多，六龙夜驭兵卫疲。县官无人具军顿，行宫彻屋屠云螭。马嵬驿前驾不发，宰相射杀冤者谁。长眉鬓发作凝血，空有君王潜涕洟。青泥坂上到三蜀，金堤城边止九旂。移文泣祭昔臣墓，度曲悲歌秋雁辞。明年尚父上捷书，洗清观阙收封畿。两君相见望贤顿，君臣鼓舞皆歔欷。宫中亲呼高骠骑，潜令改葬杨真妃。花肤雪艳不复见，空有香囊和泪滋。銮舆却入华清宫，满山红实垂相思。飞霜殿前月悄悄，迎春亭下风飔飔。雪衣女失玉笼在，长生鹿瘦铜牌垂。象床尘凝罨飒被，画檐虫网颇梨碑。碧菱花覆云母陵，风篁雨菊低离披。真人影帐偏生草，果老药堂空掩扉。鼎湖一日失弓剑，桥山烟草俄霏霏。空闻玉碗入金市，但见铜壶飘翠帷。开元到今逾十纪，当初事迹皆残隳。竹花唯养栖梧凤，水藻周游巢叶龟。会昌御宇斥内典，去留二教分黄缁。庆山污潴石瓮毁，红楼绿阁皆支离。奇松怪柏为樵苏，童山眢谷亡嶮巇，烟中壁碎摩诘画，云间字失玄宗诗。石鱼岩底百寻井，银床下卷红绠迟。当时清影荫红叶，一旦飞埃埋素规。韩家烛台倚林杪，千枝灿若山霞摛。昔年光彩夺天月，昨日销熔当路岐。龙宫御榜高可惜，火焚牛挽临崎峗。孔雀松残赤琥珀，鸳鸯瓦碎青琉璃。今我前程能几许，徒有余息筋力羸。逢君话此空洒涕，却忆欢娱无见期。主翁莫泣听我语，宁劳感旧休吁嘻。河清海宴不难睹，我皇已上升平基。湟中土地昔湮没，昨夜收复无疮痍。戎王北走弃青冢，虏马西奔空月支。两逢尧年岂易偶，愿翁颐养丰肤肌。平明酒醒便分首，今夕一樽翁莫违。', '津阳门北临通逵，雪风猎猎飘酒旗。泥寒款段蹶不进，<br>疲童退问前何为。酒家顾客催解装，案前罗列樽与卮。<br>青钱琐屑安足数，白醪软美甘如饴。开垆引满相献酬，<br>枯肠渴肺忘朝饥。愁忧似见出门去，渐觉春色入四肢。<br>主翁移客挑华灯，双肩隐膝乌帽欹。笑云鲐老不为礼，<br>飘萧雪鬓双垂颐。问余何往凌寒曦，顾翁枯朽郎岂知。<br>翁曾豪盛客不见，我自为君陈昔时。时平亲卫号羽林，<br>我才十五为孤儿。射熊搏虎众莫敌，弯弧出入随佽飞。<br>此时初创观风楼，檐高百尺堆华榱。楼南更起斗鸡殿，<br>晨光山影相参差。其年十月移禁仗，山下栉比罗百司。<br>朝元阁成老君见，会昌县以新丰移。幽州晓进供奉马，<br>玉珂宝勒黄金羁。五王扈驾夹城路，传声校猎渭水湄。<br>羽林六军各出射，笼山络野张罝维。雕弓绣韣不知数，<br>翻身灭没皆蛾眉。赤鹰黄鹘云中来，妖狐狡兔无所依。<br>人烦马殆禽兽尽，百里腥膻禾黍稀。暖山度腊东风微，<br>宫娃赐浴长汤池。刻成玉莲喷香液，漱回烟浪深逶迤。<br>犀屏象荐杂罗列，锦凫绣雁相追随。破簪碎钿不足拾，<br>金沟残溜和缨緌。上皇宽容易承事，十家三国争光辉。<br>绕床呼卢恣樗博，张灯达昼相谩欺。相君侈拟纵骄横，<br>日从秦虢多游嬉。朱衫马前未满足，更驱武卒罗旌旗。<br>画轮宝轴从天来，云中笑语声融怡。鸣鞭后骑何躞蹀，<br>宫妆襟袖皆仙姿。青门紫陌多春风，风中数日残春遗。<br>骊驹吐沫一奋迅，路人拥篲争珠玑。八姨新起合欢堂，<br>翔鹍贺燕无由窥。万金酬工不肯去，矜能恃巧犹嗟咨。<br>四方节制倾附媚，穷奢极侈沽恩私。堂中特设夜明枕，<br>银烛不张光鉴帷。瑶光楼南皆紫禁，梨园仙宴临花枝。<br>迎娘歌喉玉窈窕，蛮儿舞带金葳蕤。三郎紫笛弄烟月，<br>怨如别鹤呼羁雌。玉奴琵琶龙香拨，倚歌促酒声娇悲。<br>饮鹿泉边春露晞，粉梅檀杏飘朱墀。金沙洞口长生殿，<br>玉蕊峰头王母祠。禁庭术士多幻化，上前较胜纷相持。<br>罗公如意夺颜色，三藏袈裟成散丝。蓬莱池上望秋月，<br>无云万里悬清辉。上皇夜半月中去，三十六宫愁不归。<br>月中秘乐天半间，丁珰玉石和埙篪。宸聪听览未终曲，<br>却到人间迷是非。千秋御节在八月，会同万国朝华夷。<br>花萼楼南大合乐，八音九奏鸾来仪。都卢寻橦诚龌龊，<br>公孙剑伎方神奇。马知舞彻下床榻，人惜曲终更羽衣。<br>禄山此时侍御侧，金鸡画障当罘罳。绣祤衣褓日屃赑，<br>甘言狡计愈娇痴。诏令上路建甲第，楼通走马如飞翚。<br>大开内府恣供给，玉缶金筐银簸箕。异谋潜炽促归去，<br>临轩赐带盈十围。忠臣张公识逆状，日日切谏上弗疑。<br>汤成召浴果不至，潼关已溢渔阳师。御街一夕无禁鼓，<br>玉辂顺动西南驰。九门回望尘坌多，六龙夜驭兵卫疲。<br>县官无人具军顿，行宫彻屋屠云螭。马嵬驿前驾不发，<br>宰相射杀冤者谁。长眉鬓发作凝血，空有君王潜涕洟。<br>青泥坂上到三蜀，金堤城边止九旂。移文泣祭昔臣墓，<br>度曲悲歌秋雁辞。明年尚父上捷书，洗清观阙收封畿。<br>两君相见望贤顿，君臣鼓舞皆歔欷。宫中亲呼高骠骑，<br>潜令改葬杨真妃。花肤雪艳不复见，空有香囊和泪滋。<br>銮舆却入华清宫，满山红实垂相思。飞霜殿前月悄悄，<br>迎春亭下风飔飔。雪衣女失玉笼在，长生鹿瘦铜牌垂。<br>象床尘凝罨飒被，画檐虫网颇梨碑。碧菱花覆云母陵，<br>风篁雨菊低离披。真人影帐偏生草，果老药堂空掩扉。<br>鼎湖一日失弓剑，桥山烟草俄霏霏。空闻玉碗入金市，<br>但见铜壶飘翠帷。开元到今逾十纪，当初事迹皆残隳。<br>竹花唯养栖梧凤，水藻周游巢叶龟。会昌御宇斥内典，<br>去留二教分黄缁。庆山污潴石瓮毁，红楼绿阁皆支离。<br>奇松怪柏为樵苏，童山眢谷亡嶮巇，烟中壁碎摩诘画，<br>云间字失玄宗诗。石鱼岩底百寻井，银床下卷红绠迟。<br>当时清影荫红叶，一旦飞埃埋素规。韩家烛台倚林杪，<br>千枝灿若山霞摛。昔年光彩夺天月，昨日销熔当路岐。<br>龙宫御榜高可惜，火焚牛挽临崎峗。孔雀松残赤琥珀，<br>鸳鸯瓦碎青琉璃。今我前程能几许，徒有余息筋力羸。<br>逢君话此空洒涕，却忆欢娱无见期。主翁莫泣听我语，<br>宁劳感旧休吁嘻。河清海宴不难睹，我皇已上升平基。<br>湟中土地昔湮没，昨夜收复无疮痍。戎王北走弃青冢，<br>虏马西奔空月支。两逢尧年岂易偶，愿翁颐养丰肤肌。<br>平明酒醒便分首，今夕一樽翁莫违。', '津阳门为华清宫北门。诗序说，他于冬日薄暮经此门，并宿旅邸，店主老翁自言曾事唐玄宗,为道承平故实,翌日于马上据所闻写成此诗。这是唐代七言叙事诗中的巨制,共100韵，1400字。诗中借店主翁口述,写出骊山华清宫中宫殿楼观的繁华壮丽,唐玄宗携杨贵妃来此游乐的盛况，杨国忠和虢国夫人的骄横奢侈，以及梨园乐工歌伎吹奏歌舞等热闹场面；继而写安禄山的被宠和贵盛，安史乱起、玄宗奔蜀、杨妃被杀等情事；最后写华清宫乱后的荒凉景象，并以期望中兴及与店主老翁分别作结。据篇末“湟中土地昔湮没，昨夜收复无疮痍”二句，则当作于大中五年张义潮收复河湟时，所谓店主翁乃是假托。此诗反映了以唐玄宗为首的封建统治集团的骄奢荒淫，抒发了作者今昔盛衰之感。清代管世铭说：“郑嵎《津阳门诗》，七言百韵，为三唐歌行中第一长幅，可与《连昌宫词》、《长恨歌》参观”(《读雪山房唐诗钞凡例》)。诗中有作者自注，详陈典实轶事，内容丰富，为研究唐玄宗时期政治和宫廷生活的重要参考资料。', '过昭应有感天宝间事作此', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (154, '7e9a440a5d2742998183e709046be5b0', '晓发昭应', 93, 5, 843, '843年', '行人见雪愁，初作帝乡游。旅店开偏早，乡帆去未收。灯残催卷席，手冷怕梳头。是物寒无色，汤泉正自流。', '行人见雪愁，初作帝乡游。旅店开偏早，乡帆去未收。灯残催卷席，手冷怕梳头。是物寒无色，汤泉正自流。', '项斯少以诗闻名天下，于会昌三年以诗干谒时任国子祭酒杨敬之。此诗中“初作帝乡游”一句点明此诗写于项斯初来长安途中，昭应驿亦是西入长安的必经之路，所以此诗约作于项斯西入长安，干谒杨敬之时间，大约是会昌三年所作。诗的首联言作者此时出行的动机以及出行的时节为寒冬雪季。颔联写作者出发之早。“乡帆”一句可推知作者应从水路而来。诗的颈联将冬季早起时的“卷席”“手冷”“怕梳头”等寻常动作写得工稳至极，最见作者功力。尾联描绘驿馆周边景色。时值冬季。万物无色，天地冰封。唯有温泉水暖，不冻自流。唐代昭应驿周边有温泉，唐人诗作中多有提及，亦有唐人于温泉上题壁留诗，是为一种文学景观。全诗因是“初”来长安，所以诗中所写皆是诗人眼中好奇的表现。自颔联以下，全诗自醒至梳头再至出门观景，具有流动性，也是全诗的一大特色。', '会昌三年，项斯于长安以诗干谒国子祭酒杨敬之。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (155, '05ae7a3586fb4bbb9b8dbc19498b5e12', '宿昭应', 94, 5, -1, '年份未详', '夜忆开元寺，凄凉里巷间。薄烟通魏阙，明月照骊山。半壁空宫闭，连天白道闲。清晨更回首，独向灞陵还。', '夜忆开元寺，凄凉里巷间。薄烟通魏阙，明月照骊山。<br>半壁空宫闭，连天白道闲。清晨更回首，独向灞陵还。', '此诗具体创作时间无考，当是作者夜宿昭应，目见开元寺凋敝景象，心想唐玄宗开元盛世，心有所感，遂有此作。唐诗中，凡是创作于昭应县的作品，大都离不开作者对唐玄宗开元盛世的咏叹。具体表现为目见开元寺、华清宫或者温泉池，心中感叹唐玄宗开元盛世，李杨二人的悲惨爱情故事等等内容，此作也不例外。作者夜宿昭应，在夜晚思念开元寺，可知此时开元寺或是不存在，或是不复玄宗朝香火鼎盛的局面，满目凋敝景象。于是作者在下一句中写及自己的凄凉感受。颔联两句写作者夜中所见，薄烟、明月，一片雾气缭绕的朦胧景象。颈联两句写作者思考、回忆开元寺的现状，并由此写到安史之乱后昭应县的荒凉凋敝景象。安史之乱前，唐代帝王多次临幸昭应，安史之乱后，出游次数减少，昭应县也就由前代的繁盛景象转换为连皇帝的御道都整日无车，终日清闲的荒凉景象。尾联两句写作者从昭应出发，向长安出发。此句或是将昭应县不同往日的凋敝景象同自己孤寂寥落的心态相联系，感叹自我孤寂的感受，抒情婉转蕴藉。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (156, '41d545e80bf942459c9c6fd33eb8ce0e', '雪后过昭应', 95, 5, -1, '年份未详', '路过章台气象宽，九重城阙在云端。烟含上苑沈沈紫，雪露南山愯愯寒。绮陌已堪骑宝马，绿芜行即弹金丸。灞川南北真图画，更待残阳一望看。', '路过章台气象宽，九重城阙在云端。烟含上苑沈沈紫，雪露南山愯愯寒。绮陌已堪骑宝马，绿芜行即弹金丸。灞川南北真图画，更待残阳一望看。', '此诗具体创作时间无考。根据作品内容，当是作者途经昭应，见秦时旧宫，有感而作。作品的首联先点出作者在昭应目睹秦朝旧宫胜迹的感受。因为秦时建筑宽阔宏大，所以在视觉上呈现出“宽”的特征，又因为秦时实力强大，所建造的宫殿给人在心理上以“宽”的特征，所以作者在作品中直言自己面对秦时旧宫的直观感受就是“气象宽”。随后又写秦时旧宫数量之多，占地面积之广，如在云端一般。颔联两句写作者在昭应登高远望，能够看见汉时上林苑，也能够看见终南山的积雪。颈联两句以作者的感受衬托昭应环境之美。首先是昭应被花草装饰过的道路，只能配得上其宝马良驹才能行走；其次是昭应周边的绿色草原，行走在上边就像挟金丸打猎的少年一般恣肆自在。作品的最后一联又将视角移至观察灞水一带，言灞水周围的环境就像图画一般，但最值得观看的还是灞上的夕阳景色。全作以描写对象的多样化，观察视角的频繁转换，为读者呈现出了一幅昭应早春图景。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (157, '76a00e7801c04c4294da39da60a5c948', '过新丰道中', 39, 6, 740, '740年', '西下长乐阪，东入新丰道。雨多车马稀，道上生秋草。太阴蔽皋陆，莫知晚与早。雷雨杳冥冥，川谷漫浩浩。诏书植嘉木，众言桃李好。自愧无此容，归从汉阴老。', '西下长乐阪，东入新丰道。雨多车马稀，道上生秋草。<br>太阴蔽皋陆，莫知晚与早。雷雨杳冥冥，川谷漫浩浩。<br>诏书植嘉木，众言桃李好。自愧无此容，归从汉阴老。', '唐玄宗开元二十八年，储光曦自乡至秦地终南山隐居，遂有此作。作者在路途中看见桃李芬芳，佳木繁荫，自己则人生失意，被迫隐居，语言质朴，情感真挚。作品前四句概写途中所见。“雨多”两句是作者写路途中人烟稀少，秋草丛生的荒敝景象，作者目睹此景自然会与自己当下的人生经历相联系，作者的失忆寥落心情初见端倪。“太阴”至“川谷”四句接着写路途中作者所见的恶劣天气。“太阴”两句写途中雾气弥漫，行走其中不知时间早晚；“雷雨”两句写阴雨连绵，几日不见晴天。正是在这样恶劣环境及天气的烘托下，我们才可以推知此时作者的心情不会是朝气蓬勃的，而是失意寥落的。自此以下四句用典抒发感情。首句写开元二十八年皇帝下诏在路途两边手植佳木，此时作者想象佳木上结出的鲜美瓜果，将桃李鲜嫩的样子与自己的面容相对比，更显出自己憔悴的面容。接着作者在最后两句中自我叹息，感叹生不逢时，无人肯用的无奈悲凉，在这种环境下只能归去终南山隐居。', '唐玄宗开元二十八年，储光羲自乡至秦地终南山隐居，遂有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (158, 'e40157e3a4d9449faf32362e5cbeddc6', '新丰作贻殷四校书', 39, 6, 742, '742年', '汉皇思旧邑，秦地作新丰。南出华阳路，西分长乐宫。安知天地久，不与昔年同。鸡犬暮声合，城池秋霁空。纷吾从此去，望极咸阳中。不见芸香阁，徒思文雅雄。', '汉皇思旧邑，秦地作新丰。南出华阳路，西分长乐宫。<br>安知天地久，不与昔年同。鸡犬暮声合，城池秋霁空。<br>纷吾从此去，望极咸阳中。不见芸香阁，徒思文雅雄。', '唐玄宗天宝元年，储光羲闲居洛阳，多往来两京，因有此作。诗题中所称殷四校书，当属于当是新丰的政府工作人员，所以这样一篇写给政府工作人员的应酬诗作，其中难免会出现一些对新丰社会治理等方面的赞颂。起首两句先述新丰的发展历史：新丰原无此邑，是汉高祖刘邦为其父亲仿照故里丰县修建的城邑，因是新建，所以称“新丰”。接着两句又写新丰的地理位置，在华山之南，长乐宫之西。以下四句又从新丰的社会现状着手描述，赞颂了政府工作人员的卓越治理功绩，将新丰建造的犹如桃花源仙境一般的社会和睦，环境优美。作品的结尾两句作者的着眼点又从新丰社会治理上移到对友人能力的赞颂上，作者说此次离别，虽然不见友人校书的芸香阁，但依旧会想念友人雄深雅健的文笔文风。从写景到写人，作品内容优美，结构清晰，不失为一部佳作。', '殷遥大概死于天宝三年，此诗当作于天宝三年前。而储光羲于天宝元年居新丰，盖此时做。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (159, '47d09d9f0d9f4d5c89b75c051ab8e5e8', '新丰主人', 39, 6, 742, '742年', '新丰主人新酒熟，旧客还归旧堂宿。满酌香含北彻花，盈樽色泛南轩竹。云散天高秋月明，东家少女解秦筝。醉来忘却巴陵道，梦中疑是洛阳城。', '新丰主人新酒熟，旧客还归旧堂宿。满酌香含北彻花，<br>盈樽色泛南轩竹。云散天高秋月明，东家少女解秦筝。<br>醉来忘却巴陵道，梦中疑是洛阳城。', '唐玄宗天宝元年，储光羲闲居洛阳，多往来两京，因有此作。诗题写“新丰主人”，作品内容当是写作者出门做客他家的事情。作品的起首两句就以新丰主人开头，点明作者做客他家的缘由是因为主人具新酒。新丰的美酒自古有名，古往今来尤其是唐人都为这美酒留下了诸多诗篇，此作也不例外。颔联两句写美酒之香，用与竹、与花的对比衬托酒的醇香，突出酒的美妙。颈联两句写宴会时分，朗月当空，少女伴奏，美酒相伴，多有一番惬意从心而生。作品的结尾两联写作者醉酒作梦，有一番闲适惬意的感受。凡是涉及新丰，内容多描写新丰美酒，而作者另辟蹊径，借酒宴，写新丰和美的社会环境，表现作者闲适自得的心理意绪，不能不称之为佳作。', '唐玄宗天宝元年，储光羲闲居洛阳，多往来两京，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (160, '4f6273c922814a5aac3d219279fee384', '晚次新丰北野老家书事呈赠韩质明府', 1, 6, 776, '776年（大历九年）', '机鸣舂响日暾暾，鸡犬相和汉古村。数派清泉黄菊盛，一林寒露紫梨繁。衰翁正席矜新社，稚子齐襟读古论。共说年来但无事，不知何者是君恩。', '机鸣舂响日暾暾，鸡犬相和汉古村。数派清泉黄菊盛，<br>一林寒露紫梨繁。衰翁正席矜新社，稚子齐襟读古论。<br>共说年来但无事，不知何者是君恩。', '此诗或作于大历九年之后，韩质任县令期间。诗题中所称韩质，是当时新丰的县令，所以这样一篇写给县令的应酬诗作，其中难免会出现一些对新丰社会治理情况方面的赞颂。作品起首两句就从织机、鸡犬等部分着手描写新丰社会，一派和谐融融的景象。颔联两句又从新丰里盛开的菊花、紫梨等繁花描写，描绘新丰里优美的景色。颈联两句又从新丰的人文部分入手，写稚子读经，衰翁准备新的社日，井井有条的社会素描。作者在作品的最后一联对以上三联和谐融融景色进行了总结，将其归功于韩质县令治县有方，确有功劳。从整体内容上来讲，这首诗是规规矩矩的应酬作品，前有现象描述，后有功绩称颂。但从作品的前半部分而言，这首作品当是我们解读，还原唐时乡镇社会面貌的一个切入点，有值得称道的地方。', '或作于大历九年之后，韩质任县令期间。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (161, '68eb03c329f646e881b97dfb1d244a64', '送白宾客分司东都', 60, 2, 829, '829年', '赫赫声名三十春，高情人独出埃尘。病辞省闼归闲地，恩许宫曹作上宾。诗里难同相得伴，酒边多见自由身。老人也拟休官去，便是君家池上人。', '赫赫声名三十春，高情人独出埃尘。病辞省闼归闲地，恩许宫曹作上宾。诗里难同相得伴，酒边多见自由身。老人也拟休官去，便是君家池上人。', '唐文宗大和三年五月，白居易罢刑部侍郎，赴任洛阳，当时的友人悉数与白居易在长乐亭送别。此诗即作于此时期。诗的首联对白居易的为官经历以及“因病请辞”一事作了很高的赞扬。读者可以从颔联获知白居易分司洛阳一事的来龙去脉：因身体不适，白居易遂请辞归家，皇上不但恩准了这个请求，还为白居易在洛阳谋求了“太子宾客”这样的闲职。诗的下两联表现出作者对白居易从尘网中脱离，恢复“自由身”的羡慕之情。并在诗的结尾，作者也生发出“休官回家”的想法。', '大和三年五月，白乐天分司东洛，朝贤悉会兴化亭送别。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (162, 'f352a21ea5264a36ab88aa249081af71', '刑部白侍郎谢病长告，改宾客分司，以诗赠别', 59, 2, 829, '829年', '鼎食华轩到眼前，拂衣高谢岂徒然。九霄路上辞朝客，四皓丛中作少年。他日卧龙终得雨，今朝放鹤且冲天。洛阳旧有衡茆在，亦拟抽身伴地仙。', '鼎食华轩到眼前，拂衣高谢岂徒然。九霄路上辞朝客，四皓丛中作少年。他日卧龙终得雨，今朝放鹤且冲天。洛阳旧有衡茆在，亦拟抽身伴地仙。', '唐文宗大和三年五月，白居易罢刑部侍郎，赴任洛阳，当时的友人悉数与白居易在长乐亭送别。此诗即作于此时期。诗的首联写祖筵上的珍馐美食，“拂衣高谢岂徒然”写到白居易借病辞官一事。颔联中，“九霄”意为最高的地方，比喻朝廷；此句中作者还以“商山四皓”比喻不为名利，辞官归家的白居易。颈联借用《世说新语》中“支公养鹤”的典故，安慰白居易虽然辞官回家，但还是有能力再度入朝做官的。尾联中“衡茆”一词，本意是“衡门茅舍”，此指刘禹锡在洛阳的官舍。“地仙”一词则是指回家后，担任闲职的白居易就如仙人一般过着潇洒的生活。尾同张籍作品一样，表达出作者想要同白居易一同归隐的决心。', '大和三年五月，白乐天分司东洛，朝贤悉会兴化亭送别。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (163, '7e05f330916b4ae2aec94d84a8febec0', '新丰路逢故人', 12, 6, 810, '约810年', '尘土长路晚，风烟废宫秋。相逢立马语，尽日此桥头。知君不得意，郁郁来西游。惆怅新丰店，何人识马周。', '尘土长路晚，风烟废宫秋。相逢立马语，尽日此桥头。知君不得意，郁郁来西游。惆怅新丰店，何人识马周。', '元和五年，白居易在长安城周边的新丰店偶遇古人，因有此作。诗的首联点明时间地点：秋日的黄昏，新丰废宫（汉高祖刘邦为其父修建的行宫）的路旁，二人相遇。颔联对二人相遇的情景进行了简单的描述。“尽日”一词通过写二人相遇后交谈的时间较长，侧面写二人的感情深厚。后两联主要是作者在知道故人的近况（“不得意”）后，借用“马周”典故对其进行劝勉。马周是唐太宗贞观时期人，年少好学，客居新丰，却因落拓，不为人所重。而后西游长安，知遇于太宗李世明，拜官中书舍人。这里作者借助唐初马周的典故，对有着相同境遇的故人进行劝勉，希望其不要因为一时的困窘而对大有可为的未来失去信心。', '元和五年，新丰店逢故人，有诗以赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (164, '6e824ca9d3e74afb8a3516da0d8fa1b6', '寒食新丰别友人', 90, 6, -1, '年份未详', '一百五日家未归，新丰鸡犬独依依。满楼春色傍人醉，半夜雨声前计非。缭绕沟塍含绿晚，荒凉树石向川微。东风吹泪对花落，憔悴故交相见稀。', '一百五日家未归，新丰鸡犬独依依。满楼春色傍人醉，半夜雨声前计非。缭绕沟塍含绿晚，荒凉树石向川微。东风吹泪对花落，憔悴故交相见稀。', '此诗具体创作时间无考。寒食为古代的传统节日，是冬至后的第105天，又被称为一百五日。所以作品的起首两句中先以“一百五日”起头，说自己寒食节的时候，仍然在外漂泊，看见新丰街道上鸡犬相依，顿时心生思乡之情。颔联两句写作者在寒食这天的所闻所见：见窗外春色，引人陶醉；闻半夜雨声，惹人伤心。从作者的所闻所见，可以领会作者此时独处异乡，思念家人的苦痛心情。颈联两句又写作者所见，句中写“缭绕”、“向川微”等词语，可以说明这句是写作者眺望所见。眺望远方，除了观美景以愉悦内心以外，还有眺望家乡之意，是思念家乡的代表性动作。而且，在意象的选择上，此句全是或晚、或微的意象，这也是受到作者此时低落心情影响的结果。在最后一联中，作者见到故交后，就再也无法抑制心中的情绪，以一种更为直白的方式表现出自己此时的心情：“憔悴”一词当是对作者此时面容的一种特写，借此可以得知作者思乡情感的深浓。全诗从内容上来讲，前三联内容较为委婉，除了首句点明作者寒食未归家外，其他的部分读者只能凭借对意象的解读，揣测作者心情；而到了最后一联，因见到故乡旧友，作者一直压抑的内心得以抒发，表达方式相较前三联直白一些，但总体上还是婉转内敛的。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (165, '9eff3b127fdd4d2582b60aef7c1124da', '过新丰', 73, 6, 839, '约839年', '一剑乘时帝业成，沛中乡里到咸京。寰区已作皇居贵，风月犹含白社情。泗水旧亭春草遍，千门遗瓦古苔生。至今留得离家恨，鸡犬相闻落照明。', '一剑乘时帝业成，沛中乡里到咸京。寰区已作皇居贵，<br>风月犹含白社情。泗水旧亭春草遍，千门遗瓦古苔生。<br>至今留得离家恨，鸡犬相闻落照明。', '唐文宗开成四年，温庭筠长安应举，不第。归家途中，因有此作。作者应举失败，人生愿望难以实现，惆怅苦痛中，思念家乡当是对这种心情的一种慰藉，此诗就是在这一背景下产生的。开头两联先叙刘邦称帝长安，为其父模仿故居修建新丰一事。颈联言刘邦故居荒凉凋敝景象。尾联两句写当时迁移到此的刘邦故居的人至今还有思念家乡的情绪。如果对这首作品中的典故或者意象作分析的话，可以发现作者借典故抒发思乡之情的用意俯拾即是：“风月”句中写到新丰县人民对故居的思念；颈联两句又对故居进行了细致描写；尾联首句“离家恨”更是明确点出思念家乡的情绪。所以可以断定此作就是作者经过新丰，凭借新丰的典故或者意象抒发自己人生愿望难以实现而产生的思乡之情。另外，此作首句写刘邦称帝之事，也可看作是作者想要实现自己人生愿望的艺术性表达，但这种情绪不是这篇作品的主要表达的内容，所以此不赘述。', '唐文宗开成四年，温庭筠长安应举，不第。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (166, '6a42aa06fe554f98ac207a8437404511', '新丰', 71, 6, -1, '年份未详', '沛中歌舞百余人，帝业功成里巷新。半夜素灵先哭楚，一星遗火下烧秦。貔貅扫尽无三户，鸡犬归来识四邻。惆怅故园前事远，晓风长路起埃尘。', '沛中歌舞百余人，帝业功成里巷新。半夜素灵先哭楚，一星遗火下烧秦。貔貅扫尽无三户，鸡犬归来识四邻。惆怅故园前事远，晓风长路起埃尘。', '此诗具体创作时间无考。根据内容来说，此诗当属于咏史作品：通过对历史事件的回忆与当今社会作对比，抒发今昔不同的惆怅感情。作品的起首两句先言刘邦称帝长安，为其父模仿故居样貌修建新丰的典故。颔联两句再叙刘邦称帝过程中种种丰功伟绩：破秦谓推翻秦帝国统治；灭楚当是刘邦战胜项羽统一全国一事。颈联两句再次用刘邦模仿故居样貌修建新丰的典故：刘邦下令修建模仿故居样貌新丰，建成后，放入故居的鸡犬，鸡犬还是能够找回各自的家门，这里是说新丰的格局同故居相同。尾联二句写作者抒发感情，作者面对新丰这一承载太多历史故事的地点，不禁追忆往事，因而产生惆怅之情。根据内容，可以推知，作者所感叹的是刘邦的丰功伟绩，而作者所惆怅的，联系其生活时代，可以说是作者期待王朝中兴，却又无能为力的无奈之感。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (167, '8c1e170bbb1447a79e4aa8530b8f742d', '春夜赤水驿旅怀', 96, 11, -1, '年份未详', '一星残烛照离堂，失计游心归渺茫。不自寻思无道路，为谁辛苦竞时光。九衢春色休回首，半夜溪声正梦乡。却羡去年买山侣，月斜渔艇倚潇湘。', '一星残烛照离堂，失计游心归渺茫。不自寻思无道路，为谁辛苦竞时光。九衢春色休回首，半夜溪声正梦乡。却羡去年买山侣，月斜渔艇倚潇湘。', '此诗具体创作时间无考。根据作品内容，当是作者途次赤水驿，有感而作。作品首联中的“一星残烛”凸显出作者经济困窘的状态，“失计”一词体现出作者此时不仅经济困窘，而且人生道路上也遇到阻碍。颔联两句作者点明自己多次为自己的人生寻找方向，寻找道路，但都没有结果，不过是白费时光。颈联两句写赤水驿周边的景色：正是春日，溪水潺潺，不禁撩拨作者客居远游人的思乡之心。作品的最后一联是作者自己为排解悲苦情绪说出的勉励语：此时此景，自己却羡慕去年相遇的隐居高士，如今他在月下小艇之上，渡着逍遥的时光。而我还在为人生前途谋求方向，不如我也归隐深山，追求逍遥的时光。全诗从作者的经济状态写起，又通过自我心迹的抒发可以得知作者的人生状态，后来又夹杂著作者思乡的感情，最后以心生归隐之情结束全篇。可以说是，完整描绘了作者在赤水驿的思考过程，在这思考过程之中，读者不禁为作者迷惘的人生前路扼腕叹息。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (168, 'd63af164ed094ef9b1403709b9cd18f0', '华州客舍奉和崔端公春城晓望', 87, 12, -1, '年份未详', '不语看芳径，悲春懒独行。向人微月在，报雨早霞生。贫病催年齿，风尘掩姓名。赖逢骢马客，郢曲缓羁情。', '不语看芳径，悲春懒独行。向人微月在，报雨早霞生。<br>贫病催年齿，风尘掩姓名。赖逢骢马客，郢曲缓羁情。', '此诗具体创作时间无考。根据作品内容，当是作者途次华州，与友人唱和的作品。作品的首联中开头的“不”字就已经含有一些悲苦的情绪，次句中“悲”、“懒”字进一步说明作者内心有悲苦情绪。颔联两句写作者所见：山林之中，有微月当空，天气清爽，又有早霞当空。通过叠加天空中绚丽的色彩，描绘一幅春日早晨景象。颈联部分由写景转向叙事抒情，提及了导致作者心中悲苦情绪的原因是，作者年龄已大，却仍然风尘仆仆，宦游各地没有稳定的生活，这样的生活状态令作者力不从心，心生悲苦。尾联两句写作者的悲苦情绪在遇见故人之后表现出的缓和情绪。“赖”字写明了故人的到来是影响作者心情缓和的重要原因。“郢曲”写明了作者和友人在华州客舍进行的一系列娱乐活动。全诗前三联令人读来为作者担忧，最后一联作者心情的缓和又让人如释重负，有柳暗花明又一村的惊喜感。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (169, '063674940cdf4f8e9ffefe403f2d6b91', '途次华州陪钱大夫登城北楼春望因睹李崔令狐三相国唱和之什翰林旧侣继踵华城山水清高鸾凤翔集皆忝夙眷遂题此诗', 59, 12, 828, '828年', '城楼四望出风尘，见尽关西渭北春。百二山河雄上国，一双旌旆委名臣。壁中今日题诗处，天上同时草诏人。莫怪老郎呈滥吹，宦途虽别旧情亲。', '城楼四望出风尘，见尽关西渭北春。百二山河雄上国，<br>一双旌旆委名臣。壁中今日题诗处，天上同时草诏人。<br>莫怪老郎呈滥吹，宦途虽别旧情亲。', '唐文宗大和二年，刘禹锡自洛至京，任主客郎中。途经陕州，因有此作。诗的首联言登城所见。“关西渭北”是谓关中平原，因其在潼关之西，渭水流域，因有所称。颔联写秦地之险，继而突出华州刺史职位之重。“旌旆”者，唐代皇帝赐予华州刺史之信物。因诗题中李绛、崔群、令狐楚皆历任华州刺史，故有此句。颈联继续歌颂李绛、崔群、令狐楚等人的功绩。“草诏人”为翰林学士的代称，因钱、李、崔、令狐等人皆为翰林学士，故有此称。刘禹锡此时将入长安，任主客郎中，故有尾联中“老郎”之谓，突出自己官位低下。“滥吹”借用《韩非子》“滥竽充数”典故，谦辞，言己诗滥竽充数。此句处处用谦敬之辞，与诗题中“忝”“夙”二字相呼应。全诗借助对历任华州刺史的赞颂，表现作者入京之前心中满含的凌云抱负。', '大和二年由洛阳入朝任主客郎中，途经陕州华州，有诗。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (170, 'fecdb083678a462fae2e5f0d3445fa43', '旅次华州，赠袁右丞', 12, 12, 801, '801年', '渭水绿溶溶，华山青崇崇。山水一何丽，君子在其中。才与世会合，物随诚感通。德星降人福，时雨助岁功。化行人无讼，囹圄千日空。政顺气亦和，黍稷三年丰。客自帝城来，驱马出关东。爱此一郡人，如见太古风。方今天子心，忧人正忡忡。安得天下守，尽得如袁公。', '渭水绿溶溶，华山青崇崇。山水一何丽，君子在其中。才与世会合，物随诚感通。德星降人福，时雨助岁功。<br>化行人无讼，囹圄千日空。政顺气亦和，黍稷三年丰。客自帝城来，驱马出关东。爱此一郡人，如见太古风。<br>方今天子心，忧人正忡忡。安得天下守，尽得如袁公。', '贞元十七（801）年，白居易旅次华州，有诗赠时任华州州丞，遂有此诗。诗的前四句通过对华州自然风貌的简要概括，对袁右丞的功绩进行了称颂。“才与”与“德星”二句言之所以华州在袁右丞治下如此安定祥和，是由于袁右丞的才能为世所用，并且袁右丞上应德星，能为百姓带来幸福的缘故。接下来两句是对袁右丞治下的华州从风俗人情方面进行了称颂。“化行”就是指推行王道教化，是古代统治者统治百姓的必要手段。“政顺”一句是说华州在袁右丞的管理下不仅政治格局平衡无党争，而且华州还实现了粮食丰收，人民安居乐业。接下来四句依旧是称颂袁右丞的功绩。“爱此”一句是说作者喜爱华州这里古朴的风俗人情。诗的结尾两句依旧是称颂，作者言此时的唐朝就需要像袁右丞这样的有才能，有功绩，有作为的官员。全诗一味赞颂袁右丞的功绩，格调不高。但可通过此诗窥见贞元时期华州的风俗人情。', '贞元十七年，旅次华州。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (171, 'e58bc68ef2f34d4da4d9efb7a08648ab', '自渭南晚次华州', 97, 12, -1, '年份未详', '前楼仙鼎原，西经赤水渡。火云入村巷，余雨依驿树。我行伤去国，疲马屡回顾。有如无窠鸟，触热不得住。峨峨华峰近，城郭生夕雾。逆旅何人寻，行客暗中住。却思林丘卧，自惬平生素。劳役今若兹，羞吟招隐句。', '前楼仙鼎原，西经赤水渡。火云入村巷，余雨依驿树。<br>我行伤去国，疲马屡回顾。有如无窠鸟，触热不得住。<br>峨峨华峰近，城郭生夕雾。逆旅何人寻，行客暗中住。<br>却思林丘卧，自惬平生素。劳役今若兹，羞吟招隐句。', '此诗具体创作时间无考。根据作品内容，当是作者自渭南至华州，有感而作。作品前四句先对作者的行旅路径进行了简单的勾勒描写，并对行旅途中的景色作了一番简要描述：到华州时，正是傍晚黄昏微雨时分，天空上正生起绚丽的火烧云。“我行”到“有如”四句主要是作者表达自己在行旅途中产生的悲苦感受。这种感受因为不得不离开金城长安而产生。这种感受就像作者所说的，恰似一支没有巢穴的小鸟一般，无依无靠的感觉。“峨峨”至“逆旅”四句主要是写作者晚上行至华州时的所见所闻。天色将晚，作者在四处寻找着今晚休息的旅舍。找到旅舍后，作者又在回想刚才的那种无依无靠的苦痛感受。在最后四句中，作者在想不如逃离这样苦难一般的行旅生活，归隐山水，高卧林丘可能是自己最好的选择。这首作品着重对作者的羁旅情绪作了描述，是今日读者设想当年旅客心态的重要凭借。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (172, '5578e2da20794697b9ee9f0cbde0c356', '蒙恩除侍御史行次华州寄蒋相', 84, 12, -1, '年份未详', '林下天书起遁逃，不堪移疾入尘劳。黄河近岸阴风急，仙掌临关旭日高。行野众喧闻雁发，宿亭孤寂有狼嗥。荀家位极兼禅理，应笑埋轮着所操。', '林下天书起遁逃，不堪移疾入尘劳。黄河近岸阴风急，仙掌临关旭日高。行野众喧闻雁发，宿亭孤寂有狼嗥。荀家位极兼禅理，应笑埋轮着所操。', '此诗具体创作时间无考，根据作品题目，当是作者行至华州，除官御史，有感而作。作品的开头两句先写作者授官御史一事，呼应题目。首句“天书”是指皇帝的委任通知，次句中“入尘劳”是说作者不顾自己的疾病，答应朝廷作官一事。接下来的两联主要是对作者授官之后所见景物进行了一番描绘。颔联以对仗的形式写出黄河水势汹涌，华山危岩高耸，营造一番辽阔雄宏的意境，凸显出作者授官之后对未来的信心与美好期待。颈联仍是对仗形式些作者行旅图中所见。首句以路上人多嘈杂附以雁声阵阵，颇显喧闹景象；次句借孤亭寂静加以狼嚎凄凄，稍有凄冷意境。这两句写景甚妙，前句写人多嘈杂是喧闹景象，后句写孤寂无人是静寂之境，暗示作者授官后的心态由最初的欣喜若狂发展成了平复心情后的安定自若。就是在这种安定自若的心情主导下，作者写下最后两句，寄托了对自己的勉励。首句借三国荀家，说明作者认为这次授官当是一次飞黄腾达，平步青云的大好机会，应该加以把握。次句借古时埋轮以示坚定不移信念的意义，委婉地表现自己授官后，做好本职工作的坚定信心。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (173, '83d47b69ee5c4c168e0af51481709d5f', '晚过敷水驿却寄华州使院张郑二侍御', 98, 13, -1, '年份未详', '春雨如烟又若丝，晓来昏处晚晴时。仙人掌上芙蓉沼，柱史关西松柏祠。几许岁华销道路，无穷王事系戎师。回瞻二妙非吾侣，日对三峰自有期。', '春雨如烟又若丝，晓来昏处晚晴时。仙人掌上芙蓉沼，柱史关西松柏祠。几许岁华销道路，无穷王事系戎师。回瞻二妙非吾侣，日对三峰自有期。', '此诗具体创作时间无考。根据作品题目，当是作者途经敷水驿，有感而作。此作虽写于敷水驿，但诗中除首联外，其余三联都是写华山景色，或是表白作者心迹。作品的起首两句写敷水驿天气：此时正是春日，虽是小雨，但还不见有停雨的迹象，又加之天气阴冷，所以呈现出阴雨濛濛的隐约意境。就在这种稍显恶劣的天气之下，作者生思念友人之心，所以才引出作品的后三联。颔联两句中“仙人掌”、“芙蓉沼”、“松柏祠”等景物都是华山上的胜迹，写此与题目中“华州”相映。颈联两句写作者行旅途中的羁愁与辛苦：上句言作者多年漂泊之辛苦，下句写作者因公事在身，无奈漂泊之羁愁。尾联两句写作者想要摆脱这种羁旅生活状态，生发出归隐深山的愿望。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (174, '9c6ee80f3e6644819dc2dfa6cbb514a8', '敷水驿', 99, 13, -1, '年份未详', '空见水名敷，秦楼昔事无。临风驻征骑，聊复捋髭须。', '空见水名敷，秦楼昔事无。临风驻征骑，聊复捋髭须。', '此诗具体创作时间无考。根据作品内容，大致可以推断此作应是作者途经罗敷水，感叹今昔不同的咏史作品。作品的起首两句先叙作者途经罗敷水之事。此句中，作者正是有感于罗敷水因汉朝美女秦罗敷而得名的典故，所以才创作此作。但是直到作品的结尾处，还是未能领会体悟到作者面对罗敷水想要表达的具体情感，只用“捋髭须”结束全篇。值得一提的是，这个词出自《陌上桑》中行者见到秦罗敷的表现“下担捋髭须”一句，可以说作者在惊讶于秦罗敷美貌的同时，也在暗自感叹罗敷水之优美。全诗不写罗敷水之优美，却在形容秦罗敷的语句中道出，用意甚巧。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (175, '428d1031be4342f6ac587b95d558a082', '途次敷水驿伏睹华州舅氏昔日行县题诗处潸然有感', 59, 13, 805, '805年', '昔日股肱守，朱轮兹地游。繁华日已谢，章句此空留。蔓草佳城闭，故林棠树秋。今来重垂泪，不忍过西州。', '昔日股肱守，朱轮兹地游。繁华日已谢，章句此空留。蔓草佳城闭，故林棠树秋。今来重垂泪，不忍过西州。', '唐顺宗永贞年间，由王叔文、刘禹锡等人发起并参与的“永贞革新”宣告失败。刘禹锡因此被贬连州刺史。此诗即为被贬连州途中所作。诗题点时点地，身为贬谪官员的作者目睹亲人的作品，当有时间变化，感叹生命的年命之嗟。首联是诗人目睹作品后的想象。“股肱守”是指皇帝任命的州县长官的代称，“朱轮”指高级官员出行乘坐的车辆，此时代指丛舅卢征的出行车队。此刻身为被贬官员的作者目睹诗中所写，丛舅卢征的出行场面。身份地位的悬殊必然会使作者生发“己不如人”的伤感。颔联作者又感叹时光的匆匆变化。原来“己不如人”的伤感因此又加深一些，成为感叹时光倏忽变化，生命毫无作为的“年命之嗟”。颈联中凋敝衰败的景象亦是诗人情感的具象化体现。尾联“西州”一词用东晋谢安，羊昙典故，言谢安虽受朝寄，然心怀东山之志，每入西州门，自觉壮志未遂，自悲自哀；羊昙深知谢安，谢安死后，偶过西州门，恸哭而返。刘禹锡因“永贞革新”失败，被贬出京，遂自比谢安，感叹政治失意。', '唐顺宗永贞元年九月，赴连州途中作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (176, 'a40712fda8c14dc081ef5a26e77bd191', '华阳作贻祖三咏', 39, 12, 751, '751年', '朝行敷水上，暮出华山东。高馆宿初静，长亭秋转空。日余久沦汩，重此闻霜风。淅沥入溪树，飕飗惊夕鸿。凄然望伊洛，如见息阳宫。旧识无高位，新知尽固穷。夫君独轻举，远近善文雄。岂念千里驾，崎岖秦塞中。', '朝行敷水上，暮出华山东。高馆宿初静，长亭秋转空。日余久沦汩，重此闻霜风。淅沥入溪树，飕飗惊夕鸿。<br>凄然望伊洛，如见息阳宫。旧识无高位，新知尽固穷。夫君独轻举，远近善文雄。岂念千里驾，崎岖秦塞中。', '此诗具体创作时间无考。根据作品内容，此作可分为两部分内容。第一部分为作品的前四句，主要是点明作者的行旅途径，接着对华州的自然景物作了一番描绘。按照作品内容，作者创作此作是应是秋日夕阳之时，面对即将落下的太阳，作者身处山岭，听松风，看鸿雁，发出一番感叹。第五句开始，作品转入第二部分内容。在这一部分内容中，作者主要是一次次表达了对友人的怀念感情。首次表现在第五句中对友人隐居处的想念：作者的友人祖咏在汝南的汝坟别业隐居，因为此地又被称为息县，所以作者将友人的隐居处称之为“息阳宫”。第二次怀念表现在作品的第六七句中，通过与作者的友人相对比，衬托出祖咏能文善文的特质，这种赞美不能不称为一种想念；第三次想念比较婉转，是作者在第八句中，通过想象友人想念自己寄托出的。全诗前半部分写景，后半部分思人，当是因为华州山岭与友人隐居处有相似之处所致。', '唐玄宗天宝十年，储光羲任华州下邽县尉。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (177, '0cadea13209941e182b9878e32789fc4', '过敷水', 12, 13, 827, '827年', '垂鞭欲渡罗敷水，处分鸣驺且缓驱。秦氏双蛾久冥漠，苏台五马尚踟蹰。村童店女仰头笑，今日使君真是愚。', '垂鞭欲渡罗敷水，处分鸣驺且缓驱。秦氏双蛾久冥漠，苏台五马尚踟蹰。村童店女仰头笑，今日使君真是愚。', '唐太和元年三月，白居易征为秘书监，自苏杭至长安任职。返京途中，作有此诗。敷水，据《华山志》记载，位于华阴县15公里处，因水流甘美，遂以古代美女“秦罗敷”为名。诗的首句言敷水水流湍急，因此作者命令随身跟从的骑卒缓缓步行。接下两句作者巧妙将敷水与《陌上桑》中有关秦罗敷的传说相结合。“秦氏”一句先写秦罗敷早已作古，而当年羡慕其美貌，追求她的使君车马还踟蹰溪前。乃至下文敷水村旁的女子看见这一景象展露笑容。此诗中的使君应该是作者自喻，敷水则比喻秦罗敷，而诗中第一句所写溪水前缓驱的车马则是《陌上桑》中，使君见到美丽罗敷时的情景再现（“使君从南来，五马立踟蹰。”）通过与《陌上桑》中关于秦罗敷的记载相结合，作者将一条平淡无奇的小溪写得饶有兴趣，这条小溪也因此留名史册。', '太和元年年三月，征为秘书监，返京途中作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (178, 'c43884f1579d494e9dff6b74742aef8d', '敷水小桃盛开因作', 73, 13, -1, '年份未详', '敷水小桥东，娟娟照露丛。所嗟非胜地，堪恨是春风。二月艳阳节，一枝惆怅红。定知留不住，吹落路尘中。', '敷水小桥东，娟娟照露丛。所嗟非胜地，堪恨是春风。二月艳阳节，一枝惆怅红。定知留不住，吹落路尘中。', '此诗具体创作时间无考。根据作品内容，大致可以推断，作者途经敷水驿，见驿前桃花盛开，因有此作。作品的起首两句先点明桃花盛开的地点：二月初，百废待兴之际，桃花领先开放，花丛中极为鲜艳。颔联两句是作者化身为桃花表达他的主观感受：桃花自我嗟叹生长之地不是名胜古迹边，自我叹恨的是春风无情，环境恶劣，有消逝的风险。联系作者的生平事迹，可以发现作者早年屡试不第，产生这种感情也是情有可原。在颈联两句中，作者产生的这种嗟叹自身，万事无成的情绪进一步深化称为惆怅，怀疑自我能力的情绪。最后，在作品的尾联，这种情绪成为一种被宿命支配的无奈感觉，作者此时就像这支桃花一般，最终难逃被风吹落的命运。应该说，这首作品中，作者化身桃花，借桃花之口，说自己肺腑之言，是客体主观化的最好体现', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (179, 'e480f391f287468985eca3af4c560f2f', '华州西', 12, 13, 829, '829年', '每逢人静慵多歇，不计程行困即眠。上得篮舆未能去，春风敷水店门前。', '每逢人静慵多歇，不计程行困即眠。上得篮舆未能去，春风敷水店门前。', '唐文宗大和三年五月，白居易因病辞官，返回洛阳任太子宾客。此诗应为返洛途中作。前面提到白居易曾三别长安，均以被贬谪的身份离开的。此为第四次离开，却是以赴任的身份离开。此时的白居易在心态上不同于前三次离开，更显从容自在。所以作者首句就写到“每逢人静慵多歇，不计程行困即眠。”没有了官程的催促，作者的旅途也变得悠游自在。诗的后两句笔锋一转，没有写作者继续行走的过程，反而写作者睡醒后不忍出发的留恋之情。翻检白居易诗集，可以发现，自京至洛这条路途上，白居易对距离华州15公里远的敷水情有独钟，曾作下《过敷水》、《罗敷水》等作品。此时心态与之前大有不同的作者更是独具慧眼，处处留心敷水周边之前从未留心的景色。尽管如此，作者却不曾为敷水多着笔墨，仅仅是点明了时间地点。看似是一笔带过，实际上是诗人从容自在心态的体现。不着笔墨。更有清新秀丽的特质。', '大和三年，自京赴洛途中作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (180, 'a8eb2250b6584773995eb0af497c4082', '罗敷东馆亭下流泉云至前山拥咽经岁移时掬弄惆怅成章', 100, 13, -1, '年份未详', '看山亭下小鸣泉，呜咽难通亦可怜。惆怅无人为疏凿，拥愁含恨过年年。', '看山亭下小鸣泉，呜咽难通亦可怜。惆怅无人为疏凿，拥愁含恨过年年。', '此诗具体创作时间无考。罗敷驿旁有小溪，唐人作品中多有咏叹。因为溪水流量较小，所以在地面上流淌的时候，声音不是太大，流向也是曲曲折折，更重要的是，流水也不是很顺畅。根据作品内容来看，作者应该是抓住小溪水的最后一个特征，同自己坎坷蹭蹬的人生经历相联系，所以才创作了这首作品。作品的首两句为起兴句：先写作者看到驿站下有小溪流淌，又看见其流势艰涩，几乎到了难以流淌的境地，作者受其启发，援笔为诗。作品的后两句以水喻人，将水之流淌艰涩痛人之生活蹭蹬相联系，取喻甚巧。在这两句话中，作者看似为小溪水的流淌艰涩打抱不平，实则是为自己的生活蹭蹬自鸣不平：首句写无人开凿水渠，是说作者在为官的路途上无人指引；次句写小溪水之艰难流淌，是说作者心怀遗憾，艰难生活。全诗虽然最后四句话，但其中写景细腻，抒发感情也较委婉蕴藉，最值得称道的应是作品中以水喻人的比喻方式，甚是巧妙。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (181, '6d72a697ae0a44b6873fc4d9e1c61da3', '罗敷水', 12, 13, 835, '835年', '野店东头花落处，一条流水号罗敷。芳魂艳骨知何处，春草茫茫墓亦无。', '野店东头花落处，一条流水号罗敷。芳魂艳骨知何处，春草茫茫墓亦无。', '唐宪宗元和九年，白居易自洛阳赴下邽，途经敷水，因有此作。通过翻检白居易诗集，可以发现，自京至洛这条路途上，白居易对距离华州15公里远的敷水情有独钟，曾作下《过敷水》、《华州西》等作品。《过敷水》是借助《陌上桑》中秦罗敷的传说渲染敷水周边的景色，《华州西》则是在作者从容自在心态的主导下，对敷水的景色作了简单的描写。如果说前两首都着眼于敷水的景色的话，《罗敷水》一首则更多的是对《陌上桑》中那位机智聪慧的美女的追忆。诗的首句点明敷水的具体位置，并对敷水的周边景色作了简要概括。“花落处”初看有浪漫的基调，实际上则是作者对秦罗敷早已作古这一事实的感伤。这种感伤情绪由第一句生发，弥漫至下一句：秦罗敷的身体魂魄早已不知所踪，就连这因其命名的溪水旁边也未曾见到秦罗敷的墓碑，这不免令作者心怀感伤。', '元和九年，自洛阳赴下邽，途经敷水，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (182, '0480e25979fe41bfb69ceedd641ccfd9', '行经华阴', 101, 0, -1, '年份未详', '岧峣太华俯咸京，天外三峰削不成。武帝祠前云欲散，仙人掌上雨初晴。河山北枕秦关险，驿树西连汉畤平。借问路傍名利客，无如此处学长生。', '岧峣太华俯咸京，天外三峰削不成。武帝祠前云欲散，<br>仙人掌上雨初晴。河山北枕秦关险，驿树西连汉畤平。<br>借问路傍名利客，无如此处学长生。', '此诗具体创作年份无考。此诗主要写华山险峻奇观：诗作的前两联从华山之高入手，写华山刀劈斧削般的峻丽景象；中间一联以华山的地理位置入手，写华山地位之险要。首联两句中“岧峣”是写华山险峻高迈的气势。颔联两句主要写华山上景色：武帝祠位于华山，即巨灵祠，是汉武帝登上华山后所建；仙掌峰是华山上一座长势奇特的崖壁，因貌似掌印，得名仙掌峰。颈联两句从地理的角度凸显华山位置之重要。作品的最后部分以讽刺结尾。这句话表达出两个信息，一是作者借“终南捷径”的典故表达对世上追名逐利的人的讽刺；二是表达作者此时想要归隐山林，追求闲逸生活的想法。全诗前三联写景，后一联抒情，在诗气的书法上稍显板滞，具有程序化的特点。但最后一联讽刺部分指摘时弊，还是有可观之处的。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (183, 'a87698314b2e4e30a5b123c973b9c93c', '过华阴', 4, 0, 709, '709年', '云起太华山，云山互明灭。东峰始含景，了了见松雪。羁人感幽栖，窅映转奇绝。欣然忘所疲，永望吟不辍。信宿百余里，出关玩新月。何意昨来心，遇物遂迁别。人生屡如此，何以肆愉悦。', '云起太华山，云山互明灭。东峰始含景，了了见松雪。羁人感幽栖，窅映转奇绝。欣然忘所疲，永望吟不辍。信宿百余里，出关玩新月。何意昨来心，遇物遂迁别。人生屡如此，何以肆愉悦。', '唐中宗景龙三年，王昌龄游历河南，过华阴，因有此作。此作最值得称道的地方当属转换多种视角，选用多种意象组合搭配，为读者营造最真实完美的华山雪景图。“云起”两句将云与山相组合，营造出云山相互掩映，山在云中时隐时现的动态景象。“东峰”两句对松树与积雪进行了着重描写，从一个侧面凸显山之高耸。“羁人”至“信宿”六句当是从作者的主观感受着手描写。先言深幽的景色让人感受奇特；再言这种景色使远道而来的人忘记疲劳；最后写山中晚景也是与白天一样优美入画。作品的最后四句写作者的内心随着景物的变化发生着变化，并且抒发出这就是能够愉悦人们内心的那种景色，对华山景色作出了最高评价。', '709年，游历河南，华山赋诗。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (184, '97f1621653714d5ba0a9ce76ee78d797', '宿华阴东郭客舍忆阎防', 6, 14, 743, '743年', '次舍山郭近，解鞍鸣钟时。主人炊新粒，行子充夜饥。关月生首阳，照见华阴祠。苍茫秋山晦，萧瑟寒松悲。久从园庐别，遂与朋知辞。旧壑兰杜晚，归轩今已迟。', '次舍山郭近，解鞍鸣钟时。主人炊新粒，行子充夜饥。<br>关月生首阳，照见华阴祠。苍茫秋山晦，萧瑟寒松悲。<br>久从园庐别，遂与朋知辞。旧壑兰杜晚，归轩今已迟。', '唐玄宗天宝二年，岑参自梁宋归长安，途径华阴，有诗忆友人阎防。作品的前两联写一日将尽，作者自梁宋以来鞍马劳累，终于能够在华阴客店休整一夜 。“主人”二句写客舍主人对待远道而来的客人十分热情，为其炊米做饭，犒劳远道而来的客人。“关月”以下两联通过写景起兴作者对友人的思念之情。此时朗月当空，远山苍茫，风吹山林间，起萧瑟之悲凉声音。此情此景之下，身为旅客的作者必然会想念生活在长安的那些熟悉的人或物。诗作的下两联写作者对友人的怀念之情。这种怀念之情先是起于上两联的环境起兴，再次思念到作者与友人曾经游乐相聚的日子，再由此想到变换迅速的时光，发出“为时已晚”的感叹。全诗结构紧密，环环相扣，读者在阅读过程中毫无板趾感，全是形式流畅带来的内容上的流动感，令人读来享受。', '743年，自梁宋归长安，途径华阴，有诗忆友人阎防。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (185, 'e7e76dda314e4048bdde5b5fc733ea02', '奉和圣制次琼岳应制', 102, 63, 727, '727年', '陆海披晴雪，千旗猎早阳。岳临秦路险，河绕汉垣长。行漏通鳷鹊，离宫接建章。都门信宿近，歌舞从周王。', '陆海披晴雪，千旗猎早阳。岳临秦路险，河绕汉垣长。<br>行漏通鳷鹊，离宫接建章。都门信宿近，歌舞从周王。', '应制之作。前三联言山河襟带，华岳耸峙，潼关在望，大河奔流。尾联以周王喻玄宗。应制家法，“三部式”的颂美。', '扈从玄过琼岳宫有作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (186, '1bb305f7969f449aa20dc1ce99cf708e', '奉和圣制次琼岳应制', 103, 63, 727, '727年', '东幸从人望，西巡顺物回。云收二华出，天转五星来。十月农初罢，三驱礼复开。更看琼岳上，佳气接神台。', '东幸从人望，西巡顺物回。云收二华出，天转五星来。<br>十月农初罢，三驱礼复开。更看琼岳上，佳气接神台。', '中两眹数字词的使用是亮点，用数字将诗歌仪式化和符号化，形成“精英团体”的排他性、专有性语词。彰显身份、', '扈从玄过琼岳宫有作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (187, 'b3bc2de466634ba888e5f3997c3f8f22', '奉和圣制次琼岳韵', 15, 63, 727, '727年', '山祇亦望幸，云雨见灵心。岳馆逢朝霁，关门解宿阴。咸京天上近，清渭日边临。我武因冬狩，何言是即禽。', '山祇亦望幸，云雨见灵心。岳馆逢朝霁，关门解宿阴。<br>咸京天上近，清渭日边临。我武因冬狩，何言是即禽。', '应制之作，中二联雍容闲雅，壮观闳大，信口道来似不费功夫，颇见“曲江风度”。', '扈从玄过琼岳宫有作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (188, '96a64734b3e24b72858cbefd52944a8f', '奉和圣制早发三乡山行', 15, 35, 720, '720年', '羽卫森森西向秦，山川历历在清晨。晴云稍卷寒岩树，宿雨能销御路尘。圣德由来合天道，灵符即此应时巡。遗贤一一皆羁致，犹欲高深访隐沦。', '羽卫森森西向秦，山川历历在清晨。晴云稍卷寒岩树，<br>宿雨能销御路尘。圣德由来合天道，灵符即此应时巡。<br>遗贤一一皆羁致，犹欲高深访隐沦。', '应制之作，颔联摹景佳。', '扈从赴洛应制', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (189, '5f55b3fcbed04ea6b989564c95b37c2d', '晚至华阴', 104, 14, -1, '年份未详', '腊尽促归心，行人及华阴。云霞仙掌出，松柏古祠深。野渡冰生岸，寒川烧隔林。温泉看渐近，宫树晚沈沈。', '腊尽促归心，行人及华阴。云霞仙掌出，松柏古祠深。<br>野渡冰生岸，寒川烧隔林。温泉看渐近，宫树晚沈沈。', '此诗具体创作年份无考，当是年终时，作者归家，途中有感，因有此作。开篇首联两句为全诗关键，点明此次途经华州的因由。“腊尽”是腊节将尽，指一年将终。以下三联都是写作者途中所见之景物。颔联写作者遥望华山之景象：仙掌峰上云雾缭绕，古祠堂边松柏葱郁，一片茂密郁郁的景色。颈联两句写作者面前之水景：野渡无人寒水结冰，川中有户热烟漫生\n尾联两句写作者在车中所见，此句中“温泉”代指骊山之上的“华清宫”，因华清宫更名之前为“汤泉宫”，故有此称。此句是说，诗人在旅途之中，遥望华清宫越来越近，顿觉自己离家也就越来越近，顿时心生喜悦，在这种情绪的统摄下，闻见的宫水声再也不是呜咽难忍，而是浩流沉沉之声了。从对水声的描写，我们可以感知到作者近乡欣喜的感情。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (190, 'df2a10634d8e41dfa76ed22bbb282e02', '三月三日，自京到华阴，于水亭独酌，寄裴六、薛八', 105, 14, 755, '755年', '祗役匪遑息，经时客三秦。还家问节候，知到上巳辰。山县何所有，高城闭青春。和风不吾欺，桃杏满四邻。旧友适远别，谁当接欢欣。呼儿命长瓢，独酌湘吴醇。一酌一朗咏，既酣意亦申。言筌暂两忘，霞月只相新。裴子尘表物，薛侯席上珍。寄书二傲吏，何日同车茵。讵肯使空名，终然羁此身。他年解桎梏，长作海上人。', '祗役匪遑息，经时客三秦。还家问节候，知到上巳辰。<br>山县何所有，高城闭青春。和风不吾欺，桃杏满四邻。<br>旧友适远别，谁当接欢欣。呼儿命长瓢，独酌湘吴醇。<br>一酌一朗咏，既酣意亦申。言筌暂两忘，霞月只相新。<br>裴子尘表物，薛侯席上珍。寄书二傲吏，何日同车茵。<br>讵肯使空名，终然羁此身。他年解桎梏，长作海上人。', '唐玄宗天宝十四年，独孤及任华阴县尉，此诗或作于此时。三月三日正是古代传统的以游赏春景为主的节日，值此佳节，作者心生对家乡的思念以及想要游玩的娱乐之心，但是作者的愿望却被县尉一职所限制，不能如愿，所以作者在这首诗作中，还表现出对自由，隐逸生活的向往。全作的前四句写作者在外三年，一直被尘务所困，今日才发现是上巳佳节，惊喜之感的背后隐藏着作者在外做官的悲辛。“山县”至“霞月”几句描写华阴百姓在经历上巳佳节时的游玩场面。先言华阴县景色秀美，又是初春，在花草的掩映下，景色格外美好；又写作者独自一人酌酒自宽，宽解的是自己的思乡之情以及对友人的思念情感；最后作者写晚霞、朗月，凸显出山谷内清幽明静的景色。接下来四句，主要表现作者对裴子、薛侯两人的思念之情。作品的最后两句，作者将自己无法回家，无法与友人同赏上巳节的原因归结于自己官职的羁绊。在最后一句里，作者表达出自己的归隐之心。全诗以上巳节开端，写及自己对家乡、友人的思念，再归结于自己官职的羁绊，表达自己的归隐心愿，结构上精密工整。另外，本作的环境描写也很精妙。', '唐玄宗天宝十四年，独孤及任华阴县尉，此诗或作于此时。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (191, 'a8b4958200a746b0994eb514637dcf81', '华阴韦氏林亭', 73, 14, -1, '年份未详', '自有林亭不得闲，陌尘宫树是非间。终南长在茅檐外，别向人间看华山。', '自有林亭不得闲，陌尘宫树是非间。终南长在茅檐外，别向人间看华山。', '此诗具体创作年份无考。作品的起首两句先从林亭与宫殿想做对比，衬托出归隐山林之隐逸生活态度。最后两句写林亭具有地理优势，地处终南之间，身处于此，能够享受到闲适惬意的体验。从内容上来讲，虽然句句皆是写林中隐逸情态，但宫内复杂的人事斗争也在其中隐藏，不言自明，等待读者读来解答。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (192, '3340483ba41345b5b5e9616e3a7ff4a0', '晚望（一作夕次华阴北亭）', 106, 14, -1, '年份未详', '清晨孤亭里，极目对前岑。远与天水合，长霞生夕林。苍然平楚意，杳霭半秋阴。落日川上尽，关城云外深。方予事岩壑，及此欲抽簪。诗就蓬山道，还兹契宿心。', '清晨孤亭里，极目对前岑。远与天水合，长霞生夕林。<br>苍然平楚意，杳霭半秋阴。落日川上尽，关城云外深。<br>方予事岩壑，及此欲抽簪。诗就蓬山道，还兹契宿心。', '此诗具体创作时间无考。借助描写自然景色的秀丽天真，表现抒发作者的隐逸超脱的心境，是唐人写景诗的传统内容，此作也不例外。作品开头四句先写作者清晨远望前山，见水与山合，霞自林生，一片充满朝气的景色。中间四句写作者傍晚时候再度眺望，见苍茫原野，处处暮霭，秋意甚浓；又写落日西下，远处云深的辽阔壮美景色。作品的最后四句开始叙事，开头两句中“抽簪”一词是说作者有弃官归隐的心态，结尾两句表白心志，说明作者想要弃官归隐的心态由来已久，是自己一直未能实现的心愿。全诗上半部分写晨景秀丽，中部分写晚景壮阔，下半部分，作者受此启发，以表白心志结束全篇，结构清晰，写景叙事都有妙处。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (193, '713c01659c14486bbaa00094b4b90d58', '春暮途次华山下', 107, 14, -1, '年份未详', '他皆宴牡丹，独又出长安。远道行非易，无图住自难。离城风已暖，近岳雨翻寒。此去知谁顾，闲吟只自宽。', '他皆宴牡丹，独又出长安。远道行非易，无图住自难。<br>离城风已暖，近岳雨翻寒。此去知谁顾，闲吟只自宽。', '此诗具体创作年份无考，但根据作品表现的情感内容来看，作者在创作这篇作品时的心情是低沉的、寡欢的。根据首联“宴牡丹”一句，可知作者离京出城当是在全城因牡丹而举办宴会的时候，也就是4月中下旬时间。次句中“独”字当写作者此时孤身一人出城离京，失意寥落之情不言自明；“又”字表现作者已经不是第一次出城离京了，再次加深了作者失意寥落的情绪。颔联两句谓作者向远方目的地行路的艰辛。颈联两句主要是作者借住表现自己在不同环境下的身体感受，表现自己的心情状态。“风已暖”是说作者在离城后不久，心情已经平复下来。而又在快要离近骊山的时候，因为回想自己坎坷的经历，心情又觉得低落下来。以这种通感手法写自己内心情感的变化过程，取法甚巧。尾联两句主要是作者直抒胸臆，将自己心中的寂寞寥落情意一并抒发出来，只为宽解自己的内心。“此去”一句写作者在离京的时候，身后也没有亲友相送。到此，我们可以知道作者无人陪伴、无人相送，而且这种经历很可能有很多次，知道了这些信息，我们不禁为作者的生平扼腕叹息一番。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (194, 'c6e6fe93643a434c9c7a747791d19cfa', '寄和水部马郎中题兴德驿，时昭义已平', 14, 58, 844, '844年', '仙郎倦去心，郑驿暂登临。水色潇湘阔，沙程朔漠深。鹢舟时往复，鸥鸟恣浮沉。更想逢归马，悠悠岳树阴。', '仙郎倦去心，郑驿暂登临。水色潇湘阔，沙程朔漠深。<br>鹢舟时往复，鸥鸟恣浮沉。更想逢归马，悠悠岳树阴。', '唐武宗会昌四年，李商隐丁母忧于家，马郎中暂居永乐，因有此作。诗作首联写马郎中暂居兴德驿事。此联中，因汉魏以后，郎官多称“星郎”，又称为“仙郎”，“台郎”，所以这里用“仙郎”代指时任水部郎中的马郎中。兴德驿因在华州境内，是郑国最初的封邑，所以又称“兴德驿”为“郑驿”。颔联对仗工整，写登临兴德驿所见。以潇湘水比喻渭水、洛水，言水面阔大，水势浩荡。“沙程”句写兴德驿接壤沙漠，言沙漠之凄凉寥远。颈联两句单写登临兴德驿后，水面上的景象：游人及画船在水上游荡，惬意非凡，又有飞鸟盘旋水上，趣味甚浓。作者在这里写水面上轻松愉快的景象，实则是自己在听闻边事已平后的畅快心情的真实书写。尾联中，作者更是难掩自己内心的喜悦，写及战马归闲，天下无事的承平景象。全诗由马郎中逗留兴德驿说起，起兴于登临驿站所见的自然风光，表达作者对天下无事的承平景象的向往之情。', '会昌四年，李商隐丁母忧于家，马郎中暂居永乐，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (195, 'cb29c967c51c4173821ae9a8249cad62', '狐泉店前作', 12, 59, 835, '835年', '野狐泉上柳花飞，逐水东流便不归。花水悠悠两无意，因风吹落偶相依。', '野狐泉上柳花飞，逐水东流便不归。花水悠悠两无意，因风吹落偶相依。', '唐宪宗元和九年，白居易仍分司东都，任太子宾客。是年，赴下邽，途经狐泉店，因有此作。白居易此时期的作品，大多以描绘当地景色，抒发个人情意为主。此诗也不例外。狐泉店，位于华阴县东北26里处，店南坡下有泉水，盖因泉得名。诗的首句点时，当写于暮春杨花飘飞季节。因古时杨柳合称，故诗中“柳花”当属“杨花”。杨花具有体性轻柔，风吹不定的特性，多发生于暮春时间。杨花风吹不定的特性常使在外远游，客居的游子或官员感同身受。所以，吟咏杨花的作品多表达作者飘零不偶的伤感情绪。自大和三年白居易分司东洛，任太子宾客以来，白居易曾两次辞官，一次是病免河南尹，另一次则是辞去同州刺史一职。坚定地留任太子宾客的原因，不仅是晚年的白居易崇尚“独善其身”的人生目标，而是为官不称意的自我开解，后者可以说是前者的原因。虽然为官不称意，但最终官居闲职，总能局部缓解痛苦。故作者以花自比，将水比作洛阳东都，感叹自己与洛阳“因风吹落偶相依。”', '元和九年，自洛阳赴下邽，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (196, '286764f400dd41d2a16ce2d924277e9c', '夜泊渭津', 108, 62, -1, '年份未详', '飘飘东去客，一宿渭城边。远处星垂岸，中流月满船。凉归夜深簟，秋入雨余天。渐觉家山小，残程尚几年。', '飘飘东去客，一宿渭城边。远处星垂岸，中流月满船。<br>凉归夜深簟，秋入雨余天。渐觉家山小，残程尚几年。', '此诗具体创作时间无考，当是作者在离京返家途中，夜宿渭津关，有感而作。首联两句点题，因离京返家，所以作者自称为“东去客”，又因为作者在旅途之中，所以又加上“飘飘”二字，有飘蓬零落之意，从这里也可知道此次旅行作者应是独自一人。颔联两句写作者远眺所见：夜空无云，星月徘徊，营造一种浪漫意境。颈联两句写作者独自回到住处的身体感觉：“凉”不仅是作者在秋日的夜晚感受到的温度之低，还体现出作者独自一人在外漂泊的寥落心境。尾联两句写作者近乡情急，恨不能早日回到家乡的急迫心情，不过“残程”一句又体现出作者面临很长的路程无能为力的无奈感受，正是在这种急迫心情以及无奈感受的交织下，作者独自入眠，更体现出其独自漂泊的孤苦无依感。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (197, '5114d4d5631c4deaa44cd98367269d2d', '秋日赴阙题潼关驿楼', 13, 15, 808, '约808年', '红叶晚萧萧，长亭酒一瓢。残云归太华，疏雨过中条。树色随山迥，河声入海遥。帝乡明日到，犹自梦渔樵。', '红叶晚萧萧，长亭酒一瓢。残云归太华，疏雨过中条。<br>树色随山迥，河声入海遥。帝乡明日到，犹自梦渔樵。', '此诗具体创作年份无考，大致是作者首次自家至长安时，途中见潼关高峙，因有此作。作品首联两句作者先以“红叶”与题目中“秋日”相呼应：秋日黄昏，作者独自一人登楼远眺，遂有此作。颔联两句写中条山以及太华山在云雨之后的美丽景色。“归”字极好，写出太华山因山高，所以山上有云雾弥漫的景色；“过”字也不错，写出中条山上因雨过后，呈现出的明丽景色。颈联两句是作者远眺所见：苍茫树色随着山势的绵延略有不同，滔滔江水之声犹如在眼前流淌一般。雄浑的景色在作者面前展开，带给作者以雄壮的气势，令作者感受到震撼。诗作的最后两句叙事，写作者即将到达长安，但心中想的还是家乡的渔樵等意象，表现出作者欣喜又伤感的情绪。', '唐宪宗元和三年，作者首次自家至长安应举，途中见潼关高峙，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (198, '38a0bc81c924494285436f92141bf811', '秋霁潼关驿亭', 13, 15, 808, '约808年', '霁色明高巘，关河独望遥。残云归太华，疏雨过中条。鸟散绿萝静，蝉鸣红树凋。何言此时节，去去任蓬飘。', '霁色明高巘，关河独望遥。残云归太华，疏雨过中条。鸟散绿萝静，蝉鸣红树凋。何言此时节，去去任蓬飘。', '此诗具体创作年份无考，大致是作者首次自家至长安时，途中见潼关高峙，因有此作。首联点题，写明此作作于秋日雨后，作者登高眺望，见水河蜿蜒，营造出辽阔景象。。颔联两句写中条山以及太华山在云雨之后的美丽景色。“归”字极好，写出太华山因山高，所以山上有云雾弥漫的景色；“过”字也不错，写出中条山上因雨过后，呈现出的明丽景色。颈联两句是作者远眺所见：苍茫树色随着山势的绵延略有不同，滔滔江水之声犹如在眼前流淌一般。雄浑的景色在作者面前展开，带给作者以雄壮的气势，令作者感受到震撼。颈联两句写近处景色：山林茂密，鸟雀无声，蝉鸣阵阵，红叶零落，营造出颇有浪漫气质的意境。尾联两句似乎是作者向质疑其远游之人的回应，“何言”一词看得出作者对于此次进京抱有莫大的信心。全诗以高山开篇，先言远，后写近，最后将写景归结至自己心绪的表达，结构浑圆，一脉贯通。', '唐宪宗元和三年，作者首次自家至长安应举，途中见潼关高峙，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (199, 'bb3e464684bc437ea1f524f13d7f8aeb', '潼关驿亭', 109, 15, -1, '年份未详', '河上关门日日开，古今名利旋堪哀。终军壮节埋黄土，杨震丰碑翳绿苔。寸禄应知沾有分，一官常惧处非才。犹惊往岁同袍者，尚逐江东计吏来。', '河上关门日日开，古今名利旋堪哀。终军壮节埋黄土，<br>杨震丰碑翳绿苔。寸禄应知沾有分，一官常惧处非才。<br>犹惊往岁同袍者，尚逐江东计吏来。', '此诗具体创作无考，大致是作者途经潼关，有感而作。全诗的首联开篇即点明了作者认为名利“堪哀”的看法，具体怎么体现出名利“堪哀”，请看余下三联。颔联用终军、杨震典故。二人的共同点都是二人年少有为，功高盖世。但如今都已埋入黄土，丰碑上已长满绿荫，往日的功勋再也无处可见，这是功名的第一“哀”；颈联两句用晋代王衍典故，说明当今社会，毫无材用之人也能做官，名不副实，这是第二哀；尾联两句是第三哀，作者感慨事实已经摆在眼前，但还是有很多人追名逐利，从此潼关纷纷走过，这是作者感到现今名利“堪哀”的第三个原因。根据内容，作者创作这篇作品，目的是在于讽刺那些追名逐利的人们，实际上这篇作品还暴露了当今社会最残酷的现实，也表明了作者想要归隐，追求隐逸的人生态度。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (200, '6f0af08cf3014bc1b1da576b9587ee4e', '潼关河亭', 109, 15, -1, '年份未详', '重冈如抱岳如蹲，屈曲秦川势自尊。天地并功开帝宅，山河相凑束龙门。橹声呕轧中流渡，柳色微茫远岸村。满眼波涛终古事，年来惆怅与谁论。', '重冈如抱岳如蹲，屈曲秦川势自尊。天地并功开帝宅，山河相凑束龙门。橹声呕轧中流渡，柳色微茫远岸村。满眼波涛终古事，年来惆怅与谁论。', '此诗具体创作时间无考。根据作品题目，当是作者登临潼关河亭，眺望所作。因是眺望，所以开头就写潼关险峻的全貌：“重冈”言潼关山峦叠嶂；“岳如蹲”是借华山之势衬托潼关之险峻；“屈曲秦川”是写在潼关的作用下，潼关河蜿蜒不绝。颔联两句又对潼关的重要地理位置作了一番描述：“开帝宅”是说潼关是西京长安的屏障。颈联两句描述潼关河景色：前一句写潼关河水势凶猛，大船在其中艰难前行；后一句写潼关河两边有人家居住，柳色微茫。作品结尾的最后两句是作者观潼关河有感。面对波涛汹涌的潼关河，作者不禁心念旧事，感慨古今。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (201, '61213c94a0b54f39bae7d7e645b7f16c', '东归晚次潼关怀古', 6, 15, 737, '737年', '暮春别乡树，晚景低津楼。伯夷在首阳，欲往无轻舟。遂登关城望，下见洪河流。自从巨灵开，流血千万秋。行行潘生赋，赫赫曹公谋。川上多往事，凄凉满空洲。', '暮春别乡树，晚景低津楼。伯夷在首阳，欲往无轻舟。<br>遂登关城望，下见洪河流。自从巨灵开，流血千万秋。<br>行行潘生赋，赫赫曹公谋。川上多往事，凄凉满空洲。', '唐玄宗开元二十五年，岑参沿洛水东归，途次潼关，有诗吊古。全诗开头“暮春”两句起兴，写暮春时节作者离开长安，行至潼关已是傍晚景色。就是在这种离家乡未远，还对家乡留存依恋，又正值黄昏时间，作者飘零无依的旅者感受无处排解的双重情感作用下，作者面对着兵家必争的潼关，首阳山进行了一番思考。吊古的作品一般满含作者对历史名胜的思考，这种思考的内容或是感叹时间疏忽变幻；或是评点历史人物的功过；亦或是将自己的人生轨迹同历史人物相联系，抒发感情。此篇也不例外。“伯夷”两句是作者登城远望的结果。个人认为岑参这里提及伯夷，是出于对伯夷隐居的生活，自由的人生轨迹的羡慕情感。“欲往”一句写明此种羡慕情感，并无奈地认为自己身处俗世，未能免俗，故言“欲往无轻舟”。之后的两联是从整体角度对历史作出自己的评价。潼关自古是兵家必争之地，故言“自从巨灵开，流血千万秋。”“行行”一联则是作者对历史人物的评点，带有浓厚的主观色彩。这两句表明作者从文希望能有潘岳的文采，从武能有曹操的勇猛智慧。诗作的结尾对作者的思考做了总结，将这所有的思考归结于对往事的凭吊。', '737年，沿洛水东归，途次潼关，有诗吊古。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (202, '508cd9e758cd4d3bb6608adac68eea12', '秋日经潼关感寓', 57, 15, -1, '年份未详', '昔年曾逐汉征东，三授兵符百战中。力保山河嗟下世，秋风牢落故营空。', '昔年曾逐汉征东，三授兵符百战中。力保山河嗟下世，秋风牢落故营空。', '此诗具体创作年份无考。此诗属于英雄暮年感叹昔日功勋如今落空的作品。起首两句里，作者回忆昔日征战沙场，立功建勋的往事。结尾两句写作者看到曾经奋力挽救的江湖河山，如今一片萧条，再也不复往日繁盛的景象，不禁感叹曾经的努力全部付诸东流，一切归空。考武元衡事，武元衡曾治蜀七年，还朝之后，又任中书门下平章事，办事公允，力主中兴。其为人处事影响到一些宦官的利益，不久便被刺杀。此诗根据内容应该创作于武元衡自蜀还朝时期，但具体时间无从考证。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (203, '09a7c9c1cd90433ca2bb2d1258a0c04d', '次潼关先寄张十二阁老使君', 72, 15, 817, '817年', '荆山已去华山来，日出潼关四扇开。刺史莫辞迎候远，相公亲破蔡州回。', '荆山已去华山来，日出潼关四扇开。<br>刺史莫辞迎候远，相公亲破蔡州回。', '唐宪宗元和十二年十二月，韩愈、裴度等人夜袭蔡州得胜，班师西归路经潼关，先寄诗于张贾，因有此作。诗题中“阁老”一词专指唐代中书、门下省中有资历的官员，张贾曾任门下给事中，因有此称。元和十二年时，张贾又任华州刺史，因此诗中又有“使君”一词，特指张贾。诗作以描写宏大的场景开题，摹状班师队伍的壮大。除此之外，诗作也以班师回朝的盛大景象衬托裴度的军功伟业，为下文邀请张贾来往迎候作铺垫。因韩愈同张贾私交甚好，所以在诗作的下半部分，韩愈以朋友的口吻邀请张贾迎候西归大军，语气中透露着出征得胜后的轻松愉快心情。', '817年12月，十二月，班师西归路经潼关，先寄诗于张贾。作品：次潼关先寄张十二阁老使君。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (204, '715a56d464394fe5929b61202c4687d8', '莎栅联句', 72, 33, 811, '811年', '冰溪时咽绝，风枥方轩举。', '冰溪时咽绝，风枥方轩举。', '颇见羁旅之苦', '元和六年韩愈赴长安之官与孟郊联句', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (205, 'bd78c21b353f45df88d75ef991fd48c2', '次潼关上都统相公（韩弘也）', 72, 15, 817, '817年12月', '暂辞堂印执兵权，尽管诸军破贼年。冠盖相望催入相，待将功德格皇天。', '暂辞堂印执兵权，尽管诸军破贼年。冠盖相望催入相，待将功德格皇天。', '唐宪宗元和十二年，平淮西吴元济叛乱后，唐军行至潼关。时任行军司马的韩愈创作了这首呈给都统相公的作品。作品的前两句先写为了平定淮西吴元济叛乱，时任宰相韩弘辞去相位，执掌兵权。最终在平定叛乱的过程中，大获全胜。作品的最后两句中，冠是指官吏头戴的帽子，盖是指官吏所乘车马的车顶；“格皇天”一句出自《书经》，是感通皇天的意思。所以，作品的最后两句是说，韩弘平定淮西吴元济这件事情，不仅让文武百官争相传颂，使文武百官都引领相望，希望韩弘再次出任宰相，而且平定淮西吴元济叛乱这件事，还感通皇天，可以说这件事是利国利民的事情。', '唐宪宗元和十二年，平淮西吴元济叛乱后，唐军行至潼关。时任行军司马的韩愈创作了这首呈给都统相公的作品。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (206, 'd6cfe2e7ef2e4d1bbcad5b011734fd96', '和令狐相公入潼关', 59, 15, 828, '828年', '寒光照旌节，关路晓无尘。吏谒前丞相，山迎旧主人。东瞻军府静，西望敕书频。心共黄河水，同升天汉津。', '寒光照旌节，关路晓无尘。吏谒前丞相，山迎旧主人。东瞻军府静，西望敕书频。心共黄河水，同升天汉津。', '唐文宗大和二年，刘禹锡自洛至京，任主客郎中。在京酬和令狐楚入潼关诗，遂有此作。诗的首联点出时间。“寒光”一词指明此诗当属秋日所作。“关路”一句言入关一路，清丽秀美，风景绝好。因令狐楚曾任华州刺史，又相宪宗，故颔联“前丞相”、“旧主人”等词当代指令狐楚。颈联中“频”字极好，言期待诏书的焦急心情。刘禹锡自永贞元年以来，除了元和九年奉旨还京之外，其余时间都是在贬所度过的。贬谪之处，其恶劣的环境以及偏离政治中心的地理位置，都给刘禹锡在心理上、身体上带来了或多或少的影响，所以此言“西望敕书频”一句是对作者入朝做官愿望的真实书写。尾联“天汉津”指天河，此为京师的代称，此言作者此次入京，满怀凌云壮志，期望在这一次实现自己的政治抱负。', '大和二年，刘禹锡在京唱和令狐楚入潼关诗，遂有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (207, '4ea4967f9eff48b3a4635898487853dc', '入潼关', 110, 15, -1, '年份未详', '都城三百里，雄险此回环。地势遥尊岳，河流侧让关。秦皇曾虎视，汉祖昔龙颜。何处枭凶辈，干戈自不闲。', '都城三百里，雄险此回环。地势遥尊岳，河流侧让关。<br>秦皇曾虎视，汉祖昔龙颜。何处枭凶辈，干戈自不闲。', '此诗具体创作时间无考。作品起首两句先总写潼关之壮阔险要：“三百里”写其壮阔；“雄险”写其地势险要。颔联两句特写潼关之险：前有水河汹涌，后有山岳耸峙，这难道不是兵家必争之地的险要特征吗？颈联两句写潼关的历史：秦始皇就是占据了潼关，函谷以及崤山之险才能守卫江山，吞并六国；汉高祖刘邦起兵反秦时也曾作为攻入潼关的第一人，并在此建下百年基业。作品的收尾两句写作者对潼关的总体感受。“枭凶辈”当是指以安禄山为首的起兵反唐的农民起义军。这里写潼关自古战争不断，作者之意是通过反面衬托，凸显潼关的地势险要，军事意义重大等特点。从全诗来看，抓住潼关地势险要，军事意义重大的特征对潼关举行描写，抒发感情，令全诗读来有一种豪迈之气。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (208, 'ae648fcc584b43d2bc43738518688567', '晚秋潼关西门作', 110, 15, -1, '年份未详', '日落寒郊烟物清，古槐阴黑少人行。关门西去华山色，秦地东来河水声。', '日落寒郊烟物清，古槐阴黑少人行。关门西去华山色，秦地东来河水声。', '此诗具体创作时间无考。根据作品题目，当是作者途经潼关而作。题目言“晚秋”，作品的开头两句就写晚秋景象点题：首先写秋天之寒，再写天色寒冷，景物清晰可见；最后写槐树叶落，槐树呈现出的阴黑颜色，营造出一份寒冷凄楚的意境。作品的最后两句对潼关东西两边的景色都做了描述：先写潼关之西是华山古岳；又写潼关之东则是秦川三辅之地。全诗内容重点应该是在诗作的前半部分，通过对潼关晚秋景色的描写，暗暗传达作者目睹晚秋景色时的莫名愁苦情绪：或是思家，或是感慨年华已尽，无所成就。作品的后两句对景物的白描，看似寡淡无味，实则是作者想要排解，却无法排解的无奈之感的具体呈现。全诗传情含蓄，发人深思，值得读者对其深入探究。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (209, 'a9b8e96f83ce4bff83ec818239dc8807', '行次潼关题驿后轩', 13, 15, 808, '约808年', '飞阁极层台，终南此路回。山形朝阙去，河势抱关来。雁过秋风急，蝉鸣宿雾开。平生无限意，驱马任尘埃。', '飞阁极层台，终南此路回。山形朝阙去，河势抱关来。<br>雁过秋风急，蝉鸣宿雾开。平生无限意，驱马任尘埃。', '此诗具体创作无考，当是与作者作于潼关的其他作品同一时期。题目写“题驿后轩”，起首两句当简写潼关驿站。首句先写潼关驿站气势之宏大：楼台亭阁修建在高山上，远处望去，就像飞在天空一般。次句又点明潼关所在与终南山所在为同一位置。颔联两句，作者跳出景色，具体写潼关驿站所处的险要位置：前有水河汹涌，后有高山耸峙，一副壮阔雄险的美景。写完景色，作者又纵身回到景中，写近处景色：秋风阵阵，鸿雁南飞，宿雾渐开，鸣蝉欲噤，写秋日山林中幽美曼妙的景色。受这种景色的启发，作者在结尾两句中抒发了自己适意自如的心情。这两句中，“任”字最好，点出了作者随性而去，肆意自如的闲情雅致。从全诗来看，前半部分先写远景，壮潼关之险；后半部分着笔近景，绘山林之幽，在视角的变换之中，作者带领我们领略了潼关有险有幽的多重美景。', '唐宪宗元和三年，作者首次自家至长安应举，途中见潼关高峙，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (210, '10386b029a9c4111955e5d6c66f44223', '出潼关', 95, 15, -1, '年份未详', '重门随地险，一径入天开。华岳眼前尽，黄河脚底来。飞轩何满路，丹陛正求才。独我疏慵质，飘然又此回。', '重门随地险，一径入天开。华岳眼前尽，黄河脚底来。<br>飞轩何满路，丹陛正求才。独我疏慵质，飘然又此回。', '此诗具体创作年份无考。此诗写潼关，起首两句必是先从潼关之地势险要着笔描绘。其中，潼关是春秋时期随国的发源地，故以随地代指潼关；次句写作者远眺潼关，描绘潼关之险峻：见潼关与山相连，无边无际，知在眼前，却在天边一般。颔联两句从山水等角度写潼关之地势重要：既有华山耸峙，又有黄河漫过，此地不能不称得上险要非常。诗的前半部分写景结束，转入后半部分的叙事部分。颈联两句看似是作者实写，却是作者虚笔想象，但仍然是对当今社会追名逐利现象的一种艺术性描写。”飞轩“谓轻便的车马，这里是说因为皇帝求贤，追名逐利之人都迫不及待地想要快点到达长安，于是驾轻车，不辞劳苦，从潼关经过。作品的结尾两句是作者对这种世相的观点及做法。最后两句是说，我慵懒不能追名逐利，所以一次次从长安返回家乡。这实际上是作者的谦语，用意是在以婉转的话语实现对当今世相的讽刺目的，可谓诗思巧妙。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (211, '62c0f3a0e475431e84cef84cd27a1849', '早发潼关', 95, 15, -1, '年份未详', '天边月初落，马上梦犹残。关树苍苍晓，玉阶澹澹寒。宦游终自苦，身世静堪观。争似山中隐，和云枕碧湍。', '天边月初落，马上梦犹残。关树苍苍晓，玉阶澹澹寒。<br>宦游终自苦，身世静堪观。争似山中隐，和云枕碧湍。', '此诗具体创作年份无考。诗题是“早发潼关”，作品的起首两句必然从早晨自潼关出发写起：“月初落”写时间尚早，太阳还未升起，作者就已出门赶路了；“马上梦”写作者出门太早，还未清醒，还有残梦相伴，不得不上马出行。先写起早出行，后写马上做梦，首联两句着重从这两个细节凸显作者独自一人，宦游羁旅的孤苦感受。颔联两句写景，这里的写景也定是早晨所见：关外树还是灰蒙蒙的，模糊不可见；驿站台阶上的露水浸湿台阶，令人往来心生寒冷的感觉。上半部分写景结束，作者转入下半部分叙事。颈联两句作者表达自己宦游羁旅的孤苦之感，尾联两句写作者想要归隐山林，追求闲适隐逸生活的心态。全诗从内容上来看，叙述重点看似在事，实则在景。通过对作者描述的景物作感同身受般的观察与体悟，读者很容易感受到作者宦游之孤苦心情，并进而生出归隐之心。从这一内容设计上讲，作者的设计是成功的。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (212, '12f4e0aaee354c6fa11dcd744f846e0c', '戏题关门', 6, 15, 737, '737年', '来亦一布衣，去亦一布衣。羞见关城吏，还从旧路归。', '来亦一布衣，去亦一布衣。羞见关城吏，还从旧路归。', '唐玄宗开元二十五年，岑参自嵩阳入京考试，落第而归。途经潼关，有感而作。潼关，因其地势险要，为历代文人歌咏的对象。而潼关，作为进入长安的第一道关口，是历代诗人入京参加考试的必经之路。这首作品就是岑参入京考试时途经潼关，落第而归时又经过潼关，所以岑参有感而发，援笔写就这首作品。诗的前两句以自己入京考试，并落第而归这件事起兴，暗含作者落地后的失望情感。诗的后两句以《汉书》中终军、郭丹的事迹勉励落第而归的自己。据传，终军自西入潼关时，军吏给他一件证明过关的凭证，终军视此为侮辱，并承诺在功成之后，再度入关。作者借此典故，既有安慰，又有表述自己壮志的意思。“羞”点明自己没有像终军一样完成自己的承诺再途经潼关的心里愧疚之情。但“还从旧路归”暗示作者没有太在意科举失利这件事，并且相信自己有朝一日是可以考中入朝做官的。', '737年，自嵩阳进京考试，落第而归。途经潼关，作诗述怀。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (213, '1ac64f9a9b0f4f55bc5f321c488feec7', '宿关西客舍寄东山严、许二山人时天宝初七月初三日在内学见有高道举征', 6, 15, 742, '742年7月', '云送关西雨，风传渭北秋。孤灯然客梦，寒杵捣乡愁。滩上思严子，山中忆许由。苍生今有望，飞诏下林丘。', '云送关西雨，风传渭北秋。孤灯然客梦，寒杵捣乡愁。<br><br>滩上思严子，山中忆许由。苍生今有望，飞诏下林丘。', '唐玄宗天宝元年7月，26岁的岑参自长安东行，往游梁宋之地。途经关西，宿客舍中有诗寄友人。诗作首联除了交待创作时间及地点是渐近秋日的关西客舍外，还为整首诗奠定了感伤的感情色调，为下文抒情做准备。颔联两句写作者此时独自一人客居他乡，摇曳不定的烛光伴随作者，也点燃作者对家乡的思念。又近秋日，每户捣衣制作寒衣的声音也声声催耳，令作者联想家中此时的景色。除了对家乡有思念情感，作者还在路途中对友人生出浓厚的想念情感。独自一人，客居他乡，此种情境下，作者在思念家人，友人之外，还将这种感伤的情绪放置于对自己身份，前途命运的感叹上。诗作尾联讲26岁的作者对前途命运的期许和憧憬，有在客舍中勉励自己的意思。全诗虽然自首联以下，每句一种感情，但这三种感情是有严密的承袭关系的，想到亲近的关系，接着想到这些亲近关系对自己的期待，这种逻辑是很严密的。', '742年7月，26岁时自长安东行，往游梁宋之地。途经关西，宿客舍中有诗寄友人。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (214, '6be3328271844cbea54d0d9c5e531ae1', '唐摭言之熊执易', 111, 15, -1, '年份未详', '熊执易赴举，行次潼关。秋霖月余，滞于逆旅。俄闻邻店有一士，吁嗟数次。执易潜问之，曰：“前尧山令樊泽，举制科，至此，马毙囊空，莫能自进。”执易造焉，遽辍所乘马，倒囊济之。执易其年罢举，泽明年登科。', '熊执易赴举，行次潼关。秋霖月余，滞于逆旅。俄闻邻店有一士，吁嗟数次。执易潜问之，曰：“前尧山令樊泽，举制科，至此，马毙囊空，莫能自进。”执易造焉，遽辍所乘马，倒囊济之。执易其年罢举，泽明年登科。', '救人危困，勇于牺牲，气义之举，堪薄云天', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (215, 'ce88eac08ae84e0a9c03405d4aa31f79', '关西驿亭即事', 95, 15, -1, '年份未详', '晚霞零落雨初收，关上危阑独怅留。千里好春聊极目，五陵无事莫回头。山犹带雪霏霏恨，柳未禁寒冉冉愁。直是无情也肠断，鸟归帆没水空流。', '晚霞零落雨初收，关上危阑独怅留。千里好春聊极目，五陵无事莫回头。<br>山犹带雪霏霏恨，柳未禁寒冉冉愁。直是无情也肠断，鸟归帆没水空流。', '此诗具体创作时间无考。根据作品内容，大致可以判断，作者登临关西驿，眺望远方，心有所感，因有此作。作品起首两句先点明作者登临关西驿的时间及环境：傍晚初晴，作者凭栏远望，独生怅惘之心，但作者所指怅惘心情具体是什么，还需要在后三联中找寻。颔联两句写作者眺望所见，极目远望，千里春景，一片勃然景色。此句虽写春景令人喜悦的景色，但实际上是为后文抒发感情作铺垫。颈联两句具体写春景：山犹寒色柳尚青，一片将新未新的景色。最后一联中，作者再次提到这种惆怅感受，并与远望水流相联系，因此可以推断，作者的惆怅情绪大致是宦游他乡，春日独自思乡导致的。作品由登临所见写起，不止一次提及自己的感受，却又不写明，只等读者去探究，婉转至极。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (216, 'b856e737d57d43d382d7108d7c2453b4', '唐摭言之李义琛', 111, 15, -1, '年份未详', '武德五年，李义琛与弟义琰，三从弟上德同年三人进士。义琛等陇西人，世居邺城。国初草创未定，家业素贫之，与上德同居，事从姑，定省如亲焉。随计至潼关，遇大雪，逆旅不容，有咸阳商人见而怜之，延与同寝处。居数日，雪霁而去。琛等议鬻驴，以一醉酬之。商人窃知，不辞而去。复先赠以稻粮。琛后宰咸阳，召商人。与之抗礼亲厚。位至刑部侍郎雍州长史。义琰相高宗，上德司门郎中。', '武德五年，李义琛与弟义琰，三从弟上德同年三人进士。义琛等陇西人，世居邺城。国初草创未定，家业素贫之，与上德同居，事从姑，定省如亲焉。随计至潼关，遇大雪，逆旅不容，有咸阳商人见而怜之，延与同寝处。居数日，雪霁而去。琛等议鬻驴，以一醉酬之。商人窃知，不辞而去。复先赠以稻粮。琛后宰咸阳，召商人。与之抗礼亲厚。位至刑部侍郎雍州长史。义琰相高宗，上德司门郎中。', '救人危困，勇于牺牲，不求回报，气义之举，堪薄云天', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (217, 'f784552b918f4e74adc4fb4db570d363', '广异记之李陶', 112, 15, -1, '', '陇西李陶遇一女郎自荐枕席，陶恋其美色，与之甚密。后陶至京参选，病笃，妇前往省问，至潼关为鬼司所拦。妇承陶堂兄过关，至陶所悉心照拂。后陶赴任，妇辞去，谓缘尽云云。', '陇西李陶遇一女郎自荐枕席，陶恋其美色，与之甚密。后陶至京参选，病笃，妇前往省问，至潼关为鬼司所拦。妇承陶堂兄过关，至陶所悉心照拂。后陶赴任，妇辞去，谓缘尽云云。', '鬼女尚如此高义，重情笃义，勇于牺牲，压倒须眉。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (218, 'ea1b9e35394846caabd0c4209d6caf6f', '灵宝县西侧津', 95, 64, -1, '年份未详', '碧溪潋潋流残阳，晴沙两两眠鸳鸯。柳花无赖苦多暇，蛱蝶有情长自忙。千里宦游成底事，每年风景是他乡。高歌一曲垂鞭去，尽日无人识楚狂。', '碧溪潋潋流残阳，晴沙两两眠鸳鸯。柳花无赖苦多暇，蛱蝶有情长自忙。千里宦游成底事，每年风景是他乡。高歌一曲垂鞭去，尽日无人识楚狂。', '此诗具体创作时间无考。根据内容，当是作者宦游他乡时所作。作品起首两句先写作者所见：溪水潺潺，鸳鸯卧眠，一幅安逸自在的画面。颔联两句又写暮春景象：柳花纷飞，蝴蝶自忙。如果说上两句是写静景，这两句则是动态描写，描绘出一幅暮春时节，蝴蝶翻飞柳花新的动态春景图。作品的前半部分写景，后半部分抒情。颈联两句作者写自己此时的处境是多年以来宦游他乡。正是由于这宦游他乡的预设，才给作者在最后一联的肆意放纵提供了可能性。自古以来，宦游之人，见春伤情，已是惯例。此作不同，虽是宦游，但暮春时节，却还有纵意闲适之情，这除了与坏境影响有关，还和作者的心境有很大关系，导致此篇与其他写春的作品全然不同。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (219, 'a161b26f202f41e889f5b3df8c7de83e', '阌乡送上官秀才归关西别业', 6, 16, 762, '762年', '风尘奈汝何，终日独波波。亲老无官养，家贫在外多。醉眼轻白发，春梦渡黄河。相去关城近，何时更肯过。', '风尘奈汝何，终日独波波。亲老无官养，家贫在外多。<br>醉眼轻白发，春梦渡黄河。相去关城近，何时更肯过。', '唐肃宗宝应元年，在潼关，上官秀才归关西别业，有诗送行。阌乡县西接潼关，所以东出长安之人必经阌乡，往来车马不息。诗作首联是对友人风尘奔波的人生经历的称颂及勉励。颔联两句主要是对上官秀才的家境作简要介绍，凸显上官秀才在外奔波之不易。诗作的下半首是作者自谓。“醉眼”句写作者借酒消愁，只能在酒醉状态下忘却自己已经年老的事实。“春梦”一句写作者在酒醉状态中对家乡的想念，因岑参是湖北荆州人，所以“渡黄河”句应是作者思想之情的自然流露。诗作尾联中及时收住这种思乡感情，转而表达对友人东归的羡慕情感。“何时”两字有作者在朝为官，身不由己的无奈感情，也加深了作者思乡的程度。', '762年，在潼关，上官秀才归关西别业，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (220, 'c10a7a0c85044a6d93638cc1745592ec', '河东集之韦浦', 113, 16, -1, '年份未详', '寿州韦浦赴京，在阌乡驿遇归元昶，贫悴交困，韦浦收留之。元昶以法术赢得韦浦欢心。后过潼关，元昶以术闷绝稚儿后遁去。韦浦西行至赤水，衣衫败敝，云已受华岳神君责罚，并告知韦浦将在此地为官。后果然。', '寿州韦浦赴京，在阌乡驿遇归元昶，贫悴交困，韦浦收留之。元昶以法术赢得韦浦欢心。后过潼关，元昶以术闷绝稚儿后遁去。韦浦西行至赤水，衣衫败敝，云已受华岳神君责罚，并告知韦浦将在此地为官。后果然。', '叙事特异，奇幻诡谲，引人入胜', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (221, '1fd8368956174ebb99c64bc48ff58f6e', '使次盘豆驿望永乐县', 57, 17, -1, '年份未详', '山川不记何年别，城郭应非昔所经。欲驻征车终日望，天河云雨晦冥冥。', '山川不记何年别，城郭应非昔所经。<br>欲驻征车终日望，天河云雨晦冥冥。', '此诗具体创作时间无考，当是作者途经盘豆驿，有感而作。武元衡虽然身处高位，有佳名传世，但在其人生历程中，武元衡有一大半时间漂泊在路上，为唐王朝的中兴事业，削弱藩镇割据献出了自己一生的时间。作品的起首两句读来令人心酸：“不记何年别”，虽说是不知道何时曾经相别，但实际上是因为离别次数过多，导致作者也没法记得上一次离开永乐县是何时了。从离别次数之多，就可以读出作者宦游羁旅之心酸孤苦的人生况味。作品的后两句更让人容易有心酸之感：“欲驻”却未驻，体现出作者羁旅在外，身不由己的无奈感受，比上两句中的孤苦况味又深一层：心酸孤苦还有能够改变的余地，但身不由己的无奈感受却无从改变，正是这无法改变的现实，才让读者读来心生不安，心有感触。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (222, '9566fbd37add4690a6b126c9aa53a6f0', '传奇之江叟', 114, 17, -1, '年份未详', '开成中，江叟夜醉盘豆驿路边大槐树下，闻荆山槐与盘豆槐对话。遂至荆山向巨槐求成道之法。巨槐让其赴荆山觅鲍仙师，江叟依言，果得化水丹，服之成仙。', '开成中，江叟夜醉盘豆驿路边大槐树下，闻荆山槐与盘豆槐对话。遂至荆山向巨槐求成道之法。巨槐让其赴荆山觅鲍仙师，江叟依言，果得化水丹，服之成仙。', '大槐树喻京汴道为名利道，颇有深意。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (223, 'e88ab0ff316c4d83bf5246dd1835caaa', '传奇之张云容', 114, 35, -1, '年份未详', '元和末平陆尉薛昭好义乐施，因义释囚犯被贬海东，好友田山叟阴释之并赠丹药，告其将遇美姝。至兰昌宫果遇鬼女张云容等，与云容竟有夙缘。云容得薛昭精气而复生，二人隐居金陵，经年容颜不改。', '元和末平陆尉薛昭好义乐施，因义释囚犯被贬海东，好友田山叟阴释之并赠丹药，告其将遇美姝。至兰昌宫果遇鬼女张云容等，与云容竟有夙缘。云容得薛昭精气而复生，二人隐居金陵，经年容颜不改。', '以诗为媒，推进情节，烘托气氛。表现出诗与小说间的互动。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (224, '0bedeb4811f24f5ca59bdab618ab078d', '传奇之姚坤', 114, 17, -1, '年份未详', '太和中处士姚坤常于猎人网中解救狐狸。后有一女自名夭桃，愿以身许。姚坤携夭桃赴长安赴举，宿盘豆馆，有恶犬逐夭桃，桃化为狐，与犬博而亡。坤遍寻至夜，有老人置酒以谢，云其为夭桃之祖父，夭桃无恙。', '太和中处士姚坤常于猎人网中解救狐狸。后有一女自名夭桃，愿以身许。姚坤携夭桃赴长安赴举，宿盘豆馆，有恶犬逐夭桃，桃化为狐，与犬博而亡。坤遍寻至夜，有老人置酒以谢，云其为夭桃之祖父，夭桃无恙。', '盘豆驿狐女与犬斗一段颇精彩', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (225, '6791e8e39b8f42f1983ab29ae44a621e', '盘豆驿', 99, 17, 780, '780年', '盘豆绿云上古驿，望思台下使人愁。江充得计太子死，日暮戾园风雨秋。', '盘豆绿云上古驿，望思台下使人愁。<br>江充得计太子死，日暮戾园风雨秋。', '此诗大致创作于唐德宗建中元年，当时权德舆任汴东水陆运盐铁祖庸使包佶从事，此诗或作于此时。此诗是传统的咏史作品，作者登临盘豆驿，回想起葬身这里的汉太子刘据，不禁感叹不已，遂有此诗。作品的起首两句先言盘豆驿是一座古老驿站，为下文回忆汉朝事件作铺垫；次句言作者登临盘豆驿，远望汉武帝曾为汉太子刘据修建的“思子台”，不禁满心惆怅。作品的后两句写汉武帝修建思子台的原因：汉武帝时有官员江充与太子刘据有隙，于是诬陷刘据，诬陷其在宫中作巫蛊之事，有谋反之心，并上报汉武帝请求追杀太子刘据。后来刘据兵败，悬梁自缢，汉武帝自然知道了刘据本无反心，一切都是江充诽谤所致，所以在湖县修建思子台，并将刘据葬身于盘豆驿周边，其墓地名为“戾园”。所以可知，作者此时所愁正是为这位被诬陷致死的太子鸣不平，情感恳切，使人泪下。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (226, '5fc6e8db222b4a7c8cb6727ce8fc0d48', '题盘豆驿水馆后轩', 115, 17, 878, '878年', '极目晴川展画屏，地从桃塞接蒲城。滩头鹭占清波立，原上人侵落照耕。去雁数行天际没，孤云一点净中生。冯轩尽日不回首，楚水吴山无限情。', '极目晴川展画屏，地从桃塞接蒲城。滩头鹭占清波立，<br>原上人侵落照耕。去雁数行天际没，孤云一点净中生。<br>冯轩尽日不回首，楚水吴山无限情。', '干符五年，韦庄离家出潼关，途中有所作。作品的起首两句先言作者眺望所见：极目远眺，尽是晴川万里，城塞相通，一片优美壮观的景象。颔联两句从水陆两方面写起：诗题写“题水馆”，所以首句先写水景，鹭占清波，描绘一幅优雅娴静的画面；此句从陆上人们的耕种写起，田地耕种，一片匆忙喜悦的丰收景色。既有娴静之美，又有喜悦之色，仅仅两句，将盘豆驿馆周围的社会环境描写的十分到位，如在眼前。颈联两句又写空中景色，从空中“雁”、“云”两种意象着手，渲染出宁静淡美的空中景色。如果说前三联只是盘豆驿水馆的景色描绘的话，那么作品的最后一联则是作者在见到这种美景的主观感受的自然流露了。尾联中，作者先说自己难以离开这画面，并把这种画面，这种意象组合营造出的意境拟作“无限情”来概括：晴川画屏、鹭占清波、孤云去雁，仔细体会这三种意境，深知作者此言不谬。', '干符五年，离家出潼关，途中作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (227, 'a661df396b7e470494363e5472ff92d6', '出关宿盘豆馆对丛芦有感', 14, 17, 839, '839年', '芦叶梢梢夏景深，邮亭暂欲洒尘襟。昔年曾是江南客，此日初为关外心。思子台边风自急，玉娘湖上月应沉。清声不远行人去，一世荒城伴夜砧。', '芦叶梢梢夏景深，邮亭暂欲洒尘襟。昔年曾是江南客，<br>此日初为关外心。思子台边风自急，玉娘湖上月应沉。<br>清声不远行人去，一世荒城伴夜砧。', '唐文宗开成四年，李商隐调任弘农县尉，赴任途中所作。潘豆馆，位于潼关以东四十里。因汉武帝过此，百姓以牙豆盘献，所以名为潘豆馆，又名潘豆驿。诗作首联以微风中芦苇丛发出的“稍稍”声音起兴。因李商隐在调补弘农尉前，曾为官职奔走多处，所以自言“尘襟”。颔联写作者早年生活在江南地区，此时又被排斥到关外。仅仅两句，可以想见义山此时心中因奔波而生出的郁闷情绪。颈联两句写家人对自己的思念，更进一步加深了这种在外奔波带来的郁闷情绪。尾联写芦苇应题，写芦苇丛在风中的响声同砧声一样，催人思家，催人泪下。全诗虽作于义山得官之时，但多年奔波在外，终无所获的经历令作者心感疲惫，心生郁闷。作者很好地处理了这种情绪，使作品读来含蓄委婉，凄怆不已。', '开成四年，李商隐调任弘农县尉，赴任途中所作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (228, 'e1214ae66444442fa2138cd89b2ec98e', '秋晚题湖城驿池上亭', 59, 18, 805, '805年', '秋次池上馆，林塘照南荣。尘衣纷未解，幽思浩已盈。风莲坠故萼，露菊含晚英。恨为一夕客，愁听晨鸡鸣。', '秋次池上馆，林塘照南荣。尘衣纷未解，幽思浩已盈。风莲坠故萼，露菊含晚英。恨为一夕客，愁听晨鸡鸣。', '唐顺宗永贞年间，由王叔文、刘禹锡等人发起并参与的“永贞革新”宣告失败。刘禹锡因此被贬为远州刺史，又加贬为远州司马。行至江陵，再贬连州刺史。此诗即为被贬途中所作。诗的首联点时点地，概括描写湖城驿秋日景象。“尘衣”一句化用陆机《为顾彦先赠妇》“京洛多风尘，素衣化为缁。”言自己被贬出长安的身份。“幽思”当然是指作者被贬后郁郁不得纾解的失意情绪。“恨为”一句言自己在湖城驿不得逗留太久。因为古时贬谪有限期。《唐会要》称左贬情节稍重，当日驰十驿以上赴任。被贬出京，又有贬谪期限加身，此刻作者心中所想，读者透过诗句，可以深有体会。除了政治失意后的情绪之外，诗作的颈联也值得玩味。“坠”字虽写风吹莲坠，但此时的作者就同莲萼一般，心中满含对未来不可知的恐惧以及对现状失意的痛苦。', '唐顺宗永贞元年九月，赴连州途中作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (229, '4fd7d633f7b9460b9c3e063e97e9d955', '有小白马乘驭多时奉使东行至稠桑驿溘然而毙足可惊伤不能忘情题二十韵', 12, 19, 827, '827年', '能骤复能驰，翩翩白马儿。毛寒一团雪，鬃薄万条丝。皂盖春行日，骊驹晓从时。双旌前独步，五马内偏骑。芳草承蹄叶，垂杨拂顶枝。跨将迎好客，惜不换妖姬。慢鞚游萧寺，闲驱醉习池。睡来乘作梦，兴发倚成诗。鞭爲驯难下，鞍缘稳不离。北归还共到，东使亦相随。赭白何曾变，玄黄岂得知。嘶风觉声急，蹋雪怪行迟。昨夜犹刍秣，今朝尚絷维。卧槽应不起，顾主遂长辞。尘灭骎骎迹，霜留皎皎姿。度关形未改，过隙影难追。念倍燕求骏，情深项别骓。银收钩臆带，金卸络头羁。何处埋奇骨，谁家觅弊帷。稠桑驿门外，吟罢涕双垂。', '能骤复能驰，翩翩白马儿。毛寒一团雪，鬃薄万条丝。皂盖春行日，骊驹晓从时。双旌前独步，五马内偏骑。<br>芳草承蹄叶，垂杨拂顶枝。跨将迎好客，惜不换妖姬。慢鞚游萧寺，闲驱醉习池。睡来乘作梦，兴发倚成诗。<br>鞭爲驯难下，鞍缘稳不离。北归还共到，东使亦相随。赭白何曾变，玄黄岂得知。嘶风觉声急，蹋雪怪行迟。<br>昨夜犹刍秣，今朝尚絷维。卧槽应不起，顾主遂长辞。尘灭骎骎迹，霜留皎皎姿。度关形未改，过隙影难追。<br>念倍燕求骏，情深项别骓。银收钩臆带，金卸络头羁。何处埋奇骨，谁家觅弊帷。稠桑驿门外，吟罢涕双垂。', '唐宪宗元和元年，白居易出使洛阳，行至稠桑驿，所乘马死，因有此作。全诗共分20句，前10句描写马的样貌与作者对其的感情；后10句言白马从生病至死的过程以及作者对白马的惋惜。“毛寒”一句是总写白马的样貌，与众不同，脱尘出俗。“双旌”同“跨将”两句写作者对白马的偏爱。“慢鞚”至“北归”四句，言作者同白马度过的美好时光。“赭白”句至“卧槽”句，从白马毛色生变写至溘然而毙，不能起行。言白马从生病到死的过程。全诗的后几句写作者对白马的惋惜之情。“燕求骏”与“项别骓”两句分别用燕昭王千金求死千里马与项王兵败乌江，惜别乌骓马的典故，倾诉自己对白马的眷恋之情。诗的结尾“何处”两句毫不掩饰地写下自己沉痛至泪流的神情，令读者读来伤感。', '元和元年，出使洛阳，行至稠桑，所乘马死，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (230, 'a52a8e41a2394af98620a98815b92299', '过函谷关', 26, 65, -1, '年份未详', '二百四十载，海内何纷纷。六国兵同合，七雄势未分。从成拒秦帝，策决问苏君。鸡鸣将狗盗，论德不论勋。', '二百四十载，海内何纷纷。六国兵同合，七雄势未分。<br>从成拒秦帝，策决问苏君。鸡鸣将狗盗，论德不论勋。', '此诗具体创作时间无考。根据作品内容，当是作者途经函谷关，见函谷关城，有感而作。这是一首咏史作品。函谷关作为自古以来的兵家必争之地，在秦朝时就已经享有盛名，所以作者在亲眼见到函谷关的雄险地势后，对这里曾经发生过的故事进行了一番歌咏。首联两句写战国时期，海内不平，纷争并起。颔联两句写战国时期天下共有七国，其中最为强大也是最有侵略野心的国家即是秦国。为了对抗秦国，其他六国决定连横抗秦。颈联两句写苏秦提出连横抗秦，并使其他六国纷纷接纳这个建议。作品的最后一联写孟尝君出使秦国，被秦国软禁。后来借助孟尝君门生的帮助，孟尝君才得以逃出秦国。虽然得以逃脱，但是逃脱的方式并不光彩，后人为了讽刺孟尝君，按照这个事情起了成语为“鸡鸣狗盗”，是说使用一些卑鄙，下等的人。在最后一句，作者很明显对后人给孟尝君作出的评价不满意，他认为孟尝君逃出秦国是避免了两国之间开战，是有功劳的，但后世只看到在这件事情中孟尝君的德行底下，没看到其功勋光辉，这种认识是不全面的。联系宋之问的生平，可以发现，后人对宋之问的评价也是从德行一方面进行评价的，所以作者有可能在为孟尝君辩解的同时，也为自己辩解。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (231, '823c5723887d40369f4c850dde717058', '上阳宫侍宴应制得林字', 26, 66, 695, '695年', '广乐张前殿，重裘感圣心。砌蓂霜月尽，庭树雪云深。旧渥骖宸御，慈恩忝翰林。微臣一何幸，再得听瑶琴。', '广乐张前殿，重裘感圣心。砌蓂霜月尽，庭树雪云深。<br>旧渥骖宸御，慈恩忝翰林。微臣一何幸，再得听瑶琴。', '典型的应制诗三部式。“瑶琴”迎合武后用女性典故。', '侍宴上阳宫作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (232, 'b28ba4bb87164ff9a2192d338690b3bd', '正夜侍宴应诏（英华作正月望夜上阳宫侍宴应制）', 116, 66, 695, '695年', '臣闻神器至大，非圣无以光临；宝位至尊，非神无以长守。我金轮圣神皇帝垂妙觉，抚鸿勋，出轩宫而镇紫微，卷翚衣而袭元衮，释罘祝网，万族咸宁。革故维新，五刑不用，润玉律而含元气，转金浑而调顺晷。穷荒极远，重译左言之俗；负阻凭危，背德殊风之类：莫不厥角稽颡，执贽来庭，烟火通于万方，车书混于千界。庆延八室，享配于明祇；辟水三雍，讲论乎道义。麟凤荐祉，龟龙奉图，石铭显瑞于郊畿，玉收告祥于宫掖。以日继月，纷纶葳蕤，竹帛书之而未穷，夷夏歌之而不极。圣人之具品周矣，天子之能事毕矣，自千古以下，迄于梁隋，何功于人，比我全德？于是宁宴坐，展豫游，顺四时，乘六辨，先王洛食，上帝河都枢机正于域中，雨露均于天下。徒观其离宫别殿。弥复道而亘南端；高阁重甍，瞰崇墉而连北斗。沧洲晓气，化为宫阙之形；阊阖秋风，乱起金银之树。降雕舆而式燕，簪绋凝严；披镂槛而升高，山河在目。参光有地，游日月于天边；睨远无穷，见城池于掌上。四达分九重之路，积树捎云；双茎当铁锁之桥，流珠耿汉。霞浆玉醴，与湛露而俱倾；凤管龙丝，杂商飙而共奏。圣皇迺望芝田，赋葛天，和者万，唱者千，迺命小臣，编纪众作，流汗拜首，而为序云。', '臣闻神器至大，非圣无以光临；宝位至尊，非神无以长守。我金轮圣神皇帝垂妙觉，抚鸿勋，出轩宫而镇紫微，卷翚衣而袭元衮，释罘祝网，万族咸宁。革故维新，五刑不用，润玉律而含元气，转金浑而调顺晷。穷荒极远，重译左言之俗；负阻凭危，背德殊风之类：莫不厥角稽颡，执贽来庭，烟火通于万方，车书混于千界。庆延八室，享配于明祇；辟水三雍，讲论乎道义。麟凤荐祉，龟龙奉图，石铭显瑞于郊畿，玉收告祥于宫掖。以日继月，纷纶葳蕤，竹帛书之而未穷，夷夏歌之而不极。圣人之具品周矣，天子之能事毕矣，自千古以下，迄于梁隋，何功于人，比我全德？于是宁宴坐，展豫游，顺四时，乘六辨，先王洛食，上帝河都枢机正于域中，雨露均于天下。徒观其离宫别殿。弥复道而亘南端；高阁重甍，瞰崇墉而连北斗。沧洲晓气，化为宫阙之形；阊阖秋风，乱起金银之树。降雕舆而式燕，簪绋凝严；披镂槛而升高，山河在目。参光有地，游日月于天边；睨远无穷，见城池于掌上。四达分九重之路，积树捎云；双茎当铁锁之桥，流珠耿汉。霞浆玉醴，与湛露而俱倾；凤管龙丝，杂商飙而共奏。圣皇迺望芝田，赋葛天，和者万，唱者千，迺命小臣，编纪众作，流汗拜首，而为序云。', '典型的应制诗三部式。“湛露”迎合武后用男性典故。', '侍宴上阳宫作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (233, '9bfe5348e2bc4fdb9ba70366dd8e6713', '经函谷关', 7, 65, -1, '年份未详', '洪河绝山根，单轨出其侧。万古为要枢，往来何时息。秦皇既恃险，海内被吞食。及嗣同覆颠，咽喉莫能塞。炎灵讵西驾，娄子非经国。徒欲扼诸侯，不知恢至德。圣朝及天宝，豺虎起东北。下沉战死魂，上结穷冤色。古今虽共守，成败良可识。藩屏无俊贤，金汤独何力。驰车一登眺，感慨中自恻。', '洪河绝山根，单轨出其侧。万古为要枢，往来何时息。<br>秦皇既恃险，海内被吞食。及嗣同覆颠，咽喉莫能塞。<br>炎灵讵西驾，娄子非经国。徒欲扼诸侯，不知恢至德。<br>圣朝及天宝，豺虎起东北。下沉战死魂，上结穷冤色。<br>古今虽共守，成败良可识。藩屏无俊贤，金汤独何力。<br>驰车一登眺，感慨中自恻。', '此诗具体创作时间无考。根据作品内容，当是作者途经函谷关，目睹函谷关胜迹，有感而作。作品的前四句先叙函谷关地势险要以及其重要的地理位置。作者在这四句中说函谷关千年以来一直是交通要道，兵家必争之地。“秦皇”至“徒欲”几句写函谷关作为秦帝国的重要关口，在战国纷争时期，为秦帝国挡住了多少千难万险，也为秦帝国守住了万世基业，为后期秦帝国统一六国提供了军事保障。“圣朝”至“藩屏”几句写函谷关在唐朝时期的重要作用。先言函谷关在安史之乱中，经历了诸多场战争，埋葬了诸多灵魂。又说现在的朝廷兵少将寡，与当年安史之乱时期的兵力相比，实在是相距甚远。为此，作者在作品的最后两句中，发出了唐朝实力不如往昔的感慨。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (234, '119c10d66a814d2885bc899ef95eb918', '晓过函谷关', 117, 65, -1, '年份未详', '因韩为赵两游秦，十月冰霜渡孟津。纵使鸡鸣遇关吏，不知余也是何人。', '因韩为赵两游秦，十月冰霜渡孟津。纵使鸡鸣遇关吏，不知余也是何人。', '此诗具体创作时间无考。根据作品内容，大致推断此作当是作者为歌咏战国时期孟尝君事迹所作。作品的前两句先叙孟尝君为韩、赵两国两次游说秦国的事情，为下文叙述孟尝君鸡鸣狗盗一事作铺垫。作品的最后两句用孟尝君鸡鸣狗盗典故。这个典故是说孟尝君在被困秦国时，其手下的门客各显神通，使其危难关头脱险秦地，最终返回齐国。整首作品，应该都是在歌颂孟尝君礼贤下士，招贤纳士等优秀质量。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (235, 'd306e3ac43114e05a1005a1ba34109f1', '春过函谷关', 118, 65, -1, '年份未详', '几度作游客，客行长苦辛。愁看函谷路，老尽布衣人。岁远关犹固，时移草亦春。何当名利息，遣此绝征轮。', '几度作游客，客行长苦辛。愁看函谷路，老尽布衣人。<br>岁远关犹固，时移草亦春。何当名利息，遣此绝征轮。', '此诗具体创作时间无考。根据作品内容，当是作者在游历函谷关时，有感而作。作品的起首两句先叙作者心志。从这两句中，可以得知作者仕途坎坷，常为客行宦游之事。据此，诗作的内容情感可推知一二。颔联两句中“愁”字当是作者宦游客身份驱使下形成的特有感受。句中“布衣人”一词也可推测作者此时尚未做官。作者在颈联两句主要表达对时间的嗟叹。作者在这两句话中说，面对时间的流逝，函谷关依然常在，春草依旧常新，言下之意是嗟叹自己受时间的折磨：已经青丝变白发了，自己的命运依旧没有什么大的改变。作品的最后一联作者给出了排解方法：勉励自己淡泊名利，或许不受名利的驱使与折磨，自己可能还会好受一些。此诗中，最值得称赞的一处当是作品的颈联，虽言时光对关城、春草的折磨，但时光对作者的折磨之痛皆在言外。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (236, '3f79c538f2da4e82bcd6a3809f9ef3ff', '过函谷关', 119, 65, -1, '年份未详', '地险崤函北，途经分陕东。逶迤众山尽，荒凉古塞空。河光流晓日，树影散朝风。圣德今无外，何处是关中。', '地险崤函北，途经分陕东。逶迤众山尽，荒凉古塞空。<br>河光流晓日，树影散朝风。圣德今无外，何处是关中。', '此诗具体创作时间无考。根据作品内容，当是作者在游历函谷关时，有感而作。作品的起首两句先从函谷关的地理位置对函谷关作了一番勾勒，令读者可以大致清楚函谷关的地理位置：位于崤山之北，陕地之东。颔联两句又从地势特征对函谷关作了细致的描写，突出函谷关地势险要的特征，也显示出函谷关兵家必争之地的显赫地位。颈联两句以作者登临函谷关所见的平静景象入手，象征着当时边境和平，社会承平的祥和景象。作品的最后两句，作者将这种社会承平景象归结于皇帝治理之功。据此，也可推测此作当是作者奉诏创作的应制作品。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (237, '37f9f0242c494f1b94e0a9040f4aab1f', '石壕吏', 91, 25, 759, '759年', '暮投石壕村，有吏夜捉人。老翁逾墙走，老妇出门看。吏呼一何怒，妇啼一何苦。听妇前致词，三男邺城戍。一男附书至，二男新战死。存者且偷生，死者长已矣。室中更无人，惟有乳下孙。有孙母未去，出入无完裙。老妪力虽衰，请从吏夜归。急应河阳役，犹得备晨炊。夜久语声绝，如闻泣幽咽。天明登前途，独与老翁别。', '暮投石壕村，有吏夜捉人。老翁逾墙走，老妇出门看。<br>吏呼一何怒，妇啼一何苦。听妇前致词，三男邺城戍。<br>一男附书至，二男新战死。存者且偷生，死者长已矣。<br>室中更无人，惟有乳下孙。有孙母未去，出入无完裙。<br>老妪力虽衰，请从吏夜归。急应河阳役，犹得备晨炊。<br>夜久语声绝，如闻泣幽咽。天明登前途，独与老翁别。', '唐肃宗干元二年春，杜甫自洛阳返华州，途经石壕，见吏强征老弱，有诗。全诗以质朴的笔触真实记录了安史之乱给人民百姓带来的深重灾难，揭露了封建战争的无情与残酷，同时也批判了统治者的残暴统治。作品前两句单刀直入，直叙主题。“捉”字极好，有猛虎扑人之势，是封建官吏对待人民的真实写照。“老翁”两句引起下文，为下文埋下伏笔。“吏呼”至“犹得”句全写老妇同官吏的对话，在这段对话中，我们可以知道：老妇生活困难，家贫无所供给，三个儿子都以充军，家中唯一的男丁是一位年幼需要抚养成人的小孙子。而在官吏蛮横无理地要求下，老妇只得答应同官吏上前线，准备晨炊。只有这样，官吏才勉强同意，不在怒吼。全诗的最后四句，写出了事件的结局与作者的感情态度。作者前日投靠时有老翁老妇二人，现在只留老翁一人。这样的故事结局，作者没有掺杂太多的感情态度于其中，只是将血淋淋的结局呈现给读者，留给读者批判，寓褒贬于叙事，这是很高明的做法。', '759年，春，自洛阳返华州，途经石壕，见吏强征老弱，有诗。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (238, '27c25d7692234fd8a455027869eeafaf', '重题郑氏东亭（在新安界）', 91, 28, 759, '759年', '华亭入翠微，秋日乱清晖。崩石欹山树，清涟曳水衣。紫鳞冲岸跃，苍隼护巢归。向晚寻征路，残云傍马飞。', '华亭入翠微，秋日乱清晖。崩石欹山树，清涟曳水衣。<br>紫鳞冲岸跃，苍隼护巢归。向晚寻征路，残云傍马飞。', '羁旅之情，见于槠墨。', '760年，春，自洛阳返华州，途经新安作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (239, '9c127e7e0ce34ce2858050e67ba63c6f', '李章武', 120, 12, -1, '年份未详', '贞元三年，李章武于华州邂逅王氏子妇，与之通。贞元十一年复至华州，此妇已逝。章武在邻妇杨氏帮助下得与女相见，临行赋诗赠别，女赠靺鞨宝。后李章武将之转赠邻妇杨氏。', '贞元三年，李章武于华州邂逅王氏子妇，与之通。贞元十一年复至华州，此妇已逝。章武在邻妇杨氏帮助下得与女相见，临行赋诗赠别，女赠靺鞨宝。后李章武将之转赠邻妇杨氏。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (240, '6bf85e70b7be4ffdbd50617ed5572112', '乾元元年华州试进士策问五首', 91, 12, 758, '758年', '问：古之山林薮泽之地，各以肥硗多少为差。故供甲兵士徒之役，府库赐与之用，给郊庙宗社之祀，奉养禄食之出，辨乎名物，存乎有司，是谓公赋知归、地著不挠者已。今圣朝绍宣王中兴之洪业於上，庶尹备山甫补衮之能事於下，而东寇犹小梗，率土未甚辟，总彼赋税之获，尽赡军旅之用，是官御之旧典阙矣。人神之攸序乖矣；欲使军旅足食，则赋税未能充备矣。欲将诛求不时，则黎元转罹於疾苦矣，子等以待问之实，知新之明，观志气之所存，於应对乎何有，伫渴救敝之通术，愿闻强学之所措，意盖在此矣，得游说乎？', '问：古之山林薮泽之地，各以肥硗多少为差。故供甲兵士徒之役，府库赐与之用，给郊庙宗社之祀，奉养禄食之出，辨乎名物，存乎有司，是谓公赋知归、地著不挠者已。今圣朝绍宣王中兴之洪业於上，庶尹备山甫补衮之能事於下，而东寇犹小梗，率土未甚辟，总彼赋税之获，尽赡军旅之用，是官御之旧典阙矣。人神之攸序乖矣；欲使军旅足食，则赋税未能充备矣。欲将诛求不时，则黎元转罹於疾苦矣，子等以待问之实，知新之明，观志气之所存，於应对乎何有，伫渴救敝之通术，愿闻强学之所措，意盖在此矣，得游说乎？', '无', '乾元间为华州司功参军试进士作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (241, '56f1fde6c4414d5cbd334a4e778d2c2f', '出官经硖石县（天复三年二月二十二日）', 121, 24, 903, '903年', '谪宦过东畿，所抵州名濮。故里欲清明，临风堪恸哭。溪长柳似帷，山暖花如醭。逆旅讶簪裾，野老悲陵谷。暝鸟影连翩，惊狐尾纛簌。尚得佐方州，信是皇恩沐。', '谪宦过东畿，所抵州名濮。故里欲清明，临风堪恸哭。<br>溪长柳似帷，山暖花如醭。逆旅讶簪裾，野老悲陵谷。<br>暝鸟影连翩，惊狐尾纛簌。尚得佐方州，信是皇恩沐。', '唐昭宗天复三年，韩偓被贬濮州司马，因有此作。作品的前两句先点明作者此次贬官去往的地点东都洛阳周边的濮州地区。“故里”两句是作者想象家乡此时正值清明佳节，临风恸哭的事情。表现作者对家乡的怀念之情。“溪长”两句是作者行旅途中对春日景物的描写。“逆旅”两句则是作者自己在贬所看到身份显赫人物产生的哀叹己不如人的情绪。“暝鸟”两句则是作者对旅途中景物的描写。作品的最后两句，作者还不忘对自己贬官濮州一事，感念皇恩。', '天复三年，贬谪途中作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (242, '31bcde93879341548e5456e40ad62955', '潼关吏', 91, 15, 759, '759年', '士卒何草草，筑城潼关道。大城铁不如，小城万丈余。借问潼关吏，修关还备胡。要我下马行，为我指山隅。连云列战格，飞鸟不能踰。胡来但自守，岂复忧西都。丈人视要处，窄狭容单车。艰难奋长戟，万古用一夫。哀哉桃林战，百万化为鱼。请嘱防关将，慎勿学哥舒。', '士卒何草草，筑城潼关道。<br>大城铁不如，小城万丈余。<br>借问潼关吏，修关还备胡。<br>要我下马行，为我指山隅。<br>连云列战格，飞鸟不能踰。<br>胡来但自守，岂复忧西都。<br>丈人视要处，窄狭容单车。<br>艰难奋长戟，万古用一夫。<br>哀哉桃林战，百万化为鱼。<br>请嘱防关将，慎勿学哥舒。', '唐肃宗干元二年春，从洛阳返回华州的途中，经潼关，感慨战事，有诗。全诗集中表现了作者对当年桃林战败的深深遗憾，这种情感全以对话的形式娓娓道出。作品的前四句写途经潼关时，对潼关怀有坚不可摧的总体印象。“借问”两句是作者向守城官吏发问。以后的十四句主要是守城官吏作出的回答。在这段话中，官吏们向作者炫耀着潼关的险要地势以及完美无缺的防御功能。从“飞鸟”、“岂复”几句可以看出，官吏对于潼关的防御功能是抱有极大信心，甚至有一些自负情绪的。正是出于对官吏过于自负情绪的担忧，作者在作品的后四句提到了当年的桃林之战。安史之乱时，哥舒翰还未整齐军备，就出潼关迎接来犯的安禄山军队，结果全军覆灭，无人生还。心怀家国之心的作者，在此以桃岭之战，告诫、劝勉守卫潼关的官吏，要吸取历史教训，严阵以待来犯之敌。', '春，从洛阳返回华州的途中，经潼关，感慨战事，有诗。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (243, 'eb41760419334c53a8b32d5940d4c39d', '过绣岭宫', 122, 0, -1, '年份未详', '古殿春残绿野阴，上皇曾此驻泥金。三城帐属升平梦，一曲铃关怅望心。苑路暗迷香辇绝，缭垣秋断草烟深。前朝旧物东流在，犹为年年下翠岑。', '古殿春残绿野阴，上皇曾此驻泥金。三城帐属升平梦，<br>一曲铃关怅望心。苑路暗迷香辇绝，缭垣秋断草烟深。<br>前朝旧物东流在，犹为年年下翠岑。', '此诗具体创作内容无考。根据作品内容，可以推知此作当属咏史作品。作品起首两句先言唐玄宗封禅绣岭宫一事，写唐玄宗开元年间盛世景象。颔联两句写唐玄宗开元时期，边境和平，海内太平，实则是暗藏安史之乱的隐患。颈联两句写唐玄宗在贵妃死后，夜中闻雨，下令制《雨淋铃》曲，自蜀眺望，心生惆怅情绪。自此以后，唐玄宗不复游幸绣岭宫，而绣岭宫也因此破败，丛生野草。作品的最后两句写作者的主观感受，可知作者面对这前朝旧物，是怀着凭吊之心来看待的。全诗凭借“残”、“暗”、“断”、“缭”、“深”等形容词，营造一种凄冷幽深的意境，令读者在感知绣岭宫破败不堪的景象同时，还能真切体会作者面对前朝旧物的惆怅心情。', '', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (244, '1a235edc0cd740cfb7631dd4b2ef8ab9', '过渑池书事', 95, 26, -1, '年份未详', '渑池城郭半遗基，无限春愁挂落晖。柳渡风轻花浪绿，麦田烟暖锦鸡飞。相如忠烈千秋断，二主英雄一梦归。莫道新亭人对泣，异乡殊代也沾衣。', '渑池城郭半遗基，无限春愁挂落晖。柳渡风轻花浪绿，<br>麦田烟暖锦鸡飞。相如忠烈千秋断，二主英雄一梦归。<br>莫道新亭人对泣，异乡殊代也沾衣。', '此诗具体创作时间无考。根据作品内容，当是作者途经渑池，怀念蔺相如与楚王渑池赴会的故事，有感而作。作品首联先对渑池遗址进行了简要描写。其中“春愁”一词可以想见作者在面对渑池遗址的主观态度。颔联两句接着又对渑池周围环境作了细致的描述。此时正值春日，正是柳条飘飞，蝴蝶翻飞的好时节，麦田上弥漫着的雾气给人以温暖的体验。颈联部分结束写景，转向叙事抒情环节。颈联先言蔺相如以及廉颇两人的纷争以及两人同心报楚的心态，歌颂了这两位历史上有名的忠臣。尾联两句写作者打破了时空的界限，在怀念蔺相如的同时，也为其忠贞不二的生命历程不禁泪下沾衣。由物及人，全作写得中规中矩。只是在作品的最后一联，作者打破时空界限，受蔺相如事的启发而感动落泪，其情感深挚令人感慨。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (245, '9c5c6b0b97ac4a44bdcc8da1dd18d672', '敷水歌，送窦渐入京', 6, 13, 762, '762年', '罗敷昔时秦氏女，千载无人空处所。昔时流水至今流，万事皆逐东流去。此水东流无尽期，水声还似旧来时。岸花仍自羞红脸，堤柳犹能学翠眉。春去秋来不相待，水中月色长不改。罗敷养蚕空耳闻，使君五马今何在。九月霜天水正寒，故人西去度征鞍。水底鲤鱼幸无数，愿君别后垂尺素。', '罗敷昔时秦氏女，千载无人空处所。昔时流水至今流，万事皆逐东流去。<br>此水东流无尽期，水声还似旧来时。岸花仍自羞红脸，堤柳犹能学翠眉。<br>春去秋来不相待，<br>水中月色长不改。罗敷养蚕空耳闻，使君五马今何在。<br><br>九月霜天水正寒，故人西去度征鞍。水底鲤鱼幸无数，愿君别后垂尺素。', '唐肃宗宝应元年，岑参在华州任职，窦渐入京，于敷水有诗送行。敷水，又称罗敷水，位于华阴县敷水镇附近。因名称同古时美女秦罗敷相同，所以古人在此地创作诗歌时，都会用美女秦罗敷同水相比拟，或是感伤时光流逝，或是通过女性美类比自然之美。诗作的前四句就是通过秦罗敷典故发出“人已作古水长流”的感叹。诗作的后几句通过女性与流水的类比，修饰敷水的美丽景色。“九月”句点明作者送别友人的时间为秋日，而友人去往的方向则是西边。诗的最后一句表达作者对友人的挽留及不舍。“尺素”本出自《饮马长城窟行》中“呼儿鹏烹鲤鱼，中有尺素书。”一句，特指远方亲友凭借鲤鱼传递的书信。', '762年，在华州，窦渐入京，于敷水有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (246, '14b6e7baa8494f689c1d6b6f2641f2b6', '稠桑驿喜逢严河南中丞便别（得时字）', 6, 19, 761, '761年', '驷马映花枝，人人夹路窥。离心且莫问，春草自应知。不谓青云客，犹思紫禁时。别君能几日，看取鬓成丝。', '驷马映花枝，人人夹路窥。<br>离心且莫问，春草自应知。<br>不谓青云客，犹思紫禁时。<br>别君能几日，看取鬓成丝。', '唐肃宗上元二年，岑参在虢州稠桑驿喜逢严武，相别时有诗。自古送别题材诗作中都包含两种情感内涵：或是对友人的美好祝愿，或是表达对友人即将离别的思念之情。而岑参是擅于撰写送别题材诗作的诗人，在此诗中可见其功力。全诗不仅将此两种情感内涵杂糅在一起，而且将这两种情感内涵写得含蓄婉转，不流于直露。这是这首诗在艺术上的一大特色。首联“驷马”一词原指高官乘坐的驾驷马的车辆，现特指严武出行的队伍，表现作者对严武政治地位的赞颂。颔联“离心”一句化用《楚辞•招隐士》中“王孙游兮不归，春草生兮萋萋。”一句言看见萋萋春草而怀念远游未归的人。此句写作者对将别未别的友人的思念，借用典故，含蓄至极。颈联“青云客”是指前途远大的人，代指严武；“紫禁”一词代指国家的权利中枢。此句言作者对严武的期望，希望严武能够实现自己的政治理想。尾联一句再次表现作者对将别未别的友人的思念之情，情深意浓，感人肺腑。', '761年，在虢州稠桑驿喜逢严武，相别，有诗。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (247, 'a9f40c712e5f404499064f4b88b478d9', '饯许州宋司马赴任', 17, 3, 708, '708年秋', '昔吾游箕山，朅来涉颍水。复有许由庙，迢迢白云里。闻君佐繁昌，临风怅怀此。傥到平舆泉，寄谢干将里。', '昔吾游箕山，朅来涉颍水。复有许由庙，迢迢白云里。<br>闻君佐繁昌，临风怅怀此。傥到平舆泉，寄谢干将里。', '唐中宗景龙二年秋，修文馆诸学士饯别许州宋司马，因有此作。此作与修文馆诸学士的其他同题作品稍有不同，作者摒弃了传统的送别诗创作范式，而是将自己游历许州等地的见闻融汇于诗，可能是劝勉友人在为官期间，多多留意许州的自然景观，不要因为事务繁忙，忽略了自身的精神建设，“临风伥怀此”的意义也在于此。除了对传统的送别诗创作范式进行一番革新之外，作者在诗中写下的包括“萁山”“颍水”“许由庙”等文学景观，都值得我们在几千年后去追思及遥想。', '708年秋，修文馆学士饯别许州宋司马。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (248, 'aea23336a6c44840ae8ece983ec8e6c8', '饯许州宋司马赴任', 20, 3, 708, '708年秋', '展骥旌时杰，谈鸡美代贤。暂离仙掖务，追送近郊筵。地惨金商节,人康璧假田。从来昆友事，咸以佩刀传。', '展骥旌时杰，谈鸡美代贤。暂离仙掖务，追送近郊筵。<br>地惨金商节,人康璧假田。从来昆友事，咸以佩刀传。', '唐中宗景龙二年秋，修文馆诸学士饯别许州宋司马，因有此作。诗的首联用典，夸赞友人卓越的能力。“展骥”说的是三国时期庞统事，这里是指友人才能出众，胜过当今豪杰。“谈鸡”是指《幽明录》中宋处宗事，这里是说友人很勤奋，其勤奋的事迹可以比得上当今的贤人。这两句主要是作者听闻宋司马赴任许州后，对友人的褒扬。诗的颔联是作者自言。这里是说作者闻知此事甚晚，赶忙追送友人至灞亭送别处。“追”字甚好，既写出作者听闻友人赴任的喜讯感到欣喜，还体现出作者与友人私交甚好。诗的颈联极为难解，大概是作者点明送别友人的时节。尾联作者以典故作结全诗。“佩刀”一事出自《晋书》中王祥得吕虔佩刀一事，这里是说作者赞许友人堪负重任，对友人远大前程的美好期许。', '708年秋，修文馆学士饯别许州宋司马。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (249, '1db5ce6b5f234287ada3642f759ef7d6', '饯许州宋司马赴任', 21, 3, 708, '708年秋', '令弟与名兄，高才振两京。别序闻鸿雁，离章动鶺鴒。远朋驰翰墨，胜地写丹青。风月相思夜，劳望颍川星。', '令弟与名兄，高才振两京。别序闻鸿雁，离章动鶺鴒。远朋驰翰墨，胜地写丹青。风月相思夜，劳望颍川星。', '唐中宗景龙二年秋，修文馆诸学士饯别许州宋司马，因有此作。 通过这首作品，我们可以知道，修文馆诸学士此次饯别的宋司马正是同为修文馆学士的宋之问的弟弟。此时，宋之问被召入修文馆，其弟又被任为许州司马，二人仕途顺意，宦途显达。诗的首联就是作者针对此时宋姓兄弟显赫的政治地位进行的称赞。除了首联之外，还有此诗的尾联足以称道。除这两联外，其他几句都表现平平，属于宫廷派离别诗的范式作品，乏善可陈。诗的尾联言作者自友人离别后，对友人日夜思念的郁郁感情。“颖水”流经唐代许州，故言“劳望颖川星”。', '708年秋，修文馆学士饯别许州宋司马。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (250, '1a91581058a64732859f168a6c60974b', '饯许州宋司马赴任', 22, 0, 708, '708年秋', '颍川开郡邑，角宿分躔野。君非仲举才，谁是题舆者。悯悯琴上鹤，萧萧路傍马。严程若可留，别袂希再把。', '颍川开郡邑，角宿分躔野。君非仲举才，谁是题舆者。<br>悯悯琴上鹤，萧萧路傍马。严程若可留，别袂希再把。', '唐中宗景龙二年秋，修文馆诸学士饯别许州宋司马，因有此作。 全诗根据内容可分为三部分。第一部分是诗作的首联，点明宋司马赴任的许州。“颖水”流经唐代许州，故言“颖川”代指许州。“角宿”当是天上的二十八星宿，对应地面上的许州界。第二部分为颔联，则是对宋司马工作能力的肯定。颔联借助东汉陈蕃典故，赞颂宋司马卓越的工作能力。第三部分则是诗作的最后两联，简要描写饯别场景，并且最终表达对友人的依依不舍的感情。“严程”一词是说古代赴任或者贬谪，都有严格的到任期限，所以时间短暂的情况下，更能见朋友间的感情深切。', '708年秋，修文馆学士饯别许州宋司马。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (251, '142d93ba73a642dab39729494bb1a86d', '饯许州宋司马赴任', 25, 3, 708, '708年秋', '旧许星车转，神京祖帐开。断烟伤别望，零雨送离杯。辞燕依空绕，宾鸿入听哀。分襟与秋气，日夕共悲哉。', '旧许星车转，神京祖帐开。断烟伤别望，零雨送离杯。辞燕依空绕，宾鸿入听哀。分襟与秋气，日夕共悲哉。', '唐中宗景龙二年秋，修文馆诸学士饯别许州宋司马，因有此作。 景龙二年秋季的这场送别，修文馆的学士们包括宋之问在内创作了大约7篇诗作。其中，徐坚的这篇作品颇有特色。全诗以时间为序，真实记录了整场饯别欢送仪式。不仅如此，更为出彩之处在于，徐坚真实记录的不是修文馆学士们对宋司马即将赴任的依依不舍之情，而是通过记录当日的自然景色为整场饯别欢送仪式着上伤感忧郁的色彩。首联言“星车”是指古代时候皇帝使者所乘坐的车辆，“祖帐”是指古代郊外送别时搭设的帐幕。这两句是全诗的开头，也是整场饯别的开场。颔联一下真实记录饯别仪式开始时，天气及自然景观。“断烟”“零雨”言天气较差，就是在这样的天气状况下，作者将郁郁离愁杂糅其中。“辞燕”当写雨后，燕子地空翱翔的景象；“宾鸿”是言秋日，鸿雁的阵阵哀鸣，令人神伤。诗作的尾联，作者将自己对于友人即将离去的离愁与秋天的景象相联系，这样做的好处在于，读者通过体会秋日的萧瑟景象就可以遥想当是灞水边友人相送的郁郁离愁了。用思巧妙。', '708年秋，修文馆学士饯别许州宋司马。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (252, '3f2f6db0a0b64a9f9df8db617787433c', '送萧颖士赴东府，得还字', 123, 3, 753, '753年', '夫子高世迹，时人不可攀。今予亦云幸，谬得承温颜。良策资入幕，遂行从近关。青春灞亭别，此去何时还。', '夫子高世迹，时人不可攀。今予亦云幸，谬得承温颜。良策资入幕，遂行从近关。青春灞亭别，此去何时还。', '唐玄宗天宝年间，萧颖士任集贤校理。因不肯依附李林甫，受其排斥，天宝十二年时出为河南参军。离京时，门人相送，唱和作诗，此诗即为当时所作。全诗表达两方面情感。一是赞颂萧夫子品德学识令人赞叹，并因此感恩萧夫子对自己的垂青与教导之恩。二是表达对此次萧颖士出为河南参军的支持，并表达对此次离别无定期的迷茫与伤感之情。', '天宝十二年，萧颖士调河南参军，门人有诗相赠。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (253, 'ddc7fbff5a6a4cf88e3e08ad518c0abe', '长乐坡送人，赋得愁字', 12, 2, 807, '807年', '行人南北分征路，流水东西接御沟。终日坡前恨离别，谩名长乐是长愁。', '行人南北分征路，流水东西接御沟。终日坡前恨离别，谩名长乐是长愁。', '唐宪宗元和二年，白居易居长安，于长乐坡送别友人，因有此作。据《长安志》记载，京兆万年县东15里的长乐坡下有驿站，因东临浐水，又称长乐水馆。东出长安的游人，官宦多在此送别。大和三年，白居易称病归东都洛阳，张籍、刘禹锡等人即是相送于长乐坡，有诗可证。此诗的首句化用《白头吟》中“今日斗酒会，明旦沟水头。躞蹀御沟上，沟水东西流。”言作者与友人情深意浓，此刻分别不知何时再会，当为东西流水，很难相逢了。“终日”一句言分离之痛苦。面临分别，作者感叹此处的不能称为长乐坡，应该称之为长愁坡，以示离别的痛苦。全诗通篇借助作者送别时的感受，写尽作者送别友人时心情的沉重。', '元和二年送友作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (254, 'a78e9b06b25e41b1beffbf5126ac61c3', '往年稠桑曾丧白马题诗厅壁今来尚存又复感怀更题绝句', 12, 19, 812, '812年', '路傍埋骨蒿草合，壁上题诗尘藓生。马死七年犹怅望，自知无乃太多情。', '路傍埋骨蒿草合，壁上题诗尘藓生。马死七年犹怅望，自知无乃太多情。', '唐宪宗元和七年，白居易仍分司东都，任太子宾客。是年，赴下邽，途经稠桑驿，见元和元年题壁，因有此作。元和元年，白居易出使洛阳，途经稠桑驿，所乘白马死，因作诗题壁稠桑驿。7年之后，再度路过稠桑驿的白居易，目睹题壁，感伤之情油然而生。诗的首句言时过境迁，当年埋骨处与题壁处已面目全非。下句抒怀，表现自己七年之后依然悲伤不已的心情。全篇字句平和，无可称赞处。吸引人的只有作者对于白马的热爱惋惜之情。此诗可与作于唐宪宗元和元年的作品比照来看。', '元和九年，自洛阳赴下邽，途经稠桑，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (255, '068051fa3d8346b384c70bc10b277d4c', '博异志之沈恭礼', 124, 18, -1, '年份未详', '阌乡主簿沈恭礼夜宿湖城驿，有鬼李忠义索食物和帽子，并帮助沈降服妖物数个。沈设酒筵款待并赠帽，忠义劝诫其防鬼之术。', '阌乡主簿沈恭礼夜宿湖城驿，有鬼李忠义索食物和帽子，并帮助沈降服妖物数个。沈设酒筵款待并赠帽，忠义劝诫其防鬼之术。', '沈恭礼形象反映了京汴道人唐人的“漂泊意识”和“漂泊母题”', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (256, '2f5774c7857c4bf3b31e173b134b94b0', '博异志之李全质', 124, 41, -1, '年份未详', '陇西李全质曾有恩于紫衣客，客云全质平生将罹水厄。太和初全质为太平军裨将，于百歇桥遇水，得紫衣客救拔。开成初宿寿安，因公务需雨夜行，紫衣客自称邮牒引全质抵三泉驿。访之已不见矣。', '陇西李全质曾有恩于紫衣客，客云全质平生将罹水厄。太和初全质为太平军裨将，于百歇桥遇水，得紫衣客救拔。开成初宿寿安，因公务需雨夜行，紫衣客自称邮牒引全质抵三泉驿。访之已不见矣。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (257, '9587be0e9b904e13bb141804022b6f41', '续定命录之李行修', 125, 19, -1, '年份未详', '谏议大夫李行修夫人仙逝，其岳父欲将其幼女嫁于行修，行修坚拒。后任东台御史，夜宿稠桑驿，在王老帮助下得见其夫人魂魄。夫人亲奉皂荚汤，并将小妹托付于行修。行修后依言取之。', '谏议大夫李行修夫人仙逝，其岳父欲将其幼女嫁于行修，行修坚拒。后任东台御史，夜宿稠桑驿，在王老帮助下得见其夫人魂魄。夫人亲奉皂荚汤，并将小妹托付于行修。行修后依言取之。', '李行修志诚令人感动，其入稠桑驿边稠桑店反映唐时的馆驿入住制度为先到者先住', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (258, '75a83b181934443288c80c91fe781c8e', '与裴华州同过敷水戏赠', 12, 13, 835, '835年', '使君五马且踟蹰，马上能听绝句无。每过桑间试留意，何妨后代有罗敷。', '使君五马且踟蹰，马上能听绝句无。每过桑间试留意，何妨后代有罗敷。', '唐宪宗元和九年，白居易仍分司东都，任太子宾客。是年，同友人赴下邽，途经敷水，因有此作。白居易自太和元年，征为秘书监以来，三次途经敷水，并留下四首作品，其中多借秦罗敷传说歌咏此地清幽脱俗的景致。此诗在继承前作诗歌创作传统的基础上，更胜一筹。试看：作为一首唱和诗作，诗的开头一语双关，“使君踟蹰”一句既借《陌上桑》“使君从南来，五马立踟蹰”引出秦罗敷的典故；又引友人关注，作为唱和诗的开场。“每过”一句言此地风景绝好，似有罗敷后人居住，需处处留意才是。此句虽词语简单，语意浅显，实则透露着此刻白居易闲居洛阳时安逸平和的心态。此诗可与同时期的作品《华州西》比照来看：《华州西》写于白居易返洛途中，即将开始闲适生活之前，诗中的作者似一位满身泥尘久经官场的官员；而此诗不同，“试”、“何妨”就写尽作者闲来无事，相试解闷的意趣纵横，而此时的白居易倒象是一位农村闲居的老翁了。', '元和九年，自洛阳赴下邽，途经敷水，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (259, 'e55bd6f1e0b5441db89a6e9d5d7fe9da', '罗敷水', 64, 13, 858, '858年', '雉声角角野田春，试驻征车问水滨。数树枯桑虽不语，思量应合识秦人。', '雉声角角野田春，试驻征车问水滨。数树枯桑虽不语，思量应合识秦人。', '罗敷水，作为汴京至长安道中重要驿站——敷水驿的周边景点，多次被往来这条道路的诗人所记录。一生中为科考奔波，多次往来这条道中的罗隐亦记录下这一重要景观。从诗作中“试驻征车问水滨”一句可以得知，此时作者应是初次经过罗敷水，又因为“征车”一般指远行的车辆。所以，罗隐初次远行，途经汴京至长安道的应是唐宣宗大中十二年的应举之行。通过对罗隐诗作的风格也可以得出这一结论：罗隐一生“十年不第”，未能考取功名对罗隐的诗风影响较大，主要表现在诗中“萧萧”“伤残”“衰”等带有破败意味的词的出现，与本诗大有不同，由此也可推断本诗应为罗隐早年所作。诗的首句借助野鸡的鸣叫声营造出野外田家干净脱俗的氛围：作者行至敷水，因受野鸡鸣叫声的吸引，于是停车询问此为何处。诗的后一句写无人应答，作者暗暗思忖此地应与秦罗敷有关系。作者虽然初次到达罗敷水，但是罗敷水作为一种文学景观常出现于先于罗隐的唐诗人笔下，所以罗隐很可能通过常识在无人应答的情况下作出推断，也有可能是有人应答，作者不曾写入诗作。无论哪种情况，都不会影响此诗为罗隐初期诗作这一推断。', '罗隐多次来往长安至汴京道中，每次来往都可能经过罗敷水。从此诗中询问水滨之人可以看出，此诗记载的应是作者初次经过罗敷水的经过。据其年谱，罗隐初次入京，当是唐玄宗大中十二年，初次应举之时。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (260, '2893233cd689476ab6783178b3e54364', '人日新安道中见梅花', 64, 28, 858, '858年', '长途酒醒腊春寒，嫩蕊香英扑马鞍。不上寿阳公主面，怜君开得却无端。', '长途酒醒腊春寒，嫩蕊香英扑马鞍。不上寿阳公主面，怜君开得却无端。', '南朝宋武帝寿阳公主“梅花妆”典运用传神', '洛赴长安过新安作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (261, '31f6c0530b1c479891bbb19608978640', '途经敷水', 13, 13, -1, '年份未详', '修蛾颦翠倚柔桑，遥谢春风白面郎。五夜有情随暮雨，百年无节待秋霜。重寻绣带朱藤合，更认罗裙碧草长。何处野花何处水，下峰流出一渠香。', '修蛾颦翠倚柔桑，遥谢春风白面郎。五夜有情随暮雨，百年无节待秋霜。重寻绣带朱藤合，更认罗裙碧草长。何处野花何处水，下峰流出一渠香。', '此诗具体创作时间无考。根据作品内容，当是作者途经敷水驿，见敷水清丽，有感而作。敷水驿是华州境内的一座驿站，周围有罗敷水，因水流清澈，环境优美，似美女秦罗敷一般，因此称为“罗敷水”。此作也是作者根据传闻将敷水比拟作秦罗敷进行描写的。首联两句中“修蛾颦翠”看似在写树枝在风中曼妙的飘舞姿态，实则很容易令人联想到女子的蛾眉团簇的姣好面容。这是作者将敷水周围的环境比作秦罗敷进行描写的证据之一。颔联两句又对敷水周边的景色作了一番描写。颈联两句则是作者将敷水周围的环境比作秦罗敷进行描写的证据之二：首句先写周围的花藤，又写周围的绿草。其中写道的“罗裙”就是从汉乐府《陌上桑》中对秦罗敷服饰描写化用而来的。作品的尾联写敷水的面貌。全诗先言敷水周围的美丽清香的环境，在最后一句中带出水流中的香味，衬托水流之美，符合逻辑。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (262, 'c4a5fb9eb5ef46f59c22f11299e3a2e8', '送贺宾客归越', 5, 6, 744, '744年', '镜湖流水漾清波，狂客归舟逸兴多。山阴道士如相见，应写黄庭换白鹅。', '镜湖流水漾清波，狂客归舟逸兴多。山阴道士如相见，应写黄庭换白鹅。', '唐玄宗天宝三年，贺知章告老还乡，上疏请为道士。李白相送，因有此作。题目中“宾客”应指贺知章曾任太子宾客一职。此诗与作者的另一篇送别贺知章的作品相比，更通俗易懂一点，试看。开头两句中“镜湖”一词是指贺知章的老家会稽山下的湖泊，“狂客”一词因为贺知章曾称自己为“四明狂客”，故有此言。开头两句是写，贺知章即将辞官回家，回家之后没事务的干扰，贺知章的心情也当是飘逸自在的，如同镜湖的湖水一般。诗的最后两句用东晋时道士养鹅与王羲之交换手写《黄庭经》的典故，暗指此次回家后，贺知章有心求道的事情。全诗四句，没有写作者在友人离别后如何想，如何思，也没有写作者在友人离别之后，如何泪下沾襟，如何泪流不止。读者仅仅通过开头两句作者幻想友人回家后的安逸心情，就可推知作者同贺知章的情感深厚，并继而感受出此次离别，作者对友人的思念之情。四句话没有一句写思，却又是处处写思，这正是此诗的妙处。', '744年春，玄宗命群臣于长乐坡送贺知章归越，赋诗命群臣赓和。李白于长乐坡送别后，又亲送至阴盘驿（新丰驿）', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (263, '9f32066c94a34ee3bd9a7e32e7f866b3', '送贺监归四明应制', 103, 2, 744, '744年', '挂冠知止足，岂独汉疏贤。入道求真侣，辞恩访列仙。睿文含日月，宸翰动云烟。鹤驾吴乡远，遥遥南斗边。', '挂冠知止足，岂独汉疏贤。入道求真侣，辞恩访列仙。<br><br>睿文含日月，宸翰动云烟。鹤驾吴乡远，遥遥南斗边。', '唐玄宗天宝三年，贺知章告老还乡，上疏请为道士。李林甫相送，故有此诗。诗的首联写贺知章辞官回家事。颔联写贺知章有心学道，心向仙班。“真侣”为道教名词，道教称本原、本性为真。颈联中“睿文”指贺知章的文笔蕴含智慧；“宸翰”是指帝王的书写富含真情。这里是指贺知章告老还乡时，唐玄宗曾亲赐《送贺知章归四明》的作品，因此，李林甫也写了这篇作品。尾联中“鹤驾”一句是写贺知章回家修道，归乡隐居事。古人认为地面上的地点对应天上的星斗，所以称贺知章的家乡越地为天上的星宿南斗。这篇作品仅仅是应皇帝要求而创作，诗中很难见到作者同贺知章在私下里的感情。没有独到的感情支撑，这篇作品也写得平平无奇，除了典故的使用引人耳目外，再没有其他吸引人的地方。', '744年春，玄宗命群臣送贺知章归越，赋诗命群臣赓和。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (264, 'f5adb3a79f764b5f961668f7bbab83ff', '昭应官舍', 85, 0, 813, '813年', '绕厅春草合，知道县家闲。行见雨遮院，卧看人上山。避风新浴后，请假未醒间。朝客轻卑吏，从他不往还。', '绕厅春草合，知道县家闲。行见雨遮院，卧看人上山。避风新浴后，请假未醒间。朝客轻卑吏，从他不往还。', '唐宪宗元和八年，王建任昭应县丞，因有此作。唐朝时，县官职位较低，官务闲散，官舍清闲。也正因为如此，被那些在朝为官的官职较高的人所轻视。作品的起首两句先言作者官舍环境：官舍厅中生有春草，从一个侧面凸显县官官职清闲，无人来访，所以地面常生春草。颔联两句从作者日常活动着笔描写作者为官之闲散：县官无人看重，也无人管理，更无人办事，所以作为县官的作者在馆舍内常优游自在，下雨时听雨，雨霁后看山，描绘一种闲适自在的生活方式。颈联两句承接颔联句意，点明虽为县官，却无人管理，所以可以经常享受这样闲适自在的生活。社会之中，为官者有忙有闲，忙者势必嫉妒怨恨闲者。作者在作品的最后一联即点出这一官场矛盾，并表明自己的立场及态度“从他不往还”，是说任凭朝官嫉妒，我作为闲职县官就是不愿意同这些嫉妒心强的人同流合污，郊游酬唱，表现出作者不愿趋炎附势，苟合逢迎的人生态度。', '元和八年，王建任昭应县丞', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (265, 'a5e86258837b41bfbd9a1ddd2de277c7', '昭应李郎中见贻佳作次韵奉酬', 85, 0, 813, '813年', '窗户风凉四面开，陶公爱晚上高台。中庭不热青山入，野水初晴白鸟来。精思道心缘境熟，粗疏文字见诗回。诸生围绕新篇读，玉阙仙官少此才。', '窗户风凉四面开，陶公爱晚上高台。中庭不热青山入，野水初晴白鸟来。精思道心缘境熟，粗疏文字见诗回。诸生围绕新篇读，玉阙仙官少此才。', '唐宪宗元和八年，王建任昭应县丞，因有此作。唐朝时，县官职位较低，官务闲散，官舍清闲。也正因为如此，王建在昭应为官时，创作了一系列表现自己清闲生活的作品，此诗亦属此类。在作品的开头两句中，作者将自己同陶渊明相比，暗示自己为官的生活就如陶渊明隐居时的生活一样优游自在。颔联两句对县官官舍的自然环境描绘一番：中庭清爽能见青山，舍外有水可嬉白鸟，一片闲适优美的生活图景。颈联两句着笔对作者为官生活内容作一番描写：既可以修行道法，又可以玩弄笔墨，一种自由自在的生活方式映然纸上。作品的最后一联以作者对自己的夸赞结束全篇。在这一联中，作者承接上联句意，写自己能够在这种闲适的生活环境中创作新的作品，因此夸赞自己是玉阙仙宫中少有的仙才，这样一种夸赞可以看作是作者对这种闲适自在生活喜悦之情的另类表现。', '元和八年，王建任昭应县丞', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (266, '425a3d8432f243458fa22f5164ac1ae7', '归昭应留别城中', 85, 57, 813, '813年', '喜得近京城，官卑意亦荣。并床欢未定，离室思还生。计拙偷闲住，经过买日行。如无自来分，一驿是遥程。', '喜得近京城，官卑意亦荣。并床欢未定，离室思还生。计拙偷闲住，经过买日行。如无自来分，一驿是遥程。', '唐宪宗元和八年，王建任昭应县丞，因有此作。作品的开头两句先点明此诗创作时间：此诗应该创作于作者担任昭应县官，有机会进京之时。次句写虽然县官在唐代算得上是最小的官职，但身为京畿地区的县官总要比地方的县官，荣誉感强烈一些，所以作者说自己此次进京的心理是极具荣誉感的。颔联两句中，作者提及此次进京的事由，应该是应久别的老友张籍的邀请。所以在颔联中，作者对老友重逢，以及暂聚又别的场景作了简单描述，虽然只有两句但朋友之间的情谊已经在语言之外得以表达了。颈联两句怀念朋友间往日的交游生活，对往日生活的怀念，从一方面也是朋友间深厚情谊的体现。作品的最后一联写朋友惜别的场景，虽然长安至昭应，仅仅两个驿站之隔，却已觉得如在天边那么远，足以看得出作者同友人的深厚友谊。', '元和八年，王建任昭应县丞', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (267, '1271c1b2eaac46c598de52fdae6f0794', '长乐驿留别卢象裴总', 126, 2, -1, '年份未详', '朝来已握手，宿别更伤心。灞水行人渡，商山驿路深。故情君且足，谪宦我难任。直道皆如此，谁能泪满襟。', '朝来已握手，宿别更伤心。灞水行人渡，商山驿路深。故情君且足，谪宦我难任。直道皆如此，谁能泪满襟。', '此诗具体创作时间无考，根据作品内容，当是作者贬谪外地，友人相送时有感而作。作品的起首两句点明离别场景：先言“朝”后言“宿”，是说送别时间之长，借助送别时间之长，突出友人同作者深厚真挚的情感。颔联两句以工稳对仗的形式点明送别的地点以及自己即将抵达的目的地。“驿路深”一句突出商山附近山谷重峻的险要地势同时，还体现出作者去往路途之遥远，体力之艰辛。颈联两句是作者自白句：先言对友人送别设宴的感谢之情，次句又言自己对贬谪的态度是非常排斥与拒绝的，“难任”两字显示出作者在贬谪经历面前的委屈与痛苦。之所以面对贬谪感到委屈与痛苦，除了贬所地处偏僻，山林险恶的环境即将给作者在心理与生理上造成的莫大摧残外，作者还在最后一联中说出自己因直言敢谏，受他人排挤，导致无奈被贬的经历，这无疑是造成作者委屈情绪的主要原因。贬谪经历时古代士人能够遭受的较为严重的惩罚措施，尤其是遭人排挤之后获得如此惩罚，作者的失意与委屈之情可以想见。但是作者高超地将这种失意与委屈情感寓于“谁能”、“难任”两个词之中，委婉细腻的同时令人读来深有感触，如感同身受一般。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (268, 'c51734ea863e4d06a5a16e5f77edf0c6', '和李中丞醉中期王征君月夜同游浐水旧居', 93, 0, -1, '年份未详', '醉后情俱远，难忘素浐间。照花深处月，当户旧时山。事想同清话，欢期一破颜。风流还爱竹，此夜尚思闲。', '醉后情俱远，难忘素浐间。照花深处月，当户旧时山。事想同清话，欢期一破颜。风流还爱竹，此夜尚思闲。', '此诗具体创作时间无考。根据作品内容，当是作者同友人游玩，有感而作。作品的题目中写“醉中”，作品的开头两句就写作者同友人同游浐水旧居的原因点题：因为同友人饮酒，酒后兴致高迈，于是产生出游玩浐水旧居的想法。颔联两句对浐水旧居的自然环境作了一番描绘：山谷之中，本已清净，又写朗月当空，映照山中野花，凸显出浐水旧居明净雅致的优美环境。颈联两句从环境描写中跳脱出来，转写作者同友人的情谊：作者同友人在宴会之中，俱话当年友谊，共忆当年交游往事。一时间觥筹交错，推杯换盏，乐趣融融，表现出作者同友人的深厚友谊。作品的最后一联，作者借助酒兴，表白自己心迹。可以看出全作从醉中写起，写及浐水旧居的优美景致，又写到同友人的深厚友谊，最后受此影响表白自己向往隐逸生活的心迹。景中有情，情自景生，情景交融之间，又描绘出作者同友人觥筹交错的欢乐场面，可谓诗思甚巧。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (269, '3788e26534f94e129dfe3fcf5e67eb55', '新丰主人', 50, 6, 745, '745年', '明代少知己，夜光频暗投。迍邅终薄命，动息尽穷愁。自欲归飞鹢，当为不系舟。双垂素丝泪，几弊皂貂裘。暮鸟栖幽树，孤云出旧丘。蛩悲衣褐夕，雨暗转蓬秋。客里冯谖剑，歌中甯戚牛。主人能纵酒，一醉且忘忧。', '明代少知己，夜光频暗投。迍邅终薄命，动息尽穷愁。自欲归飞鹢，当为不系舟。双垂素丝泪，几弊皂貂裘。暮鸟栖幽树，孤云出旧丘。蛩悲衣褐夕，雨暗转蓬秋。客里冯谖剑，歌中甯戚牛。主人能纵酒，一醉且忘忧。', '唐玄宗天宝四年，钱起入长安赴试求官，未果，客游陇右，因有此作。全诗写“忧”，这种“忧”可能来自作者长期在外，客游应试的生活；也有可能是多年应试，屡次无果的残酷现实所致。“明代”句写作者对新丰主人为自己提供住宿的感谢之情。正是作者应试无果的人生黑暗期，主人为作者提供了衣食酒肉以及住宿的地方，就如雪中送炭一般，为失望中的作者重燃希望。“迍邅”至“蛰悲”句主要是作者叙述自己在外客游，应试无果的这段悲惨经历以及这段经历给自己在身心上带来的折磨：这几句先谴责命运的捉弄，使自己穷愁潦倒；又表白自己愿意像画在轻舟上的水鸟一般无拘无束；接下来摹状自己穷愁潦倒的样子；最后作者又以暗色调幽微景色烘托自己应试无果的悲惨境遇，令人读来生恨。作品的最后四句，作者想到主人为自己提供酒肉衣食这些事情，自觉万事还是有希望的，所以用冯谖，甯戚牛的典故勉励自己，认为自己虽然此时潦倒穷愁，但以后一定能成就一番大业，实现自己的人生理想。总的来说，全诗无论是在表现自己苦痛经历还是表白自己内心心迹的时候，都呈现出婉转蕴藉的形式，令全诗可读性较强。', '唐玄宗天宝四年，钱起入长安赴试求官，未果，客游陇右。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (270, 'dabd01177526452f9140bd784b66c0b2', '和銮舆顿戏下（一作追从銮舆夕顿戏下应令）', 127, 7, 627, '627年', '重轮依紫极，前耀奉丹霄。天经恋宸扆，帝命扈仙镳。乘星开鹤禁，带月下虹桥。银书含晓色，金辂转晨飙。雾澈轩营近，尘暗苑城遥。莲花分秀萼，竹箭下惊潮。抚己惭龙干，承恩集凤条。瑶山盛风乐，抽简荐徒谣。', '重轮依紫极，前耀奉丹霄。天经恋宸扆，帝命扈仙镳。<br>乘星开鹤禁，带月下虹桥。银书含晓色，金辂转晨飙。<br>雾澈轩营近，尘暗苑城遥。莲花分秀萼，竹箭下惊潮。<br>抚己惭龙干，承恩集凤条。瑶山盛风乐，抽简荐徒谣。', '无', '扈从太宗至戏水作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (271, '96dd25b2070b4c51b7d6b6d976057ddd', '晚过横灞寄张蓝田', 50, 3, 755, '755年', '乱水东流落照时，黄花满径客行迟。林端忽见南山色，马上还吟陶令诗。', '乱水东流落照时，黄花满径客行迟。林端忽见南山色，马上还吟陶令诗。', '唐玄宗天宝十四年，钱起任蓝田县尉，此诗或作于此时。题目中“横灞”是指横跨灞河河面上的拱桥，所以此作描写景色应是作者横跨河面所见。秋日夕阳之时，作者出长安横跨灞河，见夕阳下的水流向东流去，夕阳将水流映照得熠熠生辉，光彩动人；待过河之后，作者又见路径上又有农人手植的菊花正式盛开的季节，菊花的黄色同夕阳的黄色，相互辉映，共同组合成为一幅绝美的落日图景。作品的后两句又写作者远望所见。作者透过树梢，远望南边，见终南山依稀可见。前有菊花满径，后又抬头见南山，此情此景令作者不禁吟起陶潜名句“采菊东篱下，悠然见南山”。全诗虽然只是对作者途中所见进行简单的勾勒描绘，但深得陶潜诗作之悠然意境。', '唐玄宗天宝十四年，钱起任蓝田县尉，此诗或作于此时。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (272, '51dd9e0e89764cba8432b66062c0a905', '赋得灞岸柳留辞郑员外', 128, 3, -1, '年份未详', '杨柳含烟灞岸春，年年攀折为行人。好风倘借低枝便，莫遣青丝扫路尘。', '杨柳含烟灞岸春，年年攀折为行人。好风倘借低枝便，莫遣青丝扫路尘。', '此诗具体创作时间无考。根据作品题目，当是作者在灞水边，饯别友人所作。此诗题目虽言饯别，但作者不写饯别场景，却另辟蹊径，选择具有饯别意蕴的灞岸柳枝意象进行描写，使全作含蓄内敛，婉转多情。有唐一代，唐人因为柳树之“柳”与“留”谐音，喜爱在送别时折断柳枝，送与友人，以示对友人的期望以及不舍之情，所以柳枝在唐诗中也就成为了具有离别含义的意象。又因为唐人喜爱选择灞岸作为送别地点，所以“灞岸折柳”就成为了送别的固定意象以及写及送别必须要写的内容。此作中作者正是使用“灞岸折柳”的意象内涵的基础上，才将作品写得如此含蓄。作品前两句描绘灞岸春日景象起兴，杨柳含烟桃花点红，正是春日最有色彩的时节。最后两句写作者的殷切请求，请求好风不要吹拂柳枝，扫去行人的足迹行踪，使自己无从想念。表现自己对友人的不舍之情，通过请求的方式表达出来，婉转之至。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (273, '2f2facbea3e848df88d4064b4d5d472a', '洛州萧司兵谒兄还赴洛成礼', 23, 3, -1, '年份未详', '棠棣日光辉，高襟应序归。来成鸿雁聚，去作凤凰飞。细草承轻传，惊花惨别衣。灞亭春有酒，岐路惜芬菲。', '棠棣日光辉，高襟应序归。来成鸿雁聚，去作凤凰飞。细草承轻传，惊花惨别衣。灞亭春有酒，岐路惜芬菲。', '此诗具体创作时间无考，根据作品题目，当是作者在长安送别友人时有感而作。题目中“成礼”在古代是指婚礼。首联以《诗经》中《棠棣》一篇起兴。《棠棣》主要讲述了兄弟之间相亲相爱的感情，用以起兴友人即将赴洛阳参加襟兄的婚礼，再合适不过。次句点明友人离京的原因是去洛阳参加兄弟的婚礼。颔联两句先以“鸿雁聚‘点明在洛阳友人与兄弟相聚，再写”凤凰飞“对即将成婚的两位新人送去自己的一份祝福。颈联两句写送别场景。以花草借代自己，以花草对离别的惊心感受婉转说出自己对友人离别的伤心与不舍之感受。尾联一句接着承接此意绪，接着说自己对友人的不舍之情。“芬菲”是芳草的代名词，自《招隐士》一篇后，古人作诗多用春草、芳草等意象婉转寄托别离情思。诗中虽然没有写与友人的情感，但作者对友人的真挚情感在开篇写及友人之事，后半篇又写作者同友人的别离愁绪中处处可见。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (274, '00619bdb32fe4c4fa86a3c3d0d04661b', '送崔十三东游', 50, 3, -1, '年份未详', '千里有同心，十年一会面。当杯缓筝柱，倏忽催离宴。丹凤城头噪晚鸦，行人马首夕阳斜。灞上春风留别袂，关东新月宿谁家。官柳依依两乡色，谁能此别不相忆。', '千里有同心，十年一会面。当杯缓筝柱，倏忽催离宴。丹凤城头噪晚鸦，行人马首夕阳斜。灞上春风留别袂，关东新月宿谁家。官柳依依两乡色，谁能此别不相忆。', '此诗具体创作时间无考，根据作品题目，当是作者送别友人，有感而作。作品的开篇两句写作者同友人的深厚情谊，即使是相隔千里，也能互相惦念。“十年一会面”写这场会面来之不易，更应颇为重视。接下来写离别宴会场景。在这两句中，作者希望借放缓宴会音乐的速度，延长自己同友人相聚的时间，体现出作者对这“十年一会面”的重视。“丹凤”两句主要对送别场景进行描绘，点明送别时间在晚上黄昏时节。晚鸦聒噪，给人一种不安之感；夕阳斜照，让人产生即将离别的失意之情。就在这样聒噪昏沉的意境之中，作者同友人立马相见，离别的失意不快情绪都在不言之中。“灞上”两句对仗极工，将此时送别的灞上场景与友人即将途经的关外场景相对仗，首句写作者对友人离别不舍而流泪，次句写作者遥想友人路途艰辛而为其担心警惕，这些都是作者与友人深厚情谊的表现。作品的最后两句接着承接此意绪，离别已然接近，分别即将开始，所以作者在最后一联中较为直白地表现出作对友人的不舍之情。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (275, '515d35b66b8e488f91739916facaf717', '送粲上人兼寄梁镇员外', 51, 3, -1, '年份未详', '季月还乡独未能，林行溪宿厌层冰。尺素欲传三署客，雪山愁送五天僧。连空朔气横秦苑。满目寒云隔灞陵。借问从来香积寺，何时携手更同登。', '季月还乡独未能，林行溪宿厌层冰。尺素欲传三署客，雪山愁送五天僧。连空朔气横秦苑。满目寒云隔灞陵。借问从来香积寺，何时携手更同登。', '此诗具体创作时间无考，根据作品题目，当是作者送别友人时，有感而作。根据作品内容，友人离京或是因为回家省亲，这一行为惹起作者的思乡之情，因此作者在首联首句为自己不能回家感到遗憾与感叹。颔联两句中用“尺素传书”典故，将要传书却难以传达，表现作者与友人离别之后再度相见的困难，就连别后寄去相思书信都难以实现，侧面凸显出此时正处于送别场景中的作者对友人的不舍之情。颈联两句描写送别场景。句中全用“朔气”、“寒云”这样带有消极情感意蕴的词汇描绘出寒冷愁郁的意境，侧面凸显出作者同友人即将分别时的忧愁。作者在尾联里借问友人何时再度同登香积寺，实际上是问友人何时回到长安，侧面表现出作者同友人分别后对友人的思念。全诗前半部分写景，烘托描绘意境；后半部分以问句结束全篇，虽然没有直书自己对友人的思念，但读者还是可以将全篇联系起来，抽丝剥茧般地分析作者隐藏在词句中的内敛感情。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (276, '10ca97903e11489885804d8c26125ff8', '送前东阳于明府由鄂渚归故林', 13, 3, -1, '年份未详', '结束征东换黑貂，灞西风雨正潇潇。茂陵久病书千卷，彭泽初归酒一瓢。帆背夕阳湓水阔，棹经沧海甑山遥。殷勤为谢南溪客，白首萤窗未见招。', '结束征东换黑貂，灞西风雨正潇潇。茂陵久病书千卷，彭泽初归酒一瓢。帆背夕阳湓水阔，棹经沧海甑山遥。殷勤为谢南溪客，白首萤窗未见招。', '此诗具体创作时间无考，根据作品题目，当是作者送别友人回家时，有感而作。作品的起首两句先点明友人离京回家的原因是结束战争后，回家省亲。呼应题目，起兴离思。再用风雨潇潇渲染送别场景之凄凉悲伤，突出作者送别友人的不舍与无奈情感。颔联两句用司马相如因病辞官，贫居茂陵以及陶渊明隐居南山的典故，点出友人此次离京不只是回家省亲，而是带有一些解甲归田的意味。英雄解甲，归田贫居，颇有一些寥落的意蕴。接下来两句是作者遥想友人回家途中可能遇见的景色，这一部分在唐人送别诗中多有出现。究其原因，大概是作者同情友人路途上所遭受的颠簸，所以借助遥想途中景色婉转地表现出来。作品的最后两句当是送别结束之时，作者对友人的感激语。从这两句中可以看出友人同作者之前有过很深的交情。首句写友人可能向上推举过作者，从次句“车胤囊萤”的典故可以看出作者年少苦学的态度，但终究未能被重用，尽管如此，作者还是感念友人推荐之恩。唐人送别诗中多有此部分，即在最后一联中吐露作者心声。这种心声不同于前三联的程式化，是最能代表作者内心的情感独白，也是同友人情感的真实见证。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (277, 'c4cbf0cbf3dd467e820db761a7362720', '送卢缄归扬州', 90, 3, -1, '年份未详', '曾向雷塘静掩扉，荀家灯火有余辉。关河日暮望空极，杨柳渡头人独归。隋苑荒台风袅袅，灞陵残雨梦依依。今年春色还相误，为我江边谢钓矶。', '曾向雷塘静掩扉，荀家灯火有余辉。关河日暮望空极，杨柳渡头人独归。隋苑荒台风袅袅，灞陵残雨梦依依。今年春色还相误，为我江边谢钓矶。', '此诗具体创作时间无考，根据作品题目，当是作者送别友人归扬州时，有感而作。首联中“雷塘”当指扬州地名。“荀家”是作者借助东汉显赫一时的荀氏家族指代唐时声名在外的范阳卢氏一族。在这两句中，作者回忆友人曾在扬州度过的闲适的生活，点明全诗创作的主题，言及友人将再次回到扬州。颔联两句以意象的堆叠，描绘出作者独立江边，远眺离人的凄哀图景：首句言作者独立远望，其中“极”字最好，凸显出作者与友人的情感之深；次句写作者失落独归。此处“失落”的原因有两重，一是感友人离去而失落。二是望友人不见而失落。仅此一句，其中蕴含的情感就已经超越大部分唐人送别作品。颈联两句是自唐以来送别作品中频出的一句“程式化”语句，以对仗的形式分别描写友人即将抵达的目的地之景色以及送别地点之景色，其作用无非是借景色的对仗，写未行之人对远行之人的想念与思忆，凸显出送别双方的深厚情感。说起离别诗的“程式化”，本诗的尾联部分也难逃“程式”的命运。离别诗的尾联一般包含着作者对友人的诸多情感，这种情感一般是根据离人的离开原因不同而呈现差异，反过来说，离别诗的尾联一般可以推测离人的离开原因，对推测离人的身份具有重要意义。在这首诗中，作者为不能随友人同往扬州表示自己的歉意，从中可以看出这位友人有可能是作者的年少好友或是同乡之人。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (278, 'f79b6d28c8de4ae581ea38d0cebf83bf', '送李溟谒宥州李权使君', 68, 3, -1, '年份未详', '英雄典宥州，迢递苦吟游。风宿骊山下，月斜灞水流。去时初落叶，回日定非秋。太守携才子，看鹏百尺楼。', '英雄典宥州，迢递苦吟游。风宿骊山下，月斜灞水流。去时初落叶，回日定非秋。太守携才子，看鹏百尺楼。', '此诗具体创作时间无考，根据作品题目，当是作者送别友人，干谒宥州使君所作。作品的开头首联先称即将干谒宥州使君的友人为“英雄”，是对友人的夸赞之词，预祝友人干谒成功，实现自己的人生价值及理想。次句对友人近些年为谋求官职付出的努力表示同情与安慰。颔联两句以对仗的方式写出送别的灞上场景以及友人即将途经的骊山场景，寄托作者对友人的不舍以及担心，颈联两句在点明送别时间为秋季的同时，又遥想友人归来的时间，侧面反映出作者对友人即将离别的不舍以及对友人归来之日的殷切盼望。尾联两句以“才子”之名称誉友人，从夸赞之中显示出作者对友人未来的信心。有唐一代，送别题材的作品多有创作。创作数量之多，难免会出现程式化现象，此作也不例外。以此作为例，首联点明送别缘由，颔、颈两联写友人途经之地寄托离愁别绪，尾联寄托作者对友人的期望与嘱托。虽然诸多作品在内容上略有区别，但大多逃不开如此俗套。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (279, '34612cbfee2b434687460f881112e274', '送友下第游雁门', 129, 3, -1, '年份未详', '相别灞水湄，夹水柳依依。我愿醉如死，不见君去时。所诣星斗北，直行到犹迟。况复挈空囊，求人悲路岐。北门记室贤，爱我学古诗。待君如待我，此事固不疑。雁门春色外，四月雁未归。主人拂金台，延客夜开扉。舒君郁郁怀，饮彼白玉卮。若不化女子，功名岂无期。', '相别灞水湄，夹水柳依依。我愿醉如死，不见君去时。所诣星斗北，直行到犹迟。况复挈空囊，求人悲路岐。北门记室贤，爱我学古诗。待君如待我，此事固不疑。雁门春色外，四月雁未归。主人拂金台，延客夜开扉。舒君郁郁怀，饮彼白玉卮。若不化女子，功名岂无期。', '此诗具体创作时间无考，根据作品题目，当是作者送别下第友人，有感而作。作品开头先描绘杨柳依依，繁华锦簇的仲春灞岸景色，以杨柳随风舞动之依依景色起兴作者面对友人即将离京远行的不舍之情。这种不舍情感本质上是作者与友人深厚友谊的体现，这种深厚友谊首先以颇为直白的形式体现在“我愿”两句，作者直言对友人的不舍，令读者在惊异作者抒情之直接的同时，又感叹作者同友人深厚的情感。在“所诣”以下四句中，作者采用离别诗中常用的设想的方式，通过设想离人即将面对的路途遥远，陌生人事等离开熟悉环境后可能遇见的困难，突出送行人对离人的担心，借以表现送行人与离人的感情。在这几句中，作者先写友人目的地之遥远，后写友人贫苦寥落，即使是抵达目的地也要暂时度过一段颇为悲苦的生活。从作者设想之细密，可以看出作者与友人是拥有很深厚的感情的。接下来几句是作者对友人的嘱托，简单明了，平白无奇像是会发生在自己身上的嘱托一样，拉近了读者及作品的距离，令作者更容易为读者接受。接下来几句亦是设想法，设想友人抵达雁门之后会经历的歌舞酒宴场面，就在在宴会上，酒酣耳热之时，作者还惦念友人因落第而产生的失意情绪，不忘在最后两句安慰勉励友人，作者与友人的深厚友谊可见一斑。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (280, '52403c99009d4c69b832382e43f86acc', '送友人擢第东归', 129, 3, 850, '850年', '同家楚天南，相识秦云西。古来悬弧义，岂顾子与妻。携手践名场，正遇公道开。君荣我虽黜，感恩同所怀。有马不复羸，有奴不复饥。灞岸秋草绿，却是还家时。青门一瓢空，分手去迟迟。期君辙未平，我车继东归。', '同家楚天南，相识秦云西。古来悬弧义，岂顾子与妻。携手践名场，正遇公道开。君荣我虽黜，感恩同所怀。有马不复羸，有奴不复饥。灞岸秋草绿，却是还家时。青门一瓢空，分手去迟迟。期君辙未平，我车继东归。', '刘驾同曹邺友善。唐宣宗大中四年，曹邺先及第东归，刘驾则在长安应举准备下一年的考试，此作或作于此时。“同家”两句以作者及友人的相识写二者之缘分：二人同为楚地之人，又在长安应举时相识，可谓缘分使然。“古来”两句用古代生男孩即在左门悬弓的典故，说明自己作为男子，应心系远方，颇有丈剑远游的豪迈。“携手”至“有奴”几句写作者同友人共同应举，携手征战考场之上。然而命运不同，友人早早中第，而作者只能失落的等待在此应举的机会。“灞岸”至“分手”几句是写作者对友人即将离开的依依不舍情感，其中“迟迟”两字最能见作者与友人的不舍之情，深情婉转，令人读来伤感。作品的末尾两句当是作者开解自己的失意情绪。在这两句中，作者写希望你东去的车辙还未平整，我已踏上东归的路途，寄托了作者对未来的信心与期许。', '刘驾同曹邺友善。唐宣宗大中四年，曹邺先及第东归，刘驾则在长安应举准备下一年的考试，此作或作于此时。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (281, 'a861775e11644e4fae4f9aa05af27c53', '送友人下第东归', 130, 3, -1, '年份未详', '漠漠杨花灞岸飞，几回倾酒话东归。九衢春尽生乡梦，千里尘多满客衣。流水雨余芳草合，空山月晚白云微。金门自有西来约，莫待荧光照竹扉。', '漠漠杨花灞岸飞，几回倾酒话东归。九衢春尽生乡梦，千里尘多满客衣。流水雨余芳草合，空山月晚白云微。金门自有西来约，莫待荧光照竹扉。', '此诗具体创作时间无考，根据作品题目，当是作者送别下第友人，有感而作。作品的起首两句先点明送别的时间与地点是暮春时节的灞水岸边，再由次句说明作者同友人送别时推杯换盏的情景，突出作者同友人的深厚友谊。颔联两句以对仗的形式婉转地写出友人此次离京的原因，友人此次离京应是落第还家，而作者委婉地说成友人因思乡情生所以还家，体现出作者对友人失意情绪的照顾与考虑。颈联两句是作者遥想友人家乡情景：微雨芳草，空山微云，营造出一片闲逸自在的意境，突出友人家乡之美好。尾联两句当是作者对友人的安慰与勉励。“金门”一句是鼓励友人来年再次应举，“莫待”一句是勉励友人在家也不要放弃上进的念头，仍需努力才是。全作可以说是打破了唐代送别诗创作的模式化现象，虽然具有送别诗应该有的一些因素，但其表现形式还是令人耳目一新。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (282, '44d5dea1df6a400394c6106dae66026e', '送李频之南陵主簿', 107, 3, -1, '年份未详', '赴县是还乡，途程岂觉长。听莺离灞岸，荡桨入陵阳。野蕨生公署，闲云拂印床。晴天调膳外，垂钓有池塘。', '赴县是还乡，途程岂觉长。听莺离灞岸，荡桨入陵阳。野蕨生公署，闲云拂印床。晴天调膳外，垂钓有池塘。', '此诗具体创作时间无考，根据作品题目，当是作者送别友人赴南陵所作。作品的起首两句点明友人离京去往的地点是南陵：首句写南陵是友人故乡，所以友人赴任也是回家。因此在次句写此去路程不觉漫长遥远。颔联两句以对仗的形式点明送别地点以及目的地。此句意在简单勾勒友人此去经历的路程，符合作诗规范的同时，也使作品在“听”、“荡”两个动词的点缀下显得饶有意趣。颈联两句着重描写友人的馆舍场景：野草丛生显示友人官卑职冷，官舍无人问津的荒凉景象。闲云拂影暗示友人官职清闲，即将在任上度过一种颇为闲适自在的生活。尾联两句承接上句意绪，将这种闲适生活的内涵拓展开来：晴天调膳煮羹，亦能垂钓观鱼。从内容上来说，作品的后两句主要是作者对友人即将拥有的任上生活的憧憬。从情感上来讲，作者在首联用旅途“长”字暗示友人此去赴任并不是升迁，而是贬谪。所以作者在最后两联写友人即将享受到的闲适生活，实际上是对深受贬谪影响，身处苦痛之中的友人的一种劝勉与安慰。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (283, '7f4a200214dc4ff69186b8617e02d7dd', '灞上感别', 96, 3, -1, '年份未详', '灞水何人不别离，无家南北倚空悲。十年此路花时节，立马沾襟酒一卮。', '灞水何人不别离，无家南北倚空悲。十年此路花时节，立马沾襟酒一卮。', '此诗具体创作时间无考，根据作品内容，当是作者在灞上送别友人时，有感而作。长安城外灞水边，是终唐一代，迎来送往的胜地，离愁别绪的故乡。所以作者在开篇写下首句。次句写作者此时立马灞水，因客居长安，无家可归，所以抒发自己的悲伤情绪。作品的后两句先写作者来往长安已十年，时间之长凸显出作者悲伤感慨颇多。而作者并没有将这满腔愁绪全部一泄而出，反而借用饮下一卮酒将自己的愁绪表达出来，婉转至极，令人读来心中似有愁绪。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (284, '6f3650214e094821ba707579f37a2a1f', '送进士臧濆下第后归池州', 64, 3, -1, '年份未详', '赋成无处换黄金，却向春风动越吟。天子爱才虽仄席，诸生多病又沾襟。柳攀灞岸狂遮袂，水忆池阳渌满心。珍重彩衣归正好，莫将闲事系升沈。', '赋成无处换黄金，却向春风动越吟。天子爱才虽仄席，诸生多病又沾襟。柳攀灞岸狂遮袂，水忆池阳渌满心。珍重彩衣归正好，莫将闲事系升沈。', '此诗具体创作时间无考，根据作品题目，当是作者送落第友人回家时，有感而作。作品的首联以友人的口吻说“做好诗赋之后却无处换取黄金，只能在春日自唱自叹”这样的文学无用论，侧面表现出友人因落第而产生的失意情绪。值得注意的是，这种对文学取士价值观的否定，消极情绪频频出现于落第文人口中。颔联两句中“仄席”是指天子侧坐等待贤良之士的行为。这句是说天子贤明以待贤良，听闻士子多病又泪下沾襟，塑造出一位礼贤下士，体察民情的贤明君主形象。颈联两句对仗工稳。首句“狂遮袂”是指因离别时的伤感情绪影响涕泗滂沱，用袖抹泪的行为，显示友情颇深；次句“渌满心”是说友人身在长安，心中却系念池阳家乡，显示思乡情重。尾联两句是作者劝勉、嘱托友人的语言。最后一句中，作者将“落第”一事比作“闲事”，言不要让这些闲事影响友人的心情，嘱托愈殷切，与友人的情感愈深浓。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (285, '617f1a957bc24cbfbf0d135b719ab168', '长安道', 131, 57, -1, '年份未详', '朱门映绿杨，双阙抵通庄。玉珮声逾远，红尘犹自香。', '朱门映绿杨，双阙抵通庄。玉珮声逾远，红尘犹自香。', '此诗具体创作时间无考。根据作品题目，当是作者旅居长安时所作。全诗写作者在长安道中所见景象，对长安道上的自然景物以及送别场景都作了简要描写。“朱门”两句先对长安道之繁荣昌盛的景象作了一番描述：以“朱门”、“绿杨”管窥长安之繁荣；以长安道通达四方，侧面衬托长安之昌盛。作品的后两句写长安道上的送别场景。诗人面对整场送别，只选取了送别后送人远望，离人离去这一场景进行细致描写：离人离去，渐行渐远已经无法看到离人的身影，只能听到身上的玉佩声音渐远渐小。虽然离人已远，但当是送别时留下的香浓味代还弥漫在送别的地点，久久不能散去，为送人留下念想的同时也为其留下了思念的凭证。长安道，作为京畿地区的道路，其规格与环境定有别于其他地区的道路。而且作为出入长安城的必经之路，每日在此迎来送往之人多不胜数。所以，作者在抓住长安道上这两个特点，创作此诗，为后人想象长安周边繁荣通达的交通情景提供了依据。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (286, 'e97cea03c98e4c72bf83ba6e7d2a3f9c', '初至长安（时自外郡再授郎官）', 59, 57, 828, '828年', '左迁凡二纪，重见帝城春。老大归朝客，平安出岭人。每行经旧处，却想似前身。不改南山色，其余事事新。', '左迁凡二纪，重见帝城春。老大归朝客，平安出岭人。每行经旧处，却想似前身。不改南山色，其余事事新。', '唐文宗大和二年，刘禹锡至长安为主客郎中，此诗为初至长安时所作。作品的起首两句先言自己的为官生涯中曾两次贬谪：第一次是在元和九年自朗州返京，随后再次离京贬谪至连州，此次当是刘禹锡为官生涯中第二次回到京城长安，此次距离为官生涯中第一次被贬，已经过去了将近二十四年，所以作者在首联中言“凡二纪”。颔联两句作者自言年岁已高，能够在半老的年纪里能够安全地从贬所回京，何尝不是一种幸运呢？颈联两句写作者自连州返回长安的所见所闻所感：重走一遍贬谪时走过的道路，旧路旧景无一不牵动撩拨着作者对往事的怀念及思考。不过，与贬谪时不同的是，除却终南山依旧苍茫外，其他的所有景物事物都是作者从未见过的全新景象。作品的最后两联虽然写全新的事物景象带给作者全新的感受，但作者对全新生活的憧憬及期待都寓于其中。', '唐文宗大和二年，刘禹锡至长安为主客郎中，此诗为初至长安时所作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (287, 'a833e829cb1c4321b4d41b45a13db992', '长安道', 8, 57, 792, '792年', '胡风激秦树，贱子风中泣。家家朱门开，得见不可入。长安十二衢，投树鸟亦急。高阁何人家，笙簧正喧吸。', '胡风激秦树，贱子风中泣。家家朱门开，得见不可入。长安十二衢，投树鸟亦急。高阁何人家，笙簧正喧吸。', '唐德宗贞元八年，孟郊初次应试不第，客居长安，有愤世之作。作品的前三联先写作者长安应举失败后的悲苦感受。首联以风起兴，应举失败，心情本已难受至极，又值西风拂面，心绪更加杂乱，诗思也由此产生。颔联写作者伫立长安街头，见长安高楼万丈，亭台数间，却无一间无一楼能够接纳收容应举失利的作者，孤苦无依之感由此产生；颈联两句，作者将视角聚焦到小鸟归巢这一自然现象，又联想至小鸟有家可回，自己却无家可回，心中那种孤苦无依的感受再一次加深。作品到了第三联，作者这种由应举失利产生的孤苦无依感受逐步加深，深到难以复加之时，就需要排解和抒发。所以，作者将这种感受的产生缘由归结到社会不公，并选取社会不公中的贫富差距较大这一方面着手描写。作者在尾联中，感叹谁家在举行宴会，歌声乐声声声入耳呢？看似平常的一句话，在作者内心情感无以复加之时突然发出，体现出作者对社会不公问题的深入思考，极具力量感。', '唐德宗贞元八年，孟郊初次应试不第，客居长安，有愤世之作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (288, '56c745e9b63e42a2b39cdaaa496e4a27', '莎栅联句', 8, 33, 811, '811年', '此处不断肠，定知无断处。', '此处不断肠，定知无断处。', '时孟郊连失三子，痛苦之情可见。', '元和六年韩愈赴长安之官与孟郊联句', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (289, '0214e41aa58a47568a5e202dae740ea0', '长安旅情', 8, 57, 793, '793年', '尽说青云路，有足皆可至。我马亦四蹄，出门似无地。玉京十二楼，峨峨倚青翠。下有千朱门，何门荐孤士。', '尽说青云路，有足皆可至。我马亦四蹄，出门似无地。玉京十二楼，峨峨倚青翠。下有千朱门，何门荐孤士。', '根据作品内容，此作全写作者郁郁不得志的心情，联系其写于长安，姑且将其定于唐德宗贞元九年，孟郊再次应举不第后所作。首联先言他人的宦途如平步青云一般，非常顺利，只要是能够走动，都能够为官任职。颔联又写自己屡次进京应举，多次无果，似乎没有他人那样好的运气和前途。颈联两句是作者描绘西京城内繁荣景象：高大绮翠的楼阁亭台满目皆是，作者眼里看着这长安繁荣景象，心中不禁发叹：这些楼阁亭台里，哪一座才能为我打开大门，让我实现自己的人生愿望。全作应用对比手法，营造悲凉凄楚格调：前半部分以他人命运与自己命运作对比，显示出作者命运之悲凉；后半部分以繁荣的长安景象同作者孤身一人的孤士形象作对比，又显出作者应举落第后的凄楚心情。', '唐德宗贞元九年，孟郊于长安应举，再次落第，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (290, 'd9f0169229f14fc394ddaad4ded2f957', '长安道', 12, 57, -1, '年份未详', '花枝缺处青楼开，艳歌一曲酒一杯。美人劝我急行乐，自古朱颜不再来。君不见外州客，长安道，一回来，一回老。', '花枝缺处青楼开，艳歌一曲酒一杯。美人劝我急行乐，自古朱颜不再来。君不见外州客，长安道，一回来，一回老。', '此诗具体创作时间无考。根据作品内容，当是在外做官的作者，有机会到长安，见长安繁荣景象，有感而作。全诗抒发时光易逝，须及时行乐的思想内容。作品起首“花枝”一句言长安城内既有芳美的花枝，又有林立的高楼，真是满目珠玉琳琅。自“艳歌”一句开始，作者从长安城内景象描写转向对青楼内部歌舞宴会场面的描写：先言觥筹交错，推杯换盏间又有艳曲入耳，虽然没有具体描写，但奢华糜烂的筵会场面还是可以想见的。“美人”句至全诗结尾句借美人之口，表达时光易逝，须及时行乐的主题，并且以外州客很少有机会来到京畿长安地区为例，劝诫作者要及时行乐。古代交通不便，职官制度较为森严，一地为官者很少有机会到其他地区游历或者视察，甚至很少有机会去其他地区工作。所以在这样情况下，能来到京畿地区游历视察的官员更需要珍惜这来之不易的机会，及时行乐，领略长安城的别样风光了。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (291, '423b9018f820446e86c4e09bddde4504', '再至长安', 117, 57, -1, '年份未详', '十年谪宦鬼方人，三遇鸿恩始到秦。今日九衢骑马望，却疑浑是刹那身。', '十年谪宦鬼方人，三遇鸿恩始到秦。今日九衢骑马望，却疑浑是刹那身。', '此诗具体创作时间无考。根据作品内容，当是作者自峡州返回长安，结束十年贬谪生活的作品。在这首诗中，作者初至长安，除了感到长安繁华昌盛之外，还为自己能够幸运地离开贬所，重获新生欣喜到难以置信，也对皇帝朝廷等释放他的人心存感激。作品起首“十年”一句提及自己在十年贬谪生活中遭受到的非人待遇以及残酷折磨。“三遇”一句对皇帝的洪恩表示感谢之情。“今日”两句着笔作者初至长安，骑马游荡时候的心理描写。从肮脏逼仄的贬所到四通八达的长安九衢，生活环境的落差之大，使作者难以相信这一切的改变，所以作者不禁自疑“这一切是否是真实的”。从全诗来看，作者在回忆贬谪生活以及面对全新生活的表现，都为我们去推知古代贬谪生活给被贬官员造成的压力与折磨提供了依据。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (292, '48c670336c344c16875a020c055689ab', '长安客感', 132, 57, -1, '年份未详', '日过千万家，一家非所依。不及行尘影，犹随马蹄归。', '日过千万家，一家非所依。不及行尘影，犹随马蹄归。', '此诗具体创作时间无考。根据作品题目，当是作者客居长安，有感而作。此诗写作者骑马于街上游荡，所见所感。“日过”两句写作者伫立街头，见太阳普照之下有房屋无数，没有一间房屋能够容作者存身，凸显出作者客居长安的孤苦寥落感受。“不及”两句写作者无处存身，只能独自离去，行走带起的尘土随着作者前进的马蹄飘散在空中，久久不能散去，夕阳映照在作者背上，凸显出作者独自一人，无奈离去的孤苦零丁的无奈之感。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (293, 'b79e12b3d513444893cf8573c466b1c5', '长安客感', 132, 57, -1, '年份未详', '客泪如危叶，长悬零落心。况是悲秋日，临风制不禁。', '客泪如危叶，长悬零落心。况是悲秋日，临风制不禁。', '此诗具体创作时间无考。根据作品题目，当是作者客居长安，有感而作。此作看似是悲秋作品，实则是客居外地，思念家乡时候的作品，饱含着作者对家乡的思念，以及对自己客居外地，孤苦无依生活状态的自怜。作品的开头就言作者落泪，单刀直入，落泪的原因引人深思。作者把将落未落的泪水比作是即将凋谢的树叶，取喻甚巧。“况是”两句写明此诗的创作时间应是秋季。在秋天，万物枯黄，生命凋敝的季节里，本来就饱受思乡情绪折磨的作者的心情上又增添一层悲秋的情绪，所以作者在作品的最后一句，不能自禁，面对着家乡的方向，泪下如线。全诗之中，虽言悲秋，但思乡之情全在言外，婉转至极。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (294, '42b961653b2f4f9cb445b88fabe27698', '长安旅夜', 13, 57, -1, '年份未详', '久客怨长夜，西风吹雁声。云移河汉浅，月泛露华清。掩瑟独凝思，缓歌空寄情。门前有归路，迢递洛阳城。', '久客怨长夜，西风吹雁声。云移河汉浅，月泛露华清。掩瑟独凝思，缓歌空寄情。门前有归路，迢递洛阳城。', '此诗具体创作时间无考，根据作品题目，当是作者久居长安时所作。作品的起首两句先点明自己久客长安的身份，以“怨”字概括作者此时的心理状态；再以西风雁鸣的声音，委婉地衬托出作者此时心情之愁郁。颔联两句当写长安晚景，以朗月云淡，花露香浓营造出淡雅清静的意境。就在这种清静的意境中，作者抚琴寄怀，极有当年冯谖困顿寥落时的风范，凸显出作者此时失意的状态。作品的尾联是作者自勉语，门前自有归路，如果不能坚持，何不如尽早打算，早日回家。这句不是作者久居长安后，想要退缩之语，而是作者久居长安，希望自己的思乡之情有所寄托，所以作者写下此句，将自己的思乡感情寄托在迢迢归路之上，用情至深，蕴藉之至。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (295, '045daf66167c4f5c9ae8dfc17c052d32', '饯王岑判官赴襄阳道', 6, 57, 754, '754年', '故人汉阳使，走马向南荆。不厌楚山路，祗怜襄水清。津头习氏宅，江上夫人城。夜入橘花宿，朝穿桐叶行。害群应自慑，持法固须平。暂得青门醉，斜光速去程。', '故人汉阳使，走马向南荆。不厌楚山路，祗怜襄水清。津头习氏宅，江上夫人城。夜入橘花宿，朝穿桐叶行。<br>害群应自慑，持法固须平。暂得青门醉，斜光速去程。', '唐玄宗天宝十四年，王岑赴襄阳任山南东道采访处置使判官，岑参有诗送行。作品的起首两句先言王岑曾担任过“汉阳使”之类的职务，再点明王岑离京此去的地点是荆州一带。接下来两句先写因王岑曾担任过楚地的官职，所以说王岑“不厌楚山路”。又说王岑对楚地深有感情，所以言王岑“只怜襄水清”。“津头”两句用晋代都在襄阳出任官职的山简、朱序二人典故寄托作者对友人未来的期望：山简典故是作者期待友人能够在襄阳度过一段逍遥自在的为官生活；朱序典故是作者期望友人在襄阳任职期间有所建树。“夜入”两句主要是写友人此去南行的路途中，景色优美。“害群”两句写作者对友人的嘱托：先说邪恶之人在友人的管理之下能够自我畏惧，改过自新；再说友人在为官过程中，执法应以公平为主。最后两句，表现作者对友人的不舍：虽然作者写趁着黄昏，劝友人早早出发，但联系上文作者对友人殷切期待以及嘱托，可以看出此时的作者对友人是不舍的。这种婉转的表达方式，是需要耐心细读得出的，也正是如此，使作品经久耐读。', '唐玄宗天宝十四年，王岑赴襄阳任山南东道采访处置使判官，岑参有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (296, '3c0671610fe545c98b85a22df70cc5b1', '送弘文李校书往汉南拜亲', 6, 57, 758, '758年', '未识已先闻，清辞果出群。如逢祢处士，似见鲍参军。梦暗巴山雨，家连汉水云。慈亲思爱子，几度泣沾裙。', '未识已先闻，清辞果出群。如逢祢处士，似见鲍参军。<br>梦暗巴山雨，家连汉水云。慈亲思爱子，几度泣沾裙。', '唐肃宗乾元元年，李舟赴汉南省亲，有诗送行。作品的前四句是作者从文学才能方面对李舟作了简要评价：首联两句先对李舟的文学才能作了总评，写其文学功底深厚，属诗撰文，可算得上是出类拔萃；颔联两句主要以祢衡、鲍照两位在文学史上享有盛誉的文士比喻李舟，是说李舟的为人似祢衡一般拥有恃才傲物，不与世俗苟同的性格，其创作出的诗作如鲍照诗作一般俊逸洒脱。接下来四句作者宕开一笔，转向对李舟省亲事件的书写。颈联两句先从李舟的角度写李舟在长安任官期间，经常产生思念家乡的浓郁情感；尾联两句从李舟父母的角度，写李舟父母对友人的深深思念之情：思念之深，几度泪沾衣裙。这首作品先从李舟的官职身份出发，写其文学才能出众，是饯别抒情的一般写法。但从颈联以后，从友人与其家人两个角度书写相互的思念情感，这种处理方式是独树一帜的：站在友人角度为其着想，体现作者对友人的深厚友谊；而从不同角度描写思念情感，不仅将情感写得深刻，也不至于太过直露。', '唐肃宗乾元元年，李舟赴汉南省亲，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (297, '41da0dab69394dc6a49cbf6217f2da15', '送人归江宁', 6, 57, 758, '758年', '楚客忆乡信，向家湖水长。住愁春草绿，去喜桂枝香。海月迎归楚，江云引到乡。吾兄应借问，为报鬓毛霜。', '楚客忆乡信，向家湖水长。住愁春草绿，去喜桂枝香。<br>海月迎归楚，江云引到乡。吾兄应借问，为报鬓毛霜。', '唐肃宗乾元元年，友人归家，有诗送行。作品的起首两句先以友人的角度写友人思念家乡，情深意浓；又站在友人的角度，感叹路遥日长，离家甚远，表现友人不能立刻回家的惆怅无奈情绪。颔联两句以春草、桂枝两种生长于不同时节的植物代表时间，暗示友人离京出发时正是春草正绿的仲春时节，而抵家之时则是桂枝飘香的八月时节，突出友人离家甚远。以时间之漫长写离家路途遥远，友人对家乡的思念都在言外。颈联两句是作者想象友人回家途中可能遇到的景色：朗月水清，又有青云暗布，渲染出明净清新的水中景色。在尾联两句里，作者从友人对家乡的思念，写及自身对家乡的思念。古时信息流通不便，最便捷的流通方式莫过于依靠旅人传播，因此作者在最后一联央求友人向家人传达自己的消息，安慰远在他乡的家人，不要记挂担心。', '唐肃宗乾元元年，友人归家，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (298, 'c2b73a7470de43f9a9fcde0f97c29141', '送扬州王司马', 6, 57, 758, '758年', '君家旧淮水，水上到扬州。海树青官舍，江云黑郡楼。东南随去鸟，人吏待行舟。为报吾兄道，如今已白头。', '君家旧淮水，水上到扬州。海树青官舍，江云黑郡楼。<br>东南随去鸟，人吏待行舟。为报吾兄道，如今已白头。', '唐肃宗乾元元年，友人归家，有诗送行。作品的起首两句言友人家乡的地点，点明友人此去的目的地。颔联两句是作者遥想友人家乡的景色：江云黑沉，正要下雨时节，在江边树的掩映下能够隐约看见青楼瓦舍以及郡楼台阁。在这两句对江南景色的描写中，可以看出唐时江南的繁华昌盛的场面。颈联两句中，因友人是南去，故言友人“随去鸟”；因友人离去，众人在江边为其设宴饯别，所以写众人“待行舟”，即等待友人离别时刻的到来。尾联两句与岑参此年所作的作品同一格式，都是希望友人能够为自己传递家信，为家人传递自己的近况。但是友人此去是扬州，与岑参家人居住的荆州距离甚远，所以此句可能是作者离别作品程式化的体现，即写此句，并非传递家信，而是表现自己思念家乡的情感，形式意义大于内容意义。', '唐肃宗乾元元年，友人归家，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (299, '730975ccbc2645f9bbab6221f7816b7f', '赵少尹南亭送郑侍御归东台（得长字）', 6, 57, 753, '753年', '红亭酒瓮香，白面绣衣郎。砌冷虫喧坐，帘疏雨到床。钟催离兴急，弦逐醉歌长。关树应先落，随君满鬓霜。', '红亭酒瓮香，白面绣衣郎。砌冷虫喧坐，帘疏雨到床。<br>钟催离兴急，弦逐醉歌长。关树应先落，随君满鬓霜。', '唐玄宗天宝十二年，岑参于长安送别友人归东台，因有此作。作品的起首两句先总写送别场面：首句写红亭之中，酒瓮正香，正是盛宴将开之时；次句写友人面容美丽，衣着光鲜，一副意气风发的模样。颔联两句对送别时候的场景作了一番描绘：此时正是微雨时分，天气正寒未暖，一片幽寂之中，忽有小虫鸣叫，饶有趣味。颈联两句写纵性恣肆的饯别场面：既有音乐相伴，当有歌舞助兴。送行人酒酣耳热，把酒言欢的场景都在言外，欢乐恣肆的饯别场面可以想见。而伤感的离别情绪就寓于这种欢乐的饯别场面之中。可以说，对友人离别有多么不舍，送别场面就有多么欢乐。作品的最后两句表现作者对友人的不舍：首先知道，古人送别，不知何时再见。正是在这种再难相逢的恐惧感的基础上，作者以自然植物的凋零比喻友人年岁渐长，凸显出作者对于友人离别一事的伤心失意情绪。', '唐玄宗天宝十二年，岑参于长安送别友人归东台，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (300, 'bd167128522c4e6d882587d1046a252b', '送宇文舍人出宰元城（得阳字）', 6, 57, 753, '753年', '双凫出未央，千里过河阳。马带新行色，衣闻旧御香。县花迎墨绶，关柳拂铜章。别后能为政，相思淇水长。', '双凫出未央，千里过河阳。马带新行色，衣闻旧御香。县花迎墨绶，关柳拂铜章。别后能为政，相思淇水长。', '唐玄宗天宝十二年，岑参于长安送别友人出任元城令，因有此作。友人因上任元城离京。所以作者在作品的起首两句中，先以《后汉书》中叶县县令王乔的典故说明友人新近授官一事，次句中又点明友人上任的地点在今日河南以东，古言“过河阳”。友人因新近授官，所以面貌气色上稍显喜悦，所以作者在颔联中以新马旧衣凸显出友人新近授官的喜悦之情。颈联两句因元城此时正值春日，所以写“花迎墨绶”，“柳拂铜章”，暗示友人性格为人之好，受到元城百姓的欢迎。作品的最后一联表现作者对友人的不舍与思念。这种不舍与思念之情是建立在古代交通、交流的不便，古人离别再难相逢的恐惧的基础上产生的。在最后一句中，作者将自己对于友人的思念形象化比喻为淇水的长度，这种形象化的表达更易让读者感知作者对友人思念之深，也显示出作者高超的文笔功力。', '唐玄宗天宝十二年，岑参于长安送别友人出任元城令，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (301, 'cda92387afc441bd9e3df121b61e0549', '送魏四落第还乡', 6, 57, 753, '753年', '东归不称意，客舍戴胜鸣。腊酒饮未尽，春衫缝已成。长安柳枝春欲来，洛阳梨花在前开。魏侯池馆今尚在，犹有太师歌舞台。君家盛德岂徒然，时人注意在吾贤。莫令别后无佳句，只向垆头空醉眠。', '东归不称意，客舍戴胜鸣。腊酒饮未尽，春衫缝已成。长安柳枝春欲来，洛阳梨花在前开。魏侯池馆今尚在，犹有太师歌舞台。君家盛德岂徒然，时人注意在吾贤。莫令别后无佳句，只向垆头空醉眠。', '唐玄宗天宝十二年，岑参于长安送别友人东归，因有此作。友人因落第而还乡，所以作者在诗中全写劝勉规戒之语。全作的开头两句由于题目已经点明友人回家之原因，所以这两句主要是写友人落第后的忧郁失意情绪。又写友人客居长安，听闻戴胜鸟叫，心中忧郁情绪又重了一番。“腊酒”至“洛阳”句写长安春景。虽是写景，实则是点出时间的迁转，凸显出友人随时间的迁转，最终无所成就的落寞失意感受。“魏侯”两句引出友人显赫之身份：友人乃是开国功臣魏徵之后代。名门后代，世间对其必有厚望，所以作者承此意绪，写下“君家”两句。作者以魏家盛德劝勉友人不要对生活以及仕途失去信心。并且，作者在作品的最后两句中，以“莫要虚度光阴”为意规戒友人。言下之意是借助祖上盛德，友人能够稍加努力，未来远大可期。', '唐玄宗天宝十二年，岑参于长安送别友人东归，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (302, '82a502540f254f02a6ad121aeabd5939', '落第归乡留别长安主人', 133, 57, -1, '年份未详', '客里愁多不记春，闻莺始叹柳条新。年年下第东归去，羞见长安旧主人。', '客里愁多不记春，闻莺始叹柳条新。年年下第东归去，羞见长安旧主人。', '此时具体创作时间无考。根据作品题目，当是作者下第归乡，与长安主人相别时所作。全诗的前两句先写作者由客居长安，产生的羁旅思乡情感：首句“客里”点明作者此时应是客人身份居住在长安，“愁多”没有指明所愁何事，但联系作者身份，大致可以推知作者所愁是应举屡次失利带来的挫败感；长期旅居外地，对家乡的思念之情。“不记春”有可能是作者在长安准备应举，无暇顾及时间，也有可能是作者因发愁，所以不敢顾及时间，唯恐时间流逝太快。次句“闻莺”写作者听闻鸟叫，才知此时应是春日，继而感叹柳条抽芽，事事更新。作品的后两句写作者对旅居长安多年，接纳收容自己的主人的感谢之情。最后这两句是作者生活经历的真实写照：“羞见”一词真为诗眼。因年年应举失利，所以羞于见主人，其背后又蕴藏着作者年年应举失利的心酸与哭泪，婉转至极。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (303, 'a74d1529b27f4bcbb52b911d338a7059', '送冷朝阳及第东归江宁', 134, 57, -1, '年份未详', '高第由佳句，诸生似者稀。长安带酒别，建业候潮归。稚子欢迎棹，邻人为扫扉。含情过旧浦，鸥鸟亦依依。', '高第由佳句，诸生似者稀。长安带酒别，建业候潮归。稚子欢迎棹，邻人为扫扉。含情过旧浦，鸥鸟亦依依。', '此诗具体创作时间无考，根据作品题目，当是作者送及第友人回家时所作。因为友人此去离京是及第而归，所以作者在这篇作品中处处充满了对于友人及第后的喜悦感情。作品的起首两句先言友人及第事，以诸位考生都没有友人之才能夸赞友人之能力，显示出作者闻知友人及第后的喜悦。颔联两句言送别场景，写作者为送别友人专门设下酒宴，虽然简陋，但却蕴含着作者对友人深厚情谊。颈联两句写作者遥想友人回家后受到的热烈欢迎场面。从及第后回家受到的如此重视以及如此重大的欢迎场面，可以推知唐诗进士科之难考以及人们对进士科之重视。作品的最后两句写送别之后，以鸟鸣之依依寄托作者对友人深厚的情谊，结尾含蓄，饶有意趣。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (304, '00f8e245ffa24c638988775091f3a8f0', '送温初下第', 8, 57, -1, '年份未详', '日落浊水中，夜光谁能分。高怀无近趣，清抱多远闻。欲识丈夫志，心藏孤岳云。长安风尘别，咫尺不见君。', '日落浊水中，夜光谁能分。高怀无近趣，清抱多远闻。欲识丈夫志，心藏孤岳云。长安风尘别，咫尺不见君。', '此诗具体创作时间无考，根据作品题目，当是作者送友人下第返家时即兴而作。因为友人此去离京是落第后失意回家，所以这篇作品处处充满了失意的情感及内容。起首两句点明送别时景色。值得注意的是，这些景色都是作者闻知友人落第后所见，所以作者在选择意象是必然会选择蕴含失意情绪的意象，如“浊水”之类。日照浊水，营造一种污浊难分的混沌意境，就是在这种意境之中，我们能够领会到作者此时听闻友人落第后的失落情绪，进而体会作者与友人的深厚情谊。颔联、颈联四句夸赞友人虽有高远志向，却无人能识别，察觉，以至于落第而归。既是夸赞也是对失意友人的勉励。尾联两句依旧写景。“咫尺”两字可算诗眼，咫尺不见，固然是作者的夸大之词，但于此其中，还是能见到作者对友人的不舍情绪。另外，结合“风尘”两字来看，可见送别时天气恶劣，几乎达到了扬沙漫天的地步。所以作者借助此种景色，令人读来更能身临其境地感受作者此时与友人的不舍情绪。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (305, '958979a8bfcd47e8be8714ec61cfded1', '送魏简能东游二首', 117, 57, -1, '年份未详', '献赋论兵命未通，却乘羸马出关东。灞陵原上重回首，十载长安似梦中。燕市悲歌又送君，目随征雁过寒云。孤亭宿处时看剑，莫使尘埃蔽斗文。', '献赋论兵命未通，却乘羸马出关东。灞陵原上重回首，十载长安似梦中。燕市悲歌又送君，目随征雁过寒云。孤亭宿处时看剑，莫使尘埃蔽斗文。', '此诗具体创作时间无考，根据作品题目，当是魏简能离京东游，作者相送，即兴而作。作品的第一首写友人久居长安近十年，写其“献赋论兵”却无所成就，只能乘羸弱小马出城。回首观望，似在梦中，体现出作者对友人经历的同情。第二首作品起首两句先用燕国高渐离送别荆轲一事，暗示友人同作者的关系非同一般，情深意浓。结尾两句是作者对友人的劝勉语，作者劝勉友人虽然久居长安，一事无成，然而在东游的时候，须要常常看剑，不要忘记自己的雄心壮志才对。唐人喜用“剑”比喻壮士游子的雄心壮志，此篇也不例外。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (306, 'b0e392d4e0f547f5abb4222658e2608d', '送庞子肃', 89, 57, 821, '821年', '三年游宦也迷津，马困长安九陌尘。都作无成不归去，古来妻嫂笑苏秦。', '三年游宦也迷津，马困长安九陌尘。都作无成不归去，古来妻嫂笑苏秦。', '唐穆宗长庆元年，沈亚之逗留长安。当时距离沈亚之登第为官大约已有三年，所以可推断此作为长庆元年所作。全诗的前两句先言自己的人生经历：沈亚之于元和十年登第，后来又游历岐陇、会稽等地，在泾源等地为官，所以可称得上是“游宦”。后一句写作者此次逗留长安，应该是在长安谋求生路，选择新的官职。但这个过程是非常漫长枯燥，使人迷茫的，所以作者说自己此时是“马困长安”。作品的后半部分是诗作的最后两句，主要是告诫和勉励友人：首句先以自己“无成”，凸显出友人之成功，次句又以苏秦的典故，告诫友人一定要在成功之时归去，否则会遭受他人的白眼与歧视。	这是一首内容短小，语言简单质朴的送别作品，从此其中，可以读出当时为官者的迷茫以及身上背负的压力之大，不输于今人。', '唐穆宗长庆元年，沈亚之逗留长安。当时距离沈亚之登第为官大约已有三年，所以可推断此作为长庆元年所作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (307, 'cc8d4e30dcc445028b052c8f65903882', '送杨发东归', 13, 57, -1, '年份未详', '红花半落燕于飞，同客长安今独归。一纸乡书报兄弟，还家羞着别时衣。', '红花半落燕于飞，同客长安今独归。一纸乡书报兄弟，还家羞着别时衣。', '此诗具体创作时间无考，根据作品题目，当是作者送别友人时即兴而作。作品的起首两句写红花半落，燕子独飞的景色，点明送别时间应在暮春时节。另外，红花半落这样具有伤心失落内蕴的意象还承担起兴全作的功用，因此，作者在次句写友人独归，便有一种伤感情绪淤积于中。作品的最后两句以友人口吻写其回家之后，仍着旧衣，羞于见人的难言之隐。最后一句反用“衣锦还乡”典故，按理说久居长安，一定要衣锦还乡，但如今友人着旧衣回家，言下之意正是无所成就，所以羞于见人也算得友人无所成就后回家的正常反应。虽然本诗较短，但是其中蕴含的情感是极深厚的，有言短情深之艺术特点。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (308, 'c350447733834d5f8f1dd9706597dc48', '登渭南县楼', 107, 9, -1, '年份未详', '近甸名偏着，登城景又宽。半空分太华，极目是长安。雪助河流涨，人耕烧色残。闲来时甚少，欲下重凭栏。', '近甸名偏着，登城景又宽。半空分太华，极目是长安。雪助河流涨，人耕烧色残。闲来时甚少，欲下重凭栏。', '此诗具体创作时间无考，根据作品题目，当是作者登临渭南城楼，有感而作。首联先写作者登临渭南城楼所见。“宽”字极具画面感，概括了作者登楼所见景色之宽阔特征，营造一种广袤辽阔的图景。颔联两句写“宽”的主要内涵，向东看有太华险岳，向西看则是长安皇城。视野十分辽阔，意境何其苍茫雄阔。颈联两句是写作者所见的近景。此时正是初春时节，冰雪消融，河水激流，草木生芽的美好时节，百姓在田中燃烧秸秆，准备农耕之事。无论是从自然景物，还是从人事，都写出了初春时节所蕴含的勃勃生机，令人读来神往。尾联两句主要写作者目睹以上诸景后的感受。作者感叹每日为公务所累，闲暇时候非常少，所以更为珍惜这次来之不易的登楼赏景的机会。此作先以“宽”概括所见景物，再分别以颔联、颈联两联阐述解释，最后将全篇归结于自我感情的抒发，结构清晰。写景也较真实，令人读来如在眼前。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (309, '1c8ad4837984453a9b69e76fbfcc93f9', '华清宫', 135, 0, -1, '年份未详', '骊岫接新丰，岧峣驾翠空。凿山开秘殿，隐雾闭仙宫。绛阙犹栖凤，雕梁尚带虹。温泉曾浴日，华馆旧迎风。肃穆瞻云辇，沈深闭绮栊。东郊倚望处，瑞气霭蒙蒙。', '骊岫接新丰，岧峣驾翠空。凿山开秘殿，隐雾闭仙宫。绛阙犹栖凤，雕梁尚带虹。温泉曾浴日，华馆旧迎风。肃穆瞻云辇，沈深闭绮栊。东郊倚望处，瑞气霭蒙蒙。', '此诗具体创作时间无考。根据作品内容，当是作者途经华清宫，目睹华清宫旧迹，心有所感，有志而发，因有此作。有唐一代，凡是写及华清宫的作品，内容总是离不开唐玄宗的盛世，杨贵妃的美貌，以及安禄山的叛乱。此作就是从唐玄宗盛世角度来着手书写的。作品起首两句先从华清宫周围的骊山着手描写：首句先写骊山地势之广阔，自昭应能够接连新丰；次句写骊山秀美，苍翠满眼。接下来两句写唐玄宗时：首句先写唐玄宗在骊山之上开凿宫殿，大兴土木，只为了游幸玩乐；次句写骊山之高，许多宫殿都隐藏在云雾之中，营造出云雾缭绕的景致。“绛阙”至“华馆”主要写华清宫内，楼台馆阁之精美，雕梁画栋之绝伦，以此衬托出玄宗朝奢靡享乐的生活场面。“肃穆”句至“瑞气”句是赞颂皇帝美德的常用词句：皇帝出行，除了车马壮大，景象盛大之外，其气势也是肃穆谨严，有秩序的；其次，古人相信天上的云气同地上的人相互照应，认为只要是贤明的君主进过的地方，天上就会出现祥瑞的征兆。全诗从骊山美景着笔，遥想玄宗朝盛世，从宫殿场面的盛大以及皇帝出行队伍的谨严有序两方面，虽然有诸多对皇帝的溢美之词，但从此其中，玄宗朝盛世景象，可见一斑。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (310, '0f2911569983417eb2bad8c2b422cd3f', '华清宫', 136, 5, -1, '年份未详', '天宝承平奈乐何，华清宫殿郁嵯峨。朝元阁峻临秦岭，羯鼓楼高俯渭河。玉树长飘云外曲，霓裳闲舞月中歌。只今惟有温泉水，呜咽声中感慨多。', '天宝承平奈乐何，华清宫殿郁嵯峨。朝元阁峻临秦岭，羯鼓楼高俯渭河。玉树长飘云外曲，霓裳闲舞月中歌。只今惟有温泉水，呜咽声中感慨多。', '此诗具体创作时间无考。根据作品内容，当是作者途经华清宫，目睹华清宫旧迹，心有所感，有志而发，因有此作。有唐一代，凡是写及华清宫的作品，内容总是离不开唐玄宗的盛世，杨贵妃的美貌，以及安禄山的叛乱。此作就是从唐玄宗盛世角度来着手书写的。作品的起首两句就点明此作描写玄宗朝的盛世景象：先写唐玄宗天宝年间，社会呈现出承平繁盛的景象，与此同时，皇室贵族也开始重视物质享乐，在骊山之上大修宫殿，兴造土木。因在骊山之上，所以华清宫诸宫殿更显郁郁嵯峨。颔联两句着重写华清宫内的两座宫殿：朝元阁以及羯鼓楼，因为二者都是皇室建筑，必然在作者眼中呈现出高耸入云的气势，所以作者先写朝元阁俯瞰秦岭，又写羯鼓楼俯视渭河。营造出宫殿楼阁高耸入云的气魄与景致。颈联两句写玄宗朝游玩享乐的场景：写此场景，不着眼于宫殿内，只着眼于宫殿外部的歌声曲声，取意甚巧。作品最后两句，结束对玄宗朝盛世的遥想，回到现实，抒发作者此时见到华清宫旧迹的感叹与悲慨：见证过华清宫繁盛场景的人、物都已经逝去，只有着温泉水潺潺流淌，似乎叙说着经历时光迁转后的感慨。这种感慨可能是多重的，或是感慨盛世不再；或是感慨李杨二人爱情的消逝；或是感慨安史之乱对李唐王朝造成的创伤与致命打击。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (311, '64e346b088794ea18df9ce3f01e53333', '华清宫感旧', 85, 5, -1, '年份未详', '尘到朝元边使急，千官夜发六龙回。辇前月照罗衫泪，马上风吹蜡烛灰。公主妆楼金锁涩，贵妃汤殿玉莲开。有时云外闻天乐，知是先皇沐浴来。', '尘到朝元边使急，千官夜发六龙回。辇前月照罗衫泪，马上风吹蜡烛灰。公主妆楼金锁涩，贵妃汤殿玉莲开。有时云外闻天乐，知是先皇沐浴来。', '此诗具体创作时间无考。根据作品内容，当是作者途经华清宫，目睹华清宫旧迹，心有所感，有志而发，因有此作。有唐一代，凡是写及华清宫的作品，内容总是离不开唐玄宗的盛世，杨贵妃的美貌，以及安禄山的叛乱。通过细读诗作内容，可以发现作者所感之旧事涉及到了上述全部三种内容。作品的起首两句先写玄宗朝承平繁盛的景象之下隐藏着祸根：此句用安史之乱前夕，边使告急，朝廷官员为了不打扰唐玄宗的享乐生活，所以隐瞒不报，贻误战机，导致唐玄宗仓皇出逃。此句一是写及玄宗朝的奢靡景象，二是借此对玄宗晚年政治上的失误作出批判。颔联两句是写出逃前夜，宫廷内部的慌乱景象。作者选用杨贵妃形象，以贵妃落泪，暗示此时宫廷内部的恐慌、害怕气氛。颈联两句结束对玄宗朝的遥想，转而写作者亲眼所见：作者伫立华清宫前，见妆楼紧锁，石莲依旧保持着盛开的状态，似乎什么都没有改变，但上句妆楼紧锁已经暗示出此时华清宫凋敝破败的景象。尾联两句写作者对玄宗朝尤其是唐玄宗的怀念，表现出作者对大唐盛世的怀念与追忆。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (312, 'eacb151743444c939ebbd4caeed659a1', '华清宫前柳', 85, 5, -1, '年份未详', '杨柳宫前忽地春，在先惊动探春人。晓来唯欠骊山雨，洗却枝头绿上尘。', '杨柳宫前忽地春，在先惊动探春人。晓来唯欠骊山雨，洗却枝头绿上尘。', '此诗具体创作时间无考。根据作品内容，当是作者任昭应县令时，有机会游赏华清宫春景，有感而作。作品的前两句写作者游赏华清宫，看到华清宫之春景，感到惊讶：“忽”、“惊”二字当为全句诗眼，“忽”字写春天来临速度之快；“惊”字写作者目睹华清宫春色后的感受，侧面烘托华清宫春色之美。后两句写美中不足：不足之处在于没有下一场春雨，使柳枝花梢全都巧然一新，颜色秾丽，惹人喜爱。有唐一代，尤其是中唐以后的诗人，尤喜描写华清宫之残垣断壁，并同玄宗盛世相联系，生发感叹，以古论今。此作不同，虽然描写华清宫，但主要关注点在于华清宫之秀色春景，令人读来有一种轻松自在的直感。这种轻松自在的阅读直感是作者创作时的心境决定的。王建任昭应县令期间，曾多次在作品中表达了这种官卑职冷的官场生活就像闲居一般轻松自在，而华清宫与昭应相近，这也是推断这首作品作于王建任昭应县令的重要原因。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (313, '90741fca61584b95b949b18069e5ec43', '华清宫', 60, 5, -1, '年份未详', '温泉流入汉离宫，宫树行行浴殿空。武帝时人今欲尽，青山空闭御墙中。', '温泉流入汉离宫，宫树行行浴殿空。武帝时人今欲尽，青山空闭御墙中。', '此诗具体创作时间无考。根据作品内容，当是作者途经华清宫，目睹华清宫旧迹，心有所感，有志而发，因有此作。有唐一代，尤其是中唐以后的诗人，尤喜描写华清宫之残垣断壁，并同玄宗盛世相联系，生发感叹，以古论今。但为了追求含蓄婉转的表达，也为了避免不必要的祸端，唐人在咏叹前任皇帝时，常采用“以汉喻唐”的比喻方式，此作就是采用此种比喻方式，如果不稍加判断，必定会对作品内容产生误读现象。作品的前两句先写华清宫内丰富的温泉资源，再写曾经热闹非凡的温泉浴殿，现如今也空无一人，描绘华清宫凋敝破败的景象为下文感叹盛世不再起兴。作品的后两句写玄宗时代的人如今寥寥无几，曾经代表着大唐盛世下奢靡生活的华清宫如今空空如也，只能锁闭大门，令其自我消亡。这是说安史之乱后，唐玄宗失势，不再出游。随后唐朝的历代皇帝也不再热衷于游幸华清宫，曾经象征着辉煌、鼎盛的华清宫也就变得破败不堪，引人唏嘘不已。全诗还是遵循目睹华清宫旧迹，感叹时光流转的老套路，只是采用“以汉喻唐”的比喻方式，令人眼前一新。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (314, '96c8e233088a4d4ea31229b31d768209', '晓望华清宫', 137, 5, -1, '年份未详', '晓看楼殿更鲜明，遥隔朱栏见鹿行。武帝自知身不死，教修玉殿号长生。', '晓看楼殿更鲜明，遥隔朱栏见鹿行。武帝自知身不死，教修玉殿号长生。', '此诗具体创作时间无考。根据作品题目为“晓望华清宫”，可知作者此时并不在华清宫，而昭应县同华清宫较近，所以此作当是作者途经昭应，晓望华清宫，有感而作。作为一位曾经生活在唐宪宗时期的苦吟诗人，最不能忘却的就是唐宪宗对仙对药的执着与追求。面对唐宪宗这一没有任何结果的行为与举止，身为朝廷官员的作者，深感其肩上的责任，所以创作此诗，以历史上有同样经历的汉武帝讽刺唐宪宗的疯狂求仙行为。作品起首两句，先言作者晓望所见起兴后面抒发之情：晓望华清，楼殿鲜明，既有朱栏玉砌，又有野鹿行走，一片祥和温暖的晨景之中，作者看到了汉武帝曾经为长生不老作出的努力。作品的后两句中，作者写武帝自知能够长生不老，所以在此地大修宫殿。但是，汉武帝的求仙行为当然是失败了的，他早已化身成为秋风之客，仅留下一堆荒冢任后人凭吊。作者想以这样的失败经历讽刺警醒唐宪宗，可是历史证明，这种讽刺作品的作用微乎其微，最终唐宪宗还是死于药物中毒。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (315, '52f77ce8fcde482c9cbadf2ddb5e4bdd', '华清宫', 14, 5, -1, '年份未详', '朝元阁迥羽衣新，首按昭阳第一人。当日不来高处舞，可能天下有胡尘。', '朝元阁迥羽衣新，首按昭阳第一人。当日不来高处舞，可能天下有胡尘。', '此诗具体创作时间无考。根据作品内容，当是作者途经华清宫，目睹华清宫旧迹，心有所感，有志而发，因有此作。有唐一代，凡是写及华清宫的作品，内容总是离不开唐玄宗的盛世，杨贵妃的美貌，以及安禄山的叛乱。事实上，这三者在逻辑上是有密切联系的：杨贵妃的美貌举世无双，只能诞生在唐朝最为强盛的唐玄宗时期。换句话讲，杨贵妃的美貌就是唐玄宗盛世的一个侧面表现。而安禄山的叛乱是唐玄宗晚年任用奸佞，贪图享乐的结果，二者呈现因果关系。所以提及唐玄宗的盛世不能不绕开安史之乱这一沉痛话题。此诗就是从杨贵妃的美貌入手创作的。作品的起首两句先写杨贵妃的美貌，没有面容的特写，只写杨贵妃舞技属昭阳第一，以其舞技高超，写其容貌华美，下笔巧妙。后来两句先以杨贵妃没有起舞发问，又以安史之乱祸起回答，一问一答间，既见到作者对唐玄宗盛世，杨贵妃美貌的惋惜之情，亦能见到作者对安禄山、史思明等人的指责与痛恨。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (316, '52a1393e3956446e97ecd9967eb164a2', '华清宫', 138, 5, -1, '年份未详', '帝业山河固，离宫宴幸频。岂知驱战马，只是太平人。', '帝业山河固，离宫宴幸频。岂知驱战马，只是太平人。', '此诗具体创作时间无考。根据作品内容，当是作者途经华清宫，目睹华清宫旧迹，心有所感，有志而发，因有此作。凡写及华清宫的作品，其内容或是目睹华清宫旧迹，感念唐玄宗盛世；或是目睹华清宫旧迹，感慨李杨二人爱情故事；或是目睹残垣断壁，心生对历史变化规律的感慨。此篇作品属于后者。作品的开篇两句先写唐玄宗开元天宝年间，江山稳固，社会承平，整个大唐帝国呈现出祥和繁盛的景象。正是基于此，唐玄宗多次游幸华清宫，并于华清宫之上大兴土木，修建宫廷楼阁，以供皇家游乐。当年繁荣之景，如今只能凭借想象得以看见。于是作者在作品的最后两句中，对当时唐玄宗繁盛之时表面隐藏下的动乱祸患予以揭示，体现出作者面对华清宫旧迹产生的对历史的感慨与反思。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (317, 'cff535a2655841c0aeda4309d83b6440', '华清宫', 65, 5, -1, '年份未详', '何事金舆不再游，翠鬟丹脸岂胜愁？重门深锁禁钟后，月满骊山宫树秋。', '何事金舆不再游，翠鬟丹脸岂胜愁？重门深锁禁钟后，月满骊山宫树秋。', '此诗具体创作时间无考。根据作品内容，当是作者途经华清宫，目睹华清宫旧迹，心有所感，有志而发，因有此作。有唐一代，凡是写及华清宫的作品，内容总是离不开唐玄宗的盛世，杨贵妃的美貌，以及安禄山的叛乱。此诗就是从作者目睹华清宫旧迹，回忆唐玄宗盛世写起的：开头两句先以为何李唐王朝不再游幸华清宫发问，言下之意是点出安史之乱是李唐皇室不再游幸驾临华清宫的直接原因；次句又以宫廷女子角度，怀念唐玄宗盛世，感叹时光迁转带来的无尽忧伤。作品的后两句着重写及华清宫秋夜：写朗月当空，华清宫内重门闭锁，没有一丝响动，只有月华流照在宫树之上。短短两句，营造出华清宫寂静凄冷令人心生悲叹的意境，使前两句中的感叹在意境的描绘中得到升华，使读者能够身临其境，与千年前的诗人同心感叹时光之迁转。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (318, '901b384159584a52870605f8d1532fbb', '华清宫二首', 95, 5, -1, '年份未详', '四郊飞雪暗云端，唯此宫中落旋干。绿树碧檐相掩映，无人知道外边寒。长生秘殿倚青苍，拟敌金庭不死乡。无奈逝川东去急，秦陵松柏满残阳。', '四郊飞雪暗云端，唯此宫中落旋干。绿树碧檐相掩映，无人知道外边寒。长生秘殿倚青苍，拟敌金庭不死乡。无奈逝川东去急，秦陵松柏满残阳。', '此诗具体创作时间无考。根据作品内容，当是作者游赏华清宫时，见华清宫破败景色，不同玄宗盛世，有感而作。全作的第一首诗借助宫殿内外的环境对比，表现出唐玄宗在开天时期，只贪图享乐，无暇顾及宫外百姓们贫苦的生活，表达作者对君王贪图享乐行为的讽刺，具有极深的警觉意味。全作起首两句以宫廷内外环境的对比开端，读来令人发叹：如何宫内雪落即干呢？后边两句从内写外，原来是宫殿内奢华生活的气息能将雪烘干，突出宫殿内生活之奢华，“无人知道”极见功力，凸显作者对君王享乐行为的讽刺。全作的第二首作品对黄昏时刻自然景物进行描写，借此突出作者感叹唐玄宗盛世不再的主题。作品的最后一句用《论语》中“子在川上曰”典故，表现时间的转移；最后一句以作者眺望远处的残阳黄昏景色，表现出作者对唐玄宗盛世时期的追忆与怀念。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (319, '3f2c5bbffc3b4250821ff85c9d582ad7', '新安道中玩流水', 95, 28, -1, '年份未详', '一渠春碧弄潺潺，密竹繁花掩映间。看处便须终日住，算来争得此身闲。萦纡似接迷春洞，清冷应连有雪山。上却征车再回首，了然尘土不相关。', '一渠春碧弄潺潺，密竹繁花掩映间。看处便须终日住，算来争得此身闲。<br>萦纡似接迷春洞，清冷应连有雪山。上却征车再回首，了然尘土不相关。', '羁旅情思，尽在尾联', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (320, 'cb165217cdd94fd195690cfe590bc81e', '东京次新安道中', 139, 28, -1, '年份未详', '贼去兵来岁月长，野蒿空满坏墙匡。旋从古辙成深谷，几见金舆过上阳。洛水送年催代谢，嵩山擎日拂穹苍。殊时异世为儒者，不见文皇与武皇。', '贼去兵来岁月长，野蒿空满坏墙匡。旋从古辙成深谷，几见金舆过上阳。<br>洛水送年催代谢，嵩山擎日拂穹苍。殊时异世为儒者，不见文皇与武皇。', '羁旅与怀古打成一片，更显深厚。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (321, '76fe96ea1da645a788a15f2b09288c5e', '登骊山高顶寓目', 140, 61, -1, '年份未详', '四郊秦汉国，八水帝王都。阊阖雄里閈，城阙壮规模。贯渭称天邑，含岐实奥区。金门披玉馆，因此识皇图。', '四郊秦汉国，八水帝王都。阊阖雄里閈，城阙壮规模。贯渭称天邑，含岐实奥区。金门披玉馆，因此识皇图。', '此诗具体创作无考。根据作品题目，当是作者登临骊山，极目远眺，有感而作。作品的起首两句写作者极目远眺，环视骊山四周所见。因秦汉两朝都曾在此定都，故有首句；“八水”一句是写八条河流环绕长安。这两句通过作者的环视，营造出一种辽阔壮大的意境。颔联两句写骊山关隘险要，城阙险峻，凸显出骊山因地势之重要而表现出的险峻。颈联两句写骊山范围之广大，能够包容岐山与渭水，凸显骊山之壮大的同时，作为帝王象征的骊山还凸显出帝王之德传播范围之广泛。作品的最后两句以骊山这一关隘都镶金嵌玉，那么宫殿内部的华丽装饰可以想见，凸显出此时唐代帝国之强盛。受制于作者身份，作者以一种皇家视角看待骊山，眼中满是骊山之雄壮，皇恩之浩荡，大唐之强盛，见诸文学，形成了这篇内容独特的描写骊山的作品。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (322, '7f3c371d4f8842a69b90f84f91a4335d', '骊山有感', 14, 0, -1, '年份未详', '骊岫飞泉泛暖香，九龙呵护玉莲房。平明每幸长生殿，不从金舆惟寿王。', '骊岫飞泉泛暖香，九龙呵护玉莲房。平明每幸长生殿，不从金舆惟寿王。', '此诗具体创作时间无考。根据作品内容，当是作者途经骊山，有感而作。首句先写骊山顶峰上有唐玄宗时期开凿的温泉数十眼，如今登临此地，还能感受到玄宗朝温泉的温暖与香气；次句写骊山之上，除了温泉，还有唐玄宗为游幸骊山，下令修建敕造的宫殿楼阁。据《长安志》记载，天宝六年，改温泉宫为华清宫，其殿名为九龙殿。殿内有温泉，亦有安禄山进贡的石莲花立于温泉汤池之中央。所以此句说，九龙殿包围着石莲花，像呵护石莲一般。作品的后两句又写唐玄宗游幸骊山之盛况以及程度之频繁。句中“长生殿”为华清宫内侍奉皇帝就寝的宫殿，所以“每幸长生殿”谓唐玄宗出游之频繁；此句写及唐玄宗出游的盛况，朝廷上下只有寿王不随从，其他的官员都在游幸之列。此句甚巧，一是点明唐玄宗出游之盛况，二是叙出唐玄宗与寿王李瑁的恩怨纠葛，点出作者对唐玄宗宠幸儿媳的指责以及讽刺态度，一句两用，不可谓不巧。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (323, '66b5c024f6a649eba425afbf954c2b77', '骊山', 96, 0, -1, '年份未详', '风摇岩桂露闻香，白鹿惊时出绕墙。不向骊山锁宫殿，可知仙去是明皇。', '风摇岩桂露闻香，白鹿惊时出绕墙。不向骊山锁宫殿，可知仙去是明皇。', '此诗具体创作时间无考。根据作品内容，当是作者途经华清宫，目睹华清宫旧迹，心有所感，有志而发，因有此作。有唐一代，凡是写及华清宫的作品，内容总是离不开唐玄宗的盛世，杨贵妃的美貌，以及安禄山的叛乱。此作就是从唐玄宗盛世角度来着手书写的：写及唐玄宗盛世，或是遥想唐玄宗盛世状况，最后归结于安史之乱后华清宫的残垣断壁；或是由华清宫的残垣断壁出发，悲叹唐玄宗盛世已不再的历史事实，此作当属后者。作品起首两句先言华清宫内的自然景物，首句先写秋日华清宫，桂花飘香；次句写华清宫内白鹿游荡，一是知华清宫内无人看管，二是华清宫内杂草丛生，果实繁茂，一片无人打扫，任其生长的景色，给人一种华清宫内无人看管后导致的凋敝破败的感受。接下来两句结束写景，开始抒情。通过对华清宫残迹的观察，作者心神不宁，感叹明皇已经仙逝。随着唐明皇仙逝的还有那个令整个中华民族自豪的大唐盛世。所以，进一步讲，作者是在感慨那个繁荣昌盛的大唐已经不再。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (324, '58ba5235c5dc43bf829f2f41dac1aa19', '题温泉', 117, 5, -1, '年份未详', '能使时平四十春，开元圣主得贤臣。当时姚宋并燕许，尽是骊山从驾人。', '能使时平四十春，开元圣主得贤臣。<br>当时姚宋并燕许，尽是骊山从驾人。', '此诗具体创作时间无考。根据作品题目，应是作者途经昭应温泉，见昭应温泉宫残迹，有感而作。唐诗中，凡创作于昭应，又是目睹昭应旧迹的作品，在内容上无外乎感叹感叹唐玄宗开元盛世；感慨李杨爱情故事几类。此作也不例外，属于歌咏感叹唐玄宗开元盛世的作品。作品开篇点题，先感叹唐玄宗开元时期能够风雨平和四十年，全部功劳都在于圣主当朝，贤臣当道，君臣相和，才能使社会繁荣，国家鼎盛。作品的后两句中，简单介绍了几位唐玄宗开元时期的贤臣：“姚宋”是指开元时期的贤能宰相姚崇、宋璟，二人为实现开元盛世奠定了基础；“燕许”指张说、苏頲二人。二人以文章著称而身份显赫，地位相当，又因二者被封为燕国公、许国公，所以世称二人为“燕许”。“尽是”句言，唐玄宗开元盛世时，还能够驾幸温泉宫，出游骊山。如今盛世不在，当朝皇帝也无心出游，导致昭应县内日益凋敝破败。作者看到此景，感慨往事，不禁泪下。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (325, '11d8e043f15a449c9bca44d4804af181', '小桃', 66, 13, -1, '年份未详', '和烟和雨遮敷水，映竹映村连灞桥。撩乱春风耐寒令，到头赢得杏花娇。', '和烟和雨遮敷水，映竹映村连灞桥。撩乱春风耐寒令，到头赢得杏花娇。', '此诗具体创作时间无考，根据作品内容，当是作于作者年少在华阴县居住时所作。作品的前两句对仗工稳，显示出作者早年应举时的准备对此时期诗文创作的影响。简单两句将自己身处敷水所见景物全部写在其中，营造描绘出一幅朦胧秀丽的山谷溪水景象。作品的最后两句写自己身处溪水边，春风拂动时的感受。此句深有寓意，“耐寒”比喻自己此时客居华州，准备应举的贫苦生活；“杏花娇”是指自己即将进士中举，是对自己的鼓励语。先以自己身处溪水时所见开始全篇，再以自己目睹景色后的感触结束，作品虽短，却寓意深厚，寄托作者在即将考试之前，对自己的勉励与激励。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (326, 'f9fe0a4d4a2746eebe0d334667162f6e', '华阴县楼', 138, 14, 889, '889年', '丹霄能有几层梯，懒更扬鞭耸翠蜺。偶凭危栏且南望，不劳高掌欲相携。', '丹霄能有几层梯，懒更扬鞭耸翠蜺。偶凭危栏且南望，不劳高掌欲相携。', '唐昭宗龙纪元年，作者避李克用乱，因隐居华阴。此诗或作于此时。全诗共四句，以作者登临华阴县楼所见开头，再以作者所见感受结束全篇，短小精悍。起首两句写作者登临华阴县楼所见。因作者此时身在高处，所以尽览空中美景：既有丹霞，亦有翠蜺。面对此种美景，作者没有产生追逐向往的心态，而是以一种“懒”的淡泊心态置身事外，冷眼看待此时的景色。实际上，作者此处将功名利禄等世人皆追随之物都以“丹霞”、“翠蜺”等美好景物的形态比喻出来，婉转地表现自己此时淡泊名利，追求潇洒隐逸的生活态度。作品的最后两句也是承此意绪，一个“偶”字说明作者登临高处次数之少，“不劳”是说自己不需要他人的提携，享受如此闲逸的生活就已经满足自己的愿望了，虽然不写淡泊名利，但淡泊名利之心态都在言外。', '唐昭宗龙纪元年，作者避李克用乱，因隐居华阴。此诗或作于此时。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (327, '2880a4fb982e4bfda8951c50d9c68ff8', '敷溪桥院有感', 138, 13, 890, '890年', '昔岁攀游景物同，药炉今在鹤归空。青山满眼泪堪碧，绛帐无人花自红。', '昔岁攀游景物同，药炉今在鹤归空。青山满眼泪堪碧，绛帐无人花自红。', '唐昭宗大顺元年，司空图移居华州敷水别墅，因有此作。作者的老师曾隐居敷水，所以作者隐居华州，访恩师故居而怀念追忆。作品的开头两句写作者目睹恩师曾炼丹药的药炉仍在，而恩师当年攀游的行迹却无处寻找，通过目睹今景感念往事，凸显出作者对恩师的怀念与追忆。作品的最后两句写山中景色：满眼碧绿，野花自红。通过自然景物的生了又长，看似不曾凋敝的景象，与已经成回忆的人事相对比，衬托之下凸显出人事的物是人非，表现出作者对恩师的怀念。', '唐昭宗大顺元年，司空图移居华州敷水别墅，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (328, '7e568878a1fb4f93b5bb4c03c7e7b88e', '华山南庙', 53, 60, -1, '年份未详', '阴山临古道，古庙闭山碧。落日春草中，搴芳荐瑶席。明灵达精意，仿佛如不隔。岩雨神降时，回飙入松柏。常闻坑儒后，此地返秦璧。自古害忠良，神其辅宗祏。', '阴山临古道，古庙闭山碧。落日春草中，搴芳荐瑶席。明灵达精意，仿佛如不隔。岩雨神降时，回飙入松柏。常闻坑儒后，此地返秦璧。自古害忠良，神其辅宗祏。', '此诗具体创作时间无考，根据作品题目，当是作者途经华山，有感而作。作品的起首两句说明游览地点，同时点题。“阴山”是指华山背阴的一面，“古道”则是指古代舒平道路。这两句中“古道”一句读来有“深山藏古寺”的幽静意境，为作者随后几句中表现自己对神明的向往以及虔诚的心情作了环境铺垫。“落日”两句点明作者游览的时间。就是在这种春日傍晚时间，作者游览华山南庙，将自己采摘的鲜花芳草贡献给庙中神灵，突出作者对待神灵的虔诚之心。“明灵”至“岩雨”几句写此时的华山忽然下雨，凸显出作者对神明的虔诚之心。接下来四句是作者用江神送给秦始皇沉璧的传说。以焚书坑儒事件塑造秦始皇暴君形象，并以沉璧传说成真表现作者痛恨暴君，向往明君治世的思想感情。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (329, '6e8eb44dcc0c475a8f5330cbeea79305', '李植题名', 141, 60, 857, '857年', '侍御史内供奉李植大中十一年十一月十三日自浙右赴阙', '侍御史内供奉李植大中十一年十一月十三日自浙右赴<br><br>阙', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (330, '3decdfe357f74cd4b9842100a748cef4', '华岳题名', 72, 60, 816, '816年', '淮西宣慰处置使门下侍郎平章事裴度、副使刑部侍郎兼御史大夫马总、行军司马太子右庶子兼御史中丞韩愈、判官司勋员外郎兼侍御史李正封、都官员外郎兼侍御史冯宿、掌书记礼部员外郎兼侍御史李宗闵、都知兵马使左骁卫将军威远军使兼御史大夫李文悦、左厢都押衙兼都虞候左卫将军兼御史中丞密国公高承简，元和十一年八月，丞相奉诏平淮右，八日，东过华阴，礼于岳庙，总等八人，实备将佐以从。', '淮西宣慰处置使门下侍郎平章事裴度、副使刑部侍郎兼御史大夫马总、行军司马太子右庶子兼御史中丞韩愈、判官司勋员外郎兼侍御史李正封、都官员外郎兼侍御史冯宿、掌书记礼部员外郎兼侍御史李宗闵、都知兵马使左骁卫将军威远军使兼御史大夫李文悦、左厢都押衙兼都虞候左卫将军兼御史中丞密国公高承简，元和十一年八月，丞相奉诏平淮右，八日，东过华阴，礼于岳庙，总等八人，实备将佐以从。', '无', '赴淮西平吴无济过西岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (331, 'aeaef707e50749038ac4918d5dfb4086', '李贻孙题名', 142, 60, 851, '851年', '福建都团练观察处置等使兼备御史中孙李贻孙，大中五年七月二廿七日，□镇将男意承文尉□复含昭，谒岳而退。', '福建都团练观察处置等使兼备御史中孙李贻孙，大中五年七月二廿七日，□镇将男意承文尉□复含昭，谒岳而退。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (332, '5c4ef5b3baeb41a9b044be57d9f6764b', '严□题名', 143, 60, -1, '年份未详', '监察御史里行严□，乙卯岁八月庚寅，奉使东周，展敬。', '监察御史里行严□，乙卯岁八月庚寅，奉使东周，展敬。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (333, 'c18e0739b23e4e59b7262d9174c616f7', '华山庙', 60, 60, -1, '年份未详', '金天庙下西京道，巫女纷纷走似烟。手把纸钱迎过客，遣求恩福到神前。', '金天庙下西京道，巫女纷纷走似烟。手把纸钱迎过客，遣求恩福到神前。', '此诗具体创作时间无考。根据作品题目，当是作者途经华山庙时有感而作。此诗内容简单，语言质朴，只言片语间就可以将这篇作品讲得很透彻：作品大意不过是作者途经华山庙，写华山庙里香火鼎盛，信徒成群的热闹景象。但是只要将作品同张籍生活的年代相联系，就可发现，这篇作品中暗含着深深讽刺。张籍的生命历程有一部分是在唐宪宗的统治中度过的。唐宪宗是个热衷求仙访药的皇帝，早年间就问及群臣求仙之事，时至晚年，唐宪宗对仙人的渴求程度更疯狂，甚至到了任命方士作官的地步。在古代，皇帝是全民明星，他的行为举止影响着人民百姓，所以在那个皇帝求仙不求贤的年代，百姓们也就学起皇家风范，以自己微薄之力为自己的人生寻求一点解脱。所以，作者在诗中写道，人们将自己的钱送给会巫术的人，祈求上苍给予他们幸福与恩赐，至于能否得到恩赐，答案是简单明了。此作的妙处就在于此处只写人们求仙的疯狂，对疯狂背后愚昧的讽刺，是在作品之外表达的。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (334, '2425a857c73341f48567c772941e583e', '华山寻隐者', 144, 60, -1, '年份未详', '自入华山居，关东相见疏。瓢中谁寄酒，叶上我留书。巢鸟寒栖尽，潭泉暮冻余。长闻得药力，此说又何如。', '自入华山居，关东相见疏。瓢中谁寄酒，叶上我留书。巢鸟寒栖尽，潭泉暮冻余。长闻得药力，此说又何如。', '此诗具体创作时间无考。根据作品内容，全诗写作者隐居华山时的生活方式以及心迹表达，当推定创作时间为作者隐居华山之时。作品开头两句先写自从作者隐居华山之后，与旧日宾朋往来甚少，有一种“兴来每独往”的闲逸之气。颔联两句写作者隐居时候，瓢中有酒，叶上著书：不管在物质上还是精神上，作者都达到了自满自足的地步，可见作者在隐居生活中自得其乐，意趣非凡。颈联两句写山中景色，点明创作此诗的时间当是秋末冬初寒冷之时：巢鸟栖尽言山中幽静，无鸟雀嘈杂之声；潭泉结冰状山中寒冷，万物皆生冰霜之色。尾联两句从隐居生活以及山中美景的描述中跳脱出来，表白作者长时隐居以来的心迹。这两句是说，目睹如此幽静美景，体验这般自得生活，纵然没有著书立说传名后世，在山中隐居一生，也算值得，足见作者隐居之志，闲逸之心。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (335, '60b38856b982445f9faea2eac143a034', '宿华山', 107, 14, -1, '年份未详', '异境良难测，非仙岂合游。星辰方满岳，风雨忽移舟。喷月泉垂壁，栖松鹤在楼。因知修养处，不必在嵩丘。', '异境良难测，非仙岂合游。星辰方满岳，风雨忽移舟。喷月泉垂壁，栖松鹤在楼。因知修养处，不必在嵩丘。', '此诗具体创作时间无考，根据作品题目，当是作者夜宿华山，眼见华山之闲逸秀美景色，有感而作。作者在起首两句提出了“异境难寻，仙侣难觅”的疑问，目的是以此起兴，引出下文对华山仙境的描写。颔联两句写华山之高，离天尤近，所以能够看清星辰，也能体会风雨，为全诗的写作营造出一片自在幽静的意境，令读者在此意境中遨游腾飞，如在仙境。颈联两句着重写“喷泉”以及“栖鹤”两种意象。先写喷泉，反而写月光垂壁，显示出一种清丽意境；后写栖鹤，又写高楼，凸显出仙逸的意境。尾联两句写作者对这种景色、意境的观后感，显示出作者对这种景色的喜爱之情。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (336, '938ce18d76904e4c8f1af09c682f2d4a', '华山上方', 145, 60, -1, '年份未详', '独上上方上，立高聊称心。气冲云易黑，影落县多阴。有云草不死，无风松自吟。会当求大药，他日复追寻。', '独上上方上，立高聊称心。气冲云易黑，影落县多阴。有云草不死，无风松自吟。会当求大药，他日复追寻。', '此诗具体创作时间无考。根据作品内容，当是作者登临华山大上方，观危岩美景，心旷神怡，有感而作。华山大上方，位于华山北峰之西。因靠近悬崖峭壁，所以人迹罕至，以至于成为华山道士修炼的一处绝佳场所。大上方的景色也因为人迹罕至，显得更加秀美，给人一种“无限风光在险山”的感觉。作品的起首两句是全诗的总括句，先言作者登临华山大上方，观景感物，实在是有称心之处。接下来三联写华山大上方之上的使作者称心愉悦的美景。颔联两句写大上方之天空美景：先写上方之上天气变化多端，风云变幻，一时间晴天也能变成阴霾密布的景色；又写华阴县日常不充分，经常没有阳光普照，借此凸显出大上方之高，能遮云蔽日。颈联两句从大上方的地面景色着手进行描写：先写大上方之上云气丰足，水草丰美，各种奇花异草都能在此生长；又写大上方之上纵然无风，也能使其上的松树发出阵阵松涛声音。作品的最后一联写上方之上如此美景给作者带来心灵上的震撼：作者见如此美景，不禁生出归隐之心，想要在上方之上修炼求药，寻求人生之大道。全诗妙处在于写景，即使是读过二、三联句，未尝亲眼目睹上方之美，也能心生与作者同样的心理愿望', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (337, '0cdf154c7aa1439389ef82170404b640', '野狐泉柳林', 61, 59, 804, '804年', '去日野狐泉上柳，紫牙初绽拂眉低。秋来寥落惊风雨，叶满空林踏作泥。', '去日野狐泉上柳，紫牙初绽拂眉低。秋来寥落惊风雨，叶满空林踏作泥。', '唐德宗贞元二十年，作者自长安赴洛阳途中作。作品的前两句写作者上次途经野狐泉时，泉边呈现的一片春意盎然的优美景色：从泉边柳树着笔，写柳树抽芽，短小刚好拂眉。在写柳树初生之可爱状态时，也凸显出泉边春日景色的优美多姿，饶有意趣。作品的后两句写作者此时途经野狐泉的景致：此时正值秋日，泉边也是寒风瑟瑟，秋雨凄凄，落叶满地，踩踏成泥，营造一片萧条凄冷的景致。此时所见之景致同春日雅致精美的景色相对比，凸显出春去秋来之变化，突出作者对时光流逝的伤感情绪。', '唐德宗贞元二十年，作者自长安赴洛阳途中作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (338, '3e964714de124225b4f56e6cc1bb2c88', '野狐泉', 96, 59, -1, '年份未详', '潏潏寒光溅路尘，相传妖物此潜身。又应改换皮毛后，何处人间作好人。', '潏潏寒光溅路尘，相传妖物此潜身。又应改换皮毛后，何处人间作好人。', '此诗具体创作时间无考，当是作者途经野狐泉，有感而作。野狐泉本是位于潼关西边的泉水名称，因民间传说此处有野狐出现，因此得名。此处又有驿站，名称野狐泉驿。此地位于潼关之西，是来往长安的必经之路，所以有唐一代，多有作者于此作诗。罗邺生活在唐僖宗年间，此时的大唐王朝经历了安史之乱，虽然有短暂的唐宪宗“中兴”气象，国运还是转向了衰弱。此时的唐朝比起开天盛世来说，社会政治环境都黑暗了许多，不能称得上是好的时代。因此作者途经野狐泉店，用民间野狐传说，感叹时代的黑暗与坏境的恶劣。这些感叹都是在最后两句里发出的：作者先写野狐必将换了皮毛，打扮成人的模样，造福世间。紧接着作者说这世间哪还有好的地方能够容纳下一个造福人间的好人呢？作者的感叹可算直白地提到了自己生活的那个年代的黑暗与痛苦。此诗虽然简短，但仍然为我们了解晚唐的概况提供了宝贵的资料。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (339, '5f7b2f51ce944dce9f72998bb7e4231c', '题潼关楼', 101, 15, -1, '年份未详', '客行逢雨霁，歇马上津楼。山势雄三辅，关门扼九州。川从陕路去，河绕华阴流。向晚登临处，风烟万里愁。', '客行逢雨霁，歇马上津楼。山势雄三辅，关门扼九州。川从陕路去，河绕华阴流。向晚登临处，风烟万里愁。', '此诗具体创作时间无考。根据作品题目，当是作者行至潼关，有感而作。题目写“题潼关楼”，作品的起首两句先言作者登临潼关楼之缘由点题：作者行路途中，恰逢雨霁，于是歇马登上关楼，想要趁着雨停后的晴朗天气，一览远处胜迹。作品的颔联就是在作者登临潼关楼后所见。因是雨过天晴，所以作者所见便是一览无余：先从潼关的山势写起，写其重峦叠嶂之险峻；又从其重要地理位置着笔，凸显其扼守关中的重要地位。颈联两句亦是眺望所见。与颔联不同的是，此两句是写潼关河水之景：河水依关山成势，自华阴向南流去，蜿蜒曲折，连绵不绝。作品的尾联两句从上三联写景跳出，转入作者观景后的感受描写：作者晚登津楼，眺望远方，感慨古今，心中不禁生出愁思。这种愁思，作者没有确指，或是感古今胜事；或是叹人生不平；或是起思乡之情，不得而知。这也是这样写作的巧妙之处，不同人读到此处有不同感觉，处理甚妙。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (340, '4ac182e095d540f4ad70f4d5c7a43d3b', '经潼关赠宇文十', 146, 15, 804, '约804年', '古有弓旌礼，今征草泽臣。方同白衣见，不是弃繻人。', '古有弓旌礼，今征草泽臣。方同白衣见，不是弃繻人。', '唐德宗贞元末年（约804年），窦群受到韦夏卿的援引得官，此诗或作于入京上任途中。作品的起首两句用《左传》中齐侯用弓招士，用旌招聘大夫的典故，后世用此典故比喻朝廷招聘官员。次句因为作者长期赋闲在家，自称“草泽臣”，这一句表现作者受到朝廷征召的感谢之情。作品的结尾两句表现作者心迹：“白衣”应是宇文十。唐代，通过除进士科目以外的科目入朝做官之人，被称为“一品白衫”；“弃繻人”一句人用终军典故。终军典故是说终军曾于潼关立下誓言“大丈夫西游不得志绝不出关”，并将出关的凭证（繻）丢弃，后世常用此典故比喻志向远大。作者这里用此典故，表白自己的理想。这种人生理想实际上同许多途经潼关，入朝为官之人的理想相比，属于一种不怎么积极的人生理想。当然，这种不怎么积极的人生理想同作者早年隐居山林的人生经历有很大的关系。入朝做官，只是为了应付朋友对自己的期望，也不为了立名立德，所以作者的人生理想也就不那么积极上进了。', '唐德宗贞元末年（约804年），窦群受到韦夏卿的援引得官，此诗或作于入京上任途中。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (341, '8c2bd681339f45c88cbf4f946f1a8a0b', '过潼关', 73, 15, -1, '年份未详', '地形盘屈带河流，景气澄明是胜游。十里晓鸡关树暗，一行寒雁陇云愁。片时无事溪泉好，尽日凝眸岳色秋。麈尾角巾应旷望，更嗟芳霭隔秦楼。', '地形盘屈带河流，景气澄明是胜游。十里晓鸡关树暗，一行寒雁陇云愁。片时无事溪泉好，尽日凝眸岳色秋。麈尾角巾应旷望，更嗟芳霭隔秦楼。', '此诗具体创作时间无考。根据作品题目，当是作者行至潼关，有感而作。题目写“潼关”，作品的起首两句先总写潼关点题：首句先写潼关的地形盘曲遒劲，摹状潼关之险峻；次句写潼关周围的景色晴明，一览无余，可称得上是古之胜迹。颔联两句是作者登临潼关远望所见，因是晴天登临，所以作者所见便是一览无余：先用孟尝君鸡鸣狗盗典故写及潼关胜景；又写秋日潼关，鸿雁过陇，营造一片静美忧愁的意境。颈联两句仍写作者所见。尾联两句值得称道，是写作者想象。在作者的幻想中，作者化身为一位手拿塵尾，拢发角巾的隐居道士形象，心中顿时生发旷达情绪，表现出作者受到所见景物的影响，生发出隐居山林的想法。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (342, 'fce84727f2fa40539e69d0ad60b1c270', '潼关兰若', 13, 15, -1, '年份未详', '来往几经过，前轩枕大河。远帆春水阔，高寺夕阳多。蝶影下红药，鸟声喧绿萝。故山归未得，徒咏采芝歌。', '来往几经过，前轩枕大河。远帆春水阔，高寺夕阳多。蝶影下红药，鸟声喧绿萝。故山归未得，徒咏采芝歌。', '此诗具体创作时间无考。根据作品内容，当是作者途经潼关，游潼关寺院，有感而作。作品的起首两句点明作者途经潼关，目睹兰若寺院次数之多，亦在第二句中点出兰若寺院靠近潼关河岸，环境优美。因寺院近水，所以颔联先写作者在寺院中看到的潼关河水之上的孤帆远舟，描绘一片辽阔的意境；次句中“夕阳多”是说寺院之高，作者能够轻易看到夕阳，营造一幅绝美的山边落日图景。颔联中点出此诗的创作时间应是春季，所以作者在颈联中又对寺院中的春日景象做了一番描绘：写蝴蝶翻飞，鸟雀喧噪，红药初开，绿箩才绿的初春景象，趣意横生。受此环境的影响，本来想早日回家的作者，打消了回家的念头，生发出吟咏《采芝歌》的心绪。《采芝歌》原本是汉代有名的四位隐士商山四皓所作，在这里，作者用其说明自己的归隐之心。全诗从水景远处写起，一步步写及寺院春景近处，由远及近，条理清晰。尤其是颈联描绘寺院初春景象的句子，描写到位，读来如在眼前。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (343, '805b4c0aaf8442bf90e5f6a8bf78316c', '题潼关兰若', 147, 15, 866, '866年', '潼津罢警有招提，近百年无战马嘶。壮士不言三尺剑，谋臣休道一丸泥。昔时驰道洪波上，今日宸居紫气西。关吏不劳重借问，弃繻生拟入耶溪。', '潼津罢警有招提，近百年无战马嘶。壮士不言三尺剑，谋臣休道一丸泥。昔时驰道洪波上，今日宸居紫气西。关吏不劳重借问，弃繻生拟入耶溪。', '唐懿宗咸通七年，皮日休进京应举不第。返乡途中，路经潼关，游潼关寺院，生隐逸之心，遂有此作。作品题目中所言“兰若”，是梵语“阿兰若”的简称，也就是寺院的意思。作品的起首两句写寺院，点题也：先点明潼关河岸之上有名为“招提”的寺院，环境清幽静寂，近百年来都没有遭遇过兵乱战争。颔联中“一丸泥”是用《东观汉记》中王元的典故。王元请求用一丸泥就可以守卫函谷关，这是说函谷关易守难攻的险要地形。皮日休在这里没有采用典故的本意，而是转换其意，表达在招提寺院精深佛法的感召下，无人能够再言追名逐利之俗事。颈联两句借招提寺的视角，写其见到的古今之事。作品的最后一联用“终军”典故：终军曾于潼关立下誓言“大丈夫西游不得志绝不出关”，并将出关的凭证（繻）丢弃。作者在最后一联用此典故，是为了告诉读者自己是一位拥有远大志向的少年。但就是这样一位有着远大志向的少年早早生发出“入耶溪（神仙居处，说明自己有隐居之心）”的想法，联系作者此时科举落第，便可知道社会环境恶劣至极，致使这样一位自比终军的少年都生出隐逸之心，真是可悲可叹：悲皮日休之命运坎坷；叹社会世道之黑暗无情。', '唐懿宗咸通七年，皮日休进京应举不第。返乡途中，路经潼关，游潼关寺院，生隐逸之心，遂有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (344, 'a0a8bb61bc90471fad78aaa6ce9bdfbf', '秋日行次关西', 13, 0, 808, '约808年', '金风荡天地，关西群木凋。早霜鸡喔喔，残月马萧萧。紫陌秦山近，青枫楚树遥。还同长卿志，题字满河桥。', '金风荡天地，关西群木凋。早霜鸡喔喔，残月马萧萧。紫陌秦山近，青枫楚树遥。还同长卿志，题字满河桥。', '此诗具体创作时间无考，当是作者途经关西驿有感而作。作品的开头两句就有一种雄浑的力量感：首句中“荡”字寄寓豪迈气概，次句“凋”字又直接写群木凋零的结果，亦有一种坚硬的质感，所以总的来说有一种力量感蕴藏于这两句中。颔联两句以对仗的形式，写关西驿早晨与傍晚的景致，尤其是次句以马嘶为重点写起鸣叫的声音，凸显出潼关外尖利硬朗的秋日特点。颈联两句亦是以对仗的形式，点明关西驿站近秦远楚的地理位置。值得注意的是，这里作者选择“楚树”与“秦山”相对仗，绝非偶然。试想，如果作者不是生为楚人，怎么会在作诗过程中如此凸显楚地意象呢？所以，由此可以看出本籍文化对作者创作的影响。尾联两句是作者表白自己心迹的部分，在这里作者与汉人司马相如自比，凸显出作者对自己未来的信心及希望。', '唐宪宗元和三年，作者首次自家至长安应举，途经关西驿，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (345, '6f14b6abb7de41fdb0ec8227e5c22c4f', '湖城东遇孟云卿，复归刘颢宅宿宴，饮散因为醉歌', 91, 18, 758, '758年', '疾风吹尘暗河县，行子隔手不相见。湖城城南一开眼，驻马偶识云卿面。向非刘颢为地主，懒回鞭辔成高宴。刘侯叹我携客来，置酒张灯促华馔。且将款曲终今夕，休语艰难尚酣战。照室红炉促曙光，萦窗素月垂文练。天开地裂长安陌，寒尽春生洛阳殿。岂知驱车复同轨，可惜刻漏随更箭。人生会合不可常，庭树鸡鸣泪如线。', '疾风吹尘暗河县，行子隔手不相见。湖城城南一开眼，驻马偶识云卿面。向非刘颢为地主，懒回鞭辔成高宴。刘侯叹我携客来，置酒张灯促华馔。且将款曲终今夕，休语艰难尚酣战。照室红炉促曙光，萦窗素月垂文练。天开地裂长安陌，寒尽春生洛阳殿。岂知驱车复同轨，可惜刻漏随更箭。人生会合不可常，庭树鸡鸣泪如线。', '唐肃宗乾元元年，杜甫遇孟云卿于湖城县东，此诗或作于此时。全作可根据内容大致分为三部分。前四句为第一部分，写作者与孟云卿相遇的偶然过程：前两句写此日湖城驿沙尘漫天，天气恶劣，与朋友相遇不相识。后两句写作者与友人相遇，“偶”字突出相遇的偶然性，令读者能够体会作者与友人相遇时的惊喜感受。“向非”至“寒尽”几句是全诗的第二部分，主要写作者邀请友人同去刘颢家作客的过程。在此其中，作者对酒宴场面的描写甚是细致：既有“置酒张灯”这样的动态场面，又有“照室红炉”这样的静态场面。这样细致的场面描写使我们可以借此一睹唐时家宴的醉人场面。作品的最后四句是全诗的第三部分，主要是作者抒怀。作者以时间的流转开始，想起与友人的偶遇，感叹人生事物不可捉摸的偶然性，可见作者同友人的深厚友谊。', '唐肃宗乾元元年，杜甫遇孟云卿于湖城县东，此诗或作于此时。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (346, 'a421be3737334c228ebe68d504884864', '题湖城县西道中槐树', 95, 18, -1, '年份未详', '零落欹斜此路中，盛时曾识太平风。晓迷天仗归春苑，暮送鸾旗指洛宫。一自烟尘生蓟北，更无消息幸关东。而今只有孤根在，鸟啄虫穿没乱蓬。', '零落欹斜此路中，盛时曾识太平风。晓迷天仗归春苑，暮送鸾旗指洛宫。一自烟尘生蓟北，更无消息幸关东。而今只有孤根在，鸟啄虫穿没乱蓬。', '此诗具体创作时间无考，根据作品题目，当是作者客居湖城县时，有感而作。首联先写槐树点题。首句写道中槐树“零落攲斜”起兴，描绘槐树衰败凋敝的近况，暗示大唐衰败的现状。次句是作者幻想，因湖城县在唐两京道中，所以作者幻想道中槐树必然也曾目睹大唐盛世时的太平气象。颔联两句写作者幻想槐树应该目睹自长安向洛阳而去的天子出行的盛大队伍，喧闹的场面。作品的前两联以槐树之眼写大唐盛世气息，后两联转折，再以槐树之言婉转描述唐朝凋敝惨淡的现状。颈联两句写安史之乱后，唐朝衰败，王室也不再巡幸东都，所以也不再见到天子巡游的盛大场面。作者在尾联里以槐树的衰败景象，暗指唐王朝此时衰败惨淡的社会现状与统治现状。用语婉转却表意直白，使人读来一惊。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (347, '0988cbb83aa24b849360bca10b3c1d66', '任弘农尉献州刺史乞假还京', 14, 21, 839, '839年', '黄昏封印点刑徒，愧负荆山入座隅。却羡卞和双刖足，一生无复没阶趋。', '黄昏封印点刑徒，愧负荆山入座隅。却羡卞和双刖足，一生无复没阶趋。', '唐文宗开成四年，李商隐由秘书省校书郎调任弘农县尉。由于减免对受冤屈犯人的处刑，触怒上司孙简。作者愤而辞官，此作正是写给州刺史请求辞官的。作品首句写作者即将辞官，封存官印，清点个人管辖下的囚徒数量，点明写作此诗的缘由。次句写作者望着就在眼前的荆山，联系到自己对下管辖百姓，对上趋走逢迎的生活状态，生发出愧疚的感情。作品的最后两句，作者将门前的荆山同卞和献玉的那座荆山相联系，说明自己对卞和无需趋走逢迎一事的羡慕之情。联系到作者愤而辞官的事实，可以体会到作者狷介不屈的性格。', '唐文宗开成四年，李商隐由秘书省校书郎调任弘农县尉。由于减免对受冤屈犯人的处刑，触怒上司孙简。作者愤而辞官，此作正是写给州刺史请求辞官的。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (348, 'f2f87363f771410f92c81c96ff1a1a88', '陕城即事', 137, 23, 840, '约840年', '左右分京阙，黄河与宅连。何功来此地，窃位已经年。天下才弥小，关中镇最先。陇山望可见，惆怅是穷边。', '左右分京阙，黄河与宅连。何功来此地，窃位已经年。天下才弥小，关中镇最先。陇山望可见，惆怅是穷边。', '唐文宗开成五年，姚合曾任陕虢州观察使，此诗或做于此时。作品首句点出陕州所处之地理位置在西京与东都洛阳之间，故称“分京阙”。次句写出陕州周围有黄河环绕，所以称“与宅连”。首联点明陕州地理位置与周遭环境同时，营造出辽阔壮丽的山川意境，可谓作品起首奇高。颔联中可以看出作者在陕州任官已有多年时间。颈联两句则是作者登临高处，远望所感。关中当指长安皇城，作者以关中镇最先，写出自己不不甘于在陕州长期为官的寥落无聊的生活，心中所向的还是那个锦楼玉瓦的皇城长安。如果不能抵达那个盛世繁华的长安皇城，作者在最后一联中还表现出自己对边塞的向往，希望自己能够有机会抵达边塞，建立功业，实现自己的人生价值。不过此时的作者仍然没能脱离现实，从高处下来后，自己还是要回归现实，回归那个无聊苦闷的穷边生活之中，这也是作者在最后一句中想要表现的无奈心情。', '唐文宗开成五年，姚合曾任陕虢州观察使，此诗或做于此时。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (349, '256968a63bbd457fbb37b44794344b3e', '硖石遇雨，宴前主簿从兄子英宅', 11, 24, 743, '743年', '县城苍翠里，客路两崖开。硖石云漠漠，东风吹雨来。吾兄此为吏，薄宦知无媒。方寸抱秦镜，声名传楚材。折腰五斗间，黾勉随尘埃。秩满少余俸，家贫仍散财。谁言次东道，暂预倾金罍。虽欲少留此，其如归限催。', '县城苍翠里，客路两崖开。硖石云漠漠，东风吹雨来。吾兄此为吏，薄宦知无媒。方寸抱秦镜，声名传楚材。折腰五斗间，黾勉随尘埃。秩满少余俸，家贫仍散财。谁言次东道，暂预倾金罍。虽欲少留此，其如归限催。', '唐玄宗天宝二年，刘长卿因家中贫困，东游梁、宋等地，求亲友资助。此诗或作于此时。诗题中有“硖石”，所以作品开头四句先描述硖石县景象点题：硖石县里，苍翠掩映，一片和谐无事景象。从“吾兄”至“谁言”句，是从作者兄弟的近况以及才能、为人等方面进行着手描写：先写作者兄弟在硖石县为官吏，过着一种薄宦闲居，却苦于无法向上升官的生活；又说作者从兄有才，既能像“秦镜”一般明察秋毫，又能像屈原一般善文能写；再次说从兄虽然为官县里，却只是为了维持生计，没有功利之心而已；最后以从兄家贫仍散财的美好品德结束这段夸赞之语。实际上，这段满是溢美之辞的言论，也符合此时求亲友资助的作者身份。作品的最后两句写作者在从兄这里受到了优渥的待遇，虽然想长期待在这里，但归家期限将至，只得无奈离去，表现出作者对从兄款待自己的感激之情。', '唐玄宗天宝二年，刘长卿因家中贫困，东游梁、宋等地，求亲友资助。此诗或作于此时。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (350, 'c7ead09fb8a840efb69356da8dfe4e56', '发硖石路上却寄内', 99, 24, -1, '年份未详', '莎栅东行五谷深，千峰万壑雨沈沈。细君几日路经此，应见悲翁相望心。', '莎栅东行五谷深，千峰万壑雨沈沈。细君几日路经此，应见悲翁相望心。', '此诗具体创作时间无考。根据作品题目，当是作者朝发硖石，有感而作。作品的前两句点明作者此去方向，以及对行进路程上的景色作了简单描绘：首句先言作者东去的方向以及山谷深处幽静的美景；次句写深谷之中所见，尽是千峰万壑，又值雨水盛大，将山谷中的景物洗刷一新，营造出山谷中行走，幽静的意境。作品的后两句从写景中跳出，转入抒情：作者感念友人前日从此经过，痴痴望向友人前进的路向，表现出作者对友人深深的情意。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (351, '89e2d6d7680d43bbaaa20839fa83ba51', '次硖石', 72, 24, 817, '817年', '数日方离雪，今朝又出山。试凭高处望，隐约见潼关。', '数日方离雪，今朝又出山。试凭高处望，隐约见潼关。', '唐宪宗元和十二年，唐皇下令平定淮西吴元济，韩愈时任军中行军司马，行军至硖石，有感而作。作品的前两句可视为全诗的起兴句，先对行进路程以及途中所见作简单描述：几日之前才离开雪山，今日又要行军离开山川。行军途中，景色变幻不一，也为作者提供了广泛的诗思。作品的后两句通过写景言情：这里的潼关可以视为长安的象征，权力中心的象征，优游生活的象征。行走在外的作者所面对的坏境固然不如长安的生活环境，所以作者试着向西远望，看是否能望见潼关所在，抒发自己对潼关，对长安的怀念情感。“试”字为尝试，看不看得见潼关都有可能，但作者还是做出了试探举动，表现出作者对潼关、长安的深挚想念。', '唐宪宗元和十二年，唐皇下令平定淮西吴元济，韩愈时任军中行军司马，行军至硖石，有感而作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (352, '5c4b99ff532440b3818eac738f9c2886', '硖石遇赦', 117, 24, 821, '821年', '天网初开释楚囚，残骸已废自知休。荷蓑不是人间事，归去沧江有钓舟。', '天网初开释楚囚，残骸已废自知休。荷蓑不是人间事，归去沧江有钓舟。', '此诗或作于唐穆宗长庆元年，当是作者在硖石县听闻赦免的消息，有感而作。作品的开头两句中：“天网”出自《老子》“天网恢恢，疏而不失”句，谓天道广大，在这里指皇帝发出的赦令。“楚囚”是作者自称。因元和六年时，作者因罪贬谪峡州司仓参军，在巴山楚水凄凉地过了十年囚徒似的逐臣生活，所以作者将自己称之为“楚囚”，这段经历这是判断此作创作时间的重要依据。次句是说十年贬谪生活给作者在身心上都带来了折磨，所以作者称自己“残骸已废”，是对自己失去信心的表现。作品的后两句承接上一句，写作者此时对生活、前途失去信心，生发出归隐之心，想要归去江边做一位渔樵翁。事实上，作者也是这样行动的，这次赦免之后，作者放舟南下，遍游江南，最终在嵩山隐居。应该说明的是，这首作品呈现出的内容情感，完全可以作为一位贬谪孤臣的心迹表白，从他的表白中，观一斑而窥全豹，知道当时贬谪生活对臣子身心的摧残', '此诗或作于唐穆宗长庆元年，当是作者在硖石县听闻赦免的消息，有感而作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (353, '9f60a249be9943318cde02292322c3e2', '绣岭宫词', 148, 24, -1, '年份未详', '春日迟迟春草绿，野棠开尽飘香玉。绣岭宫前鹤发翁，犹唱开元太平曲。', '春日迟迟春草绿，野棠开尽飘香玉。绣岭宫前鹤发翁，犹唱开元太平曲。', '此诗具体创作时间无考，根据作品内容，当是作者途经绣岭宫，有感所作。绣岭宫是唐高宗时期所修建，是唐时皇帝巡幸至东都时居住的行宫，从这个层面来讲，绣岭宫可视作唐王朝鼎盛时期的代表意象。但安史之乱后，皇帝不复东游，绣岭宫因此呈现出废弃破败景象。因此，唐人常目睹绣岭宫景象，发出盛世不再的感叹，与主题为华清宫的作品在内容情感上有异曲同工之妙。作品的开头两句先言作者目睹绣岭宫内的自然景象起兴：草绿花开，正是一年最好的季节；万物复苏，趁此机会可以赏春游园，营造出一幅草绿花开的秀美景致。作者写绣岭宫内绿草红花一片春意实则是写绣岭宫内的凋敝破败景致，这是从绣岭宫内景物的描写出发感叹盛唐不再。作品的最后两句写绣岭宫内人事，与前两句实相枝干。这两句写绣岭宫内白头翁，至今犹唱太平曲。作者化身为白头翁形象，抒发自己对盛唐不再的感叹以及对盛唐的追忆与怀念，比上两句表达的情意更近一层。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (354, '80d43dca80e7492185abdee4dd0ba21a', '开元杂题七首 绣岭宫', 149, 24, -1, '年份未详', '绣岭花残翠倚空，碧窗瑶砌旧行宫。闲乘小驷浓阴下，时举金鞭半袖风。', '绣岭花残翠倚空，碧窗瑶砌旧行宫。闲乘小驷浓阴下，时举金鞭半袖风。', '此诗具体创作时间无考，根据作品内容，当是作者途经绣岭宫，有感而作。生活在中晚唐时期的诗人喜爱在途经旧的行宫时，作诗表达自己对盛唐气象的追忆与怀念，此诗的前两句也有此意韵，但后两句却另辟蹊径，抒发作者乘车游玩时的闲适娱乐心态。作品的前两句写作者乘车游玩时所见绣岭宫内的破败景象，表达作者对盛唐气象的回忆与怀念。作品的后两句，作者没有承接这种情感继续书写，而是宕开一笔，转向自己乘车出游，内心情感的描写。“闲”字凸显出此时作者的闲适心情，“时”字则凸显出作者此时乘车出游所具有的一种娱乐心态。全作的前半首看似落入俗套，而作者在后半首里又转向自我内心书写，这种转向看似突兀，实际上又属于作者出游所感，符合逻辑，饶有意趣。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (355, '476b3edb009c477cb6b7d8000ab97df0', '渑池', 150, 26, -1, '年份未详', '西秦北赵各称高，池上张筵列我曹。何事君王亲击缶，相如有剑可吹毛。', '西秦北赵各称高，池上张筵列我曹。何事君王亲击缶，相如有剑可吹毛。', '此诗具体创作时间无考，根据作品内容，当是作者途经渑池，感怀而作。《史记》记载，秦赵两国曾在渑池会盟。席间秦王使赵王鼓瑟，蔺相如使秦王击缶；秦王使赵王献出十五城，蔺相如使秦王献出都城咸阳。作者在开头两句先写秦赵会盟一事，在后两句中写出秦赵会盟过程中，秦赵两国政治博弈的过程。从作者的叙述中，可以看出作者对于席间蔺相如为维护赵国地位作出的种种行为表示称赞与认同。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (356, 'c2f2134266ee408ab5a43af14ff3ace6', '送许拾遗恩归江宁拜亲', 6, 57, 758, '758年', '诏书下青琐，驷马还吴洲。束帛仍赐衣，恩波涨沧流。微禄将及亲，向家非远游。看君五斗米，不谢万户侯。适出西掖垣，如到南徐州。归心望海日，乡梦登江楼。大江盘金陵，诸山横石头。枫树隐茅屋，橘林系渔舟。种药疏故畦，钓鱼垂旧钩。对月京口夕，观涛海门秋。天子怜谏官，论事不可休。早来丹墀下，高驾无淹留。', '诏书下青琐，驷马还吴洲。束帛仍赐衣，恩波涨沧流。微禄将及亲，向家非远游。看君五斗米，不谢万户侯。适出西掖垣，如到南徐州。归心望海日，乡梦登江楼。大江盘金陵，诸山横石头。枫树隐茅屋，橘林系渔舟。种药疏故畦，钓鱼垂旧钩。对月京口夕，观涛海门秋。天子怜谏官，论事不可休。早来丹墀下，高驾无淹留。', '唐肃宗乾元元年，许登获皇恩假归江宁省亲，有诗送行。作品的起首八句点明题目，说清许登回家的缘由：先从许登获诏书一事说起，接着说到许登因皇恩可以归家省亲一事。从“微禄将及亲”一句可以看出久居外地多年的作者对即将回家的友人的羡慕。“适出”至“乡登”句言友人领了诏书后就登楼远望，期望能够早日回家。从友人登楼远眺的急切，可以看出友人对家乡的想念之深。“归心”两句对仗极工，将友人思乡之情同友人的登楼动作全部写出，令读者读来身临其境，友人登楼似乎就在眼前，可以更为真切地体验友人这样的细微感情。“大江”至“观涛”句是作者遥想友人家乡，描写其景色的句子：先以鸟瞰的形式描写大江及石山，为画面描上一层底色；再突出描写枫林之内有茅屋，橘林之下系渔舟，这可算作近景；再在近景之内描画人物的活动，种药垂钓，对月观涛。整整三层画面，整体营造出一种闲逸自在的意境，令人读来心神向往。作品的最后几句是惯常的作者对友人的叮嘱、嘱托句。在这句中，作者叮嘱友人，虽然家乡优美，但也不能忘记自己谏官的身份，应早早归来，为王朝尽责。', '唐肃宗乾元元年，许登获皇恩假归江宁省亲，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (357, '99f9581a50a14f9f83602116a40d5223', '送刘郎将归河东（同用边字）', 6, 57, 758, '758年', '借问虎贲将，从军凡几年。杀人宝刀缺，走马貂裘穿。山雨醒别酒，关云迎渡船。谢君贤主将，岂忘轮台边。', '借问虎贲将，从军凡几年。杀人宝刀缺，走马貂裘穿。山雨醒别酒，关云迎渡船。谢君贤主将，岂忘轮台边。', '唐肃宗乾元元年，刘郎将归河东，有诗送行。作品的开头两句点明友人刘郎的身份是“虎贲将”，此次离京有可能是赴边守卫。“虎贲将”一职起源于周代，在汉代盛行，是宫廷禁军的代称，唐代废止。因此这里说刘郎为虎贲将，可知这是唐人善用的“以汉喻唐”手法。颔联两句是作者在友人离京前，对友人的告诫语：友人身居高位，虽是武职，但久居长安，深处宫廷，荣华富贵傍身，早已忘记建功立业，保家卫国的职责与义务。颈联两句是作者遥想友人行旅途中景色所写。遥想其行旅景色，是感受其旅途辛苦，是作者对友人关心与担忧的一种表现，体现出友人同作者深厚的情感，所以这部分内容常在送别诗中出现。尾联两句是作者夸赞友人之语，体现作者对友人的信心与期望。', '唐肃宗乾元元年，刘郎将归河东，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (358, 'dd3ccfaf5c014a519cf293bb74f6c794', '送周子落第游荆南', 6, 57, 764, '764年', '足下复不第，家贫寻故人。且倾湘南酒，羞对关西尘。山店橘花发，江城枫叶新。若从巫峡过，应见楚王神。', '足下复不第，家贫寻故人。且倾湘南酒，羞对关西尘。<br>山店橘花发，江城枫叶新。若从巫峡过，应见楚王神。', '唐代宗广德二年，周子科举落第游荆南，有诗送行。作品的起首两句点明友人此次离京出游的缘由：因友人在京屡试不第，遂起游侠访友之心。颔联两句是作者遥想友人客游湘南的情景。此言“羞对”，是作者站在友人的立场上，因友人新近落第，此时饮酒必然有借酒消愁，排解失意之功用，所以作者站在友人立场上，体察友人之失意心情。作者同友人感情之深厚可以想见。颈联、尾联四句是作者遥想友人此去湘南之景色。写湘南橘花盛开，枫叶新落，描绘一幅色彩鲜明图景。作者此处遥想其行旅景色，是感受其旅途辛苦，是作者对友人关心与担忧的一种表现，亦表现出友人同作者深厚的情感。全作写送别之情，重在前四句。但最后四句遥想友人途经之景色，实是作者突破送别诗格套的努力所在。', '唐代宗广德二年，周子科举落第游荆南，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (359, 'b09544bc1fa647d2b565a37a2d4e3f1c', '送崔主簿赴夏阳', 6, 57, 764, '764年', '常爱夏阳县，往年曾再过。县中饶白鸟，郭外是黄河。地近行程少，家贫酒债多。知君新称意，好得奈春何。', '常爱夏阳县，往年曾再过。县中饶白鸟，郭外是黄河。<br>地近行程少，家贫酒债多。知君新称意，好得奈春何。', '唐代宗广德二年，崔主簿赴任夏阳，有诗送行。作者曾逗留夏阳，虽是短暂停留，但夏阳的优美风景还是给作者留下了深刻的印象。因此作者在友人即将赴任夏阳之际，回忆起夏阳县那白鸟鸣叫，黄河不绝的悠然壮阔的景象。颈联两句作者从自己对夏阳的回忆中跳脱出来，转向交代即将离别的友人近况。这两句写友人所赴任的夏阳离京城不远。次句写友人家中贫苦却酒债很多，凸显出友人极富浪漫的人生态度。尾联两句写友人新近得官的喜悦态度，比春天带来的喜悦还要更深一些。', '唐代宗广德二年，崔主簿赴任夏阳，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (360, '064241c6686c46f48a6a45e0aecad19c', '送蜀郡李掾', 6, 57, 764, '764年', '饮酒俱未醉，一言聊赠君。功曹善为政，明主还应闻。夜宿剑门月，朝行巴水云。江城菊花发，满道香氛氲。', '饮酒俱未醉，一言聊赠君。功曹善为政，明主还应闻。<br>夜宿剑门月，朝行巴水云。江城菊花发，满道香氛氲。', '唐代宗广德二年，李掾归蜀，有诗送行。作品的起首两句从送别宴会上的饮酒交流说起，饶有新意。以往的送别诗，一般在作品的首联都会点明送别的缘由、送别地点以及友人前往出发的目的地等信息，岑参诗作也不例外。但这首作品可以算是打破了如此格套，以新的朋友间交流的形式显示送别之不舍以及作者对友人的赞美。颔联两句是作者夸赞友人的句子。先言友人“善为政”能够将自己本职工作内容处理得当；再写“明主应闻”，是说皇帝不应错过如此贤才。颈联两句又落入俗套，作者在此联悬想友人离京至目的地这一路上的景色：既有朗月，又有青云，营造出清丽宁静的意境，从侧面也显示出友人此行的轻松愉快。尾联两句是作者幻想友人抵达目的地后所看见的景色：秋菊盛开，满道氤氲，暗示友人到任地民众百姓对友人的热情欢迎以及期待，侧面以友人声名远闻，体现出友人“善为政”的优秀才能。尽管此作中出现了一些饶有新意，突破俗套的语句及处理方式，但终究不能彻底突破俗套，使作品焕然一新。从作品中出现的“酒”意象以及满篇的称赞褒奖友人的语句，可以看出此作应是送别友人的宴会上，只为娱乐而创作的游戏之作。', '唐代宗广德二年，李掾归蜀，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (361, '406957c730d84fe98c65107dd50eac4b', '祁四再赴江南别诗', 6, 57, 764, '764年', '万里来又去，三湘东复西。别多人换鬓，行远马穿蹄。山驿秋云冷，江帆暮雨低。怜君不解说，相忆在书题。', '万里来又去，三湘东复西。别多人换鬓，行远马穿蹄。<br>山驿秋云冷，江帆暮雨低。怜君不解说，相忆在书题。', '唐代宗广德二年，祁乐再赴江南，有诗送行。题目中写“再赴”，言下之意是说祁乐多年之前已经去过江南。作品的起首两句用“又”、“复”字也是屡次点明祁乐有过多次前往江南的经历。颔联两句写时间迁转为人造成的变化，令读者读来心生伤感之情绪。颈联两句主要是作者对友人此去路上的景色作了一番想象与描绘。这两句中所用之语都是些“冷”、“暮”等具有伤感意味的词语，暗示此时正值秋季的同时，还为作者抒发伤感情绪营造了一番凄冷的意境。作品的最后两句表现作者对友人此番远行所受劳苦的同情。但是作者没有将这种同情直白地说出来，而是借助“在书题”三字委婉道出，同情之心溢于言表', '唐代宗广德二年，祁乐再赴江南，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (362, 'c3c01c75b150435ab28ea589036a6869', '送江陵泉少府赴任便呈卫荆州', 6, 57, 765, '765年', '神仙吏姓梅，人吏待君来。渭北草新出，江南花已开。城边宋玉宅，峡口楚王台。不畏无知己，荆州甚爱才。', '神仙吏姓梅，人吏待君来。渭北草新出，江南花已开。<br>城边宋玉宅，峡口楚王台。不畏无知己，荆州甚爱才。', '唐代宗永泰元年，泉少府赴任江陵，有诗送行。友人因离京任职，所以作者在全作的起首两句以汉代梅福典故点明此诗：汉代梅福曾任县尉，因讽刺王凤专政而得罪权贵。王莽专政时，离家修仙，最终成仙，所以被称为“神仙吏”。首联中首句点明友人离京任职事，次句以百姓的期待称赞友人为官品德高尚，执法精明。颔联两句以对偶的形式写明长安与江南的春景，也是映衬友人即将离京之事。颈联两句是作者遥想友人在江陵的官舍环境，写其官舍周围或是有文采之人，或是有政治能力的人。通过这样写，婉转地夸饰友人能力高超。尾联两句是作者对友人的劝勉语，情深意浓，全在其中。', '唐代宗永泰元年，泉少府赴任江陵，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (363, 'deb22b46f3e14df4b7fac56521d1a09a', '送庐郎中除杭州赴任', 6, 57, 765, '765年', '罢起郎官草，初分刺史符。海云迎过楚，江月引归吴。城底涛声震，楼端蜃气孤。千家窥驿舫，五马饮春湖。柳色供诗用，莺声送酒须。知君望乡处，枉道上姑苏。', '罢起郎官草，初分刺史符。海云迎过楚，江月引归吴。<br>城底涛声震，楼端蜃气孤。千家窥驿舫，五马饮春湖。<br>柳色供诗用，莺声送酒须。知君望乡处，枉道上姑苏。', '唐代宗永泰元年，卢幼平赴任杭州刺史，有诗送行。作品的起首两句点明离别缘由是友人离京赴任：首句先写友人被罢免又被起用的经历；次句写友人赴任杭州刺史一事。短短两句中，交代了作品产生的背景。“海云”至“莺声”句是作者从友人赴任路上的风景写及友人目的地之风景：先言路上风景既有海云，亦有朗月，一片舒适自在的意境，凸显出友人新近授官，前往赴任的轻松适意感；再写杭州景象，杭州近水，所以说城底有涛声，又因为近水，所以经常出现海市蜃楼景象；最后写杭州百姓对友人的热情欢迎以及热烈期待，夹道欢迎，争相盼望。“五马”一句用《陌上桑》使君所乘为五匹马的典故，婉转表现出友人此时刺史的身份。接下来的“柳色”两句，是作者结束幻想，将目光集中在此时的送别之中，对送别场景进行了一番书写。作品的最后两句当是作者对友人的告诫及劝勉，友人远行任官，作者当安慰其思乡情绪，此句也不例外。', '唐代宗永泰元年，卢幼平赴任杭州刺史，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (364, '68ac3a2d055748f0b7aa542fde42ba8c', '送襄州任别驾', 6, 57, 765, '765年', '别乘向襄州，萧条楚地秋。江声官舍里，山色郡城头。莫羡黄公盖，须乘彦伯舟。高阳诸醉客，唯见古时丘。', '别乘向襄州，萧条楚地秋。江声官舍里，山色郡城头。<br>莫羡黄公盖，须乘彦伯舟。高阳诸醉客，唯见古时丘。', '唐代宗永泰元年，任别驾赴襄州，有诗送行。作品的开头两句先点明友人即将赴任的目的地在襄州，是秋景萧瑟的古代楚地。颔联两句中作者遥想友人任职地点的景色：面水背山，既有江声，也有山色，惬意自在。颈联两句借用汉时“黄公盖”、晋时“袁宏”的典故：前者因善执政被皇帝委任高管；后者因才高而被高官重用。作者否定前者而崇尚后者行为，意图向友人传达一种不受俗务所劳，闲适自在的人生状态。作品的最后两句写诸人在送别友人时，酩酊大醉的状态。以结果的形式凸显出送别宴会的欢愉程度 。全作无甚新意，全篇都采用送别作品的范式写成。只是在颈联出现的两个典故给人一种耳目一新的感觉，也是这首作品中出彩的地方。', '唐代宗永泰元年，任别驾赴襄州，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (365, '66e699a9a67e4e77a975f80224c7adc0', '送王七录事赴虢州', 6, 57, 765, '765年', '早岁即相知，嗟君最后时。青云仍未达，白发欲成丝。小店关门树，长河华岳祠。弘农人吏待，莫使马行迟。', '早岁即相知，嗟君最后时。青云仍未达，白发欲成丝。小店关门树，长河华岳祠。弘农人吏待，莫使马行迟。', '唐代宗永泰元年，王季友赴任虢州，有诗送行。作品的起首两句写作者同友人有着深厚的情感：先言作者同友人早年即识，交往时间是较长的；再说作者嗟叹友人时运不济，命运多舛，仕途一路坎坷蹭蹬，很晚才得到一个小的官职。次句语句真切，令人读来只觉作者同友人情感之深厚。颔联两句承接首联意绪，仍是作者感叹友人多舛命运，蹭蹬仕途。在此联中作者感叹友人头发已白，但仍然没有实现自己的人生价值及理想。颈联两句是作者遥想友人一路上将要遇到的风景。自长安香西，必然经过华阴，而华阴县内有华山上有华岳祠，离华阴不远处还有潼关，潼关之下有黄河流过。虽然这些景色不在一处，但都是友人即将经过的风景，所以作者在此将其并列，统一写下来。尾联两句是作者对友人的告诫语，也算是作者对受此影响赴外上任的友人一种殷勤嘱托。在这首作品中，虽然没有涉及出现“酒”等代表宴会的意象，“钟”等代表乐器的意象，但作者在这篇作品中的情感抒发倒是引人发叹，令人感叹作者与友人情感之深。换句话说，在这首诗中描写的送别场面虽然未及其他送别场面那样奢华娱乐，但就是这样简单的四目相对式送别，更能显示出送别者与离人的真实情感，也更能打动读者。', '唐代宗永泰元年，王季友赴任虢州，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (366, '302f6b0ad35e4a2c9d73b6f6efaaef8f', '送江陵黎少府', 6, 57, 765, '765年', '悔系腰间绶，翻为膝下愁。那堪汉水远，更值楚山秋。新橘香官舍，征帆拂县楼。王城不敢住，岂是爱荆州。', '悔系腰间绶，翻为膝下愁。那堪汉水远，更值楚山秋。新橘香官舍，征帆拂县楼。王城不敢住，岂是爱荆州。', '唐代宗永泰元年，黎燧赴任江陵，有诗送行。此作起首两句称“悔系”，有后悔作官的意味，似乎不是作者对即将赴任的友人所说的恭贺之语，应该是作者在外做官多年，久未还家，此日见友人去江陵自己家乡一带作官，心中泛起思乡之愁，自己对自己说的话。起首四句全是如此：先称自己悔恨当初选择在外做官，如今想要回家省亲既没有空闲，交通也不便利，再者家乡与长安相距甚远，更无法轻易回家。颈联两句是作者想象友人出任地点官舍景色之优美：既有香橘繁茂，又能翘首以待过往的船只，推窗见江，真是佳处。最后两句是作者对友人的问语：你为何不在长安久留，难道是留恋荆州么？这两句话首句言作者对友人的挽留，体现作者对友人的不舍之情；次句以悬想友人速速离去之缘由，体现作者对家乡荆州的怀念。作者此时长期客居长安作官，每逢友人离京，一定前去饯别。一边是体现情谊，交际往来；另一面则是幻想自己能有一天离开这种羁旅劳苦的生活。', '唐代宗永泰元年，黎燧赴任江陵，有诗送行。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (367, '410bfee2101b4930bb6c097772257067', '早秋上阳宫侍宴序', 26, 66, 695, '695年', '……阊阖秋风，乱起金银之树。降雕舆而式燕，簪绋凝严；披镂槛而?高，山河在目。参光有地，游日月於天边；睨远无穷，见城池於掌上……', '……阊阖秋风，乱起金银之树。降雕舆而式燕，簪绋凝严；披镂槛而?高，山河在目。参光有地，游日月於天边；睨远无穷，见城池於掌上……', '此文笔法多变。开篇以议论入手，盛赞武后功德。继而描写宫殿内秋日景象。最后以交代作文缘起结束全篇。文虽不长，而笔墨全备。', '武则天证圣元年，在上阳宫宴幸群臣，诏令宋之问作赋以记', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (368, 'e77f66b98663480b834227527c96529d', '上阳宫赋', 151, 66, -1, '年份未详', '……天子卜惟洛食，受於河图；开上阳之别馆，取大壮之规模……', '……天子卜惟洛食，受於河图；开上阳之别馆，取大壮之规模……', '此文虽短，但描写全备。先写“受于河图”是说君王的地位之正统。继而描写上阳宫壮丽奢华的景象，并借此凸显出帝王地位之尊贵，影响力之巨大。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (369, 'a8ec5411c59b4e4ba09bbe597e03622d', '上阳宫望幸赋', 152, 66, -1, '年份未详', '……思紫泥而日日将来，仰玉辂而年年未至。徒使万室向曙，千门洞开。蔼臣心以西望，希天眷之东回。见红轮之渐晚，又翠华之不来。及夫玉漏报更，蟾辉永夜……', '……思紫泥而日日将来，仰玉辂而年年未至。徒使万室向曙，千门洞开。蔼臣心以西望，希天眷之东回。见红轮之渐晚，又翠华之不来。及夫玉漏报更，蟾辉永夜……', '从作品的开篇可以看出，此赋或是作者游历河南，途径上阳宫时，目睹上阳宫破败景象，有感而作。中间这一段尤为感人，作者目睹上阳宫之长久无人问津的破败景象，自己也化身成为无人问津的含有“弃子”意味的大臣。所以作者在这一段中将自己日思夜想盼望的皇帝的宠幸与重用全部写了出来，笔调婉转，令人感叹。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (370, 'fe8fe509560745ed828da3040b112522', '仙掌铭（并序）', 105, 60, 755, '755年', '……阴阳开阖，元气变化，泄为百川，凝为崇山，山川之作，与天地并，疑有真宰，而未知尸其功者。有若巨灵??，攘臂其间，左排首阳，右拓太华，绝地轴使中裂，坼山脊为两道，然後导河而柬，俾无有害，留此巨迹，於峰之颠……', '……阴阳开阖，元气变化，泄为百川，凝为崇山，山川之作，与天地并，疑有真宰，而未知尸其功者。有若巨灵??，攘臂其间，左排首阳，右拓太华，绝地轴使中裂，坼山脊为两道，然後导河而柬，俾无有害，留此巨迹，於峰之颠……', '全文先是提及阴阳开合、元气变化到形成高山大川，并论及“造物者”是否存在及其作用。文笔恣肆宏大，可见唐人崇尚瑰奇壮美的审美意趣以及宏阔的胸怀。', '天宝十四年，独孤及任华阴县尉，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (371, 'f7e9793825d946de9cc58bf6fabb4940', '古函谷关铭（并序）', 105, 20, -1, '年份未详', '……长河屈盘，万里来束。崖奔岭蹙谷抱溪斗，崛起重险，为，秦东门。截函夏於阃阈，锁天府於户牖。外扼九州之咽喉，故百二形焉；内拥六合之奥区，故霸王出焉……', '……长河屈盘，万里来束。崖奔岭蹙谷抱溪斗，崛起重险，为，秦东门。截函夏於阃阈，锁天府於户牖。外扼九州之咽喉，故百二形焉；内拥六合之奥区，故霸王出焉……', '全文开篇即论到函谷关天险对取得以及维护封建集权统治的重要性。接着写道函谷关地理位置以及周边环境的险要，真乃是“截函夏於阃阈，锁天府於户牖。”接着作者又写到与函谷关有关的史实，并写及创作此文的缘由是“示来裔”。全文虽短，但各体全备。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (372, '8c0beda215f64c5298d80e1b4edd36e8', '函谷关赋', 153, 20, -1, '年份未详', '……函谷天险，宏农邦镇；南据二虢，北荒三晋。洞开一轨，壁立千仞；迳荐双合，梯苔孤峻。世浊先封，道康後顺。远秦塞，近崤陵……', '……函谷天险，宏农邦镇；南据二虢，北荒三晋。洞开一轨，壁立千仞；迳荐双合，梯苔孤峻。世浊先封，道康後顺。远秦塞，近崤陵……', '全文开篇即论到函谷关之险要对维护封建集权统治的重要性。接着以函谷关的环境险要，要塞设置规格之高，与历代与函谷关相关的著名史实相联系，凸显出函谷关在军事战略地位上的重要性。', '背景未详', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (373, '6b789b4d617f4390856001c46d6b5efd', '西岳太华山碑序', 154, 60, 723, '723年', '……轩帝游焉，以会众神；虞舜柴焉，以觐群后。爰自夏氏，迄於隋室，朝延五姓，载历三千，祀典相因，旧章未改，坛场庙宇，何代不修，一祷三祠，无岁而缺……', '……轩帝游焉，以会众神；虞舜柴焉，以觐群后。爰自夏氏，迄於隋室，朝延五姓，载历三千，祀典相因，旧章未改，坛场庙宇，何代不修，一祷三祠，无岁而缺……', '全文先写华山所处的地理位置，再写及华山的历史。接着作者另辟一段，写到历代帝王祭祀华山的史实，以阐释自己祭祀华山的合理性。', '开元十一年，唐玄宗勒铭华山，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (374, 'f1b66ec89fea4144b31061438aa043b1', '华岳庙题名', 155, 60, 758, '758年', '皇唐乾元元年岁次戊戌冬十月戊申，真卿自蒲州刺史蒙恩除饶州刺史。十有二日辛亥，次於华阴，与监察御史王延昌、大理评事摄监察御史穆宁、评事张澹、华阴令刘?、主簿郑镇同谒金天王之神祠。颜真卿题记。', '皇唐乾元元年岁次戊戌冬十月戊申，真卿自蒲州刺史蒙恩除饶州刺史。十有二日辛亥，次於华阴，与监察御史王延昌、大理评事摄监察御史穆宁、评事张澹、华阴令刘?、主簿郑镇同谒金天王之神祠。颜真卿题记。', '无', '唐肃宗乾元元年，颜真卿途径华山，因有此作。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (375, '258ee60cf55a43f8af1a276df80fafce', '太华山仙掌辩', 156, 60, -1, '年份未详', '西岳太华，华之首峰，有五崖比壑破岩而列，自下远望，偶为掌形……', '西岳太华，华之首峰，有五崖比壑破岩而列，自下远望，偶为掌形……', '作者以华山仙掌传说开篇，通篇辩论所谓“巨灵劈山论”。其中，作者重视文献的使用，可谓为此篇论文增色不少。', '背景未详', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (376, '734d93130aef40b28e2e813af841060e', '潼关赋', 157, 15, -1, '年份未详', '维皇王之建国，分中外於上京。凭山河以作固，阈夷狄而腾声。诚曰咽喉，吞八荒而则大。是称岩险，控万国以来平。周有掌货之节，礼关门之征。巨防宵扃，倚洪波而作镇。重扉击析，连太华而为城。创中代之新号，变函谷之旧名。柱史老聃，拥仙云而西迈。终军童子，建使节而东行。文仲不仁，废六关而兴诮……', '维皇王之建国，分中外於上京。凭山河以作固，阈夷狄而腾声。诚曰咽喉，吞八荒而则大。是称岩险，控万国以来平。周有掌货之节，礼关门之征。巨防宵扃，倚洪波而作镇。重扉击析，连太华而为城。创中代之新号，变函谷之旧名。柱史老聃，拥仙云而西迈。终军童子，建使节而东行。文仲不仁，废六关而兴诮……', '全文论述潼关在军事领域的重要地位与唐王朝封建统治之间的关系，并得出“故知建功定霸，期乎此关。”这样的结论，从此结论可以看出作者认为潼关对获得以及维护封建集权统治具有重要的影响作用。换句话说，没有潼关，就没有盛世大唐。', '背景未详', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (377, 'f24e54fc39644f3fb7e04ec898786dd6', '为东都韦留守谢赐食状', 59, 2, 803, '803年', '具官臣某。右臣今日发至长乐驿，中使某奉宣圣旨，赐臣食者。伏以味兼海陆，品溢圆方。降自御厨，光临传舍。臣初辞魏阙，倍怀犬马之诚；猥受珍羞，更切稻粱之感。无任欣跃。', '具官臣某。右臣今日发至长乐驿，中使某奉宣圣旨，赐臣食者。伏以味兼海陆，品溢圆方。降自御厨，光临传舍。臣初辞魏阙，倍怀犬马之诚；猥受珍羞，更切稻粱之感。无任欣跃。', '代笔之作，体现高度程式化。先言对赐食者的感谢，再写被赐食者的欣喜之感受。', '唐德宗贞元十九年，韦夏卿为东都留守，刘禹锡代笔作此文。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (378, '0696101673b842eca80851c6ae29087c', '管城新驿记', 59, 48, 829, '829年', '大和二年闰三月，荥阳守归厚上言：\"臣治所直天下大逵，肘武牢而咽东夏。谁何宜谨，启闭宜度。先是驿於城中，驿遽不时，四门牡键，通夕弗禁。请更於外隧，永永便安。\"制曰：\"可。\"守臣奉诏，无征命，无夺时，縻羡财，募游手，逮八月既望，新驿成。郑人胥说，琢石而记曰：在兑之方，面元负阳，门衔周道，墙阴行桑（一作墙荫行栗），境胜於外也。远购名材，旁延世工，暨涂宣皙，瓴甓刚滑，术精於内也。蘧庐有甲乙，床帐有冬夏，庭容牙节，庑卧囊橐，示礼而不也。内庖外厩，高仓邃库，积薪就阳，峙刍就燥，有素而不愆也。主吏有第，役夫有区，师行者有飨亭，孥行者有别邸。周以高墉，乃楼其门。劳迎展蠲洁之敬，饯别起登临之思。溱洧波澜，嵩邱云烟，四时万象，来贶於我。走毂奔蹄，遄征急宣，入而忘劳，出必屡顾，其传舍之尤乎！', '大和二年闰三月，荥阳守归厚上言：\"臣治所直天下大逵，肘武牢而咽东夏。谁何宜谨，启闭宜度。先是驿於城中，驿遽不时，四门牡键，通夕弗禁。请更於外隧，永永便安。\"制曰：\"可。\"守臣奉诏，无征命，无夺时，縻羡财，募游手，逮八月既望，新驿成。郑人胥说，琢石而记曰：在兑之方，面元负阳，门衔周道，墙阴行桑（一作墙荫行栗），境胜於外也。远购名材，旁延世工，暨涂宣皙，瓴甓刚滑，术精於内也。蘧庐有甲乙，床帐有冬夏，庭容牙节，庑卧囊橐，示礼而不也。内庖外厩，高仓邃库，积薪就阳，峙刍就燥，有素而不愆也。主吏有第，役夫有区，师行者有飨亭，孥行者有别邸。周以高墉，乃楼其门。劳迎展蠲洁之敬，饯别起登临之思。溱洧波澜，嵩邱云烟，四时万象，来贶於我。走毂奔蹄，遄征急宣，入而忘劳，出必屡顾，其传舍之尤乎！', '大和二年八月，刘禹锡过郑州，主政的好友杨归厚主持修建的新驿站刚落成，遂请刘禹锡纸墨，梦得欣然作此文。文章首先介绍建新驿站的背景，是因为\"驿遽不时，四门牡键，通夕弗禁\"，已以不适合繁忙的交通需求，遂移城西。耗时五个月，终于建成。驿站外是宽阔大道，桑栗夹道成荫。\"暨涂\"指用泥抹屋顶或者墙壁，\"宣皙\"为明亮的白色，则知管城驿主体为洁白的粉墙，\"瓴甓\"指砖石，管城驿主体建筑的砖石坚固平整。。\"蘧庐有甲乙，床帐有冬夏\"说明驿站的房间是分等级的，客房的被褥也分季节分明，一应俱全，\"庭容牙节，庑卧囊橐\"，驿站设有专门存放行李的房间或空间。随行的役夫有专门的住处，行军者有专门的进餐处，带小孩及内眷者也有别邸以便避嫌。驿站后部是马厩、厨房和仓库，堆积的柴火、草料等均分区储藏，保持干燥。为了安全起见，驿馆四周皆峻宇高墙，正门有高轩的门楼。从梦得文看，管城驿确实是一座规模很大、规格也不低的驿站，我们从其建制也能揆知长安都亭、长乐、洛阳都亭诸驿的盛大。从《管城新驿记》我们大概能了解到唐代驿站的大体规模和结构，有门楼，四周有高墙合围，进门后有影壁，绕过影壁是驿庭，是驿站的公共空间。驿庭后有驿厅，为驿站长官办公这所，亦承担接待功能；驿庭两侧为客房，分等级，亦分主仆，同时还辟有女眷幼童住所。驿厅后是厨房，仓库，再后边则是马厩。另外，大的驿站还设有相对大型的专门\"餐厅\"供行军者使用。唐代驿站的规模大概为十丈见方，唐时一丈约等于2.6米，则知一个驿站的建筑面积大概为近千平方米，如果再算上周围的亭台池阁，则会更大。', '大和二年过管城受朋友邀请作', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (379, '6ffeed87d6054e35a78db91d5198ef28', '为荥阳公赴桂州长乐驿谢敕设馔状', 14, 2, -1, '年份未详', '太守姓杨氏，字贞一，华阴宏农人。郑为雄州，非闻人大吏不得在其选。夫驿之宜迁於外也，前此二千石尝言之而重改作，若贞一可谓果於从政而决行其言，惜乎未施於大也。', '太守姓杨氏，字贞一，华阴宏农人。郑为雄州，非闻人大吏不得在其选。夫驿之宜迁於外也，前此二千石尝言之而重改作，若贞一可谓果於从政而决行其言，惜乎未施於大也。', '代笔之作，体现高度程式化。先言对赐食者的感谢，再写被赐食者的欣喜之感受。', '荥阳公赴桂州，文宗遣中使于长乐坡设饯。义山代笔以谢', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (380, 'a391c689a8614077b946995737de829e', '为中丞荥阳公谢借飞龙马送至府界状', 14, 2, -1, '年份未详', '右中使某奉宣恩旨，以臣赴任，特借飞龙马一匹并鞭辔等，送至京兆府界者。臣谬奉恩荣，出叨廉问，岂期蹇步，深轸皇慈，特命内臣，俾腾上驷。梁悬蜀镫，几覆吴鞍，每多曳练之疑，不假著鞭之力倏逾秦甸，将复周闲，照地回光，瞻天送影。长亭欲别，未期东道而来；双阙尚嘶，愿附北风之思。无任感恩，恋阙雪涕，屏营之至。', '右中使某奉宣恩旨，以臣赴任，特借飞龙马一匹并鞭辔等，送至京兆府界者。臣谬奉恩荣，出叨廉问，岂期蹇步，深轸皇慈，特命内臣，俾腾上驷。梁悬蜀镫，几覆吴鞍，每多曳练之疑，不假著鞭之力倏逾秦甸，将复周闲，照地回光，瞻天送影。长亭欲别，未期东道而来；双阙尚嘶，愿附北风之思。无任感恩，恋阙雪涕，屏营之至。', '帝王赐驿马，撰文以谢。程式化的颂圣与自谦，表达臣子对帝王的感恩与眷念。此类谢上表犹如君臣“场域”中的优质文化资本，帝王览之愉悦，则会对其有好感，成为后期拔擢的对象。故多夸张之辞，真情被掩，为“游词”。', '荥阳公赴桂州，文宗遣中使于长乐坡设饯，赐白龙马。义山代笔以谢', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (381, '3e54af2a2ce94f10a7b5c8870e349236', '无双传', 158, 2, -1, '年份未详', '建中间王仙客与表妹无双恋爱，后因朱泚乱揆违。仙客为富平县尹，知长乐双。无双后入掖庭为宫女，随从拜祭帝陵宿长乐驿，二人得以相见。仙客志诚不改，在古押衙帮助下得以与无双团聚。而古押衙等人亦因此殉命。', '建中间王仙客与表妹无双恋爱，后因朱泚乱揆违。仙客为富平县尹，知长乐双。无双后入掖庭为宫女，随从拜祭帝陵宿长乐驿，二人得以相见。仙客志诚不改，在古押衙帮助下得以与无双团聚。而古押衙等人亦因此殉命。', '这段写得极为精彩。王仙客作为驿长，自然不便与宫嫔有任何接触，这也为男女主人公的相逢之难找到合理的理由，很好地吊足了读者的胃口，引起阅读的期待。塞鸿假扮驿吏烹茶，近帘等待，符合生活的实际情况。塞鸿先是推脱，云宫嫔数千，无双未必有机会前来，此为 一“顿”；仙客予塞鸿钱财，让他坚守帘外，此为一“解”；夜阑人声渐歇，仍未见无双，此又一“顿”；塞鸿洗涤茶具，拨亮柴火，不敢打盹，此又一“解”；忽然无双出现，与塞鸿语，并托塞鸿传书于仙客，此为一大“解”；然无双遽去，侍中使汤药，此又一大“顿”。如此一“顿”一“解”中，读者的心情也因此起伏紧松，如腾云于天，时飞时坠，为男女主人公的命运担忧。接着又言帝王仪仗过渭桥，仙客假扮修桥官吏于桥边匆匆一瞥，更添伤悲，方有后边的古押衙救拔一事。而渭桥相见事，也非常符合唐时的交通实际。唐帝陵多在渭北，帝王祭扫由长乐驿东北行渡渭至渭桥镇，其赴在今西安市高陵区耿镇白家咀村，然后北行至奉先、富平等地。日僧园仁《入唐求法巡礼行记》载其开成五年八月由蒲州至长安，取道河西县——朝邑县——冯翊县——故市店——永安店——操阳县——高陵县渭桥——灞桥——长安。其中在渭桥北操阳县看到还葬文宗的仪仗：“于县南头见山陵使回入京城。是葬开成天子使。营幕军兵。陈列五里。军兵在大路两边对立。不妨百姓人马车从中路过。县西。去县八九十里山中。有陵头。”由圆仁的行记可知，《无双传》的故事推进严格按照实际的道路情况展开，表现出极强的写实意识与本事意识。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (382, 'd1d44455557a479193d86c5273c8c4b8', '冯涓', 159, 2, -1, '年份未详', '大中四年，进士冯涓登第，榜中文誉最高。是岁新罗国起楼，厚赍金帛，奏请撰记，时人荣之。初官京兆府参军，恩地即杜相审权也。杜有江西之拜，制书未行，先召长乐公密话，垂延辟之命，欲以南昌牋奏任之，戒令勿泄。长乐公拜谢，辞出宅，速鞭而归。于通衢遇友人郑賨，见其喜形于色，驻马恳诘。长乐遽以恩地之辟告之。荥阳寻捧刺诣京兆门谒贺，具言得于冯先辈也。京兆嗟愤，而鄙其浅露。洎制下开幕，冯不预焉，心绪忧疑，莫知所以。廉车发日，自灞桥乘肩舆，门生咸在长乐拜别，京兆公长揖冯曰：“勉旃！”由是嚣浮之誉，遍于搢绅，竟不通显。中间又涉交通中贵，愈招清议，官止祠部郎中.眉州刺史。仕蜀，至御史大夫。（原缺出处，谈氏初的本文同。', '大中四年，进士冯涓登第，榜中文誉最高。是岁新罗国起楼，厚赍金帛，奏请撰记，时人荣之。初官京兆府参军，恩地即杜相审权也。杜有江西之拜，制书未行，先召长乐公密话，垂延辟之命，欲以南昌牋奏任之，戒令勿泄。长乐公拜谢，辞出宅，速鞭而归。于通衢遇友人郑賨，见其喜形于色，驻马恳诘。长乐遽以恩地之辟告之。荥阳寻捧刺诣京兆门谒贺，具言得于冯先辈也。京兆嗟愤，而鄙其浅露。洎制下开幕，冯不预焉，心绪忧疑，莫知所以。廉车发日，自灞桥乘肩舆，门生咸在长乐拜别，京兆公长揖冯曰：“勉旃！”由是嚣浮之誉，遍于搢绅，竟不通显。中间又涉交通中贵，愈招清议，官止祠部郎中.眉州刺史。仕蜀，至御史大夫。（原缺出处，谈氏初的本文同。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (383, '6f3d04803e9e44b78557ecf825b0b44e', '唐摭言卷八“忧中有喜”', 111, 2, -1, '年份未详', '公乘亿，魏人也，以辞赋著名。咸通十三年，垂三十举矣。尝大病，乡人误传已死，其妻自河北来迎丧。会亿送客至坡下，遇其妻。始，夫妻阔别积十余岁，亿时在马上见一妇人，粗衰跨驴，依稀与妻类，因睨之不已；妻亦如是。乃令人诘之，果亿也。亿与之相持而泣，路人皆异之。后旬日，登第矣。', '公乘亿，魏人也，以辞赋著名。咸通十三年，垂三十举矣。尝大病，乡人误传已死，其妻自河北来迎丧。会亿送客至坡下，遇其妻。始，夫妻阔别积十余岁，亿时在马上见一妇人，粗衰跨驴，依稀与妻类，因睨之不已；妻亦如是。乃令人诘之，果亿也。亿与之相持而泣，路人皆异之。后旬日，登第矣。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (384, '5c29ea8d02664638a9634bf551f65b15', '唐摭言', 111, 3, -1, '年份未详', '许棠，宣州泾县人，早修举业。乡人汪遵者幼为小吏。洎棠应二十余举，遵犹在胥徒。然善为绝句诗，而深晦密。一旦辞役就贡，会棠送客至灞浐，忽遇遵于途中。棠讯之曰：“汪都，（都者。吏之呼也。）何事至京？”遵对曰：“此来就贡。”棠怒曰：“小吏无礼。”而果与棠同砚席，棠甚侮之。后遵成名五年，棠始及第。', '许棠，宣州泾县人，早修举业。乡人汪遵者幼为小吏。洎棠应二十余举，遵犹在胥徒。然善为绝句诗，而深晦密。一旦辞役就贡，会棠送客至灞浐，忽遇遵于途中。棠讯之曰：“汪都，（都者。吏之呼也。）何事至京？”遵对曰：“此来就贡。”棠怒曰：“小吏无礼。”而果与棠同砚席，棠甚侮之。后遵成名五年，棠始及第。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (385, 'c6be95f7b5654ab193cd3e7ee5245019', '唐摭言卷六“公荐”', 111, 2, -1, '年份未详', '崔郾侍郎既拜命，于东都试举人，三署公卿皆祖于长乐传舍；冠盖之盛，罕有加也。时吴武陵任太学博士，策蹇而至。郾闻其来，微讶之，乃离席与言。武陵曰：“侍郎以峻德伟望，为明天子选才俊，武陵敢不薄施尘露！向者，偶见太学生十数辈，扬眉抵掌，读一卷文书，就而观之，乃进士杜牧《阿房宫赋》。若其人，真王佐才也，侍郎官重，必恐未暇披览。”于是搢笏郎宣一遍。郾大奇之。武陵曰：“请侍郎与状头。”郾曰：“已有人。”曰：“不得已，即第五人。”郾未遑对。武陵曰：“不尔，即请比赋。”郾应声曰：“敬依所教。”既即席，白诸公曰：“适吴太学以第五人见惠。”或曰：“为谁？”曰：“杜牧。”众中有以牧不拘细行间之者。郾曰：“已许吴君矣。牧虽屠沽，不能易也。”', '崔郾侍郎既拜命，于东都试举人，三署公卿皆祖于长乐传舍；冠盖之盛，罕有加也。时吴武陵任太学博士，策蹇而至。郾闻其来，微讶之，乃离席与言。武陵曰：“侍郎以峻德伟望，为明天子选才俊，武陵敢不薄施尘露！向者，偶见太学生十数辈，扬眉抵掌，读一卷文书，就而观之，乃进士杜牧《阿房宫赋》。若其人，真王佐才也，侍郎官重，必恐未暇披览。”于是搢笏郎宣一遍。郾大奇之。武陵曰：“请侍郎与状头。”郾曰：“已有人。”曰：“不得已，即第五人。”郾未遑对。武陵曰：“不尔，即请比赋。”郾应声曰：“敬依所教。”既即席，白诸公曰：“适吴太学以第五人见惠。”或曰：“为谁？”曰：“杜牧。”众中有以牧不拘细行间之者。郾曰：“已许吴君矣。牧虽屠沽，不能易也。”', '崔郾于东都试举人，三署公卿饯于长乐驿。吴武陵后至，向崔郾举杜牧为，崔郾许之。众人曰牧不捐细行，崔郾云牧虽屠沽，不能易也。', '众人长乐驿送崔郾，吴武陵举荐杜牧', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (386, '187cc37c170b40beb19fb51eaf2f0ecb', '灵怪集一则故事', 160, 2, -1, '', '有中官行，宿于官坡馆，脱绛裳，覆锦衣，灯下寝。忽见一童子，捧一樽酒，冲扉而入。续有三人至焉，皆古衣冠，相谓云：“崔常侍来何迟？”俄复有一人续至，凄凄然有离别之意，盖崔常侍也。及至举酒，赋诗聊句，末即崔常侍之词也。中官将起，四人相顾，哀啸而去，如风雨之声。及视其户，扃闭如旧，但见酒樽及诗在。中官异之，旦馆吏云：“里人有会者，失其酒樽。”中官出示之，乃里人所失者。聊句歌曰：“床头锦衾斑复斑，架上朱衣殷复殷。空庭朗月闲复闲，夜长路远山复山。”', '有中官行，宿于官坡馆，脱绛裳，覆锦衣，灯下寝。忽见一童子，捧一樽酒，冲扉而入。续有三人至焉，皆古衣冠，相谓云：“崔常侍来何迟？”俄复有一人续至，凄凄然有离别之意，盖崔常侍也。及至举酒，赋诗聊句，末即崔常侍之词也。中官将起，四人相顾，哀啸而去，如风雨之声。及视其户，扃闭如旧，但见酒樽及诗在。中官异之，旦馆吏云：“里人有会者，失其酒樽。”中官出示之，乃里人所失者。聊句歌曰：“床头锦衾斑复斑，架上朱衣殷复殷。空庭朗月闲复闲，夜长路远山复山。”', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (387, 'aeaa804e520847cbb23c6e5b993a432f', '唐摭言卷三“慈恩寺题名游赏赋咏杂纪”：', 111, 2, -1, '', '大中十年，郑颢都尉放榜，请假往东洛觐省，生徒饯于长乐驿。俄有纪于屋壁曰：“三十骅骝一烘尘，来时不锁杏园春。杨花满地如飞雪，应有偷游曲水人。”', '大中十年，郑颢都尉放榜，请假往东洛觐省，生徒饯于长乐驿。俄有纪于屋壁曰：“三十骅骝一烘尘，来时不锁杏园春。杨花满地如飞雪，应有偷游曲水人。”', '无', '郑颢东归，生徙饯于长乐，有生题壁', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (388, '1f6653348b244a038c754e2466c58b79', '唐摭言卷一三“敏捷”', 111, 2, -1, '年份未详', '韦蟾左丞，至长乐驿亭，见李汤给事题名，索笔纪之曰：“渭水秦山豁眼明，希仁何事寡诗情。只应学得虞姬婿，书字才能记姓名。”', '韦蟾左丞，至长乐驿亭，见李汤给事题名，索笔纪之曰：“渭水秦山豁眼明，希仁何事寡诗情。只应学得虞姬婿，书字才能记姓名。”', '用项羽典以戏谑手法嘲李汤题名不落款。', '长乐驿送别，李汤无文才，仅题名耳。未落款。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (389, '160fd4ddb3b442adaab1ead870a3e155', '曹文洽', 160, 2, -1, '年份未详', '曹文洽，郑滑之裨将也。时姚南仲为节度使，被监军薛盈珍怙势干夺军政，南仲不从，数为盈珍构谗于上，上颇疑之。后盈珍遣小使程务盈驰表，奏南仲不法，诬谗颇甚。文洽时奏事赴京师，窃知盈珍表中语，洽私怀愤怒，遂晨夜兼道追务盈。至长乐矣，及之，与同舍宿。中夜，杀务盈，沉盈珍表于厕中，乃自杀。日旰，驿吏开门，见血流满地，傍得文洽二缄，一状告盈珍罪，一表理南仲冤，且陈谢杀务盈。德宗闻其事，颇骇异。南仲虑衅深，遂入朝。初至，上曰：“盈珍扰卿甚耶？”南仲曰：“盈珍不扰臣，自隳陛下法耳。如盈珍辈所在，虽羊.杜复生，抚百姓，御三军，必不能成恺悌父母之政，师律善阵之制矣。”德宗默然久之。', '曹文洽，郑滑之裨将也。时姚南仲为节度使，被监军薛盈珍怙势干夺军政，南仲不从，数为盈珍构谗于上，上颇疑之。后盈珍遣小使程务盈驰表，奏南仲不法，诬谗颇甚。文洽时奏事赴京师，窃知盈珍表中语，洽私怀愤怒，遂晨夜兼道追务盈。至长乐矣，及之，与同舍宿。中夜，杀务盈，沉盈珍表于厕中，乃自杀。日旰，驿吏开门，见血流满地，傍得文洽二缄，一状告盈珍罪，一表理南仲冤，且陈谢杀务盈。德宗闻其事，颇骇异。南仲虑衅深，遂入朝。初至，上曰：“盈珍扰卿甚耶？”南仲曰：“盈珍不扰臣，自隳陛下法耳。如盈珍辈所在，虽羊.杜复生，抚百姓，御三军，必不能成恺悌父母之政，师律善阵之制矣。”德宗默然久之。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (390, 'f8c23b3780434adcb168bdbaa66357c3', '谢借飞龙马状', 99, 2, -1, '年份未详', '右。今月十日，中使张少禺至长乐驿，奉宣进止：借臣前件马送出府界者。臣以庸薄，谬叨恩私，宠荣遝至，感戴难处。以今月十五日至东驿，出京兆府界。天阙储胥，仰瞻日远，御闲蹀躞，假借恩深。仰元泽以难酬，顾微生而何幸？况代劳比君子之德，负乘积小人之忧。感恋明庭，涕泗横积。当君陈之寄，必竭肺肝；羡袅之回，恨无羽翼。无任感荷恋慕之至！谨奉状陈谢以闻。谨奏。', '右。今月十日，中使张少禺至长乐驿，奉宣进止：借臣前件马送出府界者。臣以庸薄，谬叨恩私，宠荣遝至，感戴难处。以今月十五日至东驿，出京兆府界。天阙储胥，仰瞻日远，御闲蹀躞，假借恩深。仰元泽以难酬，顾微生而何幸？况代劳比君子之德，负乘积小人之忧。感恋明庭，涕泗横积。当君陈之寄，必竭肺肝；羡袅之回，恨无羽翼。无任感荷恋慕之至！谨奉状陈谢以闻。谨奏。', '帝王赐驿马，撰文以谢。程式化的颂圣与自谦，表达臣子对帝王的感恩与眷念。此类谢上表犹如君臣“场域”中的优质文化资本，帝王览之愉悦，则会对其有好感，成为后期拔擢的对象。故多夸张之辞，真情被掩，为“游词”。', '权德舆赴任，帝王赐驿马，上表以谢', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (391, '5b7ddb5f86dc4e6d95328e855d12823f', '为杨湖南谢赐设表', 14, 2, -1, '年份未详', '臣某言：中使某乙至，奉宣圣旨，赐臣长乐驿设者。恩荣特殊，宴饮斯及，顾兹厚礼，猥集微躬。臣某诚欢诚庆，顿首顿首。臣以多幸，属此昌时，任重方隅，职忝文武。甘受素餐之刺，知无肉食之谋，以忧以惶，寤寐无措。岂谓鸿恩继至，丰膳爰来，陆海兼陈，饴醴皆设。庶当奉扬圣泽，覃布远人，流恺悌于皇风，均乳哺于赤子。少陈微效，上答殊私。无任感恩欣跃之至。', '臣某言：中使某乙至，奉宣圣旨，赐臣长乐驿设者。恩荣特殊，宴饮斯及，顾兹厚礼，猥集微躬。臣某诚欢诚庆，顿首顿首。臣以多幸，属此昌时，任重方隅，职忝文武。甘受素餐之刺，知无肉食之谋，以忧以惶，寤寐无措。岂谓鸿恩继至，丰膳爰来，陆海兼陈，饴醴皆设。庶当奉扬圣泽，覃布远人，流恺悌于皇风，均乳哺于赤子。少陈微效，上答殊私。无任感恩欣跃之至。', '代笔之作，体现高度程式化。先言对赐食者的感谢，再写被赐食者的欣喜之感受。', '杨湖南赴任，中使送于长乐驿。杨湖南谢上，义山代笔', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (392, '8a60dfdac9774e28bf0f253e03be1599', '为杜相公自淮南追入长安至长乐驿谢赐酒食状', 59, 2, 803, '803年', '具官臣某。右臣今日至长乐驿，高品某奉宣圣旨，赐臣酒食者。伏以恩降王人，荣分御膳。未展仪於双阙，先受赐於八珍。品越??番，味兼醪醴。顿惊凡口，倍益欢心。无任欣跃。', '具官臣某。右臣今日至长乐驿，高品某奉宣圣旨，赐臣酒食者。伏以恩降王人，荣分御膳。未展仪於双阙，先受赐於八珍。品越??番，味兼醪醴。顿惊凡口，倍益欢心。无任欣跃。', '代笔之作，体现高度程式化。先言对赐食者的感谢，再写被赐食者的欣喜之感受。', '唐德宗贞元十九年，杜佑自淮南入朝，刘禹锡代笔作此文。', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (393, '72ef3f585fca4a1581ba06fd93c5c768', '幸新丰蠲复敕', 154, 61, -1, '年份未详', '惟此新丰，汤泉是出，古之顺豫，义兼巡省。顷者观风，数临兹地，以察冤滞，询於故老。闾里欢康，田畴垦辟。况冬降积雪，春期有年，且谚王游，果符时迈。虽千乘万骑，咸给於主司，而累月再来，颇勤於除扫。宜下复蠲之令，慰其望幸之心。新丰县百姓，免其一年杂差科；县官及温泉监官，经两度祗承者，与一中上考。', '惟此新丰，汤泉是出，古之顺豫，义兼巡省。顷者观风，数临兹地，以察冤滞，询於故老。闾里欢康，田畴垦辟。况冬降积雪，春期有年，且谚王游，果符时迈。虽千乘万骑，咸给於主司，而累月再来，颇勤於除扫。宜下复蠲之令，慰其望幸之心。新丰县百姓，免其一年杂差科；县官及温泉监官，经两度祗承者，与一中上考。', '此为皇帝免除新丰县百姓一年赋税的诏令文章。其中描写新丰县人民安康，社会和谐的文字与有唐一代提及新丰社会治理状况的诗作相类似，可见唐时新丰县的社会状况真如卢纶诗中所说“衰翁正席矜新社，稚子齐襟读古论。”这样和谐美好。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (394, '644fe621417948738aeb91b0b531179c', '骊山伤古赋', 161, 5, -1, '年份未详', '……令四海惟精惟一，俾万人是则是效。安有临伐木而弃其斧，将涉水而投其棹？童子犹知其不可，况天下之龙豹。初虎视八荒，鲸吞六国；攻必取，战必克；所往而枭其渠帅，所敌而断其衔勒。起、翦之功逞，苏、张之诈塞，招星纪之南，伐天街之北……', '……令四海惟精惟一，俾万人是则是效。安有临伐木而弃其斧，将涉水而投其棹？童子犹知其不可，况天下之龙豹。初虎视八荒，鲸吞六国；攻必取，战必克；所往而枭其渠帅，所敌而断其衔勒。起、翦之功逞，苏、张之诈塞，招星纪之南，伐天街之北……', '全文开篇先写秦皇开疆拓土，一统天下之伟业。接着写秦国统一六国，成就霸业后恣肆妄为，修驰道，修阿房宫，收天下之兵，销之以金人十二。种种不仁之举，最终使帝国迅速衰败，百姓起义，帝国随之覆灭。此文虽写秦国史实，然而借古讽今意味甚浓。', '背景未详', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (395, 'c67c92c2fb3147f49848811634b13ea0', '过骊山赋', 162, 5, -1, '年份未详', '六国血於秦，秦皇还化尘。尘惊而为楚为汉，路在而今人古人，但见愁云黯惨，叠嶂嶙峋。时迁而金石非固，地改而荆榛旋新。愚闻周衰则避债登台，秦暴则焚书建国。贵蝼蚁於人命，法豺狼於帝德。两曜昏翳，九围倾侧。扶桑几里，我鞭石以期通。溟海几重，我驱山而要塞。惨惨元穹，嗷嗷七雄……', '六国血於秦，秦皇还化尘。尘惊而为楚为汉，路在而今人古人，但见愁云黯惨，叠嶂嶙峋。时迁而金石非固，地改而荆榛旋新。愚闻周衰则避债登台，秦暴则焚书建国。贵蝼蚁於人命，法豺狼於帝德。两曜昏翳，九围倾侧。扶桑几里，我鞭石以期通。溟海几重，我驱山而要塞。惨惨元穹，嗷嗷七雄……', '全文从秦皇统一六国，建立霸业写起，写及秦帝国在统一之后的种种不仁举动，导致最终的覆灭与破败，最后以“想秦史以神竦，吊秦陵而恨永。”句结束全文，颇具教育警戒意义。', '背景未详', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (396, '3e7f26ca612a429f9da53338313aa224', '会昌主簿厅壁记', 163, 5, -1, '年份未详', '会昌行在也，新邑作焉；主簿纠曹也，我公吏焉。公名挚，字某，由秘书正字而拜。初蒙泉秘涌於山下，阴火潜燃於地中，是开汤池。以御宿获灵符之三载，有诏留之，冠新丰渭南而为畿县……', '会昌行在也，新邑作焉；主簿纠曹也，我公吏焉。公名挚，字某，由秘书正字而拜。初蒙泉秘涌於山下，阴火潜燃於地中，是开汤池。以御宿获灵符之三载，有诏留之，冠新丰渭南而为畿县……', '全文的开篇，作者先写会昌驿的由来，接着写道会昌驿的官员尽职尽责，恪尽职守，只是“游泳恩波，膏沐圣泽”为了报答皇恩而已。', '背景未详', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (397, 'acd471ceba2d4d1094684acda54bd760', '狠石铭', 164, 5, -1, '年份未详', '……昔秦皇帝，谋之不臧。七十万人，兹焉惶惶。发石此山，言础於坟。若有凭依，屹住中逵。淫刑蹴迫，人力无施。故老相传，以狠名之……', '……昔秦皇帝，谋之不臧。七十万人，兹焉惶惶。发石此山，言础於坟。若有凭依，屹住中逵。淫刑蹴迫，人力无施。故老相传，以狠名之……', '全文着眼于骊山之石。先论述自古以来无有采石修陵之作法，凸显出秦皇的荒淫无道。接着作者写秦皇的行为最终触犯了天帝，而“天毒其衷，神愤其凶。谪戍一呼，九州风从。”导致衰败毁灭的结局，颇具有警戒意味。', '背景未详', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (398, 'd114fdda05ea4af6bae349f3be742496', '驾幸华清宫赋', 165, 5, -1, '年份未详', '惟我皇御宇兮法象乾坤，天步顺动兮行幸斯存，雨师洒路兮九门洞启，千旗火生兮万乘雷奔。紫云霏微，随六龙而欲散还聚；白日照耀，候一人兮当寒却温。荩上豫游以叶运，岂伊沐浴而足论……', '惟我皇御宇兮法象乾坤，天步顺动兮行幸斯存，雨师洒路兮九门洞启，千旗火生兮万乘雷奔。紫云霏微，随六龙而欲散还聚；白日照耀，候一人兮当寒却温。荩上豫游以叶运，岂伊沐浴而足论……', '全文写唐皇驾幸华清宫温泉池事。语言多溢美赞颂之辞。', '背景未详', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (399, '27f236100a734fd58ed73b7f43af9080', '华清宫汤池记', 166, 5, -1, '年份未详', '……新广汤池，制作宏丽，安禄山於范阳以白玉石为鱼龙凫雁，仍以石梁及石莲花以献，雕镌巧妙，殆非人功。上大悦，命陈於汤中，仍以石梁横亘汤上，而莲花才出水际。上因幸华清宫，至其所，解衣将入，而鱼龙凫雁，皆若奋鳞举翼，状欲飞动。上甚恐，遽命撤去。而莲花今犹存……', '……新广汤池，制作宏丽，安禄山於范阳以白玉石为鱼龙凫雁，仍以石梁及石莲花以献，雕镌巧妙，殆非人功。上大悦，命陈於汤中，仍以石梁横亘汤上，而莲花才出水际。上因幸华清宫，至其所，解衣将入，而鱼龙凫雁，皆若奋鳞举翼，状欲飞动。上甚恐，遽命撤去。而莲花今犹存……', '全文写唐玄宗时期，华清宫内奢华景象。先写汤池中石莲盛开，又写银楼谷船、白香木船漂浮期间，只为君王游玩取乐。最后，作者对这时期华清宫内装饰以及娱乐活动作了总评“其穷奢而极欲，古今罕匹矣。”这篇作品有两个意义，首先是可以看出唐人对待华清宫尤其是唐玄宗鼎盛时期的华清宫的态度，其次则可以从中看出唐人对唐玄宗时期华清宫的真实描写，进而还原一个真实的处于鼎盛时期的华清宫。', '背景未详', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (400, '1d279e51bce44fb2ae579b906caa4114', '华清宫望幸赋', 167, 5, -1, '年份未详', '上苑之左兮，骊山之中。天作高岫，帝为离宫。示宸游之有所，表圣鉴於无穷。临峻路而赫其?户々，标爽垲而屹以崇崇。惜翠华之未至，阒紫殿而犹空。则有望幸其中，流睇延慕……', '上苑之左兮，骊山之中。天作高岫，帝为离宫。示宸游之有所，表圣鉴於无穷。临峻路而赫其?户々，标爽垲而屹以崇崇。惜翠华之未至，阒紫殿而犹空。则有望幸其中，流睇延慕……', '全文以女性的角度，写女性希望君王早日来到华清宫宠幸自己的愿望。实则是作者对唐玄宗开天年间华清宫繁荣景象的追忆与遥思。', '背景未详', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (401, 'cd536e73821b4ef6964f77e303d259b3', '三月三日於灞水曲饯豫州杜长史别昆季序', 26, 3, -1, '年份未详', '上已佳游，近郊春色，朱轩映野，见东流之祓禊；白?在天，怆南登之送别。杜长史言辞灞?，将适荆河，恋旧乡之乔木，藉故园之芳草。?原四鸟，是日分飞；舆泉二龙，此时云远。绿潭一望，青山四极，秦人去国，乘右辅之修途；洛客思归，忆东京之曲水。请染翰操纸，即事形言，各赋兰亭之诗，咸申葛陂之赠。', '上已佳游，近郊春色，朱轩映野，见东流之祓禊；白?在天，怆南登之送别。杜长史言辞灞?，将适荆河，恋旧乡之乔木，藉故园之芳草。?原四鸟，是日分飞；舆泉二龙，此时云远。绿潭一望，青山四极，秦人去国，乘右辅之修途；洛客思归，忆东京之曲水。请染翰操纸，即事形言，各赋兰亭之诗，咸申葛陂之赠。', '全文先写杜长史即将离京赴荆，接着体察友人离别时应有的伤感情绪，并作诗安慰。', '背景未详', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (402, 'f101e7cb23564561adb13d0a6dfe5ae6', '李蠙新雪题名', 168, 60, 860, '860年', '左谏议大夫赐绯鱼袋李蠙咸通元年二十月廿九日奉恩命祈雪', '左谏议大夫赐绯鱼袋李蠙咸通元年二十月廿九日奉<br>恩命祈雪', '无', '咸通二年祈雪题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (403, '388ec8e32c5f4f0e8e88d9c783e74e0e', '李憺华岳题名', 169, 60, 736, '736年', '郑县尉李憺以开元廿四六月六日充敕蕳募飞骑使判官，向陕虢州点覆，其月十四日事了，回便充京畿采访使，句覆判官此过赴亦。', '郑县尉李憺以开元廿四六月六日充敕蕳募飞骑使判官，向陕虢州点覆，其月十四日事了，回便充京畿采访使，句覆判官此过赴亦。', '无', '开元间之官过岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (404, 'ffaa5a3318304c588aa5ab56dcd2b7b1', '苏颎题名', 170, 60, 738, '738年', '开元廿六年八月廿七日，朝请大夫守别驾临潼县开国易苏颖从内使奉敕祭', '开元廿六年八月廿七日，朝请大夫守别驾临潼县开国易苏颖从<br><br>内使奉<br><br>敕祭', '无', '开元间祭岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (405, '5c2f642dd1a346c7b8193359f93df8a8', '张惟一等祈雨记', 171, 60, 759, '759年', '在唐中兴，克复两京后，乾元元年自十月不雨。至于明年春，朝散大夫使持节华州诸军军事检校华州刺史平原郡开国公赐紫金鱼袋张惟一与华阴县令刘暠、丞□峋、丞员外置同正员李缓、主簿郑、镇尉王、禁尉高佩、尉崔季阳于西岳金天王庙祈请。初发言云兴，倏登车雨降，盖精意所感，致明神应期，庶似颍川之能，不惭方伯之事。时二月十日题纪。前金州刺史李权书', '在唐中兴，克复两京后，乾元元年自十月不雨。至于明年春，朝散大夫使持节华州诸军军事检校华州刺史平原郡开国公赐紫金鱼袋张惟一与华阴县令刘暠、丞□峋、丞员外置同正员李缓、主簿郑、镇尉王、禁尉高佩、尉崔季阳于<br><br>西岳金天王庙祈请。初发言云兴，倏登车雨降，盖精意所感，致明神应期，庶似颍川之能，不惭方伯之事。时二月十日题纪。<br><br>前金州刺史李权书', '无', '乾元二年祈雨题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (406, '233f0d9003c047c7a08798bc5b02c624', '王宥等谒岳祠题记', 172, 60, 674, '674年', '大唐上元元年冬，十有二月十一日，同诣岳祠书记。华阴县令王宥、前令王纾、丞王沐、尉李齐佺、尉□颂、郑县主簿张彬、尉宝彧、下邽县丞李演、尉邢涉、处士王季友、张彪、著作郎孟昌、原京兆府法曹参军李枢书并篆。', '大唐上元元年冬，十有二月十一日，同诣岳祠书记。华阴县令王宥、前令王纾、丞王沐、尉李齐佺、尉□颂、郑县主簿张彬、尉宝彧、下邽县丞李演、尉邢涉、处士王季友、张彪、著作郎孟昌、原京兆府法曹参军李枢书并篆。', '无', '上元元年谒庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (407, '807d4eb5441146b98b80050c7c55f40c', '丘据题名', 173, 60, 763, '763年', '陈郑泽潞等州节度行军司马殿中待御史丘据、再随使主赴上都，朝谒往来皆虔拜神祠，时宝应二年六月八日记。', '陈郑泽潞等州节度行军司马殿中待御史丘据、再随使主赴上都，朝谒往来皆虔拜神祠，时宝应二年六月八日记。', '无', '宝应间太谒庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (408, '6e3b0e4f54b241ae946c0c958a5b7985', '韦□题名', 174, 60, 764, '764年', '银青光禄大夫守太常卿使持节、延州都督侍御史上柱国岐阳县开国男韦□，以广德二年二□□七日越自师旅，将谐朝廷，□□□神，式祈景福，时与□□□□□秀之同谒。', '银青光禄大夫守太常卿使持节、延州都督侍御史上柱国岐阳县开国男韦□，以广德二年二□<br>□七日越自师旅，将谐朝廷，□□□神，式祈景福，时与□□□□□秀之同谒。', '无', '广德间谒庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (409, '25b29700bd894eb7aa8f1894f49b9ead', '李仲昌等题名', 175, 60, 764, '764年', '朝议郎行太子司议郎兼华阴县令李仲昌、行丞田游业、行丞同正韦澣、行主簿韦涚、行尉崔颋、行尉王卓、行尉苏淮阳。广德□年三月韦澣书记', '朝议郎行太子司议郎兼华阴县令李仲昌、行丞田游业、行丞同正韦澣、行主簿韦涚、行尉崔颋、行尉王卓、行尉苏淮阳。广德□年三月韦澣书记', '无', '广德间谒庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (410, '15ccd0baca454b5a9019906c5105a1ff', '第五公等题名', 176, 60, 770, '770年', '前相国京地第五公自户部侍郎出牧括州，子□关内，河东副元帅判官礼部郎中兼侍御史虞当自中都济河于华阴拜见从谒灵祠，因纪贞石时。在唐大□五年六月四日。司勋庚子中兼侍御史李国清，仓部员外兼侍御史张昙，大理正兼监察御史王翙，右卫录事参军第五准。', '前相国京地第五公自户部侍郎出牧括州，子□关内，河东副元帅判官礼部郎中兼侍御史虞当自中都济河于华阴拜见从谒灵祠，因纪贞石时。在唐大□五年六月四日。司勋庚子中兼侍御史李国清，仓部员外兼侍御史张昙，大理正兼监察御史王翙，右卫录事参军第五准。', '无', '大历五年出牧括州过华岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (411, '6e430bcdd9da4651b74ae57b88247d8f', '焦□题名', 177, 60, 765, '765年', '前大理司直焦□与缑氏县焦琳、华州司仓参军焦贲，永泰元□春同游此庙。', '前大理司直焦□与缑氏县焦琳、华州司仓参军焦贲，永泰元□春同游此庙。', '无', '永泰间春游岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (412, 'bcdb311ea712428d9fb96661d68948df', '苏敦等题名', 178, 60, 770, '770年', '沃州司马苏敦、邶华阴令发、弟咸阳主簿参、弟太常寺主簿敞、弟吏部常选曒、弟少府监主簿教、弟□（时年十一）大□中发任华阴县令，时礼部尚书河东裴公出牧鄱阳，敦与发彻同送至此拜谒金天王，便过东驿，不避炎暑，亭午而回。故列名于前，刻石题记。五年夏六月六日。', '沃州司马苏敦、邶华阴令发、弟咸阳主簿参、弟太常寺主簿敞、弟吏部常选曒、弟少府监主簿教、弟□（时年十一）<br>大□中发任华阴县令，时礼部尚书河东裴公出牧鄱阳，敦与发彻同送至此拜谒金天王，便过东驿，不避炎暑，亭午而回。故列名于前，刻石题记。五年夏六月六日。', '无', '之官前赴岳庙谒题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (413, '4908b4fd2f824c32b2cd63ee18379ce9', '裴士淹题名', 179, 60, 770, '770年', '礼部尚书裴士淹出为饶州刺史，大□五年六月六日于此礼谒。', '礼部尚书裴士淹出为饶州刺史，大□五年六月六日于此礼谒。', '无', '贬饶州之官题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (414, '6e5a15007a224eefa52b4588e8b61b79', '元澄等题名', 180, 60, 773, '773年', '尚书虞部员外郎兼殿中侍御史元澄与大理评事卢恒、华县丞斐言则，以唐大□八年十二月三日题纪。', '尚书虞部员外郎兼殿中侍御史元澄与大理评事卢恒、华县丞斐言则，以唐大□八年十二月三日题纪。', '无', '大历八年谒庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (415, '032da184c36248a983aa445e031dea54', '上官沼题名', 181, 60, -1, '年份未详', '侍御史上官沼大□十三年七月廿九日赴东台，谒岳过。', '侍御史上官沼大□十三年七月廿九日赴东台，谒岳过。', '无', '之官前赴岳庙谒题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (416, '32ca7c7e0fc1485589a075868746c43d', '崔汉衡题名', 182, 60, 784, '784年', '莲华巍嵬，竹箭暄□，浩浩今古，憧憧往来。银青光禄大夫守兵部尚书博陵郡开国公崔汉衡，兴元元年十二月廿三日，华阴县令卢倣书。', '莲华巍嵬，竹箭暄□，浩浩今古，憧憧往来。银青光禄大夫守兵部尚书博陵郡开国公崔汉衡，兴元元年十二月廿三日，华阴县令卢倣书。', '无', '之官前赴岳庙谒题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (417, 'b2f053c098a6480ca056178ec41ca82b', '裴潾等题名', 183, 60, 793, '793年', '前万年县尉裴堪、前华阴县丞卢佩、怀州河内县丞卢渚、郑驈、进士裴谏、裴諲，贞元九季七月廿五日参军事裴潾题请雨文', '前万年县尉裴堪、前华阴县丞卢佩、怀州河内县丞卢渚、郑驈、进士裴谏、裴諲，贞元九季七月廿五日参军事裴潾题请雨文', '无', '贞元间祈雨题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (418, 'ca8c7bdcad3d47b18451d2865622846d', '裴颖题名', 184, 60, 821, '821年', '长庆元□□□十三日朝散大夫行华阴县令上柱国裴颖，奉勾当再□中门屋，至廿五日功毕。庙令张从本题。', '长庆元□□□十三日朝散大夫行华阴县令上柱国裴颖，奉勾当再□中门屋，至廿五日功毕。庙令张从本题。', '无', '之官前赴岳庙谒题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (419, '0638eee2fb664a49b69550f28651b81f', '方参等岳祠题名', 185, 60, 828, '828年', '（缺）执笾豆，为国讨叛，思契丹恳，敬祭敬拜，牢饩毕陈，所期感通，昭鉴不昧。列旌旗于绿野，罗冠剑于明庭，共展礼容，因此题记。大和二年八月廿八日。男守左骁卫仓参军上柱国赐绯鱼袋方参待从朝觐。', '（缺）执笾豆，为国讨叛，思契丹恳，敬祭敬拜，牢饩毕陈，所期感通，昭鉴不昧。列旌旗于绿野，罗冠剑于明庭，共展礼容，因此题记。大和二年八月廿八日。男守左骁卫仓参军上柱国赐绯鱼袋方参待从朝觐。', '无', '讨伐王庭凑过华岳题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (420, '16f336854f30489f995eec218ab0462e', '韦公式题名', 186, 60, 830, '830年', '□唱京兆府功曹、前秘书郎韦公式，外甥郑县尉摄华阴县主簿斐虔右公式顷年佐理斯邑，自后向逾一纪，六变官曹。今者虑以官成，身有所系，奔马到此，追寻旧游，览前题处，岂胜□□。大和四年上元日题。', '□唱京兆府功曹、前秘书郎韦公式，外甥郑县尉摄华阴县主簿斐虔<br>右公式顷年佐理斯邑，自后向逾一纪，六变官曹。今者虑以官成，身有所系，奔马到此，追寻旧游，览前题处，岂胜□□。大和四年上元日题。', '无', '官成告庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (421, 'd319e9dda48f49e5820ad399469c64d2', '李虞仲题名', 187, 60, 831, '831年', '正议大夫使持节华州诸军事守华州刺史、御史中丞充潼关□□国军等使上柱国□□□开国男食邑三□□□□金鱼袋李虞仲，大和四年七月十□□，诏以立秋修祀，南都团练判官□□□事柳乘同来□□。', '正议大夫使持节华州诸军事守华州刺史、御史中丞充潼关□□国军等使上柱国□□□开国男食邑三□□□□金鱼袋李虞仲，大和四年七月十□□，诏以立秋修祀，南都团练判官□□□事柳乘同来□□。', '无', '秋祀西岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (422, '8e50ed24ed584c95ad53d117193b0f40', '宝存辞等题名', 188, 60, 837, '837年', '□县主簿宝存辞、乡贡进士冯耽，开成二年四月存辞请假赴洛，耽侍从祈□。', '□县主簿宝存辞、乡贡进士冯耽，开成二年四月存辞请假赴洛，耽侍从祈□。', '无', '赴洛过岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (423, '4168e48bdddd4e6188418a00a40a4af9', '□庚题名', 189, 60, 838, '838年', '开成二年九月五日，庚将赴京告辞，金天王遇遐于此，便同道（缺）。', '开成二年九月五日，庚将赴京告辞，金天王遇遐于此，便同道（缺）。', '无', '赴京过岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (424, 'e8d6cc1a19534239b5f07eb44c123ecd', '陈商题名', 190, 60, 847, '847年', '□门郎中史馆修撰陈商，会昌元年七月廿五日，商□召赴，阙与卢溪处士邓君蟠同题时□□□□□。商题后六年自礼部侍郎出镇□陕，又与邓支使同来。十月□□。', '□门郎中史馆修撰陈商，会昌元年七月廿五日，商□召赴，阙与卢溪处士邓君蟠同题时□□□□□。商题后六年自礼部侍郎出镇□陕，又与邓支使同来。十月□□。', '无', '会昌元年与大中元年二度过岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (425, '7a52b796678b442eb1d2bed5665527c8', '常侍□□祀岳庙残诗', 191, 60, 842, '842年', '（缺数字）鬼神陪质明三献虽终礼祈寿千年别上杯岂是璅材□祀事宏农太守主□来（常侍曾领此句，故有□句）。会昌二年六月廿二日立秋。', '（缺数字）鬼神陪质明三献虽终礼祈寿千年别上杯岂是璅材□祀事宏农太守主□来（常侍曾领此句，故有□句）。会昌二年六月廿二日立秋。', '无', '秋祀西岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (426, '16c7fd432c1f4f92b00b810c185cf728', '崔郇等题名', 192, 60, 842, '842年', '正议大夫守京兆尹赐紫金鱼袋崔郇，华州华阴县令崔宏，会昌二年六月十六日，郇自汝海将赴阙庭，时与宏同谒庙而过。', '正议大夫守京兆尹赐紫金鱼袋崔郇，华州华阴县令崔宏，会昌二年六月十六日，郇自汝海将赴阙庭，时与宏同谒庙而过。', '无', '赴长安过岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (427, 'afc2bd4bb0584089bbd2bce3ef60724d', '崔慎由等题名', 193, 60, 845, '845年', '殿中侍御史集贤殿直学士崔慎由，右补阙李当，乡贡进士崔安潜，会昌五年二月八日同赴。', '殿中侍御史集贤殿直学士崔慎由，右补阙李当，乡贡进士崔安潜，会昌五年二月八日同赴。', '无', '谒庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (428, '708adc0e6de142e5a4136df4d5711d0f', '李□方题名', 194, 60, 847, '847年', '右议大夫李□方，大中元年三月□□雨。', '右议大夫李□方，大中元年三月□□雨。', '无', '祈雨岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (429, '5040ae202d204d56b7dcb68ab12a0835', '李贻孙祈雪题记', 142, 60, 849, '849年', '左谏议大夫充宏文馆学士判馆事赐紫金鱼袋李贻孙，大中三年十二月八日奉制祈雪，小男进士同吉学究静复从行。', '左谏议大夫充宏文馆学士判馆事赐紫金鱼袋李贻孙，大中三年十二月八日奉制祈雪，小男进士同吉学究静复从行。', '无', '祈雪岳庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (430, '68b6fda77b4f4889b9197a22bc80c6c6', '尉旻题名', 195, 60, 806, '806年', '宁远将军守左金吾卫□□军试太常卿尉旻，元和元年正月世日题。', '宁远将军守左金吾卫□□军试太常卿尉旻，元和元年正月世日题。', '无', '谒庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (431, '47c495ed93d848bebe091bc5a8690357', '王璠题名', 196, 60, 821, '821年', '起居舍人赐绯鱼袋王璠元和拾伍年□一月二十陆日□使镇州宣尉□。', '起居舍人赐绯鱼袋王璠元和拾伍年□一月二十陆日□使镇州宣尉□。', '无', '之官前赴岳庙谒题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (432, '6b46cc2d622247908b00f186594d8e23', '协律郎李□等题名', 197, 60, 821, '821年', '太常寺协律郎李□、华阴县令崔□、华阴县尉郑公幹、华阴县尉裴混、华阴县尉陶洪、进士苏□，元和十年五月十二日谒岳。', '太常寺协律郎李□、华阴县令崔□、华阴县尉郑公幹、华阴县尉裴混、华阴县尉陶洪、进士苏□，元和十年五月十二日谒岳。', '无', '谒庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (433, 'b27866165ecc45ad8ffd3cc3f0ec7d2f', '许环等题名', 198, 60, 870, '870年', '郑县丞摄尉许环、宇文珪、华阴县尉□殷图，咸通十一年十月十七日题。', '郑县丞摄尉许环、宇文珪、华阴县尉□殷图，咸通十一年十月十七日题。', '无', '谒庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (434, '1c7d4908e33e4dcca9e3c40b02fd58f8', '韦公右等题名', 199, 60, -1, '年份未详', '（缺）郎行华阴县（缺）寮吏以时麦（缺）明神咸列名（缺）。行令韦公右、行丞同正韦澣、行尉李贶、行尉刘□蒨、校书郎卢舒□。', '（缺）郎行华阴县（缺）寮吏以时麦（缺）明神咸列名（缺）。行令韦公右、行丞同正韦澣、行尉李贶、行尉刘□蒨、校书郎卢舒□。', '无', '谒庙题', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (435, 'c4879a651df84c77bab4372fe7530892', '宣室志之淮南军卒', 35, 60, -1, '年份未详', '相国陈少游派军卒赵某赴长安送信，至华阴旅舍，赵某被金天王捉去送信至成都。赵因赴成都而耽搁行程，被陈少游下狱。金天王托梦于陈，具告实情。陈遂释赵并升其为副将。', '相国陈少游派军卒赵某赴长安送信，至华阴旅舍，赵某被金天王捉去送信至成都。赵因赴成都而耽搁行程，被陈少游下狱。金天王托梦于陈，具告实情。陈遂释赵并升其为副将。', '从小中的地望看，主人公淮南军卒先后经历行淮南——华阴逆旅——华岳庙——成都——华岳庙——长安——淮南，在每一个节点均有伏笔、插叙或预叙。在淮南时，节度使陈少游命卒传书，即警告他不按时送到书信并返回，就要杀死他，从而为其命运设下伏笔；在华阴逆旅，卒夜梦有绿衣吏自称为金天王使者，为金天王出场设下伏笔；在华岳庙，金天王请军卒赴蜀送信，军卒云有使命在身，若晚归必死，金天王说但可放心前去，定不耽搁其使命，则 埋下伏笔；此时又插入金天王审案情节，虎豹麋鹿、狐兔禽鸟、鬼神精怪皆来拜谒，使紧张的故事得以中和，所谓“冷”“热”交替，颇见施“闲笔”之功。审案后金天王给军卒书信和万贯钱作为盘缠，军卒问这么多钱该如何携带，答曰置于怀中，这又为后面的钱皆为纸钱张目；行至道中发现所予钱财皆纸钱，正无助时，又插入金天王吏送真钱的“闲笔”；至蜀后见到收信人肖敬之，肖敬之又云自己如何与金天王女成婚及求官事，又一“闲笔”，然此闲笔并不“闲”，在不经意间交代了金天王遣书的目的；军卒快马加鞭至华岳庙，金天王大喜，告诉军卒不日将擢升裨将，此又为“预叙”。并且交给军卒十缣，特意交待这是人间缣帛，回应前述“纸钱”情节；军卒疾驰长安，完命后归淮南，果被陈少游责怪并羁押，军卒以金天王事告之，少游不信，欲杀之，悬念又起；金天王深夜托梦少游，告以实情，少游翌日释军卒，并擢禆将。整个故事摇曳生姿，既有一路向前的主流，又有蜿蜒缠绵的河汊；既有结言端直的主干，又有旁逸斜出的横枝；既有扎实厚重的基调，又有欢快谐趣的和声，可谓秩序中寓变化，变化中含规律。小说作者有非常严谨的“地方感”，完全按照现实时空安排情节，但在叙事过程中，又有变通和创意，在节点上大做文章，将插叙、预叙、设伏笔、下悬念等灵活运用，形成了叙事文学独特的节奏和秩序美。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (436, 'd5cc301f5c954735ba321bed3704612d', '广异记之三卫', 112, 60, -1, '年份未详', '开元初有做三卫者自长安赴青州，于岳庙遇华山府君第三子之妻，托其给父亲北海君捎书言其受欺。三卫许之。北海君谢其二疋绢布，售得二万贯。三卫还青州，于华阴复遇前妇，告知其夫将在潼关报复三卫，须待玄宗幸洛时乘鼓车过关方可免祸。后果如所言。', '开元初有做三卫者自长安赴青州，于岳庙遇华山府君第三子之妻，托其给父亲北海君捎书言其受欺。三卫许之。北海君谢其二疋绢布，售得二万贯。三卫还青州，于华阴复遇前妇，告知其夫将在潼关报复三卫，须待玄宗幸洛时乘鼓车过关方可免祸。后果如所言。', '小说将地域的写实与历史的写实结合起来，又加入奇幻的传书和神灵斗法的情节，虚实相生，亦真亦幻，极大地提高了阅读的兴味。文中描写北海君讨伐华山府君的情节非常传神。神灵斗法时，但见墨云翻涌，雷声震彻，电光雪亮，惮赫百里。华山飓风折树，雷电击中岩石树木，火光冲天，与电光交汇，炫目震撼。大战过后，白银世界的华山变成焦黑涸赤。此段文字颇有力度，将一场大自然的雷雨用神异奇幻的故事展现出来，令人胆寒。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (437, '3edc1e117fd84d5f87ba8b078b7707e2', '闻奇录之韦叔文', 200, 60, -1, '年份未详', '唐进士韦叔文赴举过华岳庙，夜梦金天王召见，索二马。适李叔文方画二马，金天王命其着色以献，并告知翌年易名参加科考必及第。果验之。', '唐进士韦叔文赴举过华岳庙，夜梦金天王召见，索二马。适李叔文方画二马，金天王命其着色以献，并告知翌年易名参加科考必及第。果验之。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (438, '646df8e6cc8a42769179101126e540dd', '国史记之李靖', 0, 60, -1, '年份未详', '卫公李靖，始困于贫贱，因过华山庙，诉于神，且请告以官位所至。辞色抗厉，观者异之。伫立良久，乃出庙门百许步，闻后大声曰：“李仆射好去。”顾之不见人。后竟至端揆。', '卫公李靖，始困于贫贱，因过华山庙，诉于神，且请告以官位所至。辞色抗厉，观者异之。伫立良久，乃出庙门百许步，闻后大声曰：“李仆射好去。”顾之不见人。后竟至端揆。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (439, 'b4add39534204660be3aca65a6a7aa6d', '稽神录之华阴店妪', 201, 60, -1, '年份未详', '天复间杨彦伯赴长安科考，宿华阴旅店。临发前店妪云京城祸乱，杨彦伯仕途不顺，然其官运在江淮，终为门下侍郎。后果为户部侍郎，终为门下侍郎。', '天复间杨彦伯赴长安科考，宿华阴旅店。临发前店妪云京城祸乱，杨彦伯仕途不顺，然其官运在江淮，终为门下侍郎。后果为户部侍郎，终为门下侍郎。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (440, '7840bacd64b644c2b61f391602fcd0b8', '集异记之张光晟', 202, 60, -1, '年份未详', '张光晟在华岳庙祭金天王并问前程穷通，夜梦金天王告知若为相，天下必太平。建中间德宗西狩，光晟邀朱泚扈从，适适泾原叛兵至，朱泚遂反。扶光晟为平章事，统兵大败，朱泚伏诛。', '张光晟在华岳庙祭金天王并问前程穷通，夜梦金天王告知若为相，天下必太平。建中间德宗西狩，光晟邀朱泚扈从，适适泾原叛兵至，朱泚遂反。扶光晟为平章事，统兵大败，朱泚伏诛。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (441, '00490b2b4dce4f2da5118f526b54a8a5', '玉堂闲语之郑昌图', 203, 60, -1, '年份未详', '郑昌图未及第前，遇一仆从，曰路遇自泰山给金天王送榜者，云状头与榜尾姓偏傍有“阝”，名两字且下一字在口中。其年昌图果中榜头，榜尾为邹希回。皆如所言。', '郑昌图未及第前，遇一仆从，曰路遇自泰山给金天王送榜者，云状头与榜尾姓偏傍有“阝”，名两字且下一字在口中。其年昌图果中榜头，榜尾为邹希回。皆如所言。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (442, '9a6bcb5f5b974155aeb2718902745be4', '录异记之进士崔生', 204, 60, -1, '年份未详', '崔生进京赶考过岳庙，路遇天官崔侍御，遂为其做敷水桥神的表丈求官为南山觜神。事果成，表丈答谢五百疋绸缎，并以神力保全崔生家于渭水洪流。', '崔生进京赶考过岳庙，路遇天官崔侍御，遂为其做敷水桥神的表丈求官为南山觜神。事果成，表丈答谢五百疋绸缎，并以神力保全崔生家于渭水洪流。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (443, 'fd18e1b35e304bc68e8bb805b90c3bac', '奇录之张偃', 0, 60, -1, '年份未详', '张偃赴举过金天王庙，夜梦金天王问事，言偃将被赤狸虎所食。偃惧，求于金天王，王命偃过虎食之时再出发，并云虎将被王存射死。偃择时出发，果如王所言。', '张偃赴举过金天王庙，夜梦金天王问事，言偃将被赤狸虎所食。偃惧，求于金天王，王命偃过虎食之时再出发，并云虎将被王存射死。偃择时出发，果如王所言。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (444, '429ab5008c074fa08d1cd8f3e8e35c67', '广异记之王僴', 112, 60, -1, '年份未详', '王僴开元末应通事舍人举，夜宿阙西槐树下，梦有使者携其至华岳庙，见其妻被拷打，使者悯之，遂放其归家解救。僴之魂魄骑狐狸飞奔至家，其妻复生。后十余日身方至，与魂魄合一。', '王僴开元末应通事舍人举，夜宿阙西槐树下，梦有使者携其至华岳庙，见其妻被拷打，使者悯之，遂放其归家解救。僴之魂魄骑狐狸飞奔至家，其妻复生。后十余日身方至，与魂魄合一。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (445, 'db811ca003414a8585eae32a3d53a8ec', '广异记之王僴', 112, 60, -1, '年份未详', '书生进京赶考，宿于关西逆旅，邂逅公主，与之同寝并载之怀远里，同居七载生二子一女。后谓书生曰其非人，遂为书生娶妇，仍阴与之往来。书生妇求术士之符阴藏书生之衣，公主知之，与书生诀。自言华岳第三女云云。', '书生进京赶考，宿于关西逆旅，邂逅公主，与之同寝并载之怀远里，同居七载生二子一女。后谓书生曰其非人，遂为书生娶妇，仍阴与之往来。书生妇求术士之符阴藏书生之衣，公主知之，与书生诀。自言华岳第三女云云。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (446, 'a29b6741f7a24669bb52b492e1e8c863', '广异记之仇嘉福', 112, 60, -1, '年份未详', '富平人仇嘉福赴洛赶考，路遇白姓少年同行至华岳庙。少年自云天帝使赴人家审案，并解救嘉福之妻。后在嘉福之请下又解邓州崔司法之妻。少年原为太乙神，为嘉福回换五六政官。', '富平人仇嘉福赴洛赶考，路遇白姓少年同行至华岳庙。少年自云天帝使赴人家审案，并解救嘉福之妻。后在嘉福之请下又解邓州崔司法之妻。少年原为太乙神，为嘉福回换五六政官。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (447, 'c164b0b35d8a47ecbdda1496391031fc', '广异记之刘可大', 112, 60, -1, '年份未详', '天宝间刘可大举进士，路遇少年同行，自云华山神之子。可大遂问仕途，对方云翌年进士及第上，历官七任。大可苦求当年及第，少年云若改当年仅得县尉。可大固求，许之。果以荥阳县尉而终。', '天宝间刘可大举进士，路遇少年同行，自云华山神之子。可大遂问仕途，对方云翌年进士及第上，历官七任。大可苦求当年及第，少年云若改当年仅得县尉。可大固求，许之。果以荥阳县尉而终。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (448, '9140f479f396431fb0001e1d958e6ee4', '广异记之李湜', 112, 60, -1, '年份未详', '李湜开元中谒华岳，邂逅三妇，自云神女，自荐枕席。后每岁岳神上天时，湜均与神女欢会。术士予湜符，三女遂不得近，訾之。湜问及官运，云将及第，然终小县令。', '李湜开元中谒华岳，邂逅三妇，自云神女，自荐枕席。后每岁岳神上天时，湜均与神女欢会。术士予湜符，三女遂不得近，訾之。湜问及官运，云将及第，然终小县令。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (449, '7cf07d67651a4613be5033737aec5328', '广异记之王勋', 112, 60, -1, '年份未详', '进士王勋与其徒赵望舒谒岳庙，被神女像所惑，气绝。望舒急呼巫于像前鼓舞，勋方醒。怪望舒坏其好事，正与神女缠绵缱绻，不意竟被失落至地，遂醒。', '进士王勋与其徒赵望舒谒岳庙，被神女像所惑，气绝。望舒急呼巫于像前鼓舞，勋方醒。怪望舒坏其好事，正与神女缠绵缱绻，不意竟被失落至地，遂醒。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (450, '0882ad1eb2fe4726b3b2e30cf9e7b723', '逸史之李主簿妻', 205, 60, -1, '年份未详', '先人李主簿携新妇拜金天王，新妇忽气绝。李主簿请叶仙师以符救之，须臾开眼能言。自谓被金天王留取云云。', '先人李主簿携新妇拜金天王，新妇忽气绝。李主簿请叶仙师以符救之，须臾开眼能言。自谓被金天王留取云云。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (451, 'f741bc985f5243998ffdfe7cffcd4671', '逸史之皇甫弘', 205, 23, -1, '年份未详', '华州皇甫弘赴举陕州，因此前酒仵主考官钱徽，自知不中，欲返。夜梦已故乳母携其赴石婆庙求神，石婆曰必中。皇甫遂赴举，果及第。过陕州，果有石婆神，飨之。', '华州皇甫弘赴举陕州，因此前酒仵主考官钱徽，自知不中，欲返。夜梦已故乳母携其赴石婆庙求神，石婆曰必中。皇甫遂赴举，果及第。过陕州，果有石婆神，飨之。', '皇甫弘科考经历反映出唐士子流连场屋、仕进艰难的境况。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (452, '0048fbf11ed74fa380a74f57788f6deb', '纪闻之桃林令', 206, 60, -1, '年份未详', '桃林令韩光祚携家人之官，谒岳庙，妾暴卒。巫言华岳神第三子取之。光祚命匠人以金铸观世音菩萨像，暂止之。复以千钱召金工造像，金工以千钱赎将屠之豕。后豕驱三郎迎娶之仗，妾遂免。', '桃林令韩光祚携家人之官，谒岳庙，妾暴卒。巫言华岳神第三子取之。光祚命匠人以金铸观世音菩萨像，暂止之。复以千钱召金工造像，金工以千钱赎将屠之豕。后豕驱三郎迎娶之仗，妾遂免。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (453, '01c3b4872af54f0498c6ce11cf25d9d1', '河东集之郑驯', 0, 60, -1, '年份未详', '贞元进士郑驯因霍乱死于渭桥给纳判官高叔让处，因天热，高仓卒办葬礼。同邑李道古从虢归华阴，于潼关见郑驯，二人同乘马而归。郑不肯过华岳庙，李独自归县，方知郑已死，所见者鬼也。', '贞元进士郑驯因霍乱死于渭桥给纳判官高叔让处，因天热，高仓卒办葬礼。同邑李道古从虢归华阴，于潼关见郑驯，二人同乘马而归。郑不肯过华岳庙，李独自归县，方知郑已死，所见者鬼也。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (454, 'dd7d4d780c564b3f98da191b29b544b3', '河东集之板桥三娘子', 0, 49, -1, '年份未详', '板桥店娃三娘子以幻术驱木偶木牛耦耕植荞麦，做成饼后食客食之皆化为驴。元和中许州客赵季和施计令三娘子自食其饼变为驴，骑之西行，至赤水驿遇老人相救，释之。', '板桥店娃三娘子以幻术驱木偶木牛耦耕植荞麦，做成饼后食客食之皆化为驴。元和中许州客赵季和施计令三娘子自食其饼变为驴，骑之西行，至赤水驿遇老人相救，释之。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (455, '9bf6af9e5a6649299173b3501053d28f', '经慈涧题', 207, 31, -1, '年份未详', '岸与恩同广，波将慈共深。涓涓劳日夜，长似下流心。', '岸与恩同广，波将慈共深。涓涓劳日夜，长似下流心。', '以慈涧之“慈”喻慈涧水之柔，隐喻人之情，可谓精妙。', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (456, '6183f1bf82fb4c71bc9f650d02f8360b', '陈留郡上源新驿记', 208, 50, -1, '年份未详', '《周官》：行夫掌邦国传遽之事，施於政者，盖有章焉。皇唐之兴，盛於古制。自京师四极，经启十道，道列以亭，亭实以驷。而亭惟三十里，驷有上中下。丰屋美食，供亿是为，人迹所穷，帝命流洽。用之远者，莫若於斯矣。伊陈留雄称山东，声英海内，昌大嚣庶；有梁魏之遗迹，风烟两河之眇，襟带九州之半。洎皇华轺传，夷使骏奔，出关而驰，南向北户，山川水陆之役，兆於是矣。故上源所置，与其难哉！居里之冲，濒河之阳，地形湫隘，馆次卑狭，巽在堤下，面於剧旁，走庭以隅，建步终坎，车靡方驾，骑无并鞭，其郁闭有如此者！壬辰岁，太守元公连率河南之三载也。尧治四岳而神人理，汉诏八使而风俗清。举德推贤，事高典策，革已成之弊，持独断之明。迨兹邮亭，俯视颓朽，何逼侧蹇浅，不称其声？将圆鼎新，岂曰仍旧？顾谓长史李公曰：「夫开释故实，发挥制度，不有攸居者，谁其允协？今奏计阙前，先甲而往，小大之务，公其领之。」申命录事参军冯元掌曰：「维操绳墨者盖用於正；蕴廉慎者，俾临於财。公以正身，用财均力，纪纲相佐，善莫大焉。」复命浚仪令裴胜曰：「公之为县也，简易於理，训迪其源，秉清白之一门，据忠信之馀地。夫忠以创物，清而守官，立言有程，指使而可。」於是北吞里室，人以利迁也；南豁路旅，事无苟免也。合土以峻墉，攻木以高户，栋宇相翼，群材如生。兹所谓动乃有经，徐而不费。於戏！久於否者，宜以改作；本於功者，终乎永贞。则亭之成焉，我方访王公澄清之初也。公时膺迈德，天与大才，属梁宋不登，朝廷旰食，求瘼之重，不其然欤？用能官去?比政，人无菜色，百城偃於迅风，万象纳於明镜。乃因寮吏，慨然於兹亭曰：「且夫木石之新者，而犹可观；况人而自新，孰不观者？」又曰：「《传》不云乎？『启塞从时』，用之善者。而今而後，吾以无事为事焉。」君子是以知邮亭之可嘉，而我公之清净无穷也。末吏不敏，纪於贞石云。', '《周官》：行夫掌邦国传遽之事，施於政者，盖有章焉。皇唐之兴，盛於古制。自京师四极，经启十道，道列以亭，亭实以驷。而亭惟三十里，驷有上中下。丰屋美食，供亿是为，人迹所穷，帝命流洽。用之远者，莫若於斯矣。伊陈留雄称山东，声英海内，昌大嚣庶；有梁魏之遗迹，风烟两河之眇，襟带九州之半。洎皇华轺传，夷使骏奔，出关而驰，南向北户，山川水陆之役，兆於是矣。故上源所置，与其难哉！居里之冲，濒河之阳，地形湫隘，馆次卑狭，巽在堤下，面於剧旁，走庭以隅，建步终坎，车靡方驾，骑无并鞭，其郁闭有如此者！壬辰岁，太守元公连率河南之三载也。尧治四岳而神人理，汉诏八使而风俗清。举德推贤，事高典策，革已成之弊，持独断之明。迨兹邮亭，俯视颓朽，何逼侧蹇浅，不称其声？将圆鼎新，岂曰仍旧？顾谓长史李公曰：「夫开释故实，发挥制度，不有攸居者，谁其允协？今奏计阙前，先甲而往，小大之务，公其领之。」申命录事参军冯元掌曰：「维操绳墨者盖用於正；蕴廉慎者，俾临於财。公以正身，用财均力，纪纲相佐，善莫大焉。」复命浚仪令裴胜曰：「公之为县也，简易於理，训迪其源，秉清白之一门，据忠信之馀地。夫忠以创物，清而守官，立言有程，指使而可。」於是北吞里室，人以利迁也；南豁路旅，事无苟免也。合土以峻墉，攻木以高户，栋宇相翼，群材如生。兹所谓动乃有经，徐而不费。於戏！久於否者，宜以改作；本於功者，终乎永贞。则亭之成焉，我方访王公澄清之初也。公时膺迈德，天与大才，属梁宋不登，朝廷旰食，求瘼之重，不其然欤？用能官去?比政，人无菜色，百城偃於迅风，万象纳於明镜。乃因寮吏，慨然於兹亭曰：「且夫木石之新者，而犹可观；况人而自新，孰不观者？」又曰：「《传》不云乎？『启塞从时』，用之善者。而今而後，吾以无事为事焉。」君子是以知邮亭之可嘉，而我公之清净无穷也。末吏不敏，纪於贞石云。', '无', '无法确定', 1574400380, 0, 0);
INSERT INTO `jbd_poetry` VALUES (457, '40ce792114714ebea683da300ac05d13', '春日离长安客中言怀', 3, 66, 640, '公元640年', '年华开早律，霁色荡芳尘。城阙千门晓，山河四望春。御沟通太液，戚里对平津。宝瑟调中妇，金罍引上宾。剧谈推曼倩，惊坐揖陈遵。意气一言合，风期万里亲。自惟安直道，守拙忌因人。谈器非先木，图荣异后薪。揶揄惭路鬼，憔悴切波臣。玄草终疲汉，乌裘几滞秦。生涯无岁月，岐路有风尘。还嗟太行道，处处白头新。', '年华开早律，霁色荡芳尘。城阙千门晓，山河四望春。御沟通太液，戚里对平津。宝瑟调中妇，金罍引上宾。剧谈推曼倩，惊坐揖陈遵。意气一言合，风期万里亲。自惟安直道，守拙忌因人。谈器非先木，图荣异后薪。揶揄惭路鬼，憔悴切波臣。玄草终疲汉，乌裘几滞秦。生涯无岁月，岐路有风尘。还嗟太行道，处处白头新。', '此诗当作于贞观十四年庚子（公元640年）。时年二十二岁，诗人进京应试，不第，游于京、洛，后回义乌省亲。春意破空而来。碧空如练，芳气袭人。诗人晨起将发，回望长安，御沟萋萋连通太液，宫阙迢递延伸平津。诗人想象着自己也是那紫阁丹楼里的座上之宾。有如东方曼倩之俦，得遇陈遵投辖之举。承蒙圣上恩眷，风信相投，入仕以报效知遇之恩。“谈器”二句犹写诗人对自己的宽慰自解，大器晚成也未可知。而后笔锋一转，回到残酷的现实。虽然文阙三冬，结果还如苏秦说秦一样“书劳十上”，失意落第。离长安越来越远，入仕之路越来越艰辛。前路漫漫，内心不由写满凄怆，何时才能衣锦还乡，不逾虚诞。', '此诗当作于贞观十四年庚子（公元640年）。时年二十二岁，诗人进京应试，不第，游于京、洛，后回义乌省亲。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (458, '9f5ed8699e734e989b9f7ea92e18643b', '经三泉路作', 28, 41, 721, '公元721年', '三月松作花，春行日渐赊。竹障山鸟路，藤蔓野人家。透石飞梁下，寻云绝磴斜。此中谁与乐，挥涕语年华。', '三月松作花，春行日渐赊。竹障山鸟路，藤蔓野人家。透石飞梁下，寻云绝磴斜。此中谁与乐，挥涕语年华。', '开元九年（公元721年），时年五十二岁。诗人任检校益州长史，三月，由京入蜀途中过三泉驿而作。入蜀途中的艰辛使苏颋感受到悲愁与迷惘的同时，也嗟叹于时间的骤然飞逝。阳春三月，正是草长莺飞，游园赋诗的好时节。诗人却不得不拖着老迈的身躯，在深山野林间踯躅而行。松树抽出了花蕊，天气日暖，白昼渐渐拉长。一路上竹林繁茂，枝桠斜溢，密林阻挡着山鸟，只能绕飞。藤曼缠绕着树林，一直蜿蜒到野村人家的院落。蜀道艰险，山鸟便翾尚不得轻易飞过，更何况我这年过半百的垂老之人？极目骋望，那连接山石的飞桥忽然穿石而下，忽然沿石而上，纵伸向云雾深处。峥嵘春景横绝眼前，诗人却顾不得低回流连，只有自己掩面拭泪感叹韶华已逝了。', '开元九年（公元721年），时年五十二岁。诗人任检校益州长史，三月，由京入蜀途中过三泉驿而作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (459, '6c07c44c94524474bf798b760418b866', '夜发三泉即事', 28, 41, 722, '公元722年', '暗发三泉山，穷秋听骚屑。北林夜鸣雨，南望晓成雪。只咏北风凉，讵知南土热。沙溪忽沸渭，石道乍明灭。宛若银碛横，复如瑶台结。指程赋所恋，遇虞不遑歇。重纩濡莫解，悬旌冻犹揭。下奔泥栈榰，上觏云梯设。搏颊羸马顿，回眸惴人跌。憧憧往复还，心注思逾切。冉冉年将病，力困衰怠竭。天彭信方隅，地势诚斗绝。忝曳尚书履，叨兼使臣节。京坻有岁饶，亭障无边孽。归奏丹墀左，骞能俟来哲。', '暗发三泉山，穷秋听骚屑。北林夜鸣雨，南望晓成雪。只咏北风凉，讵知南土热。沙溪忽沸渭，石道乍明灭。宛若银碛横，复如瑶台结。指程赋所恋，遇虞不遑歇。重纩濡莫解，悬旌冻犹揭。下奔泥栈榰，上觏云梯设。搏颊羸马顿，回眸惴人跌。憧憧往复还，心注思逾切。冉冉年将病，力困衰怠竭。天彭信方隅，地势诚斗绝。忝曳尚书履，叨兼使臣节。京坻有岁饶，亭障无边孽。归奏丹墀左，骞能俟来哲。', '开元十年（公元722年）九月，苏颋从益州准备返长安参加祭汾活动，再次经过三泉时，有感而作。此诗着重表现了苏颋作为尚书大臣、皇家使节不辞年迈体衰为国奔走报效的一片赤诚。诗人先以描述风雪交加的暮秋，梁州三泉一带山道的自然环境来突出此行之艰难。接着表述自己希望国家富庶、边陲安定的愿景。全诗以短促、凄苦的入声韵更进一步表现诗人内心的苦况。从而衬托出为国效命、坚贞不屈的精神世界。', '开元十年（公元722年）九月，苏颋从益州准备返长安参加祭汾活动，再次经过三泉时，有感而作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (460, 'c1142e4749054995a9668c46f884f30b', '题寿安王主簿池馆', 28, 23, 720, '公元720—公元725年', '洛邑通驰道，韩郊在属城。馆将花雨映，潭与竹声清。贤俊鸾栖棘，宾游马佩衡。愿言随狎鸟，从此濯吾缨。', '洛邑通驰道，韩郊在属城。馆将花雨映，潭与竹声清。贤俊鸾栖棘，宾游马佩衡。愿言随狎鸟，从此濯吾缨。', '此诗当作于开元八年至开元十三年，诗人两次入蜀途中。首联点名池馆的地理位置。颔联描写池中清幽的环境，池馆与落花相映成趣，潭水与竹声清越动人。颈联由物写人，置身如此古雅之境，诗人却联想到鸾鸟栖息在隐刺的灌木丛中，怎么与德才兼备之人遭受宵小构陷如此类似呢？当是香草美人象征手法的妙用。心随意动，乃至驭车之马所佩装在车辕上限制它的横木，都使人感到环境恶劣，与眼前的美景是如此难融。在尾联，诗人以“狎鸟”、“濯缨”两个典故把自己退隐的情绪淋漓尽致地揭示出来。狎鸟是咏超逸出世生活情趣之典，濯缨是喻超脱尘俗、清高自守之典。两者合用，足以深刻地展示诗人向往隐逸生活的内心世界。', '此诗当作于开元八年至开元十三年，诗人两次入蜀途中。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (461, '816ecb33cf234b9eb6cea332808c65ed', '送崔融', 209, 66, 696, '公元696年', '君王行出将，书记远从征。祖帐连河阙，军麾动洛城。旌旃朝朔气，笳吹夜边声。坐觉烟尘扫，秋风古北平。', '君王行出将，书记远从征。祖帐连河阙，军麾动洛城。旌旃朝朔气，笳吹夜边声。坐觉烟尘扫，秋风古北平。', '武则天万岁通天元年（公元696年），杜审言于洛阳送著作佐郎崔融随梁王武三思东征契丹而作。杜审言在描绘自然环境时，时露“诗家缚虎手”的千钧之力。诗人紧抓关山朔风、戎衣胡笳等边地特点，对祖饯之盛、军威之壮、边塞之奇三个方面作了尽情的渲染。所写境界及其开阔，纵横捭阖间洋溢着豪迈的战斗热情。情调的高昂和笔力的雄健，构成了该诗的主要风格。', '武则天万岁通天元年（公元696年），杜审言于洛阳送著作佐郎崔融随梁王武三思东征契丹而作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (462, 'd37057edf3be43dda5db8e956c621e83', '留别之望舍弟', 26, 66, 705, '公元705年', '同气有三人，分飞在此晨。西驰八岭徼，东去洛阳滨。强饮离前酒，终伤别后神。谁怜散花萼，独赴日南春。', '同气有三人，分飞在此晨。西驰八岭徼，东去洛阳滨。强饮离前酒，终伤别后神。谁怜散花萼，独赴日南春。', '公元705年春，中宗复辟，坐附二张，贬陇州参军。赴贬之前，留别舍弟之逊，遂作此诗。发端点题，极言惜别之感伤。犹如伯劳飞燕，风流云散。颔联二句参互成文，此去山高水长，不知归期。古人重离别，遂有江淹《别赋》云：“有别必怨，有怨必盈，使人意夺神骇，心折骨惊。”自古别离总少不了饮酒，而醉意总将离愁别绪酝酿得更浓。尾二句以花散喻人离，悲恻之情弥漫开来。落笔至此，感伤的基调笼罩全篇，诗兴婉惬，颇为动人。', '公元705年春，中宗复辟，坐附二张，贬陇州参军。赴贬之前，留别舍弟之逊，遂作此诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (463, 'a1f79072ece047dcaa765c184117e3a5', '同储十二洛阳道中作', 210, 66, 729, '公元729年', '珠弹繁华子，金羁游侠人。酒酣白日暮，走马入红尘。', '珠弹繁华子，金羁游侠人。酒酣白日暮，走马入红尘。', '唐代的东都洛阳，是孟浩然一生往来最多的所在之一。公元729年2月，孟浩然逗留洛阳数月，此诗当作于刚入洛之际。此诗沿袭了作者一贯的洒脱自然。沈德潜称其为：“语淡而味终不薄”。通篇只是把入洛的行为简单地叙述下来，不事铺张，也隐隐感到作者跳脱灵魂束缚的不羁与爽利。“酒”于此处不化入愁肠，而凝于澎湃热血，炼成干云豪气。全诗寥寥二十字，却如行云流水般畅行无滞。在白描中见整炼之致，经纬绵密处又似不经意道出。一气挥洒，妙极自然。其超脱之神韵，蕴藉之心态，洵为大唐正声。', '公元729年2月，孟浩然逗留洛阳数月，此诗当作于刚入洛之际。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (464, 'c790eb92ad5c4b5091d1fae9ce31df49', '留别郑三韦九兼洛下诸公', 208, 66, 749, '公远749年', '忆昨相逢论久要，顾君哂我轻常调。羁旅虽同白社游，诗书已作青云料。蹇质蹉跎竟不成，年过四十尚躬耕。长歌达者杯中物，大笑前人身后名。幸逢明盛多招隐，高山大泽徵求尽。此时亦得辞渔樵，青袍裹身荷圣朝。犁牛钓竿不复见，县人邑吏来相邀。远路鸣蝉秋兴发，华堂美酒离忧销。不知何日更携手，应念兹晨去折腰。', '忆昨相逢论久要，顾君哂我轻常调。羁旅虽同白社游，诗书已作青云料。蹇质蹉跎竟不成，年过四十尚躬耕。长歌达者杯中物，大笑前人身后名。幸逢明盛多招隐，高山大泽徵求尽。此时亦得辞渔樵，青袍裹身荷圣朝。犁牛钓竿不复见，县人邑吏来相邀。远路鸣蝉秋兴发，华堂美酒离忧销。不知何日更携手，应念兹晨去折腰。', '公元749年，唐玄宗天宝八载，时年四十六岁，为阳太守张九皋所举荐，应有道科，中第，授封丘尉。此诗当作于此间。此诗不落普通的送别诗唱酬之俗套。高适作诗少见景致名物的描摹，而善于议论抒情。轻哂调侃间可见达观，字里行间洋溢着新官上任的喜悦，其喜怒形色可以想见。昔日落拓猥琐，拟作谈资，不值一提；今日平步青云，恣意任诞，不必拘谨。远路迢递难以阻挡秋意的生发，推杯换盏间溘然消解往事之多舛。此二句豪气纵升，酒酣耳热间一扫胸中垒块。尾句点名题意，又极诙谐，虽言离别，却多是相逢的憧憬和期待，当与诗人粗放的心理结构暗相契合。', '公元749年，唐玄宗天宝八载，时年四十六岁，为阳太守张九皋所举荐，应有道科，中第，授封丘尉。此诗当作于此间。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (465, '624e06621ff84706abd0bb5a3843f09b', '还东山洛上作', 6, 66, 734, '公元734年', '春流急不浅，归枻去何迟。愁客叶舟里，夕阳花水时。云晴开螮蝀，棹发起鸬鹚。莫道东山远，衡门在梦思。', '春流急不浅，归枻去何迟。愁客叶舟里，夕阳花水时。云晴开螮蝀，棹发起鸬鹚。莫道东山远，衡门在梦思。', '开元二十二年（公元734年），岑参20岁岁至长安，献书求仕无成，奔走京洛，此诗当作于由偃师往返洛阳所作。起句发问，道出诗人心中困惑。仔细思量，原来湍流急旋哪曾追赶得上诗人归心似箭。颔联是离愁的升发。独在异乡为客，已是羁旅；寓居扁舟一叶，甚言漂泊。黄昏向晚，夕阳的余晖在水面洒下粼粼波光，像是一片盛开的花海，漾开了弥漫的愁意。颈联词意一迭宕，由乌云蔽日到晴空万里，只需要一个起棹扬帆的瞬间。尾句是诗人的自我宽慰。大有苏轼“此心归处是吾乡”之旷达，饶有意味。纵然仕途濩落，亦有州闾衡门，可以栖迟。', '开元二十二年（公元734年），岑参20岁岁至长安，献书求仕无成，奔走京洛，此诗当作于由偃师往返洛阳所作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (466, '9664dcbc83254937ae44c4c644a74dfb', '过缑山王处士黑石谷隐居', 6, 68, 758, '公元758年', '旧居缑山下，偏识缑山云。处士久不还，见云如见君。别来逾十秋，兵马日纷纷。清溪开战场，黑谷屯行军。遂令巢由辈，远逐麋鹿群。独有南涧水，潺湲如昔闻。', '旧居缑山下，偏识缑山云。处士久不还，见云如见君。别来逾十秋，兵马日纷纷。清溪开战场，黑谷屯行军。遂令巢由辈，远逐麋鹿群。独有南涧水，潺湲如昔闻。', '公元758年，岑参过缑氏山王处士隐居所黑石谷，有诗抒怀。缑山鹤飞，自古便是羽化升仙得道参悟之妙处。“偏”字似乎有所针对，是朝夕相对间才有的熟稔与热络。远道而来，不一定是客，也有可能是旧识。“见云如见君”，睹物思人当脱胎于此。“别来”四句，穿插回忆，交待了诗人数十载间的往来行迹。喋血疆场，戮力报国，早已力不从心。遂动避世之念，不若效仿许由浮瓢、巢父洗耳，从此落戈归隐，渔樵山水，悠游卒年。', '公元758年，岑参过缑氏山王处士隐居所黑石谷，有诗抒怀。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (467, '7bb964b28311470da306d2a6e7bb1593', '偃师东与韩樽同诣景云晖上人即事', 6, 45, -1, '年代不详', '山阴老僧解楞伽，颍阳归客远相过。烟深草湿昨夜雨，雨后秋风渡漕河。空山终日尘事少，平郊远见行人小。尚书碛上黄昏钟，别驾渡头一归鸟。', '山阴老僧解楞伽，颍阳归客远相过。烟深草湿昨夜雨，雨后秋风渡漕河。空山终日尘事少，平郊远见行人小。尚书碛上黄昏钟，别驾渡头一归鸟。', '唐朝时佛教发展鼎盛，一时风会所趋，岑参受风潮鼓动，再加上自身独特的漫游经历，遂与僧人、处士交游甚密。映入眼帘的是老僧传解楞伽经的画面，如此禅意弥漫的场景，当然要有听众才不枉费诗人的良苦用心。接着以远道而来的归客缀之，“远”犹言风尘之态；“过”当作“拜谒”之举，紧扣诗题中的“诣”，羁旅之行依稀可见。“烟深”二句由动态的放映转为静态的描摹。“深”字用得极妙，将雨后晓雾杳霭的幽静迷蒙写得深邃；“湿”沾染了身体的触觉，同样是写雨，却隐而不露，又想见淋漓。渐入禅境，而不复人间烟火。“空山”与“古寺”相得益彰，犹言不受俗世牵扰的百无聊赖。远郊平畴无际，又有意选择了“渺小”之行人与其相对，通过大小比对迭宕出诗意。尾句笔力益深，黄昏落日、渡头归鸟，层层意象相叠，足以令人息心淹留。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (468, '140a887f2e7947b0be5989973664367b', '春寻河阳陶处士别业', 6, 45, 739, '公元739年', '风煖日暾暾，黄鹂飞近村。花明潘子县，柳暗陶公门。药椀摇山影，鱼竿带水痕。南桥车马客，何事苦喧喧。', '风煖日暾暾，黄鹂飞近村。花明潘子县，柳暗陶公门。药椀摇山影，鱼竿带水痕。南桥车马客，何事苦喧喧。', '公元739年，岑参23岁时漫游河朔之地，春至河阳，访闻处士。岑参边塞诗古今独步，但他绝不止于“边塞”。他曾隐逸嵩阳十年之久，他笔下的自然田园也熔铸了诗人自身独特的生命体验。和风袅袅，焱日暾暾，扑面而来的盎然春意已让人微醺。更有黄鹂、村郭次第点染，诗人以明丽画笔描绘了一幅充满张力的乡间春日图。此番春日踏青乃是诗人追寻魏晋名士的风流雅致而来，何以见得？西晋潘岳曾援受河阳令，东晋陶潜更是隐居在此，终日以琴酒诗书自娱。“浮光掠金，静影沉碧，渔歌互答，此乐何极”，倘若结庐在此般人间仙境，又岂是车马逢迎，风尘邀结之人所能体会的简单的快乐。这首诗与岑参的典型之作不同，没有蔽日的沙尘，凛冽的萧风，而是以细腻笔触宕开，正所谓“放情山水，故常怀逸念（辛文房《唐才子传》），读之尤能令人感怀。”', '公元739年，岑参23岁时漫游河朔之地，春至河阳，访闻处士。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (469, '6d93f898fc52497dadb69c8b28cf715a', '秋夜宿龙门香山寺奉寄王方城十七丈奉国莹上人从弟幼成令问', 5, 66, 731, '公元731年', '朝发汝海东，暮栖龙门中。水寒夕波急，木落秋山空。望极九霄迥，赏幽万壑通。目皓沙上月，心清松下风。玉斗横网户，银河耿花宫。兴在趣方逸，欢余情未终。凤驾忆王子，虎溪怀远公。桂枝坐萧瑟，棣华不复同。流恨寄伊水，盈盈焉可穷。', '朝发汝海东，暮栖龙门中。水寒夕波急，木落秋山空。望极九霄迥，赏幽万壑通。目皓沙上月，心清松下风。玉斗横网户，银河耿花宫。兴在趣方逸，欢余情未终。凤驾忆王子，虎溪怀远公。桂枝坐萧瑟，棣华不复同。流恨寄伊水，盈盈焉可穷。', '公元731年，暮秋到洛阳，宿龙门香山寺，有诗临赠友人。李白涉水越山，朝发汝水而夕至龙门，是夜居于香山寺。次二句点明了时令。一叶落而知天下秋，兀自伫立于深山，落叶簌簌，满目萧条，尤显空荡幽寂。水也沾染了秋意，夕波湍流，骤升寒气。没有了林叶的遮挡，视线畅通无阻，仿佛可以贯通九宵云巅，纵赏万壑千幽，顿觉目明心清。转瞬已至深夜，心事挑拨，难于成寐。但见北斗星明澈横亘于雕窗上，银河光粼粼洒耀在佛寺前。隔绝了世间纷杂喧嚷，不受教化约束，肆意冥想万物真机。神思遨游间，不禁抚忆起王上凤鸾翠驾，何时才能相见？或是干脆坐化于此，谛悟禅经，一如庐山东林寺的高僧慧远，摒弃朝服，誓入罗浮。从此葳蕤常棣之华，似海兄弟之情，也再不复相同。只留下满腔遗恨连绵，如不绝之流水，盈盈不可穷尽。', '公元731年，暮秋到洛阳，宿龙门香山寺，有诗临赠友人。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (470, '3f6b41d5f6f84ee586d7dc557233ab4d', '游龙门奉先寺', 91, 66, 741, '公元741年', '已从招提游，更宿招提境。阴壑生虚籁，月林散清影。天阙象纬逼，云卧衣裳冷。预觉闻晨钟，令人发深省。', '已从招提游，更宿招提境。阴壑生虚籁，月林散清影。天阙象纬逼，云卧衣裳冷。预觉闻晨钟，令人发深省。', '公元741年，游览洛阳，夜宿于龙门奉先寺，有感而发，遂作此诗。奉先寺，唐上元二年寺中有庐舍那大佛像及石刻群，蔚然壮观，为龙门石窟之首。读杜诗，一般都是从《游龙门奉先寺》开始。这应和杜诗通行本仇兆憋《杜诗详注》、杨伦《杜诗镜铨》等书籍的选择排列有关。杜甫此诗，无意于造语，所谓因事以陈词。发端两句，直纪漫游尔，语意递变，两个“招提”相叠，无一字言欢欣，而雀跃之情却在语中。“阴壑”四句，景趣泠然，“不用禅语而得禅理”。“散”字用得精警，疏影横斜之态仿佛眼前，可谓穷形尽相，妙不可言。视线延伸向天际，繁星璀璨，经纬交织，仿佛触手可及，生动地展现了星辰赘累，直欲向人逼近的压迫感。“云卧”二句塑造了一个虚白高寒的环境，诗人夜卧，衣着单薄，蚀骨侵肌之凉意，让人不胜其寒。在这样的环境里，尘俗之念必然为之一洗，悉数忘却凡尘喧嚣，心灵得以净化。', '公元741年，游览洛阳，夜宿于龙门奉先寺，有感而发，遂作此诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (471, '842c57ad3e3d490b87bb3d9531ab506e', '龙门', 91, 66, 713, '公元713年', '龙门横断野，驿树出城来。气色皇居近，金银佛寺开。往还时屡改，川水日悠哉。相阅征途上，生涯尽几回。', '龙门横断野，驿树出城来。气色皇居近，金银佛寺开。往还时屡改，川水日悠哉。相阅征途上，生涯尽几回。', '龙门一山，连跨数郡，此诗盖指东京而言。天宝元年，杜甫在东京，为姑万年县君制服，又为墓志。四载，又为皇甫妃范阳太君卢氏作墓志。此诗当系此间所作。此诗再游龙门而作，由“往来时屡改”可以想见。上四句写景，下四句感怀。断山之上，佛寺弘开；洛城之中，皇居壮丽，此登高之所见者也。时屡改而川陆长存，方见前游已过。然“吾生也有涯”，相阅征途悉为转旆，伊阙天泉能复几回？末二句当为后游难必之嗟叹。', '天宝元年，杜甫在东京，为姑万年县君制服，又为墓志。四载，又为皇甫妃范阳太君卢氏作墓志。此诗当系此间所作。此诗再游龙门而作，由“往来时屡改”可以想见。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (472, '77ac7f305bd04f02aa52b5abd9e037e3', '重题郑氏东亭（在新安界）', 91, 27, 715, '公元715年', '华亭入翠微，秋日乱清晖。崩石欹山树，清涟曳水衣。紫鳞冲岸跃，苍隼护巢归。向晚寻征路，残云傍马飞。', '华亭入翠微，秋日乱清晖。崩石欹山树，清涟曳水衣。紫鳞冲岸跃，苍隼护巢归。向晚寻征路，残云傍马飞。', '天宝三载，杜甫游东都，故地重游，有感而发，遂作此诗。华亭山翠，映于秋日，故见清辉摇乱。华亭倚山而靠，所以有崩石；又临水而瞰，所以有清涟。紫麟承水，苍隼承树。顾宸云：“此诗得力，全在诗腰数十字。着一敧字，如见？岩参错。着一曳字，宛然藻荇交横。”顾氏评议，洵为的当言论。诗人以一系列的贴切精妙的动词，点染激活，化静为动，颇能体现老杜炼字之功力。颈联下笔如有神助。“冲岸”已经写出鱼的跳突排涌，还似意犹未尽，再以“跃”字筹谋，写出其跳脱活泼；“护巢”犹言鸟的疾飞急赴，再继之以“归”字渲染，点出其归心似箭。杜甫深谙“鱼鸟之精神”，可见一斑。尾联“傍”字”快“意立现，写出了马逐云飞的高超意境。把一路驰骋山水、逸兴踹飞的快感畅意挥毫，可贯虹霓。正所谓“语不惊人死不休”。', '天宝三载，杜甫游东都，故地重游，有感而发，遂作此诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (473, '1cba983b8903402ba743b3a0f16392a3', '东京府县诸公与綦毋潜李颀相送至白马寺宿', 4, 67, 740, '公元740年', '鞍马上东门，裴回入孤舟。贤豪相追送，即棹千里流。赤岸落日在，空波微烟收。薄宦忘机括，醉来即淹留。月明见古寺，林外登高楼。南风开长廊，夏夜如凉秋。江月照吴县，西归梦中游。', '鞍马上东门，裴回入孤舟。贤豪相追送，即棹千里流。赤岸落日在，空波微烟收。薄宦忘机括，醉来即淹留。月明见古寺，林外登高楼。南风开长廊，夏夜如凉秋。江月照吴县，西归梦中游。', '公元740年冬，诗人被任命为江宁县丞。741年夏，停留洛阳期间与綦毋潜、李颀相别，作此诗。清人王夫之在强调诗歌情景关系的处理问题时曾言：“情景名为二，而实不可离，神于诗者，妙合无垠。”王昌龄作诗尤重渲染。在诗人笔端，孤舟、赤岸、落日、空波、微烟、明月、古寺、高楼、南风，无不构成一幅清冷幽静的凉夏月夜图。意象的更迭概括了时空的转换，时光流转间，由暮色四合的江岸边聚焦到皓月当空的古刹里。禅意骤然涌现，却全然没有失意者的孤寂忧愤。尾二句暗合题意，没有直言之，却借由午夜梦回，将诗人即欲西归的喜悦之情娓娓道来。由策马起程的事件，到精心撷取的意象，再到梦回归乡的情态，由动及近，由外至内，层层递进。情景相生，着实耐人寻味。', '公元740年冬，诗人被任命为江宁县丞。741年夏，停留洛阳期间与綦毋潜、李颀相别，作此诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (474, '9276350175154176928f1fae5ce1ee8d', '将往江淮，寄李十九儋', 7, 66, 769, '公元769年', '燕燕东向来，文鹓亦西飞。如何不相见，羽翼有高卑。徘徊到河洛，华屋未及窥。秋风飘我行，远与淮海期。回首隔烟雾，遥遥两相思。阳春自当返，短翮欲追随。', '燕燕东向来，文鹓亦西飞。如何不相见，羽翼有高卑。徘徊到河洛，华屋未及窥。秋风飘我行，远与淮海期。回首隔烟雾，遥遥两相思。阳春自当返，短翮欲追随。', '公元769年，秋，东归洛阳，将往江淮，有诗寄李儋。李儋是韦应物挚友，两人在长安时，经常往来酬和。此诗竭尽“诗言志”之能事。诗人以禽类取譬，借燕东归与文鹓西飞不得相见比喻友人离别。鸟有翱翔九天者如文鹓；有盘旋低飞者如轻燕。羽翼有大小之别，仕途有高卑之分。纵然寥落现实让道阻且长，纵然僚僳廓落秋风不定，然而旬日可期，终会驭着春风，盘旋缱绻，相互追随。', '公元769年，秋，东归洛阳，将往江淮，有诗寄李儋。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (475, '19fd1f54ce07466390ce212ecd55446a', '自巩洛舟行入黄河即事寄府县僚友', 7, 66, 769, '公元769年', '夹水苍山路向东，东南山豁大河通。寒树依微远天外，夕阳明灭乱流中。孤村几岁临伊岸，一雁初晴下朔风。为报洛桥游宦侣，扁舟不系与心同。', '夹水苍山路向东，东南山豁大河通。寒树依微远天外，夕阳明灭乱流中。孤村几岁临伊岸，一雁初晴下朔风。为报洛桥游宦侣，扁舟不系与心同。', '公元769年，秋暮，自巩洛舟行入黄河，有即事诗寄洛阳府县僚友。起首二句全用白描，不事铺陈，简单交代了此行的踪迹。“夹”字写出了两山之间水道的狭窄，“通”尤写豁然开朗，既是河道的通达，又是视线的开旷。虽为写景，然其中却包含着情感由低沉到高昂的承转。“寒树”四句又一迭宕，有韦应物苏州牧歌之风，冲淡闲远外又暗生悲戚，循此情愫漫作展衍：寒、微、远、夕、灭、乱、孤、一、朔，均是清冷幽僻的字眼。王国维说：“以我观物，故物皆著我之色彩”，流露出若隐若现的忧伤凄切。末句又是一个情感上的转折突围，虽有悲怨铺垫，但仍能看见与友人毕力同心之愿景。感受深细，读来饶有生意。', '公元769年，秋暮，自巩洛舟行入黄河，有即事诗寄洛阳府县僚友。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (476, '18cd3da9ec9045a592165605254f252e', '留别洛京亲友', 7, 66, 774, '公元774年', '握手出都门，驾言适京师。岂不怀旧庐，惆怅与子辞。丽日坐高阁，清觞宴华池。昨游倏已过，后遇良未知。念结路方永，岁阴野无晖。单车我当前，暮雪子独归。临流一相望，零泪忽沾衣。', '握手出都门，驾言适京师。岂不怀旧庐，惆怅与子辞。丽日坐高阁，清觞宴华池。昨游倏已过，后遇良未知。念结路方永，岁阴野无晖。单车我当前，暮雪子独归。临流一相望，零泪忽沾衣。', '大历八年，韦应物罢河南府兵曹参军，在洛阳同德寺养病。同年冬，诗人由洛阳赴长安，担任京兆功曹。离开洛阳之际，作诗感怀。此诗为低回留连送别之作。“握手”、“驾言”勾勒了一幅走马临别的动态图景。不涉具体人事，而以寥寥数字一笔带过。“岂不”二句言情，以内心之真，写离愁别绪，故能深挚；“丽日”二句叙景，以胸襟之阔，状触目之景，故能豁然。宴飨达旦，曲水流觞，是刹那间的欢愉。然而天下没有不散的筵席，云有将雪之色，雁起离群之思，只能以希望于慰藉中，永结俦侣，遥寄相思。“忽”字用得极妙，内心的郁积喷薄而出，万语千言尽在蓦然回望中宣泻而下。正是这种信念满怀间的倏一动摇，才让如此朴实无华的留别之语焕然出尘，铸就出醇厚动人的诗情。', '大历八年，韦应物罢河南府兵曹参军，在洛阳同德寺养病。同年冬，诗人由洛阳赴长安，担任京兆功曹。离开洛阳之际，作诗感怀。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (477, 'e417df55b3184acfb3608d8920c3b482', '同德精舍旧居伤怀', 211, 66, 782, '公元782年', '洛京十载别，东林访旧扉。山河不可望，存没意多违。时迁迹尚在，同去独来归。还见窗中鸽，日暮绕庭飞。', '洛京十载别，东林访旧扉。山河不可望，存没意多违。<br>时迁迹尚在，同去独来归。<br>还见窗中鸽，日暮绕庭飞。', '公元782年（建中三年），诗人出守滁州经过洛阳，重游昔年同德精舍旧居，有诗悼亡。“斯人既已矣”。此诗当为诗人悼念亡妻触怀之作。同德精舍当为诗人与发妻旧居，触目伤怀，欢愉之情不复，思念之语尤深。首事铺陈，又参酌比兴，以世事沧桑之感喟反衬低黯条达之孤独。诗人情意迭宕，由感伤，到抒怀，遂又笔锋陡转，将目光投向暮色中绕庭而飞的鸽子。禽鸟尚且能够白头，诗人却孑然孤身，落笔至此，惆怅之情愈发绵长。然而时光毕竟稀释了锥心泣血的悲痛，诗人虽意兴索然，但已消解了昔日痛苦的心境，情绪已明显趋于缓和，取而代之的是萦绕心头经久不散的深沉怀念，绵密悠远，辞竭情深。', '公元782年（建中三年），诗人出守滁州经过洛阳，重游昔年同德精舍旧居，有诗悼亡。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (478, 'fca002f61fe544caa5138beea84b0c3a', '洛阳行', 60, 66, -1, '年代不详', '洛阳宫阙当中州，城上峨峨十二楼。翠华西去几时返，枭巢乳鸟藏蛰燕。御门空锁五十年，税彼农夫修玉殿。六街朝暮鼓冬冬，禁兵持戟守空宫。百官月月拜章表，驿使相续长安道。上阳宫树黄复绿，野豺入苑食麋鹿。陌上老翁双泪垂，共说武皇巡幸时。', '洛阳宫阙当中州，城上峨峨十二楼。翠华西去几时返，枭巢乳鸟藏蛰燕。御门空锁五十年，税彼农夫修玉殿。六街朝暮鼓冬冬，禁兵持戟守空宫。百官月月拜章表，驿使相续长安道。上阳宫树黄复绿，野豺入苑食麋鹿。陌上老翁双泪垂，共说武皇巡幸时。', '张籍是新乐府运动积极的践行者，其乐府诗也不乏反映民瘼之佳作。洛阳形胜，位于九州之中，历朝历代在此地盘踞建都。洛阳今古繁华，宫阙嵯峨，曹市兴隆。于城楼登临远眺者代不乏人。此诗作者亦选用俯瞰视角，将煌煌明堂、巍巍天枢，不遗余力展现在读者眼前。不仅如此，作者还善于概括事物的对立面，在一句或一篇中形成鲜明的对比。此诗如是。“乳鸟”和“蛰燕”，“野豺”和“麋鹿”，刻画了百姓危机四伏的生存现状。御门听政盛景不再，空留戍卫徒劳站守耗费资力。于峭练含蓄中达到意在言外的批判和反讽效果。尾句作者着意提炼结语，老翁见证了武皇东巡之盛况，又由老翁之口嗟叹，一喜一悲，一昔一今，迭宕出强烈的反差，想见盛衰。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (479, '7eb643c526374ee8a9a0a53e4a6f39ac', '过故洛城', 12, 66, -1, '年代不详', '故城门前春日斜，故城门里无人家。市朝欲认不知处，漠漠野田飞草花。', '故城门前春日斜，故城门里无人家。市朝欲认不知处，漠漠野田飞草花。', '白居易的诗歌题材集中，除了最受瞩目的讽喻诗，当属冶游抒怀之作为个中翘楚。此诗是一首风格明丽的小诗。春至洛城，南山北渚蓄满春意。“斜”字用得极妙，给春意刻上了角度，仿佛沾染了人的情感，城内荒凉，对城门内的颓垣败壁也不曾照拂。春意罕至，更显得萧索寂寞。第三句是自然之理的直述。年深日久，不熟悉也是情理之中。可是结句却并没有物是人非的失落感喟，反而以鲜活跃动的景语作结，颇有新意。“野”字紧扣题意，广漠而沉寂；“飞”又施以动感，情感不直露，却能体会到诗人心绪由黯然到释怀的转折。虽不加藻饰，但沉吟之间却萌生新意，“一语天然万古新”，洵为至论。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (480, 'fd050372057d48c2b6ed2aaacdb36ed6', '初到洛下闲游', 12, 66, -1, '年代不详', '汉庭重少身宜退，洛下闲居迹可逃。趁伴入朝应老丑，寻春放醉尚粗豪。诗携彩纸新装卷，酒典绯花旧赐袍。曾在东方千骑上，至今躞蹀马头高。', '汉庭重少身宜退，洛下闲居迹可逃。趁伴入朝应老丑，寻春放醉尚粗豪。诗携彩纸新装卷，酒典绯花旧赐袍。曾在东方千骑上，至今躞蹀马头高。', '“达则兼济天下，穷则独善其身”。退避守身、知足保和是中唐诗人官场碰壁后惯用的“伎俩”。仕途失意，只得寄闲情于山水之间。诗人不为外物所囿，将满腹郁结沉潜内转，放诞于诗酒文章，聊以慰藉。结句化用汉朝乐府《陌上桑》：“东方千余骑，夫婿居上头”的典故，流露出昂扬外放的生命张力。时移势易，遥想昔日身份煊赫，而今就算陆沉下僚也难以摒弃文人的铮铮傲骨。尤可醉心诗酒，寻一方心灵的诗意栖居。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (481, 'df9f64015ab247ca901a2836c3b8a6bf', '将发洛中枉令狐相公手札兼辱二篇宠行以长句答之', 12, 66, -1, '年代不详', '尺素忽惊来梓泽，双金不惜送蓬山。八行落泊飞云雨，五字枪鏦动珮环。玉韵乍听堪醒酒，银钩细读当披颜。收藏便作终身宝，何啻三年怀袖间。', '尺素忽惊来梓泽，双金不惜送蓬山。八行落泊飞云雨，五字枪鏦动珮环。玉韵乍听堪醒酒，银钩细读当披颜。收藏便作终身宝，何啻三年怀袖间。', '乐天一生交友甚广，《醉吟先生传》记云乐天交友有四类，即空门友、山水友、诗友、酒友。令狐相公尤善作诗，才思俊丽，当属乐天之诗友，常与乐天、梦得等人相互唱和。此诗写乐天洛下金谷园中得令狐鸿鳞，不胜欣喜，令狐相公的诗文金辉玉洁，乐天与其有种文人间的惺惺相惜，此刻更是不吝以青峦相赠这种近乎夸张的方式来表达自己的心情。颈联乐天别出机杼，兼取视觉与听觉的独特感受，以玉珠当空溅落的“落泊”、发出清越悠扬之声的玉佩，侧面描写令狐文辞精妙，音律铿锵；颔联从主观视角出发，切实入微地突出令狐玉韵天然，携喷珠噀玉之才，其翰墨袭银钩惊鸾之姿，锦字松烟之态；尾联乐天直抒己怀，不加掩饰地表达自己得令狐尺素，必当视若金石，怀袖三年亦不为过之情。此作情感表达浅切直露，然读之畅快淋漓，颇为流利畅达。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (482, '31adfb43ceb54291b125deafc76cd061', '归履道宅', 12, 66, 829, '公元829年', '驿吏引藤舆，家童开竹扉。往时多暂住，今日是长归。眼下有衣食，耳边无是非。不论贫与富，饮水亦应肥。', '驿吏引藤舆，家童开竹扉。往时多暂住，今日是长归。眼下有衣食，耳边无是非。不论贫与富，饮水亦应肥。', '此诗当作于唐文宗太和三年（公元829年）春，白居易因病改授太子宾客分司，回洛阳履道里，自此不复出。白居易一生与洛阳有着不解之缘。落叶归根的思想始终盘踞在诗人的脑海：“老爱东都好寄身”。大和以后，二李朋党事起，是非排陷，朝升暮黜。诗人深感不安，故求置身散地，冀于远害。此诗抒发了重归故土的欣喜之情。自乘着车舆由驿吏牵引着避开长安这多事之地后，人间事了，且得自由。从此回归到平凡的世俗生活：“要眠即眠，要坐即坐”，“热即取凉，寒即向火”。看淡身外之物，活在当下，“只合欢愉不合悲”（白居易《对镜吟》）。', '此诗当作于唐文宗太和三年（公元829年）春，白居易因病改授太子宾客分司，回洛阳履道里，自此不复出。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (483, 'd298d478db2848be89c292e56573982d', '与太白同之东洛至栎阳太白染疾驻行……雪后望山', 61, 66, -1, '年代不详', '共作洛阳千里伴，老刘因疾驻行轩。今朝独自山前立，雪满三峰倚寺门。', '共作洛阳千里伴，老刘因疾驻行轩。今朝独自山前立，雪满三峰倚寺门。', '首句消弭了时间和空间的界限，将漫长的行旅过程浓缩成了一句简单的概括。用寥寥数字一笔带过。以平白如话的通俗语言交代自己的行踪。本应与友人结伴而行打发闲散时光，孰料友人半途染疾，只得停步消歇。大雪弥漫山头，更难将发。面对此情此景，诗人早没了赏雪的逸趣。望着眼前一座座山峰连绵，前路迢递，心中不由得泛起了一丝涟漪。这种情绪刻画得十分隐晦。“倚”字既是诗人百无聊赖的简笔勾勒，又是倚马可待的俟机之态。全诗无一难字险语，但并非率尔为之。可以窥见诗人日益月滋间娴熟的语言技巧。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (484, '6df47727069a4fa19ba8c58f928e8b93', '雪后宿同轨店，上法护寺钟楼望月', 61, 37, 826, '826年', '满山残雪满山风，野寺无门院院空。烟火渐稀孤店静，月明深夜古楼中。', '满山残雪满山风，野寺无门院院空。烟火渐稀孤店静，月明深夜古楼中。', '题为“望月”，字字都从月色中照出。“月光”是此诗的诗眼。因为有朗照的月光，所以消残的积雪才在夜晚历历可见。以两个“满山”依次缀之，将“残雪”与“烈风”轻轻绾合，连通视觉与触觉。第二句紧扣一个“野”字，诗人用远视角加以观照。荒郊僻壤，门庭冷落，鲜有香火的供奉，破落自是情理之中。夜色渐晚，劳累了一天的人也都歇息。烟火也变得阑珊，光线更加晦暗，古楼掩映在幽深岑寂的夜色中。只有一轮朗月还静静地悬挂在半空中，洒落满地空明。落笔至此，勾勒出一幅皓月当空、孤高旷远的境界氛围。', '赴洛于同轨城作', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (485, 'cab3fec991644dfaa1d12be2d587dbba', '三泉驿', 61, 41, -1, '年代不详', '三泉驿内逢上巳，新叶趋尘花落地。劝君满盏君莫辞，别后无人共君醉。洛阳城中无限人，贵人自贵贫自贫。', '三泉驿内逢上巳，新叶趋尘花落地。劝君满盏君莫辞，别后无人共君醉。洛阳城中无限人，贵人自贵贫自贫。', '《论语》中“欲乎沂，风乎舞雩，咏而归”，即是上巳风俗。三月三日，春和景明，人们倾城而出，聚集于水边，祭祀宴饮。诗人途经三乡，是处彩幄翠帐，人流如潮。“劝君”一句当滥觞于“羽裳环阶转，清澜傍席疏”（梁刘孝绰《三日侍华光殿曲水宴》）。流觞赋诗、禊饮踏青，将文人雅集的盛况烘托至高处。“别后”是诗人敏锐的警觉。珍惜眼下，短暂的相逢后又将是踽踽独行。结尾二句是情感的又一迭宕，诗人透过上巳盛会感触到热闹背后的凄凉。欢愉只是一瞬的浮光掠影，繁华褪尽，仓促起行，身份地位的界限又将再一次清晰。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (486, '6348e19ee6024aa6ab83a43d64749c26', '和李司勋过连昌宫', 72, 0, -1, '年代不详', '夹道疏槐出老根，高甍巨桷压山原。宫前遗老来相问，今是开元几叶孙。', '夹道疏槐出老根，高甍巨桷压山原。宫前遗老来相问，今是开元几叶孙。', '韩愈诗歌创作的写实主义与盛唐诸子追求的兴象玲珑的朦胧意境大相径庭。发端单刀直入。一棵枝桠纵横的老槐树硬生生地闯入眼前，阻挡了前行的去路。诗人造语直露而不事拘谨，取裁雄奇而力求险僻。韩愈在创作上独辟蹊径，直绘所见所历。一方面以奇崛的笔触点染老树的蜿蜒，一方面又以清淡的笔触抒怀，将抚今追昔的兴衰之感娓娓道来。在诗人的如椽笔下，使诗歌萌生出耐人寻味的艺术感染力。正如叶梦得在《石林诗话》中所说：“韩退之笔力最为杰出，然每苦意与语俱尽”,信然。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (487, '000c338db16741dba8f47e971788ae32', '伤春', 8, 66, 792, '公元792年', '两河春草海水清，十年征战城郭腥。乱兵杀儿将女去，二月三月花冥冥。千里无人旋风起，莺啼燕语荒城里。春色不拣墓傍株，红颜皓色逐春去。春去春来那得知，今人看花古人墓，令人惆怅山头路。', '两河春草海水清，十年征战城郭腥。乱兵杀儿将女去，二月三月花冥冥。千里无人旋风起，莺啼燕语荒城里。春色不拣墓傍株，红颜皓色逐春去。春去春来那得知，今人看花古人墓，令人惆怅山头路。', '公元792年，本年春，下第东归赴徐州，途经建中年间两河战场时追吊，遂作此诗。比起《登科后》的恣意任诞，这首诗心情则稍显狭窄。孟郊性情耿介，作诗亦横空硬语，率意直言。此诗即为反映社会现实，揭露藩镇罪恶，抨击浇薄世风，心系民生民瘼之作。诗人以明丽之春意与萧条之古意作比，两两反衬，一明一暗、一盛一衰间更能产生鲜明的对比，让人心念一紧。孟郊作诗力戒平庸，勾章棘句，精思苦练间，往往见雕刻其险。“腥”字化用通感，可以想见两兵相接，尸横遍野，血流漂杵的场面。“冥”字又将自然花落与虚诞生死暗相契合，幽冥窈远，读来寒意骤生。故而韩愈有诗赞曰：“规模背时利，文字觑天巧”（《答孟郊》），绝非谀词。', '公元792年，本年春，下第东归赴徐州，途经建中年间两河战场时追吊，遂作此诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (488, '194f0822ac324e3b8f1ac77cb6d2b5b7', '答卢虔故园见寄', 8, 66, 792, '公元792年', '访旧无一人，独归清雒春。花闻哭声死，水见别容新。乱后故乡宅，多为行路尘。因悲楚左右，谤玉不知珉。', '访旧无一人，独归清雒春。花闻哭声死，水见别容新。乱后故乡宅，多为行路尘。因悲楚左右，谤玉不知珉。', '公元792年，本年春，落第东归后，途经洛阳，重游两河战场，感时伤乱之作，因答卢虔赠诗。洛友零落尽，穷愁孤独的诗人徘徊故园不由悲从中来。春天本应是万卉吐芳焕发生命力的季节，诗人笔下的春色却并不应景。感时花溅泪，花仿佛也沾染了人的情感，化为一抔黄土沉淀哀愁。水是故交，然诗人的惨淡愁容仿佛也吓煞了溪水，迎面而不识。这暗合了诗人的际遇与心情，于是诗人调动乱离的意象，营造出悲寒惨厉的诗境。在整首诗中，诗人对景物的渲染可谓信手拈来，到结尾将悲楚推向高潮，悲情苦意集中喷薄，读来感人至深。', '公元792年，本年春，落第东归后，途经洛阳，重游两河战场，感时伤乱之作，因答卢虔赠诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (489, '1cbd3250329a41809098a3a143e2592f', '旅次洛城东水亭', 8, 66, 642, '公元642年', '水竹色相洗，碧花动轩楹。自然逍遥风，荡涤浮竞情。霜落叶声燥，景寒人语清。我来招隐亭，衣上尘暂轻。', '水竹色相洗，碧花动轩楹。自然逍遥风，荡涤浮竞情。霜落叶声燥，景寒人语清。我来招隐亭，衣上尘暂轻。', '此诗当作于贞观十六年，孟郊年逾五十，第一次踏上洛阳的土地，应吏部于洛铨选而来。“招隐”暗合归隐之义。在霜落景寒的时候，诗人来到招隐亭，自在之风荡涤了诗人追求蜗角虚名之心，让诗人摆脱了尘俗名利的喧扰。竹子经过雨水的洗濯更加青翠，碧色浓郁地好像要滴下来，空气里也浸透着竹叶的清新。花如水般澄碧，微风拂过，花枝摇曳，映衬着轩冕也随着枝桠律动。置身于如此情境中，诗人也偷得霎时的轻松，卸下了凡尘俗念，冥思坐忘于天地之间。', '此诗当作于贞观十六年，孟郊年逾五十，第一次踏上洛阳的土地，应吏部于洛铨选而来。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (490, '57824f8d733542dcb353a9dc243527c9', '初于洛中选', 8, 66, 800, '公元800年', '尘土日易没，驱驰力无余。青云不我与，白首方选书。宦途是非远，浊者取自疏。终然恋皇邑，誓以结我庐。帝城富高门，京路饶胜居。碧水走龙蛇，蜿蜒绕庭除。寻常异方客，过此亦踯蹰。', '尘土日易没，驱驰力无余。青云不我与，白首方选书。宦途是非远，浊者取自疏。终然恋皇邑，誓以结我庐。帝城富高门，京路饶胜居。碧水走龙蛇，蜿蜒绕庭除。寻常异方客，过此亦踯蹰。', '贞元十六年，孟郊于洛阳铨选，诗人年逾五十，终于要踏上入世之路。本应是任诞恣意之际，却无端生出归隐之念。缘何？此诗暗合了作者的心路历程，垂垂老去的时光早已把诗人追求青云之路的念头打击地淡漠了。诗人感喟自己错过了良机，作为仕途上的拙者只能自取疏远。于是，诗人很坚决地表示要归隐田园，远离宦途。但是诗人又是禁不住诱惑的。帝城高门迤逦，雕栏瓦肆笙歌填溢。即使没有仕途想法的人，也会想逗留在这繁华之地。此诗即发“长恨此身非我有”之叹，道出人类共有的在逍遥适志与妥协求名间徘徊依违的矛盾心态。', '贞元十六年，孟郊于洛阳铨选，诗人年逾五十，终于要踏上入世之路。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (491, '94373ce2aea347fdaf97d8498a3699a3', '携新文谒张籍韩愈途中成', 68, 66, 806, '公元806年', '袖有新诗成，预见张韩老。青竹未生翼，一步万里道。仰望青冥天，云血压我脑。失却终南山，惆怅满怀抱。安得西北风，身愿变蓬草。地祇闻此语，突出惊我倒。', '袖有新诗成，预见张韩老。青竹未生翼，一步万里道。仰望青冥天，云血压我脑。失却终南山，惆怅满怀抱。安得西北风，身愿变蓬草。地祇闻此语，突出惊我倒。', '公元806年12月，贾岛携新作诣洛阳，见张籍、韩愈。张为太常寺太祝，韩拜国子博士。谒孟郊未果。此诗当为于洛途中所作。贾岛写诗，以吟哦不辍著称。他“推敲”常常煞费苦心却真有其事。这在他自己的诗中也颇多例证：“两句三年得，一吟双泪流。”因此新诗初成，自然按捺不住心中的狂喜想觅得知音激赏。昂扬的灏气流注于字里行间。胸怀凌云之志，所以普通的竹叶在诗人眼里也插上了翅膀。万水千山也不过等闲一步的距离。凛然的骨气使诗境由狭窄而变得开阖。“失却”以后数句是情感的一个跌宕。穷愁的情绪又溢满纸张，笔端依旧清冷。贾岛本是半俗半僧，为僧难免思俗，入俗难弃禅心。他正是游走于这种双重性中，耐不住释门的清净，又受不了尘网的裹胁。他出生微贱，犹如蓬草般难以主宰自身的命运。因此难免流露出压抑之感。然而作者终究是自命不凡的。结尾处是情感的又一转折，仿佛震惊于自己突然萌生的念头，一扫黯然的基调，英雄气概占了上风。', '公元806年12月，贾岛携新作诣洛阳，见张籍、韩愈。张为太常寺太祝，韩拜国子博士。谒孟郊未果。此诗当为于洛途中所作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (492, '3b00c6978f754a7b8c471bc388b6ebc1', '欲游嵩岳留别李少尹益', 68, 66, 810, '公元810年', '孤侧迟回洛水湄，孤禽嘹唳幸人知。嵩岳望中常待我，河梁欲上未题诗。新秋爱月愁多雨，古观逢仙看尽棋。微眇此来将敢问，凤凰何日定归池。', '孤侧迟回洛水湄，孤禽嘹唳幸人知。嵩岳望中常待我，河梁欲上未题诗。新秋爱月愁多雨，古观逢仙看尽棋。微眇此来将敢问，凤凰何日定归池。', '公元810年，诗人在洛阳初识河南少尹李益。此间，与李益、韦执中、诸葛觉等游赏于洛阳各处。同年，欲游嵩岳，辞别少尹李益，作诗留赠。李益与贾岛的关系可谓十分亲厚。纵然贾岛小李益31岁，也难以阻挡二人成为不折不扣的忘年之交。此诗虽言离别，却写得极尽隐晦，带来低回遐想的艺术效果。开篇渲染，寓离情于景中，两个“孤”字相缀，把离别的伤感涂抹得尤为浓郁。诗人将嵩岳赋予了人的情感，全然不写缠绵幽怨的老调，而是将寄希望以新的征程，意永而不悲。“新秋”二句当是诗人对未来的构想。委婉地表达对友人的邀约。叙写思念，却不直言，而是借看棋的活动逗出，下棋当逢胜友，留连之情可见一斑。尾句反诘，“凤凰”暗合李益，有“归去凤池夸”之意，诗人将殷切的祝福相与，情意悠渺，直以落句见情。', '公元810年，诗人在洛阳初识河南少尹李益。此间，与李益、韦执中、诸葛觉等游赏于洛阳各处。同年，欲游嵩岳，辞别少尹李益，作诗留赠。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (493, '496c5d499aff4556aebdcba1bd1c1082', '洛阳道中寄弟', 68, 66, -1, '年代不详', '趋走破流年，惭经此路偏。密云埋二室，积雪度三川。生类梗萍泛，悲无金石坚。翻鸿有归翼，极目仰联翩。', '趋走破流年，惭经此路偏。密云埋二室，积雪度三川。生类梗萍泛，悲无金石坚。翻鸿有归翼，极目仰联翩。', '贾岛一生“蹉跎随泛梗”，以亦儒亦释的身份，足迹遍布大唐的南山北渚。他曾淹留长安二十余年，“三十年来常在客”的羁旅飘零，使他的诗歌难免打上消极的、与常情背道而驰的烙印。然而早年皈依青门的生活经历终究是留下了难以磨灭的印记，虽然偶有流露失意之情，却都在尾端振起，衲气终身未除。“趋”字将远行之人的飘零意绪平添了一层，诗人刻意忽略时间的前置，仿佛只有快节奏的疾行才能打破时间的流动。诗人累举不第，短褐悬结所以曰“惭”。“偏”字缘何？太室、少室二山掩映在密云深处，泾、渭、洛三川被皑皑积雪覆盖。此赴洛中已是严冬，诗人在大雪中跼躅难行，步履维艰，顿生人若浮萍漂泊无定之感。然而苦寒之境毕竟不能掩夺性情，纵然孤鸿也有归来之际。落笔至此，诗中的冷寂愁苦之色渐渐淡出，转而以豪宕振起，于劫灰之上泠然独存。固形成贾岛特有的清峭诗风。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (494, 'c3bfbfcd965e4f16b79b533b2e4597a4', '送卢二弟茂才罢举游洛谒新相', 137, 66, -1, '年代不详', '踏碎作赋笔，驱车出上京。离筵俯岐路，四坐半公卿。守命贫难掷，忧身梦数惊。今朝赴知己，休咏苦辛行。', '踏碎作赋笔，驱车出上京。离筵俯岐路，四坐半公卿。守命贫难掷，忧身梦数惊。今朝赴知己，休咏苦辛行。', '管窥姚合的诗，可以从中瞥见些许诗人的人生际遇。姚合是写五律的能手。虽与贾岛并称，在诗风上还是有很大不同。姚合作诗，不使劲，不雕刻，没有怨气的掺杂，也不似贾岛那么苦寒料峭。姚合的仕途生涯，当始于四十岁之后。作为底层的小官吏，他从一开始就不仰仗于升迁敛财，而是表现得三心二意，试图逃遁于官场，然而现实与理想大多背道而驰。首二联是诗人入洛行迹的追踪，如赋般铺陈，一气贯注而来。颈联是诗人心迹的释放。午夜梦回，诗人常感于自己官微言轻，挣扎于固守清贫和恪守命数的矛盾中难以自拔。但诗人终究还是没能逃脱尘世的樊笼。不得不屈从于现实，摒弃自己疏懒的野性，与上位者交好。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (495, '1dfc80e17266481abf06747220cd0948', '初归平泉过龙门南岭遥望山居即事', 212, 66, 836, '公元836年', '初归故乡陌，极望且徐轮。近野樵蒸至，平泉烟火新。农夫馈鸡黍，渔子荐霜鳞。惆怅怀杨仆，惭为关外人。', '初归故乡陌，极望且徐轮。近野樵蒸至，平泉烟火新。农夫馈鸡黍，渔子荐霜鳞。惆怅怀杨仆，惭为关外人。', '文宗开成元年（公元836年）九月，李德裕以太子宾客分司居洛阳，期间居于平泉别墅，有感而作。李德裕常受累于良相的威名，而让人忽略了他的文才。此诗道出了万千游子初归故土的陌生与眷恋。诗人置身于故乡陌生而熟悉的环境中，一路迤逦而来，心情颇不平静。发端言情，写得极为真挚，当为以情动人之表率。踏上故乡的第一片土地，就再也挪不开目光，车轮徐徐前行，只为了将故乡的一草一木尽收眼底。用细腻的笔触宕开，极言眷恋却不藻饰。正是这样的纯粹白描才配得上毫无掺杂的思乡之情。中间四句写景。视野由近及远、由静及动，截取日常的生活场景入诗，烟火气扑面而来，充斥着盎然的生命张力。观此情境，惆怅寥落之情不禁涌上心头。遥想当年杨仆不甘做关外侯，不得已将封地东迁至新安。自己又何尝不是如此呢？诗人阔别故乡多年，人事已非，不禁感慨系之，自己早已不能以家乡人自居，反主为宾的客伤之感萦绕心间。', '文宗开成元年（公元836年）九月，李德裕以太子宾客分司居洛阳，期间居于平泉别墅，有感而作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (496, '11d241f615934a329a0b6de4a1f9162f', '寄题惠林李侍郎旧馆', 212, 0, 840, '公元840年', '栋宇非吾室，烟山是我邻。百龄惟待尽，一世乐长贫。半壁悬秋日，空林满夕尘。祇应双鹤吊，松路更无人。', '栋宇非吾室，烟山是我邻。百龄惟待尽，一世乐长贫。半壁悬秋日，空林满夕尘。祇应双鹤吊，松路更无人。', '开成五年（公元840年）秋，李德裕被召入朝，由淮南节度使入相，赴京途中过洛阳惠林寺，聊作此诗。唐大和初至会昌年间，洛阳形成一个以白居易为核心的闲适文人群体。他们好禅亲佛，耽玩园林，表现出闲适自得的生活情趣，思想上追步白居易的新吏隐观—中隐说。发端四句，诗人用五言诗的形式坦露出内心世界中更为真切的一面，呈现在世人面前的不再是那个内修外攘、政绩粲然的宰相形象，而是一个细腻、孤峭的心灵。文体是一种有意味的形式，“七言以浩歌，五言以穆诵（刘熙载《艺概·诗概》）。”李德裕正是以五言诗的形式抒写自己内心的隐幽情思。物色的变化总是牵动着诗人的心。夕阳的余辉攀缘在墙壁间，斜斜地穿梭于枝桠的罅隙，让空寂的山林造成一种繁茂的视觉印象，给人一种久远的时间感。此外，诗人还善于将松、鹤等包蕴士大夫坚贞品格、高洁志趣的意象并置摄入笔下，更增其不俗意蕴。', '开成五年（公元840年）秋，李德裕被召入朝，由淮南节度使入相，赴京途中过洛阳惠林寺，聊作此诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (497, '57eee8c4cd38479c93a1ef66e59528b0', '题寿安县甘棠馆御沟', 79, 23, -1, '年代不详', '一渠东注芳华苑，苑锁池塘百岁空。水殿半倾蟾口涩，为谁流下蓼花中。', '一渠东注芳华苑，苑锁池塘百岁空。水殿半倾蟾口涩，为谁流下蓼花中。', '杜牧的诗歌，承袭了诗人的风流倜傥，读来俊爽清丽，在唐贤中另是一种笔意。杜牧的七绝画意颇浓。他的诗有如苏州园林，小巧雅致，饶有情味。此诗兼具诗画之功。他讲究色彩的调和、构图的呼应和景物的相互映衬。苑锁池塘，蟾蜍样式的池台因很久没有流水的浸润而显得枯涩。因朝代更迭被人经久遗忘的角落，本是封闭晦涩的。诗人却以富有生命力的蓼花点缀，将悲凉之意从笔端强行抽离。他的初衷当是为史“翻案”，给人以鲜明的快感。显然，时移势易，诗人自己虽“怏怏不乐”，但收尾却不那么萧瑟，而是饶有寄托。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (498, '734c01eae3ad4d2ab1019c7f5e291c37', '铜驼悲', 78, 66, 809, '公元809年', '落魄三月罢，寻花去东家。谁作送春曲，北山饶古人。客饮杯中酒，驼悲千万春。生世莫徒劳，风吹盘上烛。厌见桃株笑，铜驼夜来哭。', '落魄三月罢，寻花去东家。谁作送春曲，北山饶古人。客饮杯中酒，驼悲千万春。生世莫徒劳，风吹盘上烛。厌见桃株笑，铜驼夜来哭。', '公元809年三月，春，诗人考进士受阻，离长安东归，道经洛阳铜驼街，为宽慰失意的愁苦，遂作《铜驼悲》诗。晚清吴汝纶评此诗：“此首忧乱之旨”（《李长吉诗评注》）。诗中，藉个人升沉，体察世态炎凉，且以感物感时为体己的诗人，特别以“铜驼悲”为塑造，咏人物之衰，盛世之不复，以为世人处事之警策。发端四句，乃是诗人以落魄者的身份与心情即为悲剧主角亦为精神主观，一方面企图藉赴洛阳牡丹花会赏花送春以为伤痛不平之聊慰，另一方面再藉“悲铜驼”为感时思物，并拟人体己做《铜驼悲》以为郁闷心情之排遣，合而旨在可藉相应符号个涵历史意蕴地形象展示来收达观人生，超越自我之效也。诗腰四句，诗人禀其独有的人生苦短之感，先拿桥南紫陌骅骝骄骋繁华之地，与北邙前贤亡灵归葬陵墓蒿荒之地参酌对照，复拿四会道街之畔贵客正豪饮高会，与所立铜驼因阅历已多而不胜变迁之感为景致观照，以作承转，共同映射死生之虚诞，好发人之深省也。文末四句合而言说人生之脆弱多舛，诚然一如烛光临风，但灵魂突围的要义，却不离百折不回的坚韧。如不然，必不能真正体验自被大理想、大破灭所嚼烂了的诗心。', '公元809年三月，春，诗人考进士受阻，离长安东归，道经洛阳铜驼街，为宽慰失意的愁苦，遂作《铜驼悲》诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (499, 'd4c7858c100b46e8ae661382d4d32146', '三月过行宫', 78, 66, 809, '公元809年', '渠水红繁拥御墙，风娇小叶学娥妆。垂帘几度青春老，堪锁千年白日长。', '渠水红繁拥御墙，风娇小叶学娥妆。垂帘几度青春老，堪锁千年白日长。', '公元809年三月，春，举进士不第，离长安东归，道经洛阳，有《三月过行宫》诗。李贺是写生的个中能手。长安、洛阳的深巷广陌，故乡旅途的苦雨凄风，折磨着这位年轻的诗人。心中的泪水、沉闷的哀音、失望的心理，交织溶入他的诗篇。诗人以特有的细腻笔触，捕捉生活中的细枝末节，因此他笔下的人物，无不跃然于纸上。李贺尤善描绘宫女。御沟里的水荭白蒿交相铺叠，簇拥着行宫的围墙。淡红柔绿像宫女的红颊青黛般妖娇。本是氤氲着蓬勃生气的图景，却乍然一个停歇，陡然跳转向宫门昼掩、门庭晏然的一帧画面。在繁华与萧索、喧嚣与落寞的调度中，显示出人物的特点。', '公元809年三月，春，举进士不第，离长安东归，道经洛阳，有《三月过行宫》诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (500, 'dae053971c6940ceb4a7f76a0e534050', '许公子郑姬歌', 78, 66, 809, '公元809年', '许史世家外亲贵，宫锦千端买沈醉。铜驼酒熟烘明胶，古堤大柳烟中翠。桂开客花名郑袖，入洛闻香鼎门口。先将芍药献妆台，后解黄金大如斗。莫愁帘中许合欢，清弦五十为君弹。弹声咽春弄君骨，骨兴牵人马上鞍。两马八蹄踏兰苑，情如合竹谁能见。夜光玉枕栖凤凰，袷罗当门刺纯线。常翻蜀纸卷明君，转角含商破碧云。自从小靥来东道，曲里长眉少见人。相如冢上生秋柏，三秦谁是言情客。娥鬟醉眼拜诸宗，为谒皇孙请曹植。', '许史世家外亲贵，宫锦千端买沈醉。铜驼酒熟烘明胶，古堤大柳烟中翠。桂开客花名郑袖，入洛闻香鼎门口。先将芍药献妆台，后解黄金大如斗。莫愁帘中许合欢，清弦五十为君弹。弹声咽春弄君骨，骨兴牵人马上鞍。两马八蹄踏兰苑，情如合竹谁能见。夜光玉枕栖凤凰，袷罗当门刺纯线。常翻蜀纸卷明君，转角含商破碧云。自从小靥来东道，曲里长眉少见人。相如冢上生秋柏，三秦谁是言情客。娥鬟醉眼拜诸宗，为谒皇孙请曹植。', '公元809年春，举进士不第，离长安东归，道经洛阳，有《许公子郑姬歌》诗。李贺的诗歌斫取了屈原的浪漫主义之光，照耀了中晚唐的诗坛。他撷取古代流传的民间传说，佐以天马行空的想象，恣意驰骋，建构诗歌的情节框架，创造诗歌的艺术形象。他的想象，常常突如其来。往往不遵循现实或历史上实际存在的状况反映生活的本来面貌，而是把自己所要描写的人物事件理想化，直接表达自己的主观感受，故富有浓厚的浪漫主义色彩。诗人藉许公子为博美娇娥郑姬一笑豪掷千金的故事，发挥以大胆的想象，为其润色，也让畅意挥毫的诗人从幕后走到台前，引一段佳话，借一斛清酒，以千秋文章举荐这被偏见掩埋了的堪舆八斗的才气。', '公元809年春，举进士不第，离长安东归，道经洛阳，有《许公子郑姬歌》诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (501, '25500f5ad7fb42cf8637c7d878d11fa9', '洛阳城外别皇甫湜', 78, 66, 809, '公元809年', '洛阳吹别风，龙门起断烟。冬树束生涩，晚紫凝华天。单身野霜上，疲马飞蓬间。凭轩一双泪，奉坠绿衣前。', '洛阳吹别风，龙门起断烟。冬树束生涩，晚紫凝华天。单身野霜上，疲马飞蓬间。凭轩一双泪，奉坠绿衣前。', '公元809年，十月十五日，为寻求仕途出路，自洛阳出发去长安。皇甫湜送之洛城外，贺临别赠诗。李贺的诗素来以“词诡调激，色浓藻密”而著称。此诗虽极言离别之萧索，沉吟之间，仍能体味出独特的艺术个性，被浪漫地形象化了。诗人泼墨挥毫，毫不吝惜色彩的点染。冬天的树寒颤颤地兀自站在道旁，紧缩着一身的枯涩与辛酸。晚霞凝紫，把灰白的天空涂抹成灰暗。孤单、寂寞的人，身影渐渐淹没在田野层层霜光里。徒留诗人独倚栏杆，满目潸然，不禁发问：能否凭借风，把两行清泪，奉送到友人的绿衣前。不难看出，李贺诗歌中瑰丽和奇峭两种截然相反的风格被有机地融合在一起。诗人抒发的意趣固然幽冷，但秾丽色彩的敷衍，把诗歌的艺术形象烘托地更具立体感。在这些雕琼镂玉的措辞中，急剧地跳动着诗人的情感脉搏，感情色彩更趋浓郁。', '公元809年，十月十五日，为寻求仕途出路，自洛阳出发去长安。皇甫湜送之洛城外，贺临别赠诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (502, '971e8a8be0bd48b8a0d46f3725b007bd', '将发', 78, 66, 814, '公元814年', '东床卷席罢，濩落将行去。秋白遥遥空，日满门前路。', '东床卷席罢，濩落将行去。秋白遥遥空，日满门前路。', '此诗为元和九年（公元814年）秋入潞前于洛所作。时友人张彻在潞州军幕，贺欲北上从戎，然体弱多病，渡落无依。澹望前程。秋空茫茫，日色满路，不知入潞后能否如愿。揣测其情怀，乃是遭受重挫后的反应，与落第后惆怅犹疑的心绪较为吻合。首句诗人截取了一个特写镜头，将远行的动态延展开来，紧扣将发的主题。但远行的原因诗人并未细说，而是给读者留下了一个想象的空间。短小精悍的诗被诗人建构地错落有致。一二句言事，三四句抒情。天色将晓，故遥遥而白，可见苍深；日头渐升，故灼灼而华，可见光耀。旭日盈途，正是将发时候。诗人用寥寥数字，将言外之理熔铸笔端，扩大了诗歌本身承载的容量，真正达到了以少总多的目的，言有尽而意无穷。', '此诗为元和九年（公元814年）秋入潞前于洛所作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (503, '5353de04d0be43ed99c43124698dbdb9', '经故洛阳城', 64, 66, 887, '公元887年', '败垣危堞迹依稀，试驻羸骖吊落晖。跋扈以成梁翼在，简书难问杜乔归。由来世事须翻覆，未必馀才解是非。千载昆阳好功业，与君门下作恩威。', '败垣危堞迹依稀，试驻羸骖吊落晖。跋扈以成梁翼在，简书难问杜乔归。由来世事须翻覆，未必馀才解是非。千载昆阳好功业，与君门下作恩威。', '公元887年，经洛阳，有诗表达对朝政的担忧。罗隐是晚唐诗坛横生的一怪杰。在这一充满绝望与叹息的时代里，诗人却保持着高标的人格，将怨刺的笔指向世间的角角落落。他不屈服于仕途的濩落，尽管这种桀骜不驯的个性带给他的是一次又一次的偃蹇困顿。他反而愈战愈勇，毫不收敛，以一种凤凰涅槃般的伟大感获得精神的自由。故后人评价他：“发文为诗，皆不平之鸣，与夫忠愤之气也”。罗隐的讽刺，是渗透到他的每一首诗中去的。这在他的咏史诗里体现得更为明显。衰颓的现实打破了诗人对美好未来的展望，只能将目光投射向深远的历史洪流，企图找寻到理想社会的影子。罗隐以尖锐的笔锋揭开尘封的历史面纱，败墙危堞赫然横亘眼帘。日薄西山，驭一匹瘦马停驻在此地，心中百感蓦然涌现。遥想当年梁翼跋扈，敢于站出来谏诤不法的杜乔也寥落于历史的尘埃中。诗人愿接替杜乔的使命，针砭时弊，凌轹权贵，展露出其笔刃尖批判的锋芒。', '公元887年，经洛阳，有诗表达对朝政的担忧。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (504, '0cc24ab06e994ebf9d45003d2d7ca7ac', '人日新安道中见梅花', 64, 27, 870, '公元870年', '长途酒醒腊春寒，嫩蕊香英扑马鞍。不上寿阳公主面，怜君开得却无端。', '长途酒醒腊春寒，嫩蕊香英扑马鞍。不上寿阳公主面，怜君开得却无端。', '咸通十一年（公元870年），本年停举，罗隐经新安至长沙途中所作。诗至晚唐，已经缺乏盛唐那种阔大的气象和飞动的气势，而转向一种精细的情趣的宁静的境界。尤其是咏物诗，取材愈发细小，而着意在意境刻画上下功夫。罗隐现存咏物诗六十首，也同样具有上述特点。此诗恰似信手拈来，笔法上沿袭了现实主义的诗歌传统。然细读之后又仍能窥探出罗隐一贯的创作路数。即隶事用典。发端两句平淡无奇，尚未能跳脱出前人吟咏梅花的窠臼。然当梅花攀附于寿阳公主的额间，发生了奇妙的化学反应。那朵小小的梅花，将她的名字留在暗香浮动的梅林间。不仅如此，此诗的格调也更上一层，从细小的题材里挖掘出新意，读来沁香扑面。', '咸通十一年（公元870年），本年停举，罗隐经新安至长沙途中所作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (505, 'd5fbf86cf85a4692a82959fd2960ab3e', '旅寓洛南村舍', 66, 66, 870, '公元870年', '村落清明近，秋千稚女夸。春阴妨柳絮，月黑见梨花。白鸟窥渔网，青帘认酒家。幽栖虽自适，交友在京华。', '村落清明近，秋千稚女夸。春阴妨柳絮，月黑见梨花。白鸟窥渔网，青帘认酒家。幽栖虽自适，交友在京华。', '公元870年，本年停春试，乃小住洛南，有诗留作。郑谷此诗乍看是耽于田园之乐，虽不乏闲情逸致和对生活的热爱，若从他的结句来看，也是另有隐衷的。诗人虽是寓居村舍的一匆匆过客，却也几乎把自己当作农家的一份子而置身其间。诗人以敏锐的目光触及到乡村日常朴实的生活图景，以质朴之笔描摹了孩童天真无邪的欢洽之情，富有鲜活的生活气息，因此可信之感更为深重。清明时节，春意正浓，草木蓊郁葱茏，花木的阴翳仿佛牵绊了柳絮腾空翻飞的脚步，荫蔽劈面而来。是夜，月光被流云遮蔽，没有倾泻下一星半点。恍然若失间但见一树梨花开得繁茂，一簇簇攒聚在枝头，白炽如昼，点亮了深沉的夜色。着一“窥”字而神态毕现。透过纸背，仿佛看见一只水鸟正斜着脑袋在渔网的缝隙间打探，犹疑不敢上前。而结尾笔锋陡然一转，诗人在快要深陷此番幽栖闲适之地时蓦地跳脱出来，斩断留连，纵然身在山水，心仍向往于京都的繁华。', '公元870年，本年停春试，乃小住洛南，有诗留作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (506, '613807dfb0c84623832574ae3ab83476', '过洛阳故城', 122, 66, 876, '公元876年', '三十世皇都，萧条是霸图。片墙看破近，遗迹渐应无。野径通荒苑，高槐映远衢。独吟人不问，清冷自呜呜。', '三十世皇都，萧条是霸图。片墙看破近，遗迹渐应无。野径通荒苑，高槐映远衢。独吟人不问，清冷自呜呜。', '公元876年，经过河南府。晚唐国事衰微，千疮百孔的唐王朝大厦将倾，风雨欲来。这个时代的诗人们敏锐地将自己苍凉的兴亡之叹、黍离之悲寓于所见所闻的离宫废都、荒台旧苑之中。礼山登临古迹，抚今追昔之情愈加哀婉落寞。首联写汉魏时期洛阳城繁华一时，卓荦诸夏，然而宏图霸业亦不过化作眼前萧条之景，巍巍神都随着时间的浪潮渐渐失去了往日的繁华，空留如今寂寞清冷之景；颔联在作者的视角下，看到的是断壁残垣，颓败荒凉，早已不能想象汉魏时洛城亭台楼榭栉比的盛况；颈联“野”、“荒”、“高”、“远”四字更添凄凉之景的纵深感，在空间维度上摘取通向废弃颓唐苑落的小径、孤矗在远街之上的槐树这些景物，愈加映衬出而今洛城的衰颓；尾联作者由眼前景道出心中伤，想起曾经浮屠当空，马卒车往的洛城，心中涌起兴废荣枯的交替感伤，路绝人稀处，独留一人唏嘘而已。当时的长安由于战乱早已破败不堪，洛阳更不必言之，这首诗既是对故去繁华洛城的追忆，更是时局的反映，诗人心中对现实的无奈之感与历史的兴衰变迁激烈碰撞，情感色彩十分强烈。', '公元876年，经过河南府。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (507, '82c6103390dc46bca38bdf500dda6042', '夕次洛阳道中', 122, 66, 875, '公元875年', '秋风吹故城，城下独吟行。高树鸟已息，古原人尚耕。流年川暗度，往事月空明。不复叹岐路，马前尘夜生。', '秋风吹故城，城下独吟行。高树鸟已息，古原人尚耕。流年川暗度，往事月空明。不复叹岐路，马前尘夜生。', '此诗大约是乾符二年秋礼山行旅洛中时所作。礼山的诗歌，师法张籍之清新淡雅。刘熙载《艺概》说 “五言无闲字易，有余味难”，礼山律诗语言，就颇有余味，用词质朴而给人淡雅古致之感，此作既是如此。首联与颔联，先以眼前之景直接入诗，万里秋风、故城洛阳、孤树归鸟、躬耕农夫，皆是礼山独行途中所见，虽是寻常景象，而“独吟行”却为之加上些许凄清哀绝的色彩；颔联由所见所闻抒发往事如烟之感慨，时光河流般暗自流逝，不舍昼夜，前尘往事清晰明亮如同天上月，却始终无法触及，“暗”与“空”字用的相当绝妙，道尽了诗人心中逝者如斯夫的无奈与感伤；尾联从心灵波动中抽离，诗人不再叹息，只将一腔愁思与夜色，一齐抛置在踏起尘埃的马蹄之下。整首诗语言平淡自然，不求一字一句的绮丽，给人以不事雕琢之感，然而其孤苦的感伤之情却在起承转合之间刻画地淋漓尽致。', '此诗大约是乾符二年秋礼山行旅洛中时所作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (508, 'e9eb5ba966974e32a067f6ce41bd7887', '煌煌京洛行', 213, 66, -1, '年代不详', '长安近甸巡游遍，洛阳寻有黄龙见。千城万骑如雷转，差差清跸祥云卷。百司旧分当玉殿，太平官属无遗彦。歌钟沸激香尘散，晨旗隐隐罗轩冕。周公旧迹生红藓，瀍涧波光春照晚。但听嵩山万岁声，将军旗鼓何时偃。', '长安近甸巡游遍，洛阳寻有黄龙见。千城万骑如雷转，差差清跸祥云卷。百司旧分当玉殿，太平官属无遗彦。歌钟沸激香尘散，晨旗隐隐罗轩冕。周公旧迹生红藓，瀍涧波光春照晚。但听嵩山万岁声，将军旗鼓何时偃。', '李咸用沿用汉乐府旧题，煌煌赫赫的洛阳城在眼前拔地而起。武则天时东迁，洛阳一跃荣膺为“神都”。帝后移驾出宫巡游，车辇两侧戍守的卫兵蹔驻清跸，传道声此起彼伏，扬天威以壮军声。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (640, '428706fc62d14607a21bc5e94801023a', '彭门用兵后经汴路三首', 95, 0, 869, '869年', '长亭一望一徘徊，千里关河百战来。细柳旧营犹锁月，祁连新冢已封苔。霜凋绿野愁无际，烧接黄云惨不开。若比江南更牢落，子山词赋莫兴哀。隋堤风物已凄凉，堤下仍多旧战场。金镞有苔人拾得，芦花无主鸟衔将。秋声暗促河声急，野色遥连日色黄。独上寒城正愁绝，戍鼙惊起雁行行。铁马云旗梦渺茫，东来无处不堪伤。风吹白草人行少，月落空城鬼啸长。一自纷争惊宇宙，可怜萧索绝烟光。曾为塞北闲游客，辽水天山未断肠。', '长亭一望一徘徊，千里关河百战来。细柳旧营犹锁月，祁连新冢已封苔。霜凋绿野愁无际，烧接黄云惨不开。若比江南更牢落，子山词赋莫兴哀。隋堤风物已凄凉，堤下仍多旧战场。金镞有苔人拾得，芦花无主鸟衔将。秋声暗促河声急，野色遥连日色黄。独上寒城正愁绝，戍鼙惊起雁行行。铁马云旗梦渺茫，东来无处不堪伤。风吹白草人行少，月落空城鬼啸长。一自纷争惊宇宙，可怜萧索绝烟光。曾为塞北闲游客，辽水天山未断肠。', '咸通十年（公元869年）秋，康承训率领唐军镇压了来势汹汹的庞勋起义。庞勋之乱虽然以失败告终，但唐朝的胜利却只是更大灾难前的回光返照。事后，吴融途径战场，写下了此诗。这是一场统治者与被统治者之间的殊死较量。由于阶级意识的局限，吴融不可能站在起义者的立场上为其发声。然而对于唐军的获胜，作者却没有丝毫熙悦，而是陷入深沉的思索中。从长安东来，触目满是凄凉景象，造成战乱不休的根由何在？当叛军残党依然流窜在各地，藩镇与中央上下离心，百姓的日子自然不会安定。讽刺的是，皇帝本人的生活却日益繁华。不仅大兴土木，还纵情声乐，', '咸通十年（公元869年）秋，康承训率领唐军镇压了来势汹汹的庞勋起义。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (639, 'cb04e843f3e647ac9d513b58c5e8c4e9', '汴上晚泊', 95, 0, -1, '阙如', '亭上风犹急，桥边月已斜。柳寒难吐絮，浪浊不成花。岐路春三月，园林海一涯。萧然正无寐，夜橹莫咿哑。', '亭上风犹急，桥边月已斜。柳寒难吐絮，浪浊不成花。岐路春三月，园林海一涯。萧然正无寐，夜橹莫咿哑。', '吴融写景较少曲折晦僻的艺术手法，也不作周详细致的规划，而是气主收敛，情表坦易。意境尚淡远，语言近白描，具有清空一气，明白自然的特点。月落夜深，诗人仍辗转难眠。在暗夜中，诗人的听觉升居为对外界事物景象感受的首位。诗的前幅布景密度很大，二十个字集中写了八种意象。后幅却十分疏朗，孤孑漂泊的旅人长夜无眠。愁人自不能寐，却咎橹声咿呀，搅扰了人的清梦。不但衬托出了夜的静谧，而且揭示了心绪的深永和清廖，有了这“夜橹咿呀”的一笔，这首诗就不再停留在月夜景物画的层次，而创造出了情景交融的典型化艺术意境。', '阙如', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (638, '03846f1bbbd64c8b884d4ec4dfbfd485', '汴路即事', 85, 0, -1, '年代不详', '千里河烟直，青槐夹岸长。天涯同此路，人语各殊方。草市迎江货，津桥税海商。回看故宫柳，憔悴不成行。', '千里河烟直，青槐夹岸长。天涯同此路，人语各殊方。草市迎江货，津桥税海商。回看故宫柳，憔悴不成行。', '发端两句，最得王维“大漠孤烟直，长河落日圆”的深致。诗人极目骋望，汴河上千里青烟直指苍天，青槐夹道，将河岸线拉伸得愈长。在诗人构建的所向空阔、毫无窒碍的境界中情感却陡然转向促狭。从此长路漫漫，天涯契阔，耳边盘亘殊语，相逢恒难。长羡草市营集、海商络绎的繁华，徒留一人憔悴。回望故宫烟柳摇曳，我独前行，洒落满地落寞。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (637, '149776ad32af447bbbc395e58d527766', '汴路水驿', 85, 0, -1, '年代不详', '晚泊水边驿，柳塘初起风。蛙鸣蒲叶下，鱼入稻花中。去舍已云远，问程犹向东。近来多怨别，不与少年同。', '晚泊水边驿，柳塘初起风。蛙鸣蒲叶下，鱼入稻花中。去舍已云远，问程犹向东。近来多怨别，不与少年同。', '王建曾“从军走马十三年”，沉沦下僚，因此诗中往往杂有消极颓废的思想。晚泊汴水。诗人以宁静的笔调描写了充满着活跃气氛的夏夜。晚风拂柳，吹散了心头的燥热。青蛙藏匿在池塘的蒲叶下，沸鸣一片。鱼穿梭在稻花中撒欢儿，俨然是诗人心头可感的甜蜜之感。然诗的下半部分情感却陡然转折。浓郁的田园生气愈发衬得诗人孤孑落寞。故乡远，流云近。颇多的别离早已生怨，已然没有了少年郎鲜衣怒马的风发意气。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (635, '11e8c3fa166441069ff599ef339beb59', '宋汴道中', 65, 0, -1, '年代不详', '平野有千里，居人无一家。甲兵年正少，日久戍天涯。', '平野有千里，居人无一家。甲兵年正少，日久戍天涯。', '高蟾的诗风与李白相似，清通不尚雕琢。此诗虽是写景，却处处弥漫着个人心绪的凄风苦雨，浸透着晚唐倾颓的时代哀音。开篇便是望中之景：平畴千里，延伸向视线的尽头。在这空旷辽阔的底色上，竟没有一寸生机。缘何？藩镇割据、宦官专权的晚唐，战火连连，连尚未及笄的幼童也被迫征用。唐王朝已向着不可挽回的趋势衰败下去。“世间无限丹青手，一片伤心画不成”（高蟾《金陵晚望》）。念及此，种种悲慨潮涌上心头，拂逆不去。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (636, '3de164f10f90454589effa8f882a9713', '使至汴州喜逢宋之问', 252, 0, 708, '公元708年', '阮籍蓬池上，孤韵竹林才。巨源从吏道，正拥使车来。相逢且交臂，相命且衔杯。醉后长歌毕，馀声绕吹台。', '阮籍蓬池上，孤韵竹林才。巨源从吏道，正拥使车来。相逢且交臂，相命且衔杯。醉后长歌毕，馀声绕吹台。', '此诗当作于唐中宗景龙二年（公元708年），史上记载宋之问此时被贬汴州长史。阮籍《咏怀》之二有言：“徘徊蓬池上，还顾望大梁。”诗人以阮籍自诩，清词孤韵、逸气烟翔，有“竹林七贤”嗣音。孤韵稍寡，谁人曲合？接着诗人以山涛比作宋之问，沿吏道拥蹙而来。重逢的欢喜弥漫开来。诗人与友人之问挽臂同游，把盏言欢。酒逢知己千杯少。酩酊大醉后的两人放声而歌，余音袅袅环绕吹台。', '此诗当作于唐中宗景龙二年（公元708年），史上记载宋之问此时被贬汴州长史。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (634, '060c79fd14be464fae0b670910b2a78b', '大梁见乔诩', 64, 52, 877, '877年', '湘水春浮岸，淮灯夜满桥。六年悲梗断，两地各萍漂。刀笔依三事，篇章奏珥貂。迹卑甘汨没，名散称逍遥。好似松为径，空江桂作桡。野香花伴落，缸暖酒和烧。普招寻游凤，秦冠竟叹鸮，骨凡鸡犬薄，魂断惠兰招。怅望添燕琯，蹉跎厌鲁瓢。败桐方委爨，冤匣正冲霄。战代安釐国，封崇孝景朝。千年非有限，一醉解无聊。漏永灯花暗，炉红雪片销。久游家共远，相对鬓俱凋。运命从难合，光阴奈不饶。到头蓑笠契，两信钓鱼潮。', '湘水春浮岸，淮灯夜满桥。六年悲梗断，两地各萍漂。刀笔依三事，篇章奏珥貂。迹卑甘汨没，名散称逍遥。好似松为径，空江桂作桡。野香花伴落，缸暖酒和烧。普招寻游凤，秦冠竟叹鸮，骨凡鸡犬薄，魂断惠兰招。怅望添燕琯，蹉跎厌鲁瓢。败桐方委爨，冤匣正冲霄。战代安釐国，封崇孝景朝。千年非有限，一醉解无聊。漏永灯花暗，炉红雪片销。久游家共远，相对鬓俱凋。运命从难合，光阴奈不饶。到头蓑笠契，两信钓鱼潮。', '公元877年，春雪中，至大梁封禅寺，有诗。', '公元877年，春雪中，至大梁封禅寺，有诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (633, '5051851d71e34707a25b836589c9e11a', '喜皇甫大夫同宿大梁驿', 251, 52, -1, '年代不详', '江头旌旆去，花外卷帘空。夜色临城月，春声渡水风。也知行李别，暂喜话言同。若问庐山事，终身愧远公。', '江头旌旆去，花外卷帘空。夜色临城月，春声渡水风。也知行李别，暂喜话言同。若问庐山事，终身愧远公。', '清江是江右有名的诗僧。常于禅诵之余，辙赋诗歌。舟行江中，长风吹动，船上旌帜招展。卷帘空旷水云间。花外珠帘锦绣，别处无花。夜幕降临，月出东山，朗然相照城郭。春寒料峭，水风还残留了冬天的寒意。深知友人还肩负着朝廷的使命。与君短暂的相逢后，终须离别。但值得安慰的是知音难觅，有意趣相投之人已属幸事。结句化用东晋时名僧慧远之典，以表自谦。若问远公庐山讲学之事，吾终身求索，亦难使惑者晓然。当是诗人的谦逊客套之语。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (632, '6866ccc9c1f844c084de325ee2bde2db', '同使君宿大梁驿', 250, 52, -1, '年代不详', '旌旗江上出，花外卷帘空。夜色临城月，春寒度水风。虽然行李别，且喜语音同。若问匡庐事，终身愧远公。', '旌旗江上出，花外卷帘空。夜色临城月，春寒度水风。虽然行李别，且喜语音同。若问匡庐事，终身愧远公。', '灵一童子出家，初于麻源第三谷中遁隐，结茆读书。后辗转于各个古庙佛寺，从学者四方而至。禅诵之余，辙赋诗歌。与朱放、灵澈、皇甫冉兄弟为诗友，酬唱不绝。因此诗中常饱含禅意。舟行江中，长风吹动，船上旌帜招展。卷帘空旷水云间。花外珠帘锦绣，别处无花。夜幕降临，月出东山，朗然相照城郭。春寒料峭，水风还残留了冬天的寒意。深知友人还肩负着朝廷的使命。与君短暂的相逢后，终须离别。但值得安慰的是知音难觅，有意趣相投之人已属幸事。结句化用东晋时名僧慧远之典，以表自谦。若问远公庐山讲学之事，吾终身求索，亦难使惑者晓然。当是诗人的', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (631, 'b77711e6a7b147d8bca9d1a5b2cc4205', '题大梁临汴驿', 249, 53, -1, '年代不详', '近日侯门不重才，莫将文艺拟为媒。相逢若要如胶漆，不是红妆即拨灰。', '近日侯门不重才，莫将文艺拟为媒。相逢若要如胶漆，不是红妆即拨灰。', '诗人将微韫之情诉诸于笔端。生活在晚唐的诗人，有和同时期其他文人一样的特定心理：既承受着中唐士人“会狂澜于既倒”信心的影响，又被黑暗的现实压抑。进取与退避交织，希望与失望共存。诗人敏感地意识到晚唐王朝已经走向行将崩溃的道路，这种深沉的时代衰落感便不自觉地显露。以才学走向仕途的道路阻碍重重。劝慰自己不要再以文艺为媒，而亵渎了文学的神圣性。诗人以情人重逢作象征。如果不能一鸣惊人得到赏识，莫不如收敛光芒,韫匵而藏。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (630, '2b26994240ae4c24a4b1c05556bf7a25', '途次大梁雪中奉天平令狐相公书问兼……形于短篇以申仰谢', 59, 0, -1, '年代不详', '远守宦情薄，故人书信来。共曾花下别，今独雪中回。纸尾得新什，眉头还暂开。此时同雁鹜，池上一徘徊。', '远守宦情薄，故人书信来。共曾花下别，今独雪中回。纸尾得新什，眉头还暂开。此时同雁鹜，池上一徘徊。', '刘禹锡曾和令狐楚牡丹花下别：“莫道两京非远别，春明门外即天涯”（《和令狐相公别牡丹》）。故此诗中流露出对友人深沉的思忆。刘禹锡远贬连州，许多昔日好友都刻意与其疏远，唯有乐天、令狐诸人等闲视之，不改初衷，永结一心。诗人拆封翻阅，拾得令狐的满纸赠语，故心结顿解。池上雁鹜正卧，诗人借机撷来作比，以雁鹜徘徊相栖之态，喻二人投合相携之情。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (629, '480301346d65443b96603053d345102a', '板桥晓别', 14, 49, -1, '年代不详', '回望高城落晓河，长亭窗户压微波。水仙欲上鲤鱼去，一夜芙蓉红泪多。', '回望高城落晓河，长亭窗户压微波。水仙欲上鲤鱼去，一夜芙蓉红泪多。', '此诗作于唐代汴州西的板桥店。诗人于此地客逢李郢，可能还有李郢在汴州的情人。聚散匆匆，欢宴言别，于是一首奇幻绚丽的离别诗在李商隐的脑海中油然而生。纪晓岚在《玉溪生诗说》中赞此诗：“何等风韵，如此作艳语，乃佳。”回望汴州方向，原先斜贯中天、高悬在城头的银河，此刻已经黯淡了，西移垂地。长亭自古分离地。在朦胧曙色中，这隐现于波光水际的长亭仿佛是幻化出来的蓬莱仙阁。窗棂下摇漾的烟波，颇似心中起伏难平的感情波流。“水仙”一句，化用了琴高的典故。赵国人琴高，会神仙术，曾乘赤鲤来，留月余复入水去。诗人把游子暗喻作琴高，生', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (628, 'af9797e24cdc4ee99c977aa640d7d7c7', '赴京初入汴口，晓景即事，先寄兵部李郎中', 79, 0, 835, '公元835年', '清淮控隋漕，北走长安道。樯形栉栉斜，浪态迤迤好。初旭红可染，明河澹如扫。泽阔鸟来迟，村饥人语早。露蔓虫丝多，风蒲燕雏老。秋思高萧萧，客愁长袅袅。因怀京洛间，宦游何戚草。什伍持津梁，澒涌争追讨。翾便讵可寻，几秘安能考。小人乏馨香，上下将何祷。唯有君子心，显豁知幽抱。', '清淮控隋漕，北走长安道。樯形栉栉斜，浪态迤迤好。初旭红可染，明河澹如扫。泽阔鸟来迟，村饥人语早。露蔓虫丝多，风蒲燕雏老。秋思高萧萧，客愁长袅袅。因怀京洛间，宦游何戚草。什伍持津梁，澒涌争追讨。翾便讵可寻，几秘安能考。小人乏馨香，上下将何祷。唯有君子心，显豁知幽抱。', '大和九年（公元835年），杜牧被朝廷征为监察御史，赴长安任职，分司东都。杜牧常自负有“真王佐才”。虽怀才不遇，但又往往旷达自解。诗人以君子称，以津梁自比。便翾九天，燕雀之属怎能寻觅其踪？几密审固，又岂是平庸之辈可以参阅的？此诗以写景、抒情、议论相杂，间有对佞小的悻悻嘲讽。“不为龌龊小谨，刚直有奇节”的形象跃然于纸上。', '大和九年（公元835年），杜牧被朝廷征为监察御史，赴长安任职，分司东都。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (627, '78535a38b7d34078af2b9b469d42e111', '汴人舟行答张祜', 79, 0, -1, '年代不详', '千万长河共使船，听君诗句倍怆然。春风野岸名花发，一道帆樯画柳烟。', '千万长河共使船，听君诗句倍怆然。春风野岸名花发，一道帆樯画柳烟。', '当时杜牧当度支使，待张祜十分亲厚，有诸多诗答赠：“何人得似张公子，千首诗轻万户侯。”贺裳在《载酒园诗话右编》里评：“杜紫微诗，惟绝句最多风调，味永趣长，有明月孤映，高霞独举之象，余诗则不能尔。”此诗为杜牧典型的写景抒情类绝句，意境优美，韵味隽永，于盛唐七绝之外，别开妙境。诗人先以绝句小体将汴水行舟的总幕纳入眼帘，再以细腻笔触捕捉自然中美的形象。春风吹绿了江岸，花坞萍汀。远远望去，柳树像一蓬鹅黄色的烟沿着帆樯袅袅升起。情寓景中，神韵悠远。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (625, '182b6b25b9474ff9958ebe73858914f2', '送汴州监军俱文珍', 72, 0, 796, '796年', '奉使羌池静，临戎汴水安。冲天鹏翅阔，报国剑铓寒。晓日驱征骑。春风咏采兰。谁言臣子道，忠孝两全难。', '奉使羌池静，临戎汴水安。冲天鹏翅阔，报国剑铓寒。晓日驱征骑。春风咏采兰。谁言臣子道，忠孝两全难。', '贞元十二年，董晋为宣武军节度使，文珍为监军，退之为观察推官，文珍将入京师，退之作诗并序送之。此诗俱是颂扬文珍功德的溢美之词。文珍坐镇之际，羌池静、汴水安。鹏飞冲天、展翅高阔，剑锋所指、寒光逼人。“天子无后顾之忧，方伯有同和之美”（《序》）。行文若春风化雨，温润濡笔，吟咏君子如兰之美志。虽奉相国陇西公之命作诗铺叙功德，却颂美自然，绝非谀词。', '贞元十二年，董晋为宣武军节度使，文珍为监军，退之为观察推官，文珍将入京师，退之作诗并序送之。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (626, '13db9f2d72af477a8204756d4569ca88', '过大梁闻河亭方宴赠孙子端', 79, 0, -1, '年代不详', '梁园纵玩归应少，赋雪搜才去必频。板落岂缘无罚酒，不教客右更添人。', '梁园纵玩归应少，赋雪搜才去必频。板落岂缘无罚酒，不教客右更添人。', '诗人纵笔就梁园史事落墨。昔日梁孝王好营园囿之乐，大治宫室，招揽贤才，豪俊之士靡集梁园。司马相如、枚乘等人，蕴藉风流，都曾是他的座上之宾。南朝宋谢惠莲为《雪赋》，曲尽描绘梁园大雪景色，传为妙文。梁园客右，赋雪宴游，本是诗人趋心向往之事。无奈有信陵家世，叹才华高掩，只能被人拒之门外，难共迟留。诗人不禁暇愁于人生达命，饮恨于报国无门。仕途困厄之感伤溢满纸背。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (623, 'a5b097dd541c403698b0acffdf107709', '远游联句', 248, 0, 799, '公元799年', '取之讵灼灼，此去信悠悠。', '取之讵灼灼，此去信悠悠。', '无', '公元799仲春，在汴州任观察推官。孟郊将离汴州南下，韩愈、孟郊、李翱三人共作联句。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (624, '19f0449a648f411389bdebf25143e6eb', '远游联句', 72, 0, 798, '公元798年', '离思春冰泮，澜漫不可收（愈）。取之讵灼灼，此去信悠悠（翱）。。灵瑟时窅窅，阴猿夜啾啾。愤涛气尚盛，恨竹泪空幽。长怀绝无已，多感良自尤。即路涉献岁，归期眇凉秋。两欢日牢落，孤悲坐绸缪（愈）。观怪忽荡漾，叩奇独冥搜。海鲸吞明月，浪岛没大沤。我有一寸钩，欲钓千丈流。良知忽然远，壮志郁无抽（郊）。魍魅暂出没，蛟螭互蟠蟉。昌言拜舜禹，', '离思春冰泮，澜漫不可收（愈）。取之讵灼灼，此去信悠悠（翱）。。灵瑟时窅窅，阴猿夜啾啾。愤涛气尚盛，恨竹泪空幽。长怀绝无已，多感良自尤。即路涉献岁，归期眇凉秋。两欢日牢落，孤悲坐绸缪（愈）。观怪忽荡漾，叩奇独冥搜。海鲸吞明月，浪岛没大沤。我有一寸钩，欲钓千丈流。良知忽然远，壮志郁无抽（郊）。魍魅暂出没，蛟螭互蟠蟉。昌言拜舜禹，', '无', '公元798仲春，在汴州任观察推官。孟郊将离汴州南下，韩愈、孟郊、李翱三人共作联句。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (622, '5454ff3686c942d48a09100b7d8684b6', '远游联句', 8, 0, 798, '公元798年', '别肠车轮转，一日一万周（郊）。驰光忽以迫，飞辔谁能留（郊）。楚客宿江上，夜魂栖浪头。晓日生远岸，水芳缀孤舟。村饮泊好木，野蔬拾新柔。独含悽悽离思春冰泮，澜漫不可收。灵瑟时窅窅，阴猿夜啾啾。愤涛气尚盛，恨竹泪空幽。长怀绝无已，多感良自尤。即路涉献岁，归期眇凉秋。两欢日牢落，孤悲坐绸缪。魍魅暂出没，蛟螭互蟠蟉。昌言拜舜禹，举马风凌斗牛。怀糈馈贤屈，乘桴追圣丘。飘然天外步，岂肯区中囚。驰深鼓利楫，趋险惊蜚輶。系石沈靳尚，开弓射鴅吺。路暗执屏翳，波惊戮阳侯。广泛信缥缈，高行恣浮游。外患萧萧去，中悒稍稍瘳。振衣造云阙，跪坐陈清猷。德风变谗巧，仁气销戈矛。名声照西海，淑问无时休。归哉孟夫子，归去无夷犹。', '别肠车轮转，一日一万周（郊）。驰光忽以迫，飞辔谁能留（郊）。楚客宿江上，夜魂栖浪头。晓日生远岸，水芳缀孤舟。村饮泊好木，野蔬拾新柔。独含悽悽离思春冰泮，澜漫不可收。灵瑟时窅窅，阴猿夜啾啾。愤涛气尚盛，恨竹泪空幽。长怀绝无已，多感良自尤。即路涉献岁，归期眇凉秋。两欢日牢落，孤悲坐绸缪。魍魅暂出没，蛟螭互蟠蟉。昌言拜舜禹，举马风凌斗牛。怀糈馈贤屈，乘桴追圣丘。飘然天外步，岂肯区中囚。驰深鼓利楫，趋险惊蜚輶。系石沈靳尚，开弓射鴅吺。路暗执屏翳，波惊戮阳侯。广泛信缥缈，高行恣浮游。外患萧萧去，中悒稍稍瘳。振衣造云阙，跪坐陈清猷。德风变谗巧，仁气销戈矛。名声照西海，淑问无时休。归哉孟夫子，归去无夷犹。', '无', '公元798仲春，在汴州任观察推官。孟郊将离汴州南下，韩愈、孟郊、李翱三人共作联句。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (620, 'fe23add790264047aa8d63b5330e41a8', '汴河路有感', 12, 53, -1, '年代不详', '三十年前路，孤舟重往还。绕身新眷属，举目旧乡关。事去唯留水，人非但见山。啼襟与愁鬓，此日两成斑。', '三十年前路，孤舟重往还。绕身新眷属，举目旧乡关。事去唯留水，人非但见山。啼襟与愁鬓，此日两成斑。', '无', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (621, 'cb6889eb304a4e21a103b9a0577dccfb', '汴州留别韩愈', 8, 53, 798, '公元798年', '不饮浊水澜，空滞此汴河。坐见绕岸水，尽为还海波。四时不在家，弊服断线多。远客独憔悴，春英落婆娑。汴水饶曲流，野桑无直柯。但为君子心，叹息终靡他。', '不饮浊水澜，空滞此汴河。坐见绕岸水，尽为还海波。四时不在家，弊服断线多。远客独憔悴，春英落婆娑。汴水饶曲流，野桑无直柯。但为君子心，叹息终靡他。', '此诗当作于贞元十四、五年冬末春初孟郊离开汴州前夕。孟郊赴汴入幕求官，依附陆长源曲线救国。然而夷门豪士的骄纵不法、董晋的一味姑息与陆长源的无能为力都在一步步加深孟郊对董晋所领宣武军的认识。起首的“不饮浊水澜”一句，虽涉比兴，但并不难解。在孟郊眼中，董晋汴州幕已是一潭浊水。所谓“饶曲流”、“无直柯”盖与“浊水澜”同一手法，同一所指。这样的一滩浑水，对于秉持“一不改方圆，破质为琢磨”（《上张徐州》）的坚贞操守，信守“变衣非变性”的入幕原则的孟郊来说，多半是不会去淌的。尽管如此，孟郊对此次汴州之行仍是不能释怀，所', '此诗当作于贞元十四、五年冬末春初孟郊离开汴州前夕。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (619, '6e4f793698754b779ed8809a66cd9d73', '板桥路', 12, 49, -1, '年代不详', '梁苑城西二十里，一渠春水柳千条。若为此路今重过，十五年前旧板桥。曾共玉颜桥上别，不知消息到今朝。', '梁苑城西二十里，一渠春水柳千条。若为此路今重过，十五年前旧板桥。曾共玉颜桥上别，不知消息到今朝。', '无', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (618, '06bc467294264f09977e3ec42e338ec0', '别陈留诸官', 11, 0, 743, '公元743年', '恋此东道主，能令西上迟。徘徊暮郊别，惆怅秋风时。上国邈千里，夷门难再期。行人望落日，归马嘶空陂。不愧宝刀赠，维怀琼树枝。音尘徜未接，梦寐徒相思。', '恋此东道主，能令西上迟。徘徊暮郊别，惆怅秋风时。上国邈千里，夷门难再期。行人望落日，归马嘶空陂。不愧宝刀赠，维怀琼树枝。音尘徜未接，梦寐徒相思。', '公元743年，以家境困窘，东游梁、宋，求亲友资助。留别陈留有诗。黄昏与秋是传统诗人的时间与季候。刘长卿深谙此道，最擅长将清幽之景深描，寄托身世浮沉之嗟叹，从而显示出一种轻度的衰飒之感。羁旅行役，夷门送别，已是人间一等苦事。诗人还嫌不够，将落日余晖、惆怅秋风竞相纳入读者的视野里。斜阳古道、马嘶长空，为这幅临别图抹上了一层冷暗的色彩。淡淡的哀愁与凄凉划过，实为盛唐溽暑中飘落的第一片秋叶。', '公元743年，以家境困窘，东游梁、宋，求亲友资助。留别陈留有诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (617, '4c17684855ae45e38a0c6c2bfd8021b8', '至大梁却寄匡城主人', 6, 0, 741, '公元741年', '一从弃鱼钓，十载干明王。无由谒天阶，却欲归沧浪。仲秋至东郡，遂见天雨霜。昨夜梦故山，蕙草色已黄。平明辞铁丘，薄暮游大梁。仲秋萧条景，拔刺飞鹅鶬。四郊阴气闭，万里无晶光。长风吹白茅，野火烧枯桑。故人南燕史，籍籍名更香。聊以玉壶赠，置之君子堂。', '一从弃鱼钓，十载干明王。无由谒天阶，却欲归沧浪。仲秋至东郡，遂见天雨霜。昨夜梦故山，蕙草色已黄。平明辞铁丘，薄暮游大梁。仲秋萧条景，拔刺飞鹅鶬。四郊阴气闭，万里无晶光。长风吹白茅，野火烧枯桑。故人南燕史，籍籍名更香。聊以玉壶赠，置之君子堂。', '岑参曾于开元二十九年（公元741年）北游河朔。岑参自二十献书阙下未果后，屡屡出入于京洛之间为仕途奔波。此后十年，诗人“出入二郡，蹉跎十秋”。历经十载干禄，诗人思想发生了很大的转变。由“云霄坐致，青紫俯拾”的踌躇满志变得郁悒满怀。诗人将求仕无果而意欲归隐之念通过一系列暗淡的秋景展现出来：长风、白茅、野火、枯桑。肃杀的秋意是最会撩拨人的心绪的。诗人心中秋草黄尽，愁云密布遮住了前行的光。故诗人笔下的景物意象多呈现一种清幽之味，笼罩着淡淡的哀愁。', '岑参曾于开元二十九年（公元741年）北游河朔。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (616, 'f58604c7886943fe862f64e238277fb7', '公无渡河', 5, 0, -1, '阙如', '黄河西来决昆仑，咆哮万里触龙门。波滔天，尧咨嗟，大禹理百川，儿啼不窥家。杀湍烟洪水，九州始蚕麻。其害乃去，茫然风沙。被发之叟狂而痴，清晨临流欲奚为。旁人不惜妻止之，公无渡河苦渡之。虎可搏，河难凭，公果溺死流海湄。有长鲸白齿若雪山，公乎公乎挂罥于其间，箜篌所悲竟不还。', '黄河西来决昆仑，咆哮万里触龙门。波滔天，尧咨嗟，大禹理百川，儿啼不窥家。杀湍烟洪水，九州始蚕麻。其害乃去，茫然风沙。被发之叟狂而痴，清晨临流欲奚为。旁人不惜妻止之，公无渡河苦渡之。虎可搏，河难凭，公果溺死流海湄。有长鲸白齿若雪山，公乎公乎挂罥于其间，箜篌所悲竟不还。', '此诗具体作年难以考证。有人认为此诗作于安禄山反叛前，有人认为此诗当作于流放夜郎与宗夫人分别时。李白借乐府古题，依傍而作。借助古老的渡河故事吟诵了一曲狂放而怫悒的悲歌。开篇就将巨笔伸向了辽远的往古。以突兀之语，写它在霎那间冲决的力量和磅礴的气势的象征—横亘天地的昆仑山。随即挟着雷鸣般地咆哮，直撞万里之外的龙门。诗人只寥寥两笔，就在“昆仑”、“龙门”的震荡声中，展现了西来黄河的无限声威。茫茫荒古，顿时充斥了尧帝放勋的浩然叹息。于是，“大禹”出现了。桀骜狂暴的洪水在这位英雄的脚下驯服。黄河的荒古之害从此驱除，但', '此诗具体作年难以考证。有人认为此诗作于安禄山反叛前，有人认为此诗当作于流放夜郎与宗夫人分别时。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (615, '481ff97d524843c39d184dda82cc65aa', '留别于十一兄逖裴十三游塞垣', 5, 0, 751, '公元751年', '太公渭川水，李斯上蔡门。钓周猎秦安黎元，小鱼鵕兔何足言。天张云卷有时节，吾徒莫叹羝触藩。于公白首大梁野，使人怅望何可论。即知朱亥为壮士，且愿束心秋毫里。秦赵虎争血中原，当去抱关救公子。裴生览千古，龙鸾炳天章。悲吟雨雪动林木，放书辍剑思高堂。劝尔一杯酒，拂尔裘上霜。尔为我楚舞，吾为尔楚歌。且探虎穴向沙漠，鸣鞭走马凌黄河。耻作易水别，临岐泪滂沱。', '太公渭川水，李斯上蔡门。钓周猎秦安黎元，小鱼鵕兔何足言。天张云卷有时节，吾徒莫叹羝触藩。于公白首大梁野，使人怅望何可论。即知朱亥为壮士，且愿束心秋毫里。秦赵虎争血中原，当去抱关救公子。裴生览千古，龙鸾炳天章。悲吟雨雪动林木，放书辍剑思高堂。劝尔一杯酒，拂尔裘上霜。尔为我楚舞，吾为尔楚歌。且探虎穴向沙漠，鸣鞭走马凌黄河。耻作易水别，临岐泪滂沱。', '天宝十年（公元751年）暮秋，李白往幽州至安禄山麾下谋求出路，在汴州稍作停留，将要再次启程时作此诗向逖、裴十三这两位他的好友告别，倾诉离情别绪。历史的洪流席卷而来，尘封的卷轴在眼前展开。姜子牙在渭水边垂钓，李斯出上蔡门狩猎。白发苍苍的于公是大梁的野老隐逸，使人怅望而不可一论。既然知道朱亥是壮士豪杰，且愿收心于秋毫笔墨之中。在对古人的一番追思后，诗人没有让自己的感情沉浸在对历史的凭吊中，他把目光又投向自然。接下来两句写送行者，没有离情的渲染，尽是饯别场景的直叙。诗的后半是抒情，语及幽州之行，仅末四句而已。“', '天宝十年（公元751年）暮秋，李白往幽州至安禄山麾下谋求出路，在汴州稍作停留，将要再次启程时作此诗向逖、裴十三这两位他的好友告别，倾诉离情别绪。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (614, 'c56df110428c4d9e9bc9c502a599d886', '晚入汴水', 101, 53, -1, '年代不详', '昨晚南行楚，今朝北溯河。客愁能几日，乡路渐无多。晴景摇津树，春风起棹歌。长淮亦已尽，宁复畏潮波。', '昨晚南行楚，今朝北溯河。客愁能几日，乡路渐无多。晴景摇津树，春风起棹歌。长淮亦已尽，宁复畏潮波。', '首联扣题稳起，开阔大气，写的是行走路径。诗人家住开封，宿州西北，故云“北溯河”。二联上承，客愁他乡二十年，当下已到乡音大同的淮北宿州，距离魂牵梦萦的故乡已经很近了，愉悦之情溢于言表。三联转而写景，且语断意连，更进一层。家近人喜的心情跃然纸上，字字珠玑。二、三联以情为线，既转又承，写得绝妙。“摇”、“起”两个精准的动词活化了春意盎然的汴水，可谓笔运千斤。尾联水到渠成，一意贯通。江天浩荡的淮水都已经渡过了，碧水细浪的汴河还有什么可畏惧的呢？长期浪迹的苦楚和游子近乡的喜悦交织、碰撞，融入了春潮滚滚的汴河。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (613, '39a0be4f1d4644c58615f6275a7226ad', '古大梁行', 208, 0, 744, '公元 744年', '古城莽苍饶荆榛，驱马荒城愁杀人。魏王宫观尽禾黍，信陵宾客随灰尘。忆昨雄都旧朝市，轩车照耀歌钟起。军容带甲三十万，国步连营一千里。全盛须臾那可论，高台曲池无复存。遗墟但见狐狸迹，古地空馀草木根。暮天摇落伤怀抱，抚剑悲歌对秋草。侠客犹传朱亥名，行人尚识夷门道。白璧黄金万户侯，宝刀骏马填山丘。年代凄凉不可问，往来唯见水东流。', '古城莽苍饶荆榛，驱马荒城愁杀人。魏王宫观尽禾黍，信陵宾客随灰尘。忆昨雄都旧朝市，轩车照耀歌钟起。军容带甲三十万，国步连营一千里。全盛须臾那可论，高台曲池无复存。遗墟但见狐狸迹，古地空馀草木根。暮天摇落伤怀抱，抚剑悲歌对秋草。侠客犹传朱亥名，行人尚识夷门道。白璧黄金万户侯，宝刀骏马填山丘。年代凄凉不可问，往来唯见水东流。', '天宝三载（公元744年），高适与李白、杜甫同游大梁古城时所作。时高适四十岁，正隐居宋中，以耕钓为生，虽欲跻身仕途，但又求尽无门，因此萌生一种无可奈何的凄凉意绪。发端伉爽。以形象笔墨勾勒出了一幅生动的荒城图。在缓辔徐行中，只见满城荆棘丛生，莽莽苍苍，昔日巍峨壮丽的魏王宫观如今长满了禾黍，曾经威震江湖的信陵君和他的三千食客，也以烟消云散，化作满地尘滓。“忆昨”四句推开。在雄都朝市中，轩车驰骤，歌钟四起，一片繁华盛景。“全盛”句折入，前有“忆昨”一段作铺垫，后有“遗墟”、“古地”作反衬，情感就自然跳脱而出。“暮', '天宝三载（公元744年），高适与李白、杜甫同游大梁古城时所作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (611, 'c83e6fb46884434292b01c27dd820822', '大梁途中作', 4, 52, 710, '公元710年', '怏怏步长道，客行渺无端。郊原欲下雪，天地棱棱寒。当时每酣醉，不觉行路难。今日无酒钱，凄惶向谁叹。', '怏怏步长道，客行渺无端。郊原欲下雪，天地棱棱寒。当时每酣醉，不觉行路难。今日无酒钱，凄惶向谁叹。', '景龙四年（公元710年），游历河南。王昌龄此诗中浸润着深沉的感伤情结。天寒欲雪，客游在外的诗人裹挟着一身的寒气与落寞，怏怏行走在人迹罕至的长道上。棱棱霜气、簌簌风威。昔日酣醉畅饮，尚可麻痹，不用忍受清醒时的颓唐。而今仕途濩落，捉襟见肘，只能体味生活的苦涩。诗人将身世之感溶进诗中，故笔下的自然景物附上了阴暗、冷重的主观情绪，读来唇齿生寒。', '景龙四年（公元710年），游历河南。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (612, '6dda23de67924873908ad927288ac3c2', '题李别驾壁', 208, 52, 735, '公元735年', '去乡不远逢知己，握手相欢得如此。礼乐遥传鲁伯禽，宾客争过魏公子。酒筵暮散明月上，枥马长鸣春风起。一生称意能几人，今日从君问终始。', '去乡不远逢知己，握手相欢得如此。礼乐遥传鲁伯禽，宾客争过魏公子。酒筵暮散明月上，枥马长鸣春风起。一生称意能几人，今日从君问终始。', '开元二十三年（公元735年），征诣长安，应制科试，过开封，春，有诗。', '开元二十三年（公元735年），征诣长安，应制科试，过开封，春，有诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (610, 'edd6cb71db3c4c48a98e87fabca5b603', '永福湖和杨郑州', 68, 70, -1, '年代不详', '积水还平岸，春来引郑溪。旧渠通郭下，新堰绝湖西。嵩少分明对，潇湘阔狭齐。客游随庶子，孤屿草萋萋。', '积水还平岸，春来引郑溪。旧渠通郭下，新堰绝湖西。嵩少分明对，潇湘阔狭齐。客游随庶子，孤屿草萋萋。', '春水初生，涵虚愈清。积水翻滚冲刷着堤岸。旧的池渠顺着城郭蜿蜒而下，设置新堰阻断湖水自西流淌。嵩山与少室遥遥相对，湘江与潇水被大自然的鬼斧神工裁剪得熨帖整齐。贾岛很少触笔生机盎然的春季，即使有也经过了诗人的冷处理。“客游随庶子，孤峪草萋萋”。本是碧绿如丝的春草，在客游的诗人眼里却仿佛愁煞人，浸染着深重的凄清意绪。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (609, '2bc6ad81872f41c28a8b02546376f4bd', '登首阳山谒夷齐庙', 247, 45, -1, '年代不详', '古人已不见，乔木竟谁过。寂寞首阳山，白云空复多。苍苔归地骨，皓首采薇歌。毕命无怨色，成仁其若何。我来入遗庙，时候微清和。落日吊山鬼，回风吹女萝。石崖向西豁，引领望黄河。千里一飞鸟，孤光东逝波。驱车层城路，惆怅此岩阿。', '古人已不见，乔木竟谁过。寂寞首阳山，白云空复多。苍苔归地骨，皓首采薇歌。毕命无怨色，成仁其若何。我来入遗庙，时候微清和。落日吊山鬼，回风吹女萝。石崖向西豁，引领望黄河。千里一飞鸟，孤光东逝波。驱车层城路，惆怅此岩阿。', '天下宗周，叔齐、伯夷不耻，二人遂隐于首阳山，采薇而食，后人修庙以纪之。黄昏向晚，时令清和，诗人登高拜谒，慨然有怀。古人的身影早已消散在历史的烟尘中。草木无过，却被殃及迁怒。首阳山上林木森森，清幽疏旷更添寂寞。昔人已去，空留白云千载，舒卷悠悠。诗人于写景之外申发出自己的新见：夷齐二人杀身成仁是自己的选择，后人又有什么立场妄加议论？落日吊山、孤鸟横波，峭崖千韧，日光寒峻。数句之间，写尽意态，包孕着高苍古朴之气。诗人以议论作结，天涯沦落的漂泊之感乘风而来。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (608, '70442ab8878c4000bafeb1d4fc79676c', '宿泊孟津寄三堂友人', 115, 69, -1, '阙如', '解缆西征未有期，槐花又逼桂花时。鸿胪陌上归耕晚，金马门前献赋迟。只恐愁苗生两鬓，不堪离恨入双眉。分明昨夜南池梦，还把渔竿咏楚词。', '解缆西征未有期，槐花又逼桂花时。鸿胪陌上归耕晚，金马门前献赋迟。只恐愁苗生两鬓，不堪离恨入双眉。分明昨夜南池梦，还把渔竿咏楚词。', '刘勰在《文心雕龙·明诗》篇说：“人禀七情，应物斯感，感物吟志，莫非自然。”韦庄正是由自身经历所感，然后是其情自然流露。鸿胪、金门皆是官署攒集之地。而诗人屡困场屋、频举不第的经历，让他饱受内心的折磨。韶华已逝，而功业未成。调子低沉，胸宏不野。诗人遂在潸然中隐隐流露出归隐之意。莫不如垂钓南池，渔樵山水。', '阙如', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (607, '51f9e522f9ca48b5817962546626644d', '自孟津舟西上雨中作', 115, 69, 886, '公元886年', '秋烟漠漠雨濛濛，不卷征帆任晚风。百口寄安沧海上，一身逃难绿林中。来时楚岸杨花白，去日隋堤蓼穗红。却到故园翻似客，归心迢递秣陵东。', '秋烟漠漠雨濛濛，不卷征帆任晚风。百口寄安沧海上，一身逃难绿林中。来时楚岸杨花白，去日隋堤蓼穗红。却到故园翻似客，归心迢递秣陵东。', '光启二年（公元886年），韦庄奉周宝之命北上迎驾，后沿渭、黄河而返，困守孟津数月，此诗大致作于此后。诗人将离乱的苦楚、身世的飘零甚至故园的相思倾诉在此诗中，向后人剖白了一位乱世文人的幽微心曲。他回旋万里，求安，求衣，求食，结果仍多是失意。因此诗作中常弥漫出一股淡淡的哀愁。自古逢秋总是寂寥。雾霭迷蒙仿佛诗人心中笼罩着的阴霾，挥散不去。秋雨淅沥仿佛滴在诗人心尖的愁绪，淋漓不尽。尽管是这种伤心语的抒怀，色彩也没有趋向晦暗。“绿林中”、“杨花白”、“蓼穗红”，虽以乐景写哀，却犹如晚照晴岚，哀而不暗，清茜馥郁，当是', '光启二年（公元886年），韦庄奉周宝之命北上迎驾，后沿渭、黄河而返，困守孟津数月，此诗大致作于此后。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (605, '0958fea67fdf4e9b98bc75d1170b9dfc', '传记之萧旷', 33, 46, -1, '年代不详', '太和间萧旷夜宿孝义驿，与洛神谈《洛神赋》事，与洛浦龙王之女织绡娘子谈龙事。', '太和间萧旷夜宿孝义驿，与洛神谈《洛神赋》事，与洛浦龙王之女织绡娘子谈龙事。', '无', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (606, '598d62abf01542fc85a74497a0e538dd', '中牟县经鲁公庙', 146, 70, -1, '年代不详', '青史编名在箧中，故林遗庙揖仁风。还将文字如颜色，暂下蒲车为鲁公。', '青史编名在箧中，故林遗庙揖仁风。还将文字如颜色，暂下蒲车为鲁公。', '东汉章帝初，鲁恭为中牟令，以德化不以刑法，有鲁恭“三异”之美誉。即稚子有仁心、蝗虫不入境、雉不避人。后鲁恭升司徒，乡民立祠祀之。后经多次扩建，形成庙宇。鲁公本事，当来源于此。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (604, 'ede1187678a443a2ac0fe624ec9b98d6', '法苑珠林', 246, 46, -1, '年代不详', '贞观十七年孙回璞赴齐州为齐王疗疾，至孝义驿遇鬼云魏征招其当记室。回璞云王命未毕，不当前往。鬼遂与回璞同行至长安复命。魏征果亡。回璞辞别家人，随鬼至高山大殿，有君子云其修福不宜留之，推之使堕，璞遂复生矣。', '贞观十七年孙回璞赴齐州为齐王疗疾，至孝义驿遇鬼云魏征招其当记室。回璞云王命未毕，不当前往。鬼遂与回璞同行至长安复命。魏征果亡。回璞辞别家人，随鬼至高山大殿，有君子云其修福不宜留之，推之使堕，璞遂复生矣。', '无', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (603, '420cea44fcdc4c049c00a3cb75a992c6', '送谏议十六叔至孝义渡后奉寄', 8, 46, -1, '年代不详', '晓渡明镜中，霞衣相飘飖。浪凫惊亦双，蓬客将谁僚。别饮孤易醒，离忧壮难销。文清虽无敌，儒贵不敢骄。江吏捧紫泥，海旗剪红蕉。分明太守礼，跨蹑毗陵桥。伊洛去未回，遐瞩空寂寥。', '晓渡明镜中，霞衣相飘飖。浪凫惊亦双，蓬客将谁僚。别饮孤易醒，离忧壮难销。文清虽无敌，儒贵不敢骄。江吏捧紫泥，海旗剪红蕉。分明太守礼，跨蹑毗陵桥。伊洛去未回，遐瞩空寂寥。', '沈德潜《说诗语》：“孟东野亦从风骚中出，特意象孤俊。”“浪凫、蓬客”都寓有随波逐荡之意，暗合了诗人的人生际遇。他两举进士而不第，一任县尉而未终。世风浇薄，沉沦下僚，在仕进的路途中，遭遇了无数磨难和打击，故爆发了极度压抑下的不平之鸣。长风横波，凫鸟受惊尚且相互依偎。而我身如蓬草，转涉飘零，却难遇同僚知己。包含诗人对亲友的依依惜别之情。“孤”、“离”、“忧”，构成了一个清冷的意象世界，映照了诗人凄涩苦寒的心境。诗人以“紫泥”“红蕉”等高洁矜贵之物自比。江吏有先知之意，“捧”犹言对待精贵之物的小心翼翼之态。“红', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (602, 'd9fd48f7affc4a3e80de1b7f1676f07d', '至孝义渡寄郑军事唐二十五', 8, 46, -1, '年代不详', '咫尺不得见，心中空嗟嗟。官街泥水深，下脚道路斜。嵩少玉峻峻，伊雒碧华华。岸亭当四迥，诗老独一家。洧叟何所如，郑石唯有些。何当来说事，为君开流霞。', '咫尺不得见，心中空嗟嗟。官街泥水深，下脚道路斜。嵩少玉峻峻，伊雒碧华华。岸亭当四迥，诗老独一家。洧叟何所如，郑石唯有些。何当来说事，为君开流霞。', '诗人成功地抒发了对友人浓烈、沉重、多况味的思念之情。相隔咫尺而不得相见，唯有寄诗一封聊表相思。官场倾轧、宵小构陷，仕途荆棘丛生。诗人以象征的手法曲折隐晦地刻画了当时的政治环境。莫如移步。极目骋望，嵩山青峻如玉，伊洛碧华如练。洧川老叟岂有威名，老于诗者唯有郑石一家。既是诗人的自谦，又蕴含着对友人的偏爱。看那一路流霞幻彩，映染九天，当是沾溉了我的情意。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (601, '45a8711fc913443db24e4eb8582a81eb', '宿龙潭寺', 12, 0, -1, '阙如', '夜上九潭谁是伴，云随飞盖月随杯。明年尚作三川守，此地兼将歌舞来。', '夜上九潭谁是伴，云随飞盖月随杯。明年尚作三川守，此地兼将歌舞来。', '此诗深得李白“月下独酌”神髓。是夜，背景是前往龙潭寺的道路中，诗人独自登场。场面略显单调。诗人忽发奇想，邀请天上的流云和高悬的明月同游，效仿李白“对影成三人”。良辰美景，勾起了诗人的兴致，扬言要做泾、渭、洛三河的太守，还要佐之以歌舞，活跃气氛。诗人夜宿龙潭，却没有孑然一身的孤寂落寞，而是善自排遣寂寞，显示出旷达不羁的个性。', '阙如', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (600, '1881feb647734b1f917a5667b648d57b', '宿荥阳', 12, 71, 829, '公元829年', '生长在荥阳，少小辞乡曲。迢迢四十载，复向荥阳宿。去时十一二，今年五十六。追思儿戏时，宛然犹在目。旧居失处所，故里无宗族。岂唯变市朝，兼亦迁陵谷。独有溱洧水，无情依旧绿。', '生长在荥阳，少小辞乡曲。迢迢四十载，复向荥阳宿。去时十一二，今年五十六。追思儿戏时，宛然犹在目。旧居失处所，故里无宗族。岂唯变市朝，兼亦迁陵谷。独有溱洧水，无情依旧绿。', '此诗当作于唐文宗太和三年（公元829年）春，白居易因病改授太子宾客分司，回洛阳履道里，自此不复出。白居易生于新郑，迁居下郢。这里的荥阳，是唐时的荥阳郡，新郑当时隶属荥阳管辖。此诗当为白居易的生平提供了清晰的线索。诗人于十一二岁时，举家迁往南方。年过半百之后，才有机会重新踏上故土。他的内心深处，自然生出了和贺知章一样的情思。贺在《回乡偶书》之二中写到了湖水：“离别家乡岁月多，近来人事半消磨。惟有门前镜湖水，春风不改旧时波。”白居易受其影响，“独有溱洧水，无情依旧绿”，自然如出一辙。', '此诗当作于唐文宗太和三年（公元829年）春，白居易因病改授太子宾客分司，回洛阳履道里，自此不复出。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (598, '81cf238463534938a6be2d4d75515278', '酬李相公喜归乡国自巩县夜泛洛水见寄', 59, 46, -1, '年代不详', '巩树烟月上，清光含碧流。且无三已色，犹泛五湖舟。鹏息风还起，凤归林正秋。虽攀小山桂，此地不淹留。', '巩树烟月上，清光含碧流。且无三已色，犹泛五湖舟。鹏息风还起，凤归林正秋。虽攀小山桂，此地不淹留。', '无', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (599, '8f4e6e72ca19471b87b62086c9bd1084', '行次荥阳却寄诸弟', 245, 47, 890, '890年', '难把归书说远情，奉亲多阙拙为兄。早知寸禄荣家晚，悔不深山共汝耕。枕上算程关月落，帽前搜景岳云生。如今已作长安计，只得辛勤取一名。', '难把归书说远情，奉亲多阙拙为兄。早知寸禄荣家晚，悔不深山共汝耕。枕上算程关月落，帽前搜景岳云生。如今已作长安计，只得辛勤取一名。', '此诗当作于昭宗大顺元年之前，诗人欲赴长安参加秋贡春试。杜荀鹤生处晚唐五代之际，遭世战乱，坎坷半生，诗名甚彰而悭于一第，仕途驰驱，精神疲劳。故诗中常常流露出未能光耀门楣的惭愧之情，“落第愁生晓鼓出，地寒才薄欲何如？”生逢乱世，物态浇漓，类似诗人这样出身孤寒缺乏奥援的人当然是奔走仕途半生沦谢了。屡试不第，生活困厄仅免饥寒，故生出垄耕遁世之念。然而名利之所，人之所趋。更何况不由进士者，终不为美。月落云生，诗人在经历一番自我挣扎后，毅然踏上入京之路。', '此诗当作于昭宗大顺元年之前，诗人欲赴长安参加秋贡春试。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (594, 'b803acc7a2754fce829d1fab1bd2e171', '题嵩高隐者居', 66, 72, 876, '876年', '岂易访仙踪，云萝千万重。他年来卜隐，此景愿相容。乱水林中路，深山雪里钟。见君琴酒乐，回首兴何慵。', '岂易访仙踪，云萝千万重。他年来卜隐，此景愿相容。乱水林中路，深山雪里钟。见君琴酒乐，回首兴何慵。', '乾符三年（公元876年），下第后，应辟为汝州从事。大概来时，经嵩山，有题诗。此诗有贾岛《寻隐者不遇》之遗风。首二句写得颇有深致。云萝低垂，如一道道绿屏交叠，掩映了仙人的行踪。水流、林荫、山雪、钟鸣，构成一派幽深杳远之景，表露出归隐的祁向。随意而适，任运自然，浸透在人生况味的抚琴酌饮之中。', '乾符三年（公元876年），下第后，应辟为汝州从事。大概来时，经嵩山，有题诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (595, '5ee6a995068b4688a56d264e59cf5663', '过襄城', 72, 72, 817, '817年', '郾城辞罢过襄城，颍水嵩山刮眼明。以去蔡州三百里，家人不用远来迎。', '郾城辞罢过襄城，颍水嵩山刮眼明。以去蔡州三百里，家人不用远来迎。', '元和九年（公元817年），韩愈出任宰相裴度的行军司马，参与讨平“淮西之乱”。韩愈也是在这时“讨淮蔡，道由襄”的。裴度等人完成了征讨的使命后凯旋，班师的过程轻松而愉快。从蔡州到郾城，再从郾城到襄城，每一段路程都显得短暂。胜利带来的喜悦心情，使得眼前的景物也变得明丽了许多。这和出征时途径襄城所感受到的彤云密布的场景，形成了强烈的对比。全诗节奏明快，极富动感，“气盛”一说绝非虚妄。', '元和九年（公元817年），韩愈出任宰相裴度的行军司马，参与讨平“淮西之乱”。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (596, 'eca6bab8f677494ea8013bea17420d55', '晚次东亭献郑州宋使君文', 39, 48, -1, '阙如', '自出宾上国，乃到邹人乡。曽点与曽子，俱升阙里堂。武皇恢大略，逸翮思寥廓。三居清宪台，两拜文昌阁。为道既贞信，处名犹謇谔。铁柱励风威，锦轴含光辉。夜闻持简立，朝看伏奏归。洞门清佩响，广路玉珂飞。骧首入丹掖，抟空趋太微。丝纶逢圣主，出入飘华组。愔愔宿帝梧，侃侃居文府。海内语三独，朝端谋六户。善计在弘羊，清严归仲举。侍郎跨方朔，中丞蔑周处。天眷择循良，惟贤降宠章。分符指聊摄，为政本农桑。籍籍歌五袴，祁祁颂千箱。随车微雨洒，逐扇清风飏。既以迁列国，复兹邻帝乡。褰帷乃仍旧，坐啸非更张。居敬物无扰，履端人自康。薄游出', '自出宾上国，乃到邹人乡。曽点与曽子，俱升阙里堂。武皇恢大略，逸翮思寥廓。三居清宪台，两拜文昌阁。为道既贞信，处名犹謇谔。铁柱励风威，锦轴含光辉。夜闻持简立，朝看伏奏归。洞门清佩响，广路玉珂飞。骧首入丹掖，抟空趋太微。丝纶逢圣主，出入飘华组。愔愔宿帝梧，侃侃居文府。海内语三独，朝端谋六户。善计在弘羊，清严归仲举。侍郎跨方朔，中丞蔑周处。天眷择循良，惟贤降宠章。分符指聊摄，为政本农桑。籍籍歌五袴，祁祁颂千箱。随车微雨洒，逐扇清风飏。既以迁列国，复兹邻帝乡。褰帷乃仍旧，坐啸非更张。居敬物无扰，履端人自康。薄游出', '', '阙如', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (597, '3df6ab41544045b09f1b90c2988a4ea3', '季冬自嵩山赴洛道中作', 105, 72, 764, '764年', '皇运偶中变，长蛇食中土。天盖西北倾，众星陨如雨。胡尘动地起，千里闻战鼓。死人成为阜，流血涂草莽。策马何纷纷，捐躯抗豺虎。甘心赴国难，谁谓荼叶苦。天子初受命，省方造区宇。斩鲸安溟波，截鳌作天柱。三微复正统，五玉归文祖。不图汉官仪，今日忽再睹。升高望京邑，佳气连海浦。宝鼎歊景云，明堂舞干羽。虎臣□激昂，□□□御侮。腐儒著缝掖，何处议邹鲁。西上轘辕山，丘陵横今古。和气蒸万物，腊月春霭吐。得为太平人，穷达不足数。他日遇封禅，著书继三五。', '皇运偶中变，长蛇食中土。天盖西北倾，众星陨如雨。胡尘动地起，千里闻战鼓。死人成为阜，流血涂草莽。策马何纷纷，捐躯抗豺虎。甘心赴国难，谁谓荼叶苦。天子初受命，省方造区宇。斩鲸安溟波，截鳌作天柱。三微复正统，五玉归文祖。不图汉官仪，今日忽再睹。升高望京邑，佳气连海浦。宝鼎歊景云，明堂舞干羽。虎臣□激昂，□□□御侮。腐儒著缝掖，何处议邹鲁。西上轘辕山，丘陵横今古。和气蒸万物，腊月春霭吐。得为太平人，穷达不足数。他日遇封禅，著书继三五。', '此诗大致作于广德二年（公元764）冬，代宗诏征独孤及为左拾遗，于洪州入京途中。安史之乱后，社稷濒临倾覆。满目疮痍的现实激起了诗人的报国之志。诗人师法汉朝，乞灵古人，出入于两汉典籍，用《后汉书·光武帝纪》之语，“不图汉官仪，今日忽再睹”，呈现一片和平之音。诗人以文为诗，大笔挥毫，将捐躯报国的爱国情怀以散文笔法铺叙开来。', '此诗大致作于广德二年（公元764）冬，代宗诏征独孤及为左拾遗，于洪州入京途中。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (593, '1c684657e0664495a87c1825ee91c117', '颍阳别元丹丘之淮阳', 5, 72, 738, '公元738年', '吾将元夫子，异姓为天伦。本无轩裳契，素以烟霞亲。尝恨迫世网，铭意俱未伸。松柏虽寒苦，羞逐桃李春。悠悠市朝间，玉颜日缁磷。所失重山岳，所得轻埃尘。精魄渐芜秽，衰老相凭因。我有锦囊诀，可以持君身。当餐黄金药，去为紫阳宾。万事难并立，百年犹崇晨。别尔东南去，悠悠多悲辛。前志庶不易，远途期所遵。已矣归去来，白云飞天津。', '吾将元夫子，异姓为天伦。本无轩裳契，素以烟霞亲。尝恨迫世网，铭意俱未伸。松柏虽寒苦，羞逐桃李春。悠悠市朝间，玉颜日缁磷。所失重山岳，所得轻埃尘。精魄渐芜秽，衰老相凭因。我有锦囊诀，可以持君身。当餐黄金药，去为紫阳宾。万事难并立，百年犹崇晨。别尔东南去，悠悠多悲辛。前志庶不易，远途期所遵。已矣归去来，白云飞天津。', '开元二十六年（公元738年），李白至嵩山元丹丘处，居留有日，然后东南游陈州，有诗别元丹丘。纵观李白一生“五岳寻仙不辞远，一生好入名山游”。蜀中道风繁盛，一些著名的“道门龙凤”，多在幼年即入道，受其影响，李白“弱龄”时就结交了著名道士元丹丘，与其共同修道，长期游历“吾与霞子元丹，烟子元演，气澈道合，结神仙交，殊身同心，警志云海，不可夺也”。但李白从来不是甘心平庸之辈，他走隐逸访道求官的终南捷径，最终目的就是以布衣之身直抵卿相。他向往松柏无惧凌寒，不追附桃李芳菲。但得失之心又隐约显露笔端，所失去的东西重于山岳', '开元二十六年（公元738年），李白至嵩山元丹丘处，居留有日，然后东南游陈州，有诗别元丹丘。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (590, '85f8bb5a758d4fa2b932f1e83a680594', '寒食汜上作', 244, 71, 726, '726年', '广武城边逢暮春，汶阳归客泪沾巾。落花寂寂啼山鸟，杨柳青青渡水人。', '广武城边逢暮春，汶阳归客泪沾巾。落花寂寂啼山鸟，杨柳青青渡水人。', '开元十四年（公元726年），四年期满后，王维结束了济州司仓参军的任职，启程归京，准备待命新职。诗人走水路，船至广武城时，写下了这首诗。发端两句直叙归途情景，然后移情入景。行至广武城边时，为什么“泪沾巾”？是广武城勾起了诗人往昔的回忆？还是恰逢暮春撩动了文人伤春惜春的情思？亦或是归客的身份让诗人近乡情怯？诗人写得扑朔迷离。这种写法是倒装结构，答案往往在下文揭露。后两句是开篇两句的对照。暮春百花凋零，寂寂的落花，仿佛现在的自己，难以主宰自己的命运。本来是鸟鸣，诗人却用“啼”字落笔，哀婉凄切的嘶鸣一寸寸浸淫着诗', '开元十四年（公元726年），四年期满后，王维结束了济州司仓参军的任职，启程归京，准备待命新职。诗人走水路，船至广武城时，写下了这首诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (591, '5dc172b80e3f40518ddfa62fee8f9299', '早入荥阳界', 244, 47, 721, '公元721年', '泛舟入荥泽，兹邑乃雄藩。河曲闾阎隘，川中烟火繁。因人见风俗，入境闻方言。秋野田畴盛，朝光市井喧。渔商波上客，鸡犬岸旁村。前路白云外，孤帆安可论。', '泛舟入荥泽，兹邑乃雄藩。河曲闾阎隘，川中烟火繁。因人见风俗，入境闻方言。秋野田畴盛，朝光市井喧。渔商波上客，鸡犬岸旁村。前路白云外，孤帆安可论。', '开元九年（公元721年）王维从武牢出发，坐船经荥阳东北的敖仓口入荥泽，遂作此诗。诗人驾一叶轻舟泊入荥泽。惊叹于此地的繁华烟火，心中愁绪一扫而光。起笔将荥阳的地理、形势、风俗及郊野风光娓娓道来，如一帧帧画面在眼前渐次播放，井然有序，更显得意态从容。贩夫走卒的叫卖声穿檐过甍，裹挟着浓重的乡音，散发出浓郁的生活气息。田间庄稼黄熟，村舍鸡犬相闻，声光满纸，让人流连。', '开元九年（公元721年）王维从武牢出发，坐船经荥阳东北的敖仓口入荥泽，遂作此诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (592, '83c304abe27045129252961681ee3413', '留别山中温古上人兄并示舍弟缙', 244, 72, 735, '公元735年', '解薛登天朝，去师偶时哲。岂惟山中人，兼负松上月。宿昔同游止，致身云霞末。开轩临颍阳，卧视飞鸟没。好依盘石饭，屡对瀑泉渴。理齐小狎隐，道胜宁外物。舍弟官崇高，宗兄此削发。荆扉但洒扫，乘闲当过歇。', '解薛登天朝，去师偶时哲。岂惟山中人，兼负松上月。宿昔同游止，致身云霞末。开轩临颍阳，卧视飞鸟没。好依盘石饭，屡对瀑泉渴。理齐小狎隐，道胜宁外物。舍弟官崇高，宗兄此削发。荆扉但洒扫，乘闲当过歇。', '开元二十三年（公元735年），诗人离别嵩山赴东都洛阳就任右拾遗，作此诗留赠诸友人。纵观王维早期，其政治态度和居于思想核心地位的当属儒家修齐治平的兼济天下之愿，积极的出世之心昭昭可见。', '开元二十三年（公元735年），诗人离别嵩山赴东都洛阳就任右拾遗，作此诗留赠诸友人。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (588, 'b24d4fb61db34832a60342c1bdd72d65', '归嵩山作', 244, 72, 734, '734年', '清川带长薄，车马去闲闲。流水如有意，暮禽相与还。荒城临古渡，落日满秋山。迢递嵩高下，归来且闭关。', '清川带长薄，车马去闲闲。流水如有意，暮禽相与还。荒城临古渡，落日满秋山。迢递嵩高下，归来且闭关。', '开元二十二年（公元734年）秋，王维隐居嵩山，有诗。首句紧扣题目“归”字。清澈的川流环绕着一片草木丛生的青泽地。离归的车马徐徐前进，暗合诗人归山临发时安详闲适的心境。“流水”承“清川”，“飞还”接“长簿”，此二句由“车马去闲闲”直接发展而来。流水蕴含一去不复返之意，表达自己坚决归隐的态度；暮禽包孕鸟倦飞而知还之意，表露自己失望退隐的原因。荒城、古渡、落日、秋山，构成了一幅野傍秋景图。荒凉的城池临靠着古老的渡口，落日的余晖洒满了萧飒的秋山。愈靠近归隐地，心境就愈发凄凉。尾二句照应题目，当是结构的回环呼应，点', '开元二十二年（公元734年）秋，王维隐居嵩山，有诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (589, 'be164367547a4293b896b34b3f4fbda1', '宿郑州', 244, 48, 721, '721年', '朝与周人辞，暮投郑人宿。他乡绝俦侣，孤客亲僮仆。宛洛望不见，秋霖晦平陆。田父草际归，村童雨中牧。主人东皋上，时稼绕茅屋。虫思机杼悲，雀喧禾黍熟。明当渡京水，昨晚犹金谷。此去欲何言，穷边徇微禄。', '朝与周人辞，暮投郑人宿。他乡绝俦侣，孤客亲僮仆。宛洛望不见，秋霖晦平陆。田父草际归，村童雨中牧。主人东皋上，时稼绕茅屋。虫思机杼悲，雀喧禾黍熟。明当渡京水，昨晚犹金谷。此去欲何言，穷边徇微禄。', '开元九年（公元721年）赴济州途中，路过郑州所写。后人评此诗：“闲雅幽寂，有彭泽遗韵”（《汇编唐诗十集》）。发端两句紧扣题意，将跋山涉水的奔波疲惫都不露声色地连缀在数十字中，一笔带过。异乡为客，看到他人童仆亲睦难免涌上一丝艳羡。诗人用一场淋漓不尽的秋雨浇灭了心头的不平意。南阳、洛阳在视线中已逐渐模糊，空阔辽远的原野笼罩在霏霏淫雨、蒙蒙烟气之中。田父荷锄踏青而归，伴以牧童声声短笛。悲鸣的秋虫、摇动的机杼、喧嚣的雀鸟，都沾染了诗人的深衷密绪。尾二句的“明”和“晚”都是开端“朝”和“暮”的回环映带，既体现出感情', '开元九年（公元721年）赴济州途中，路过郑州所写。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (585, 'd339e58a61194da6a3fe436696805d68', '使至嵩山寻杜四不遇慨然复伤田洗马韩观主…赠杜侯杜四', 26, 72, -1, '年代不详', '洛桥瞻太室，期子在云烟。归来不相见，孤赏弄寒泉。与君阔松石，于兹二十年。田公谢昭世，韩子秘幽埏。忆昔同携手，山栖接二贤。笙歌入玄地，诗酒坐寥天。旧友悉零落，罢琴私自怜。逝者非药误，餐霞意可全。为余理还策，相与事灵仙。', '洛桥瞻太室，期子在云烟。归来不相见，孤赏弄寒泉。与君阔松石，于兹二十年。田公谢昭世，韩子秘幽埏。忆昔同携手，山栖接二贤。笙歌入玄地，诗酒坐寥天。旧友悉零落，罢琴私自怜。逝者非药误，餐霞意可全。为余理还策，相与事灵仙。', '', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (586, '829baca204cf43629bac7d34bd1f9833', '谒焦錬师', 4, 72, 713, '713年', '中峰青苔碧，一点云生时。岂意石堂里，得逢焦錬师。炉香净琴案，松影闲摇墀。拜受长年药，翩翻西海期。', '中峰青苔碧，一点云生时。岂意石堂里，得逢焦錬师。炉香净琴案，松影闲摇墀。拜受长年药，翩翻西海期。', '开元元年（公元713年），诗人于嵩山访道求仙。王昌龄《赵十四兄见谢》：“晚来常读易，顷者欲还嵩。”', '开元元年（公元713年），诗人于嵩山访道求仙。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (587, '92a27b23272b4796b4a125a45cc61a17', '就道士问<周易参同契>', 4, 72, 714, '714年', '仙人骑白鹿，发短耳何长。时余采菖蒲，忽见嵩之阳。稽首求丹经，乃出怀中方。披读了不误，归来问嵇康。嗟余无道骨，发我入太行。', '仙人骑白鹿，发短耳何长。时余采菖蒲，忽见嵩之阳。稽首求丹经，乃出怀中方。披读了不误，归来问嵇康。嗟余无道骨，发我入太行。', '开元二年（公元714年），王昌龄欲北上太行（《悲哉行》）、游猎邯郸，离嵩山前夕所作。诗人正于嵩山之阳采菖蒲，忽然看见骑着白鹿、垂着长耳的仙人。和《谒焦錬师》一样，诗人每次访求道人几乎都是不期而遇，平添了道人浮踪萍影的神秘感和得遇仙人的幸运。既已遇到仙人，诗人便抓住机会求访丹经，无奈却“披读了不误”，拿回丹经也只能询问高士指点。诗人虽无道骨，也急切地想要如仙人般长生不老。全诗通过遇仙、求仙、未悟、归问等描写，表达了诗人对道教的崇尚和对生命永恒的追求。', '开元二年（公元714年），王昌龄欲北上太行（《悲哉行》）、游猎邯郸，离嵩山前夕所作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (584, '5993c6dad0d24b78b7dc7a4e8ebe17c2', '嵩南九里旧鹊村作', 26, 72, 699, '699年', '弊庐接箕颖，綵翠横天衢。家世事灵岳，嵒栖安敢渝。从俗因迹化，归静知心愚。上违先人训，下怜菲薄躯。自问何功业，谬与贤俊俱。执羁翊龙羽，秉笔游鸿都。尸禄负诸己，日使田园芜。常恐白云意，溘尽黄埃涂。妙年负恩德，欲去何踟蹰？明主本尚道，黄屋均蓬壶。饮惠可冥分，归事烧金鑪。', '弊庐接箕颖，綵翠横天衢。家世事灵岳，嵒栖安敢渝。从俗因迹化，归静知心愚。上违先人训，下怜菲薄躯。自问何功业，谬与贤俊俱。执羁翊龙羽，秉笔游鸿都。尸禄负诸己，日使田园芜。常恐白云意，溘尽黄埃涂。妙年负恩德，欲去何踟蹰？明主本尚道，黄屋均蓬壶。饮惠可冥分，归事烧金鑪。', '圣历二年（公元699）年十二月，宋之问出使嵩山，过嵩山旧宅，有诗言志。“儒道两相妨，达兼济天下，穷独善其身”，这是传统知识分子的初衷。诗中着重表现了出世与入世相搏的矛盾心理。诗人虽有归田之志的自述：“尸禄负诸己，日使田园芜”，但只是怀才不遇的牢骚与激愤。根本上仍是俟机而出，并未真正抛弃宫廷荣华而遁隐。', '圣历二年（公元699）年十二月，宋之问出使嵩山，过嵩山旧宅，有诗言志。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (583, '62eb56bf1d1b4c979fca2092c4860b43', '扈从登封途中作', 26, 72, 695, '公元695年', '帐殿郁崔嵬，仙游实壮哉。晓云连幕捲，夜火杂星回。谷暗千骑出，山鸣万乘来。扈从良可赋，终乏掞天才。', '帐殿郁崔嵬，仙游实壮哉。晓云连幕捲，夜火杂星回。谷暗千骑出，山鸣万乘来。扈从良可赋，终乏掞天才。', '公元695年，宋之问奉使嵩山，至崖口，有诗。此诗描述了武则天登嵩山时的盛大场面。帐幔锦帷在巍峨的嵩山上层层铺叠，放佛神仙驾游云端，盛况非凡。清晨登临时，云霞与绣帐一同舒卷，如霓虹闪耀；夜晚下山时，灯火与星光融汇交织，似银河回转。旌旗招展，遮天蔽日，山谷也显得幽暗；天子乘辇清跸而来，军队欢呼声响谒行云。诗的首联用一个“壮”自概括登山声势之浩大；诗腰两联具体描写登山的浩大场面，笔法生动；尾联诗人自谦才疏学浅，言皇恩浩荡，结构完整。全诗寥寥四十字，即事即景，对仗工巧，锦绣成文，壮阔沉雄，气象宏大。', '公元695年，宋之问奉使嵩山，至崖口，有诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (582, 'd2488c1725de42558288cfb30ee778f1', '入崖口五渡寄李适', 26, 72, -1, '阙如', '抱琴登绝壑，伐木溯清川。路极意谓尽，势回趣转绵。入远草木秀，山深云景鲜。余负海峤情，自昔微尚然。弥旷十馀载，今来宛仍前。未窥仙源极，独进野人船。时攀乳窦憩，屡薄天窗眠。夜弦响松月，朝楫弄苔泉。因冥象外理，永谢区中缘。碧潭可遗老，丹砂堪学仙。莫使驰光暮，空令归鹤怜。', '抱琴登绝壑，伐木溯清川。路极意谓尽，势回趣转绵。入远草木秀，山深云景鲜。余负海峤情，自昔微尚然。弥旷十馀载，今来宛仍前。未窥仙源极，独进野人船。时攀乳窦憩，屡薄天窗眠。夜弦响松月，朝楫弄苔泉。因冥象外理，永谢区中缘。碧潭可遗老，丹砂堪学仙。莫使驰光暮，空令归鹤怜。', '阙如', '阙如', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (581, 'f4fd463968e54bfb8a520fde88025288', '初至崖口', 26, 72, 695, '公元695年', '崖口众山断，嵚崟耸天壁。气冲落日红，影入春潭碧。锦缋织苔藓，丹青画松石。水禽泛容与，岩花飞的砾。微路从此深，我来限于役。惆怅情未已，群峰暗将夕。', '崖口众山断，嵚崟耸天壁。气冲落日红，影入春潭碧。锦缋织苔藓，丹青画松石。水禽泛容与，岩花飞的砾。微路从此深，我来限于役。惆怅情未已，群峰暗将夕。', '公元695年，宋之问奉使嵩山，至崖口，有诗。诗人初至崖口，惊叹于大自然的鬼斧神工，用大量笔墨敷衍，赞美峥嵘好景。在此首诗篇中，风景占据了主要的篇幅，而诗人的反应则是简短的、抒情的。从刀削斧刻的崖口众山、嵚崟高耸的连天绝壁，到彤红的落日、澄碧的春潭，再到潮湿的苔藓、清幽的松石，视野由远及近，浑然而成地将诸多景物熔铸于笔端。诗中的视觉形象相对统一，更接近于盛唐纯熟的风景处理的手法。诗人截取了一个特定的时刻—夜色渐浓的崖口，将“时不可兮骤得，聊逍遥兮容与”的自得自适与行役的劳苦困顿相对立，惆怅的思绪浸淫着诗人的', '公元695年，宋之问奉使嵩山，至崖口，有诗。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (580, 'fa4e074f6d364c3aa11b01f96b9bc44c', '朝野佥载之饮酒令', 243, 43, -1, '年代不详', '龙朔年间饮酒令云：“子母相去离，连台拗倒。”永昌中宿卫十余人于清化坊饮，为此令。十人皆弃市。后庐陵徙均州、则天被废、诸武迁放，皆应之矣。', '龙朔年间饮酒令云：“子母相去离，连台拗倒。”永昌中宿卫十余人于清化坊饮，为此令。十人皆弃市。后庐陵徙均州、则天被废、诸武迁放，皆应之矣。', '无', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (577, '60867307662e4f519fbe77df26174338', '和三乡诗', 241, 35, 843, '843年', '女几山前岚气低，佳人留恨此中题。不知云雨归何处，空使王孙见即迷。', '女几山前岚气低，佳人留恨此中题。不知云雨归何处，空使王孙见即迷。', '无', '和若耶女三乡题壁', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (578, '949ba54e4b9e4701a3e3a3a5cb175296', '和三乡诗会昌时有女子题诗三乡驿和者十人', 242, 35, 844, '844年', '惆怅残花怨暮春，孤鸾舞镜倍伤神。清词好个干人事，疑是文姬第二身。', '惆怅残花怨暮春，孤鸾舞镜倍伤神。清词好个干人事，疑是文姬第二身。', '无', '和若耶女三乡题壁', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (579, '9a19d5f5bf0c465a84021fff4b965468', '朝野佥载之江融', 243, 43, -1, '年代不详', '唐左使江融，耿介正直。扬州徐敬业反，被罗织。酷吏周兴等枉奏杀之，斩于东都都亭驿前。融将被诛，请奏事引见，兴曰：“囚何得奏事？”融怒叱之曰：“吾无罪枉戮，死不舍汝。”遂斩之，尸乃激扬而起，蹭蹬十余步，行刑者踏倒。还起坐，如此者三，乃绝。虽断其头，似怒不息，无何周兴死。', '唐左使江融，耿介正直。扬州徐敬业反，被罗织。酷吏周兴等枉奏杀之，斩于东都都亭驿前。融将被诛，请奏事引见，兴曰：“囚何得奏事？”融怒叱之曰：“吾无罪枉戮，死不舍汝。”遂斩之，尸乃激扬而起，蹭蹬十余步，行刑者踏倒。还起坐，如此者三，乃绝。虽断其头，似怒不息，无何周兴死。', '无', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (574, '1c5824b79e614810b0a9f8ab83020878', '和三乡诗', 238, 35, 843, '843年', '无姓无名越水滨，芳词空怨路傍人。莫教才子偏惆怅，宋玉东家是旧邻。', '无姓无名越水滨，芳词空怨路傍人。莫教才子偏惆怅，宋玉东家是旧邻。', '无', '和若耶女三乡题壁', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (575, '3c928133714743b3a9b4387a7ca44903', '和三乡诗', 239, 35, 843, '843年', '红粉萧娘手自题，分明幽怨发云闺。不应更学文君去，泣向残花归剡溪。', '红粉萧娘手自题，分明幽怨发云闺。不应更学文君去，泣向残花归剡溪。', '无', '和若耶女三乡题壁', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (576, '72d1160cad724d34bfeccb797edc5dff', '和三乡诗', 240, 35, 842, '842年', '来时欢笑去时哀，家国迢迢向越台。待写百年幽思尽，故宫流水莫相催。', '来时欢笑去时哀，家国迢迢向越台。待写百年幽思尽，故宫流水莫相催。', '无', '和若耶女三乡题壁', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (571, 'af6e1de76b3540379e0854d38a30603c', '和三乡诗', 235, 35, 842, '842年', '会稽王谢两风流，王子沉沦谢女愁。归思若随文字在，路傍空为感千秋。', '会稽王谢两风流，王子沉沦谢女愁。归思若随文字在，路傍空为感千秋。', '无', '和若耶女三乡题壁', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (572, '176f71853a844fffaa9fe9ae2a6bc022', '和三乡诗', 236, 35, 842, '842年', '南北千山与万山，轩车谁不思乡关。独留芳翰悲前迹，陌上恐伤桃李颜。', '南北千山与万山，轩车谁不思乡关。独留芳翰悲前迹，陌上恐伤桃李颜。', '无', '和若耶女三乡题壁', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (573, '3c753df900dd47d0a7be8302d54bec1d', '和三乡诗', 237, 35, 842, '842年', '浣纱游女出关东，旧迹新词一梦中。槐陌柳亭何限事，年年回首向春风。', '浣纱游女出关东，旧迹新词一梦中。槐陌柳亭何限事，年年回首向春风。', '无', '和若耶女三乡题壁', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (566, 'fa42a3a16e1a41e5866b5fc159d82af8', '送邱儒生序', 231, 66, 828, '828年', '', '', '无', '公元828年年，三月左右，送邱生赴京，作《送邱儒生序》。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (567, '33334a2950d540e09aac212e96e46c10', '投秘书监韦尚书启', 64, 66, 867, '867年', '', '', '无', '公元867年，夏秋之际，赴东都洛阳干谒、取解，先以《谗书》行卷检校户部尚书、秘书监韦澳，随后作《投秘监韦尚书启》，《启》中提及自己本年已考了六次。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (568, '204999e4cabc46308e289569a6fc433d', '题三乡诗', 232, 35, 842, '842年', '昔逐良人西入关，良人身殁妾空还。谢娘卫女不相待，为雨为云归此山。', '昔逐良人西入关，良人身殁妾空还。谢娘卫女不相待，为雨为云归此山。', '无', '本若耶女，远嫁长安，后其夫殁，为家人不容，会昌间遂返故乡过三乡题壁。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (569, '54e4786f3e6f4e4f9177f40f3bbf8f6c', '和三乡诗', 233, 35, 842, '842年', '洛川依旧好风光，莲帐无因见女郎。云雨散来音信断，此生遗恨寄三乡。', '洛川依旧好风光，莲帐无因见女郎。云雨散来音信断，此生遗恨寄三乡。', '无', '和若耶女三乡题壁', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (570, '4c9481239f32472e827bc9a8a37b33ca', '和三乡诗', 234, 35, 842, '842年', '兰蕙芬香见玉姿，路傍花笑景迟迟。苎萝山下无穷意，并在三乡惜别时。', '兰蕙芬香见玉姿，路傍花笑景迟迟。苎萝山下无穷意，并在三乡惜别时。', '无', '和若耶女三乡题壁', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (565, '10476e98a3504c6a984a2d58e14b5b9c', '与卫中行书', 72, 66, 800, '800年', '大受足下：辱书，为赐甚大，然所称道过盛，岂所谓诱之而欲其至于是欤？不敢当，不敢当！其中择其一二近似者而窃取之，则于交友忠而不反于背面者，少似近焉。亦其心之所好耳，行之不倦，则未敢自谓能尔也。不敢当，不敢当！至于汲汲于富贵以救世为事者，皆圣贤之事业，知其智能谋力能任者也。如愈者又焉能之？始相识时，方甚贫，衣食于人；其后相见于汴、徐二州，仆皆为之从事，日月有所入，比之前时，丰约百倍，足下视吾饮食衣服，亦有异乎？然则仆之心，或不为此汲汲也，其所不忘于仕进者，亦将小行乎其志耳。此未易遽言也。凡祸福吉凶之来，似不在我。惟君子得祸为不幸，而小人得祸为恒；君子得福为恒，而小人得福为幸：以其所为似有以取之也。必曰“君子则吉，小人则凶”者，不可也。贤不肖存乎己，贵与贱、祸与福存乎天，名声之善恶存乎人。存乎己者，吾将勉之；存乎天、存乎人者，吾将任彼而不用吾力焉，其所守者，岂不约而易行哉！足下曰：“命之穷通，自我为之”，吾恐未合于道。足下征前世而言之，则知矣；若曰以道德为己任，穷通之来，不接吾心，则可也。穷居荒凉，草树茂密，出无驴马，因与人绝，一室之内，有以自娱。足下喜吾复脱祸乱，不当安安而居、迟迟而来也。', '大受足下：辱书，为赐甚大，然所称道过盛，岂所谓诱之而欲其至于是欤？不敢当，不敢当！其中择其一二近似者而窃取之，则于交友忠而不反于背面者，少似近焉。亦其心之所好耳，行之不倦，则未敢自谓能尔也。不敢当，不敢当！至于汲汲于富贵以救世为事者，皆圣贤之事业，知其智能谋力能任者也。如愈者又焉能之？始相识时，方甚贫，衣食于人；其后相见于汴、徐二州，仆皆为之从事，日月有所入，比之前时，丰约百倍，足下视吾饮食衣服，亦有异乎？然则仆之心，或不为此汲汲也，其所不忘于仕进者，亦将小行乎其志耳。此未易遽言也。凡祸福吉凶之来，似不在我。惟君子得祸为不幸，而小人得祸为恒；君子得福为恒，而小人得福为幸：以其所为似有以取之也。必曰“君子则吉，小人则凶”者，不可也。贤不肖存乎己，贵与贱、祸与福存乎天，名声之善恶存乎人。存乎己者，吾将勉之；存乎天、存乎人者，吾将任彼而不用吾力焉，其所守者，岂不约而易行哉！足下曰：“命之穷通，自我为之”，吾恐未合于道。足下征前世而言之，则知矣；若曰以道德为己任，穷通之来，不接吾心，则可也。穷居荒凉，草树茂密，出无驴马，因与人绝，一室之内，有以自娱。足下喜吾复脱祸乱，不当安安而居、迟迟而来也。', '无', '公元800年，五月后，韩愈脱徐州之难，卫中行有书贺之。韩愈抵洛阳后，作书答之，言穷居之状。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (564, '3e95f09eb5f04739aa08a496b724d5e6', '东都留守谢上表', 99, 66, 813, '813年', '臣某言：伏奉今月三日制命，授臣检校吏部尚书兼御史大夫充东都留守判东都尚书省事，以今月二十四日到东都上讫。荣渥特加，宠光备至，俯仰印绶，惭惧交怀。臣某中谢。臣才不出众，禄期代耕，因缘文字，进越名级。自圣明临御，特沐鸿私，历五曹之贰，居九卿之长。遂叨大任，忽涉四年，智虑孱愚，言辞朴讷，徒淹岁月，莫展涓埃。虽竭匪躬之诚，竟乖陈力之效，自免枢务，犹忝春官，才经半岁，复授今任。前后所历，皆不因人，无非睿奖，以至崇大。况留守所寄，东夏式瞻，在君陈毕公之选，兼天官府宪之重。面辞之日，亲奉圣谟，式遏匪人，整训戎事，用资窃发，必资兼材，岂臣懦薄，所宜负荷？今兵力至微，武备不足，此皆天鉴所察，臣不敢累黩聪明。社其奸情，亦在早计，必冀磨砺铅钝，罄竭肝心。忠心事君，死而后巳。违离宸眷，感恋阙庭，以陛下注意之恩，誓微臣捐躯之节。无任荷戴屏营之至！谨差押衙试殿中监成党奉表以闻。臣某诚惶诚恐顿首顿首。谨言。', '臣某言：伏奉今月三日制命，授臣检校吏部尚书兼御史大夫充东都留守判东都尚书省事，以今月二十四日到东都上讫。荣渥特加，宠光备至，俯仰印绶，惭惧交怀。臣某中谢。臣才不出众，禄期代耕，因缘文字，进越名级。自圣明临御，特沐鸿私，历五曹之贰，居九卿之长。遂叨大任，忽涉四年，智虑孱愚，言辞朴讷，徒淹岁月，莫展涓埃。虽竭匪躬之诚，竟乖陈力之效，自免枢务，犹忝春官，才经半岁，复授今任。前后所历，皆不因人，无非睿奖，以至崇大。况留守所寄，东夏式瞻，在君陈毕公之选，兼天官府宪之重。面辞之日，亲奉圣谟，式遏匪人，整训戎事，用资窃发，必资兼材，岂臣懦薄，所宜负荷？今兵力至微，武备不足，此皆天鉴所察，臣不敢累黩聪明。社其奸情，亦在早计，必冀磨砺铅钝，罄竭肝心。忠心事君，死而后巳。违离宸眷，感恋阙庭，以陛下注意之恩，誓微臣捐躯之节。无任荷戴屏营之至！谨差押衙试殿中监成党奉表以闻。臣某诚惶诚恐顿首顿首。谨言。', '无', '公元813年七月三日，制命检校吏部尚书、兼御史大夫、充东都留守，判东都尚书省事。七月二十四日，抵东都，作谢表。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (563, '22473b1f094f45568082a03ea26cbdb3', '祭杨盈川文', 26, 66, 694, '694年', '维大周某年月日，西河宋某，谨以清酌脯羞之奠，敬祭于杨子之灵曰:自古皆死，不朽者文!北河流液，西岳吐云，叶神通契，降精于君。伏道孔门，游刃诸子，精微博识，黄中通理。属词比事，宗经匠史，玉璞金浑，风摇云起。闻人之善，若在诸已;受人之恩，许之以死。惟子坚刚，气陵秋霜，行不苟合，言不苟忘。大君有命，征子文房，馀亦叨忝，随君颉颃。同趋北禁，并拜东堂，志事俱得，形骸两忘。载罹寒暑，贫病洛阳，裘马同弊，老幼均粮。自君出宰，南浮江海，馀尝苦饥，今日犹在。之子妙年，香名早传，从来金马，夙昔崇贤。门庭若市，翰墨如泉，千载之后，闻而凛然。死而不亡，问馀何伤?伤予命薄，益友零落。生平之言，幽显相托，痛君不嗣，匪我孤诺。君有兄弟，同心异体，陟冈增哀，归葬以礼。旅榇飘零，于洛之汀，我之怀矣，感叹入冥。见子之弟，类子之形，悼往心绝，慰存涕盈。古人有言，一死一生，昔子往矣，追送倾城，今子来也，乃知交情。惟郭是戚，有崔不易，来哭来祭，哀文在席。帷席可依，冰雪四满，家人哀哀，宾径微断。今我伤悲，情勤昔时，子文子翰，我缄我持，子宅子兆，我营我思。子有神鉴，我言不欺;我有絮酒，子其歆之。我亦引满，傥昭神期，魂兮归来，闻馀此词。折叠', '维大周某年月日，西河宋某，谨以清酌脯羞之奠，敬祭于杨子之灵曰:自古皆死，不朽者文!北河流液，西岳吐云，叶神通契，降精于君。伏道孔门，游刃诸子，精微博识，黄中通理。属词比事，宗经匠史，玉璞金浑，风摇云起。闻人之善，若在诸已;受人之恩，许之以死。惟子坚刚，气陵秋霜，行不苟合，言不苟忘。大君有命，征子文房，馀亦叨忝，随君颉颃。同趋北禁，并拜东堂，志事俱得，形骸两忘。载罹寒暑，贫病洛阳，裘马同弊，老幼均粮。自君出宰，南浮江海，馀尝苦饥，今日犹在。之子妙年，香名早传，从来金马，夙昔崇贤。门庭若市，翰墨如泉，千载之后，闻而凛然。死而不亡，问馀何伤?伤予命薄，益友零落。生平之言，幽显相托，痛君不嗣，匪我孤诺。君有兄弟，同心异体，陟冈增哀，归葬以礼。旅榇飘零，于洛之汀，我之怀矣，感叹入冥。见子之弟，类子之形，悼往心绝，慰存涕盈。古人有言，一死一生，昔子往矣，追送倾城，今子来也，乃知交情。惟郭是戚，有崔不易，来哭来祭，哀文在席。帷席可依，冰雪四满，家人哀哀，宾径微断。今我伤悲，情勤昔时，子文子翰，我缄我持，子宅子兆，我营我思。子有神鉴，我言不欺;我有絮酒，子其歆之。我亦引满，傥昭神期，魂兮归来，闻馀此词。<br><br>折叠', '无', '公元694年冬，杨炯卒盈川任，其弟扶榇归葬，经洛阳，宋作文哭祭。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (562, 'a481d68baca14b98b8b59e1ad1e2e86a', '祭远祖当阳君文', 91, 66, 741, '741年', '维开元二十九年岁次辛巳月日，十三叶孙甫，谨以寒食之奠，敢昭告于先祖晋驸马都尉镇南大将军当阳成侯之灵:初陶唐出自伊祁，圣人之后，世食旧德。降及武库，应乎虬精。恭闻渊深，罕得窥测，勇功是立，智名克彰。缮甲江陵，祲清东吴，建侯于荆，邦于南土。河水活活，造舟为梁。洪涛，莽汜，未始腾毒，《春秋》主解，膏隶躬亲。呜呼笔迹，流宕何人?苍苍孤坟，独出高顶，静思骨肉，悲愤心胸。峻极于天，神有所降，不毛之地，俭乃孔昭。取象邢山，全模祭仲，多藏之诫，焯序前文。小子筑室，首阳之下，不敢忘本，不敢违仁。庶刻丰石，树此大道，论次昭穆，载扬显号。于以采蘩，于彼中园，谁其尸之?有斋列孙。呜呼!敢告兹辰，以永薄祭。尚飨!', '维开元二十九年岁次辛巳月日，十三叶孙甫，谨以寒食之奠，敢昭告于先祖晋驸马都尉镇南大将军当阳成侯之灵:初陶唐出自伊祁，圣人之后，世食旧德。降及武库，应乎虬精。恭闻渊深，罕得窥测，勇功是立，智名克彰。缮甲江陵，祲清东吴，建侯于荆，邦于南土。河水活活，造舟为梁。洪涛，莽汜，未始腾毒，《春秋》主解，膏隶躬亲。呜呼笔迹，流宕何人?苍苍孤坟，独出高顶，静思骨肉，悲愤心胸。峻极于天，神有所降，不毛之地，俭乃孔昭。取象邢山，全模祭仲，多藏之诫，焯序前文。小子筑室，首阳之下，不敢忘本，不敢违仁。庶刻丰石，树此大道，论次昭穆，载扬显号。于以采蘩，于彼中园，谁其尸之?有斋列孙。呜呼!敢告兹辰，以永薄祭。尚飨!', '无', '公元741年4月4日，从齐赵归洛阳，寒食日祭远祖杜预，为此告飨之词。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (561, '6e6d6e402b2740898419d183aedae468', '秋晚入洛于毕公宅别道王宴序', 230, 66, 672, '公元672年', '下官才不旷俗，宠不动时，充帝王之万姓，预乾坤之一物。早师周礼，偶爱儒宗；晚读老庄，动谐真性。进非干物，自疏朝市之机；退不邀荣，谁识王侯之贵?散琴樽於北阜，喜耕凿於东陂。野老披荷，暂辞幽涧；山人卖药，忽至神州。惊帝室之威灵，伟皇居之壮丽。朝游魏阙，见轩冕於南宫；暮宿灵台，闻弦歌於北里。交情独放，已厌人间；野性时违，少留都下。道王以天孙之重，分曲阜之新基；毕公以帝室之华，拥平阳之旧馆。迹尘钟鼎，思在江湖。居荣命於中朝，接风期於下士。绿滕朱绂，且混以萝裳;列榭崇轩，坐均於蓬户。宾主由其莫辨，语默於是同归。终大王之乐善，备将军之揖客。是日也?繁雨骤，气爽风驰。高秋九月，王畿千里。高扃向术，似元礼之龙门；甲第临衢，有当时之驿骑。英王入座，牢醴还陈；高士临筵，樵苏不爨。是非双遣，自然天地之间;荣贱两忘，何必山林之下?元谈清论，泉石纵横；雄笔壮词，烟霞照灼。既而神驰象外，宴洽寰中。白露下而南亭虚，苍烟生而北林晚。ジス始望，不及牲牢;麋鹿长怀，非忘林薮。先生负局，倦城市之尘埃;游子横琴，忆汀洲之杜若。况乎迹不皆遂，时不再来?属宸驾之方旋，值群公之毕从。洛城风景，此会无期；戚里笙竽，浮易尽仰?霞而道意，舍尘事而论心。夏仲御之浮舟，愿乘春水；张季鹰之命驾，思动秋风。策藜杖而非遥，整柴车之有日。青溪数曲，幽人长往;白?万里，帝乡难见。安贞抱朴，已甘心於下走；全忠履道，是所望於群公；倘心迹克谐，去留咸遂。庙堂多暇，返身沧海之隅；轩冕所辞，回首箕山之路。寻赤松而见及，泛黄菊以相从。虽源水桃花，时时失路；而幽山桂树，往往逢人。庶公子之来游，幸王孙之毕至。茅君待客，自有金坛；王烈迎宾，还开石架。维恐一邱风月，侣山水而忘年；三径蓬蒿，待公卿之来日。对光阴之易晚，惜?雾之难披。群公叶县凫飞，入朝廷而不出;下走辽州鹤去，谢城阙而依然。敢抒重襟，爰疏短引。式命离前之笔，希存别后之资。凡我故人，其辞云尔。', '下官才不旷俗，宠不动时，充帝王之万姓，预乾坤之一物。早师周礼，偶爱儒宗；晚读老庄，动谐真性。进非干物，自疏朝市之机；退不邀荣，谁识王侯之贵?散琴樽於北阜，喜耕凿於东陂。野老披荷，暂辞幽涧；山人卖药，忽至神州。惊帝室之威灵，伟皇居之壮丽。朝游魏阙，见轩冕於南宫；暮宿灵台，闻弦歌於北里。交情独放，已厌人间；野性时违，少留都下。<br><br>道王以天孙之重，分曲阜之新基；毕公以帝室之华，拥平阳之旧馆。迹尘钟鼎，思在江湖。居荣命於中朝，接风期於下士。绿滕朱绂，且混以萝裳;列榭崇轩，坐均於蓬户。宾主由其莫辨，语默於是同归。终大王之乐善，备将军之揖客。<br><br>是日也?繁雨骤，气爽风驰。高秋九月，王畿千里。高扃向术，似元礼之龙门；甲第临衢，有当时之驿骑。英王入座，牢醴还陈；高士临筵，樵苏不爨。是非双遣，自然天地之间;荣贱两忘，何必山林之下?元谈清论，泉石纵横；雄笔壮词，烟霞照灼。既而神驰象外，宴洽寰中。白露下而南亭虚，苍烟生而北林晚。ジス始望，不及牲牢;麋鹿长怀，非忘林薮。先生负局，倦城市之尘埃;游子横琴，忆汀洲之杜若。<br><br>况乎迹不皆遂，时不再来?属宸驾之方旋，值群公之毕从。洛城风景，此会无期；戚里笙竽，浮易尽仰?霞而道意，舍尘事而论心。夏仲御之浮舟，愿乘春水；张季鹰之命驾，思动秋风。策藜杖而非遥，整柴车之有日。青溪数曲，幽人长往;白?万里，帝乡难见。安贞抱朴，已甘心於下走；全忠履道，是所望於群公；倘心迹克谐，去留咸遂。庙堂多暇，返身沧海之隅；轩冕所辞，回首箕山之路。寻赤松而见及，泛黄菊以相从。虽源水桃花，时时失路；而幽山桂树，往往逢人。庶公子之来游，幸王孙之毕至。茅君待客，自有金坛；王烈迎宾，还开石架。维恐一邱风月，侣山水而忘年；三径蓬蒿，待公卿之来日。对光阴之易晚，惜?雾之难披。<br><br>群公叶县凫飞，入朝廷而不出;下走辽州鹤去，谢城阙而依然。敢抒重襟，爰疏短引。式命离前之笔，希存别后之资。凡我故人，其辞云尔。', '无', '公元672年，时高宗在洛阳.秋,曾到洛阳,至毕国公宅参预饯别道王李诱的宴席,有《秋晚入洛于毕公宅别道王宴序》。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (560, '4287fd8dd1c8459283f29b7138bc8ec8', '宿寿安山阴馆闻泉', 229, 40, -1, '阙如', '一支清急万山来，穿竹喧飞破石苔。梦在故乡临欲到，声闻孤枕却惊回。多愁鬓发余甘老，有限年光尔莫催。缘忆旧游相似处，月明山响子陵台。', '一支清急万山来，穿竹喧飞破石苔。梦在故乡临欲到，声闻孤枕却惊回。多愁鬓发余甘老，有限年光尔莫催。缘忆旧游相似处，月明山响子陵台。', '一支清泉穿山越石奔流而来 。激起的水花溅在沿壁横生的竹叶上，摇落喧哗一片。飞流激湍一路叫嚣着奔腾前进，击溃了石缝间潮软的青苔。梦中还乡，还未来得及细看，便倏然从睡梦中惊醒。白发攀鬓而上，诗人不禁流露出韶华易逝之感。幽思遥遥飘到子陵台。仿佛看到东汉严光躬耕富春、垂钓沧波的图景。反观自己，水阔风摇舟不定。莫不如从此偃帆收橹，渔樵山水，归隐之念骤生。', '阙如', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (558, '7feb8b3d736a418d9285e088b01f5945', '宿寿安甘棠馆', 228, 23, -1, '年代不详', '行人方倦役，到此似还乡。流水来关外，青山近洛阳。溪云归洞鹤，松月半轩霜。坐恐晨钟动，天涯道路长。', '行人方倦役，到此似还乡。流水来关外，青山近洛阳。溪云归洞鹤，松月半轩霜。坐恐晨钟动，天涯道路长。', '诗在六朝的发展下至唐代成熟而诸体均备，禅在各宗衍化下至唐代禅宗一花五叶。故累至晚唐，雅道大振，古风再作，诗僧大量涌现。尚颜虽信般若智慧，却也喜欢游历，雅景与墨香交织建构了其精神阁楼。流水青山、夕云洞鹤、松月轩霜，诗人以清寂之景纳入诗中，再加上晨钟暮鼓、岐路天涯，无尽的清味就弥漫开来。在诗末，诗人又延伸出“清”的余绪和升华，酝酿出“苦”味，相互叠加：“坐恐晨钟动，天涯道路长”。在独坐难眠之际，将破衲披行的天涯沦落感衍入诗中。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (559, '2f79803d71fa4473827a7bdfcb6ef980', '宿寿安甘棠馆', 228, 23, -1, '年代不详', '山空蕙气香，乳管折云房。愿值壶中客，亲传肘后方。三更礼星斗，寸匕服丹霜。默坐树阴下，仙经横石床。', '山空蕙气香，乳管折云房。愿值壶中客，亲传肘后方。三更礼星斗，寸匕服丹霜。默坐树阴下，仙经横石床。', '尚颜虽是晚唐有名的诗僧，乔松于灌莽，野鹤于鸡群。但道家的思想也对其诗歌创作产生了难以磨灭的影响。壶中客有仙人之意。道家自古就有彩虹光化、羽化升仙的说法。道教二葛之一自号抱朴子的葛洪著有《肘后方》，又称《肘后备急方》。尚颜对道教还坚持内外兼修的法则，不仅顺星礼斗，还内服金丹。发清静志，读仙道经。当他对红尘的渴求归于寂灭，内心的澄澈就与外在的物象交融，从而形成冷寂的禅境。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (557, '9973bdab334747308054b20307fe9dbc', '兰昌宫', 129, 35, -1, '年代不详', '宫兰非瑶草，安得春长在。回首春又归，翠华不能待。悲风生辇路，山川寂已晦。边恨在行人，行人无尽岁。', '宫兰非瑶草，安得春长在。回首春又归，翠华不能待。悲风生辇路，山川寂已晦。边恨在行人，行人无尽岁。', '诗人是写五言的圣手。其诗风浅切通俗深得白居易的精髓。兰昌宫在河谷之东。昌涧水蜿蜒流淌而过。空谷幽兰，清茜馥郁。但兰草毕竟不是仙界的琪花瑶草，纵是晶莹美丽，也难逃岁岁枯荣的命运。春去春又来，连天芳草碧绿如丝，满园的春色仿佛要灼伤了诗人的眼。惟恐透过繁华最胜处看到美好的倏然凋谢，所以要匆匆归去。而在诗末，诗人又将“伤春惜春”与“羁旅离愁”交相叠加，重笔托意。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (556, '5373b6e22f504f70a1b96b4a28f9a1ff', '过三乡望女几山，早岁有卜筑之志', 227, 35, -1, '年代不详', '女几山头春雪消，路傍仙杏发柔条。心期欲去知何日，惆怅回车上野桥。', '女几山头春雪消，路傍仙杏发柔条。心期欲去知何日，惆怅回车上野桥。', '青山意气峥嵘，路两旁的杏树抽出柔嫩的枝条，顿生妩媚。也许是朝廷上的趋走伏奔并不舒心吧，看到如此好景，诗人难免流露出向慕静处的卜隐之志。不过，诗人还是不能效仿陶渊明潇洒地挂冠而去。冠带朝笏，虽然掩盖了诗人的真性情，却没有遭受致命的打击，他的升迁贬谪，在官员诗人中是太寻常不过了。皇权之下，文人似乎只有踏入官场，才能实现修齐治平的理想。诗人深谙此理，只能惆怅归去，不禁畅想，何时才能卸下朝服冠冕，恣意轻狂。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (554, '2715971dc8294282abf6629c40f9477d', '经慈涧题', 207, 31, -1, '年代不详', '岸与恩同广，波将慈共深。涓涓劳日夜，长似下流心。', '岸与恩同广，波将慈共深。涓涓劳日夜，长似下流心。', '', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (555, '3e238666c1944b1e91f9eeaf581b05ff', '兰昌宫', 80, 35, -1, '年代不详', '宫门两片掩埃尘，墙上无花草不春。谁见当时禁中事，阿娇解佩与何人。', '宫门两片掩埃尘，墙上无花草不春。谁见当时禁中事，阿娇解佩与何人。', '闭锁的宫殿庭燎深深掩映了历史的埃尘。发端犹如电影近景的聚焦，重门深锁，掩盖着尘封的历史。镜头向前推进，掀开两扇紧闭的门扉，苍白的现实赫然闯入眼帘。虽是春意盎然的时节，宫中却一片死寂。阳光仿佛也刻意回避了这被世人遗忘的角落。墙上一片斑驳，枯萎的花枝兀自挂在墙头。春草野蛮生长，许久没有人修葺打理。诗人眼前倏地掠过一帧画面，仿佛窥见当年陈阿娇解佩相赠，芳心暗许的场景，不由揶揄轻哂。', '过三乡驿作', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (553, '8b9a8c42b9cb428f9d4eed6c146ef6f6', '宿白马寺', 136, 67, -1, '年代不详', '白马驮经事已空，断碑残刹见遗踪。萧萧茅屋秋风起，一夜雨声羁思浓。', '白马驮经事已空，断碑残刹见遗踪。萧萧茅屋秋风起，一夜雨声羁思浓。', '“安史之乱”的余波席卷东都，白马寺也未能幸免。张继于乱后邸洛，投宿白马寺，目睹兵燹之残状，触景生情作此诗。白马负载着千年的积蕴踏空而来。诗人从眼前的断碑残刹联想到当年汉明帝派遣使者向西域求取佛法，白马驮经而来的盛事，一“空”一“见”的鲜明对比，极为准确地传达出作者的感伤情绪。夜色浓郁得如同叹息。雨点滑过树叶的沙沙声，秋风撕破长空的哀嚎声，一寸寸敲击着羁旅诗人的心。“浓”字使溢满的愁思瞬间宣泄而下，将伤时之恨、身世之感、羁旅之愁在诗中达到水乳交融的地步，因此使此诗成为继《枫桥夜泊》后的又一写愁名篇。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (552, '411da57eba7749eb82417c0faf781c81', '东京次新安道中', 139, 27, -1, '年代不详', '贼去兵来岁月长，野蒿空满坏墙匡。旋从古辙成深谷，几见金舆过上阳。洛水送年催代谢，嵩山擎日拂穹苍。殊时异世为儒者，不见文皇与武皇。', '贼去兵来岁月长，野蒿空满坏墙匡。旋从古辙成深谷，几见金舆过上阳。洛水送年催代谢，嵩山擎日拂穹苍。殊时异世为儒者，不见文皇与武皇。', '', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (550, 'f81f67bbea404a6ab72af0ac288b7db3', '新安江上寄处士', 226, 27, 758, '758年', '深潭与浅滩，万转出新安。人远禽鱼静，山空水木寒。啸起青蘋末，吟瞩白云端。即事遂幽赏，何必挂儒冠。', '深潭与浅滩，万转出新安。人远禽鱼静，山空水木寒。啸起青蘋末，吟瞩白云端。即事遂幽赏，何必挂儒冠。', '此诗当作于乾元元年（公元758年）冬，孟云卿从长安赴洛阳途中所作。杜甫有诗为证《冬末以事之东郊，城湖东遇孟云卿，复归刘颢宅宿，饮宴散因为醉歌》。孟云卿此诗在语言上承袭了韦应物的高古逸奥。韦应物为其上入室者。诗人选取的都是一些稀松平常的意象，禽鱼、水木、空山、青蘋、白云。写法上省力自然，建构出一种冲淡平和的意境。文末借景抒情，将身世遭遇化之于诗歌，呈现出一种情感的哀音，隐隐坦露放下功名的归隐之志。', '此诗当作于乾元元年（公元758年）冬，孟云卿从长安赴洛阳途中所作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (551, 'f4dd1a49bbf54aa0a12f01ae4c4c6419', '将归宜春留题新安馆', 205, 27, -1, '年代不详', '东里如今号郑乡，西家昔日近丘墙。芸台四部添新学，秘殿三年学老郎。天外鸳鸾愁不见，山中云鹤喜相忘。犹张皂盖归蓬荜，直谓时无许子将。', '东里如今号郑乡，西家昔日近丘墙。芸台四部添新学，秘殿三年学老郎。天外鸳鸾愁不见，山中云鹤喜相忘。犹张皂盖归蓬荜，直谓时无许子将。', '', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (549, '4db284a606e7482f884e4fcee50bfd3f', '宿新安江深渡馆寄郑州王使君', 225, 27, -1, '年代不详', '霜飞十月中，摇落众山空。孤馆闭寒木，大江生夜风。赋诗忙有意，沈约在关东。', '霜飞十月中，摇落众山空。孤馆闭寒木，大江生夜风。赋诗忙有意，沈约在关东。', '无', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (547, '5fabc6cf769242139592c06c2728b4aa', '洛阳道', 224, 66, -1, '年代不详', '憧憧洛阳道，尘下生春草。行者岂无家，无人在家老。鸡鸣前结束，争去恐不早。百年路傍尽，白日车中晓。求富江海狭，取贵山岳小。二端立在途，奔走无由了。', '憧憧洛阳道，尘下生春草。行者岂无家，无人在家老。鸡鸣前结束，争去恐不早。百年路傍尽，白日车中晓。求富江海狭，取贵山岳小。二端立在途，奔走无由了。', '翻者，唐末人也。出身寒微，步行至京师科考，落第而归，遂放浪江湖，吟诗弹琴自娱。任翻过洛城，洛阳道上人影憧憧，春草萌生，行者并非无家可归之人，只是不愿终日在乡，蹉跎一生。鸡鸣之时就动身出发，多年行旅与落日、破晓为伴。富贵于己如浮云，山岳江河才是心之所往，心中打定这种主意，多年羁旅又何须理由呢？读任翻之诗，有无畏之感，好似万事万物皆与我不相干，诗歌任性自然，清奇雅正，语言明白晓畅，脍炙人口。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (548, '37585acf5d7a4275a056e12bd7251bfa', '丙戌岁正月出洛阳书怀', 105, 66, -1, '年代不详', '往岁衣褐见，受服金马门。拟将忠与贞，来酬主人恩。天地暂雷雨，洪波生平原。穷鳞遂蹭蹬，夙昔事罕存。幸逢帝出震，授钺清东藩。白日忽再中，万方咸骏奔。王风从西来，春光满乾坤。蛰虫竞飞动，余亦辞笼樊。遭遇思自强，宠辱安足言。唯将四方志，回首谢故园。', '往岁衣褐见，受服金马门。拟将忠与贞，来酬主人恩。天地暂雷雨，洪波生平原。穷鳞遂蹭蹬，夙昔事罕存。幸逢帝出震，授钺清东藩。白日忽再中，万方咸骏奔。王风从西来，春光满乾坤。蛰虫竞飞动，余亦辞笼樊。遭遇思自强，宠辱安足言。唯将四方志，回首谢故园。', '无', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (546, '2e874d5e67e6493f809eb9f3738c1bbb', '洛阳道', 223, 66, -1, '年代不详', '客亭门外路东西，多少喧腾事不齐。杨柳惹鞭公子醉，苎麻掩泪鲁人迷。通宵尘土飞山月，是处经营夹御堤。顷刻知音几存殁，半回依约认轮蹄。', '客亭门外路东西，多少喧腾事不齐。杨柳惹鞭公子醉，苎麻掩泪鲁人迷。通宵尘土飞山月，是处经营夹御堤。顷刻知音几存殁，半回依约认轮蹄。', '郑渥约为晚唐时人，抵达故城，多会有兴叹时逝之感，渥也是如此。客居异乡，只见驿亭门外路上的车马往来不绝，喧哗异常。公子轻骑出行，柳枝拂过策马的鞭子，像是故意招引马上之人。尘土激扬，处处可见禁苑中的堤坝，倏然间从前的知己友人已然殒没，好似半个轮回之久，才隐约看到车马来来回回不间断。这首诗歌以情入景，抒发了作者行旅中伤时嗟叹之感，语言浅切，情思真挚。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (544, '15bc43e2ec1e4e45aa61153e856df825', '经故洛城', 222, 66, -1, '年代不详', '卜世何久远，由来仰圣明。山河徒自壮，周召不长生。几主任奸谄，诸侯各战争。但馀崩垒在，今古共伤情。', '卜世何久远，由来仰圣明。山河徒自壮，周召不长生。几主任奸谄，诸侯各战争。但馀崩垒在，今古共伤情。', '有道生活的时代，已是唐王朝江河日下之日，此时途径洛城，更有覆巢之下的忧畏之情。卜世，即国运，洛城历载数朝，几经沉浮，历史长河中的圣贤明君何其不令人仰慕。如今山河犹在，依旧壮丽无比，只是当年的周公旦与召公奭已然作古。周王朝后期奸邪谄媚之人祸国乱纲，诸侯之间烽火不断，如今经过洛城还能见到昔日之残迹，无论是昔时还是今日，此情此景都教人感慨沉思。有道此作，颇有慨叹兴衰之幽情，登临旧地，本就容易感怀伤势，有道就洛城遥想周王朝旧事，以史入诗，又融入兴叹，令人生出朝兴夕败之感。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (545, '83cebfaf8ddd4972afa6e0d2a1bf9b74', '经故洛城', 96, 66, 870, '870年', '一片危墙势恐人，墙边日日走蹄轮。筑时驱尽千夫力，崩处空为数里尘。长恨往来经此地，每嗟兴废欲沾巾。那堪又向荒城过，锦雉惊飞麦陇春。', '一片危墙势恐人，墙边日日走蹄轮。筑时驱尽千夫力，崩处空为数里尘。长恨往来经此地，每嗟兴废欲沾巾。那堪又向荒城过，锦雉惊飞麦陇春。', '唐懿宗咸通年间前期，罗邺旅寄京城十载，流连举场，但屡次下第，后期（约公元870年—公元873年）离京归东欲投武宁节度使郭铨幕，此作即为从长安东出途径洛阳所作。洛阳是历代古都，其中包含的历史情感自不待言喻，罗邺途径洛城见到昔日劳民伤财的巍巍皇城，如今崩塌荒芜，徒惹游人嗟叹湿巾。但诗人这首咏史之作的重点并未放在洛阳遗址上，也不在城外锦雉惊飞的大片麦陇，而在于“那堪又向荒城过”之句，一个“堪”字，一个“又”字，包含了诗人难以言说的惆怅，科场失意，羁旅劳累，年月往复。罗邺将自身的身世之感寄托在歌咏历史遗迹的诗中，咏叹历史古迹反而成了其舒展怀抱的凭借，此作以对历史遗址的描写起手，承转之后即合于抒发自身的感叹，所选之景虽然带有某种历史的苍凉荒芜之感，但主要是作为作者情感的载体，意在寄托自身身世之感。', '唐懿宗咸通年间前期，罗邺旅寄京城十载，流连举场，但屡次下第，后期（约公元870年—公元873年）离京归东欲投武宁节度使郭铨幕，此作即为从长安东出途径洛阳所作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (542, '089deff2785347259770162e6be80388', '自淮中却赴洛途中作', 221, 66, -1, '年代不详', '惆怅策疲马，孤蓬被风吹。昨东今又西，冉冉长路岐。岁晚树无叶，夜寒霜满枝。旅人恒苦辛，冥寞天何知。', '惆怅策疲马，孤蓬被风吹。昨东今又西，冉冉长路岐。岁晚树无叶，夜寒霜满枝。旅人恒苦辛，冥寞天何知。', '诗人用疏澹笔墨为我们勾勒了一幅秋日行旅图。让天涯游子策一匹疲马出现在一派凄凉的背景上，透出倦于漂泊的凄苦愁楚之情。风起苍岚，只能如蓬草般漂泊无定。前路漫漫，却只能辗转于一个又一个城市，落脚栖留。年岁深晚，树也掉落了一地的枝叶。卸去了御寒的盔甲，只能在风中忍受着寒霜的侵蚀。诗人不由得缩紧一身的廓落，向上苍祈求一丝怜悯，垂怜这孤身在外的游子。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (543, '3b4af2a1a63543ce847d3eda9e34852c', '洛阳道', 222, 66, -1, '年代不详', '喧喧洛阳路，奔走争先步。唯恐著鞭迟，谁能更回顾。覆车虽在前，润屋何曾惧。贤哉只二疏，东门挂冠去。', '喧喧洛阳路，奔走争先步。唯恐著鞭迟，谁能更回顾。覆车虽在前，润屋何曾惧。贤哉只二疏，东门挂冠去。', '洛阳陌上车马喧喧，人们竞相攀踩前仆后继惟恐错过了名利的青睐。这种丑态毕现的嘴脸深深伤及了诗人的心。诗人深感自己已无力挽救日益倾颓的大唐社稷，只能将目光投射向遥远的历史，渴望向疏广、疏受叔侄二人一样，实现理想后功成身退。亦或像渊明一样潇洒地挂冠而去，“抽簪解朝衣，散发归海隅”。从此渔樵山水，悠游卒岁。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (540, '4c3279097f8a4bd1b584b79d2422813e', '洛阳道', 220, 66, -1, '年代不详', '浮世若浮云，千回故复新。旋添青草冢，更有白头人。岁暮容将老，雪晴山欲春。行行车与马，不尽洛阳尘。', '浮世若浮云，千回故复新。旋添青草冢，更有白头人。岁暮容将老，雪晴山欲春。行行车与马，不尽洛阳尘。', '于武陵一生仕途不达，沉沦不僚，游踪遍及商洛、巴蜀间，这首诗是于武陵途经洛中所作。《庄子•知北游》云“人生天地之间，若白驹之过隙，忽然而已”，于武陵许是有同样的感受。行旅途中，回首浮世，如同天上倏忽而过的片片云彩，千古光阴一霎时；茔茔蔓草，岁岁不老，而故人不似冢上青草，一抔黄土湮灭不复。百转千回间，青丝容颜业已变成华发老叟。浮世如云，青冢白发，本是事事无常之叹，而作者却笔锋一转，写岁暮催人老，雪霁天终晴，山岚围绕中的青峰隐隐欲现，洛阳道上车马往来不尽。以淡泊的语气闲闲道出“复新”、“欲春”的人生，是骨子里的潇洒。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (541, '45893fed796b4f0eae513f78f797788b', '过洛阳城', 220, 66, -1, '年代不详', '古来利与名，俱在洛阳城。九陌鼓初起，万车轮已行。周秦时几变，伊洛水犹清。二月中桥路，鸟啼春草生。', '古来利与名，俱在洛阳城。九陌鼓初起，万车轮已行。周秦时几变，伊洛水犹清。二月中桥路，鸟啼春草生。', '洛阳山河拱戴，为历代帝王所青睐。诗人途径此地，登临凭吊，堪破世情的兴废之感油然而生。洛阳今古繁华，是文人名士竞相追逐的名利场。晨晓的箫鼓声刚鸣遍九陌的时候，洛阳道上已经布满了密密匝匝的车辙印。王朝更迭不息，就算战争血流漂杵，历经岁月的沉淀，洛水也变得如往昔般清澈。新绿乍破土，诗人独立中桥，鸟一声声啼鸣，舒徐了这刚欲蓄势的春意。正是“春风不识兴亡意，草色年年满故城”。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (539, 'fa3330c0b1c442188eaaa7040eeeb46e', '新安道中玩流水', 95, 27, -1, '年代不详', '一渠春碧弄潺潺，密竹繁花掩映间。看处便须终日住，算来争得此身闲。萦纡似接迷春洞，清冷应连有雪山。上却征车再回首，了然尘土不相关。', '一渠春碧弄潺潺，密竹繁花掩映间。看处便须终日住，算来争得此身闲。萦纡似接迷春洞，清冷应连有雪山。上却征车再回首，了然尘土不相关。', '子华之诗，浅切精巧，这首七律即是如此。首联从视觉、听觉及空间三个角度写春水碧玉般澄澈透明，泠泠声不绝，掩映在竹间花下，“弄”字更添些许生气，赋予碧波以生命感；颔联由玩水而引发兴叹，修竹繁花，淙淙青溪，如此佳处，须终日在此为好，可惜并未有长久的清闲之时；颈联进一步发挥联想，突出流水曲折绵延、清冷之状；尾联诗人登车回望青山绿水，仍觉春水净土乃凡尘未及之景，侧面烘托出此地之美，诗人之留恋向往。此作轻巧绮丽，音节和谐雅正，在修辞表现上配合视、听等感官，使诗歌细腻而鲜活，有简洁明快之感。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (537, '5b5a147a4df04eeeaac38c869541b015', '重入洛阳东门', 219, 66, 835, '公元835年', '商颜重命伊川叟，时事知非入洛人。连野碧流通御苑，满阶秋草过天津。每惭清秩容衰齿，犹有华簪寄病身。驱马独归寻里巷，日斜行处旧红尘。', '商颜重命伊川叟，时事知非入洛人。连野碧流通御苑，满阶秋草过天津。每惭清秩容衰齿，犹有华簪寄病身。驱马独归寻里巷，日斜行处旧红尘。', '大和九年（835年），李绅为李宗闵所排挤，心怀悒郁，此刻重入洛城，心中惆怅可想而知。“碧流”、“秋草”、“病身”无不透露出仕途不顺之感。尾联作者驱马独归，滚滚红尘中只一轮斜日，往事也一同湮灭其中。这首诗歌融情入诗，给人以苍凉无奈之感，读之不禁怆然。', '大和九年（835年），李绅为李宗闵所排挤，心怀悒郁，此刻重入洛城。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (538, '1ffe7c9e953d40759a62b24552f28143', '寒食洛阳道', 95, 66, -1, '年代不详', '路岐无乐处，时节倍思家。彩索飏轻吹，黄鹂啼落花。连乾驰宝马，历禄斗香车。行客胜回首，看看春日斜。', '路岐无乐处，时节倍思家。彩索飏轻吹，黄鹂啼落花。连乾驰宝马，历禄斗香车。行客胜回首，看看春日斜。', '百五节中，子华羁旅漂泊，独在异乡为异客，此刻倍加思念故乡。寒食秋千满地时，黄鹂相逐落花飞，洛阳道中香车宝马竞相驶过，途中回首，只一轮落日与自己为伴。“人言落日是天涯，望极天涯不见家”，寒食节本是与家人祭祖之时，而独处异乡，本是一伤，所到之处，只见他乡彩索蹁跹，香车宝马，只余啼鸟落花、斜阳相伴，愈添愁肠。子华此作，语言曲丽清雅，借景物点染思乡愁情，令人断肠。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (536, '2008aede0ed84bd5bd2aa196bb22d3b0', '七年初到洛阳寓居宣教里时已春暮而四老俱在洛中分司', 219, 66, 833, '公元833年', '青莎满地无三径，白发缘头忝四人。官职谬齐商岭客，姓名那重汉廷臣。圣朝寡罪容衰齿，愚叟多惭未退身。惟有门人怜钝拙，劝教沈醉洛阳春。', '青莎满地无三径，白发缘头忝四人。官职谬齐商岭客，姓名那重汉廷臣。圣朝寡罪容衰齿，愚叟多惭未退身。惟有门人怜钝拙，劝教沈醉洛阳春。', '大和七年（833年）春暮，李绅抵达洛阳，寓居宣教里，随后白居易以太子宾客分司东都，他们二人和皇甫镛、张仲方共称“四老”，以商山四皓自比，酬唱同游，自得其乐，沉醉洛阳春。婆娑青莎，友人齐聚，甚是畅意；多年来仕途浮沉，自是比不得汉廷重臣，况近花甲之年，已是多病之身，怎有枕流漱石之恣意。此刻的李绅，因受李德裕之事牵连，往东都分司。幸逢好友，虽官途多舛，然得知音美景相伴，便油然生出轻举世俗之感，希冀耽身于杂花生树，群莺乱飞的洛中。此作简洁畅达，作者心情实一“醉”字，落英缤纷，意气相投者四人，真真天地间一大幸事。', '大和七年（833年）春暮，李绅抵达洛阳。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (535, '3c2d9a5600574eb5933d9a9f33b13092', '留别杜审言并呈洛中旧游', 218, 66, -1, '年代不详', '斑鬓今为别，红颜昨共游。年年春不待，处处酒相留。驻马西桥上，回车南陌头。故人从此隔，风月坐悠悠。', '斑鬓今为别，红颜昨共游。年年春不待，处处酒相留。驻马西桥上，回车南陌头。故人从此隔，风月坐悠悠。', '无', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (534, '09ddc16cfa1f4b768fcf9b0e9bc22d83', '客洛酬刘驾', 144, 66, 852, '公元852年', '浮世总应相送老，共君偏更远行多。此回不似前回别，听进离歌逐槕歌。', '浮世总应相送老，共君偏更远行多。此回不似前回别，听进离歌逐槕歌。', '此诗大致作于大中六年以前。诗题不称刘驾博士，意想亦为刘驾中进士前至洛之时，客中之别。浮世清欢，云水无涯。离人独往总是凄凉。诗人写离情，没有横山断岭的阻隔，发端全是内心情感宣泄而下。幽情寓思，嚼之便觉得人生兴味溢满齿颊。相思之情深重。船在洛水中远去渐渐凝聚成小点，徒留满腔离情追逐着木棹演绎成一曲离歌，经久不绝。短短数句，诗人用疏淡笔墨绘出，又掺杂人生况味。可谓言近旨远，语短情长。', '此诗大致作于大中六年以前。诗题不称刘驾博士，意想亦为刘驾中进士前至洛之时，客中之别。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (532, 'dbb44bd6ea5f4864b49f180fa7232fd5', '题寿安甘棠馆二首其二', 59, 23, 831, '831年', '门前洛阳道，门里桃花路。尘土与烟霞，其间十馀步。', '门前洛阳道，门里桃花路。尘土与烟霞，其间十馀步。', '“门”在此诗中起了不可忽视的潜通作用，将现实世界与理想家园轻轻绾合。门外是曹市兴隆、货物琳琅的洛阳大道，门内是“不知有汉，无论魏晋”的世外桃源。门的一侧是扑面而来的市井气，另一侧是馥郁迎鼻的桃花香。作者的情感似乎有所偏向。显然更侧重于门内的桃花陌，在浮世身心俱蒙尘，不若跨越十余步的距离，置身于桃花树下“疏瀹五藏，澡雪精神”，在“虚静”中恒念天地亘远，遥想宇宙洪荒，仿佛与此花同化。', '赴京于甘棠馆作', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (533, '5545b2bd65e245b88ba2f01f1bee72af', '三乡驿楼伏睹玄宗望女几山诗，小臣斐然有感', 59, 35, -1, '年代不详', '开元天子万事足，唯惜当时光景促。三乡陌上望仙山，归作霓裳羽衣曲。仙心从此在瑶池，三清八景相追随。天上忽乘白云去，世间空有秋风词。', '开元天子万事足，唯惜当时光景促。三乡陌上望仙山，归作霓裳羽衣曲。仙心从此在瑶池，三清八景相追随。天上忽乘白云去，世间空有秋风词。', '女几山素负盛名，人称“雄峻赛五岳，奇秀冠中原”。玄宗皇帝曾为其风流俏丽所倾倒，挥毫写下千古绝唱。诗人途经三乡驿，登临此地，伏睹玄宗留作，感慨有怀。此诗是诗人驰骋想象的杰作。玄宗当年莅临巡游此地，见飞瀑高悬，流水鸣琴，遂悠然神往，撰写了名流千古的霓裳羽衣曲。“上元点环招萼绿，王母挥诀别飞琼”。诗人仿佛也醉心于仙界生活，乘白鹤而去，只在人间留一首秋风词，承载着曾经来过的印记。', '由长安赴洛过三乡驿作', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (530, 'b8d93e86d0674a2d9cd9efca1365e100', '酬仆射牛相公晋国池上别后至甘棠馆忽梦同游…口号见寄', 59, 23, 831, '831年', '已嗟池上别魂惊，忽报梦中携手行。此夜独归还乞梦，老人无睡到天明。', '已嗟池上别魂惊，忽报梦中携手行。此夜独归还乞梦，老人无睡到天明。', '无', '赴京于甘棠馆作', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (531, 'f46e5ba5e30b43a9940a1014d1739314', '题寿安甘棠馆二首其一', 59, 23, 831, '831年', '公馆似仙家，池清竹径斜。山禽忽惊起，冲落半岩花。', '公馆似仙家，池清竹径斜。山禽忽惊起，冲落半岩花。', '《诗经》中有《召南·甘棠》之诗：“蔽芾甘棠，勿剪勿伐，召伯所茇。蔽芾甘棠，勿剪勿败，召伯所憩。蔽芾甘棠，勿剪勿拜，召伯所说。”《甘棠之诗》一咏三叹，借物抒情，歌颂了召公为民服务的政绩。自此，“甘棠”一直成为人们喜用的专有词语，大量地出现在地名中，也就不足为奇了。诗人发端劈头以“仙家”名之，没有敞露自己的爱憎，但稍加品酌，却有淡淡的话外音流淌出来。何以为“仙”？池清，方得以映鉴高洁之人的心志。正所谓“饮水必清源”，当是如此。石子铺就的小路蜿蜒伸向远方。“斜”是诗意的补足，试想，如果是笔直而平坦的大道，就少了行石磴间的乐趣，索然无味。不同于前两句疏淡的点染，后两句有一种拔地而起的生气。可能是访者未加收敛的脚步声，惊扰了山禽的休憩，凌空腾起，打破了古朴幽闭公馆的一向的静谧。当奉为以动写静的圭臬。', '赴京于甘棠馆作', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (528, '1d2ffc89799a42ea8f8d5422aae5617d', '将赴苏州途出洛阳留守李相公累申宴饯宠行…以申仰谢', 59, 66, 831, '公元831年', '岁杪风物动，雪馀宫苑晴。兔园宾客至，金谷管弦声。洛水故人别，吴宫新燕迎。越郎忧不浅，怀袖有琼英。', '岁杪风物动，雪馀宫苑晴。兔园宾客至，金谷管弦声。洛水故人别，吴宫新燕迎。越郎忧不浅，怀袖有琼英。', '无', '大和五年冬，刘禹锡出为苏州刺史，经洛阳途中所作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (529, '5792edd6e3c34351a56951f3bb3fc72a', '自左冯归洛下酬乐天兼呈裴令公', 59, 66, -1, '年代不详', '新恩通籍在龙楼，分务神都近旧丘。自有园公紫芝侣，仍追少傅赤松游。华林霜叶红霞晚，伊水晴光碧玉秋。更接东山文酒会，始知江左未风流。', '新恩通籍在龙楼，分务神都近旧丘。自有园公紫芝侣，仍追少傅赤松游。华林霜叶红霞晚，伊水晴光碧玉秋。更接东山文酒会，始知江左未风流。', '无', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (527, '79b7012d29f14727a8eee94fc2449700', '罢郡归洛阳寄友人', 59, 66, 836, '公元836年', '远谪年犹少，初归鬓已衰。门闲故吏去，室静老僧期。不见蜘蛛集，频为佝偻欺。颖微囊未出，寒甚谷难吹。濩落唯心在，平生有己知。商歌夜深后，听者竟为谁。', '远谪年犹少，初归鬓已衰。门闲故吏去，室静老僧期。不见蜘蛛集，频为佝偻欺。颖微囊未出，寒甚谷难吹。濩落唯心在，平生有己知。商歌夜深后，听者竟为谁。', '开成元年，刘禹锡罢郡，为太子宾客、分司东都，归洛阳作。刘禹锡正道直行，却无辜遭贬，远弃遐荒二十二年。他“忧国不谋身”，但却大半生“落魄不自聊”。偃蹇寡和，堙厄感郁，一寓诸文，便自然流露深沉的悲伤意绪和强烈的孤愤情怀。诗人将故吏离去、门庭寥落与内饰静谧、邀约老僧两个场景镶嵌在同一幅构图中，即使心头负载幽怨，也不被忧怨所拘笼，而是借参禅问佛加以排遣。“蜘蛛集而百事喜”是古人的一种猜度。时乖命蹇的诗人一直难逢好事。不仅如此，诗人身形佝偻、行动不便的窘况也愁煞人。然而诗人毕竟有可以代雄的豪气，“世道剧颓波，我心如砥柱”。借春秋时宁戚高唱商歌，引起齐桓公注意，终得赏识以成就大业的典故，抒发内心施展才华的渴望之情。在万死投荒、量移无望的境况下，仍能不降心辱志，在思索中积极振起，并对现实人生和政治仕途充满信心。', '开成元年，刘禹锡罢郡，为太子宾客、分司东都，归洛阳作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (526, '8eaac29265484b88ad635352a1048f09', '罢郡归洛阳闲居', 59, 66, 836, '公元836年', '十年江海守，旦夕有归心。及此西还日，空成东武吟。花间数杯酒，月下一张琴。闻说功名事，依前惜寸阴。', '十年江海守，旦夕有归心。及此西还日，空成东武吟。花间数杯酒，月下一张琴。闻说功名事，依前惜寸阴。', '开成元年，刘禹锡罢郡，为太子宾客、分司东都，归洛阳作。“应怜一罢金闺籍，枉著逢春十度伤”。诗人曾转涉巴山楚水长达十年之久。随着贬谪岁月的流逝，植根于诗人内心的悲愤和郁悒与日俱增。诗人的被弃感、被拘囚感和生命荒废感往往不自觉地流露于笔端。只有故乡能抚平诗人内心的创伤。对于濩落已久的诗人，《东武吟》那样的辛苦词往往是触发其一腔愁绪的媒介。而对于即将奔赴故乡的诗人而言，《东武吟》也不再能触发诗人的身世之感。转而以一声响谒行云的断喝代之，“人生不失意，焉能暴己知”。莫不如将目光投向花前月下，抚琴对酌，岁月静好。诗人虽长期被堙厄所囿，却没有沉溺于困顿的泥潭，而是以通脱圆融的态度，超然自适的心灵，来面对多舛的命运。可以窥见，贬谪固然带来了诗人生命的沉沦，但昂扬的生活态度始终占据着诗人内心的最深处。', '开成元年，刘禹锡罢郡，为太子宾客、分司东都，归洛阳作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (525, '9eefa4127bfe4a77b45f431be2455bd6', '答张侍御贾喜再登科后，自洛赴上都赠别', 59, 66, -1, '年代不详', '又被时人写姓名，春风引路入京城。知君忆得前身事，分付莺花与后生。', '又被时人写姓名，春风引路入京城。知君忆得前身事，分付莺花与后生。', '金榜题名自古便是万千文人奉为皋臬的人生信仰。登科后有传胪的传统，即中进士后唱名的仪式。此诗是诗人为恭贺张侍御贾喜再度登科所作，用“又”字尤不突兀。蟾宫折桂，从此平步青云指日可待。“一切景语皆情语”。所谓“引路”，当是遂平生所愿，襟抱得展后的踌躇满志。春风也化作信使，传递着捷报，指引着友人踏上驱向理想之道的坦途。而昔日种种前尘往事不足萦怀，都交付与莺啼花开的春色和前途可畏的后生吧。你犹自沉浸在二战告捷的喜悦中就好。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (524, '91f93396da3f4113be481775f838a9e8', '赴连州途径洛阳诸公置酒相送张员外贾……率尔酬之', 59, 66, 805, '公元805年', '谪在三湘最远州，边鸿不到水南流。如今暂寄樽前笑，明日辞君步步愁。', '谪在三湘最远州，边鸿不到水南流。如今暂寄樽前笑，明日辞君步步愁。', '永贞元年，刘禹锡以“忧国不谋身”的精神，与柳宗元一道参加了王叔文、王伾为首的内抑宦官、外制方镇的政治革新运动，时称“二王、刘柳”。由于宦官藩镇势力的联合反扑，这次革新不久归于失败。刘禹锡被贬为连州刺史。此诗当是刘禹锡贬谪连州经洛途中于饯别筵席上所作。贺裳云：“梦得佳作，多在郎、连、夔和时作……”。播迁，曾使千百封建文人痛不欲生！而在刘禹锡，于无辜见逐的怨愤中，又糅合着理想受挫、事业遭殃，同志被黜的忧伤，自是十分哀痛。连州坐落于三湘的最远僻处，饶是边鸿也难以逾越这重重的峦嶂，令千军破溃如入无人之境的川流到了这也要转弯改道，更遑论人迹，自是寥寥。纵然是有“诗豪”之美誉的诗人，一想到“一辞御苑青云去，十见蛮江白芷生”的漫漫征途，干云豪气也不由得偃旗息鼓，败下阵来。推杯换盏在人前强颜欢笑，内心却被愁绪无形的绳索交织缠绕。', '永贞元年，刘禹锡以“忧国不谋身”的精神，与柳宗元一道参加了王叔文、王伾为首的内抑宦官、外制方镇的政治革新运动，时称“二王、刘柳”。由于宦官藩镇势力的联合反扑，这次革新不久归于失败。刘禹锡被贬为连州刺史。此诗当是刘禹锡贬谪连州经洛途中于饯别筵席上所作。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (522, 'f361bdc66d004efcb2d1911ce351a978', '留赠偃师主人', 13, 45, -1, '年代不详', '孤城漏未残，徒侣拂征鞍。洛北去游远，淮南归梦阑。晓灯回壁暗，晴雪卷帘寒。强尽主人酒，出门行路难。', '孤城漏未残，徒侣拂征鞍。洛北去游远，淮南归梦阑。晓灯回壁暗，晴雪卷帘寒。强尽主人酒，出门行路难。', '陆游曾推许许浑：“若论风月江山主，丁卯桥应胜午桥”。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (523, '009d3edee0a740488ce3b4e8785e0d72', '送郑佶归洛阳', 217, 66, -1, '年代不详', '苍苍楚色水云间，一醉春风送尔还。何处乡心最堪羡，汝南初见洛阳山。', '苍苍楚色水云间，一醉春风送尔还。何处乡心最堪羡，汝南初见洛阳山。', '翻检唐人临赠的诗篇，那些凄清缠绵、低回留连的作品固然感人至深，但另一种积极昂扬、畅意明快的诗作，却以豪迈劲健的崭新风貌，给湖光山色涂抹上另一种明丽的色彩。楚地是历代诗人拂拂笔端竞相描摹的绘本。但在归心似箭的游子看来，却不及故乡的半分。春风骀荡，令人沉酣迷醉。春风不能醉人，而诗人以物色的变化关乎了人的情感，将内心世界的隐微波动揭橥眼前。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (521, '516a1672678c420ab595b5977644b7ad', '纂异录之许生', 216, 41, -1, '年代不详', '会昌间许生落第东归，于甘泉店西遇五人以《喷玉泉感旧游书怀》为题作七言诗，吟诵后散去。许生继续赶路，天未亮抵甘泉店，店媪曰三更有人卖酒，视其钱为纸钱。知五人皆鬼也。', '会昌间许生落第东归，于甘泉店西遇五人以《喷玉泉感旧游书怀》为题作七言诗，吟诵后散去。许生继续赶路，天未亮抵甘泉店，店媪曰三更有人卖酒，视其钱为纸钱。知五人皆鬼也。', '诗歌与小说的互动', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (519, '1a5ba58cdb234e39a36a3d3353f11bdf', '洛阳客舍逢祖咏留宴', 215, 66, -1, '年代不详', '绵绵钟漏洛阳城，客舍贫居绝送迎。逢君贳酒因成醉，醉后焉知世上情。', '绵绵钟漏洛阳城，客舍贫居绝送迎。逢君贳酒因成醉，醉后焉知世上情。', '羁旅飘零的人是敏感的。外界的事物很容易触发其心绪，何况是在湿漉漉的下雨天。雨点砸落在地上开成了几瓣，在诗人听来就像是深夜被放大了的更漏声，一声一声绵密地滴在心尖，流淌一地悲伤。酒醉的诗人在尚存有一丝意识的情况下，婉言谢绝了东道主的相送，保全了最后的颜面。穷愁的诗人寡居寒舍，连醉酒也是沾了他人款待设宴的光。清醒时潦倒濩落不敢细思，只有借醉意好好梳理这一生所赊欠的情谊，享受这须臾之乐。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (520, '95653505552d4d5d885c39c9451f3683', '洛中次甘水', 13, 41, -1, '年代不详', '带月饭行侣，西游关塞长。晨鸡鸣远戍，宿雁起寒塘。云卷四山雪，风凝千树霜。谁家游侠子，沉醉卧兰堂。', '带月饭行侣，西游关塞长。晨鸡鸣远戍，宿雁起寒塘。云卷四山雪，风凝千树霜。谁家游侠子，沉醉卧兰堂。', '许浑可谓跻身“晚唐铮铮者”之流。《丁卯集》中皆近体，其中又以律诗为最多。与当时大多数文人样，许浑走的也是读书—游历—应举—为官这般传统的路数。这样的阅历影响到诗人的创作。因此许浑的诗中多登山临水、寄赠留达之作。此诗当咏旅愁莫释之情。首二句是诗人宦游足迹的概括。诗腰四句纵横开阖，晨鸡啼鸣、宿雁惊飞，云卷积雪、风凝落霜，大小远近、高下动静写得极有层次，使得声色相宜，生动地展现了沿途的景致。尾二句是诗意的升华，飘零转涉的游子，思乡之情无所排遣，只能借酒浇愁，兀自迷醉消沉。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (518, 'c50d97f1d74241e99feee6564ff38e42', '洛阳道五首献吕四郎中', 39, 66, 725, '725年', '洛水照千门，千门碧空里。少年不得志，走马游新市。', '洛水照千门，千门碧空里。少年不得志，走马游新市。', '储光羲的山水诗淡逸成妍，世人常与王维并论，却全然没有幽深杳远的禅意，遂同王右丞分道扬镳。诗人似乎极为关照洛水。在诗人的笔下，洛水总是春意登临的第一处。洛水澄碧，蜿蜒穿城而过，千门万户的幢幢剪影倒映在水面。水天相接，碧绿的水面仿佛把天色也洗涤得澄澈而明净。洛水如镜，是承接千门与碧空的媒介，经过诗人魔力的点化，春意霎时变得立体而生动。结尾处诗人蓦地涌上一抹自嘲，只一瞬便倏然释怀，莫不如乐天从命，汲汲营营哪有蓄满春意的洛阳城更有吸引力呢？', '开元十一年（公元725）储光羲离开家乡，辗转于长安、洛阳应试，此诗当作于此间。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (517, '6c5790a9170d43c894bb82605eaa1b54', '洛阳道五首献吕四郎中', 39, 66, 725, '公元725年', '春风二月时，道傍柳堪把。上枝覆官阁，下枝覆车马。', '春风二月时，道傍柳堪把。上枝覆官阁，下枝覆车马。', '后人常谓太祝诗格高调逸，趣远而情深。此首绝句即是如此，此诗凡二十言，看似平平无奇，却通过惠风、垂柳等目之所及的常见物象，将春日之中洛阳道的韶景畅然勾勒出来，语尽辞竭之后而余味愈长。煦煦的春风中，徜徉在车水马龙、游人交织的洛阳道上，两旁的垂柳在微醺的春风中已然翠色欲滴。东风二月柳宜春，春风与袅娜的柳枝是最相配的；清风舞翩纤，嫩绿的柳枝在风中摇曳着拂过游人休憩的阁楼和来来往往的马车。太祝绝句一首读来，仿佛自己也处身彼时之景，春风中嫩柳似乎氤氲着芬芳，淡淡地消弭在接袂成帷的游人身旁。故后人道其诗“质朴之中，有古雅之味”，绝非谀词。', '开元十一年（公元725）储光羲离开家乡，辗转于长安、洛阳应试，此诗当作于此间。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (516, 'd14e005dbad54821ae71c7adc98369cb', '洛阳道五首献吕四郎中', 39, 66, 725, '公元725年', '大道直如发，春日佳气多。五陵贵公子，双双鸣玉珂。', '大道直如发，春日佳气多。五陵贵公子，双双鸣玉珂。', '洛阳自古繁华地。诗人初入洛中，欣喜之情溢于言表。寥寥二十字，将洛阳城的热闹繁盛穷形尽相。起句作比，官道广阔延伸，像头发一样笔直。作者以笔直的大道为原点，引领我们进入到蓄满春意的洛阳城。春和景明。万物抽吐新绿，碧丝荡漾，弥漫着蓊郁的春意。而熙熙攘攘的人群结伴踏青，环佩碰撞后叮当作响，又何尝不是是另一种生命力的勃发？五陵年少，鲜衣怒马，自是青春蓬勃的朝气，洋溢着鲜活的生命张力。', '开元十一年（公元725）储光羲离开家乡，辗转于长安、洛阳应试，此诗当作于此间。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (515, '4b28db2381db4967a15cf71733cc4415', '洛阳道五首献吕四郎中', 39, 66, 725, '公元725年', '剧孟不知名，千金买宝剑。出入平津邸，自言娇且艳。', '剧孟不知名，千金买宝剑。出入平津邸，自言娇且艳。', '剧孟为西汉时闻名遐迩的游侠，其人嵚奇磊落，周亚夫说“剧孟一人，能抵一国诸侯之兵”，足见其万夫不当之勇。储太祝此作中化用剧孟的典故，写他当时在豪杰迭出之世已赫赫有名，为西汉丞相公孙弘府中时常进出的嘉客。平津邸实非寻常人所能踏入之所，公孙弘起客馆，开东阁，招请士人，有战国四君之一孟尝君食客三千之遗风，而剧孟自然是为公孙弘所重视的能人志士。太祝此意甚明，诗中写的是剧孟，却似诉自己郁郁不得志的愤懑。此作以典入诗，隐晦曲微地传情达意，质朴而挟风雅之迹，颇有浩然之气。', '开元十一年（公元725）储光羲离开家乡，辗转于长安、洛阳应试，此诗当作于此间。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (513, '76b201996ae547069ce0ed161409604e', '夜到洛口入黄河', 39, 66, 725, '公元725年', '河洲多青草，朝暮增客愁。客愁惜朝暮，枉渚暂停舟。中宵大川静，解缆逐归流。浦溆既清旷，沿洄非阻修。登舻望落月，击汰悲新秋。倘遇乘槎客，永言星汉游。', '河洲多青草，朝暮增客愁。客愁惜朝暮，枉渚暂停舟。中宵大川静，解缆逐归流。浦溆既清旷，沿洄非阻修。登舻望落月，击汰悲新秋。倘遇乘槎客，永言星汉游。', '开元十一年（公元725）储光羲离开家乡，辗转于长安、洛阳应试，此诗当作于此间。就诗歌体裁而言，储独以五言古胜场耳。储光羲的小诗清新可诵，其山水田园诗与王维等为伯仲之欢。储光羲应举不第，襟抱难展，遂常常将放归自然作为调适身心的最好去处，散怀山水而至萧然忘羁。故行旅途中的每一片青草，每一条溪流，每一抹月色，都投射了诗人主观的精神和情感。诗人将身世之感并入山水诗的内容，以整个自然山水为喻体，寄托了他的失意不平和羁旅离愁。诗人尤爱用“清”字，营造一种雅致清旷的意境。山水的清幽，伴以写法上的浑然而就，格外显得韵致高远。胡应麟谓其：“储光羲清而适，”推誉过当。', '开元十一年（公元725）储光羲离开家乡，辗转于长安、洛阳应试，此诗当作于此间。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (514, '380b5d826fe44d14ac8229f051283f95', '洛阳道五首献吕四郎中', 39, 66, 725, '公元725年', '洛水春冰开，洛城春水绿。朝看大道上，落花乱马足。', '洛水春冰开，洛城春水绿。朝看大道上，落花乱马足。', '六九冰开，七九燕来，洛阳城里春意初绽。诗人不吝惜笔墨，用大量篇幅写入春后的洛水。洛水穿城而过，将宫城、皇城与城郭分开。洛口是诗人途经水路入洛的交通要冲。春天的第一声脚步，总是格外眷顾于冰雪消融的声音。因此洛水被大面积地纳入到诗人关注的视野中。后两句诗人将镜头聚焦于眼前，由远及近。“乱”字写得尤为传神。早春生命刚吐露新绿，落花尤衬得万物生长的不易。阵阵落花放佛要淹没了行路，马也生出恻隐之心，小心躲避惟恐惊扰了这脆弱的生命。诗歌虽造语自然，但我们却能从中吟味出自然之旨趣。故《四库提要》说储诗：“质朴之中有古雅之味”，当是如此。', '开元十一年（公元725）储光羲离开家乡，辗转于长安、洛阳应试，此诗当作于此间。', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (509, 'ee5b8abf159c4d09aee68e6543cf1bf5', '晓过函谷关', 117, 69, -1, '年代不详', '因韩为赵两游秦，十月冰霜渡孟津。纵使鸡鸣遇关吏，不知余也是何人。', '因韩为赵两游秦，十月冰霜渡孟津。纵使鸡鸣遇关吏，不知余也是何人。', '', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (510, '2a96b0d1350345d58a7b2a07b1000bd9', '咏史诗·孟津', 214, 69, -1, '年代不详', '秋风飒飒孟津头，立马沙边看水流。见说武王东渡日，戎衣从此咤阳侯。', '秋风飒飒孟津头，立马沙边看水流。见说武王东渡日，戎衣从此咤阳侯。', '诗人独立孟津头，冽冽的萧风吹起了被沙尘掩埋了的历史。他仿佛看到当年周武王在此地筑台阅兵，洞察了人心向背。一路所向披靡，颠覆了商王朝的政权，让无数仁人志士看到了希望的曙光。只要有军功傍身，纵使短褐布衣也能摇身一变叱咤政坛。诗人也是这万千失意中人的一员。他曾在《下第》诗中抱怨道：“上林新桂年年发，不许平人折一枝”。诗人在现实的社会中屡屡碰壁，只好把目光投射向遥远的历史。故诗人每游览于古今兴废陈迹，慷慨怀古，作有《咏史诗三卷》，每首以地名为题。此为其中之一。', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (511, '6686988850b646b38dc04765bff5ae03', '过洛阳城', 107, 66, -1, '年代不详', '七百数还穷，城池一旦空。夕阳唯照草，危堞不胜风。岸断河声别，田荒野色同。去来皆过客，何处问遗宫。', '七百数还穷，城池一旦空。夕阳唯照草，危堞不胜风。岸断河声别，田荒野色同。去来皆过客，何处问遗宫。', '', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (512, '6f36091e73604151b32f32663bccdb05', '早发洛中', 107, 66, -1, '年代不详', '半夜发清洛，不知过石桥。云增中岳大，树隐上阳遥。堑黑初沉月，河明欲认潮。孤村人尚梦，无处暂停桡。', '半夜发清洛，不知过石桥。云增中岳大，树隐上阳遥。堑黑初沉月，河明欲认潮。孤村人尚梦，无处暂停桡。', '', '无', 1574483322, 0, 0);
INSERT INTO `jbd_poetry` VALUES (641, '3da931461b544441b8393edc378e20f5', '辞宣武郑尚书启', 64, 0, 864, '864年', '某啟。某聞鄭司農之東去絳紗，感深吾道。謝記室之西辭朱邸，戀切所知。雖定名之分則殊，而懷德之心不異。其有棲羸樂廄，養病醫門。海燕辭巢，即摧萍影。林烏繞樹，忽軫蓬心。又安得下棄席於詩人，感崩波於行客。某也風塵下物，天地中材。光逸門寒，無因自進。揚雄口吃，徒欲解嘲。屬者尚書置驛恩寬，敦風志大。孔文舉之乾元禮，既忝登門。徐孺子之謁陳蕃，俄蒙下榻。淹延館宇，荏苒春秋。稻粱有異於他人，觴豆時陪於上客。那言此際，遽愴離聲。背重德於邱山，揖紅塵於道路。緬懷今日，杳不勝情。加以貢部傷心，名場落羽。獸因鬥困，羊以多亡。前瞻既倦於吹齏，內顧徒悲於求劍。昔也來慚賦雪，謬稱梁苑之游。今則去類乞師，已抱秦庭之哭。倚征輪而悵望，指斷鞭以夷猶。尚書儻或仙客壺中，旁均日月。山公啟里，別借篇題。無令一葉先秋，遂對滿堂垂泣。舍此丹須九轉，桃指千年。天也何如，時乎不再。謹啟。', '某啟。某聞鄭司農之東去絳紗，感深吾道。謝記室之西辭朱邸，戀切所知。雖定名之分則殊，而懷德之心不異。其有棲羸樂廄，養病醫門。海燕辭巢，即摧萍影。林烏繞樹，忽軫蓬心。又安得下棄席於詩人，感崩波於行客。某也風塵下物，天地中材。光逸門寒，無因自進。揚雄口吃，徒欲解嘲。屬者尚書置驛恩寬，敦風志大。孔文舉之乾元禮，既忝登門。徐孺子之謁陳蕃，俄蒙下榻。淹延館宇，荏苒春秋。稻粱有異於他人，觴豆時陪於上客。那言此際，遽愴離聲。背重德於邱山，揖紅塵於道路。緬懷今日，杳不勝情。加以貢部傷心，名場落羽。獸因鬥困，羊以多亡。前瞻既倦於吹齏，內顧徒悲於求劍。昔也來慚賦雪，謬稱梁苑之游。今則去類乞師，已抱秦庭之哭。倚征輪而悵望，指斷鞭以夷猶。尚書儻或仙客壺中，旁均日月。山公啟里，別借篇題。無令一葉先秋，遂對滿堂垂泣。舍此丹須九轉，桃指千年。天也何如，時乎不再。謹啟。', '公元864年五月，投诗干谒新任宣武节度使郑处诲。公元864年秋，辞别郑处诲南归。', '公元864年五月，投诗干谒新任宣武节度使郑处诲。公元864年秋，辞别郑处诲南归。', 1574483322, 0, 0);
INSERT INTO `jbd_user` VALUES (1, '8d40ebbb1ea043808b6c90815ba08630', 'admin', 'admin@jingbiandao.com', '$2y$10$7UJ8ESS1WYDjpTzRF648S.4e0bWRKX4EjhoKpUMDMiRlK/UYNjzRq', 1, 1574084626, 0, 0);
