-- ----------------------------
-- Table structure for jbd_poetry
-- ----------------------------
DROP TABLE IF EXISTS `jbd_poetry`;
CREATE TABLE `jbd_poetry`  (
  `id` int(11) UNSIGNED NOT NULL AUTO_INCREMENT,
  `uuid` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `name` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '',
  `poet_id` int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '诗人id',
  `location_id` int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '地点id',
  `year_int` int(11) NOT NULL COMMENT '年份数字，主要用来搜索，-1表示年份不确定',
  `year_text` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '年份格式多变，用字符串存',
  `content_src` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '诗歌原内容，查询用这个',
  `content_html` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '诗歌html内容，显示用这个',
  `desc` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '赏析',
  `background` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '作品背景',
  `create_time` int(10) UNSIGNED NOT NULL DEFAULT 0,
  `update_time` int(10) NOT NULL DEFAULT 0,
  `delete_time` int(10) NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uniq_uuid`(`uuid`) USING BTREE,
  INDEX `idx_name`(`name`) USING BTREE,
  INDEX `idx_create`(`create_time`) USING BTREE,
  INDEX `idx_update`(`update_time`) USING BTREE,
  INDEX `idx_delete`(`delete_time`) USING BTREE,
  INDEX `idx_poet_id`(`poet_id`) USING BTREE,
  INDEX `idx_location_id`(`location_id`) USING BTREE,
  INDEX `idx_year`(`year_int`) USING BTREE,
  INDEX `idx_content`(`content_src`(100)) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 642 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;

